        [6084] = {11104,1,2,"主角",1,0,"恭贺大哥新婚之喜！","恭贺大哥新婚之喜！",1,"story_zj2_534","story_nzj2_534","刘备",202,},
        [6085] = {11104,2,1,"刘备",202,0,"这……新房之中为何枪刀簇满？侍婢皆佩剑悬刀，立于两傍，难道是孙权设的埋伏？","这……新房之中为何枪刀簇满？侍婢皆佩剑悬刀，立于两傍，难道是孙权设的埋伏？",1,"story_lb2_361","story_lb2_361","孙尚香",310,},
        [6086] = {11104,3,2,"孙尚香侍女",125,0,"贵人休得惊惧，夫人自幼好观武事，居常令侍婢击剑为乐，故尔如此。","贵人休得惊惧，夫人自幼好观武事，居常令侍婢击剑为乐，故尔如此。",1,"story_ssxsv_1","story_ssxsv_1","刘备",202,},
