        [6180] = {11402,5,1,"主角",1,0,"舍鞍马、杖舟楫乃孙吴之所长，而曹兄军队多北方人，不习水战，铁索连环致使舟船调动不便，此其一也。","舍鞍马、杖舟楫乃孙吴之所长，而曹兄军队多北方人，不习水战，铁索连环致使舟船调动不便，此其一也。",1,"story_zj2_543","story_nzj2_543","曹操",103,},
