        [6276] = {11803,3,1,"周瑜",303,0,"吾有一计，子敬不必去见吴侯，直接回去荆州对刘备说：孙、刘两家，既结为亲，便是一家；","吾有一计，子敬不必去见吴侯，直接回去荆州对刘备说：孙、刘两家，既结为亲，便是一家；",1,"story_zy2_271","story_zy2_271","鲁肃",319,},
