        [6309] = {11902,3,1,"诸葛亮",203,0,"代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。","代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。",1,"story_zgl_326","story_zgl_326","刘备",202,},
        [6310] = {11902,4,1,"诸葛亮",203,0,"代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。","代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。",1,"story_zgl_327","story_zgl_327","刘备",202,},
