        [6453] = {12703,1,1,"杨秋",131,0,"马超仗着自己勇猛，常有欺凌主公之心，以某愚见，不如按投曹公，他日不失封侯之位。","马超仗着自己勇猛，常有欺凌主公之心，以某愚见，不如按投曹公，他日不失封侯之位。",1,"story_yq_1","story_yq_1","韩遂",141,},
