        [6461] = {12801,1,1,"张鲁",442,0,"西凉马腾被曹操杀了，马超新败，曹操必将入侵我汉中。我欲自称汉宁王，督兵拒曹操，如何？","西凉马腾被曹操杀了，马超新败，曹操必将入侵我汉中。我欲自称汉宁王，督兵拒曹操，如何？",1,"story_zl_1","story_zl_1","阎圃",138,},
