        [6481] = {12902,4,1,"张松",245,0,"（从头至尾看了一遍）……我还以为是什么书呢，这书我们蜀中小孩子都会背，有什么新的！","（从头至尾看了一遍）……我还以为是什么书呢，这书我们蜀中小孩子都会背，有什么新的！",1,"story_zs4_9","story_zs4_9","杨修",131,},
