        [6505] = {13003,4,2,"张松",245,0,"明公可速图之，我有心腹契友法正和孟达，此二人必能相助。","明公可速图之，我有心腹契友法正和孟达，此二人必能相助。",1,"story_zs4_23","story_zs4_23","主角",1,},
        [6506] = {13003,5,1,"主角",1,0,"青山不老，绿水长存，他日事成，必当厚报。","青山不老，绿水长存，他日事成，必当厚报。",1,"story_zj2_568","story_nzj2_568","张松",245,},
        [6507] = {13004,1,1,"张松",245,0,"我这次见到曹操，轻贤傲士，只可同忧，不可同乐。吾已将益州许给了刘皇叔。","我这次见到曹操，轻贤傲士，只可同忧，不可同乐。吾已将益州许给了刘皇叔。",1,"story_zs4_24","story_zs4_24","法正",219,},
        [6508] = {13004,2,2,"法正",219,0,"我早就觉得刘璋无能，有心想见刘皇叔很久了。既然我们想法一致，还在犹豫什么呢？","我早就觉得刘璋无能，有心想见刘皇叔很久了。既然我们想法一致，还在犹豫什么呢？",1,"story_fz_8","story_fz_8","张松",245,},
        [6509] = {13004,3,1,"张松",245,0,"明日我去见刘璋，让他派二位哥哥为使，前往荆州。与刘皇叔共商大计。","明日我去见刘璋，让他派二位哥哥为使，前往荆州。与刘皇叔共商大计。",1,"story_zs4_25","story_zs4_25","法正",219,},
        [6510] = {13101,1,1,"法正",219,0,"益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。","益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。",1,"story_fz_9","story_fz_9","刘备",202,},
        [6511] = {13101,2,1,"法正",219,0,"益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。","益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。",1,"story_fz_10","story_fz_10","刘备",202,},
        [6512] = {13101,3,1,"法正",219,0,"今日自付与将军，不可错失。将军欲取，某当效死。","今日自付与将军，不可错失。将军欲取，某当效死。",1,"story_fz_11","story_fz_11","刘备",202,},
        [6513] = {13101,4,2,"庞统",211,0,"我们就在宴会上除掉刘璋，埋伏刀斧手一百人，主公掷杯为号。","我们就在宴会上除掉刘璋，埋伏刀斧手一百人，主公掷杯为号。",1,"story_pt_44","story_pt_44","刘备",202,},
        [6514] = {13101,5,1,"刘备",202,0,"刘季玉与我同宗，不忍取之。你们不必再说了。","刘季玉与我同宗，不忍取之。你们不必再说了。",1,"story_lb2_395","story_lb2_395","",0,},
        [6515] = {13102,1,2,"刘璋部下",142,0,"主公叫刘玄德来益州，恐引狼入室，不如早些叫他回去，免生后患。","主公叫刘玄德来益州，恐引狼入室，不如早些叫他回去，免生后患。",1,"story_lzsc_1","story_lzsc_1","刘璋",246,},
