        [6517] = {13102,3,2,"刘璋部下",142,0,"虽玄德无此心，他手下人皆欲吞并西川，以图富贵。","虽玄德无此心，他手下人皆欲吞并西川，以图富贵。",1,"story_lzsc_2","story_lzsc_2","刘璋",246,},
