        [6519] = {13103,1,1,"刘璋",246,0,"我接到报告说，张鲁忽然整顿兵马，将犯葭萌关。玄德可有何良策？","我接到报告说，张鲁忽然整顿兵马，将犯葭萌关。玄德可有何良策？",1,"story_lz3_6","story_lz3_6","刘备",202,},
