        [6544] = {13301,1,1,"董昭",133,0,"自古以来，人臣未有如丞相之功者，丞相立下多少丰功伟绩，怎可与诸臣宰同列乎？","自古以来，人臣未有如丞相之功者，丞相立下多少丰功伟绩，怎可与诸臣宰同列乎？",1,"story_dz2_6","story_dz2_6","",0,},
