        [6566] = {13403,4,1,"曹操",103,0,"孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。","孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。",1,"story_cc1_687","story_cc1_687","",0,},
        [6567] = {13403,5,1,"曹操",103,0,"孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。","孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。",1,"story_cc1_688","story_cc1_688","",0,},
        [6568] = {13403,6,1,"曹操",103,0,"公宜自思焉。足下不死，孤不得安。哈哈哈！孙仲谋不欺我也！退兵！","公宜自思焉。足下不死，孤不得安。哈哈哈！孙仲谋不欺我也！退兵！",1,"story_cc1_689","story_cc1_689","",0,},
