        [6570] = {13501,2,2,"主角",1,0,"大哥放心，有孔明军师在荆州，料想东吴不敢来犯。大哥可以去问刘璋要兵马钱粮，以抵御曹操。","大哥放心，有孔明军师在荆州，料想东吴不敢来犯。大哥可以去问刘璋要兵马钱粮，以抵御曹操。",1,"story_zj2_569","story_nzj2_569","刘备",202,},
