        [6583] = {13503,5,2,"庞统",211,0,"我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，","我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，",1,"story_pt_46","story_pt_46","刘备",202,},
        [6584] = {13503,6,2,"庞统",211,0,"我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，","我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，",1,"story_pt_47","story_pt_47","刘备",202,},
        [6585] = {13503,7,2,"庞统",211,0,"趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。","趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。",1,"story_pt_48","story_pt_48","刘备",202,},
        [6586] = {13503,8,2,"庞统",211,0,"趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。","趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。",1,"story_pt_49","story_pt_49","刘备",202,},
