        [6589] = {13601,2,2,"张肃",122,0,"捡到张松给刘备的书信：……松当为内应，万勿自误！这！张松要造反！我要通知主公！","捡到张松给刘备的书信：……松当为内应，万勿自误！这！张松要造反！我要通知主公！",1,"story_zs3_1","story_zs3_1","",0,},
        [6590] = {13601,3,1,"刘璋",246,0,"什么！我待张松不薄，他竟串通外人想要谋反！来人，杀了张松全家！","什么！我待张松不薄，他竟串通外人想要谋反！来人，杀了张松全家！",1,"story_lz3_9","story_lz3_9","",0,},
        [6591] = {13601,4,2,"杨怀",131,0,"我与高沛二人计划藏利刃在身，借送行的名义杀了刘备，以绝后患。","我与高沛二人计划藏利刃在身，借送行的名义杀了刘备，以绝后患。",1,"story_yh1_5","story_yh1_5","",0,},
        [6592] = {13602,1,1,"庞统",211,0,"杨怀、高沛如果来了，要提防着点，如果不来，我们就直接起兵直取其关，不得迟缓。","杨怀、高沛如果来了，要提防着点，如果不来，我们就直接起兵直取其关，不得迟缓。",1,"story_pt_50","story_pt_50","刘备",202,},
