        [6595] = {13602,4,2,"杨怀",131,0,"听说刘皇叔要回荆州了，我兄弟二人特来送行。","听说刘皇叔要回荆州了，我兄弟二人特来送行。",1,"story_yh1_6","story_yh1_6","刘备",202,},
