        [6616] = {13801,4,2,"庞统",211,0,"观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。","观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。",1,"story_pt_55","story_pt_55","刘备",202,},
        [6617] = {13801,5,2,"庞统",211,0,"观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。","观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。",1,"story_pt_56","story_pt_56","刘备",202,},
        [6618] = {13801,6,1,"刘备",202,0,"好，既然如此，我们兵分两路去取雒城。","好，既然如此，我们兵分两路去取雒城。",1,"story_lb2_408","story_lb2_408","庞统",211,},
        [6619] = {13802,1,1,"刘备",202,0,"这劣马，竟将军师掀了下来！军师何故乘此劣马？","这劣马，竟将军师掀了下来！军师何故乘此劣马？",1,"story_lb2_409","story_lb2_409","庞统",211,},
