        [6623] = {13803,1,1,"庞统",211,0,"此处是何地？","此处是何地？",1,"story_pt_59","story_pt_59","",0,},
        [6624] = {13803,2,1,"庞统",211,0,"此地名为落凤坡，我道号凤雏，这里不利于我。还是赶快撤退。","此地名为落凤坡，我道号凤雏，这里不利于我。还是赶快撤退。",1,"story_pt_60","story_pt_60","",0,},
        [6625] = {13803,3,2,"张任",148,0,"骑白马者必是刘备，他来了，弓箭手！发射！","骑白马者必是刘备，他来了，弓箭手！发射！",1,"story_zr3_1","story_zr3_1","",0,},
        [6626] = {13803,4,1,"庞统",211,0,"不好！有埋伏！快撤！","不好！有埋伏！快撤！",1,"story_pt_61","story_pt_61","",0,},
        [6627] = {13901,1,1,"诸葛亮",203,0,"哀哉！痛哉！西方星坠，庞士元命必休矣！","哀哉！痛哉！西方星坠，庞士元命必休矣！",1,"story_zgl_342","story_zgl_342","",0,},
        [6628] = {13901,2,1,"诸葛亮",203,0,"我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。","我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。",1,"story_zgl_343","story_zgl_343","",0,},
        [6629] = {13901,3,1,"诸葛亮",203,0,"我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。","我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。",1,"story_zgl_344","story_zgl_344","",0,},
        [6630] = {13901,4,1,"诸葛亮",203,0,"今吾主丧一臂矣！马上就会收到消息了。","今吾主丧一臂矣！马上就会收到消息了。",1,"story_zgl_345","story_zgl_345","",0,},
        [6631] = {13901,5,2,"关平",218,0,"报告诸葛军师，庞军师被张任在落凤坡前箭射身故。","报告诸葛军师，庞军师被张任在落凤坡前箭射身故。",1,"story_gp_4","story_gp_4","诸葛亮",203,},
        [6632] = {13902,1,1,"诸葛亮",203,0,"既然主公现在涪关进退两难，亮不得不去。","既然主公现在涪关进退两难，亮不得不去。",1,"story_zgl_346","story_zgl_346","",0,},
        [6633] = {13902,2,2,"关羽",205,0,"军师去，谁保荆州？荆州乃重地，干系非轻。","军师去，谁保荆州？荆州乃重地，干系非轻。",1,"story_gy1_138","story_gy1_138","诸葛亮",203,},
        [6634] = {13902,3,1,"诸葛亮",203,0,"主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。","主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。",1,"story_zgl_347","story_zgl_347","关羽",205,},
        [6635] = {13902,4,1,"诸葛亮",203,0,"主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。","主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。",1,"story_zgl_348","story_zgl_348","关羽",205,},
        [6636] = {13902,5,2,"关羽",205,0,"大丈夫既领重任，至死方休。","大丈夫既领重任，至死方休。",1,"story_gy1_139","story_gy1_139","诸葛亮",203,},
        [6637] = {13903,1,1,"诸葛亮",203,0,"如果曹操带兵来到，当如何应对？","如果曹操带兵来到，当如何应对？",1,"story_zgl_349","story_zgl_349","关羽",205,},
        [6638] = {13903,2,2,"关羽",205,0,"以力拒之。","以力拒之。",1,"story_gy1_140","story_gy1_140","诸葛亮",203,},
        [6639] = {13903,3,1,"诸葛亮",203,0,"如果曹操，孙权一齐起兵来，如之奈何？","如果曹操，孙权一齐起兵来，如之奈何？",1,"story_zgl_350","story_zgl_350","关羽",205,},
        [6640] = {13903,4,2,"关羽",205,0,"分兵拒之。","分兵拒之。",1,"story_gy1_141","story_gy1_141","诸葛亮",203,},
        [6641] = {13903,5,1,"诸葛亮",203,0,"若如此，荆州危矣。吾有八个字，将军牢记，可保荆州。","若如此，荆州危矣。吾有八个字，将军牢记，可保荆州。",1,"story_zgl_351","story_zgl_351","关羽",205,},
        [6642] = {13903,6,2,"关羽",205,0,"军师请讲，军师之言，当铭肺腑。","军师请讲，军师之言，当铭肺腑。",1,"story_gy1_142","story_gy1_142","诸葛亮",203,},
        [6643] = {13903,7,1,"诸葛亮",203,0,"北拒曹操，东和孙权。","北拒曹操，东和孙权。",1,"story_zgl_352","story_zgl_352","关羽",205,},
        [6644] = {14001,1,1,"诸葛亮",203,0,"西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。","西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。",1,"story_zgl_353","story_zgl_353","张飞",206,},
        [6645] = {14001,2,1,"诸葛亮",203,0,"西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。","西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。",1,"story_zgl_354","story_zgl_354","张飞",206,},
        [6646] = {14001,3,2,"张飞",206,0,"听军师的，但投降者秋毫无犯。","听军师的，但投降者秋毫无犯。",1,"story_zf1_83","story_zf1_83","诸葛亮",203,},
        [6647] = {14002,1,1,"严颜军士",129,0,"张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。","张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。",1,"story_yyjs_1","story_yyjs_1","",0,},
        [6648] = {14002,2,1,"严颜军士",129,0,"张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。","张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。",1,"story_yyjs_2","story_yyjs_2","",0,},
        [6649] = {14002,3,2,"张飞随从",142,0,"我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！","我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！",1,"story_zfsc_1","story_zfsc_1","严颜",122,},
        [6650] = {14002,4,2,"张飞随从",142,0,"我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！","我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！",1,"story_zfsc_2","story_zfsc_2","严颜",122,},
        [6651] = {14002,5,1,"严颜",122,0,"岂有此理！匹夫怎敢如此无礼！来人割了这走狗的耳鼻！","岂有此理！匹夫怎敢如此无礼！来人割了这走狗的耳鼻！",1,"story_yy2_1","story_yy2_1","",0,},
        [6652] = {14003,1,1,"张飞",206,0,"严颜这老匹夫！气死我了！","严颜这老匹夫！气死我了！",1,"story_zf1_84","story_zf1_84","",0,},
        [6653] = {14003,2,2,"张飞随从",142,0,"将军不须心焦，近几日军士砍柴打探到一条小路，可以偷过巴郡。","将军不须心焦，近几日军士砍柴打探到一条小路，可以偷过巴郡。",1,"story_zfsc_3","story_zfsc_3","张飞",206,},
        [6654] = {14003,3,1,"张飞",206,0,"事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。","事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。",1,"story_zf1_85","story_zf1_85","",0,},
        [6655] = {14003,4,1,"张飞",206,0,"事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。","事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。",1,"story_zf1_86","story_zf1_86","",0,},
        [6656] = {14003,5,2,"严颜",122,0,"果然张飞忍耐不得，中我计也。前面那个就是张飞！我们过去擒了他！","果然张飞忍耐不得，中我计也。前面那个就是张飞！我们过去擒了他！",1,"story_yy2_2","story_yy2_2","",0,},
        [6657] = {14003,6,1,"张飞",206,0,"老贼休走！我在这呢！前面那是假的！","老贼休走！我在这呢！前面那是假的！",1,"story_zf1_87","story_zf1_87","",0,},
        [6658] = {14003,7,2,"严颜",122,0,"糟糕！中计了！","糟糕！中计了！",1,"story_yy2_3","story_yy2_3","",0,},
        [6659] = {14004,1,1,"张飞",206,0,"大将到此，为何不降，而敢拒敌？","大将到此，为何不降，而敢拒敌？",1,"story_zf1_88","story_zf1_88","严颜",122,},
        [6660] = {14004,2,2,"严颜",122,0,"汝等无义！侵我州郡！只有断头将军，没有投降将军！","汝等无义！侵我州郡！只有断头将军，没有投降将军！",1,"story_yy2_4","story_yy2_4","张飞",206,},
        [6661] = {14004,3,2,"严颜",122,0,"贼匹夫，要砍头你就砍！","贼匹夫，要砍头你就砍！",1,"story_yy2_5","story_yy2_5","张飞",206,},
        [6662] = {14004,4,1,"张飞",206,0,"（亲自给严颜松绑，穿好衣服）刚才言语冒犯，请勿责怪。吾素知老将军乃豪杰之士也。","（亲自给严颜松绑，穿好衣服）刚才言语冒犯，请勿责怪。吾素知老将军乃豪杰之士也。",1,"story_zf1_89","story_zf1_89","严颜",122,},
        [6663] = {14004,5,2,"严颜",122,0,"（被张飞恩义感动，投降）败军之将，荷蒙厚恩，无可以报，愿施犬马之劳。","（被张飞恩义感动，投降）败军之将，荷蒙厚恩，无可以报，愿施犬马之劳。",1,"story_yy2_6","story_yy2_6","张飞",206,},
        [6664] = {45,1,1,"张星彩",216,0,"太好啦主公！又有新伙伴加入呢！一起去看看吧~","太好啦主公！又有新伙伴加入呢！一起去看看吧~",1,"","","",0,},
    }
}

