        [48] = {308,"Kim Linh Đao ","Đại đao của Cam Ninh, rất đẹp đẽ quý giá, trang trí bằng hoa văn vàng.","Cam Ninh","Công thường gây sát thương thêm cho mục tiêu thiêu đốt tăng 80% ","Nộ ban đầu +1","Nộ ban đầu +1","Đầu trận tăng Ngự Giáp bằng 18% sinh lực tối đa",},
        [49] = {309,"Liệt Diệm Thuẫn ","Khiên của Tôn Kiên, rất nặng, đầu thú mặt trước như hổ lửa tấn công, có thể khiến kẻ địch bốc cháy.","Tôn Kiên","Khi bị kẻ địch ở trạng thái Thiêu Đốt tấn công, giảm 65% sát thương trực tiếp phải chịu","Nộ ban đầu +1","Nộ ban đầu +1","Đầu trận tăng Ngự Giáp bằng 18% sinh lực tối đa",},
        [50] = {310,"La Sát Luân ","Thượng Hương từ nhỏ thích múa đao múa kiếm nên Tôn Kiên đã tìm hai vòng đao này cho con gái, khi xoay sẽ rất đẹp và sắc bén, dễ dàng tiêu diệt kẻ địch.","Thượng Hương","Sau khi thi triển thêm 1 lần công thường (Công thường thêm không hồi nộ khí)","Nộ ban đầu +1","Nộ ban đầu +1","Đầu trận tăng Ngự Giáp bằng 18% sinh lực tối đa",},
        [51] = {311,"Chước Nguyệt Đao","Khi Lục Tốn vung cây kiếm lấp lánh ánh sáng này, xung quanh Lục Tốn sẽ có vô số ánh sáng kiếm, chỉ cần địch nhân sơ ý là sẽ bị tiêu diệt ngay.","Lục Tốn","Thi triển kỹ năng có 96% gây Thiêu Đốt","Nộ ban đầu +1","Nộ ban đầu +1","Đầu trận tăng Ngự Giáp bằng 18% sinh lực tối đa",},
        [52] = {312,"Tụ Bảo Bồn","Lỗ Túc đại nhân thích giúp đỡ người, cả đời chưa bao giờ đói khổ qua, bởi vì thứ trong tay ông là Tụ Bảo Bồn của thần tài, tiền tài dùng không hết.","Lỗ Túc","Đánh thường, mục tiêu bị giảm 1 nộ","Nộ ban đầu +1","Nộ ban đầu +1","Đầu trận tăng Ngự Giáp bằng 18% sinh lực tối đa",},
        [53] = {313,"Vô Cực Côn ","Kim Cô Bổng hóa thân vô số cây gậy, không ngừng chiến đấu, nhiệt huyết dâng trào như đang bốc cháy hừng hực.","Lăng Thống","Khi bị tấn công thường có 40% khiến người tấn công bị bỏng, duy trì 2 lượt","0","0","0",},
        [54] = {314,"Đoạn Hải Tiên ","Tương truyền Chu Du chính là dùng Đoạn Hải Tiên này đánh Hoàng Cái đến nỗi không xuống được giường, như vậy Tào Tháo mới tin tưởng khổ nhục kế.","Hoàng Cái","Hiệu quả trị liệu phải chịu tăng 40%","0","0","0",},
        [55] = {315,"Kim Ngọc Hốt ","Hãy sợ hãi Kim Ngọc Hốt trong tay của ta đi.","Trương Chiêu","Tỉ lệ hộ hấp thu sát thương trực tiếp của Hộ Thuẫn tăng đến 100%","0","0","0",},
        [56] = {316,"Bất Khuất Nhẫn ","Lưỡi dao như trắng sáng, sắc bén vô cùng, trên thân điêu khắc hoa văn rồng, thể hiện ý chí bất khuất của nó.","Chu Thái","Diệt 1 mục tiêu, hồi 1 nộ","0","0","0",},
        [57] = {317,"Tiêu Dao Cầm ","Cao sơn lưu thủy, cầm âm không dứt, đàn nhẹ vui tai, đàn mạnh diệt địch.","Bộ Luyện Sư","Khi thi triển kỹ năng, nếu mục tiêu đang trong trạng thái thiêu đốt thì xác suất bị choáng sẽ tăng 80%","0","0","0",},
        [58] = {318,"Kinh Thiên Cung ","Thân cung làm từ ngà voi và khảm đá quý, một cung bắn ra tiêu diệt địch dễ như trở bàn tay.","Hàn Đương","Sát thương kỹ năng gây cho mục tiêu thiêu đốt tăng 80%","0","0","0",},
