        [160] = {305,"張譲","董卓の入京は、乱世の始まりにすぎないのですよ！フハハハハッ！","董卓の入京は、乱世の始まりにすぎないのですよ！フハハハハッ！","",},
        [161] = {305,"張星彩","くっ、遅かった！張譲はもう何進を殺してしまったわ！少帝も張譲に捕まってる！","くっ、遅かった！張譲はもう何進を殺してしまったわ！少帝も張譲に捕まってる！","張譲",},
        [162] = {305,"関銀屏","張譲！帝を脅迫して何をするつもりですの?帝と名将令をこちらに渡せば、命だけは助けてさしあげますわ！","張譲！帝を脅迫して何をするつもりですの?帝と名将令をこちらに渡せば、命だけは助けてさしあげますわ！","",},
        [163] = {305,"張譲","おやおや、やる気ですか?ではまずこのハサミの切れ味をたっぷり堪能させてあげましょう。","おやおや、やる気ですか?ではまずこのハサミの切れ味をたっぷり堪能させてあげましょう。","",},
        [164] = {305,"張譲","子孫を絶たれる痛みと苦しみの味をね！","子孫を絶たれる痛みと苦しみの味をね！","",},
        [165] = {306,"張譲","情勢は決しました……あの者たちは強い。ですが、十常侍は皆、哀れな者たちなのです。この程度で引き下がるわけには……","情勢は決しました……あの者たちは強い。ですが、十常侍は皆、哀れな者たちなのです。この程度で引き下がるわけには……","",},
        [166] = {306,"献帝","張譲、やめよ！","張譲、やめよ！","張譲",},
        [167] = {306,"張譲","……ああ、これも天命でしょうか。陛下、どうかご自愛ください。では皆様、陛下を頼みます。(張譲は水に身を投げ自決した)","……ああ、これも天命でしょうか。陛下、どうかご自愛ください。では皆様、陛下を頼みます。(張譲は水に身を投げ自決した)","",},
        [168] = {306,"献帝","ともあれ、お前たちが反乱を鎮めてくれたことには礼を言う。この#名将令#は褒美として遣わそう。好きにするがよい。","ともあれ、お前たちが反乱を鎮めてくれたことには礼を言う。この#名将令#は褒美として遣わそう。好きにするがよい。","張星彩",},
        [169] = {306,"張星彩","十常侍の乱は鎮まったけど、董卓はもう都に入ったわ！面倒なことになったわね、ご主君様！","十常侍の乱は鎮まったけど、董卓はもう都に入ったわ！面倒なことになったわね、ご主君様！","関銀屏",},
        [170] = {306,"関銀屏","かつて、朝廷は董卓に命じて黄巾を討伐させました。ヤツはその時の西涼兵をそのまま引き連れ、わたしたちの名将令を奪うつもりです。","かつて、朝廷は董卓に命じて黄巾を討伐させました。ヤツはその時の西涼兵をそのまま引き連れ、わたしたちの名将令を奪うつもりです。","張星彩",},
        [171] = {306,"関銀屏","数に物を言わせていたぶろうなんて、悪党の見本のような男ですわ！","数に物を言わせていたぶろうなんて、悪党の見本のような男ですわ！","張星彩",},
        [172] = {306,"張星彩","ご主君様、曹操を探しましょう。曹操は官吏の家の生まれで、洛陽には人脈があるわ。きっと董卓に対抗する手段が見つかるはずよ。","ご主君様、曹操を探しましょう。曹操は官吏の家の生まれで、洛陽には人脈があるわ。きっと董卓に対抗する手段が見つかるはずよ。","プレイヤー",},
        [173] = {306,"関銀屏","それに、他の#名将令#の手がかりがあるかもしれないですしね。","それに、他の#名将令#の手がかりがあるかもしれないですしね。","プレイヤー",},
        [174] = {306,"主君","ああ、行こう！洛陽城で曹操を探すんだ！","ええ！洛陽城で曹操を探しましょう！","関銀屏",},
        [175] = {401,"曹操","良いところに来たな、若き英雄よ。董卓は入京以来、自らの兵を固め、朝廷の綱紀を乱し、残虐非道を働いておる。","良いところに来たな、若き英雄よ。董卓は入京以来、自らの兵を固め、朝廷の綱紀を乱し、残虐非道を働いておる。","プレイヤー",},
        [176] = {401,"曹操","董卓は袁紹を殺そうとしたが、袁紹は冀州に逃げた。わしはその袁紹から王允のところに手紙を届けるように頼まれたのだ。","董卓は袁紹を殺そうとしたが、袁紹は冀州に逃げた。わしはその袁紹から王允のところに手紙を届けるように頼まれたのだ。","プレイヤー",},
        [177] = {401,"曹操","董卓対策の相談がてら一緒にどうだ?","董卓対策の相談がてら一緒にどうだ?","プレイヤー",},
        [178] = {401,"主君","ええ、是非！私も董卓入京の件で話があって来たんです。","ええ、是非！私も董卓入京の件で話があって来たんです。","曹操",},
        [179] = {401,"王允","董卓は主君を蔑ろにし、権力を弄び、国は傾かんとしている。老骨の身ながら、自らの力不足に恥じ入るばかりだ。","董卓は主君を蔑ろにし、権力を弄び、国は傾かんとしている。老骨の身ながら、自らの力不足に恥じ入るばかりだ。","曹操",},
        [180] = {401,"曹操","嘆くな王允。不才ながらこの曹操が董卓を斬る。","嘆くな王允。不才ながらこの曹操が董卓を斬る。","王允",},
        [181] = {401,"主君","奴は罪もない人をむやみに殺している。董卓を討つなら、私も仲間に加えてください！","奴は罪もない人をむやみに殺している。董卓を討つなら、私も仲間に加えてください！","王允",},
        [182] = {401,"王允","そなたらのような熱き英雄がいるなら、大漢もまだ捨てたものではないな。だが、董卓も西涼から連れてきた手下も、虎狼のごとく凶暴だ。","そなたらのような熱き英雄がいるなら、大漢もまだ捨てたものではないな。だが、董卓も西涼から連れてきた手下も、虎狼のごとく凶暴だ。","曹操",},
        [183] = {401,"王允","半端な実力では、返り討ちに遭うぞ。","半端な実力では、返り討ちに遭うぞ。","曹操",},
        [184] = {401,"王允","そこで、だ……もし私の屋敷にいる武の達人に勝って実力を証明できれば、七星宝刀を授けよう。それで董卓を討ってくれ！","そこで、だ……もし私の屋敷にいる武の達人に勝って実力を証明できれば、七星宝刀を授けよう。それで董卓を討ってくれ！","曹操",},
        [185] = {401,"主君","わかりました、私が相手になりましょう。曹操兄は休んでいてください。","わかったわ、私が相手になる。曹操兄さんは休んでいてください。","曹操",},
        [186] = {402,"王允","ううむ、二人の実力は充分にわかった！七星宝刀はここにある。天下を頼んだぞ。","ううむ、二人の実力は充分にわかった！七星宝刀はここにある。天下を頼んだぞ。","曹操",},
        [187] = {402,"曹操","無論だ。良い知らせを待っておれ、王允！","無論だ。良い知らせを待っておれ、王允！","王允",},
        [188] = {402,"曹操","若き英雄よ、董卓の屋敷に入ったら、わしの従者のふりをして、機を見て行動するのだ。2回せきをしたら、それが始まりの合図だ！","若き英雄よ、董卓の屋敷に入ったら、わしの従者のふりをして、機を見て行動するのだ。2回せきをしたら、それが始まりの合図だ！","プレイヤー",},
        [189] = {402,"主君","わかりました。お任せを！","わかりました。お任せを！","曹操",},
        [190] = {403,"董卓","曹操よ、今日は我が屋敷に何用かな?","曹操よ、今日は我が屋敷に何用かな?","曹操",},
        [191] = {403,"曹操","よい宝刀が手に入りましてな、ぜひ恩ある将軍に差し上げたく……ゴホゴホ！","よい宝刀が手に入りましてな、ぜひ恩ある将軍に差し上げたく……ゴホゴホ！","董卓",},
        [192] = {403,"董卓","ほほう！どれどれ、見せてみろ！ふむふむ、なんと鋭い！これは良い刀だ！","ほほう！どれどれ、見せてみろ！ふむふむ、なんと鋭い！これは良い刀だ！","曹操",},
        [193] = {403,"主君","今だ……！老賊董卓、覚悟！","今よ……！老賊董卓、覚悟！","董卓",},
        [194] = {404,"董卓","うおっ?！誰か、刺客だ！呂布っ！息子の呂布はどこにおる?早く助けろ！","うおっ?！誰か、刺客だ！呂布っ！息子の呂布はどこにおる?早く助けろ！","呂布",},
        [195] = {404,"呂布","義父上っ、呂布はここに！","義父上っ、呂布はここに！","プレイヤー",},
        [196] = {404,"主君","呂布に邪魔された！?くそっ、あと一歩だったのに！","呂布に邪魔された！?くっ、あと一歩なのに！","董卓",},
        [197] = {404,"董卓","ま～～たお前か！前回は#名将令#を奪われたが、今こそ好機！ゆけ、息子よ、奴らを殺せ！","ま～～たお前か！前回は#名将令#を奪われたが、今こそ好機！ゆけ、息子よ、奴らを殺せ！","プレイヤー",},
        [198] = {404,"曹操","失敗か……逃げるぞ、若き英雄よ！！呂布は、ずば抜けた強者だ。それに、董卓の屋敷内には人が多い。不利な戦いは避けるのだ！","失敗か……逃げるぞ、若き英雄よ！！呂布は、ずば抜けた強者だ。それに、董卓の屋敷内には人が多い。不利な戦いは避けるのだ！","プレイヤー",},
        [199] = {404,"曹操","(若者と曹操は馬で城を出て、東南へ走り去った)","(若者と曹操は馬で城を出て、東南へ走り去った)","プレイヤー",},
        [200] = {405,"陳宮","私は中牟県県令の陳宮。そちらの二人は何者か。","私は中牟県県令の陳宮。そちらの二人は何者か。","曹操",},
        [201] = {405,"曹操","行商人の皇甫と申します。以前、お世話になりました。","行商人の皇甫と申します。以前、お世話になりました。","陳宮",},
        [202] = {405,"陳宮","そのような嘘では騙されませんよ。かつて洛陽で官職を求めていた時、あなたが曹操と名乗ったのを聞いています！","そのような嘘では騙されませんよ。かつて洛陽で官職を求めていた時、あなたが曹操と名乗ったのを聞いています！","曹操",},
        [203] = {405,"陳宮","それにしても暗殺とは、不義理な企てをしたものですね。董卓殿には散々世話になったはずなのに。","それにしても暗殺とは、不義理な企てをしたものですね。董卓殿には散々世話になったはずなのに。","曹操",},
        [204] = {405,"陳宮","誰か、この二人を牢へ入れておけ。明日、都へ連れて行き、褒美をもらうとしよう。","誰か、この二人を牢へ入れておけ。明日、都へ連れて行き、褒美をもらうとしよう。","曹操",},
        [205] = {405,"曹操","燕雀安くんぞ鴻鵠の志を知らんや！見破られたからには、戦うしかあるまい！","燕雀安くんぞ鴻鵠の志を知らんや！見破られたからには、戦うしかあるまい！","陳宮",},
        [206] = {406,"陳宮","失礼、先ほどは少し探りを入れさせていただきました。董卓は帝をないがしろにし、権力を弄ぶ賊臣。","失礼、先ほどは少し探りを入れさせていただきました。董卓は帝をないがしろにし、権力を弄ぶ賊臣。","曹操",},
        [207] = {406,"陳宮","私も漢王室の禄を食む身です。国に報いなければ、畜生にも劣りましょう。","私も漢王室の禄を食む身です。国に報いなければ、畜生にも劣りましょう。","曹操",},
        [208] = {406,"陳宮","お二人は真に天下の忠義の士！この陳宮、官職を捨ててお二人に付き従いましょう。","お二人は真に天下の忠義の士！この陳宮、官職を捨ててお二人に付き従いましょう。","曹操",},
        [209] = {406,"曹操","なんと幸先の良いことか！若き英雄に出会っただけでなく、今また陳宮の助けを得るとは！","なんと幸先の良いことか！若き英雄に出会っただけでなく、今また陳宮の助けを得るとは！","陳宮",},
        [210] = {406,"曹操","さあさあ、今日は心行くまで飲むぞ、飲むからには三百杯だ！","さあさあ、今日は心行くまで飲むぞ、飲むからには三百杯だ！","陳宮",},
        [211] = {406,"曹操","知己に逢うては千杯でも少ないと言うが、今宵は痛快だ。よし、飲んだら次は歌だ。人生は短いぞ！","知己に逢うては千杯でも少ないと言うが、今宵は痛快だ。よし、飲んだら次は歌だ。人生は短いぞ！","陳宮",},
        [212] = {406,"主君","歌もいいですが……ひとつ腕試しといきませんか?せっかくの素晴らしい時間を無駄にしないために！","歌もいいですが……ひとつ腕試しといきません?せっかくの素晴らしい時間を無駄にしないために！","曹操",},
        [213] = {406,"曹操","ふむ、そうくるか！　面白い。では一勝負願おうか！","ふむ、そうくるか！　面白い。では一勝負願おうか！","プレイヤー",},
        [214] = {407,"曹操","ふん、やはり大したものだな、若き英雄よ。この曹操、恐れ入った！","ふん、やはり大したものだな、若き英雄よ。この曹操、恐れ入った！","プレイヤー",},
        [215] = {407,"主君","いえいえ、褒めすぎですよ。私はただ、三国最強の武将になりたいだけです！","いえいえ、褒めすぎですよ。私はただ、三国最強の武将になりたいだけ！","",},
        [216] = {407,"曹操","ふむ、結構な大志ではないか。ならば、この#名将令#はお前にやろう。一日も早く願いを叶えよ、若き英雄よ！","ふむ、結構な大志ではないか。ならば、この#名将令#はお前にやろう。一日も早く願いを叶えよ、若き英雄よ！","",},
        [217] = {407,"主君","ありがとうございます！曹操兄は、これからどうするつもりですか?","ありがとうございます！曹操兄さんは、これからどうするつもりですか?","",},
        [218] = {407,"曹操","うむ。ひとまず陳留に帰って兵馬を集め、董卓討伐の兵を挙げるよう天下の諸侯に号令する！","うむ。ひとまず陳留に帰って兵馬を集め、董卓討伐の兵を挙げるよう天下の諸侯に号令する！","",},
        [219] = {407,"主君","私も仲間に加えてください。董卓の首は必ず取ります！","私も仲間に加えてください。董卓の首は必ず取ります！","",},
        [220] = {407,"曹操","それは心強い。若き英雄なら、大事は成るであろうな。","それは心強い。若き英雄なら、大事は成るであろうな。","",},
        [221] = {501,"張星彩","ご主君様と曹操が董卓討伐の檄文を飛ばして以来、方々の諸侯が次々と呼応して兵を挙げたわ。ご主君様、スゴい！","ご主君様と曹操が董卓討伐の檄文を飛ばして以来、方々の諸侯が次々と呼応して兵を挙げたわ。ご主君様、スゴい！","プレイヤー",},
        [222] = {501,"主君","幽州の公孫瓚も来てくれた。兄者達が身を寄せているはずだが、一緒に来ているのかどうか……","幽州の公孫瓚も来てくれた。兄さんたちが身を寄せているはずだが、一緒に来ているのかどうか……","",},
        [223] = {501,"劉備","無論、来ているぞ。義弟の危機に、駆けつけぬはずがない。","無論、来ているぞ。義妹の危機に、駆けつけぬはずがない。","",},
        [224] = {501,"主君","兄者！やっぱり来てくれたんですね！","兄さんたち！やっぱり来てくれたんですね！","",},
        [225] = {501,"袁術","フン、無名の輩どもが。格好だけは立派だな！","フン、無名の輩どもが。格好だけは立派だな！","",},
        [226] = {501,"曹操","袁術殿、やめよ！この若き英雄は人中の龍鳳であるぞ！","袁術殿、やめよ！この若き英雄は人中の龍鳳であるぞ！","",},
        [227] = {501,"曹操","この場の諸侯は董卓討伐のために集まったのだ。つまらぬ争いで大事を誤るな！","この場の諸侯は董卓討伐のために集まったのだ。つまらぬ争いで大事を誤るな！","",},
        [228] = {501,"主君","しかし、互いに不満があるままでは士気も乱れる。どうでしょう?　実際に腕前を競ってみるというのは……","しかし、互いに不満があるままでは士気も乱れます。どうでしょう?　実際に腕前を競ってみるというのは……","",},
        [229] = {501,"袁紹","そうか！最後まで勝ち残った者が、諸侯の盟主となるのだな！この袁紹、#名将令#を譲ってもよいぞ！","そうか！最後まで勝ち残った者が、諸侯の盟主となるのだな！この袁紹、#名将令#を譲ってもよいぞ！","",},
        [230] = {501,"主君","では、勝負開始です。#名将令#はいただきますよ！","なら、勝負開始ね。#名将令#はいただくわ！","",},
        [231] = {502,"劉備","義弟よ、こちらは北平太守の公孫瓚。私の昔の学友だ。","義妹よ、こちらは北平太守の公孫瓚。私の昔の学友だ。","プレイヤー",},
        [232] = {502,"主君","白馬将軍と名高い公孫瓚殿ですね。初めまして。","白馬将軍と名高い公孫瓚殿ですね。初めまして。","",},
        [233] = {502,"公孫瓚","ふむ、劉備の義弟か……ちょうどいい、少し腕試しでもどうだ?","ふむ、劉備の義妹か……ちょうどいい、少し腕試しでもどうだ?","",},
        [234] = {503,"袁術","汝らは何者か。オレは四世三公の名門・袁家の嫡子、袁術である！","汝らは何者か。オレは四世三公の名門・袁家の嫡子、袁術である！","劉備",},
        [235] = {503,"劉備","私は漢王室が一族、中山靖王の末裔、姓は劉、名は備、字は玄徳なり。","私は漢王室が一族、中山靖王の末裔、姓は劉、名は備、字は玄徳なり。","",},
        [236] = {503,"袁術","フン、大漢も堕ちたものよな。一介のわらじ売りに過ぎぬ賤民が、漢王室の一族を名乗るとは！","フン、大漢も堕ちたものよな。一介のわらじ売りに過ぎぬ賤民が、漢王室の一族を名乗るとは！","",},
        [237] = {503,"主君","袁術殿、兄者にいわれのない非難はやめてもらいたい！","袁術殿、兄さんにいわれのない非難はやめてください！","",},
        [238] = {503,"関銀屏","そうですわ、うちのご主君様のわらじ持ちにもふさわしくないくせに！","そうですわ、うちのご主君様のわらじ持ちにもふさわしくないくせに！","",},
        [239] = {503,"張星彩","ぶっ飛ばして分からせるしかないわよね。ご主君様、やっちゃいましょ！","ぶっ飛ばして分からせるしかないわよね。ご主君様、やっちゃいましょ！","",},
        [240] = {504,"袁紹","参った、降参だ！わしはこの若き英雄を盟主に推挙する！この#名将令#は盟主殿が受け取ってくれ。","参った、降参だ！わしはこの若き英雄を盟主に推挙する！この#名将令#は盟主殿が受け取ってくれ。","プレイヤー",},
        [241] = {504,"主君","#名将令#はありがたく頂戴します。しかし、十八路諸侯の盟主の座に興味はありません。袁紹殿にお譲りします！","#名将令#はありがたく頂戴しますわ。しかし、十八路諸侯の盟主の座に興味はありません。袁紹殿にお譲りします！","",},
        [242] = {504,"劉備","こら、義弟よ。なにを勝手に……","こら、義妹よ。なにを勝手に……","",},
        [243] = {504,"袁術","もう遅いわ、ハハハッ！盟主の座は、結局我ら袁家のものとなったな！","もう遅いわ、ハハハッ！盟主の座は、結局我ら袁家のものとなったな！","",},
        [244] = {504,"関銀屏","ずいぶん得意げですわねえ。あなた自身が座るわけでもないのに。","ずいぶん得意げですわねえ。あなた自身が座るわけでもないのに。","",},
        [245] = {504,"袁術","なにをっ！このっ……(袁術は怒りのあまり言葉も出ない)","なにをっ！このっ……(袁術は怒りのあまり言葉も出ない)","",},
        [246] = {504,"張星彩","ご主君様、華雄とかいう敗将がまた挑戦しに来たわ！呂布も関所を守っているみたい！","ご主君様、華雄とかいう敗将がまた挑戦しに来たわ！呂布も関所を守っているみたい！","プレイヤー",},
        [247] = {504,"主君","華雄は恐るるに足らない！虎牢関へ行くぞ。三国一の戦神、呂布に会いに！","華雄は恐るるに足らないわ！虎牢関へ行きましょう。三国一の戦神、呂布に会いに！","",},
        [248] = {601,"呂布","俺の手には赤兔馬と方天画戟、そして#名将令#がある。もはや天下に敵はいない！","俺の手には赤兔馬と方天画戟、そして#名将令#がある。もはや天下に敵はいない！","プレイヤー",},
        [249] = {601,"主君","敵ならばここにいるぞ。私が倒してやる！","敵ならここにいるわよ。私が倒してやる！","",},
        [250] = {601,"呂布","貴様か。あの大耳野郎に加担して、俺の#名将令#を奪った……","貴様か。あの大耳野郎に加担して、俺の#名将令#を奪った……","",},
        [251] = {601,"主君","ああそうだ、怖いだろう！早く#名将令#を寄こすんだ。","そうよ！さぞかし怖いでしょう！早く#名将令#を寄こしなさい。","",},
        [252] = {601,"呂布","馬鹿め。やれるものならやってみろ！","馬鹿め。やれるものならやってみろ！","",},
        [253] = {602,"李傕","あの若大将、実に堂々としとるのぉ……ときに、董卓様にゃあ娘がおるが、どうじゃろう。お似合いじゃと思わんか?","あの若大将、実に堂々としとるのぉ……ときに、董卓様にゃあ息子がおるが、どうじゃろう。お似合いじゃと思わんか?","関銀屏",},
        [254] = {602,"関銀屏","オホホホホ、世迷い言も大概になさい！董卓は天に逆らう不届き者、ご主君様の親戚になるなど、百万年早いですわ！","オホホホホ、世迷い言も大概になさい！董卓は天に逆らう不届き者、ご主君様の親戚になるなど、百万年早いですわ！","",},
        [255] = {602,"張星彩","それに、美男子のご主君様と董卓の娘じゃ、ぜんっぜん釣り合わないわ！","それに、美少女のご主君様と董卓の息子じゃ、ぜんっぜん釣り合わないわ！","",},
        [256] = {603,"華雄","潘鳳とやらは斬ってやったぜ！次は誰だ、お前か～！?","潘鳳とやらは斬ってやったぜ！次は誰だ、お前か～！?","関羽",},
        [257] = {603,"関羽","身の程を知らぬようだな、華雄！","身の程を知らぬようだな、華雄！","",},
        [258] = {603,"主君","華雄は一度私に負けた者。兄者の相手じゃありませんよ。酒でも飲んで待っててください。","華雄は一度私に負けたのよ。兄さんの相手じゃないわ。酒でも飲んで待っていて！","",},
        [259] = {604,"関羽","また腕を上げたようだな、義弟よ。兄として誇らしいぞ。華雄、破れたり！","また腕を上げたようだな、義妹よ。兄として誇らしいぞ。華雄、破れたり！","劉備",},
        [260] = {604,"関羽","しかし、もどかしいものだな。まだ関所へ攻め込んではならぬとは。","しかし、もどかしいものだな。まだ関所へ攻め込んではならぬとは。","",},
        [261] = {604,"劉備","関羽よ、少し落ち着け。呂布は勇猛無敵、まず奴を捕らえれば、董卓を倒すのも容易いだろう。","関羽よ、少し落ち着け。呂布は勇猛無敵、まず奴を捕らえれば、董卓を倒すのも容易いだろう。","",},
        [262] = {604,"劉備","義弟よ。お前と呂布との戦い、我らも存分に力を貸そう。","義妹よ。お前と呂布との戦い、我らも存分に力を貸そう。","",},
        [263] = {604,"呂布","呂布ここにあり！我が義父に刃を向けるのは誰か！","呂布ここにあり！我が義父に刃を向けるのは誰か！","",},
        [264] = {604,"張飛","燕人・張飛ここにあり！覚悟しやがれ、呂布！","燕人・張飛ここにあり！覚悟しやがれ、呂布！","",},
        [265] = {604,"呂布","なんの！俺は戦神呂布！貴様らでは天地がひっくり返っても勝てぬわ！もし負けたら、この#名将令#をくれてやる！","なんの！俺は戦神呂布！貴様らでは天地がひっくり返っても勝てぬわ！もし負けたら、この#名将令#をくれてやる！","",},
        [266] = {604,"主君","言ったな！#名将令#は必ず手に入れる！行くぞ！","言ったわね！#名将令#は必ず手に入れる！行くわよ！","",},
        [267] = {605,"呂布","うおっ、貂蝉！?なぜか今、貂蝉の顔が頭に浮かんだぞ！ええい、戦などやめだやめだ！","うおっ、貂蝉！?なぜか今、貂蝉の顔が頭に浮かんだぞ！ええい、戦などやめだやめだ！","張飛",},
        [268] = {605,"呂布","#名将令#ならくれてやる！貂蝉よ、待っていろ！(呂布の馬は足が速く、一目散に逃げ去った)","#名将令#ならくれてやる！貂蝉よ、待っていろ！(呂布の馬は足が速く、一目散に逃げ去った)","張飛",},
        [269] = {605,"張飛","戦より女が大事だってのか！?待ちやがれ！","戦より女が大事だってのか！?待ちやがれ！","",},
        [270] = {605,"劉備","張飛よ、逃げる敵は追うな！","張飛よ、逃げる敵は追うな！","",},
        [271] = {605,"劉備","義弟よ、虎牢関は落ちた。我らは公孫瓚と共に幽州へ帰る。また会おう！","義妹よ、虎牢関は落ちた。我らは公孫瓚と共に幽州へ帰る。また会おう！","プレイヤー",},
        [272] = {605,"主君","はい、また会いましょう！","ええ、また会いましょう！","",},
        [273] = {605,"張星彩","また#名将令#を手に入れたわね！ご主君様、おめでとう！それで、今度はどこへ行くの?","また#名将令#を手に入れたわね！ご主君様、おめでとう！それで、今度はどこへ行くの?","",},
        [274] = {605,"主君","賊を大人しくさせるなら、頭を押さえるしかない。洛陽へ行って董卓を倒し、禍根を絶つぞ！","賊を大人しくさせるなら、頭を押さえるしかないわ。洛陽へ行って董卓を倒し、禍根を絶ちましょう！","",},
        [275] = {701,"曹操","密偵から報告があった。董卓は諸侯の勢いを恐れるあまり、洛陽を焼き、天子を強奪して長安へ向かうつもりのようだ。","密偵から報告があった。董卓は諸侯の勢いを恐れるあまり、洛陽を焼き、天子を強奪して長安へ向かうつもりのようだ。","プレイヤー",},
        [276] = {701,"曹操","諸侯よ、どうする?","諸侯よ、どうする?","プレイヤー",},
        [277] = {701,"主君","今こそ好機！このまま勢いに乗じて洛陽を攻め、董卓を生け捕りにし、天下の趨勢を決するべきだ！","今こそ好機よ！このまま勢いに乗じて洛陽を攻め、董卓を生け捕りにし、天下の趨勢を決するべきよ！","",},
        [278] = {701,"袁紹","しかし、兵たちは疲れきっているし、董卓には手勢も多い。無理に追えば返り討ちに遭うのではないか……","しかし、兵たちは疲れきっているし、董卓には手勢も多い。無理に追えば返り討ちに遭うのではないか……","",},
        [279] = {701,"曹操","ふん、青二才め！共に謀る価値もないわ！","ふん、青二才め！共に謀る価値もないわ！","",},
        [280] = {701,"主君","袁紹はやっぱり大事を成す器ではないようだな……","袁紹はやっぱり大事を成す器ではないようね……","",},
        [281] = {701,"曹操","だが、方々の諸侯は袁紹と同意見であろう。お前だけが真の豪傑だ。知り合えたことを今日ほど幸運に思ったことはないぞ。","だが、方々の諸侯は袁紹と同意見であろう。お前だけが真の豪傑だ。知り合えたことを今日ほど幸運に思ったことはないぞ。","",},
        [282] = {701,"曹操","江東の孫堅はすでに兵を率いて洛陽城内に向かった。","江東の孫堅はすでに兵を率いて洛陽城内に向かった。","",},
        [283] = {701,"曹操","若き英雄よ、我らは兵を分けて、わしは董卓を追い、お前は孫堅を助けるというのはどうだ?","若き英雄よ、我らは兵を分けて、わしは董卓を追い、お前は孫堅を助けるというのはどうだ?","",},
        [284] = {701,"主君","いいでしょう、ではここでお別れです。私は洛陽へ行き、孫堅殿の手助けをします！","いいでしょう、ではここでお別れね。私は洛陽へ行き、孫堅殿の手助けをするわ！","",},
        [285] = {702,"黄蓋","何奴じゃ！?なぜ洛陽城内を逃げ回っている?","何奴じゃ！?なぜ洛陽城内を逃げ回っている?","張星彩",},
        [286] = {702,"張星彩","わたしたちは孫堅さんを助けて、董卓の残党を討伐するために来た助っ人よ！","わたしたちは孫堅さんを助けて、董卓の残党を討伐するために来た助っ人よ！","",},
        [287] = {702,"黄蓋","お前らのような怪しい助っ人がいるか！さては、董卓が寄越した密偵じゃな！?","お前らのような怪しい助っ人がいるか！さては、董卓が寄越した密偵じゃな！?","",},
        [288] = {702,"張星彩","なんでよ！どう見ても正義の使者じゃない！","なんでよ！どう見ても正義の使者じゃない！","",},
        [289] = {702,"関銀屏","ぶん殴れば正気に戻るかもですわね！","ぶん殴れば正気に戻るかもですわね！","",},
        [290] = {703,"張星彩","ご主君様、孫堅は元々袁術の部下だし、袁術や袁紹とは同じ穴のムジナかもよ。ちょっと試してやりましょ！","ご主君様、孫堅は元々袁術の部下だし、袁術や袁紹とは同じ穴のムジナかもよ。ちょっと試してやりましょ！","関銀屏",},
        [291] = {703,"関銀屏","星彩ったら、また大人をからかいますの?","星彩ったら、また大人をからかいますの?","",},
        [292] = {703,"張星彩","へへん！","へへん！","",},
        [293] = {703,"張星彩","孫堅将軍、董丞相の娘さんって、とっても美人なのよ。将軍の息子さんとお似合いなんじゃないかなーって思うんだけど、どうかしら?","孫堅将軍、董丞相の娘さんって、とっても美人なのよ。将軍の息子さんとお似合いなんじゃないかなーって思うんだけど、どうかしら?","孫堅",},
        [294] = {703,"孫堅","馬鹿な、縁組みなどできるか！董卓は天に逆らい、王室を揺るがした大罪人だぞ。わしはヤツの一族郎党を滅ぼし天下に報いたいのだ。","馬鹿な、縁組みなどできるか！董卓は天に逆らい、王室を揺るがした大罪人だぞ。わしはヤツの一族郎党を滅ぼし天下に報いたいのだ。","",},
        [295] = {704,"主君","それくらいにしろ、星彩。孫将軍は、黄巾賊や董卓の討伐で先鋒を務めた真の英雄。漢王室への忠誠は手本にしていいくらいだ。","それくらいにしなさい、星彩。孫将軍は、黄巾や董卓の討伐で先鋒を務めた真の英雄。漢王室への忠誠は手本とすべきものよ。","孫堅",},
        [296] = {704,"孫堅","それはほめ過ぎだ、若き英雄よ。お前こそ戦功めざましく、名声は遠くまで及んでいるではないか。","それはほめ過ぎだ、若き英雄よ。お前こそ戦功めざましく、名声は遠くまで及んでいるではないか。","",},
        [297] = {704,"孫堅","この孫堅、恥じ入るばかりだ。この#名将令#は洛陽城で手に入れたものだ。受け取ってくれ。","この孫堅、恥じ入るばかりだ。この#名将令#は洛陽城で手に入れたものだ。受け取ってくれ。","",},
        [298] = {704,"主君","ありがとうございます、孫将軍！遠慮なくいただきます。","ありがとうございます、孫将軍！遠慮なくいただきますわ。","",},
        [299] = {704,"主君","それで、孫将軍はこの後どうするおつもりなのですか?","それで、孫将軍はこの後どうするおつもりなのです?","",},
        [300] = {704,"孫堅","ああ、江東に帰ることに決めたよ。十八路諸侯どもは互いにだまし合い、いがみ合っているだけだからな。","ああ、江東に帰ることに決めたよ。十八路諸侯どもは互いにだまし合い、いがみ合っているだけだからな。","",},
        [301] = {704,"主君","私も将軍と同感です。江東までご一緒してもいいですか?","私も将軍と同感ですわ。江東までご一緒してもいいですか?","",},
        [302] = {704,"孫堅","ああ、かまわん。今は袁紹が十八路諸侯の盟主だから、これから暇乞いに行こうと思う。","ああ、かまわん。今は袁紹が十八路諸侯の盟主だから、これから暇乞いに行こうと思う。","",},
        [303] = {801,"孫堅","近頃どうも体の具合がすぐれず……ついては長沙に帰りたく、お別れに参りました。","近頃どうも体の具合がすぐれず……ついては長沙に帰りたく、お別れに参りました。","袁紹",},
        [304] = {801,"袁紹","ふん、騙されんぞ。貴殿は朝廷の伝国の玉璽を隠し持っているであろう。何の下心があるのだ。","ふん、騙されんぞ。貴殿は朝廷の伝国の玉璽を隠し持っているであろう。何の下心があるのだ。","",},
        [305] = {801,"孫堅","玉璽など知らん！この孫堅、天に誓おう。もし、玉璽を隠していたなら、天寿を全うせず刀矢の下に死ぬだろう！","玉璽など知らん！この孫堅、天に誓おう。もし、玉璽を隠していたなら、天寿を全うせず刀矢の下に死ぬだろう！","",},
        [306] = {801,"袁紹","そう誓われてこのまま留め置けば、わしの度量が狭く見られるではないか！(袁紹は退去を許し、孫堅は別れを告げた)","そう誓われてこのまま留め置けば、わしの度量が狭く見られるではないか！(袁紹は退去を許し、孫堅は別れを告げた)","孫堅",},
        [307] = {801,"袁紹","誰か！急いで荊州牧の劉表に手紙を出せ。道中で孫堅を押しとどめ、玉璽を奪わせるのだ。","誰か！急いで荊州牧の劉表に手紙を出せ。道中で孫堅を押しとどめ、玉璽を奪わせるのだ。","プレイヤー",},
        [308] = {801,"張星彩","袁紹って、本当に小さな男ね。ご主君様は、孫堅が本当に玉璽を隠し持っていると思う?","袁紹って、本当に小さな男ね。ご主君様は、孫堅が本当に玉璽を隠し持っていると思う?","プレイヤー",},
        [309] = {801,"主君","どちらでも構わないさ。たとえ孫堅将軍の手にあっても、それが実力ってものなのかな……","どちらでも構わないわ。たとえ孫堅将軍の手にあっても、それが実力ってものなのかも……","",},
        [310] = {801,"主君","それに、私たちは荊州に行くんだ。玉璽が誰の手にあろうと、関係のないことだ。","それに、私たちは荊州に行く。玉璽が誰の手にあろうと、関係のないことよ。","",},
        [311] = {801,"張星彩","そうよね、さすがご主君様！そういえば、劉表の手元にも#名将令#があるって聞いたわ。せっかくだし訪ねてみたらどうかな。","そうよね、さすがご主君様！そういえば、劉表の手元にも#名将令#があるって聞いたわ。せっかくだし訪ねてみたらどうかな。","",},
        [312] = {801,"主君","そうか。グズグズしている暇はないな。荊州へ急ごう！","そう。グズグズしている暇はないわね。荊州へ急ぎましょう！","",},
        [313] = {802,"蔡瑁","これはこれは、美しいお嬢さん方！この蔡瑁の妾にならぬか?","これはこれは、美しいお嬢さん方！この蔡瑁の妾にならぬか?","張星彩",},
        [314] = {802,"張星彩","あなた誰?いきなり何言い出すの?","あなた誰?いきなり何言い出すの?","",},
        [315] = {802,"蔡瑁","わが蔡家は荊州襄陽で名高い豪族だぞ。しかも姉が漢王室の一族、劉表殿に嫁いでなあ。まさに飛ぶ鳥を落とす勢いよ！","わが蔡家は荊州襄陽で名高い豪族だぞ。しかも姉が漢王室の一族、劉表殿に嫁いでなあ。まさに飛ぶ鳥を落とす勢いよ！","",},
        [316] = {802,"張星彩","ふん、あんたの手柄でもないくせに。偉そうにはしゃいじゃって！","ふん、あんたの手柄でもないくせに。偉そうにはしゃいじゃって！","",},
        [317] = {802,"関銀屏","あまり調子に乗っていると、早死しますわよ?","あまり調子に乗っていると、早死しますわよ?","",},
        [318] = {803,"劉表","名も知らぬこわっぱどもと思っていたが、まさか蔡瑁や黄祖を打ち破るとは……その実力、侮れんな。","名も知らぬこわっぱどもと思っていたが、まさか蔡瑁や黄祖を打ち破るとは……その実力、侮れんな。","張星彩",},
        [319] = {803,"張星彩","なんでも、劉表は若いころ単身荊州に乗り込んで、各地の豪族を平定して全域を支配したんだって。スゴいね。","なんでも、劉表は若いころ単身荊州に乗り込んで、各地の豪族を平定して全域を支配したんだって。スゴいね。","",},
        [320] = {803,"関銀屏","あら、その程度、別にスゴくなんかないですわ。","あら、その程度、別にスゴくなんかないですわ。","",},
        [321] = {803,"関銀屏","うちのご主君様はやすやすと襄陽城に入ったじゃありませんか。すぐに張り倒されますわ。","うちのご主君様はやすやすと襄陽城に入ったじゃありませんか。すぐに張り倒されますわ。","",},
        [322] = {803,"劉表","誰が誰を張り倒すか知らんが、この劉表がどう凄いか、とっくりと見せてやろう！","誰が誰を張り倒すか知らんが、この劉表がどう凄いか、とっくりと見せてやろう！","",},
        [323] = {804,"劉表","ううぬ……やはり若さと勢いには勝てぬか。この劉表、甘んじて降参いたしましょう！","ううぬ……やはり若さと勢いには勝てぬか。この劉表、甘んじて降参いたしましょう！","プレイヤー",},
        [324] = {804,"主君","すっかり手のひらを返したな……しかし負けを認めたからには、#名将令#を差し出していただきましょう。","すっかり手のひらを返したわね……しかし負けを認めたからには、#名将令#を差し出していただきますわ。","",},
        [325] = {804,"劉表","#名将令#?なんのことですか。わしは持っておりませんぞ！","#名将令#?なんのことですか。わしは持っておりませんぞ！","",},
        [326] = {804,"主君","なにっ?まさか、無駄足だったとは……","えっ?まさか、無駄足だったの……","",},
        [327] = {804,"黄蓋","将軍、火急の要件じゃ！我が殿が蒯良の奸計にはまり、黄祖に誘われて城を出た。","将軍、火急の要件じゃ！我が殿が蒯良の奸計にはまり、黄祖に誘われて城を出た。","",},
        [328] = {804,"黄蓋","少数の兵で敵陣深く入り込み、極めて危険な状態じゃ！","少数の兵で敵陣深く入り込み、極めて危険な状態じゃ！","",},
        [329] = {804,"主君","落ち着いてください、黄蓋将軍。私たちが孫堅殿を助けに行きます！","落ち着いてください、黄蓋将軍。私たちが孫堅殿を助けに行きます！","",},
        [330] = {805,"主君","くそっ、遅かった！黄祖は捕らえたけど、孫堅将軍は敵の矢を浴びて、もう……","くっ、遅かった！黄祖は捕らえたけど、孫堅将軍は敵の矢を浴びて、もう……","張星彩",},
        [331] = {805,"張星彩","あの日の孫堅さんの誓いが、こんなに早く現実になっちゃうなんて……","あの日の孫堅さんの誓いが、こんなに早く現実になっちゃうなんて……","",},
        [332] = {805,"張星彩","つまり、玉璽は彼の手に……(話していると、孫策が馬に乗って駆け付けた)","つまり、玉璽は彼の手に……(話していると、孫策が馬に乗って駆け付けた)","",},
        [333] = {805,"孫策","我が父を殺したのは誰だ?お前たちか?","我が父を殺したのは誰だ?お前たちか?","",},
        [334] = {805,"主君","あなたは?","あなたは?","",},
        [335] = {805,"孫策","俺は孫堅が長子、江東の小霸王孫策！父の仇だ、お命頂戴！","俺は孫堅が長子、江東の小霸王孫策！父の仇だ、お命頂戴！","",},
        [336] = {806,"黄蓋","若、誤解ですじゃ！この若き将軍は殿を助けに来たのじゃ。殿を射殺したのは黄祖たちですじゃ。","若、誤解ですじゃ！この若き将軍は殿を助けに来たのじゃ。殿を射殺したのは黄祖たちですじゃ。","孫策",},
        [337] = {806,"孫策","うむ、そうだったのか。突然父を失った怒りと絶望で我を忘れ、誤解してしまった。どうか気を悪くしないでくれ。","うむ、そうだったのか。突然父を失った怒りと絶望で我を忘れ、誤解してしまった。どうか気を悪くしないでくれ。","",},
        [338] = {806,"主君","いえ、お察しします。せめて敵陣から、孫堅将軍の遺体を引き取りましょう。生け捕りにした黄祖の身柄と引き換えに。","いえ、お察しします。せめて敵陣から、孫堅将軍の遺体を引き取りましょう。生け捕りにした黄祖の身柄と引き換えに。","",},
        [339] = {806,"孫策","それはありがたい！この大恩にどう報いればいいか……せめてこの#名将令#を謝礼に受け取ってくれ！","それはありがたい！この大恩にどう報いればいいか……せめてこの#名将令#を謝礼に受け取ってくれ！","",},
        [340] = {806,"主君","ありがとうございます。孫策将軍は、これからどうするおつもりですか?","ありがとうございます。孫策将軍は、これからどうするおつもりですか?","",},
        [341] = {806,"孫策","父のかつての部下は、多くが袁術の幕下にある。俺もしばらく袁術を頼り、父の仇を討つため、十分に力を蓄えようと思う。","父のかつての部下は、多くが袁術の幕下にある。俺もしばらく袁術を頼り、父の仇を討つため、十分に力を蓄えようと思う。","",},
        [342] = {806,"孫策","いつかまた江東に来た時は、俺を訪ねてくれ。","いつかまた江東に来た時は、俺を訪ねてくれ。","",},
        [343] = {806,"孫策","必ず力になろう！(孫策は悲憤を抱えたまま、孫堅の遺体とともに去っていった)","必ず力になろう！(孫策は悲憤を抱えたまま、孫堅の遺体とともに去っていった)","",},
        [344] = {806,"張星彩","ご主君様、これからどうする?","ご主君様、これからどうする?","",},
        [345] = {806,"主君","あの日、董卓を追って行った曹操は、いま滎陽あたりらしい。そこへ行ってみよう！","あの日、董卓を追って行った曹操は、いま滎陽あたりらしいわ。そこへ行ってみましょう！","",},
        [346] = {901,"主君","夏侯将軍、曹操殿はどこです?","夏侯将軍、曹操殿はどこに?","夏侯惇",},
        [347] = {901,"夏侯惇","長安へ逃げた董卓を追って、途中の滎陽で戦っておるっ！","長安へ逃げた董卓を追って、途中の滎陽で戦っておるっ！","プレイヤー",},
        [348] = {901,"主君","現在の戦况は?","現在の戦况は?","夏侯惇",},
        [349] = {901,"夏侯惇","滎陽太守の徐栄が董卓を手引きし、呂布が精鋭を率いて殿軍を務めている！","滎陽太守の徐栄が董卓を手引きし、呂布が精鋭を率いて殿軍を務めている！","プレイヤー",},
        [350] = {901,"夏侯惇","李儒の献策で伏兵があるようだが、殿の本軍は勢いに任せて突っ込んでいった！","李儒の献策で伏兵があるようだが、殿の本軍は勢いに任せて突っ込んでいった！","プレイヤー",},
        [351] = {901,"主君","危険な状況ですね……早急に助けに向かいます！","危険な状況ですね……早急に助けに向かわないと！","夏侯惇",},
        [352] = {902,"張星彩","ご主君様、まずいよ！曹操は滎陽の城外で待ち伏せに遭って、董卓の大軍に挟み撃ちにされちゃった！","ご主君様、まずいよ！曹操は滎陽の城外で待ち伏せに遭って、董卓の大軍に挟み撃ちにされちゃった！","プレイヤー",},
        [353] = {902,"主君","大丈夫だ、星彩。私に任せろ！","大丈夫よ、星彩。私に任せて！","張星彩",},
        [354] = {902,"張星彩","ウン、ご主君様を信じるわ。前にいるのは曹操軍の夏侯淵みたい。詳しい状況を聞きに行きましょう。","ウン、ご主君様を信じるわ。前にいるのは曹操軍の夏侯淵みたい。詳しい状況を聞きに行きましょう。","プレイヤー",},
        [355] = {902,"夏侯淵","クッ、また追手か！殿、行ってください。しんがりは、この夏侯淵が！","クッ、また追手か！殿、行ってください。しんがりは、この夏侯淵が！","プレイヤー",},
        [356] = {902,"夏侯淵","何者だ、お前たち！?","何者だ、お前たち！?","プレイヤー",},
        [357] = {902,"関銀屏","あらあら、我が御主君様をご存知ありませんの?なんて物知らずな！","あらあら、我が御主君様をご存知ありませんの?なんて物知らずな！","夏侯淵",},
        [358] = {903,"張星彩","曹操さん、あなた肩に矢傷があるじゃない！わたしが治してあげる。","曹操さん、あなた肩に矢傷があるじゃない！わたしが治してあげる。","曹操",},
        [359] = {903,"曹操","うぅぅぅ……誰だ、わしを殺そうとするのは……?","うぅぅぅ……誰だ、わしを殺そうとするのは……?","張星彩",},
        [360] = {903,"張星彩","曹操は意識が朦朧としてるみたい。きっと傷口から感染して高熱がでてるのね","曹操は意識が朦朧としてるみたい。きっと傷口から感染して高熱がでてるのね","曹操",},
        [361] = {903,"関銀屏","猜疑心が強い曹操ですから、ご主君様の事も分からないみたいですわね。","猜疑心が強い曹操ですから、ご主君様の事も分からないみたいですわね。","曹操",},
        [362] = {903,"主君","無理もない。連戦で疲れ果てたところに、この怪我だ。悪いけど治療するため、少し気を失ってもらおう。","無理もないわ。連戦で疲れ果てたところに、この怪我だもの。悪いけど治療のため、少し気を失ってもらうわ。","曹操",},
        [363] = {904,"張星彩","気分はどう?曹操さん。","気分はどう?曹操さん。","曹操",},
        [364] = {904,"曹操","おお……若き英雄よ、助力に感謝する！星彩の治療の腕前にもな。しかし、その見事な医術、一体誰に学んだのだ?","おお……若き英雄よ、助力に感謝する！星彩の治療の腕前にもな。しかし、その見事な医術、一体誰に学んだのだ?","張星彩",},
        [365] = {904,"張星彩","わたしの師匠は神医と名高い華佗先生よ。あと左慈っていう道士が、「天命の人」を全力で輔佐するようにって、薬袋をくれたの。","わたしの師匠は神医と名高い華佗先生よ。あと左慈っていう道士が、「天命の人」を全力で輔佐するようにって、薬袋をくれたの。","曹操",},
        [366] = {904,"曹操","ふむ、星彩の医術にはそのような由来があったのか。しかし、天命の人とは?","ふむ、星彩の医術にはそのような由来があったのか。しかし、天命の人とは?","張星彩",},
        [367] = {904,"張星彩","うちのご主君様に決まってるじゃない！この乱世を終わらせる天命を受けた人なんだから！","うちのご主君様に決まってるじゃない！この乱世を終わらせる天命を受けた人なんだから！","曹操",},
        [368] = {904,"張星彩","ご主君様、この星彩が一生おそばにいて、永遠にお守りするからね！","ご主君様、この星彩が一生おそばにいて、永遠にお守りするからね！","曹操",},
        [369] = {904,"関銀屏","ご主君様、わたしも！ケンカならぞんぶんに手を貸して差し上げますわ！","ご主君様、わたしも！ケンカならぞんぶんに手を貸して差し上げますわ！","張星彩",},
        [370] = {904,"主君","ふたりとも、ありがとう。心強いぞ！","ふたりとも、ありがとう。心強いわよ！","関銀屏",},
        [371] = {904,"曹操","ふむ、大したものだ。若き英雄よ、お前の配下は実に優れ者ぞろいだな。","ふむ、大したものだ。若き英雄よ、お前の配下は実に優れ者ぞろいだな。","プレイヤー",},
        [372] = {905,"夏侯惇","無事かぁっ！夏侯惇、馬を飛ばして助太刀に来たぞ！曹仁、李典、楽進もおるぞぉっ！","無事かぁっ！夏侯惇、馬を飛ばして助太刀に来たぞ！曹仁、李典、楽進もおるぞぉっ！","曹操",},
        [373] = {905,"曹操","よくぞ来てくれた。滎陽太守の徐栄に待ち伏せされ、若き英雄の助けがなければ危ういところだったのだ。","よくぞ来てくれた。滎陽太守の徐栄に待ち伏せされ、若き英雄の助けがなければ危ういところだったのだ。","夏侯惇",},
        [374] = {905,"夏侯惇","そうか、若き将軍か！ならば、折り入って頼みがあるっ！","そうか、若き将軍か！ならば、折り入って頼みがあるっ！","プレイヤー",},
        [375] = {905,"主君","え、何でしょう?","え、何でしょう?","夏侯惇",},
        [376] = {905,"夏侯惇","聞けば、将軍は虎牢関で呂布と戦い、十八路諸侯を震撼させ、一騎当千の強者と、その名は天下に鳴り響いておるとか。","聞けば、将軍は虎牢関で呂布と戦い、十八路諸侯を震撼させ、一騎当千の強者と、その名は天下に鳴り響いておるとか。","プレイヤー",},
        [377] = {905,"夏侯惇","この夏侯惇と一勝負してもらえんかぁっ！","この夏侯惇と一勝負してもらえんかぁっ！","プレイヤー",},
        [378] = {905,"主君","わかりました。絶え間ない戦いの中で腕を磨くということですね。夏侯惇将軍、いざ！","わかりました。絶え間ない戦いの中で腕を磨くということですね。夏侯惇将軍、いざ！","夏侯惇",},
        [379] = {906,"夏侯惇","うはははは！将軍は噂以上の強者っ！この夏侯惇、恐れ入ったぁ！この名将令は将軍が持つべきだな！","うはははは！将軍は噂以上の強者っ！この夏侯惇、恐れ入ったぁ！この名将令は将軍が持つべきだな！","プレイヤー",},
        [380] = {906,"主君","ありがとう。夏侯惇将軍も、噂以上の豪傑ですね！","ありがとう。夏侯惇将軍も、噂以上の豪傑ですね！","曹操",},
        [381] = {906,"曹操","あの董卓は天子を欺き、民衆を虐げ、眉城を築き、罪なき人々を虐殺している。もはや年貢の納め時、人も神も怒り心頭だ！","あの董卓は天子を欺き、民衆を虐げ、眉城を築き、罪なき人々を虐殺している。もはや年貢の納め時、人も神も怒り心頭だ！","プレイヤー",},
        [382] = {906,"曹操","残念ながら、滎陽の戦いはこの曹操の惨敗だった。あれさえなければ、今ごろ董卓めの首を討ち取っていたはずだ！","残念ながら、滎陽の戦いはこの曹操の惨敗だった。あれさえなければ、今ごろ董卓めの首を討ち取っていたはずだ！","プレイヤー",},
        [383] = {906,"主君","曹操兄、安心してください。私が長安へ向かい、董卓を討ちます！","曹操兄さん、安心してください。私が長安へ向かい、董卓を討ちます！","曹操",},
        [384] = {906,"曹操","やはり、若き英雄は真の豪傑よ！頼んだぞ！わしは河内に戻り再起を図る。またいつか会おう。","やはり、若き英雄は真の豪傑よ！頼んだぞ！わしは河内に戻り再起を図る。またいつか会おう。","プレイヤー",},
        [385] = {906,"関銀屏","あの豚めの命も長くはありませんわ！参りましょう、長安に行って董卓を倒すのです！","あの豚めの命も長くはありませんわ！参りましょう、長安に行って董卓を倒すのです！","張星彩",},
        [386] = {906,"張星彩","焦っちゃダメだよ！前回の暗殺が失敗したのは、董卓の手勢が多かったからよ。","焦っちゃダメだよ！前回の暗殺が失敗したのは、董卓の手勢が多かったからよ。","プレイヤー",},
        [387] = {906,"張星彩","ご主君様、まずは長安城に忍び込んで、王允に状況を聞くのはどうかしら?","ご主君様、まずは長安城に忍び込んで、王允に状況を聞くのはどうかしら?","プレイヤー",},
        [388] = {906,"主君","そうだな、星彩の言う通りだ！長安で王允殿に会おう！","そうね、星彩の言う通りよ！長安で王允殿に会いましょう！","張星彩",},
        [389] = {1001,"張星彩","ひどい董卓、洛陽を焼き払って、みんなを強制的に長安へ移住させるなんて。あ、ご主君様、前にあるのが王允の屋敷みたい。","ひどい董卓、洛陽を焼き払って、みんなを強制的に長安へ移住させるなんて。あ、ご主君様、前にあるのが王允の屋敷みたい。","関銀屏",},
        [390] = {1001,"関銀屏","お待ちになって！あそこにいるのは呂布ですわ！王允に会って、何をしようというのかしら?","お待ちになって！あそこにいるのは呂布ですわ！王允に会って、何をしようというのかしら?","張星彩",},
        [391] = {1001,"呂布","王允っ！貴様、俺と貂蝉の仲を裂くつもりかっ！?なぜ董卓に与えたっ！?","王允っ！貴様、俺と貂蝉の仲を裂くつもりかっ！?なぜ董卓に与えたっ！?","王允",},
        [392] = {1001,"王允","ひえっ、それは勘違いにございます！","ひえっ、それは勘違いにございます！","呂布",},
        [393] = {1001,"王允","昨日、董卓さまが我が屋敷を訪れ、縁起の良い日に貂蝉を将軍に与えたいとおっしゃったので、行かせたまででして！","昨日、董卓さまが我が屋敷を訪れ、縁起の良い日に貂蝉を将軍に与えたいとおっしゃったので、行かせたまででして！","呂布",},
        [394] = {1001,"王允","それに、よくお考えください。董卓さま自らお越しになったなら、この老いぼれにどうして止められましょう?","それに、よくお考えください。董卓さま自らお越しになったなら、この老いぼれにどうして止められましょう?","呂布",},
        [395] = {1001,"呂布","そうか……誤解していたようだな。悪く思わないでくれ、王允殿。この詫びは、いずれ！(呂布は急いで董卓の屋敷に帰っていった)","そうか……誤解していたようだな。悪く思わないでくれ、王允殿。この詫びは、いずれ！(呂布は急いで董卓の屋敷に帰っていった)","王允",},
        [396] = {1001,"関銀屏","あらあら、結構な修羅場ですわね。","あらあら、結構な修羅場ですわね。","張星彩",},
        [397] = {1001,"張星彩","貂蝉さんもお気の毒に。あのスケベ豚のことだもの、きっと今頃……","貂蝉さんもお気の毒に。あのスケベ豚のことだもの、きっと今頃……","関銀屏",},
        [398] = {1001,"主君","王允殿に話を聞こう。貂蝉に辛い思いをさせるわけにはいかない！","王允殿に話を聞きましょう。貂蝉に辛い思いをさせるわけにはいかないわ！","関銀屏",},
        [399] = {1002,"主君","王允殿！なぜ貂蝉を董卓に与えたのです?","王允殿！なぜ貂蝉を董卓に与えたのです?","王允",},
        [400] = {1002,"王允","民衆は苦しみの極みにあり、君臣も危険な状態にある。そんな中で、賊臣董卓は皇帝の座を狙っている。","民衆は苦しみの極みにあり、君臣も危険な状態にある。そんな中で、賊臣董卓は皇帝の座を狙っている。","プレイヤー",},
        [401] = {1002,"王允","私は美女連環の計を用いて、貂蝉に董卓と呂布を仲違いさせ、呂布に董卓を滅ぼさせるつもりなのだ。","私は美女連環の計を用いて、貂蝉に董卓と呂布を仲違いさせ、呂布に董卓を滅ぼさせるつもりなのだ。","プレイヤー",},
        [402] = {1002,"主君","なんて無茶な！董卓を斬るのは我々武将の仕事です！か弱い女性に任せるには荷が重すぎる！","なんて無茶な！董卓を斬るのは私たち武将の仕事よ！か弱い女性に任せるには荷が重すぎるわ！","王允",},
        [403] = {1002,"主君","こうしてはいられない！行くぞ、貂蝉を助けるんだ！","こうしてはいられないわ！行くわよ、貂蝉を助けるの！","",},
        [404] = {1003,"張星彩","助けに来たわよ、貂蝉さん！一緒に逃げましょ！","助けに来たわよ、貂蝉さん！一緒に逃げましょ！","貂蝉",},
        [405] = {1003,"貂蝉","勘違いしているようですね。お父さまに強要されたのではありません。わたくしが志願したんです。董卓を殺し、清らかな天下を取り戻すために！","勘違いしているようですね。お父さまに強要されたのではありません。わたくしが志願したんです。董卓を殺し、清らかな天下を取り戻すために！","張星彩",},
        [406] = {1003,"張星彩","そうだったの?貂蝉さんって、度胸があるのね。","そうだったの?貂蝉さんって、度胸があるのね。","貂蝉",},
        [407] = {1003,"張星彩","でも、安心して。ご主君様がきっと董卓を倒して、貂蝉さんが幸せになるようにしてくれるから。","でも、安心して。ご主君様がきっと董卓を倒して、貂蝉さんが幸せになるようにしてくれるから。","貂蝉",},
        [408] = {1004,"主君","呂布よ、貂蝉と結ばれたいなら、決して止めはしない！共に董卓を倒しに行かないか?","呂布、貂蝉と結ばれたいなら、決して止めはしないわ。共に董卓を倒しに行かない?","呂布",},
        [409] = {1004,"呂布","断る。董卓には不満があるが、俺とて親子の情がある。","断る。董卓には不満があるが、俺とて親子の情がある。","プレイヤー",},
        [410] = {1004,"主君","そんなもの、クソくらえとは思わないか?","そんなものに、本気でこだわっているの?","呂布",},
        [411] = {1004,"主君","あいつはお前の貂蝉を奪って、寝取ったんだぞ?","あいつはあなたの貂蝉を奪って、寝取ったのよ?","呂布",},
        [412] = {1004,"呂布","その通りだ！いまいましい董卓め。ヤツのために何度血の雨を浴び、天に弓を引いたと思っているのだ！","その通りだ！いまいましい董卓め。ヤツのために何度血の雨を浴び、天に弓を引いたと思っているのだ！","プレイヤー",},
        [413] = {1004,"呂布","男たるもの、天地の間に生を受けたからには、一本立ちせねばならん。","男たるもの、天地の間に生を受けたからには、一本立ちせねばならん。","プレイヤー",},
        [414] = {1004,"呂布","これ以上あの男の下にいてなるものか！行くぞ、董卓を殺してやる！","これ以上あの男の下にいてなるものか！行くぞ、董卓を殺してやる！","プレイヤー",},
        [415] = {1004,"主君","よし、それでこそ呂布だ！この覇気、この気概、まさに英雄豪傑のあるべき姿！","よし、それでこそ呂布よ！この覇気、この気概、まさに英雄豪傑のあるべき姿ね！","呂布",},
        [416] = {1005,"董卓","ぐわっはっは！天子め、やっとわしに禅譲する気になったか。やはりこの董卓は皇帝となるにふさわしい男よ！","ぐわっはっは！天子め、やっとわしに禅譲する気になったか。やはりこの董卓は皇帝となるにふさわしい男よ！","プレイヤー",},
        [417] = {1005,"主君","董卓、悪の限りもここまでだ！覚悟しろ！","董卓、悪の限りもここまでよ！覚悟しなさい！","董卓",},
        [418] = {1005,"董卓","うぬっ！誰かと思えば、また貴様か！","うぬっ！誰かと思えば、また貴様か！","プレイヤー",},
        [419] = {1005,"董卓","天子の勅書があると偽って待ち伏せするとは、卑怯者めがぁ！呂布！呂布はどこだっ?我が息子よ～！","天子の勅書があると偽って待ち伏せするとは、卑怯者めがぁ！呂布！呂布はどこだっ?我が息子よ～！","プレイヤー",},
        [420] = {1005,"呂布","ここだ！天子の命により賊を討つ！国賊董卓、覚悟せよ！","ここだ！天子の命により賊を討つ！国賊董卓、覚悟せよ！","董卓",},
        [421] = {1006,"董卓","んなっ！?バカなっ！わしに逆らうつもりか～！","んなっ！?バカなっ！わしに逆らうつもりか～！","呂布",},
        [422] = {1006,"董卓","いやだ……！わしは大漢の太師だぞ……貂蝉も手に入れた……わしこそ皇帝にふさわしい、のに……(董卓は死んだ)","いやだ……！わしは大漢の太師だぞ……貂蝉も手に入れた……わしこそ皇帝にふさわしい、のに……(董卓は死んだ)","呂布",},
        [423] = {1006,"呂布","貂蝉！鳳儀亭で別れて以来、お前を想わない夜はなかったぞ！","貂蝉！鳳儀亭で別れて以来、お前を想わない夜はなかったぞ！","貂蝉",},
        [424] = {1006,"貂蝉","わたくしもです、呂布さま！ですが……この身はすでに汚れてしまって、もうあなたにふさわしくは……うっう……","わたくしもです、呂布さま！ですが……この身はすでに汚れてしまって、もうあなたにふさわしくは……うっう……","呂布",},
        [425] = {1006,"呂布","言うな！　辛い思いをさせたな、貂蝉！だが、そんなものは俺の愛ですべて消し去ってやる！","言うな！　辛い思いをさせたな、貂蝉！だが、そんなものは俺の愛ですべて消し去ってやる！","貂蝉",},
        [426] = {1006,"呂布","これからは俺がお前を守り、大切にしよう。どんな苦しみも二度と味わわせることはないと誓おう。","これからは俺がお前を守り、大切にしよう。どんな苦しみも二度と味わわせることはないと誓おう。","貂蝉",},
        [427] = {1006,"主君","……ゲフンっ！(いい加減にしてくれないか。もうお腹いっぱいだ！)","ゲ……ゲフンっ！(いい加減にしてよね。もうお腹いっぱいよ！)","貂蝉",},
        [428] = {1006,"貂蝉","あら、将軍に睨まれてしまいました……危ういところを救っていただき、ありがとうございました。","あら、将軍に睨まれてしまいました……危ういところを救っていただき、ありがとうございました。","プレイヤー",},
        [429] = {1006,"貂蝉","郿城にいる時、董卓からもらった名将令です。どうぞお納めください。","郿城にいる時、董卓からもらった名将令です。どうぞお納めください。","プレイヤー",},
        [430] = {1006,"主君","ありがとうございます！貂蝉さんのご好意に感謝します！","ありがとうございます！貂蝉さんのご好意に感謝しますわ。","呂布",},
        [431] = {1006,"呂布","若き英雄よ。お前とは色々あったが、すべて水に流そう。今日から俺たちは友だ！","若き英雄よ。お前とは色々あったが、すべて水に流そう。今日から俺たちは友だ！","プレイヤー",},
        [432] = {1006,"呂布","必要があれば、この呂布を頼って来い。お前の頼みなら、必ず助けてやる。","必要があれば、この呂布を頼って来い。お前の頼みなら、必ず助けてやる。","プレイヤー",},
        [433] = {1006,"主君","ありがとう。じゃあ一つお願いだ。貂蝉を大切にしてあげるんだぞ！では！","ありがとう。じゃあ一つお願いよ。貂蝉を大切にしてあげてね！じゃっ！","呂布",},
        [434] = {1006,"張星彩","ご主君様、これからどうする?","ご主君様、これからどうする?","プレイヤー",},
        [435] = {1006,"関銀屏","董卓が死んで、部下の李傕や郭汜たちは、飛熊軍を率いて涼州に逃げ去りましたわ。","董卓が死んで、部下の李傕や郭汜たちは、飛熊軍を率いて涼州に逃げ去りましたわ。","プレイヤー",},
        [436] = {1006,"主君","わかった。追撃するぞ！あいつら手下が悪事を働かなければ、董卓もここまでのさばることはなかったんだ！","わかった。追撃しましょう！あいつら手下が悪事を働かなければ、董卓もここまでのさばることはなかったのよ！","関銀屏",},
        [437] = {1101,"李傕","ま～たワレか！わざわざ追うて来て、どがぁしても地獄に落ちたいようじゃのぉ！","ま～たワレか！わざわざ追うて来て、どがぁしても地獄に落ちたいようじゃのぉ！","関銀屏",},
        [438] = {1101,"関銀屏","あ～ら、ちょっと大げさですわよ?我が主君様のわらじ持ち以下の雑魚のくせに！","あ～ら、ちょっと大げさですわよ?我が主君様のわらじ持ち以下の雑魚のくせに！","李傕",},
        [439] = {1101,"張星彩","早くやっちゃいましょう、ご主君様！","早くやっちゃいましょう、ご主君様！","李傕",},
        [440] = {1102,"李傕","チクショーッ、また負けた！次こそは必ず殺しちゃるけぇなぁっ！(李傕は怒り狂いながら逃げていった)","チクショーッ、また負けた！次こそは必ず殺しちゃるけぇなぁっ！(李傕は怒り狂いながら逃げていった)","張星彩",},
        [441] = {1102,"張星彩","とかなんとか言いながら、さっさと逃げたわね！","とかなんとか言いながら、さっさと逃げたわね！","李傕",},
        [442] = {1102,"関銀屏","あっちに郭汜がいますわ、続けてぶっ飛ばしてしまいましょう！","あっちに郭汜がいますわ、続けてぶっ飛ばしてしまいましょう！","李傕",},
        [443] = {1103,"賈詡","皆さん落ち着いてください。私に考えがあります！","皆さん落ち着いてください。私に考えがあります！","李傕",},
        [444] = {1103,"賈詡","上手くいけば、再び朝廷を奉じて天下を正せますし、だめならその時逃げても遅くはないでしょう。","上手くいけば、再び朝廷を奉じて天下を正せますし、だめならその時逃げても遅くはないでしょう。","李傕",},
        [445] = {1103,"李傕","ワレはいつでも妙案を出してくれるのう、賈詡。早う言え、どがいな策があるんじゃ?","ワレはいつでも妙案を出してくれるのう、賈詡。早う言え、どがいな策があるんじゃ?","賈詡",},
        [446] = {1103,"賈詡","皆さんが軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まってしまいます。","皆さんが軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まってしまいます。","李傕",},
        [447] = {1103,"賈詡","ここは兵馬を招集しながら長安に攻め入り、董卓殿の仇を討つのがよいでしょう。","ここは兵馬を招集しながら長安に攻め入り、董卓殿の仇を討つのがよいでしょう。","李傕",},
        [448] = {1103,"李傕","ええ策じゃ！王允は今、長安で董卓の残党狩りをしとるけぇ、","ええ策じゃ！王允は今、長安で董卓の残党狩りをしとるけぇ、","賈詡",},
        [449] = {1103,"李傕","わしらが「王允は西涼人を皆殺しにするつもりじゃ」と流言を広めりゃあ、反発する勢力がすぐ集まるじゃろう。","わしらが「王允は西涼人を皆殺しにするつもりじゃ」と流言を広めりゃあ、反発する勢力がすぐ集まるじゃろう。","賈詡",},
        [450] = {1104,"張星彩","ご主君様、まずいわ！","ご主君様、まずいわ！","プレイヤー",},
        [451] = {1104,"張星彩","逃げた李傕と郭汜が、賈詡の計略通りに流言を広め、あっという間に数十万の兵を集めて、また長安に攻め入ったって！","逃げた李傕と郭汜が、賈詡の計略通りに流言を広め、あっという間に数十万の兵を集めて、また長安に攻め入ったって！","プレイヤー",},
        [452] = {1104,"張星彩","王允はすでに殺され、李傕と郭汜は、自分たちを大将軍にするよう、帝を脅してるそうよ。","王允はすでに殺され、李傕と郭汜は、自分たちを大将軍にするよう、帝を脅してるそうよ。","プレイヤー",},
        [453] = {1104,"主君","なんてこった、善悪がまるで逆じゃないか！しかし、呂布はどうした?長安城を守っているんじゃないのか?","なんてこと、善悪がまるで逆じゃない！でも、呂布はどうしたの?長安城を守っているんじゃないの?","張星彩",},
        [454] = {1104,"関銀屏","西涼軍は十万以上いるのですよ！呂布は勇猛ですが、あまりに多勢に無勢です。","西涼軍は十万以上いるのですよ！呂布は勇猛ですが、あまりに多勢に無勢です。","プレイヤー",},
        [455] = {1104,"関銀屏","すでに数少ない部下を連れ、淮南の袁術を頼って落ち延びたらしいですわ！","すでに数少ない部下を連れ、淮南の袁術を頼って落ち延びたらしいですわ！","プレイヤー",},
        [456] = {1104,"主君","事態は一刻を争うな。急いで長安に攻め入るぞ！","事態は一刻を争うわね。急いで長安に攻め入りましょう！","関銀屏",},
        [457] = {1105,"張星彩","よくもやってくれたわね、賈詡！あんたが悪だくみしなければ、長安はこんな災難に遭わなかったのに！","よくもやってくれたわね、賈詡！あんたが悪だくみしなければ、長安はこんな災難に遭わなかったのに！","賈詡",},
        [458] = {1105,"賈詡","もともと私は涼州人です。これはあくまで自分の命を守るための策略。","もともと私は涼州人です。これはあくまで自分の命を守るための策略。","張星彩",},
        [459] = {1105,"賈詡","もし私が動かなければ、この長安城ではもっと多くの文武百官が犠牲になっていたでしょう。","もし私が動かなければ、この長安城ではもっと多くの文武百官が犠牲になっていたでしょう。","張星彩",},
        [460] = {1105,"賈詡","でも安心してください。私がいれば、李傕と郭汜を牽制しながら、百官と天子を守れますから。","でも安心してください。私がいれば、李傕と郭汜を牽制しながら、百官と天子を守れますから。","張星彩",},
        [461] = {1105,"張星彩","あっ、待て！逃げるな！","あっ、待て！逃げるな！","賈詡",},
        [462] = {1106,"張星彩","ご主君様、西涼太守の馬騰、并州刺史の韓遂の二人が、李傕と郭汜を討つために来てくれたわ！","ご主君様、西涼太守の馬騰、并州刺史の韓遂の二人が、李傕と郭汜を討つために来てくれたわ！","プレイヤー",},
        [463] = {1106,"張星彩","……と思ったら、馬騰の息子の馬超が勝負を挑みに来た……何だかわたしたちのこと、誤解してるみたい。","……と思ったら、馬騰の息子の馬超が勝負を挑みに来た……何だかわたしたちのこと、誤解してるみたい。","プレイヤー",},
        [464] = {1106,"関銀屏","あらぁ、馬超ってなかなかの美男子ですわね。星のように輝く瞳、虎のようにガッチリした体つき。十七歳とは思えないほど堂々としてますわね！","あらぁ、馬超ってなかなかの美男子ですわね。星のように輝く瞳、虎のようにガッチリした体つき。十七歳とは思えないほど堂々としてますわね！","張星彩",},
        [465] = {1106,"張星彩","あれ?銀屏姉さまってば、浮気?","あれ?銀屏姉さまってば、浮気?","関銀屏",},
        [466] = {1106,"関銀屏","おだまり！私は強い相手には敵味方なく敬意を払っているだけですわ！","おだまり！私は強い相手には敵味方なく敬意を払っているだけですわ！","張星彩",},
        [467] = {1106,"馬超","馬超ここにあり！国賊よ、早く出てきて裁きを受けよ！","馬超ここにあり！国賊よ、早く出てきて裁きを受けよ！","プレイヤー",},
        [468] = {1106,"主君","へえ、若いのにすごい覇気だな。よし、会いに行ってみよう！","へえ、若いのにすごい覇気ね。よし、会いに行ってみましょう！","馬超",},
        [469] = {1106,"張星彩","ちょっと、馬超さん！李傕と郭汜を討ちに来たんでしょ?だったら、うちのご主君様は味方のはずよ！","ちょっと、馬超さん！李傕と郭汜を討ちに来たんでしょ?だったら、うちのご主君様は味方のはずよ！","馬超",},
        [470] = {1106,"関銀屏","聞く耳持っちゃいないみたいですわね。まずは彼を倒してからですわ！","聞く耳持っちゃいないみたいですわね。まずは彼を倒してからですわ！","馬超",},
        [471] = {1107,"主君","いいぞ、実に胸のすく戦いだった！こんな感覚は久しぶりだ！","いいわ、実に胸のすく戦いだった！こんな感覚は久しぶりね！","馬超",},
        [472] = {1107,"馬超","なるほど、あの十八路諸侯と並び称され、董卓を倒した若き英雄だったか！手合わせできて光栄だ。","なるほど、あの十八路諸侯と並び称され、董卓を倒した若き英雄だったか！手合わせできて光栄だ。","プレイヤー",},
        [473] = {1107,"主君","それはどうも。だけど、残念ながら無駄足だったね。李傕 、郭汜、賈詡は全員私が片付けてしまったから。","それはどうも。だけど、残念ながら無駄足だったわね。李傕 、郭汜、賈詡は全員私が片付けてしまったから。","馬超",},
        [474] = {1107,"馬超","いや、無駄足ではなかった。いい友ができたからな！ハッハッハ！","いや、無駄足ではなかった。いい友ができたからな！ハッハッハ！","プレイヤー",},
        [475] = {1107,"馬超","よし、兄弟たちよ、涼州に引き揚げるぞ！ではまたな、この名将令は差し上げよう。","よし、兄弟たちよ、涼州に引き揚げるぞ！ではまたな、この名将令は差し上げよう。","プレイヤー",},
        [476] = {1107,"馬超","もし我が涼州に来る機会があれば、この馬超誠意をもってお迎えしよう！(馬超は別れを告げた)","もし我が涼州に来る機会があれば、この馬超誠意をもってお迎えしよう！(馬超は別れを告げた)","プレイヤー",},
        [477] = {1107,"張星彩","中原はしばらく落ち着きそうね。ご主君様、次はどこに行く?","中原はしばらく落ち着きそうね。ご主君様、次はどこに行く?","プレイヤー",},
        [478] = {1107,"主君","東北の幽州、青州、冀州あたりが荒れているらしい。兄者たちも公孫瓚と共に幽州にいるはずだけど、大丈夫かな?","東北の幽州、青州、冀州あたりが荒れているらしいわ。兄上たちも公孫瓚と共に幽州にいるはずだけど、大丈夫かしら?","張星彩",},
        [479] = {1107,"主君","よし、やはり幽州に行って、劉備の兄者や星彩たちの父上に会おう！","うん、やっぱり幽州に行って、劉備兄さんや星彩たちの父上に会いましょう！","張星彩",},
        [480] = {1107,"張星彩","やったー！ちょうど懐かしくなってた頃なのよ！不思議よね、そばにいた時はブツクサうるさかったのにさ。","やったー！ちょうど懐かしくなってた頃なのよ！不思議よね、そばにいた時はブツクサうるさかったのにさ。","プレイヤー",},
        [481] = {1201,"張星彩","でも、幽州はかなり遠いから、冀州で一休みしていくっていうのはどう?","でも、幽州はかなり遠いから、冀州で一休みしていくっていうのはどう?","プレイヤー",},
        [482] = {1201,"関銀屏","ご主君様、その公孫瓚から手紙が来ていましたわ！ご覧になって。","ご主君様、その公孫瓚から手紙が来ていましたわ！ご覧になって。","プレイヤー",},
        [483] = {1201,"主君","どれどれ……手紙によると、袁紹が公孫瓚に冀州を攻めさせ、その後二人で分け合うことにしたようだな。","なになに……手紙によると、袁紹が公孫瓚に冀州を攻めさせ、その後二人で分け合うことにしたようね。","関銀屏",},
        [484] = {1201,"関銀屏","小者のくせに狡猾ですわね、袁紹は。冀州を欲しがっているのは自分なのに、公孫瓚をそそのかして攻めさせるなんて。","小者のくせに狡猾ですわね、袁紹は。冀州を欲しがっているのは自分なのに、公孫瓚をそそのかして攻めさせるなんて。","プレイヤー",},
        [485] = {1201,"主君","銀屏の評価は間違っていない。現在の冀州牧は誰だ?","銀屏の評価は間違っていないわ。現在の冀州牧は誰?","関銀屏",},
        [486] = {1201,"張星彩","韓馥よ！公孫瓚が冀州を攻めたなら、父さまもそこにいるはず。早く助けに行きましょう！","韓馥よ！公孫瓚が冀州を攻めたなら、父さまもそこにいるはず。早く助けに行きましょう！","プレイヤー",},
        [487] = {1202,"張星彩","ああ、遅かった！公孫瓚の本軍がまだ来てないのに、韓馥ったら冀州を袁紹に譲っちゃったわ！","ああ、遅かった！公孫瓚の本軍がまだ来てないのに、韓馥ったら冀州を袁紹に譲っちゃったわ！","韓馥",},
        [488] = {1202,"主君","なんて間抜けな！韓馥殿、どうしてそんな簡単に譲ってしまうんです。","なんて間抜けなの！韓馥殿、どうしてそんな簡単に譲ってしまうんです。","韓馥",},
        [489] = {1202,"関銀屏","袁紹は小さな渤海の太守にすぎないのですよ。冀州はお金や食料も豊富で、人も多く土地も広い。怖がることがありまして?","袁紹は小さな渤海の太守にすぎないのですよ。冀州はお金や食料も豊富で、人も多く土地も広い。怖がることがありまして?","韓馥",},
        [490] = {1202,"関銀屏","まさかとは思いますが……韓馥さん、公孫瓚が袁紹の差し金で冀州を攻めたことをご存知ないのですか?","まさかとは思いますが……韓馥さん、公孫瓚が袁紹の差し金で冀州を攻めたことをご存知ないのですか?","韓馥",},
        [491] = {1202,"韓馥","ええっ、なんですって！?そんなことは知りませんよ、だまされた！","ええっ、なんですって！?そんなことは知りませんよ、だまされた！","関銀屏",},
        [492] = {1202,"関銀屏","はぁ……朝廷も目が曇ってますわね。あなたのような弱虫に、どうやったら冀州牧が務まるのか教えてほしいですわ。","はぁ……朝廷も目が曇ってますわね。あなたのような弱虫に、どうやったら冀州牧が務まるのか教えてほしいですわ。","韓馥",},
        [493] = {1202,"関銀屏","この意気地なし！とっとと消え失せなさい、シッシッ！","この意気地なし！とっとと消え失せなさい、シッシッ！","韓馥",},
        [494] = {1202,"張星彩","キツい言い方でごめんね、韓馥さん。銀屏姉さまは逃げろって言ってるのよ。袁紹があなたを見逃すはずないもの。","キツい言い方でごめんね、韓馥さん。銀屏姉さまは逃げろって言ってるのよ。袁紹があなたを見逃すはずないもの。","韓馥",},
        [495] = {1202,"関銀屏","余計な付け足しは無用ですわ！","余計な付け足しは無用ですわ！","韓馥",},
        [496] = {1202,"韓馥","ううっ……この私のためにそんな心遣いを……！ありがとう、娘さんたち。(韓馥は悔やんでも悔みきれない様子で逃走した)","ううっ……この私のためにそんな心遣いを……！ありがとう、娘さんたち。(韓馥は悔やんでも悔みきれない様子で逃走した)","関銀屏",},
        [497] = {1203,"公孫越","いやはや！将軍の勇敢な戦い、この公孫越恐れ入った！","いやはや！将軍の勇敢な戦い、この公孫越恐れ入った！","プレイヤー",},
        [498] = {1203,"主君","えっ、公孫越と言われました?公孫瓚殿とはどんな関係なんですか?","えっ、公孫越と言われました?公孫瓚殿とはどんな関係なんですか?","公孫越",},
        [499] = {1203,"公孫越","公孫瓚はわしの兄だ！わしは兄の使者として、袁紹に会いに来たのだ。かねての約束どおり、冀州の領土を半分譲るよう、申し入れにな。","公孫瓚はわしの兄だ！わしは兄の使者として、袁紹に会いに来たのだ。かねての約束どおり、冀州の領土を半分譲るよう、申し入れにな。","プレイヤー",},
        [500] = {1203,"公孫越","すごい男だぞ、わしの兄は！小さいころから有能で武芸に優れ、遼西の烏桓の頭目をも服従させたのだからな！","すごい男だぞ、わしの兄は！小さいころから有能で武芸に優れ、遼西の烏桓の頭目をも服従させたのだからな！","プレイヤー",},
        [501] = {1203,"主君","ハハ……そうですか……(この粗野な男も、公孫瓚の信奉者の一人か……)","ハハ……そうですか……(この粗野な男も、公孫瓚の信奉者の一人なのね……)","公孫越",},
        [502] = {1203,"張星彩","公孫越さん、張飛父さまたちが今どこにいるか知ってる?","公孫越さん、張飛父さまたちが今どこにいるか知ってる?","公孫越",},
        [503] = {1203,"公孫越","ああ、お前のおやじさんたちなら今、平原県にいるぞ。","ああ、お前のおやじさんたちなら今、平原県にいるぞ。","張星彩",},
        [504] = {1203,"張星彩","そうなんだ、ありがと！ご主君様、早く平原県に行って父さまを探しましょ！","そうなんだ、ありがと！ご主君様、早く平原県に行って父さまを探しましょ！","公孫越",},
        [505] = {1204,"劉備","久しぶりだな、義弟よ。また将として一皮むけたようだ。","久しぶりだな、義妹よ。また将として一皮むけたようだ。","プレイヤー",},
        [506] = {1204,"主君","兄者！今は平原郡の相なのですね！それに皆、官職を得られたようで。おめでとうございます。","兄上！今は平原郡の相なのですね！それに皆、官職を得られたようで。おめでとうございます。","劉備",},
        [507] = {1204,"劉備","なにを言う。お前と比べたら、足元にも及ばんさ。洛陽と長安の義挙に加わったのだろう?今や知らぬ人はおらんぞ。","なにを言う。お前と比べたら、足元にも及ばんさ。洛陽と長安の義挙に加わったのだろう?今や知らぬ人はおらんぞ。","プレイヤー",},
        [508] = {1204,"主君","いやいや兄者こそ、その仁徳はすでに天下に知れ渡っていますよ。","いえいえ兄さんこそ、その仁徳はすでに天下に知れ渡っていますわ。","劉備",},
        [509] = {1204,"主君","聞けば刺客に襲われたときも礼を尽くしたため、その者は恥ずかしさのあまり逃げ去ったそうじゃないですか。","聞けば刺客に襲われたときも礼を尽くしたため、その者は恥ずかしさのあまり逃げ去ったというではありませんか。","劉備",},
        [510] = {1204,"張飛","おいおい、二人してお世辞を言い合ってるんじゃねえよ！関羽兄貴の顔を見てみろ、もう真っ赤だぜ！","おいおい、二人してお世辞を言い合ってるんじゃねえよ！関羽兄貴の顔を見てみろ、もう真っ赤だぜ！","プレイヤー",},
        [511] = {1204,"関羽","よせ。わしの顔は元から赤い","よせ。わしの顔は元から赤い","プレイヤー",},
        [512] = {1204,"張飛","へっへっへ！義弟よ、また強くなったみてえだな！オラ、ひとまずオレと一勝負だ！","へっへっへ！義妹よ、また強くなったみてえだな！オラ、ひとまずオレと一勝負だ！","プレイヤー",},
        [513] = {1205,"劉備","公孫瓚殿、なぜ平原県に来たのです?","公孫瓚殿、なぜ平原県に来たのです?","公孫瓚",},
        [514] = {1205,"公孫瓚","劉備よ、早くお前の義弟を差し出せ！そいつは冀州で我が弟の公孫越を殺したのだ！","劉備よ、早くお前の義妹を差し出せ！そいつは冀州で我が弟の公孫越を殺したのだ！","劉備",},
        [515] = {1205,"主君","それは言いがかりです、公孫瓚殿！公孫越殿とは勝負しましたが、殺してはいません！","それは言いがかりです、公孫瓚殿！公孫越殿とは勝負しましたが、殺してはいません！","公孫瓚",},
        [516] = {1205,"公孫瓚","信じられるか！我が弟のために命を差し出せ！命には命をもって報いる！","信じられるか！我が弟のために命を差し出せ！命には命をもって報いる！","プレイヤー",},
        [517] = {1206,"主君","公孫瓚殿。あなたには名誉を汚されたが、あえて殺しはしない！もう一度言うが、私は公孫越殿を殺していない！","公孫瓚殿。あなたには名誉を汚されましたが、あえて殺しはしません！もう一度言いますが、私は公孫越殿を殺していません！","公孫瓚",},
        [518] = {1206,"劉備","公孫瓚殿、何か誤解があるのではないか。","公孫瓚殿、何か誤解があるのではないか。","公孫瓚",},
        [519] = {1206,"張飛","オレも義弟を信じるぜ。隠し事ができるようなタマじゃねえからな。","オレも義妹を信じるぜ。隠し事ができるようなタマじゃねえからな。","公孫瓚",},
        [520] = {1206,"張飛","なんならオレの首を賭けてもいいぜ。義弟は絶対に殺してねえ！","なんならオレの首を賭けてもいいぜ。義妹は絶対に殺してねえ！","公孫瓚",},
        [521] = {1206,"劉備","公孫瓚殿。そもそもどうしてそんなことをお考えになったのです?","公孫瓚殿。そもそもどうしてそんなことをお考えになったのです?","公孫瓚",},
        [522] = {1206,"公孫瓚","袁紹が手紙で教えてくれたのだ！","袁紹が手紙で教えてくれたのだ！","劉備",},
        [523] = {1206,"劉備","そうですか……失礼ながら、あなたは騙されているのではありませんか。恐らく、袁紹は冀州をあなたと分け合うのが嫌で、","そうですか……失礼ながら、あなたは騙されているのではありませんか。恐らく、袁紹は冀州をあなたと分け合うのが嫌で、","公孫瓚",},
        [524] = {1206,"劉備","公孫越殿を殺し、その罪を義弟になすりつけようとしているのでしょう。義弟の手であなたを殺させるために。","公孫越殿を殺し、その罪を義妹になすりつけようとしているのでしょう。義妹の手であなたを殺させるために。","公孫瓚",},
        [525] = {1206,"張飛","その通りだ、密偵から報告があったぜ。袁紹は部下に義弟のフリをさせて、公孫越を射殺したってよ！","その通りだ、密偵から報告があったぜ。袁紹は部下に義妹のフリをさせて、公孫越を射殺したってよ！","公孫瓚",},
        [526] = {1206,"公孫瓚","なんだと……！?袁紹め、自らの欲のために、弟を殺したというのか！","なんだと……！?袁紹め、自らの欲のために、弟を殺したというのか！","張飛",},
        [527] = {1206,"公孫瓚","なんと非道な陰謀を！直ちに討伐の軍を起こすぞ。誓って、この命にかけてもヤツを殺す！","なんと非道な陰謀を！直ちに討伐の軍を起こすぞ。誓って、この命にかけてもヤツを殺す！","張飛",},
        [528] = {1206,"公孫瓚","若き将軍よ、許してくれ。この公孫瓚、讒言に惑わされた！この名将令をもってお詫びの印としたい、受け取ってくれるか?","若き将軍よ、許してくれ。この公孫瓚、讒言に惑わされた！この名将令をもってお詫びの印としたい、受け取ってくれるか?","張飛",},
        [529] = {1206,"主君","そういうことならば、ありがたく……しかし袁紹め、十八路諸侯の盟主を譲ってやったのに、感謝するどころか陥れようとするなんて……","そういうことなら、ありがたく……でも袁紹のヤツ、十八路諸侯の盟主を譲ってやったのに、感謝どころか陥れようとするなんて……","公孫瓚",},
        [530] = {1206,"主君","行きましょう。私も討伐を手伝います！","行きましょう。私も討伐を手伝います！","公孫瓚",},
        [531] = {1301,"公孫瓚","袁紹っ！この裏切り者め、なぜ私を謀った?","袁紹っ！この裏切り者め、なぜ私を謀った?","袁紹",},
        [532] = {1301,"袁紹","謀るなど、人聞きの悪い。韓馥は無能ゆえ、冀州をわしに譲ることを望んだのだ。お前には何の関係もない！","謀るなど、人聞きの悪い。韓馥は無能ゆえ、冀州をわしに譲ることを望んだのだ。お前には何の関係もない！","公孫瓚",},
        [533] = {1301,"主君","袁紹、かつてはお前の忠義を買って盟主に推したが、今のお前は見る影もない。よくも世間に顔を晒していられるな！","袁紹、かつてはあなたの忠義を買って盟主に推したけど、今のあなたは見る影もないわ。よくも世間に顔を晒していられるわね！","袁紹",},
        [534] = {1301,"主君","おとなしく馬を降りて降参しろ。袁家の面子を立てて、命だけは許してやる！","おとなしく馬を降りて降参しなさい。袁家の面子を立てて、命だけは許してやるわ！","袁紹",},
        [535] = {1301,"袁紹","馬鹿め！冀州を手に入れた今、文には謀臣、武には猛将を抱え、将兵は多く、食料も十分だ。お前など恐るるに足らん！","馬鹿め！冀州を手に入れた今、文には謀臣、武には猛将を抱え、将兵は多く、食料も十分だ。お前など恐るるに足らん！","プレイヤー",},
        [536] = {1301,"袁紹","我が大将、顔良、文醜よ！ヤツらを殺せ！","我が大将、顔良、文醜よ！ヤツらを殺せ！","プレイヤー",},
        [537] = {1301,"主君","韓馥のバカ、こんな恩知らずを肥え太らせるなんて！","韓馥のバカ、こんな恩知らずを肥え太らせるなんて！","袁紹",},
        [538] = {1301,"主君","顔良、文醜が何だ！二人まとめて、私が倒してやる！","顔良、文醜が何よ！二人まとめて、私が倒してやるわ！","袁紹",},
        [539] = {1302,"文醜","なんだぁ、てめえら！俺は昨日、酒の飲み比べで顔良に負けて、機嫌が悪いんだ。よくもノコノコとケンカを売りに来たな！","なんだぁ、てめえら！俺は昨日、酒の飲み比べで顔良に負けて、機嫌が悪いんだ。よくもノコノコとケンカを売りに来たな！","張星彩",},
        [540] = {1302,"張星彩","ねえねえ、文醜さんと顔良さんって、袁紹軍の二枚看板なんでしょ?","ねえねえ、文醜さんと顔良さんって、袁紹軍の二枚看板なんでしょ?","文醜",},
        [541] = {1302,"文醜","ああん?まあな、よく知ってるじゃねえか。","ああん?まあな、よく知ってるじゃねえか。","張星彩",},
        [542] = {1302,"張星彩","すっごくいい二人組だよね！名前からして、うまく出来すぎじゃない！あはは！","すっごくいい二人組だよね！名前からして、うまく出来すぎじゃない！あはは！","文醜",},
        [543] = {1302,"文醜","ガハハ！そうだろう、わが軍はこの文醜と顔良で持っているのさ！","ガハハ！そうだろう、わが軍はこの文醜と顔良で持っているのさ！","張星彩",},
        [544] = {1302,"関銀屏","はぁ～。ブサイクなだけでなく間抜けだなんて、まったく救いようがないですわね。","はぁ～。ブサイクなだけでなく間抜けだなんて、まったく救いようがないですわね。","文醜",},
        [545] = {1302,"関銀屏","星彩がからかってるのにも気づかないなんて。お喰らいっ！","星彩がからかってるのにも気づかないなんて。お喰らいっ！","文醜",},
        [546] = {1303,"張星彩","きゃー！お姉さま、素敵！","きゃー！お姉さま、素敵！","張郃",},
        [547] = {1303,"張郃","お姉さまって、誰のことかしら?まさか、この張郃のこと?身の丈七尺で絶世の美人の、この私の?","お姉さまって、誰のことかしら?まさか、この張郃のこと?身の丈七尺で絶世の美人の、この私の?","張星彩",},
        [548] = {1303,"張星彩","ウソでしょ～！?なんかデカい美人がいると思ってたけど、あなた男！?信じられないわ！","ウソでしょ～！?なんかデカい美人がいると思ってたけど、あなた男！?信じられないわ！","張郃",},
        [549] = {1303,"張星彩","ねえねえ、そのきれいなお顔の秘訣はなに?オススメの化粧品とかあるなら、教えてよ。国中に宣伝して大儲けしましょ！","ねえねえ、そのきれいなお顔の秘訣はなに?オススメの化粧品とかあるなら、教えてよ。国中に宣伝して大儲けしましょ！","張郃",},
        [550] = {1303,"張郃","あ～らこの小娘、人が気にしていることをよくもペラペラと！親に代わってしつけちゃうわよ！","あ～らこの小娘、人が気にしていることをよくもペラペラと！親に代わってしつけちゃうわよ！","張星彩",},
        [551] = {1304,"袁紹","くっ……！まさかこの袁紹が負ける日が来るとは！公孫瓚、天子からの詔がある。","くっ……！まさかこの袁紹が負ける日が来るとは！公孫瓚、天子からの詔がある。","公孫瓚",},
        [552] = {1304,"公孫瓚","いいだろう。天子が講和せよというなら、もうやめだ。","いいだろう。天子が講和せよというなら、もうやめだ。","袁紹",},
        [553] = {1304,"袁紹","謝罪の印に、この名将令をやる。受け取ってくれ！","謝罪の印に、この名将令をやる。受け取ってくれ！","プレイヤー",},
        [554] = {1304,"主君","いいだろう。名将令に免じて、今日のところは許してやる。(公孫瓚と袁紹は去って行った)","いいでしょう。名将令に免じて、今日のところは許してあげるわ。(公孫瓚と袁紹は去って行った)","袁紹",},
        [555] = {1304,"張星彩","ふーん。公孫瓚って英雄だと思ってたけど、結局、袁紹と同類なのね。","ふーん。公孫瓚って英雄だと思ってたけど、結局、袁紹と同類なのね。","プレイヤー",},
        [556] = {1304,"関銀屏","まったくですわ。命にかけても袁紹を殺すとか誓ってたクセに、朝廷にうまい話をちらつかせただけで、すんなり撤兵だなんて。","まったくですわ。命にかけても袁紹を殺すとか誓ってたクセに、朝廷にうまい話をちらつかせただけで、すんなり撤兵だなんて。","プレイヤー",},
        [557] = {1304,"関銀屏","とんだ玉ナシ、いくじなしですわね。","とんだ玉ナシ、いくじなしですわね。","プレイヤー",},
        [558] = {1304,"張星彩","でも、無駄足にならなかったのはよかったわ。また名将令が手に入ったんだもの！ご主君様、つぎはどこへ行く?","でも、無駄足にならなかったのはよかったわ。また名将令が手に入ったんだもの！ご主君様、つぎはどこへ行く?","プレイヤー",},
        [559] = {1304,"主君","現在、青州で黄巾軍の残党が反乱を起こし、曹操が包囲討伐を行っているらしい。私たちも手伝いに行こう！","現在、青州で黄巾軍の残党が反乱を起こし、曹操が包囲討伐を行っているらしいわ。私たちも手伝いに行きましょう！","張星彩",},
        [560] = {1401,"曹操","よく来てくれたな！感謝するぞ、若き英雄よ。お前の助けがなければ、これほど簡単に青州の黄巾軍を攻略できなかっただろう。","よく来てくれたな！感謝するぞ、若き英雄よ。お前の助けがなければ、これほど簡単に青州の黄巾軍を攻略できなかっただろう。","プレイヤー",},
        [561] = {1401,"主君","青州の黄巾軍は、金もなく食うにも困る貧民たちです。","青州の黄巾軍は、金もなく食うにも困る貧民たちです。","曹操",},
        [562] = {1401,"主君","皆殺しにする必要はありませんが、このまま解散させれば、また乱を起こす恐れもあるでしょう。そこで曹操兄に、ひとつ提案があります。","皆殺しにする必要はありませんが、このまま解散させれば、また乱を起こす恐れもあるでしょう。そこで曹操兄さんに、ひとつ提案があります。","曹操",},
        [563] = {1401,"主君","彼らを帰順させてはどうでしょうか。第一は勢力拡大のため、第二は彼らもこれで飯が食えます。","彼らを帰順させてはどうでしょうか。第一は勢力拡大のため、第二は彼らもこれで飯が食えます。","曹操",},
        [564] = {1401,"主君","第三に賊が官兵に変われば朝廷も安心し、信頼も厚くなりましょう。","第三に賊が官兵に変われば朝廷も安心し、信頼も厚くなりますわ。","曹操",},
        [565] = {1401,"曹操","ふむ、いいだろう。若き英雄は勢いだけでなく、思慮深くもあるのだな。この曹操、感心したぞ。","ふむ、いいだろう。若き英雄は勢いだけでなく、思慮深くもあるのだな。この曹操、感心したぞ。","プレイヤー",},
        [566] = {1401,"主君","いえ……ほんの思いつきを口したまでです。","いえ……ほんの思いつきを口したまでですわ。","曹操",},
        [567] = {1401,"張星彩","ご主君様はすごいわ。わたし惚れ直しちゃう！","ご主君様はすごいわ。わたし惚れ直しちゃう！","プレイヤー",},
        [568] = {1401,"曹操","うむ、実に良い提案だ。若き英雄の言う通り、青州の黄巾軍を帰順させ、今後は青州兵と呼ぶことにしよう。","うむ、実に良い提案だ。若き英雄の言う通り、青州の黄巾軍を帰順させ、今後は青州兵と呼ぶことにしよう。","プレイヤー",},
        [569] = {1401,"張星彩","ご主君様、大変よ！兗州にいることが知れ渡ったっぽくて、たくさんの名士が会いに来ちゃったわ。武将も文官もいるわよ。","ご主君様、大変よ！兗州にいることが知れ渡ったっぽくて、たくさんの名士が会いに来ちゃったわ。武将も文官もいるわよ。","プレイヤー",},
        [570] = {1401,"主君","へえ、そうか。どんな人たちが来ているんだろう。曹操兄、一緒に会いに行きませんか?","ああ、そうなのね。どんな方々が来ているのかしら。曹操兄さん、一緒に会いに行きませんか?","張星彩",},
        [571] = {1402,"典韋","天下に武名を轟かす強者がいると聞いて、勝負に参った。の双戟に勝てるか！?かかって来い！ふんっ！","天下に武名を轟かす強者がいると聞いて、勝負に参った。の双戟に勝てるか！?かかって来い！ふんっ！","関銀屏",},
        [572] = {1402,"関銀屏","うふふ、気合だけはご大層ですけど、実力の方はどうかしら?","うふふ、気合だけはご大層ですけど、実力の方はどうかしら?","典韋",},
        [573] = {1402,"典韋","そんな細っこい手足で粋がるでないわ、小娘！俺の双戟は八十斤もあるのだぞ！早くお前の主人を呼んで来い！","そんな細っこい手足で粋がるでないわ、小娘！俺の双戟は八十斤もあるのだぞ！早くお前の主人を呼んで来い！","関銀屏",},
        [574] = {1402,"主君","呼ばれなくても来ている。あまり私の仲間をいじめないでほしいな。ところで、一つ約束しないか?","呼ばれなくても来ているわ。あまり私の仲間をいじめないでよ。ところで、一つ約束しない?","典韋",},
        [575] = {1402,"主君","もしそちらが勝てば、私を好きにして構わない。磔にでも八つ裂きにでもするがいい。だが私が勝ったら、言うことに従ってもらおう。","もしあなたが勝てば、私を好きにして構わないわ。磔にでも八つ裂きにでもね。でも私が勝てば、言うことに従ってもらうわ。","典韋",},
        [576] = {1402,"典韋","おお、いいとも！恐れる者などない！","約束よ。さあ、来なさい！","プレイヤー",},
        [577] = {1402,"主君","約束だぞ。さあ、来い！","約束よ。さあ、来なさい！","典韋",},
        [578] = {1403,"典韋","むうう……なんという強さだ！この典韋、恐れ入った！","むうう……なんという強さだ！この典韋、恐れ入った！","プレイヤー",},
        [579] = {1403,"典韋","言え！俺に何をさせたい?この典韋、無頼漢だが約束は必ず守る。決して逃げたりはせん。","言え！俺に何をさせたい?この典韋、無頼漢だが約束は必ず守る。決して逃げたりはせん。","プレイヤー",},
        [580] = {1403,"主君","そこまで深刻な話ではないよ。曹操殿は大人物だ。だから彼について、身の安全を守ってもらいたいんだ。やってもらえるか?","そこまで深刻な話じゃないわよ。曹操殿は大人物。だから彼について、身の安全を守ってもらいたいの。やってもらえる?","典韋",},
        [581] = {1403,"典韋","いいだろう！曹操殿、これからはあなたの護衛として、命をかけてお守りしよう。それでよろしいな?","いいだろう！曹操殿、これからはあなたの護衛として、命をかけてお守りしよう。それでよろしいな?","プレイヤー",},
        [582] = {1403,"曹操","ああ、頼むぞ。","ああ、頼むぞ。","典韋",},
        [583] = {1404,"荀彧","さすが将軍は文武両道に秀でておられる。この荀彧、ぜひとも将軍にお仕えしとうございます。","さすが将軍は文武両道に秀でておられる。この荀彧、ぜひとも将軍にお仕えしとうございます。","プレイヤー",},
        [584] = {1404,"荀彧","この荀家の名将令は、将軍に献上いたしましょう。","この荀家の名将令は、将軍に献上いたしましょう。","プレイヤー",},
        [585] = {1404,"主君","名将令はありがたく頂戴しましょう。しかし、私は自らの武力の向上にしか興味がないのです。三国最強の武将になるために。","名将令はありがたく頂戴しますわ。でも、私は自らの武力の向上にしか興味がないのです。三国最強の武将になるためにね。","荀彧",},
        [586] = {1404,"主君","あなたの夢を叶えるのには、もっとふさわしい人物がいます。よろしければ紹介いたしましょう。","あなたの夢を叶えるのには、もっとふさわしい人物がいますわ。よろしければ紹介いたしましょう。","荀彧",},
        [587] = {1404,"荀彧","ほう、誰のことを仰っているのですか?","ほう、誰のことを仰っているのですか?","プレイヤー",},
        [588] = {1404,"主君","こちらの曹操殿です。字は孟徳、私の親友です。","こちらの曹操殿です。字は孟徳、私の親友ですわ。","荀彧",},
        [589] = {1404,"曹操","おお……！あなたは潁川の名士、荀彧殿ではないか！素晴らしい！","おお……！あなたは潁川の名士、荀彧殿ではないか！素晴らしい！","荀彧",},
        [590] = {1404,"曹操","あなたがわしに従ってくれるということは、張良が大漢高祖劉邦に従ったのと同じことだ。","あなたがわしに従ってくれるということは、張良が大漢高祖劉邦に従ったのと同じことだ。","荀彧",},
        [591] = {1404,"曹操","若き英雄よ、感謝する。こんなにも多くの人材を引き合わせてくれるとはな。この曹操、感激に耐えん。どう報いるべきだろうか?","若き英雄よ、感謝する。こんなにも多くの人材を引き合わせてくれるとはな。この曹操、感激に耐えん。どう報いるべきだろうか?","荀彧",},
        [592] = {1404,"主君","いえいえ、とんでもない！私に構わず、今後のことを語り合ってください。","いえいえ、とんでもない！私に構わず、今後のことを語り合ってください。","曹操",},
        [593] = {1404,"主君","私は兗州を離れ、あちこち旅をするつもりです。それでは！","私は兗州を離れ、あちこち旅をするつもりです。それでは！","曹操",},
        [594] = {1404,"張星彩","ってなわけで、曹操は謀臣、猛将を従え、兗州を占領して一大勢力になったわ。","ってなわけで、曹操は謀臣、猛将を従え、兗州を占領して一大勢力になったわ。","プレイヤー",},
        [595] = {1404,"張星彩","天下を見渡しても、彼に対抗できるのは袁紹と袁術くらいね！","天下を見渡しても、彼に対抗できるのは袁紹と袁術くらいね！","プレイヤー",},
        [596] = {1404,"関銀屏","よろしいんですの、ご主君様?曹操の勢力がこんなに大きくなって、いつか私たちと道を違えることになったら……","よろしいんですの、ご主君様?曹操の勢力がこんなに大きくなって、いつか私たちと道を違えることになったら……","プレイヤー",},
        [597] = {1404,"主君","荘子がいうには、友とはそれとなく交わるもので、互いに助け合うものではないそうだ。","荘子がいうには、友とはそれとなく交わるもので、互いに助け合うものではないそうよ。","関銀屏",},
        [598] = {1404,"主君","つまり、友と付き合うのに余計な目的意識は不要ということ。心配ばかりしていてもつまらんさ。","つまり、友と付き合うのに余計な目的意識は不要ということ。心配ばかりしていてもつまらないわよ。","関銀屏",},
        [599] = {1404,"関銀屏","ご主君様の言う通りですわね。勉強になりましたわ！","ご主君様の言う通りですわね。勉強になりましたわ！","プレイヤー",},
        [600] = {1404,"張星彩","ご主君様、徐州に名将令の手がかりがありそうよ。徐州に行ってみましょ！","ご主君様、徐州に名将令の手がかりがありそうよ。徐州に行ってみましょ！","プレイヤー",},
        [601] = {1501,"関銀屏","徐州に着きましたけど……前にいるあの人は誰かしら?金銀財宝をあんなに見せびらかして。","徐州に着きましたけど……前にいるあの人は誰かしら?金銀財宝をあんなに見せびらかして。","曹嵩",},
        [602] = {1501,"張星彩","小さいころ父さまから教わったわ。お金を人の目に付くところに出すのは絶対ダメだって。痛い目に遭わなきゃいいけど。","小さいころ父さまから教わったわ。お金を人の目に付くところに出すのは絶対ダメだって。痛い目に遭わなきゃいいけど。","曹嵩",},
        [603] = {1501,"曹嵩","そこの小娘ども、何か言ったか?誰がこの曹嵩の財産を奪えるものか！教えてやる、わしの子は曹操だ！","そこの小娘ども、何か言ったか?誰がこの曹嵩の財産を奪えるものか！教えてやる、わしの子は曹操だ！","関銀屏",},
        [604] = {1501,"曹嵩","わしの機嫌を損ねれば、痛い目を見るのはお前らだぞ！","わしの機嫌を損ねれば、痛い目を見るのはお前らだぞ！","関銀屏",},
        [605] = {1501,"関銀屏","まあ、器の小さな男が偉そうに大見栄切って！ご主君様がいなければ、曹操などとっくに滎陽で死んでましたのに。","まあ、器の小さな男が偉そうに大見栄切って！ご主君様がいなければ、曹操などとっくに滎陽で死んでましたのに。","曹嵩",},
        [606] = {1501,"曹嵩","でたらめを言うな！どこの田舎から来たか知らんが、よくも大きな口を叩いたものよ！","でたらめを言うな！どこの田舎から来たか知らんが、よくも大きな口を叩いたものよ！","関銀屏",},
        [607] = {1501,"関銀屏","わが主君様が田舎者ですって！?ぶっとばしますわよ！","わが主君様が田舎者ですって！?ぶっとばしますわよ！","曹嵩",},
        [608] = {1502,"関銀屏","陶謙さんは徐州の太守ですから、名将令を持ってますわよね?","陶謙さんは徐州の太守ですから、名将令を持ってますわよね?","陶謙",},
        [609] = {1502,"陶謙","娘さん、いかにもワシは徐州の太守じゃが。名将令のことなど知らぬぞ。","娘さん、いかにもワシは徐州の太守じゃが。名将令のことなど知らぬぞ。","関銀屏",},
        [610] = {1502,"関銀屏","まさか、ないとおっしゃるの?","まさか、ないとおっしゃるの?","陶謙",},
        [611] = {1502,"陶謙","この老いぼれ、幼きころから孔孟の道に親しんでおる。欺きの心など少しも持ちはせぬよ。","この老いぼれ、幼きころから孔孟の道に親しんでおる。欺きの心など少しも持ちはせぬよ。","関銀屏",},
        [612] = {1502,"主君","まあいいさ、陶謙殿は見るからに人がよさそうだし。他の所をあたってみよう。","まあいいわ、陶謙殿は見るからに人がよさそうだし。他の所をあたってみましょう。","陶謙",},
        [613] = {1503,"張星彩","ご主君様、大変よ！","ご主君様、大変よ！","プレイヤー",},
        [614] = {1503,"主君","どうした、星彩。落ち着いてゆっくり話せ。何があった?","どうしたの、星彩。落ち着いてゆっくり話して。何があったの?","張星彩",},
        [615] = {1503,"張星彩","曹操が、陳留にいた父親の曹嵩を呼び寄せたの。途中の徐州で陶謙さんがもてなして、護衛をつけて送り出したんだけど……","曹操が、陳留にいた父親の曹嵩を呼び寄せたの。途中の徐州で陶謙さんがもてなして、護衛をつけて送り出したんだけど……","プレイヤー",},
        [616] = {1503,"関銀屏","あらあら、陶謙さんってば、曹操の歓心を買うために曹嵩を招待したのね。でも、そんなの大したことじゃありませんわ。","あらあら、陶謙さんってば、曹操の歓心を買うために曹嵩を招待したのね。でも、そんなの大したことじゃありませんわ。","張星彩",},
        [617] = {1503,"張星彩","最後まで聞いて！陶謙さんが曹嵩の護衛につけた部下の張闓って奴が、黄巾賊の残党だったのよ！","最後まで聞いて！陶謙さんが曹嵩の護衛につけた部下の張闓って奴が、黄巾賊の残党だったのよ！","関銀屏",},
        [618] = {1503,"張星彩","欲に目がくらんだ張闓は、曹嵩一家を殺して、金銀財宝を奪って山の中に逃げ去ったんだって！","欲に目がくらんだ張闓は、曹嵩一家を殺して、金銀財宝を奪って山の中に逃げ去ったんだって！","関銀屏",},
        [619] = {1503,"主君","なんてことだ……！陶謙殿の親切が裏目に出たか。確実に曹操の恨みを買うぞ！","なんてこと……！陶謙殿の親切が裏目に出たのね。確実に曹操の恨みを買うわよ！","張星彩",},
        [620] = {1503,"主君","受けた恨みは絶対に晴らす性格だし、しかも今回は父の仇……","受けた恨みは絶対に晴らす性格だし、しかも今回は父の仇……","張星彩",},
        [621] = {1503,"関銀屏","バカな曹嵩、やはり金のせいで命を落としましたわね。星彩の予言が見事に当たりましたわ。","バカな曹嵩、やはり金のせいで命を落としましたわね。星彩の予言が見事に当たりましたわ。","張星彩",},
        [622] = {1503,"張星彩","茶々を入れないで、姉さま！","茶々を入れないで、姉さま！","関銀屏",},
        [623] = {1503,"張星彩","ご主君様、この情報はもう兗州に伝わっているわ！曹操は十数万の兵を派遣して徐州を討つつもりよ。","ご主君様、この情報はもう兗州に伝わっているわ！曹操は十数万の兵を派遣して徐州を討つつもりよ。","プレイヤー",},
        [624] = {1503,"張星彩","徐州人を皆殺しにして、恨みを晴らすと言い放ったらしいわ！","徐州人を皆殺しにして、恨みを晴らすと言い放ったらしいわ！","プレイヤー",},
        [625] = {1503,"主君","徐州の人々に罪はない。急いで兗州に行って、曹操を止めなければ！","徐州の人々に罪はないわ。急いで兗州に行って、曹操を止めなければ！","張星彩",},
        [626] = {1504,"陳宮","これはこれは将軍。この前の陳留以来ですな。お久しぶりですね！","これはこれは将軍。この前の陳留以来ですな。お久しぶりですね！","プレイヤー",},
        [627] = {1504,"主君","陳宮殿、聞かせてください。中牟県にいた時、曹操をとても評価していたのに、どうして袂を分かったのです?","陳宮殿、聞かせてください。中牟県にいた時、曹操をとても評価していたのに、どうして袂を分かったのです?","陳宮",},
        [628] = {1504,"陳宮","それは呂伯奢一家のためですが……多くは言いますまい。","それは呂伯奢一家のためですが……多くは言いますまい。","プレイヤー",},
        [629] = {1504,"陳宮","最初は私も曹操には大志があると信じ、官職を捨てて彼に従ったのですが……","最初は私も曹操には大志があると信じ、官職を捨てて彼に従ったのですが……","プレイヤー",},
        [630] = {1504,"陳宮","こんなにも疑い深く、人情に欠け、残酷な決断ができる人間だとは思わなかった。それゆえ彼から離れたのです。","こんなにも疑い深く、人情に欠け、残酷な決断ができる人間だとは思わなかった。それゆえ彼から離れたのです。","プレイヤー",},
        [631] = {1504,"陳宮","陶謙とは古くから親交があります。このたびの徐州の災難にあたり、兗州へ曹操を止めに行くところなのです。","陶謙とは古くから親交があります。このたびの徐州の災難にあたり、兗州へ曹操を止めに行くところなのです。","プレイヤー",},
        [632] = {1504,"陳宮","事は急を要しますので、お先に失礼します！","事は急を要しますので、お先に失礼します！","プレイヤー",},
        [633] = {1504,"張星彩","ああっ、行ってしまったわ！ご主君様、彼に曹操が止められると思う?","ああっ、行ってしまったわ！ご主君様、彼に曹操が止められると思う?","プレイヤー",},
        [634] = {1504,"主君","なんとも言えないが……ここは私が行かないと収まらない気がするぞ。","なんとも言い難いけど……ここは私が行かないと収まらない気がするわ。","張星彩",},
        [635] = {1505,"曹操","陳宮！勝手にわしから離れておきながら、今さらどの面下げて戻ってきたのだ！","陳宮！勝手にわしから離れておきながら、今さらどの面下げて戻ってきたのだ！","陳宮",},
        [636] = {1505,"曹操","陶謙はわしの家族を殺したのだ。必ずや肝を抜き心臓をえぐって、この恨みを晴らしてくれる！お前が何を言おうと無駄だ！","陶謙はわしの家族を殺したのだ。必ずや肝を抜き心臓をえぐって、この恨みを晴らしてくれる！お前が何を言おうと無駄だ！","陳宮",},
        [637] = {1505,"陳宮","ううむ、取り付く島もないとはこのことか……これでは陶謙に合わせる顔がない！(陳宮は肩を落として出ていった)","ううむ、取り付く島もないとはこのことか……これでは陶謙に合わせる顔がない！(陳宮は肩を落として出ていった)","曹操",},
        [638] = {1505,"曹操","父の仇を生かしておくことはできん。もしこの恨みを晴らぬならば、この曹操、どうしてこの天地の間に立っていられよう！","父の仇を生かしておくことはできん。もしこの恨みを晴らぬならば、この曹操、どうしてこの天地の間に立っていられよう！","陳宮",},
        [639] = {1505,"曹操","曹仁よ！お前が軍を率い、徐州を蹂躙せよ！丸ごと父の墓に供えてくれるわ！","曹仁よ！お前が軍を率い、徐州を蹂躙せよ！丸ごと父の墓に供えてくれるわ！","陳宮",},
        [640] = {1505,"曹仁","はっ！かしこまりました！","はっ！かしこまりました！","曹操",},
        [641] = {1506,"曹仁","若き英雄将軍はやはり強かった。この曹仁、降参だ！この名将令は持っていけ！","若き英雄将軍はやはり強かった。この曹仁、降参だ！この名将令は持っていけ！","プレイヤー",},
        [642] = {1506,"主君","ありがとう。しかし曹操兄は、なぜ私と会おうとしないんだ?","ありがとう。でも曹操兄さんは、なぜ私と会おうとしないの?","曹仁",},
        [643] = {1506,"曹仁","無論、お前が仲裁に来ることを知っているからだ。しかし、仲違いはしたくはない。","無論、お前が仲裁に来ることを知っているからだ。しかし、仲違いはしたくはない。","プレイヤー",},
        [644] = {1506,"曹仁","だから自分は表に出ず、俺に兵を預けたのだ！(曹仁は退いて行った)","だから自分は表に出ず、俺に兵を預けたのだ！(曹仁は退いて行った)","プレイヤー",},
        [645] = {1506,"関銀屏","ふん、曹操もなかなかずる賢いですわね。まさか会おうともしないなんて！","ふん、曹操もなかなかずる賢いですわね。まさか会おうともしないなんて！","プレイヤー",},
        [646] = {1506,"張星彩","陳宮さんの言う通り、曹操はやっぱり疑り深くて人情に欠けるのかしら?","陳宮さんの言う通り、曹操はやっぱり疑り深くて人情に欠けるのかしら?","関銀屏",},
        [647] = {1506,"関銀屏","父の仇なんて、ただの口実に決まってますわ！曹操は陶謙さんの領地が欲しいのですわよ。それに、徐州の名将令もね！","父の仇なんて、ただの口実に決まってますわ！曹操は陶謙さんの領地が欲しいのですわよ。それに、徐州の名将令もね！","張星彩",},
        [648] = {1506,"主君","いずれにせよ、民に罪はない！曹操が私に会わないなら、徐州を助けに行こう。","いずれにせよ、民に罪はないわ！曹操が私に会わないなら、徐州を助けに行きましょう。","関銀屏",},
        [649] = {1601,"陶謙","(天を仰いで号泣し)この機会に曹操と誼を結びたかっただけなのじゃ！","(天を仰いで号泣し)この機会に曹操と誼を結びたかっただけなのじゃ！","プレイヤー",},
        [650] = {1601,"陶謙","なのに徐州の民をこんな大難に遭わせてしまうとは！ワシは徐州の罪人じゃ！","なのに徐州の民をこんな大難に遭わせてしまうとは！ワシは徐州の罪人じゃ！","プレイヤー",},
        [651] = {1601,"主君","陶謙殿、ご安心を！私が曹操を撃退してさしあげます！","陶謙殿、ご安心を！私が曹操を撃退してさしあげますわ！","陶謙",},
        [652] = {1601,"陶謙","(また天を仰いで号泣し)なんと仁義に厚い英雄じゃ！この危急の時に、徐州に救いの手を差し伸べてくださるとは！","(また天を仰いで号泣し)なんと仁義に厚い英雄じゃ！この危急の時に、徐州に救いの手を差し伸べてくださるとは！","プレイヤー",},
        [653] = {1601,"張星彩","ふふーん、当然でしょ！ご主君様は強くてカッコよくて頭も切れる、天下無敵の大英雄なんだから！","ふふーん、当然でしょ！ご主君様は強くてカッコよくて頭も切れる、天下無敵の大英雄なんだから！","プレイヤー",},
        [654] = {1601,"主君","それくらいにしてくれ、星彩。気恥ずかしくなってくる。","それくらいにしてよ、星彩。気恥ずかしいじゃない。","張星彩",},
        [655] = {1602,"糜竺","陶謙殿、私に一計が！","陶謙殿、私に一計が！","陶謙",},
        [656] = {1602,"陶謙","どんな案じゃ、言ってくれ！","どんな案じゃ、言ってくれ！","糜竺",},
        [657] = {1602,"糜竺","北海に助けを求めてはいかがでしょう。徐州とは一蓮托生、そして北海太守の孔融は孔子の二十代目の子孫。","北海に助けを求めてはいかがでしょう。徐州とは一蓮托生、そして北海太守の孔融は孔子の二十代目の子孫。","陶謙",},
        [658] = {1602,"糜竺","彼ならきっと仁義を重んじ、助けてくれるはずです。","彼ならきっと仁義を重んじ、助けてくれるはずです。","陶謙",},
        [659] = {1602,"主君","北海太守は孔融なのか！それなら人をやる必要はない、私が行きましょう！","北海太守は孔融だったの！なら人をやる必要はないわ、私が行きましょう！","糜竺",},
        [660] = {1602,"主君","実は前から興味があったのです。「梨を譲る」の話で有名な孔融……孔子の二十代目の子孫とは、一体どんな人物なのかと！","実は前から興味があったんです。「梨を譲る」の話で有名な孔融……孔子の二十代目の子孫とは、一体どんな人物なのかなって！","糜竺",},
        [661] = {1603,"孔融","どちら様ですかあ?北海に何かご用ですかあ?","どちら様ですかあ?北海に何かご用ですかあ?","張星彩",},
        [662] = {1603,"張星彩","あらー、ずいぶんちっちゃくてカワイイ子ね。でも、孔融ちゃん。うちのご主君様の前ではかしこまりなさいね。","あらー、ずいぶんちっちゃくてカワイイ子ね。でも、孔融ちゃん。うちのご主君様の前ではかしこまりなさいね。","孔融",},
        [663] = {1603,"張星彩","その名も高き大豪傑、やがては三国最強の大英雄になるお方なんだから！","その名も高き大豪傑、やがては三国最強の大英雄になるお方なんだから！","孔融",},
        [664] = {1603,"孔融","ふーん?大英雄だなんて、信じられませんねえ。かしこまるべきはそちらです。","ふーん?大英雄だなんて、信じられませんねえ。かしこまるべきはそちらです。","張星彩",},
        [665] = {1603,"孔融","小さいからって舐めないでくださいよ。「栴檀は双葉より芳し」ってことわざ、知ってます?","小さいからって舐めないでくださいよ。「栴檀は双葉より芳し」ってことわざ、知ってます?","張星彩",},
        [666] = {1603,"関銀屏","あらあら、勇ましいのねボウヤ。強がりは嫌いじゃないですけど、このお姉さまとまともにやり合ってからおっしゃい！","あらあら、勇ましいのねボウヤ。強がりは嫌いじゃないですけど、このお姉さまとまともにやり合ってからおっしゃい！","孔融",},
        [667] = {1604,"孔融","な、なんと……！本当にかの大英雄でしたか！黄巾賊を討ち、虎牢関で名を上げ、董卓を倒したという……","な、なんと……！本当にかの大英雄でしたか！黄巾賊を討ち、虎牢関で名を上げ、董卓を倒したという……","プレイヤー",},
        [668] = {1604,"孔融","ああ、なんという僥倖！実はずっと憧れていたんです！","ああ、なんという僥倖！実はずっと憧れていたんです！","プレイヤー",},
        [669] = {1604,"主君","それは願ってもない。私もお会いできて嬉しいです……ところで、曹操が今、徐州を攻めています。","それは願ってもない。私もお会いできて嬉しいですわ……ところで、曹操が今、徐州を攻めています。","孔融",},
        [670] = {1604,"主君","北海は距離も近く、一蓮托生と聞いています。救援の兵を出していただけませんか?","北海は距離も近く、一蓮托生と聞いています。救援の兵を出していただけませんか?","孔融",},
        [671] = {1604,"孔融","出したいのは山々ですが……目下、北海は黄巾賊の残党に攻められて、わが身も危うい状況でして……","出したいのは山々ですが……目下、北海は黄巾賊の残党に攻められて、わが身も危うい状況でして……","プレイヤー",},
        [672] = {1604,"孔融","幸い、武勇に優れた義士が助けに来てくれて、黄巾賊を食い止めてくれているのですが……","幸い、武勇に優れた義士が助けに来てくれて、黄巾賊を食い止めてくれているのですが……","プレイヤー",},
        [673] = {1604,"主君","武勇に優れた義士だって?いったい誰だ……いけない、腕がむずむずしてきた。その男とぜひ勝負したい！","武芸に優れた義士?いったい誰なの……いけない、腕がむずむずしてきたわ。その男とぜひ会って勝負しないと！","孔融",},
        [674] = {1605,"太史慈","むむっ……凄いな、お前は。この太史慈では相手にならぬか！父が遺したこの名将令はお前にやろう！","むむっ……凄いな、お前は。この太史慈では相手にならぬか！父が遺したこの名将令はお前にやろう！","プレイヤー",},
        [675] = {1605,"主君","ありがとう！私もだいぶ追い詰められたよ。紙一重の勝負だったな。","ありがとう！私もだいぶ追い詰められたわ。紙一重の勝負だったわね。","太史慈",},
        [676] = {1605,"孔融","お二人とも確かに勇敢ですが、なにしろ多勢に無勢。聞けば平原郡の相である劉備殿は大英雄だとか。","お二人とも確かに勇敢ですが、なにしろ多勢に無勢。聞けば平原郡の相である劉備殿は大英雄だとか。","プレイヤー",},
        [677] = {1605,"孔融","劉備殿に徐州の救援を頼んでみてはどうでしょう。応えてくれるかどうか分かりませんが……","劉備殿に徐州の救援を頼んでみてはどうでしょう。応えてくれるかどうか分かりませんが……","プレイヤー",},
        [678] = {1605,"主君","劉備殿は私の義兄弟です！仁義に厚い彼であれば、必ず応えてくれるでしょう。私が行きます！","劉備殿は私の義兄弟です！仁義に厚い彼なら、必ず応えてくれるわ。私が行きます！","孔融",},
        [679] = {1605,"主君","太史慈兄がいると心強い。一緒に来てくれないか?","太史慈兄さんがいると心強いわ。一緒に来てくれない?","孔融",},
        [680] = {1605,"太史慈","悪いがそれは無理だ。家には老いた母親がいて、世話が必要でな。本当に済まない。(太史慈は別れを告げ去った)","悪いがそれは無理だ。家には老いた母親がいて、世話が必要でな。本当に済まない。(太史慈は別れを告げ去った)","プレイヤー",},
        [681] = {1605,"主君","では、私だけで兄上のところへ行こう。事は一刻を争う、グズグズしていられない！","では、私だけで兄さんのところへ行こう。事は一刻を争う、グズグズしていられないわ！","太史慈",},
        [682] = {2018101,"貂蝉","「美なる一人有り、清揚にして婉たり、邂逅して相遇はば、我が願ひに適へり」……詩経の詩ね。","「美なる一人有り、清揚にして婉たり、邂逅して相遇はば、我が願ひに適へり」……詩経の詩ね。","",},
        [683] = {2018101,"貂蝉","恥ずかしいわ、誰が贈ってきたのかしら?","恥ずかしいわ、誰が贈ってきたのかしら?","",},
        [684] = {2018101,"貂蝉","楽女のみんなが、いつもわたくしをからかって言うの。","楽女のみんなが、いつもわたくしをからかって言うの。","",},
        [685] = {2018101,"貂蝉","容貌は天上の月よりも美しく、舞う姿は九天の仙女より麗しいって。将来わたくしを娶る、幸運な殿方は誰なのかって……","容貌は天上の月よりも美しく、舞う姿は九天の仙女より麗しいって。将来わたくしを娶る、幸運な殿方は誰なのかって……","",},
        [686] = {2018101,"貂蝉","(憧れるように)そうね……嫁ぐならもちろん、天下無双の大英雄のもとに……","(憧れるように)そうね……嫁ぐならもちろん、天下無双の大英雄のもとに……","",},
        [687] = {2018101,"張梁","でへへ、美人さん、それはオレのことかな?","でへへ、美人さん、それはオレのことかな?","貂蝉",},
        [688] = {2018101,"貂蝉","は?あなた誰、どうしてついてくるの?","は?あなた誰、どうしてついてくるの?","",},
        [689] = {2018101,"張梁","もちろん、今日が寒い日だからさ。そんな薄着じゃ寒いだろ?オレが温めてあげちゃうよ～！","もちろん、今日が寒い日だからさ。そんな薄着じゃ寒いだろ?オレが温めてあげちゃうよ～！","",},
        [690] = {2018101,"貂蝉","きゃあっ、来ないで痴漢！人を呼ぶわよ！","きゃあっ、来ないで痴漢！人を呼ぶわよ！","",},
        [691] = {2018101,"張梁","いくら叫んだって無駄だぜぇ、この辺りには人っ子一人いないからなあ！","いくら叫んだって無駄だぜぇ、この辺りには人っ子一人いないからなあ！","",},
        [692] = {2018101,"張梁","ぐへへっ、いただきまーす！(張梁が貂蝉に飛びかかり、貂蝉の美しい顔が色を失った)","ぐへへっ、いただきまーす！(張梁が貂蝉に飛びかかり、貂蝉の美しい顔が色を失った)","",},
        [693] = {2018101,"呂布","やめろ！","やめろ！","",},
        [694] = {2018102,"貂蝉","助けていただいてありがとうございます……あの、わたくし、貂蝉と申します。","助けていただいてありがとうございます……あの、わたくし、貂蝉と申します。","呂布",},
        [695] = {2018102,"貂蝉","お名前をうかがってもよろしいですか?(貂蝉が礼をして頭を上げると、呂布はたちまち見惚れてしまった)","お名前をうかがってもよろしいですか?(貂蝉が礼をして頭を上げると、呂布はたちまち見惚れてしまった)","呂布",},
        [696] = {2018102,"呂布","……俺の姓は呂、名は布、字は奉先だ。いま黄巾賊があちこちで暴れて危険なのに、お嬢さんはどうして一人で?","……俺の姓は呂、名は布、字は奉先だ。いま黄巾賊があちこちで暴れて危険なのに、お嬢さんはどうして一人で?","",},
        [697] = {2018102,"貂蝉","養父が病気なんです。町はずれに華佗という名医がおられると聞いて、診ていただこうと…","養父が病気なんです。町はずれに華佗という名医がおられると聞いて、診ていただこうと…","",},
        [698] = {2018102,"呂布","なるほど、親孝行な娘さんだ。しかし、養父とは何故また?実の両親はどうした?","なるほど、親孝行な娘さんだ。しかし、養父とは何故また?実の両親はどうした?","",},
        [699] = {2018102,"貂蝉","幼いころに両親を亡くし、養父に育てられたのです。","幼いころに両親を亡くし、養父に育てられたのです。","",},
        [700] = {2018102,"呂布","なんと奇遇な！この呂布も小さいころ両親を亡くしたが、幸い武芸に優れていたので、養子に迎えられたのだ。","なんと奇遇な！この呂布も小さいころ両親を亡くしたが、幸い武芸に優れていたので、養子に迎えられたのだ。","",},
        [701] = {2018102,"貂蝉","まあ、そうでしたか……若様も貂蝉と同じ哀れな身の上なのですね……","まあ、そうでしたか……若様も貂蝉と同じ哀れな身の上なのですね……","",},
        [702] = {2018102,"呂布","お嬢さん、他人行儀はやめて、俺のことは呂布と呼んでくれ。","お嬢さん、他人行儀はやめて、俺のことは呂布と呼んでくれ。","",},
        [703] = {2018102,"呂布","もしよければ、俺がその神医のところまで送って行こう。もしまた賊が出ても、この呂布があなたには指一本触れさせはしない。","もしよければ、俺がその神医のところまで送って行こう。もしまた賊が出ても、この呂布があなたには指一本触れさせはしない。","",},
        [704] = {2018102,"貂蝉","ありがとうございます、若……いえ、呂布さま！","ありがとうございます、若……いえ、呂布さま！","",},
        [705] = {2018103,"貂蝉","呂布さま、頭の羽飾りがぼろぼろでしたので、新しく作って差し上げました。いかがですか?","呂布さま、頭の羽飾りがぼろぼろでしたので、新しく作って差し上げました。いかがですか?","呂布",},
        [706] = {2018103,"呂布","ほう、見事だ、ありがたくいただこう。む、貂蝉、手を怪我しているようだな。痛むか?","ほう、見事だ、ありがたくいただこう。む、貂蝉、手を怪我しているようだな。痛むか?","",},
        [707] = {2018103,"貂蝉","(恥じらいながら)お恥ずかしい……うっかり針を刺してしまったのです。","(恥じらいながら)お恥ずかしい……うっかり針を刺してしまったのです。","",},
        [708] = {2018103,"貂蝉","あの……呂布さまはいつ、縁談を申し込んでくださるのですか?","あの……呂布さまはいつ、縁談を申し込んでくださるのですか?","",},
        [709] = {2018103,"呂布","貂蝉、お義父上は三公の一人、司徒の王允殿なのだ。軽く見られるわけにはいかん。","貂蝉、お義父上は三公の一人、司徒の王允殿なのだ。軽く見られるわけにはいかん。","",},
        [710] = {2018103,"呂布","位を進め大将軍になった時、改めて迎えにいこう。","位を進め大将軍になった時、改めて迎えにいこう。","",},
        [711] = {2018103,"貂蝉","わたくしは位など気にしませんのに……生涯寄り添えるお方であればいいのです。","わたくしは位など気にしませんのに……生涯寄り添えるお方であればいいのです。","",},
        [712] = {2018103,"貂蝉","養父もわたくしを可愛がってくれていますから、あなたのお気持ちさえわかれば、反対はしないでしょう。","養父もわたくしを可愛がってくれていますから、あなたのお気持ちさえわかれば、反対はしないでしょう。","",},
        [713] = {2018103,"呂布","しかし……気骨ある大丈夫たるもの、愛する女にわずかでも辛い思いをさせるわけにはいかぬのだ！","しかし……気骨ある大丈夫たるもの、愛する女にわずかでも辛い思いをさせるわけにはいかぬのだ！","",},
        [714] = {2018103,"呂布","貂蝉よ、どうか俺が将軍になる日を待っていてくれ。","貂蝉よ、どうか俺が将軍になる日を待っていてくれ。","",},
        [715] = {2018103,"呂布","その時には、天下一の馬に乗り、赤い錦に百花をあしらった晴れ装束でお前を娶りに来る！","その時には、天下一の馬に乗り、赤い錦に百花をあしらった晴れ装束でお前を娶りに来る！","",},
        [716] = {2018104,"貂蝉","呂布さまが中郎将に取り立てられ、爵位も授かったそうだわ。そろそろお養父さまにお話していいかしら。","呂布さまが中郎将に取り立てられ、爵位も授かったそうだわ。そろそろお養父さまにお話していいかしら。","王允",},
        [717] = {2018104,"王允","貂蝉よ、どうした?何か用か?","貂蝉よ、どうした?何か用か?","",},
        [718] = {2018104,"貂蝉","お養父さま、実はお話が……えっ！?(王允が突然倒れ、貂蝉が急いで助け起こす)お養父さま、どうなさいました?","お養父さま、実はお話が……えっ！?(王允が突然倒れ、貂蝉が急いで助け起こす)お養父さま、どうなさいました?","",},
        [719] = {2018104,"王允","(顔面蒼白で涙を流す)漢王室は存亡の危機にある！","(顔面蒼白で涙を流す)漢王室は存亡の危機にある！","",},
        [720] = {2018104,"王允","賊臣董卓が帝位を簒奪せんと謀っておるのだ。朝廷の文武の官では、もはや手の施しようもない。ああ、この老いぼれた体がもう少し動けば……","賊臣董卓が帝位を簒奪せんと謀っておるのだ。朝廷の文武の官では、もはや手の施しようもない。ああ、この老いぼれた体がもう少し動けば……","",},
        [721] = {2018104,"貂蝉","憂いを患っておいでなのですね?この貂蝉にお悩みを打ち明けてください。","憂いを患っておいでなのですね?この貂蝉にお悩みを打ち明けてください。","",},
        [722] = {2018104,"王允","(突然、貂蝉に向かって跪き)今や民衆は苦しみの極みにあり、君も臣も危険な状態にある。","(突然、貂蝉に向かって跪き)今や民衆は苦しみの極みにあり、君も臣も危険な状態にある。","",},
        [723] = {2018104,"王允","だが……！貂蝉、私に一つ策がある。美女連環の計だ。董卓を除けるのは、お前だけだ。","だが……！貂蝉、私に一つ策がある。美女連環の計だ。董卓を除けるのは、お前だけだ。","",},
        [724] = {2018104,"貂蝉","わたくしだけ……?はい、何なりと仰ってください。これまでお養父さまには実の子のように育てていただいて、","わたくしだけ……?はい、何なりと仰ってください。これまでお養父さまには実の子のように育てていただいて、","",},
        [725] = {2018104,"貂蝉","歌や舞、礼儀作法も学ばせていただきました。ご恩に報いるには、この身を投げ打っても足りません。","歌や舞、礼儀作法も学ばせていただきました。ご恩に報いるには、この身を投げ打っても足りません。","",},
        [726] = {2018105,"王允","董卓の周囲には手下の西涼兵が大勢いて、並の人間では近寄れん。だが……ヤツは色を好む。","董卓の周囲には手下の西涼兵が大勢いて、並の人間では近寄れん。だが……ヤツは色を好む。","貂蝉",},
        [727] = {2018105,"王允","ヤツには呂布という義理の息子がおり、その勇猛さは尋常ではない……","ヤツには呂布という義理の息子がおり、その勇猛さは尋常ではない……","",},
        [728] = {2018105,"王允","私は連環の計をもってまずお前を呂布に嫁がせると約束し、その後董卓に献上する。これによって親子を反目させ、呂布に董卓を殺させる。","私は連環の計をもってまずお前を呂布に嫁がせると約束し、その後董卓に献上する。これによって親子を反目させ、呂布に董卓を殺させる。","",},
        [729] = {2018105,"貂蝉","(貂蝉は小さな声で驚く)ええっ！?","(貂蝉は小さな声で驚く)ええっ！?","",},
        [730] = {2018105,"王允","貂蝉、顔色が悪いが、まさか嫌なのか?","貂蝉、顔色が悪いが、まさか嫌なのか?","",},
        [731] = {2018105,"貂蝉","いえ、ご心配なく。育てていただいたご恩に報いるためなら、喜んでこの身を捧げます。","いえ、ご心配なく。育てていただいたご恩に報いるためなら、喜んでこの身を捧げます。","",},
        [732] = {2018105,"王允","よくできた娘だ。国賊董卓を殺すために、どうか耐えてくれ。……万一事が漏れれば、わが一族は皆殺しになろう。","よくできた娘だ。国賊董卓を殺すために、どうか耐えてくれ。……万一事が漏れれば、わが一族は皆殺しになろう。","",},
        [733] = {2018105,"貂蝉","ご安心を、お養父さま。貂蝉は命をかけても、大義を果たしてみせます。","ご安心を、お養父さま。貂蝉は命をかけても、大義を果たしてみせます。","",},
        [734] = {2018201,"何進","おい、そこの道士さんよ。見たところ、ずいぶんとその道に通じているようだな。この何進の運命を占ってくれんか！","おい、そこの道士さんよ。見たところ、ずいぶんとその道に通じているようだな。この何進の運命を占ってくれんか！","左慈",},
        [735] = {2018201,"左慈","よかろう。ふむ、人相を見るに……額は広く、あごは丸く、目は大きく生き生きとして、耳も大きい……","よかろう。ふむ、人相を見るに……額は広く、あごは丸く、目は大きく生き生きとして、耳も大きい……","",},
        [736] = {2018201,"左慈","将来きっと大将軍となろう。そういう運命だ。","将来きっと大将軍となろう。そういう運命だ。","",},
        [737] = {2018201,"何進","ダハハッ、そりゃおかしい！わしゃただの肉屋だ、将軍なんかになれるのかね?","ダハハッ、そりゃおかしい！わしゃただの肉屋だ、将軍なんかになれるのかね?","",},
        [738] = {2018201,"左慈","信じぬのなら、なぜ占わせる?占わせておいて、なぜ笑う?","信じぬのなら、なぜ占わせる?占わせておいて、なぜ笑う?","",},
        [739] = {2018201,"左慈","大将軍になるのは間違いない。が、優柔不断ゆえ、ろくな死に方はせんだろう。","大将軍になるのは間違いない。が、優柔不断ゆえ、ろくな死に方はせんだろう。","",},
        [740] = {2018201,"何進","なんだと！?行っちまえ、このインチキ道士！俺の早死にを呪いやがって！","なんだと！?行っちまえ、このインチキ道士！俺の早死にを呪いやがって！","",},
        [741] = {2018202,"何太后","兄さん。両親が亡くなってから、よくわたしたち家族五人を養ってくれたわね。","兄さん。両親が亡くなってから、よくわたしたち家族五人を養ってくれたわね。","何進",},
        [742] = {2018202,"何太后","ここ数年は大変だったけど、ついに苦しみから解放されるわ。わたしが後宮に入るからね。","ここ数年は大変だったけど、ついに苦しみから解放されるわ。わたしが後宮に入るからね。","何進",},
        [743] = {2018202,"何進","妹よ、用心するのだぞ。もし後宮でいじめられたら、すぐわしに言え。必ずお前を支えるからな。","妹よ、用心するのだぞ。もし後宮でいじめられたら、すぐわしに言え。必ずお前を支えるからな。","",},
        [744] = {2018202,"何太后","残念だけど、兄さん。そんなか弱い育ち方はしてないわ。","残念だけど、兄さん。そんなか弱い育ち方はしてないわ。","",},
        [745] = {2018202,"何太后","きっと将来皇后になって、高官に任命してあげる。二度と辛い思いはさせないわ。","きっと将来皇后になって、高官に任命してあげる。二度と辛い思いはさせないわ。","",},
        [746] = {2018203,"何太后","兄さん、ついに皇后になったわ！これも帝の寵愛を得て、世継ぎになる皇子弁を産んだおかげよ。","兄さん、ついに皇后になったわ！これも帝の寵愛を得て、世継ぎになる皇子弁を産んだおかげよ。","",},
        [747] = {2018203,"何進","そりゃめでたい！わが一族もこれで大出世できるぞ！","そりゃめでたい！わが一族もこれで大出世できるぞ！","何太后",},
        [748] = {2018203,"何太后","兄さんこそ、黄巾の乱で大功を立て、馬元義を捕らえたそうね！さすがは大将軍さま！","兄さんこそ、黄巾の乱で大功を立て、馬元義を捕らえたそうね！さすがは大将軍さま！","",},
        [749] = {2018203,"何進","ダハハハッ、そうだ！わしは大将軍になった。これでお前を守ってやれるぞ！","ダハハハッ、そうだ！わしは大将軍になった。これでお前を守ってやれるぞ！","",},
        [750] = {2018203,"何太后","兄さん、帝は世継ぎを変えようとしているわ。王美人が産んだ協皇子を太子に立てたいみたい。ねえ、なにか打つ手はないかしら?","兄さん、帝は世継ぎを変えようとしているわ。王美人が産んだ協皇子を太子に立てたいみたい。ねえ、なにか打つ手はないかしら?","",},
        [751] = {2018203,"何進","安心しろ、妹よ。帝が太子を弟にすげ替えようとしても無理だ。そんなことは、わしが絶対にさせん！","安心しろ、妹よ。帝が太子を弟にすげ替えようとしても無理だ。そんなことは、わしが絶対にさせん！","",},
        [752] = {2018204,"何進","曹操や袁紹が十常侍を誅殺するよう勧めてきた。どんな手を使っても、張譲を殺してやる！","曹操や袁紹が十常侍を誅殺するよう勧めてきた。どんな手を使っても、張譲を殺してやる！","何太后",},
        [753] = {2018204,"何太后","だめよ、兄さん！張譲は前にわたしを救ってくれたの。張譲がいなければ、とっくに皇后を廃されていたわ！","だめよ、兄さん！張譲は前にわたしを救ってくれたの。張譲がいなければ、とっくに皇后を廃されていたわ！","",},
        [754] = {2018204,"何進","いいか、太子劉弁はまだ幼い。わが一族が朝廷に根を張るためには、張譲を除かねばならんのだ。","いいか、太子劉弁はまだ幼い。わが一族が朝廷に根を張るためには、張譲を除かねばならんのだ。","",},
        [755] = {2018204,"何進","でなければ、ヤツは遅かれ早かれ獅子身中の虫となろう。","でなければ、ヤツは遅かれ早かれ獅子身中の虫となろう。","",},
        [756] = {2018204,"何太后","兄さん、やめて。とにかく張譲を殺してはだめよ。","兄さん、やめて。とにかく張譲を殺してはだめよ。","",},
        [757] = {2018204,"何進","ううむ……分かった、別の方法を考える！","ううむ……分かった、別の方法を考える！","",},
        [758] = {2018205,"張譲","おのれ何進め、よくも私を殺そうなどと謀りましたね。他の宦官が密かに知らせてくれなければ、危ういところでした……","おのれ何進め、よくも私を殺そうなどと謀りましたね。他の宦官が密かに知らせてくれなければ、危ういところでした……","",},
        [759] = {2018205,"張譲","いいでしょう。私も遠慮しません。誰か！太后の命であると偽って何進を宮殿に招き入れ、誅殺するのです！","いいでしょう。私も遠慮しません。誰か！太后の命であると偽って何進を宮殿に招き入れ、誅殺するのです！","",},
        [760] = {2018206,"袁紹","この太后の詔は、間違いなく十常侍の罠です。大将軍、行ってはなりません。必ず災いを受けましょう。","この太后の詔は、間違いなく十常侍の罠です。大将軍、行ってはなりません。必ず災いを受けましょう。","何進",},
        [761] = {2018206,"何進","なんの災いがある?太后はわしの実の妹だぞ！","なんの災いがある?太后はわしの実の妹だぞ！","",},
        [762] = {2018206,"袁紹","どうしても参内されるというなら、不測の事態に備え、この袁紹が兵を連れて護衛いたしましょう。","どうしても参内されるというなら、不測の事態に備え、この袁紹が兵を連れて護衛いたしましょう。","",},
        [763] = {2018206,"何進","わしの妹は当代の太后で、わしは当代の大将軍だぞ。誰がわしを殺せるか！","わしの妹は当代の太后で、わしは当代の大将軍だぞ。誰がわしを殺せるか！","",},
        [764] = {2018206,"何進","それに、これが十常侍の陰謀なら、なおさら参内して妹を守らねばならん！","それに、これが十常侍の陰謀なら、なおさら参内して妹を守らねばならん！","",},
        [765] = {2018301,"華雄","李傕 ！おいクソ野郎、いるか！来い、飲みに行くぞ！","李傕 ！おいクソ野郎、いるか！来い、飲みに行くぞ！","李傕",},
        [766] = {2018301,"李傕","ああ、いるでぇ。飲みに行くのはええが、持ち合わせがのう……","ああ、いるでぇ。飲みに行くのはええが、持ち合わせがのう……","",},
        [767] = {2018301,"華雄","なんだ、金欠か?いいさ、アニキにおごってもらおうぜ、遠慮すんな。","なんだ、金欠か?いいさ、アニキにおごってもらおうぜ、遠慮すんな。","",},
        [768] = {2018302,"董卓","十八路諸侯はすでに虎牢関に到着しおったぞ。誰が迎え撃つ?","十八路諸侯はすでに虎牢関に到着しおったぞ。誰が迎え撃つ?","華雄",},
        [769] = {2018302,"呂布","心配するな、父上。関外の諸侯など塵芥に過ぎぬ。虎狼の軍をもってことごとく首を斬り、都の門に吊るしてくれよう。","心配するな、父上。関外の諸侯など塵芥に過ぎぬ。虎狼の軍をもってことごとく首を斬り、都の門に吊るしてくれよう。","",},
        [770] = {2018302,"華雄","鶏を裂くのに牛刀は必要ねえ。呂布殿が出るまでもねえですよ。俺がヤツらの首を斬ってきます！","鶏を裂くのに牛刀は必要ねえ。呂布殿が出るまでもねえですよ。俺がヤツらの首を斬ってきます！","",},
        [771] = {2018303,"董卓","華雄よ、大手柄じゃあ。鮑忠を斬り、祖茂を斬り、孫堅まで打ちのめすとは、見事見事！よし、華雄を都督にしてやろう！","華雄よ、大手柄じゃあ。鮑忠を斬り、祖茂を斬り、孫堅まで打ちのめすとは、見事見事！よし、華雄を都督にしてやろう！","",},
        [772] = {2018303,"華雄","ありがとうごぜえます、董卓さま！","ありがとうごぜえます、董卓さま！","",},
        [773] = {2018304,"華雄","(長い竿に孫堅の赤い頭巾を吊るして挑発しながら)オラオラァ！意気地なしども、次は誰の番だぁ！?","(長い竿に孫堅の赤い頭巾を吊るして挑発しながら)オラオラァ！意気地なしども、次は誰の番だぁ！?","袁紹",},
        [774] = {2018304,"袁紹","残念だ。こんなことなら、配下の顔良と文醜の二大将を連れてくるのだった。誰か、あの華雄とかいう生意気な輩に挑む者はおらんか?","残念だ。こんなことなら、配下の顔良と文醜の二大将を連れてくるのだった。誰か、あの華雄とかいう生意気な輩に挑む者はおらんか?","",},
        [775] = {2018304,"関羽","ではわしが、華雄の首を斬って献上しよう。ああ、酒はそこに置いておいてくだされ。冷めぬうちに戻ってくる。","ではわしが、華雄の首を斬って献上しよう。ああ、酒はそこに置いておいてくだされ。冷めぬうちに戻ってくる。","",},
        [776] = {2018305,"華雄","潘鳳とやらは斬ってやったぜ！次は誰だ、お前か～！?","潘鳳とやらは斬ってやったぜ！次は誰だ、お前か～！?","",},
        [777] = {2018305,"関羽","身の程を知らぬようだな、華雄！","身の程を知らぬようだな、華雄！","",},
        [778] = {2018305,"華雄","うおっ……！?すごい殺気だ。それになんて赤い顔だ、酔っ払いか?！","うおっ……！?すごい殺気だ。それになんて赤い顔だ、酔っ払いか?！","",},
        [779] = {2018305,"華雄","この赤ら顔の大男、強すぎる……ここで会ったのも運命か……太刀打ちできん……！","この赤ら顔の大男、強すぎる……ここで会ったのも運命か……太刀打ちできん……！","",},
        [780] = {2018401,"荀攸","叔父上、叔父上、大変です！","叔父上、叔父上、大変です！","荀彧",},
        [781] = {2018401,"荀彧","荀攸、何を慌てているのです?","荀攸、何を慌てているのです?","荀攸",},
        [782] = {2018401,"荀攸","以前、中常侍の唐衡が、彼の養女をあなたの妻にすると約束しましたが、その女性が年頃になったようです。","以前、中常侍の唐衡が、彼の養女をあなたの妻にすると約束しましたが、その女性が年頃になったようです。","荀彧",},
        [783] = {2018401,"荀攸","我ら潁川の荀氏は、聖人荀子より後、代々名門の一族。宦官の娘を妻にすれば、人々の非難を招きませんか?","我ら潁川の荀氏は、聖人荀子より後、代々名門の一族。宦官の娘を妻にすれば、人々の非難を招きませんか?","荀彧",},
        [784] = {2018401,"荀攸","唐衡はとっくにこの世を去っています。結婚は考え直しては?","唐衡はとっくにこの世を去っています。結婚は考え直しては?","荀彧",},
        [785] = {2018401,"荀彧","愚か者！すでに決まった縁談をもし断れば、それこそ信用を失うでしょう！","愚か者！すでに決まった縁談をもし断れば、それこそ信用を失うでしょう！","荀攸",},
        [786] = {2018401,"荀彧","人として節操を守るべきです。唐衡が中常侍だからといって、その娘に罪はない。","人として節操を守るべきです。唐衡が中常侍だからといって、その娘に罪はない。","荀攸",},
        [787] = {2018401,"荀彧","この荀彧は若くして名を成し、正しく生きてきたつもりです。他人からの中傷など、恐れる必要はありません。","この荀彧は若くして名を成し、正しく生きてきたつもりです。他人からの中傷など、恐れる必要はありません。","荀攸",},
        [788] = {2018401,"荀攸","叔父上の教え、しかと承りました。","叔父上の教え、しかと承りました。","荀彧",},
        [789] = {2018402,"曹操","天子の車は洛陽に戻った。この事態を、皆はどう見る?","天子の車は洛陽に戻った。この事態を、皆はどう見る?","荀彧",},
        [790] = {2018402,"荀彧","かつて晋の文公は周の襄王を受け入れたことで、諸侯を服従させました。漢の高祖は楚の義帝の葬儀を行ったことで、天下の民心を集めました。","かつて晋の文公は周の襄王を受け入れたことで、諸侯を服従させました。漢の高祖は楚の義帝の葬儀を行ったことで、天下の民心を集めました。","曹操",},
        [791] = {2018402,"荀彧","今、天子は難を避けて都を出ました。今こそ義兵を挙げ、天子を奉じて民の信望を集めるべきです。それこそが最善の策と思われます。","今、天子は難を避けて都を出ました。今こそ義兵を挙げ、天子を奉じて民の信望を集めるべきです。それこそが最善の策と思われます。","曹操",},
        [792] = {2018402,"荀彧","早く動かねば、他の者が先んじてしまいます。","早く動かねば、他の者が先んじてしまいます。","曹操",},
        [793] = {2018402,"曹操","うむ。荀彧よ、お前もそう考えるか。わしの意見と一致するな。すぐに兵を率いて洛陽へ行き、天子を奉じて、都を許昌へ移すのだ！","うむ。荀彧よ、お前もそう考えるか。わしの意見と一致するな。すぐに兵を率いて洛陽へ行き、天子を奉じて、都を許昌へ移すのだ！","荀彧",},
        [794] = {2018403,"曹操","現在、我が軍は官渡を守っているが、八月から九月が終われば、軍事力は次第に衰え、食料も底をつくだろう。","現在、我が軍は官渡を守っているが、八月から九月が終われば、軍事力は次第に衰え、食料も底をつくだろう。","荀攸",},
        [795] = {2018403,"曹操","わしは官渡を捨て、許昌に戻りたいと思うが、皆はどう考える?","わしは官渡を捨て、許昌に戻りたいと思うが、皆はどう考える?","荀攸",},
        [796] = {2018403,"荀攸","はい将軍、手紙で我が叔父に意見を聞くのはどうでしょう?","はい将軍、手紙で我が叔父に意見を聞くのはどうでしょう?","曹操",},
        [797] = {2018403,"曹操","そうだな……誰か、急いで許昌へ人をやり、この手紙を届けさせろ。","そうだな……誰か、急いで許昌へ人をやり、この手紙を届けさせろ。","荀攸",},
        [798] = {2018403,"荀攸","将軍！叔父から手紙が届きました。ご覧ください！","将軍！叔父から手紙が届きました。ご覧ください！","曹操",},
        [799] = {2018403,"曹操","うむ……(荀彧からの手紙を読む)袁紹は官渡に大軍を集めています。もし袁紹と決着を付けたいなら、今こそ不利を承知で戦うべきです。","うむ……(荀彧からの手紙を読む)袁紹は官渡に大軍を集めています。もし袁紹と決着を付けたいなら、今こそ不利を承知で戦うべきです。","荀攸",},
        [800] = {2018403,"曹操","できなければ、必ず相手に付け込まれるでしょう。これは天下分け目の戦いです。","できなければ、必ず相手に付け込まれるでしょう。これは天下分け目の戦いです。","荀攸",},
        [801] = {2018403,"曹操","袁紹軍は数こそ多いものの、上手く兵を使えていません。こちらが守りを固め、要所を決して通さなければ、","袁紹軍は数こそ多いものの、上手く兵を使えていません。こちらが守りを固め、要所を決して通さなければ、","荀攸",},
        [802] = {2018403,"曹操","敵の勢いは衰え、必ず変化が起こるでしょう。この策を用いるなら、決してその機を逃してはなりません。","敵の勢いは衰え、必ず変化が起こるでしょう。この策を用いるなら、決してその機を逃してはなりません。","荀攸",},
        [803] = {2018403,"曹操","はっはっは！荀彧はやはり王佐の才よ。諸将に命じる、官渡を死守せよ！そして機会を待つのだ！","はっはっは！荀彧はやはり王佐の才よ。諸将に命じる、官渡を死守せよ！そして機会を待つのだ！","荀攸",},
        [804] = {2018404,"董昭","古より、人臣として丞相ほどの功労者はおりません。苦節三十余年、悪人ども退治し、民衆の害を除き、漢王室を復興されました。","古より、人臣として丞相ほどの功労者はおりません。苦節三十余年、悪人ども退治し、民衆の害を除き、漢王室を復興されました。","荀彧",},
        [805] = {2018404,"董昭","ここで魏公の位を受け、九錫を加えられ、この功績と徳行を天下に明らかにすべきでしょう。","ここで魏公の位を受け、九錫を加えられ、この功績と徳行を天下に明らかにすべきでしょう。","荀彧",},
        [806] = {2018404,"荀彧","それはいけません。もともと丞相が義兵を挙げたのは、漢王室を助けるためでした。忠節を貫き、あくまで謙虚な態度を守るべきです。","それはいけません。もともと丞相が義兵を挙げたのは、漢王室を助けるためでした。忠節を貫き、あくまで謙虚な態度を守るべきです。","董昭",},
        [807] = {2018404,"荀彧","君子は徳をもって人を愛すると言います。私利私欲に走ってはなりません。","君子は徳をもって人を愛すると言います。私利私欲に走ってはなりません。","董昭",},
        [808] = {2018404,"董昭","多くの者が望んでいることを、あなた一人で止められるおつもりか?","多くの者が望んでいることを、あなた一人で止められるおつもりか?","荀彧",},
        [809] = {2018404,"荀彧","そういうことになるのなら、致し方ありません。こんな日が来るのを望んではいませんでしたが。","そういうことになるのなら、致し方ありません。こんな日が来るのを望んではいませんでしたが。","董昭",},
        [810] = {2018404,"曹操","……(曹操はにわかに顔色を変え、心の中で荀彧を深く恨んだ)","……(曹操はにわかに顔色を変え、心の中で荀彧を深く恨んだ)","荀彧",},
        [811] = {2018405,"曹操","荀彧は病に倒れ、寿春にいるそうだ。誰か、この箱を寿春までとどけよ！","荀彧は病に倒れ、寿春にいるそうだ。誰か、この箱を寿春までとどけよ！","",},
        [812] = {2018405,"荀攸","叔父上、曹操殿が箱を送ってきました。上には殿の直筆の封印があるようです。","叔父上、曹操殿が箱を送ってきました。上には殿の直筆の封印があるようです。","",},
        [813] = {2018405,"荀彧","(箱を開けるが、何も入っていない)……私は、曹操殿に長年付き従い、波乱の道をともに歩んできました。","(箱を開けるが、何も入っていない)……私は、曹操殿に長年付き従い、波乱の道をともに歩んできました。","荀攸",},
        [814] = {2018405,"荀彧","どれほどお互いを理解しているか、普通の人には計り知れないでしょうね。","どれほどお互いを理解しているか、普通の人には計り知れないでしょうね。","荀攸",},
        [815] = {2018405,"荀攸","これはどういう意味なのですか?","これはどういう意味なのですか?","荀彧",},
        [816] = {2018405,"荀彧","荀彧は今日を限りに漢にとって用済みだということです。この荀彧、すべて受け入れました。","荀彧は今日を限りに漢にとって用済みだということです。この荀彧、すべて受け入れました。","荀攸",},
        [817] = {2018405,"荀彧","抗うつもりはありません。(荀彧は人払いをし、毒を飲んで自殺した。四十九歳だった)","抗うつもりはありません。(荀彧は人払いをし、毒を飲んで自殺した。四十九歳だった)","荀攸",},
        [818] = {2018501,"沙摩柯","ゴホッゴホッ！どうも風邪をひいたようだ。だがゼニがねえ。娘さんよ、少し恵んでくれないか。医者に診てもらいたいのだ。","ゴホッゴホッ！どうも風邪をひいたようだ。だがゼニがねえ。娘さんよ、少し恵んでくれないか。医者に診てもらいたいのだ。","",},
        [819] = {2018501,"町娘","あら、かわいそうに。きっと流行り病ね。今回のはかなりひどいらしいわよ。","あら、かわいそうに。きっと流行り病ね。今回のはかなりひどいらしいわよ。","沙摩柯",},
        [820] = {2018501,"町娘","大賢良師・張角さまの符水なら治せるらしいから、頼んでみたらどうかしら。","大賢良師・張角さまの符水なら治せるらしいから、頼んでみたらどうかしら。","沙摩柯",},
        [821] = {2018501,"沙摩柯","おお！娘さんよ、ありがとう。大賢良師さんよ、お願いだ、わしを助けてくれ！","おお！娘さんよ、ありがとう。大賢良師さんよ、お願いだ、わしを助けてくれ！","",},
        [822] = {2018501,"張角","わしは南華老仙より『太平要術』の書を授かり、太平道を創立した。すでに信者は数十万人おる！","わしは南華老仙より『太平要術』の書を授かり、太平道を創立した。すでに信者は数十万人おる！","沙摩柯",},
        [823] = {2018501,"張角","汝らもわしの太平道に入れば、病や災いはきっと除かれ、平穏に暮らせよう！","汝らもわしの太平道に入れば、病や災いはきっと除かれ、平穏に暮らせよう！","沙摩柯",},
        [824] = {2018501,"沙摩柯","へえー、入るだけで病気が治るってのかい。そりゃあいい、わしは太平道に入るぞ！","へえー、入るだけで病気が治るってのかい。そりゃあいい、わしは太平道に入るぞ！","張角",},
        [825] = {2018502,"張角","この世で最も得難いものの一つは、民心である。だが今、民心はすでに従えた。","この世で最も得難いものの一つは、民心である。だが今、民心はすでに従えた。","",},
        [826] = {2018502,"張角","この勢いに乗じて天下を取らぬのは、誠に惜しい。よって、今日よりわしは自らを天公将軍と名乗る。","この勢いに乗じて天下を取らぬのは、誠に惜しい。よって、今日よりわしは自らを天公将軍と名乗る。","",},
        [827] = {2018502,"張宝","兄上兄上、私は?","兄上兄上、私は?","張角",},
        [828] = {2018502,"張角","お前は地公将軍だ。","お前は地公将軍だ。","張宝",},
        [829] = {2018502,"張梁","兄者兄者、オレは?","兄者兄者、オレは?","張角",},
        [830] = {2018502,"張角","お前は人公将軍だ。","お前は人公将軍だ。","張梁",},
        [831] = {2018502,"張角","今、漢の命運は尽き、大聖人が現れた。汝らは皆、天に従い正しい主について、この後の太平を楽しむのだ。","今、漢の命運は尽き、大聖人が現れた。汝らは皆、天に従い正しい主について、この後の太平を楽しむのだ。","張梁",},
        [832] = {2018502,"張角","はっはっは！蒼天すでに死す、黄天まさに立つべし。歳は甲子に在りて、天下大吉！わしの真気をもって、天地の万物を統べるのだ！","はっはっは！蒼天すでに死す、黄天まさに立つべし。歳は甲子に在りて、天下大吉！わしの真気をもって、天地の万物を統べるのだ！","張梁",},
        [833] = {2018503,"主君","張角よ、今日がお前の最期の日だ。おとなしく我が剣のサビとなれ！","張角よ、今日がお前の最期の日よ。おとなしく我が剣のサビとなりなさい！","張角",},
        [834] = {2018503,"張角","黄天も……死すか！","黄天も……死すか！","プレイヤー",},
        [835] = {2018601,"袁術","おい、孫堅とやらが黄巾討伐で功を立てたと聞いたが、どんなヤツなのだ?","おい、孫堅とやらが黄巾討伐で功を立てたと聞いたが、どんなヤツなのだ?","",},
        [836] = {2018601,"袁術の手下","孫氏は代々、呉で役人を務め、家は富春にあります。","孫氏は代々、呉で役人を務め、家は富春にあります。","袁術",},
        [837] = {2018601,"袁術の手下","その孫堅が生まれる前、祖先の墓から光が放たれ、五色の雲が天に連なり、数里に渡って広がったそうです。","その孫堅が生まれる前、祖先の墓から光が放たれ、五色の雲が天に連なり、数里に渡って広がったそうです。","袁術",},
        [838] = {2018601,"袁術","おお、そんな不思議なことが！","おお、そんな不思議なことが！","",},
        [839] = {2018601,"袁術の手下","地元の古老たちは「これはめでたい兆しだ。やがて孫家の勢いは盛んになろう」と噂しました。","地元の古老たちは「これはめでたい兆しだ。やがて孫家の勢いは盛んになろう」と噂しました。","袁術",},
        [840] = {2018601,"袁術の手下","生まれてきた孫堅の容貌は、果たして非凡なものでした。","生まれてきた孫堅の容貌は、果たして非凡なものでした。","袁術",},
        [841] = {2018601,"袁術の手下","孫堅は若くして三県の県丞を歴任し、その名声は高く、後に反乱軍を討伐して功を立て、長沙太守へと出世しました。","孫堅は若くして三県の県丞を歴任し、その名声は高く、後に反乱軍を討伐して功を立て、長沙太守へと出世しました。","袁術",},
        [842] = {2018601,"袁術","なるほどなるほど、よくわかった。しかし、そやつがいくら有能でも、我が四世三公の名門・袁家とは比較になるまい。","なるほどなるほど、よくわかった。しかし、そやつがいくら有能でも、我が四世三公の名門・袁家とは比較になるまい。","",},
        [843] = {2018601,"袁術の手下","殿。この者が有能である以上、配下に加え、共に董卓を討った方がよろしいかと存じます。","殿。この者が有能である以上、配下に加え、共に董卓を討った方がよろしいかと存じます。","袁術",},
        [844] = {2018601,"袁術","うむ……まあよかろう。では、そやつを破虜将軍と予州刺史にするよう奏上しよう。","うむ……まあよかろう。では、そやつを破虜将軍と予州刺史にするよう奏上しよう。","",},
        [845] = {2018602,"劉表","孫堅よ、なぜわしの荊州を通るのか?","孫堅よ、なぜわしの荊州を通るのか?","孫堅",},
        [846] = {2018602,"孫堅","決まっている。江東に帰ることに決めたのだ。","決まっている。江東に帰ることに決めたのだ。","劉表",},
        [847] = {2018602,"孫堅","最初に袁紹や袁術らとともに義兵を挙げた時、その目的は社稷を救わんがためだった。","最初に袁紹や袁術らとともに義兵を挙げた時、その目的は社稷を救わんがためだった。","劉表",},
        [848] = {2018602,"孫堅","今、逆賊は一掃されたが、今度は内輪で争っている。実くだらん。もはや志を共し、状況を挽回しようとする者はおらんのだ。","今、逆賊は一掃されたが、今度は内輪で争っている。実くだらん。もはや志を共し、状況を挽回しようとする者はおらんのだ。","劉表",},
        [849] = {2018602,"劉表","そうか、そうか。江東に帰りたければ帰ればよいが、玉璽は朝廷の宝だ。それを隠し持って、何をするつもりだ?","そうか、そうか。江東に帰りたければ帰ればよいが、玉璽は朝廷の宝だ。それを隠し持って、何をするつもりだ?","孫堅",},
        [850] = {2018602,"孫堅","(激怒して)お前には関係ないことだ！戦いたいなら受けて立つぞ！","(激怒して)お前には関係ないことだ！戦いたいなら受けて立つぞ！","劉表",},
        [851] = {2018603,"韓当","将軍、今日は風が吹き荒れ、本隊の旗竿が折れてしまいました。不吉の兆しです。出撃はおやめください。","将軍、今日は風が吹き荒れ、本隊の旗竿が折れてしまいました。不吉の兆しです。出撃はおやめください。","孫堅",},
        [852] = {2018603,"孫堅","この程度で兵を退けようか！わしは幾多の戦いで勝ち続けたのだ。襄陽を落とすなど、あっという間よ。","この程度で兵を退けようか！わしは幾多の戦いで勝ち続けたのだ。襄陽を落とすなど、あっという間よ。","韓当",},
        [853] = {2018603,"蒯良","夜に天文を見ましたところ、将星が一つ落ちかかっていました。","夜に天文を見ましたところ、将星が一つ落ちかかっていました。","孫堅",},
        [854] = {2018603,"蒯良","占星術では、これは孫堅のことです。殿、速やかに袁紹殿に書を送り、助けを求めるべきです。","占星術では、これは孫堅のことです。殿、速やかに袁紹殿に書を送り、助けを求めるべきです。","孫堅",},
        [855] = {2018603,"劉表","今、襄陽は囲まれている。誰が包囲を突破する?","今、襄陽は囲まれている。誰が包囲を突破する?","孫堅",},
        [856] = {2018603,"呂公","私が行こう！","私が行こう！","劉表",},
        [857] = {2018603,"蒯良","行っていただけるなら、私の計に従ってください。","行っていただけるなら、私の計に従ってください。","",},
        [858] = {2018603,"蒯良","五百の軍馬を与えますから、弓に優れた者を多く連れ、陣を飛び出したらすぐ峴山に逃げてください。","五百の軍馬を与えますから、弓に優れた者を多く連れ、陣を飛び出したらすぐ峴山に逃げてください。","",},
        [859] = {2018603,"蒯良","孫堅は必ず軍を率いて追ってきますから、手分けして百人を山に登らせ、石を集めて準備させます。","孫堅は必ず軍を率いて追ってきますから、手分けして百人を山に登らせ、石を集めて準備させます。","",},
        [860] = {2018603,"蒯良","別の百人には弓を持たせ、林に潜ませてください。そして敵を待ち伏せ場所まで誘き寄せたら、一斉に矢と石を浴びせるのです。","別の百人には弓を持たせ、林に潜ませてください。そして敵を待ち伏せ場所まで誘き寄せたら、一斉に矢と石を浴びせるのです。","",},
        [861] = {2018603,"呂公","おおっ、良い策だ！","おおっ、良い策だ！","蒯良",},
        [862] = {2018604,"兵士","将軍、一軍の兵馬が囲みを破り、峴山の方へ去りました。","将軍、一軍の兵馬が囲みを破り、峴山の方へ去りました。","孫堅",},
        [863] = {2018604,"孫堅","よし。わしが兵馬を連れ、急ぎ追う！","よし。わしが兵馬を連れ、急ぎ追う！","",},
        [864] = {2018604,"兵士","将軍、くれぐれも待ち伏せにご注意を！","将軍、くれぐれも待ち伏せにご注意を！","孫堅",},
        [865] = {2018604,"孫堅","(忠告を無視して馬で追いかける)おのれ逃さぬぞ、止まれ！","(忠告を無視して馬で追いかける)おのれ逃さぬぞ、止まれ！","",},
        [866] = {2018604,"兵士","将軍！(山の上から石が降り注ぎ、林の中から矢が乱れ飛ぶ。孫堅は石と矢に当たり討ち死にした。三十七歳だった)","将軍！(山の上から石が降り注ぎ、林の中から矢が乱れ飛ぶ。孫堅は石と矢に当たり討ち死にした。三十七歳だった)","孫堅",},
        [867] = {2018701,"黄県郡守","いま州と郡の役所の間で揉め事があってな。洛陽の有司(刑罰を司る役人)に裁決を仰ぐことにした。","いま州と郡の役所の間で揉め事があってな。洛陽の有司(刑罰を司る役人)に裁決を仰ぐことにした。","",},
        [868] = {2018701,"侍妾","それでしたら、先に有司に訴状を届けて主張したほうが、有利になるでしょうね。","それでしたら、先に有司に訴状を届けて主張したほうが、有利になるでしょうね。","",},
        [869] = {2018701,"黄県郡守","だとしたら、誰に任せるべきか?","だとしたら、誰に任せるべきか?","",},
        [870] = {2018701,"侍妾","推薦したい者がおりますわ。太史慈といいまして、歳は二十一。身の丈は七尺七寸で、長い腕で弓を得意とし、","推薦したい者がおりますわ。太史慈といいまして、歳は二十一。身の丈は七尺七寸で、長い腕で弓を得意とし、","",},
        [871] = {2018701,"侍妾","狙った的は決して外さないほどの達人だと聞きます。この者を遣わしては?","狙った的は決して外さないほどの達人だと聞きます。この者を遣わしては?","",},
        [872] = {2018701,"黄県郡守","よし、ではお前の言う通り、太史慈を大至急洛陽へ派遣し、訴状を届けさせよう！(太史慈は昼夜を問わず進み、洛陽に到着した)","よし、ではお前の言う通り、太史慈を大至急洛陽へ派遣し、訴状を届けさせよう！(太史慈は昼夜を問わず進み、洛陽に到着した)","",},
        [873] = {2018702,"太史慈","(役所の門前で)貴殿も訴状を届けに来たのですか?","(役所の門前で)貴殿も訴状を届けに来たのですか?","州吏",},
        [874] = {2018702,"州吏","ええ、そうですけど。","ええ、そうですけど。","太史慈",},
        [875] = {2018702,"太史慈","書状はどこです?署名に間違いはありませんか?ちょっと見てあげましょう。(州吏が訴状を取り出すと、太史慈は小刀で切り裂いた)","書状はどこです?署名に間違いはありませんか?ちょっと見てあげましょう。(州吏が訴状を取り出すと、太史慈は小刀で切り裂いた)","州吏",},
        [876] = {2018702,"州吏","ああっ、私の訴状が！?なぜ破いたのです！?","ああっ、私の訴状が！?なぜ破いたのです！?","太史慈",},
        [877] = {2018702,"太史慈","失礼、ついはずみで。しかし、もしそちらが訴状を出さなければ、こちらが破くこともなかったのです。","失礼、ついはずみで。しかし、もしそちらが訴状を出さなければ、こちらが破くこともなかったのです。","州吏",},
        [878] = {2018702,"太史慈","我々はどちらも罰を受けるでしょう。ここは座して死を待つより、一緒に逃げませんか?少なくとも命は助かります。","我々はどちらも罰を受けるでしょう。ここは座して死を待つより、一緒に逃げませんか?少なくとも命は助かります。","州吏",},
        [879] = {2018702,"州吏","うぬぬ……仕方ない、分かった！","うぬぬ……仕方ない、分かった！","太史慈",},
        [880] = {2018702,"太史慈","そうと決まれば、急いで城門を出ましょう。","そうと決まれば、急いで城門を出ましょう。","州吏",},
        [881] = {2018702,"太史慈","(その後、太史慈はこっそり洛陽に戻って郡の訴状を届け、使命を果たしたことで有名になった)","(その後、太史慈はこっそり洛陽に戻って郡の訴状を届け、使命を果たしたことで有名になった)","州吏",},
        [882] = {2018703,"太史慈","今孫策を捕らえずに、いつやるというのだ！度胸のあるヤツは俺についてこい！","今孫策を捕らえずに、いつやるというのだ！度胸のあるヤツは俺についてこい！","孫策",},
        [883] = {2018703,"孫策","お前は何者だ?","お前は何者だ?","太史慈",},
        [884] = {2018703,"太史慈","俺は東莱の太史慈、孫策を捕らえに来た！お前が孫策か?","俺は東莱の太史慈、孫策を捕らえに来た！お前が孫策か?","孫策",},
        [885] = {2018703,"孫策","いかにも俺だ。いいだろう、かかって来るがよい！お前などを恐れる孫策ではないぞ！","いかにも俺だ。いいだろう、かかって来るがよい！お前などを恐れる孫策ではないぞ！","太史慈",},
        [886] = {2018703,"太史慈","こちらのセリフよ！いざ、勝負だ！","こちらのセリフよ！いざ、勝負だ！","孫策",},
        [887] = {2018703,"太史慈","(太史慈は馬に乗り、槍を取って孫策に打ちかかり、孫策もこれを槍で迎え撃った。五十合戦っても勝負がつかない凄まじい戦いとなった)","(太史慈は馬に乗り、槍を取って孫策に打ちかかり、孫策もこれを槍で迎え撃った。五十合戦っても勝負がつかない凄まじい戦いとなった)","孫策",},
        [888] = {2018801,"小喬","お姉ちゃん、お姉ちゃん！お姉ちゃんは将来、どんな男の人と結婚したい?","お姉ちゃん、お姉ちゃん！お姉ちゃんは将来、どんな男の人と結婚したい?","大喬",},
        [889] = {2018801,"大喬","あら、何よ突然。小喬は本当にからかうのが好きね。","あら、何よ突然。小喬は本当にからかうのが好きね。","小喬",},
        [890] = {2018801,"小喬","あれあれ～?顔が赤いよ、お姉ちゃん。もしかして、もう意中の人がいたりとか?","あれあれ～?顔が赤いよ、お姉ちゃん。もしかして、もう意中の人がいたりとか?","大喬",},
        [891] = {2018801,"喬国老","よさないか、小喬。お前の姉は照れ屋なのだ。あまりからかうな。","よさないか、小喬。お前の姉は照れ屋なのだ。あまりからかうな。","小喬",},
        [892] = {2018801,"喬国老","もうすぐ皖城は落ちる。もしものことがあっては困るから、屋敷から出てはならんぞ。","もうすぐ皖城は落ちる。もしものことがあっては困るから、屋敷から出てはならんぞ。","小喬",},
        [893] = {2018801,"大喬","はい、お父さまの仰る通りに。","はい、お父さまの仰る通りに。","喬国老",},
        [894] = {2018802,"小喬","あ～、ヒマヒマヒマヒマ！一日中屋敷にいるなんてつまんないよ。お父さまがいない内に、こっそり遊びに行こう！","あ～、ヒマヒマヒマヒマ！一日中屋敷にいるなんてつまんないよ。お父さまがいない内に、こっそり遊びに行こう！","大喬",},
        [895] = {2018802,"大喬","小喬、早くお屋敷に戻りましょう。お父さまが心配するといけないわ。","小喬、早くお屋敷に戻りましょう。お父さまが心配するといけないわ。","小喬",},
        [896] = {2018802,"孫策","おお……なんと美しい娘だ。お嬢さん、お住まいはどちらです?俺が馬でお送りしましょう。","おお……なんと美しい娘だ。お嬢さん、お住まいはどちらです?俺が馬でお送りしましょう。","大喬",},
        [897] = {2018802,"大喬","(大喬は孫策を盗み見て、急に顔を赤くした)いえ……お手を煩わせるまでもありませんわ。","(大喬は孫策を盗み見て、急に顔を赤くした)いえ……お手を煩わせるまでもありませんわ。","孫策",},
        [898] = {2018802,"周瑜","孫策さま、あまり人を驚かせるものではありませんよ。","孫策さま、あまり人を驚かせるものではありませんよ。","孫策",},
        [899] = {2018802,"大喬","さあ小喬、早く帰るわよ！(大喬は小喬を引っ張って急いで屋敷に戻った)","さあ小喬、早く帰るわよ！(大喬は小喬を引っ張って急いで屋敷に戻った)","孫策",},
        [900] = {2018803,"大喬","皖城が陥ちて、お父さまは仕方なく、わたしを敵の大将に娶せるというけれど……","皖城が陥ちて、お父さまは仕方なく、わたしを敵の大将に娶せるというけれど……","",},
        [901] = {2018803,"大喬","はぁ……孫策とはどんな方なのかしら。見ず知らずの人のもとに嫁がされるなんて……","はぁ……孫策とはどんな方なのかしら。見ず知らずの人のもとに嫁がされるなんて……","",},
        [902] = {2018803,"孫策","俺を覚えているかな?俺はあの日以来、君のことが忘れられなかった。どうか俺と、永遠の契りを結んでくれないか?","俺を覚えているかな?俺はあの日以来、君のことが忘れられなかった。どうか俺と、永遠の契りを結んでくれないか?","大喬",},
        [903] = {2018803,"孫策","でも、安心してくれ。もし嫌なら、この孫策、決して無理強いはしない。","でも、安心してくれ。もし嫌なら、この孫策、決して無理強いはしない。","大喬",},
        [904] = {2018803,"大喬","(恥ずかしそうに)……","(恥ずかしそうに)……","孫策",},
        [905] = {2018803,"孫策","ふむ、言葉が出ないか……大丈夫だ、もし受けてくれるなら頷いてくれ。嫌なら首を振ってくれればいい。","ふむ、言葉が出ないか……大丈夫だ、もし受けてくれるなら頷いてくれ。嫌なら首を振ってくれればいい。","大喬",},
        [906] = {2018803,"大喬","(軽く頷いて、小さな声で)若様は、真心を見せてくださいましたから……","(軽く頷いて、小さな声で)若様は、真心を見せてくださいましたから……","孫策",},
        [907] = {2018803,"大喬","大喬も、真心をもって、永遠の愛を誓います！","大喬も、真心をもって、永遠の愛を誓います！","孫策",},
        [908] = {2018901,"涿郡太守","我が娘よ。今日はお前の縁談を決めてきたぞ！","我が娘よ。今日はお前の縁談を決めてきたぞ！","公孫夫人",},
        [909] = {2018901,"公孫夫人","お父さまったら、またそんな話なの?娘の一生の大事なのに、なんて自分勝手なのかしら！","お父さまったら、またそんな話なの?娘の一生の大事なのに、なんて自分勝手なのかしら！","",},
        [910] = {2018901,"涿郡太守","なあに。今度はきっと満足するだろうよ。その人は公孫瓚と言って、遼西の貴族、公孫氏の出身だ。","なあに。今度はきっと満足するだろうよ。その人は公孫瓚と言って、遼西の貴族、公孫氏の出身だ。","公孫夫人",},
        [911] = {2018901,"涿郡太守","美男子なうえ、声も大きく張りがあって、機知に富んでいる。将来有望な男だぞ。","美男子なうえ、声も大きく張りがあって、機知に富んでいる。将来有望な男だぞ。","公孫夫人",},
        [912] = {2018901,"公孫夫人","まぁ……なんてステキそうなお方……！それなら、お父さまに任せるわ！","まぁ……なんてステキそうなお方……！それなら、お父さまに任せるわ！","",},
        [913] = {2018901,"涿郡太守","よしよし、これでわしの娘は公孫夫人だ、わっははは！","よしよし、これでわしの娘は公孫夫人だ、わっははは！","公孫夫人",},
        [914] = {2018902,"劉備","公孫瓚殿、あなたという人は本当に幸運に恵まれているようだ。","公孫瓚殿、あなたという人は本当に幸運に恵まれているようだ。","公孫瓚",},
        [915] = {2018902,"公孫瓚","何を言うか、劉備よ。中山靖王の末裔であるお前とは比べものにならんよ。","何を言うか、劉備よ。中山靖王の末裔であるお前とは比べものにならんよ。","劉備",},
        [916] = {2018902,"劉備","ふふっ、ご謙遜を。あなたは涿郡太守の娘婿となっただけでなく、義父様の計らいによって、盧植先生の下で学べるようになった。","ふふっ、ご謙遜を。あなたは涿郡太守の娘婿となっただけでなく、義父様の計らいによって、盧植先生の下で学べるようになった。","公孫瓚",},
        [917] = {2018902,"劉備","正に前途洋々ではありませんか。","正に前途洋々ではありませんか。","公孫瓚",},
        [918] = {2018902,"公孫瓚","ははは、盧植先生に師事したというなら、立場はお前も同じではないか。学友であり、同郷の間柄だ。これからもよろしく頼むぞ。","ははは、盧植先生に師事したというなら、立場はお前も同じではないか。学友であり、同郷の間柄だ。これからもよろしく頼むぞ。","劉備",},
        [919] = {2018903,"公孫瓚","我こそは白馬将軍公孫瓚！朝廷の命を受け、辺境を守護している。","我こそは白馬将軍公孫瓚！朝廷の命を受け、辺境を守護している。","",},
        [920] = {2018903,"公孫瓚","幾たびも侵犯を繰り返す烏恒の賊どもめ！覚悟するがよい！","幾たびも侵犯を繰り返す烏恒の賊どもめ！覚悟するがよい！","",},
        [921] = {2018903,"烏恒王","なにやら誤解があるようだな、将軍よ。お前もわしも命令に従って行動しているだけ。","なにやら誤解があるようだな、将軍よ。お前もわしも命令に従って行動しているだけ。","公孫瓚",},
        [922] = {2018903,"烏恒王","殺し合う必要などないのだ。ここは、お互い目をつぶる方がよいのではないか?","殺し合う必要などないのだ。ここは、お互い目をつぶる方がよいのではないか?","公孫瓚",},
        [923] = {2018903,"公孫瓚","ふざけたことをぬかすな！侵犯して来る賊どもは、この公孫瓚が片っ端からあの世に送ってやるわ！","ふざけたことをぬかすな！侵犯して来る賊どもは、この公孫瓚が片っ端からあの世に送ってやるわ！","",},
        [924] = {2018903,"公孫瓚","絶対に見逃がしはせぬぞ！者ども、かかれ！","絶対に見逃がしはせぬぞ！者ども、かかれ！","",},
        [925] = {2018903,"烏恒王","うおおっ、なんという強さ！漢人の中にこれほどの英雄豪傑がいるとは?！","うおおっ、なんという強さ！漢人の中にこれほどの英雄豪傑がいるとは?！","公孫瓚",},
        [926] = {2018903,"烏恒王","ええい、退け退け！我ら烏恒人は勇士を最も貴ぶ。ここは撤退あるのみよ！","ええい、退け退け！我ら烏恒人は勇士を最も貴ぶ。ここは撤退あるのみよ！","公孫瓚",},
        [927] = {2018903,"烏恒王","いいか野郎ども、よく聞け！これからは、あの白馬の騎兵を見たらすぐに撤退だ！金儲けより、命の方が大事だからな！","いいか野郎ども、よく聞け！これからは、あの白馬の騎兵を見たらすぐに撤退だ！金儲けより、命の方が大事だからな！","公孫瓚",},
        [928] = {20181001,"反乱の氐族","ガハハ、今日は運がいい！漢人を十匹ほどブチ殺して、身ぐるみはいでやった！こちらは被害なしだ！ガハハ！","ガハハ、今日は運がいい！漢人を十匹ほどブチ殺して、身ぐるみはいでやった！こちらは被害なしだ！ガハハ！","賈詡",},
        [929] = {20181001,"賈詡","(ああ……今日は運が悪い。故郷に帰る途中で強盗に遭うなんて！","(ああ……今日は運が悪い。故郷に帰る途中で強盗に遭うなんて！","反乱の氐族",},
        [930] = {20181001,"賈詡","しかしこの賈詡、ここで死ぬわけにはいかない。なんとか助からなければ！)","しかしこの賈詡、ここで死ぬわけにはいかない。なんとか助からなければ！)","反乱の氐族",},
        [931] = {20181001,"賈詡","(なぜこいつらは我々を殺さず、捕まえたのだ?目的は金か、それとも殺しを楽しむためか……そうだ！)","(なぜこいつらは我々を殺さず、捕まえたのだ?目的は金か、それとも殺しを楽しむためか……そうだ！)","反乱の氐族",},
        [932] = {20181001,"賈詡","あのう……あなたたち、私が誰だか知っていますか?","あのう……あなたたち、私が誰だか知っていますか?","反乱の氐族",},
        [933] = {20181001,"反乱の氐族","なんだ漢人、何が言いたい?","なんだ漢人、何が言いたい?","賈詡",},
