        [938] = {20181001,"反乱の氐族","ガハハ、そうか！お前ら漢人は狡猾だが、誓いを立てるなら許してやろう！","ガハハ、そうか！お前ら漢人は狡猾だが、誓いを立てるなら許してやろう！","賈詡",},
        [939] = {20181001,"反乱の氐族","(氐人は誓いを立てた賈詡を放し、他の人はみな殺された。賈詡は機転によって自分の命を救ったのである)","(氐人は誓いを立てた賈詡を放し、他の人はみな殺された。賈詡は機転によって自分の命を救ったのである)","賈詡",},
        [940] = {20181002,"李傕","チッ……！王允のジジイめ、見逃してはくれんか。こがいになったけぇにゃあ、さっさと逃げるしか……！","チッ……！王允のジジイめ、見逃してはくれんか。こがいになったけぇにゃあ、さっさと逃げるしか……！","賈詡",},
        [941] = {20181002,"賈詡","待ってください。軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まっちゃいますよ。","待ってください。軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まっちゃいますよ。","李傕",},
        [942] = {20181002,"賈詡","ここは陝西の人たちを誘い集め、本隊に組み入れつつ長安に攻め入り、董卓殿の仇を討つのがよいんじゃないですか?","ここは陝西の人たちを誘い集め、本隊に組み入れつつ長安に攻め入り、董卓殿の仇を討つのがよいんじゃないですか?","李傕",},
