        [960] = {20181103,"程昱","母親が死んだことを徐庶が知れば、必ずや劉備の下に付き、仇を討とうとするじゃろう。","母親が死んだことを徐庶が知れば、必ずや劉備の下に付き、仇を討とうとするじゃろう。","曹操",},
        [961] = {20181103,"程昱","この程昱に一計がありますじゃ。必ずや徐庶を謀り、丞相を補佐させて進ぜましょう。","この程昱に一計がありますじゃ。必ずや徐庶を謀り、丞相を補佐させて進ぜましょう。","曹操",},
