        [963] = {20181104,"曹操","徐庶よ、お前は優れた人間だ。なぜ身を屈して劉備などに仕えたのか?","徐庶よ、お前は優れた人間だ。なぜ身を屈して劉備などに仕えたのか?","徐庶",},
        [964] = {20181104,"徐庶","若輩の頃より難を逃れて各地を放浪しており、たまたま新野で劉備と出会って親しくなったのです。","若輩の頃より難を逃れて各地を放浪しており、たまたま新野で劉備と出会って親しくなったのです。","曹操",},
        [965] = {20181104,"徐庶","彼が義を重んじることを聞き、これを助けるためです。","彼が義を重んじることを聞き、これを助けるためです。","曹操",},
        [966] = {20181104,"曹操","そうであったか。しかし、ここに来たからには、今日からわしを助けてくれるな?","そうであったか。しかし、ここに来たからには、今日からわしを助けてくれるな?","徐庶",},
