        [970] = {20181105,"劉備","徐庶よ。せっかく来てくれて嬉しいが、このまま留まってはくれぬのか。","徐庶よ。せっかく来てくれて嬉しいが、このまま留まってはくれぬのか。","徐庶",},
        [971] = {20181105,"徐庶","私はすでに曹操の謀士。降伏を勧めに来ておきながら自らが降れば、天下の笑いものになりましょう。","私はすでに曹操の謀士。降伏を勧めに来ておきながら自らが降れば、天下の笑いものになりましょう。","劉備",},
        [972] = {20181105,"徐庶","それに、今や劉備殿には諸葛亮の補佐がある。私などいなくても十分でしょう。","それに、今や劉備殿には諸葛亮の補佐がある。私などいなくても十分でしょう。","劉備",},
        [973] = {20181105,"徐庶","しかしご安心ください、劉備殿。たとえ曹操の下に身を置こうとも、彼のために策を献じないことを誓いましょう。","しかしご安心ください、劉備殿。たとえ曹操の下に身を置こうとも、彼のために策を献じないことを誓いましょう。","劉備",},
