        [996] = {20181301,"夏侯淵","魏のために命果てるまで奉公する。それが我が夏侯家の本望なのです。","魏のために命果てるまで奉公する。それが我が夏侯家の本望なのです。","曹操",},
        [997] = {20181302,"夏侯涓","予州の大乱で、幼くして孤児になったわたしを、淵伯父さまは引き取って、育ててくださった。","予州の大乱で、幼くして孤児になったわたしを、淵伯父さまは引き取って、育ててくださった。","夏侯淵",},
        [998] = {20181302,"夏侯涓","伯父さまは本当に仁徳の人。このご恩にどう報いたらいいのか……","伯父さまは本当に仁徳の人。このご恩にどう報いたらいいのか……","夏侯淵",},
        [999] = {20181302,"夏侯涓","実の娘以上に、淵伯父さま、いえ、お父さまには感謝しております。(叩頭して礼を言う)","実の娘以上に、淵伯父さま、いえ、お父さまには感謝しております。(叩頭して礼を言う)","夏侯淵",},
        [1000] = {20181302,"夏侯淵","いいんだ。やめなさい、夏侯涓。早く頭を起こしなさい。","いいんだ。やめなさい、夏侯涓。早く頭を起こしなさい。","夏侯涓",},
        [1001] = {20181303,"夏侯淵","おい張郃、俺の酒に付き合わないか?","おい張郃、俺の酒に付き合わないか?","張郃",},
        [1002] = {20181303,"張郃","しかし夏侯淵、もうずいぶん飲んでるじゃないの。危ないわねえ、これは。あんたのカラミ酒にはうんざりよ。","しかし夏侯淵、もうずいぶん飲んでるじゃないの。危ないわねえ、これは。あんたのカラミ酒にはうんざりよ。","夏侯淵",},
        [1003] = {20181303,"張郃","どうせ、またいつものやつでしょ。今日の話題はなんなの?娘?息子?嫁?兄上?それとも子供の時の話かしら。","どうせ、またいつものやつでしょ。今日の話題はなんなの?娘?息子?嫁?兄上?それとも子供の時の話かしら。","夏侯淵",},
        [1004] = {20181303,"夏侯淵","そうさ、よくわかってるじゃないか。もういっぺん、養女の夏侯涓の話を聞いてくれよ。","そうさ、よくわかってるじゃないか。もういっぺん、養女の夏侯涓の話を聞いてくれよ。","張郃",},
        [1005] = {20181303,"夏侯淵","今でも悔しくてなあ。あの張飛めの嫁に取られさえしなければ……","今でも悔しくてなあ。あの張飛めの嫁に取られさえしなければ……","張郃",},
        [1006] = {20181304,"黄忠","かかったのう、夏侯淵！おぬしは兵の半分を、張郃の救援に出してしもうた。","かかったのう、夏侯淵！おぬしは兵の半分を、張郃の救援に出してしもうた。","夏侯淵",},
        [1007] = {20181304,"黄忠","もはやこの定軍山からも逃げられぬぞ。おとなしく降参せい！","もはやこの定軍山からも逃げられぬぞ。おとなしく降参せい！","夏侯淵",},
        [1008] = {20181304,"夏侯淵","何を恐れるものか！いくらでもかかってくるがよい。","何を恐れるものか！いくらでもかかってくるがよい。","黄忠",},
        [1009] = {20181305,"夏侯淵","ああ……戦の中で一生を送ってきたが、今日が年貢の納め時か。なんとも腹立たしいものだな。","ああ……戦の中で一生を送ってきたが、今日が年貢の納め時か。なんとも腹立たしいものだな。","張飛",},
        [1010] = {20181305,"張飛","夏侯淵……オレの女房はてめえの姪だからなァ。","夏侯淵……オレの女房はてめえの姪だからなァ。","夏侯淵",},
        [1011] = {20181305,"張飛","オレたちは親戚だ。救ってやりてえのはやまやまだが、別の主に仕えている以上、どうしようもねえ。","オレたちは親戚だ。救ってやりてえのはやまやまだが、別の主に仕えている以上、どうしようもねえ。","夏侯淵",},
        [1012] = {20181305,"張飛","だが安心しやがれ。オレが手厚く葬ってやるからよ。","だが安心しやがれ。オレが手厚く葬ってやるからよ。","夏侯淵",},
        [1013] = {20181401,"何進","華佗殿、命を救っていただき感謝する。","華佗殿、命を救っていただき感謝する。","華佗",},
        [1014] = {20181401,"何進","このような死にかけに、また家族と共に過ごす喜びを与えてくれた。","このような死にかけに、また家族と共に過ごす喜びを与えてくれた。","華佗",},
        [1015] = {20181401,"華佗","もったいないお言葉です、郡守さま。","もったいないお言葉です、郡守さま。","何進",},
        [1016] = {20181401,"華佗","人を死から救い、怪我を治すのが医者の使命ですから。","人を死から救い、怪我を治すのが医者の使命ですから。","何進",},
        [1017] = {20181402,"孫権","華佗よ、お願いだ。周泰を救ってやってくれ。","華佗よ、お願いだ。周泰を救ってやってくれ。","華佗",},
        [1018] = {20181402,"孫権","黄巾の残党が宣城に夜襲をかけたのだ。","黄巾の残党が宣城に夜襲をかけたのだ。","華佗",},
        [1019] = {20181402,"孫権","周泰は俺をかばって、全身に十二ヶ所も傷を負ったのだ。傷口が腫れている。このままでは一日と持たない。","周泰は俺をかばって、全身に十二ヶ所も傷を負ったのだ。傷口が腫れている。このままでは一日と持たない。","華佗",},
        [1020] = {20181402,"華佗","承知しました。まずは、周泰殿をそこに寝かせなさい。","承知しました。まずは、周泰殿をそこに寝かせなさい。","孫権",},
        [1021] = {20181402,"周泰","ぐっ……か……かたじけ……ない……！","ぐっ……か……かたじけ……ない……！","華佗",},
        [1022] = {20181403,"曹操","華佗よ。宣城で周泰を見事救ったそうだな。","華佗よ。宣城で周泰を見事救ったそうだな。","華佗",},
        [1023] = {20181403,"曹操","その腕で、わしの頭痛をなんとかして欲しいのだ！そなたでなければ、恐らく治せぬ！","その腕で、わしの頭痛をなんとかして欲しいのだ！そなたでなければ、恐らく治せぬ！","華佗",},
        [1024] = {20181403,"華佗","ほほう……どうやら、曹操殿は偏頭風による目まいに苦しんでおられるようですね。","ほほう……どうやら、曹操殿は偏頭風による目まいに苦しんでおられるようですね。","曹操",},
        [1025] = {20181403,"華佗","少し針を打てば、じきに良くなりますよ。","少し針を打てば、じきに良くなりますよ。","曹操",},
        [1026] = {20181403,"曹操","おお、そうか！ならば、早く針を打ってくれ！","おお、そうか！ならば、早く針を打ってくれ！","華佗",},
        [1027] = {20181404,"華佗","関羽殿、今回はどういった症状です?","関羽殿、今回はどういった症状です?","関羽",},
        [1028] = {20181404,"関羽","毒矢で右ひじを撃たれてな。毒が骨まで染みこんで、傷がどんどん悪くなるのだ。","毒矢で右ひじを撃たれてな。毒が骨まで染みこんで、傷がどんどん悪くなるのだ。","華佗",},
        [1029] = {20181404,"華佗","わかりました。では、柱に関羽殿の腕を縄で柱に固定しなさい。","わかりました。では、柱に関羽殿の腕を縄で柱に固定しなさい。","関羽",},
        [1030] = {20181404,"華佗","関羽殿は目を閉じて、ご辛抱ください。小刀で腕を切開しますので。","関羽殿は目を閉じて、ご辛抱ください。小刀で腕を切開しますので。","関羽",},
        [1031] = {20181404,"関羽","なんだ、そんな大げさな用意は要らん。わしは怖くも、暴れもせぬから、このままやってくれ。","なんだ、そんな大げさな用意は要らん。わしは怖くも、暴れもせぬから、このままやってくれ。","華佗",},
        [1032] = {20181404,"関羽","治療が済むまで、酒を飲み、碁でも打っていよう。","治療が済むまで、酒を飲み、碁でも打っていよう。","華佗",},
        [1033] = {20181405,"曹操","華佗よ、なぜわしの頭痛は重くなるばかりなのだ?","華佗よ、なぜわしの頭痛は重くなるばかりなのだ?","華佗",},
        [1034] = {20181405,"華佗","針では病状を緩和するだけで、根治には至らないのです。","針では病状を緩和するだけで、根治には至らないのです。","曹操",},
        [1035] = {20181405,"華佗","病気の元は頭の中にあります。頭蓋骨を開いて病巣を取り除く必要がありますね。","病気の元は頭の中にあります。頭蓋骨を開いて病巣を取り除く必要がありますね。","曹操",},
        [1036] = {20181405,"曹操","頭蓋骨を開くだと、ふざけるな！","頭蓋骨を開くだと、ふざけるな！","華佗",},
        [1037] = {20181405,"曹操","貴様、さてはわしを殺す気だな。誰か！こやつを連れて行って拷問にかけろ。","貴様、さてはわしを殺す気だな。誰か！こやつを連れて行って拷問にかけろ。","華佗",},
        [1038] = {20181501,"孫堅","我が子孫策は、幼いころよりわしに従って戦場を駆けてきた。","我が子孫策は、幼いころよりわしに従って戦場を駆けてきた。","孫策",},
        [1039] = {20181501,"孫堅","勇猛で、闊達で、統率力もある。将来きっと大物となろう！","勇猛で、闊達で、統率力もある。将来きっと大物となろう！","孫策",},
        [1040] = {20181501,"孫策","父上は破虜将軍となられ、その武勇も並外れております。","父上は破虜将軍となられ、その武勇も並外れております。","孫堅",},
        [1041] = {20181501,"孫策","長子として、父上の偉業をしかと受け継いでゆかねば。","長子として、父上の偉業をしかと受け継いでゆかねば。","孫堅",},
        [1042] = {20181502,"大喬","孫策さま、大喬が舞をご覧にいれますね。","孫策さま、大喬が舞をご覧にいれますね。","孫策",},
        [1043] = {20181502,"孫策","大喬は花も恥じらう絶世の美女だ。しかも歌も踊りも上手ときている。","大喬は花も恥じらう絶世の美女だ。しかも歌も踊りも上手ときている。","大喬",},
        [1044] = {20181502,"孫策","お前を得て、俺は大変に満足だ。","お前を得て、俺は大変に満足だ。","大喬",},
        [1045] = {20181502,"大喬","まあ孫策さま、お上手だこと。","まあ孫策さま、お上手だこと。","孫策",},
        [1046] = {20181503,"孫策","周瑜、世間では俺たちを江東の双璧と呼んでいるらしいな。","周瑜、世間では俺たちを江東の双璧と呼んでいるらしいな。","周瑜",},
        [1047] = {20181503,"孫策","そう呼ばれることについて、お前はどう思う。","そう呼ばれることについて、お前はどう思う。","周瑜",},
        [1048] = {20181503,"周瑜","あなたと僕は二人で呉の国を衰退から救い、繁栄に向かわせました。","あなたと僕は二人で呉の国を衰退から救い、繁栄に向かわせました。","孫策",},
        [1049] = {20181503,"周瑜","そして、あなたは大喬を娶り、僕は小喬を得ている。その名は僕たち二人にこそ相応しいでしょうね。","そして、あなたは大喬を娶り、僕は小喬を得ている。その名は僕たち二人にこそ相応しいでしょうね。","孫策",},
        [1050] = {20181503,"孫策","ははははは、お前の言うことはもっともだな。","ははははは、お前の言うことはもっともだな。","",},
        [1051] = {20181504,"于吉","私は符水によって万民の病を治し、広く人心を得ている。","私は符水によって万民の病を治し、広く人心を得ている。","孫策",},
        [1052] = {20181504,"于吉","そんな私を、お前はなぜ殺そうというのか?","そんな私を、お前はなぜ殺そうというのか?","孫策",},
        [1053] = {20181504,"孫策","お前が民心を惑わすからだ。諸将までもが、主君たる俺をないがしろにして、お前を拝みに行ってしまう始末だ。","お前が民心を惑わすからだ。諸将までもが、主君たる俺をないがしろにして、お前を拝みに行ってしまう始末だ。","于吉",},
        [1054] = {20181504,"孫策","捨て置くわけにいかん。","捨て置くわけにいかん。","于吉",},
        [1055] = {20181505,"孫策","江東の兵を率いて戦を挑み、天下を争うことにかけては――","江東の兵を率いて戦を挑み、天下を争うことにかけては――","孫権",},
        [1056] = {20181505,"孫策","お前は俺に及ばない。","お前は俺に及ばない。","孫権",},
        [1057] = {20181505,"孫策","だが、才能のある者を登用し、江東の領土を守っていくことにかけては――","だが、才能のある者を登用し、江東の領土を守っていくことにかけては――","孫権",},
        [1058] = {20181505,"孫策","お前の方が上だ。","お前の方が上だ。","孫権",},
        [1059] = {20181505,"孫権","褒めていただき恐縮です。私は必ず江東の基盤を守ってみせます！","褒めていただき恐縮です。私は必ず江東の基盤を守ってみせます！","孫策",},
        [1060] = {20181601,"袁術","お前はオレの兄だが、オレは嫡出だ。","お前はオレの兄だが、オレは嫡出だ。","袁紹",},
        [1061] = {20181601,"袁術","そしてお前は妾腹だ！わかるか?この差がそのままオレとお前の差だ！","そしてお前は妾腹だ！わかるか?この差がそのままオレとお前の差だ！","袁紹",},
        [1062] = {20181601,"袁紹","それはどうかな。素質も人気も実力も、すべてお前より優れておるぞ。","それはどうかな。素質も人気も実力も、すべてお前より優れておるぞ。","袁術",},
        [1063] = {20181601,"袁紹","皆もそれをわかっておる。だからわしを支持してくれているのだ。","皆もそれをわかっておる。だからわしを支持してくれているのだ。","袁術",},
        [1064] = {20181602,"袁術","張譲。貴様は民から高い税金を搾り取り、私腹を肥やしているな！横暴で傲慢で貪欲、朝廷の災いの元である。","張譲。貴様は民から高い税金を搾り取り、私腹を肥やしているな！横暴で傲慢で貪欲、朝廷の災いの元である。","張譲",},
        [1065] = {20181602,"袁術","今日こそお前を斬ってやるから、覚悟しろ！","今日こそお前を斬ってやるから、覚悟しろ！","張譲",},
        [1066] = {20181602,"張譲","ほほほ。帝と陳留王はこちらの手にあるのですよ。","ほほほ。帝と陳留王はこちらの手にあるのですよ。","袁術",},
        [1067] = {20181602,"張譲","控えなさい。黄河を渡らせてもらいましょうか。","控えなさい。黄河を渡らせてもらいましょうか。","袁術",},
        [1068] = {20181602,"袁紹","馬鹿め。船もないのに河を渡れるものか。今すぐ帝たちを解放しろ！さもなくば……","馬鹿め。船もないのに河を渡れるものか。今すぐ帝たちを解放しろ！さもなくば……","張譲",},
        [1069] = {20181602,"張譲","良いのですか?我らが全滅すれば、天下の大乱は必至ですぞ！","良いのですか?我らが全滅すれば、天下の大乱は必至ですぞ！","袁紹",},
        [1070] = {20181603,"孫策","父が洛陽にいたおり、朝になると城南の井戸から五色の気が立ちのぼっていたそうです。","父が洛陽にいたおり、朝になると城南の井戸から五色の気が立ちのぼっていたそうです。","袁術",},
        [1071] = {20181603,"孫策","井戸の中を探させたところ、この伝国の玉璽が見つかったのです。","井戸の中を探させたところ、この伝国の玉璽が見つかったのです。","袁術",},
        [1072] = {20181603,"孫策","袁術殿、これをお手元に預けますから、代わりに兵馬をお貸しいただきたい。江東を平定に参ります。","袁術殿、これをお手元に預けますから、代わりに兵馬をお貸しいただきたい。江東を平定に参ります。","袁術",},
        [1073] = {20181603,"袁術","孫策よ。お前の父も、最初はオレの配下の、頼りになる猛将だった。","孫策よ。お前の父も、最初はオレの配下の、頼りになる猛将だった。","孫策",},
        [1074] = {20181603,"袁術","その才能は、息子のお前にも受け継がれておる。手放すには惜しい。","その才能は、息子のお前にも受け継がれておる。手放すには惜しい。","孫策",},
        [1075] = {20181603,"袁術","だが、玉璽をくれるというなら、大きな借りになる。兵馬を貸し与えようではないか。","だが、玉璽をくれるというなら、大きな借りになる。兵馬を貸し与えようではないか。","孫策",},
        [1076] = {20181604,"袁術","よいか、呂布よ。オレは高貴な生まれ、名門の血筋だ。","よいか、呂布よ。オレは高貴な生まれ、名門の血筋だ。","呂布",},
        [1077] = {20181604,"袁術","そしてお前は世の人々から、「人中の呂布、馬中の赤兔」と称えられておる。","そしてお前は世の人々から、「人中の呂布、馬中の赤兔」と称えられておる。","呂布",},
        [1078] = {20181604,"袁術","そんなオレとお前が組めば、天下無敵と思わぬか?ここに同盟を結び、お主の娘をオレの息子に嫁がせるというのはどうだ?","そんなオレとお前が組めば、天下無敵と思わぬか?ここに同盟を結び、お主の娘をオレの息子に嫁がせるというのはどうだ?","呂布",},
        [1079] = {20181604,"呂布","沛相の陳珪は、袁術に謀反の疑いがあると言っている。","沛相の陳珪は、袁術に謀反の疑いがあると言っている。","袁術",},
        [1080] = {20181604,"呂布","そして俺も、この前お前に約束を破られたことを恨んでいる。","そして俺も、この前お前に約束を破られたことを恨んでいる。","袁術",},
        [1081] = {20181604,"呂布","故に、お前は信用ならん。この縁談は認められぬ。","故に、お前は信用ならん。この縁談は認められぬ。","袁術",},
        [1082] = {20181605,"袁術","オレは高貴な生まれであり、玉璽も持っている。生まれつき皇帝となる運命なのだ！","オレは高貴な生まれであり、玉璽も持っている。生まれつき皇帝となる運命なのだ！","劉備",},
        [1083] = {20181605,"劉備","そのようなこと、天下の諸将で相手にする者は一人もおらん。すでに多くの者が離反したではないか。","そのようなこと、天下の諸将で相手にする者は一人もおらん。すでに多くの者が離反したではないか。","袁術",},
        [1084] = {20181605,"袁術","ありえぬ！ありえぬぞ！","ありえぬ！ありえぬぞ！","劉備",},
        [1085] = {20181605,"劉備","お前は広陵、江東のなどの広大な領土を失い、子飼いの陳蘭、雷薄にも背かれた。","お前は広陵、江東のなどの広大な領土を失い、子飼いの陳蘭、雷薄にも背かれた。","袁術",},
        [1086] = {20181605,"劉備","ここに、お前の命運は尽きたのだ。","ここに、お前の命運は尽きたのだ。","袁術",},
        [1087] = {20181605,"袁術","うぬぬぬっ、この袁術がっ！この袁術が、どうしてこんな目に！","うぬぬぬっ、この袁術がっ！この袁術が、どうしてこんな目に！","劉備",},
        [1088] = {20181701,"曹植","曹昂は戦死し、曹彰は将軍となり、僕と兄の曹丕が跡継ぎを争うことになった。","曹昂は戦死し、曹彰は将軍となり、僕と兄の曹丕が跡継ぎを争うことになった。","楊修",},
        [1089] = {20181701,"曹植","正直に言って、僕にはまったく自信がない。楊修、僕を助けてくれ。","正直に言って、僕にはまったく自信がない。楊修、僕を助けてくれ。","楊修",},
        [1090] = {20181701,"楊修","何をおっしゃいます。若様は稀有な才能の持ち主。曹丕様でもまるで及びません。","何をおっしゃいます。若様は稀有な才能の持ち主。曹丕様でもまるで及びません。","曹植",},
        [1091] = {20181701,"楊修","ご安心ください。いざという時には全力でお助けしますよ。","ご安心ください。いざという時には全力でお助けしますよ。","曹植",},
        [1092] = {20181702,"甄姫","「君を念いて常に苦悲し、夜夜寝ぬる能わず」……あなたを想うといつも辛くて、夜も眠れない……","「君を念いて常に苦悲し、夜夜寝ぬる能わず」……あなたを想うといつも辛くて、夜も眠れない……","曹植",},
        [1093] = {20181702,"曹植","ほう、よい詩ですね。誰の作です?","ほう、よい詩ですね。誰の作です?","甄姫",},
        [1094] = {20181702,"甄姫","わたしが作った詩ですわ。「塘上行」といいます。","わたしが作った詩ですわ。「塘上行」といいます。","曹植",},
        [1095] = {20181702,"曹植","そうでしたか……悲しみに暮れる沈んだ気持ちがよく詠われていて、なんとも素晴らしい。","そうでしたか……悲しみに暮れる沈んだ気持ちがよく詠われていて、なんとも素晴らしい。","甄姫",},
        [1096] = {20181703,"曹植","僕の妻はそなたの姪にあたる。","僕の妻はそなたの姪にあたる。","崔琰",},
        [1097] = {20181703,"曹植","当然、後継者争いでは僕を支持してくれるね?","当然、後継者争いでは僕を支持してくれるね?","崔琰",},
        [1098] = {20181703,"崔琰","確かに曹植さまは我が兄の娘を娶られました。が、長幼の序を乱してはなりません。","確かに曹植さまは我が兄の娘を娶られました。が、長幼の序を乱してはなりません。","曹植",},
        [1099] = {20181703,"崔琰","故にこの崔琰、断固曹丕さまを支持します。たとえ首を斬られようとも。","故にこの崔琰、断固曹丕さまを支持します。たとえ首を斬られようとも。","曹植",},
        [1100] = {20181704,"曹丕","いくらお前が父上のお気に入りだろうと、私は兄だぞ。私を太子に立てるべきだ！","いくらお前が父上のお気に入りだろうと、私は兄だぞ。私を太子に立てるべきだ！","曹植",},
        [1101] = {20181704,"曹丕","お前は好きなことをしていればいい。私と争う必要はないだろう。","お前は好きなことをしていればいい。私と争う必要はないだろう。","曹植",},
        [1102] = {20181704,"曹植","僕だってそうしたい。けれど兄上の薄情さや陰険さには黙っておれません。","僕だってそうしたい。けれど兄上の薄情さや陰険さには黙っておれません。","曹丕",},
        [1103] = {20181704,"曹植","私利私欲のために、家族すら顧みない。","私利私欲のために、家族すら顧みない。","曹丕",},
        [1104] = {20181704,"曹植","父上だって、兄上に内心わだかまりを感じておられるはず。","父上だって、兄上に内心わだかまりを感じておられるはず。","曹丕",},
        [1105] = {20181704,"曹丕","言ってくれるではないか。ならば見ていろ。","言ってくれるではないか。ならば見ていろ。","曹植",},
        [1106] = {20181705,"曹丕","父上は病死し、私が王位が継いだ。もし母上の頼みがなければ――","父上は病死し、私が王位が継いだ。もし母上の頼みがなければ――","曹植",},
        [1107] = {20181705,"曹丕","お前はとうに父上の後を追ってあの世に行っていたのだ。私に感謝するがいい！","お前はとうに父上の後を追ってあの世に行っていたのだ。私に感謝するがいい！","曹植",},
        [1108] = {20181705,"曹植","豆を煮るに、豆殻を燃やすし、これを漉して汁とするに","豆を煮るに、豆殻を燃やすし、これを漉して汁とするに","曹丕",},
        [1109] = {20181705,"曹植","豆殻は釜の下で燃え、豆は釜の中で泣く","豆殻は釜の下で燃え、豆は釜の中で泣く","曹丕",},
        [1110] = {20181705,"曹植","元は同じ根から生まれた兄弟なのに、なぜそんなに激しく責めたてるのです。","元は同じ根から生まれた兄弟なのに、なぜそんなに激しく責めたてるのです。","曹丕",},
        [1111] = {20181801,"南蛮勇士","祝融は、わが南中一の美女だ。","祝融は、わが南中一の美女だ。","祝融",},
        [1112] = {20181801,"南蛮勇士","そんな美女の相手には勇士こそふさわしい。オレはどうだ?","そんな美女の相手には勇士こそふさわしい。オレはどうだ?","祝融",},
        [1113] = {20181801,"祝融","この祝融の夫になれるのは勇士さまだけ。それは間違いないねえ。","この祝融の夫になれるのは勇士さまだけ。それは間違いないねえ。","南蛮勇士",},
        [1114] = {20181801,"祝融","でも、あんたがその勇士さまだって証拠は、どこにあるんだい?","でも、あんたがその勇士さまだって証拠は、どこにあるんだい?","南蛮勇士",},
        [1115] = {20181802,"黄月英","小さい頃、南中へ木を集めに行ったことがあります。","小さい頃、南中へ木を集めに行ったことがあります。","祝融",},
        [1116] = {20181802,"黄月英","その木で木偶人形を作ったり、からくりの犬や虎を発明しました。","その木で木偶人形を作ったり、からくりの犬や虎を発明しました。","祝融",},
        [1117] = {20181802,"黄月英","そのとき祝融と知り合ったんです。仲良くなれてよかった。","そのとき祝融と知り合ったんです。仲良くなれてよかった。","祝融",},
        [1118] = {20181802,"祝融","あたしもさ。こんな何年も経ってから、また会えるとは思いもしなかったよ。","あたしもさ。こんな何年も経ってから、また会えるとは思いもしなかったよ。","黄月英",},
        [1119] = {20181802,"祝融","それにしてもまさか、あんたが臥龍の妻になったなんてね。","それにしてもまさか、あんたが臥龍の妻になったなんてね。","黄月英",},
        [1120] = {20181803,"孟獲","一度目は己の不注意から、おめえの計略にはまったまでだ。それで心服させられると思ったら大間違いだぜ！","一度目は己の不注意から、おめえの計略にはまったまでだ。それで心服させられると思ったら大間違いだぜ！","諸葛亮",},
        [1121] = {20181803,"孟獲","勝敗は兵家の常。二度捕らわれたところで、どうってこたぁねえ！","勝敗は兵家の常。二度捕らわれたところで、どうってこたぁねえ！","諸葛亮",},
        [1122] = {20181803,"孟獲","孔明よ。度胸があるなら、またわしを放してみろ！","孔明よ。度胸があるなら、またわしを放してみろ！","諸葛亮",},
        [1123] = {20181803,"諸葛亮","ええ、いいでしょう。","ええ、いいでしょう。","孟獲",},
        [1124] = {20181804,"孟獲","こんなはずじゃねえ！弟の孟優が酒でしくじらなきゃ、捕まりゃしなかったんだ！","こんなはずじゃねえ！弟の孟優が酒でしくじらなきゃ、捕まりゃしなかったんだ！","諸葛亮",},
        [1125] = {20181804,"孟獲","やい、孔明！腕に覚えがあるなら、もう一度わしを放してみやがれ！","やい、孔明！腕に覚えがあるなら、もう一度わしを放してみやがれ！","諸葛亮",},
        [1126] = {20181804,"諸葛亮","もちろん、何度でも解放してあげますよ！","もちろん、何度でも解放してあげますよ！","孟獲",},
        [1127] = {20181805,"祝融","孟獲、このバカ！なんで孔明に七回も捕まるんだい！","孟獲、このバカ！なんで孔明に七回も捕まるんだい！","孟獲",},
        [1128] = {20181805,"孟獲","過ぎたこたぁ、仕方ねえだろ！","過ぎたこたぁ、仕方ねえだろ！","祝融",},
        [1129] = {20181805,"孟獲","それより、前に約束しちまったのよ。七度目に捕まったら、そんときゃ本気で降参するってよ。","それより、前に約束しちまったのよ。七度目に捕まったら、そんときゃ本気で降参するってよ。","祝融",},
        [1130] = {20181805,"孟獲","約束は約束だからよ。もはや蜀に降るしかねえが、おめえはどうする?","約束は約束だからよ。もはや蜀に降るしかねえが、おめえはどうする?","祝融",},
        [1131] = {20181805,"祝融","フン、なに言ってんだい！鶏に嫁げば、鶏に従い、犬に嫁げば犬に従うさ。","フン、なに言ってんだい！鶏に嫁げば、鶏に従い、犬に嫁げば犬に従うさ。","孟獲",},
        [1132] = {20181805,"祝融","腐れ縁ってやつさ。一緒に蜀に降るしかないじゃないか。","腐れ縁ってやつさ。一緒に蜀に降るしかないじゃないか。","孟獲",},
        [1133] = {20181901,"董卓","わしは今上皇帝を廃したいと思うが、誰か異議はあるか?","わしは今上皇帝を廃したいと思うが、誰か異議はあるか?","孔融",},
        [1134] = {20181901,"孔融","異議あり！それは明らかに朝廷の綱紀を乱す行いです！","異議あり！それは明らかに朝廷の綱紀を乱す行いです！","董卓",},
        [1135] = {20181901,"董卓","なにをっ！?孔子の末裔だからと言って、ここでわしに口出しできるなどと思うなよ！","なにをっ！?孔子の末裔だからと言って、ここでわしに口出しできるなどと思うなよ！","孔融",},
        [1136] = {20181901,"董卓","思い知らせてやる！明日から、孔融は北海国の国相じゃあ！出て行け！","思い知らせてやる！明日から、孔融は北海国の国相じゃあ！出て行け！","孔融",},
        [1137] = {20181902,"孔融","この黄巾賊の包囲を突破するのは至難の業ですね。","この黄巾賊の包囲を突破するのは至難の業ですね。","太史慈",},
        [1138] = {20181902,"孔融","いくらあなたが勇敢でも、あまりに無謀じゃないですか?","いくらあなたが勇敢でも、あまりに無謀じゃないですか?","太史慈",},
        [1139] = {20181902,"太史慈","昔、太守は俺の母親の面倒をよく見てくれた。俺はその恩に感謝している。","昔、太守は俺の母親の面倒をよく見てくれた。俺はその恩に感謝している。","孔融",},
        [1140] = {20181902,"太史慈","確かに突破は至難の業だろう。だが、恩は返さなければならない。","確かに突破は至難の業だろう。だが、恩は返さなければならない。","孔融",},
        [1141] = {20181902,"太史慈","これ以上はなにも言うな。俺を信じてくれ。","これ以上はなにも言うな。俺を信じてくれ。","孔融",},
        [1142] = {20181903,"太史慈","孔融殿が黄巾賊の将・管亥に包囲され、絶体絶命の状況なのです。","孔融殿が黄巾賊の将・管亥に包囲され、絶体絶命の状況なのです。","劉備",},
        [1143] = {20181903,"太史慈","劉備殿。どうか救援の兵を出してはもらえませんか。","劉備殿。どうか救援の兵を出してはもらえませんか。","劉備",},
        [1144] = {20181903,"劉備","北海の孔融殿にまで、我が名を知られているとは。恐縮ですが、実に喜ばしいことです。","北海の孔融殿にまで、我が名を知られているとは。恐縮ですが、実に喜ばしいことです。","太史慈",},
        [1145] = {20181903,"劉備","必ず救わねばならぬと思います。ただちに兵を出しましょう。","必ず救わねばならぬと思います。ただちに兵を出しましょう。","太史慈",},
        [1146] = {20181904,"孔融","むかし周の武王が殷の紂王を討ったとき、褒美に妲己を弟の周公に与えましたよね?","むかし周の武王が殷の紂王を討ったとき、褒美に妲己を弟の周公に与えましたよね?","曹操",},
        [1147] = {20181904,"孔融","曹操殿は烏桓の討伐に行かれるそうですが。","曹操殿は烏桓の討伐に行かれるそうですが。","曹操",},
        [1148] = {20181904,"孔融","夷狄といえば、近ごろ粛慎は弓矢の貢ぎ物を持って来ませんねえ。","夷狄といえば、近ごろ粛慎は弓矢の貢ぎ物を持って来ませんねえ。","曹操",},
        [1149] = {20181904,"孔融","そのむかし蘇武の羊を盗んだ丁零もけしからんですねえ。ついでに退治して来たらどうです?","そのむかし蘇武の羊を盗んだ丁零もけしからんですねえ。ついでに退治して来たらどうです?","曹操",},
        [1150] = {20181904,"曹操","おのれ孔融、故事になぞらえて何度もわしを侮辱しおって！","おのれ孔融、故事になぞらえて何度もわしを侮辱しおって！","孔融",},
        [1151] = {20181904,"曹操","董卓や袁紹は、お前が孔子の末裔だからと殺さずにいたが、わしはそうはいかんぞ！","董卓や袁紹は、お前が孔子の末裔だからと殺さずにいたが、わしはそうはいかんぞ！","孔融",},
        [1152] = {20181904,"曹操","お前が徒党を集めて不正を企み、朝廷を誹謗したと罪を着せてやる！","お前が徒党を集めて不正を企み、朝廷を誹謗したと罪を着せてやる！","孔融",},
        [1153] = {20181904,"曹操","震えて眠るがよい。一族郎党巻き添えだ！","震えて眠るがよい。一族郎党巻き添えだ！","孔融",},
        [1154] = {20181905,"献帝","汝は孔子の末裔で、忠孝を重んじ、名士の風格がある。仁義を心得ており、高潔だ。","汝は孔子の末裔で、忠孝を重んじ、名士の風格がある。仁義を心得ており、高潔だ。","孔融",},
        [1155] = {20181905,"献帝","都を許昌に移し、汝を将作大匠に任じよう。","都を許昌に移し、汝を将作大匠に任じよう。","孔融",},
        [1156] = {20181905,"孔融","ありがたき幸せ。必ずや全力で陛下を補佐いたします。","ありがたき幸せ。必ずや全力で陛下を補佐いたします。","献帝",},
        [1157] = {20182001,"袁紹","郭嘉よ、なぜわしを捨てていくのだ?","郭嘉よ、なぜわしを捨てていくのだ?","郭嘉",},
        [1158] = {20182001,"郭嘉","失礼ながら、袁紹殿は周公が賢人を重んじたのを真似しているだけで、人の使い方を分かっておりません。","失礼ながら、袁紹殿は周公が賢人を重んじたのを真似しているだけで、人の使い方を分かっておりません。","袁紹",},
        [1159] = {20182001,"郭嘉","あれこれ考えはするものの要領を欠き、計画はすれど決断が伴いません。","あれこれ考えはするものの要領を欠き、計画はすれど決断が伴いません。","袁紹",},
        [1160] = {20182001,"郭嘉","ですから、あなたと共に国家の危機を救い、大業を成すのは難しいと判断したのです。","ですから、あなたと共に国家の危機を救い、大業を成すのは難しいと判断したのです。","袁紹",},
        [1161] = {20182002,"荀彧","郭嘉、あなたは袁紹の元を離れてから六年、ずっと暇を持て余し、その策謀の才を発揮できずにいますね。","郭嘉、あなたは袁紹の元を離れてから六年、ずっと暇を持て余し、その策謀の才を発揮できずにいますね。","郭嘉",},
        [1162] = {20182002,"荀彧","ちょうど我が主の曹操が、大業を成すための策士を求めています。","ちょうど我が主の曹操が、大業を成すための策士を求めています。","郭嘉",},
        [1163] = {20182002,"荀彧","あなたを推薦したいと思っているのですが、どうですか?","あなたを推薦したいと思っているのですが、どうですか?","郭嘉",},
        [1164] = {20182002,"郭嘉","曹操とはどういう人なのです?","曹操とはどういう人なのです?","荀彧",},
        [1165] = {20182002,"荀彧","「乱世の奸雄」……と、言われていますが。人を良く観察し、賢才を愛し、貴賤にこだわらず、人を良く用います。","「乱世の奸雄」……と、言われていますが。人を良く観察し、賢才を愛し、貴賤にこだわらず、人を良く用います。","郭嘉",},
        [1166] = {20182002,"郭嘉","人を良く用いる、ですか……興味深いですね。","人を良く用いる、ですか……興味深いですね。","荀彧",},
        [1167] = {20182003,"曹操","この幾晩、天下の情勢について話し合ったが、荀彧が言った通り、お前は当代の逸物だ。","この幾晩、天下の情勢について話し合ったが、荀彧が言った通り、お前は当代の逸物だ。","郭嘉",},
        [1168] = {20182003,"曹操","わしの大業を成してくれるのは、必ずやお前であろう。期待しているぞ。","わしの大業を成してくれるのは、必ずやお前であろう。期待しているぞ。","郭嘉",},
        [1169] = {20182003,"郭嘉","賢才を愛し、貴賤にこだわらぬその姿勢。誠に感服いたします。ならば、私も全力で応えるのみ。","賢才を愛し、貴賤にこだわらぬその姿勢。誠に感服いたします。ならば、私も全力で応えるのみ。","曹操",},
        [1170] = {20182003,"郭嘉","我が君を補佐し、必ずやこの乱世を平らげてみせましょう。","我が君を補佐し、必ずやこの乱世を平らげてみせましょう。","曹操",},
        [1171] = {20182004,"陳羣","殿、郭嘉という者は、この幕下での無礼な行為は目に余ります。","殿、郭嘉という者は、この幕下での無礼な行為は目に余ります。","曹操",},
        [1172] = {20182004,"陳羣","殿と同じ車に同席しておきながら、君臣の礼儀というものを知らぬのです。","殿と同じ車に同席しておきながら、君臣の礼儀というものを知らぬのです。","曹操",},
        [1173] = {20182004,"曹操","陳羣、お前の言うことは最もだ。しかし――","陳羣、お前の言うことは最もだ。しかし――","陳羣",},
        [1174] = {20182004,"曹操","郭嘉は突出した人物、常識ではかるべき器ではない。","郭嘉は突出した人物、常識ではかるべき器ではない。","陳羣",},
        [1175] = {20182004,"曹操","気にするな。","気にするな。","陳羣",},
        [1176] = {20182005,"曹操","袁尚はすでに飼い主のいない野良犬。辺境の胡人どもはヤツらを支援するまい。","袁尚はすでに飼い主のいない野良犬。辺境の胡人どもはヤツらを支援するまい。","郭嘉",},
        [1177] = {20182005,"曹操","遠征すれば、その隙に劉備が劉表をそそのかし、この許昌を襲ってくるだろう。","遠征すれば、その隙に劉備が劉表をそそのかし、この許昌を襲ってくるだろう。","郭嘉",},
        [1178] = {20182005,"郭嘉","失礼ながら。胡人は袁紹に恩があります。彼らはきっと袁尚を助けるでしょう。","失礼ながら。胡人は袁紹に恩があります。彼らはきっと袁尚を助けるでしょう。","曹操",},
        [1179] = {20182005,"郭嘉","劉表はただの政治屋。能力は劉備におよばぬのはもちろん、その劉備に対しても備えねばならないため、攻めては来ないでしょう。","劉表はただの政治屋。能力は劉備におよばぬのはもちろん、その劉備に対しても備えねばならないため、攻めては来ないでしょう。","曹操",},
        [1180] = {20182005,"郭嘉","しかし、胡人は辺境にいるため、必然的に守りを固めていません。一気に攻撃を仕掛ければ、必ず滅ぼせるでしょう。","しかし、胡人は辺境にいるため、必然的に守りを固めていません。一気に攻撃を仕掛ければ、必ず滅ぼせるでしょう。","曹操",},
        [1181] = {20182005,"郭嘉","今は無駄な遠征に思えますが、一度苦労しておけば、後顧の憂いを絶つことができるはずです。","今は無駄な遠征に思えますが、一度苦労しておけば、後顧の憂いを絶つことができるはずです。","曹操",},
        [1182] = {20182005,"曹操","さすがは郭嘉よ。要点ずばりと突いてくれて、わしも目が覚めた。すぐに兵を柳城に向けるぞ。","さすがは郭嘉よ。要点ずばりと突いてくれて、わしも目が覚めた。すぐに兵を柳城に向けるぞ。","郭嘉",},
        [1183] = {20182005,"郭嘉","ならば身軽な兵で素早く進み、備えの無いところを攻めるのです。","ならば身軽な兵で素早く進み、備えの無いところを攻めるのです。","曹操",},
        [1184] = {20182101,"孫権","偶然、象を手に入れた。曹操殿に献上いたそう。","偶然、象を手に入れた。曹操殿に献上いたそう。","曹操",},
        [1185] = {20182101,"孫権","俺からの気持ちだ。どうか収めてくれ。","俺からの気持ちだ。どうか収めてくれ。","曹操",},
        [1186] = {20182101,"曹操","孫権殿、ご好意に感謝する。","孫権殿、ご好意に感謝する。","孫権",},
        [1187] = {20182101,"孫権","おっと、そうであった。曹操殿、俺は才が足りず、象の重さが分からなかった。","おっと、そうであった。曹操殿、俺は才が足りず、象の重さが分からなかった。","曹操",},
        [1188] = {20182101,"孫権","ですが貴公の陣営には賢者や策士が数多い。","ですが貴公の陣営には賢者や策士が数多い。","曹操",},
        [1189] = {20182101,"孫権","象の重さを量ることができたら、必ず俺に教えてほしいのだ。","象の重さを量ることができたら、必ず俺に教えてほしいのだ。","曹操",},
        [1190] = {20182101,"曹操","承知いたした。必ず……","承知いたした。必ず……","孫権",},
        [1191] = {20182102,"曹操","おのれ孫権め。わざと象を贈って、わしを辱めるつもりだな。","おのれ孫権め。わざと象を贈って、わしを辱めるつもりだな。","",},
        [1192] = {20182102,"曹操","これで象の重さが分からなかったら、わしの面子はどうなる。","これで象の重さが分からなかったら、わしの面子はどうなる。","",},
        [1193] = {20182102,"曹操","誰か、わかるものはおらんか。わしに知恵を貸してくれ。","誰か、わかるものはおらんか。わしに知恵を貸してくれ。","",},
        [1194] = {20182103,"曹沖","簡単なことだよ。まず象を船に乗せ、水面が触れたところに目印をつけるんだ。","簡単なことだよ。まず象を船に乗せ、水面が触れたところに目印をつけるんだ。","曹操",},
        [1195] = {20182103,"曹沖","次に船から象を下ろして別の荷物を積み、水面を先ほどの目印の位置にする。","次に船から象を下ろして別の荷物を積み、水面を先ほどの目印の位置にする。","曹操",},
        [1196] = {20182103,"曹沖","最後にこの荷物の重さを計れば、象の重さが分かると思うよ。","最後にこの荷物の重さを計れば、象の重さが分かると思うよ。","曹操",},
        [1197] = {20182103,"曹操","おお、そうか！はっはっは、やはりわしの曹沖は賢いな。","おお、そうか！はっはっは、やはりわしの曹沖は賢いな。","曹沖",},
        [1198] = {20182103,"曹操","さっそく孫権に教えてやろう、はっはっは。","さっそく孫権に教えてやろう、はっはっは。","曹沖",},
        [1199] = {20182201,"貂蝉","ねえ、董卓さま。呂布のヤツがわたくしに酷いことをするの……","ねえ、董卓さま。呂布のヤツがわたくしに酷いことをするの……","董卓",},
        [1200] = {20182201,"貂蝉","あの乱暴な人から、どうかわたくしを守ってください！","あの乱暴な人から、どうかわたくしを守ってください！","董卓",},
        [1201] = {20182201,"董卓","なんだとっ！?くそっ、あやつは若く勇猛で、武力もずば抜けていた。","なんだとっ！?くそっ、あやつは若く勇猛で、武力もずば抜けていた。","貂蝉",},
        [1202] = {20182201,"董卓","だから赤兔馬を贈り、養子にもしたのに！まさかそんなヤツだとは！","だから赤兔馬を贈り、養子にもしたのに！まさかそんなヤツだとは！","貂蝉",},
        [1203] = {20182201,"董卓","安心せい、わしがなんとかしてやるわ！","安心せい、わしがなんとかしてやるわ！","貂蝉",},
        [1204] = {20182202,"貂蝉","呂布さま、わたくしはあなたを恋い慕っております。","呂布さま、わたくしはあなたを恋い慕っております。","呂布",},
        [1205] = {20182202,"貂蝉","なのに、董卓はそれをこころよく思わず、わたくしを籠の鳥にしているのです。","なのに、董卓はそれをこころよく思わず、わたくしを籠の鳥にしているのです。","呂布",},
        [1206] = {20182202,"貂蝉","ああ呂布さま、どうかわたくしを救い出してください！","ああ呂布さま、どうかわたくしを救い出してください！","呂布",},
        [1207] = {20182202,"呂布","おのれ、老賊董卓……！あやつを義父などと呼ぶものではなかった！","おのれ、老賊董卓……！あやつを義父などと呼ぶものではなかった！","貂蝉",},
        [1208] = {20182202,"呂布","俺の貂蝉を弄びおって、殺してやる！","俺の貂蝉を弄びおって、殺してやる！","貂蝉",},
        [1209] = {20182203,"董卓","うおっ！?このわしを狙うとは、何者じゃあ！(董卓の車の列が北掖門の外に出た時、何者かが長戟を持って飛び出してきた)","うおっ！?このわしを狙うとは、何者じゃあ！(董卓の車の列が北掖門の外に出た時、何者かが長戟を持って飛び出してきた)","",},
        [1210] = {20182203,"董卓","こんなこともあろうかと、服の下に鎧を着ていたのだ。そう簡単にやられはせんぞ！","こんなこともあろうかと、服の下に鎧を着ていたのだ。そう簡単にやられはせんぞ！","",},
        [1211] = {20182203,"董卓","呂布ーっ！呂布はどこだ?","呂布ーっ！呂布はどこだ?","",},
        [1212] = {20182203,"董卓","呂布、わしはここだぞ！早く助けに来い！","呂布、わしはここだぞ！早く助けに来い！","",},
        [1213] = {20182204,"董卓","呂布はここかーっ！?","呂布はここかーっ！?","",},
        [1214] = {20182204,"呂布","董卓よ、今日が貴様の命日だ！詔により賊臣を討つ！(悠々と準備した詔書を取り出し、董卓に示した)","董卓よ、今日が貴様の命日だ！詔により賊臣を討つ！(悠々と準備した詔書を取り出し、董卓に示した)","董卓",},
        [1215] = {20182204,"董卓","なにをっ！?駄犬め、血迷ったか！","なにをっ！?駄犬め、血迷ったか！","呂布",},
        [1216] = {20182204,"董卓","田景っ！どこじゃ、早く助けに来い！","田景っ！どこじゃ、早く助けに来い！","呂布",},
        [1217] = {20182204,"呂布","馬鹿め、死ね！賊臣は活かしておけぬわ！(董卓、田景とその従者たちをすぐさま斬り殺した)","馬鹿め、死ね！賊臣は活かしておけぬわ！(董卓、田景とその従者たちをすぐさま斬り殺した)","董卓",},
        [1218] = {20182301,"凌統","俺は戦の前に督の陳勤を斬りつけ、その傷のせいで死なせてしまいました。","俺は戦の前に督の陳勤を斬りつけ、その傷のせいで死なせてしまいました。","孫権",},
        [1219] = {20182301,"凌統","死んで詫びようと戦場に出たところ、思いがけず大勝してしまって果たせず――","死んで詫びようと戦場に出たところ、思いがけず大勝してしまって果たせず――","孫権",},
        [1220] = {20182301,"凌統","ここに改めて自首して参りました。どうぞ軍法で裁いてください。","ここに改めて自首して参りました。どうぞ軍法で裁いてください。","孫権",},
        [1221] = {20182301,"孫権","凌統よ。確かに監督官の陳勤はお前に斬られたが、俺は処罰しない。","凌統よ。確かに監督官の陳勤はお前に斬られたが、俺は処罰しない。","凌統",},
        [1222] = {20182301,"孫権","すでに調べはついている。ヤツは酒に酔ってお前とお前の父の凌操を侮辱し、軍紀を乱した。","すでに調べはついている。ヤツは酒に酔ってお前とお前の父の凌操を侮辱し、軍紀を乱した。","凌統",},
        [1223] = {20182301,"孫権","さらに、お前は勇敢に戦い、俺の砦攻略を助けた。以上２つの理由により、お前の罪は不問とする。","さらに、お前は勇敢に戦い、俺の砦攻略を助けた。以上２つの理由により、お前の罪は不問とする。","凌統",},
        [1224] = {20182302,"凌統","呂蒙殿、今日は感謝するぞ！このような楽しい宴に招待してくれた。","呂蒙殿、今日は感謝するぞ！このような楽しい宴に招待してくれた。","",},
        [1225] = {20182302,"凌統","しかし、酒ばかり飲んでいるのもつまらんな。どれ、俺がみんなを盛り上げてやろう。(そういうと、刀を持って舞い始めた)","しかし、酒ばかり飲んでいるのもつまらんな。どれ、俺がみんなを盛り上げてやろう。(そういうと、刀を持って舞い始めた)","",},
        [1226] = {20182302,"甘寧","ハッハーッ！オレの双戟の舞いはどうだ！","ハッハーッ！オレの双戟の舞いはどうだ！","凌統",},
        [1227] = {20182302,"呂蒙","待て待て、甘寧よ。悪くはないが、私の舞ほどではないだろう。(呂蒙は急いで刀と盾を持ち、凌統と甘寧の間に割って入った)","待て待て、甘寧よ。悪くはないが、私の舞ほどではないだろう。(呂蒙は急いで刀と盾を持ち、凌統と甘寧の間に割って入った)","甘寧",},
        [1228] = {20182303,"呂蒙","凌統、殿はずっとお前と甘寧の不和を心配しておられる。","凌統、殿はずっとお前と甘寧の不和を心配しておられる。","凌統",},
        [1229] = {20182303,"呂蒙","お前の父親を殺したのは甘寧だが、この乱世ではままならぬこともあろう。","お前の父親を殺したのは甘寧だが、この乱世ではままならぬこともあろう。","凌統",},
        [1230] = {20182303,"呂蒙","今は皆、仲間ではないか。好きになれとは言わんが、昨日の宴会の剣舞のような真似はもうしないでくれ、いいな?","今は皆、仲間ではないか。好きになれとは言わんが、昨日の宴会の剣舞のような真似はもうしないでくれ、いいな?","凌統",},
        [1231] = {20182303,"凌統","わかった。納得はしかねるが、約束しよう。","わかった。納得はしかねるが、約束しよう。","呂蒙",},
        [1232] = {20182401,"孫策","あの妖術使い、于吉は斬らねばならぬ。","あの妖術使い、于吉は斬らねばならぬ。","張昭",},
        [1233] = {20182401,"張昭","それはなりません、殿！","それはなりません、殿！","孫策",},
        [1234] = {20182401,"張昭","あの者は東方より来て、廟を建て、香を焚いて道書を読み、符水を作って病を治しております。","あの者は東方より来て、廟を建て、香を焚いて道書を読み、符水を作って病を治しております。","孫策",},
        [1235] = {20182401,"張昭","人の心を深く掴んでいるのです。","人の心を深く掴んでいるのです。","孫策",},
        [1236] = {20182401,"孫策","心を掴んでいるだと?違うな。ヤツは江東の人心を惑わしているだけだ。","心を掴んでいるだと?違うな。ヤツは江東の人心を惑わしているだけだ。","張昭",},
        [1237] = {20182401,"孫策","世に知らしめねばならん。江東を統べるのはこの孫策だ。あのような妖術使いではない！","世に知らしめねばならん。江東を統べるのはこの孫策だ。あのような妖術使いではない！","張昭",},
        [1238] = {20182402,"于吉","孫策、私になんの罪がある?","孫策、私になんの罪がある?","孫策",},
        [1239] = {20182402,"孫策","お前は江東を騒がし、民心を惑わした。ここが誰の領土か分からないのか。","お前は江東を騒がし、民心を惑わした。ここが誰の領土か分からないのか。","于吉",},
        [1240] = {20182402,"于吉","私に罪はない！殺せば天罰が下るぞ！","私に罪はない！殺せば天罰が下るぞ！","孫策",},
        [1241] = {20182402,"孫策","黙れ！誰か、この妖道士を斬れ！","黙れ！誰か、この妖道士を斬れ！","于吉",},
        [1242] = {20182403,"孫策","誰だ！(鏡の中の左右に人影が写るが、振り返ると誰もいない)","誰だ！(鏡の中の左右に人影が写るが、振り返ると誰もいない)","",},
        [1243] = {20182403,"孫策","俺をからかうのは誰だ！","俺をからかうのは誰だ！","",},
        [1244] = {20182403,"于吉","……私は惨めに殺された。","……私は惨めに殺された。","孫策",},
        [1245] = {20182403,"孫策","誰だ！","誰だ！","于吉",},
        [1246] = {20182403,"于吉","……罪なくして、惨めに殺された！","……罪なくして、惨めに殺された！","孫策",},
        [1247] = {20182403,"孫策","わかったぞ！あのときの妖道士だな、出てこい！","わかったぞ！あのときの妖道士だな、出てこい！","于吉",},
        [1248] = {20182403,"孫策","出てこい！！(激怒して鏡を割り、力の限り叫ぶと、顔の矢傷が裂けて血が流れ出した)","出てこい！！(激怒して鏡を割り、力の限り叫ぶと、顔の矢傷が裂けて血が流れ出した)","于吉",},
        [1249] = {20182501,"孫尚香","あたしのお兄ちゃんのこと、どう思う?","あたしのお兄ちゃんのこと、どう思う?","歩練師",},
        [1250] = {20182501,"歩練師","孫権さまのことですか?とても聡明で、慈愛と才智の持ち主と思います。","孫権さまのことですか?とても聡明で、慈愛と才智の持ち主と思います。","孫尚香",},
        [1251] = {20182501,"孫尚香","へぇー、そうなんだ。じゃあ当ててみて、お兄ちゃんはあなたのことをどう思ってると思う?","へぇー、そうなんだ。じゃあ当ててみて、お兄ちゃんはあなたのことをどう思ってると思う?","歩練師",},
        [1252] = {20182501,"歩練師","えっ?それは……すみません、分かりません……(照れながら返事をする)","えっ?それは……すみません、分かりません……(照れながら返事をする)","孫尚香",},
        [1253] = {20182501,"孫尚香","じゃ、教えてあげるね！お兄ちゃんはあなたに一目ぼれしてゾッコンなんだってさ、よかったね！","じゃ、教えてあげるね！お兄ちゃんはあなたに一目ぼれしてゾッコンなんだってさ、よかったね！","歩練師",},
        [1254] = {20182502,"孫権","歩練師。よく、俺のそばに数十年も付き添ってくれたな。お前は寛容で慈悲深く、人の意思をよく理解する。","歩練師。よく、俺のそばに数十年も付き添ってくれたな。お前は寛容で慈悲深く、人の意思をよく理解する。","歩練師",},
        [1255] = {20182502,"孫権","それに、俺のために全公主と朱公主を生んでくれた。俺は本当に幸せ者だ！","それに、俺のために全公主と朱公主を生んでくれた。俺は本当に幸せ者だ！","歩練師",},
        [1256] = {20182502,"歩練師","お褒めが過ぎますわ、孫権様。","お褒めが過ぎますわ、孫権様。","孫権",},
        [1257] = {20182502,"歩練師","思えば不思議なものですね。","思えば不思議なものですね。","孫権",},
        [1258] = {20182502,"歩練師","もし廬江があなたに攻め落とされなければ、もし母とともに江東に渡って来なければ、そしてあなたのお目に留まらなければ――","もし廬江があなたに攻め落とされなければ、もし母とともに江東に渡って来なければ、そしてあなたのお目に留まらなければ――","孫権",},
        [1259] = {20182502,"歩練師","わたしはあなたの夫人にはなりませんでした。","わたしはあなたの夫人にはなりませんでした。","孫権",},
        [1260] = {20182502,"孫権","どこにいたとしても、お前の姿は必ず俺の目に留まったであろう。それほどに、そなたはあまりに美しかったからだ。","どこにいたとしても、お前の姿は必ず俺の目に留まったであろう。それほどに、そなたはあまりに美しかったからだ。","歩練師",},
        [1261] = {20182502,"歩練師","まぁ……お戯れを！","まぁ……お戯れを！","孫権",},
        [1262] = {20182601,"曹操","袁紹よ、前に若い夫婦がいるのがわかるか?なかなかの花嫁だ。どうだ、一緒に奪いに行かないか?","袁紹よ、前に若い夫婦がいるのがわかるか?なかなかの花嫁だ。どうだ、一緒に奪いに行かないか?","袁紹",},
        [1263] = {20182601,"袁紹","そいつは面白そうだ。","そいつは面白そうだ。","曹操",},
        [1264] = {20182601,"曹操","決まったな。宴の盃が三巡したあたりで実行しよう。","決まったな。宴の盃が三巡したあたりで実行しよう。","袁紹",},
        [1265] = {20182601,"袁紹","よし！","よし！","曹操",},
        [1266] = {20182602,"曹操","袁紹！花嫁はさらった。早く逃げるぞ。","袁紹！花嫁はさらった。早く逃げるぞ。","袁紹",},
        [1267] = {20182602,"袁紹","まずい、服が木の枝に引っかかった。助けてくれ！","まずい、服が木の枝に引っかかった。助けてくれ！","曹操",},
        [1268] = {20182602,"曹操","なにをやってる！?どうしてそんなに間抜けなのだ！……仕方ない","なにをやってる！?どうしてそんなに間抜けなのだ！……仕方ない","袁紹",},
        [1269] = {20182602,"曹操","おーい、泥棒がここにいるぞ！(大声で叫ぶ)","おーい、泥棒がここにいるぞ！(大声で叫ぶ)","袁紹",},
        [1270] = {20182602,"袁紹","ば、馬鹿っ！わしを身代わりにする気か！(焦って服を引き裂いて抜け出した)","ば、馬鹿っ！わしを身代わりにする気か！(焦って服を引き裂いて抜け出した)","曹操",},
        [1271] = {20182602,"曹操","よし、うまく抜け出せたな。作戦成功だ、逃げるぞ！","よし、うまく抜け出せたな。作戦成功だ、逃げるぞ！","袁紹",},
        [1272] = {20182603,"公孫瓚","私の弟の命は、貴様によって奪われた。","私の弟の命は、貴様によって奪われた。","袁紹",},
        [1273] = {20182603,"公孫瓚","今また貴様は兵を挙げ、今度は冀州を奪おうとしている。","今また貴様は兵を挙げ、今度は冀州を奪おうとしている。","袁紹",},
        [1274] = {20182603,"公孫瓚","この新旧二つの恨み、まとめて晴らしてくれる！","この新旧二つの恨み、まとめて晴らしてくれる！","袁紹",},
        [1275] = {20182603,"袁紹","いいだろう！わしは麹義に精鋭八百を与え、界橋でお前と戦わせよう。この勝負、受けるか?","いいだろう！わしは麹義に精鋭八百を与え、界橋でお前と戦わせよう。この勝負、受けるか?","公孫瓚",},
        [1276] = {20182603,"公孫瓚","たった八百で私の三万の精鋭を相手にしようというのか、笑わせてくれる！","たった八百で私の三万の精鋭を相手にしようというのか、笑わせてくれる！","袁紹",},
        [1277] = {20182603,"袁紹","フン！笑いごとかどうか、戦えば分かる！","フン！笑いごとかどうか、戦えば分かる！","公孫瓚",},
        [1278] = {20182604,"袁紹","わしは名門、汝南袁氏の出身だ。","わしは名門、汝南袁氏の出身だ。","",},
        [1279] = {20182604,"袁紹","曾祖父より四代続けて、我が袁氏から五人が三公の位についたのだ。","曾祖父より四代続けて、我が袁氏から五人が三公の位についたのだ。","",},
        [1280] = {20182604,"袁紹","そして、このわしも三公より上にいる。お前たちとは格が違うのだ！","そして、このわしも三公より上にいる。お前たちとは格が違うのだ！","",},
        [1281] = {20182701,"陳宮","呂伯奢はあなたの旧友、そして命の恩人だったはず……","呂伯奢はあなたの旧友、そして命の恩人だったはず……","曹操",},
        [1282] = {20182701,"陳宮","それがこんなにも冷酷に、しかも一方的な思い込みで一家ごと惨殺するとは……！","それがこんなにも冷酷に、しかも一方的な思い込みで一家ごと惨殺するとは……！","曹操",},
        [1283] = {20182701,"曹操","ふん、だからどうしたと言うのだ。","ふん、だからどうしたと言うのだ。","陳宮",},
        [1284] = {20182701,"曹操","わしが天下の人間に背くことはあっても、天下の人間がわしに背くことは許さん！","わしが天下の人間に背くことはあっても、天下の人間がわしに背くことは許さん！","陳宮",},
        [1285] = {20182701,"陳宮","なんと非道な……！もうあなたにはついていけない！","なんと非道な……！もうあなたにはついていけない！","曹操",},
        [1286] = {20182702,"陳宮","下邳はやがて包囲されるでしょう。私が思うに、ここは一部隊を連れ城外に駐屯すべきです。","下邳はやがて包囲されるでしょう。私が思うに、ここは一部隊を連れ城外に駐屯すべきです。","呂布",},
        [1287] = {20182702,"陳宮","そして城内と挟み撃ちの形にすれば、曹操軍の包囲を破ることができましょう。","そして城内と挟み撃ちの形にすれば、曹操軍の包囲を破ることができましょう。","呂布",},
        [1288] = {20182702,"呂布","この戦神呂布を、誰が止められようか！","この戦神呂布を、誰が止められようか！","陳宮",},
        [1289] = {20182702,"呂布","そのような姑息な策などいらん。下邳城を固く守り、曹操が自らやってくるのを待てばよいわ！","そのような姑息な策などいらん。下邳城を固く守り、曹操が自らやってくるのを待てばよいわ！","陳宮",},
        [1290] = {20182703,"曹操","呂布はすでに死に、張遼はわしに降伏した。","呂布はすでに死に、張遼はわしに降伏した。","陳宮",},
        [1291] = {20182703,"曹操","陳宮よ、昔の恨みは捨て、わしの元へ戻らぬか?","陳宮よ、昔の恨みは捨て、わしの元へ戻らぬか?","陳宮",},
        [1292] = {20182703,"陳宮","冗談ではない。たとえ死すとも、誓ってそちらの陣営には入らぬ。","冗談ではない。たとえ死すとも、誓ってそちらの陣営には入らぬ。","曹操",},
        [1293] = {20182703,"曹操","お前がここで死んだら、母親と妻子はどうなる?","お前がここで死んだら、母親と妻子はどうなる?","陳宮",},
        [1294] = {20182703,"陳宮","孝道をもって天下を治める者は人の親を殺さず、天下に慈悲を施す者は人の子孫を絶ちはしないものだ。","孝道をもって天下を治める者は人の親を殺さず、天下に慈悲を施す者は人の子孫を絶ちはしないものだ。","曹操",},
        [1295] = {20182703,"陳宮","すべては貴殿のお心一つ。……心配はしていない。","すべては貴殿のお心一つ。……心配はしていない。","曹操",},
        [1296] = {20182801,"王朗","お前は鮑信の募兵に応じ、黄巾軍の討伐に赴いたことがあったな。","お前は鮑信の募兵に応じ、黄巾軍の討伐に赴いたことがあったな。","于禁",},
        [1297] = {20182801,"王朗","勇猛に戦い、戦功をあげた。","勇猛に戦い、戦功をあげた。","于禁",},
        [1298] = {20182801,"王朗","その働き、実に目覚ましい。私は丞相殿にお前を推薦するつもりだ。","その働き、実に目覚ましい。私は丞相殿にお前を推薦するつもりだ。","于禁",},
        [1299] = {20182801,"于禁","ははっ、ありがたき幸せ。将軍の厚遇に感謝いたします。","ははっ、ありがたき幸せ。将軍の厚遇に感謝いたします。","王朗",},
        [1300] = {20182802,"曹操","建安二年に、お前はわしに従って宛州で戦ったな。","建安二年に、お前はわしに従って宛州で戦ったな。","于禁",},
        [1301] = {20182802,"曹操","張繍の偽りの降伏によって、わしはあやうく死ぬところだった。","張繍の偽りの降伏によって、わしはあやうく死ぬところだった。","于禁",},
        [1302] = {20182802,"曹操","しかし、お前が冷静に対処し、わしの命を守ったのだ。素晴らしい働きだったぞ。","しかし、お前が冷静に対処し、わしの命を守ったのだ。素晴らしい働きだったぞ。","于禁",},
        [1303] = {20182802,"于禁","もったいないお言葉です、丞相。俺は臣下の責任を果たしただけです。","もったいないお言葉です、丞相。俺は臣下の責任を果たしただけです。","曹操",},
        [1304] = {20182901,"夏侯惇","高順よぉ、どこへ行くっ！俺ともう五十合打ち合おうぞぉっ！","高順よぉ、どこへ行くっ！俺ともう五十合打ち合おうぞぉっ！","",},
        [1305] = {20182901,"夏侯惇","逃がすかぁっ！","逃がすかぁっ！","",},
        [1306] = {20182901,"曹性","(弓を引き絞って)よし、狙いは定めた。高順、頭を下げよ！","(弓を引き絞って)よし、狙いは定めた。高順、頭を下げよ！","夏侯惇",},
        [1307] = {20182901,"曹性","夏侯惇、お命頂戴！この矢をくらえ！","夏侯惇、お命頂戴！この矢をくらえ！","夏侯惇",},
        [1308] = {20182902,"夏侯惇","こたびの敗戦、また俺のしくじりだ！面目ないっ！","こたびの敗戦、また俺のしくじりだ！面目ないっ！","曹操",},
        [1309] = {20182902,"曹操","気にするな、大したことではない。次回勝てばいいのだ。","気にするな、大したことではない。次回勝てばいいのだ。","夏侯惇",},
        [1310] = {20182902,"曹操","お前は最も信頼している忠臣であり、寝床すら共にする義兄弟だ。","お前は最も信頼している忠臣であり、寝床すら共にする義兄弟だ。","夏侯惇",},
        [1311] = {20182902,"曹操","勝敗は兵家の常。多少苦労が続いても、自分を責めるな。","勝敗は兵家の常。多少苦労が続いても、自分を責めるな。","夏侯惇",},
        [1312] = {20183001,"蔡文姫","お父さまが先ほど弾いた曲、少し音律が違いましたわ。","お父さまが先ほど弾いた曲、少し音律が違いましたわ。","蔡邕",},
        [1313] = {20183001,"蔡文姫","琴の二番目の弦が切れていませんか?","琴の二番目の弦が切れていませんか?","蔡邕",},
        [1314] = {20183001,"蔡邕","おお、その通り！二番目の弦が切れている！(そう言うと、わざと四番目の弦を切って弾き始めた)","おお、その通り！二番目の弦が切れている！(そう言うと、わざと四番目の弦を切って弾き始めた)","蔡文姫",},
        [1315] = {20183001,"蔡邕","文姫よ、今度は切れたのは何番目の弦だ?","文姫よ、今度は切れたのは何番目の弦だ?","蔡文姫",},
        [1316] = {20183001,"蔡文姫","四番目です。","四番目です。","蔡邕",},
        [1317] = {20183001,"蔡邕","なんと素晴らしい！天性の音感だ。","なんと素晴らしい！天性の音感だ。","蔡文姫",},
        [1318] = {20183002,"匈奴追い剥ぎ","ガハハハ！董卓や李傕 が関中で乱を起こしているおかげで略奪し放題だ！乱世様様だな！","ガハハハ！董卓や李傕 が関中で乱を起こしているおかげで略奪し放題だ！乱世様様だな！","",},
        [1319] = {20183002,"匈奴追い剥ぎ","しかも、今日は運がいい！きれいな漢人の女もさらってきたぜ。","しかも、今日は運がいい！きれいな漢人の女もさらってきたぜ。","",},
        [1320] = {20183002,"匈奴追い剥ぎ","左賢王さまに献上しよう。喜んでくれるかな～！","左賢王さまに献上しよう。喜んでくれるかな～！","",},
        [1321] = {20183002,"蔡文姫","いやっ、誰か助けて！","いやっ、誰か助けて！","匈奴追い剥ぎ",},
        [1322] = {20183003,"曹操","わしは子供のころ、よく蔡邕先生の家を訪ねていた。","わしは子供のころ、よく蔡邕先生の家を訪ねていた。","",},
        [1323] = {20183003,"曹操","その娘、蔡文姫は音楽に精通した才人で、わしとも仲が良かった。","その娘、蔡文姫は音楽に精通した才人で、わしとも仲が良かった。","",},
        [1324] = {20183003,"曹操","どうやら12年もの間、異郷を放浪しているらしいが……彼女を取り戻さねばならん。","どうやら12年もの間、異郷を放浪しているらしいが……彼女を取り戻さねばならん。","",},
        [1325] = {20183003,"曹操","金璧を携えて、匈奴と交渉するのだ。","金璧を携えて、匈奴と交渉するのだ。","",},
        [1326] = {20183004,"曹操","どうしたのだ、文姫よ。なぜ裸足で跪く?","どうしたのだ、文姫よ。なぜ裸足で跪く?","蔡文姫",},
        [1327] = {20183004,"蔡文姫","丞相、どうか夫の命だけはお許しください。夫の董祀の罪は、死ぬほどのものとは思えません。","丞相、どうか夫の命だけはお許しください。夫の董祀の罪は、死ぬほどのものとは思えません。","曹操",},
        [1328] = {20183004,"曹操","刑の執行はすでに命令済みだ。一度口にした言葉は取り消せん。","刑の執行はすでに命令済みだ。一度口にした言葉は取り消せん。","蔡文姫",},
        [1329] = {20183004,"蔡文姫","なぜでございますか?丞相のもとには有能な方が大勢おられ、馬小屋には駿馬が何千頭とおりますのに。","なぜでございますか?丞相のもとには有能な方が大勢おられ、馬小屋には駿馬が何千頭とおりますのに。","曹操",},
        [1330] = {20183004,"蔡文姫","ただ一人を派遣すれば済むことではありませんか。","ただ一人を派遣すれば済むことではありませんか。","曹操",},
        [1331] = {20183004,"蔡文姫","それに……どんな不可能なことでもやってのけるのが、あなた様ではないのですか?","それに……どんな不可能なことでもやってのけるのが、あなた様ではないのですか?","曹操",},
        [1332] = {20183004,"曹操","お前は本当に弁解が上手だな。分かった、恩赦の令状を出してやろう。","お前は本当に弁解が上手だな。分かった、恩赦の令状を出してやろう。","蔡文姫",},
        [1333] = {20183004,"曹操","しかし、宴会が終わった後に一つ頼みごとがある。","しかし、宴会が終わった後に一つ頼みごとがある。","蔡文姫",},
        [1334] = {20183005,"曹操","お前の家には千冊の書があると聞いたが、それはどこにあるのだ?","お前の家には千冊の書があると聞いたが、それはどこにあるのだ?","蔡文姫",},
        [1335] = {20183005,"蔡文姫","残念ながら、戦乱の中でみな失われてしまいました。しかし、私は四百編以上を暗唱することができます。","残念ながら、戦乱の中でみな失われてしまいました。しかし、私は四百編以上を暗唱することができます。","曹操",},
        [1336] = {20183005,"曹操","そうか。不幸中の幸いというものだな。十人をお前の家に派遣するから、すべて記録させろ。","そうか。不幸中の幸いというものだな。十人をお前の家に派遣するから、すべて記録させろ。","蔡文姫",},
        [1337] = {20183005,"蔡文姫","その必要はありません。筆を一本いただければ、自分で書きます。","その必要はありません。筆を一本いただければ、自分で書きます。","曹操",},
        [1338] = {20183005,"曹操","うむ、いいだろう。","うむ、いいだろう。","蔡文姫",},
        [1339] = {20183101,"黄巾頭目","くそおっ、また許褚の野郎か！","くそおっ、また許褚の野郎か！","",},
        [1340] = {20183101,"黄巾頭目","ヤツは化物だ。牛の尾を引いて百歩以上も歩ける怪力の持ち主だぞ！","ヤツは化物だ。牛の尾を引いて百歩以上も歩ける怪力の持ち主だぞ！","",},
        [1341] = {20183101,"黄巾頭目","俺の部下も何百人も殺されたんだ、逃げろ！","俺の部下も何百人も殺されたんだ、逃げろ！","",},
        [1342] = {20183101,"許褚","待てぇ！お前ら、どこへ行く！","待てぇ！お前ら、どこへ行く！","黄巾頭目",},
        [1343] = {20183102,"曹操","潼関で、韓遂と馬超を討つ。","潼関で、韓遂と馬超を討つ。","許褚",},
        [1344] = {20183102,"曹操","先に本軍に川を渡らせ、許褚とわしが虎豹騎百人を率いて殿を務めるのだ。","先に本軍に川を渡らせ、許褚とわしが虎豹騎百人を率いて殿を務めるのだ。","許褚",},
        [1345] = {20183102,"許褚","はっ！","はっ！","曹操",},
        [1346] = {20183103,"許褚","馬超が襲ってきただぁ！矢が雨のように降ってるぞう！","馬超が襲ってきただぁ！矢が雨のように降ってるぞう！","曹操",},
        [1347] = {20183103,"許褚","恐らく1万はいるぞうっ！味方の部隊はもう川を渡り終わった！殿も早く船へ乗るんだぞう！","恐らく1万はいるぞうっ！味方の部隊はもう川を渡り終わった！殿も早く船へ乗るんだぞう！","曹操",},
        [1348] = {20183103,"曹操","わかっておる！許褚、お前も早く乗れ。","わかっておる！許褚、お前も早く乗れ。","許褚",},
        [1349] = {20183103,"許褚","いいや、おらが乗ったら沈んじまうぞうっ！任せろぉ！","いいや、おらが乗ったら沈んじまうぞうっ！任せろぉ！","曹操",},
        [1350] = {20183103,"許褚","(そう言って川に飛び込むと、左手で鞍を掴んで矢を防ぎ、右手で船を押して川を渡った)","(そう言って川に飛び込むと、左手で鞍を掴んで矢を防ぎ、右手で船を押して川を渡った)","曹操",},
        [1351] = {20183201,"趙雲","劉備さま、ただ今戻りました！","劉備さま、ただ今戻りました！","劉備",},
        [1352] = {20183201,"趙雲","申し訳ありません！阿斗さまは連れ帰ったのですが、糜夫人は……","申し訳ありません！阿斗さまは連れ帰ったのですが、糜夫人は……","劉備",},
        [1353] = {20183201,"劉備","そうか……苦労をかけたな、趙雲。","そうか……苦労をかけたな、趙雲。","趙雲",},
        [1354] = {20183201,"劉備","お前が戻ってきてくれたからいい、それでいいんだ。(そう言いながら趙雲の懐の阿斗を受け取ると、地面に投げ出した)","お前が戻ってきてくれたからいい、それでいいんだ。(そう言いながら趙雲の懐の阿斗を受け取ると、地面に投げ出した)","趙雲",},
        [1355] = {20183201,"劉備","こんな愚息のために、我が大将が命を賭けることなどないのだ！","こんな愚息のために、我が大将が命を賭けることなどないのだ！","趙雲",},
        [1356] = {20183202,"司馬昭","安楽公が洛陽に来られてからずいぶん経ったね。","安楽公が洛陽に来られてからずいぶん経ったね。","阿斗",},
        [1357] = {20183202,"司馬昭","どうだい……","どうだい……","阿斗",},
        [1358] = {20183202,"司馬昭","蜀が恋しいかい?","蜀が恋しいかい?","阿斗",},
        [1359] = {20183202,"阿斗","いーや、思い出さないなあ。だって毎日楽しいもん。","いーや、思い出さないなあ。だって毎日楽しいもん。","司馬昭",},
        [1360] = {20183301,"張郃","曹操の将兵は勇敢で戦上手というわ。必ず淳于瓊たちを破るでしょう。","曹操の将兵は勇敢で戦上手というわ。必ず淳于瓊たちを破るでしょう。","袁紹",},
        [1361] = {20183301,"張郃","そうなれば、将軍の大業も万事休すね。取り返しがつかなくなる前に救援を出すべきよ。","そうなれば、将軍の大業も万事休すね。取り返しがつかなくなる前に救援を出すべきよ。","袁紹",},
        [1362] = {20183301,"郭図","張郃の考えは良案ではありません。我が軍は曹操の本陣を攻めるべきでしょう。","張郃の考えは良案ではありません。我が軍は曹操の本陣を攻めるべきでしょう。","袁紹",},
        [1363] = {20183301,"郭図","そうすれば曹操は必ず救助に戻り、淳于瓊の危機は自ずと解決するでしょう。","そうすれば曹操は必ず救助に戻り、淳于瓊の危機は自ずと解決するでしょう。","袁紹",},
        [1364] = {20183301,"張郃","曹操の本陣は堅固よ。簡単に落とせるとは思えないわ。","曹操の本陣は堅固よ。簡単に落とせるとは思えないわ。","袁紹",},
        [1365] = {20183301,"袁紹","いや……やはり郭図の言う通り、曹操の本陣を叩くとしよう。","いや……やはり郭図の言う通り、曹操の本陣を叩くとしよう。","張郃",},
        [1366] = {20183302,"夏侯淵","馬超が張魯から兵を借りて涼州を攻め、祁山を包囲した。","馬超が張魯から兵を借りて涼州を攻め、祁山を包囲した。","張郃",},
        [1367] = {20183302,"夏侯淵","張郃は兵五千を率いて先鋒となり、陳倉から近道して進軍しろ。","張郃は兵五千を率いて先鋒となり、陳倉から近道して進軍しろ。","張郃",},
        [1368] = {20183302,"夏侯淵","俺は食料の運搬を監督した後、本軍を率いて後から行く。","俺は食料の運搬を監督した後、本軍を率いて後から行く。","張郃",},
        [1369] = {20183302,"張郃","ええっ！","ええっ！","夏侯淵",},
        [1370] = {20183303,"夏侯淵","本軍が到着したぞ。戦況はどうだ?","本軍が到着したぞ。戦況はどうだ?","張郃",},
        [1371] = {20183303,"張郃","馬超はすでに退却し、それぞれの県も投降させ平定したわ。","馬超はすでに退却し、それぞれの県も投降させ平定したわ。","夏侯淵",},
        [1372] = {20183303,"夏侯淵","わずか五千騎で馬超を退けるとは、張将軍は我が国の名将だな！","わずか五千騎で馬超を退けるとは、張将軍は我が国の名将だな！","張郃",},
        [1373] = {20183401,"孫堅","天下が荒れに荒れている。我らも義兵を挙げ、各地の賊を討伐しよう。","天下が荒れに荒れている。我らも義兵を挙げ、各地の賊を討伐しよう。","黄蓋",},
        [1374] = {20183401,"黄蓋","この黄蓋には大志があります。孫堅殿と共に起ちましょう。","この黄蓋には大志があります。孫堅殿と共に起ちましょう。","孫堅",},
        [1375] = {20183401,"孫堅","ああ！わしとお前がいれば、いつか必ず大業は成るだろう。","ああ！わしとお前がいれば、いつか必ず大業は成るだろう。","黄蓋",},
        [1376] = {20183402,"孫権","黄蓋は我が父、兄の二代に仕えてくださり、厳格で、勇猛なお方です。","黄蓋は我が父、兄の二代に仕えてくださり、厳格で、勇猛なお方です。","黄蓋",},
        [1377] = {20183402,"孫権","まさに孫呉の元勲、国家の大黒柱だ。","まさに孫呉の元勲、国家の大黒柱だ。","黄蓋",},
        [1378] = {20183402,"黄蓋","この黄蓋、幼いころに家族と別れ、暮らしにも困っておったのですじゃ。","この黄蓋、幼いころに家族と別れ、暮らしにも困っておったのですじゃ。","孫権",},
        [1379] = {20183402,"黄蓋","幸いにも孫堅さまに見いだされ、つき従って南北に転戦し、賊を討ち、城を攻めてきたのですじゃ。","幸いにも孫堅さまに見いだされ、つき従って南北に転戦し、賊を討ち、城を攻めてきたのですじゃ。","孫権",},
        [1380] = {20183402,"黄蓋","これからも、呉のため陣頭に立ちますぞ。","これからも、呉のため陣頭に立ちますぞ。","孫権",},
        [1381] = {20183403,"周瑜","曹操は百万の大軍を率い、船で赤壁まで南下して、一挙に蜀呉の連合軍をせん滅するつもりです。","曹操は百万の大軍を率い、船で赤壁まで南下して、一挙に蜀呉の連合軍をせん滅するつもりです。","黄蓋",},
        [1382] = {20183403,"周瑜","対する我が方は、せいぜい五万。どう挽回できたものでしょうね?","対する我が方は、せいぜい五万。どう挽回できたものでしょうね?","黄蓋",},
        [1383] = {20183403,"黄蓋","周瑜殿、ワシが曹操に投降すると偽って、ヤツの警戒心を解こう。","周瑜殿、ワシが曹操に投降すると偽って、ヤツの警戒心を解こう。","周瑜",},
        [1384] = {20183403,"黄蓋","その後で、薪と油を満載した軍船に火をつけ、曹操の船にぶつけるのじゃ。","その後で、薪と油を満載した軍船に火をつけ、曹操の船にぶつけるのじゃ。","周瑜",},
        [1385] = {20183403,"黄蓋","後は、混乱に陥った曹操の大軍を一挙に打ち破るだけじゃ！","後は、混乱に陥った曹操の大軍を一挙に打ち破るだけじゃ！","周瑜",},
        [1386] = {20183403,"周瑜","よい策です。ではその計画でいきましょう。","よい策です。ではその計画でいきましょう。","黄蓋",},
        [1387] = {20183501,"孟達","国賊董卓、李傕などが暴れまわったせいで、関中一帯は大飢饉だ。","国賊董卓、李傕などが暴れまわったせいで、関中一帯は大飢饉だ。","法正",},
        [1388] = {20183501,"孟達","このままでは、みな飢え死にしてしまう。一体どうすればいい?","このままでは、みな飢え死にしてしまう。一体どうすればいい?","法正",},
        [1389] = {20183501,"法正","益州牧の劉璋は道義を重んじ、乱世においてまだ道徳心を持っていると聞く。","益州牧の劉璋は道義を重んじ、乱世においてまだ道徳心を持っていると聞く。","孟達",},
        [1390] = {20183501,"法正","お前も私も大志を持つ身なれば、まず彼のところへ行き人となりを見極めるのがいいだろう。","お前も私も大志を持つ身なれば、まず彼のところへ行き人となりを見極めるのがいいだろう。","孟達",},
        [1391] = {20183501,"孟達","そうだな、お前の言う通りだ。","そうだな、お前の言う通りだ。","法正",},
        [1392] = {20183502,"法正","閣下は世に名高い英雄です。劉璋には名君たる能力はありません。張松と内応し、益州を奪い取ってください。","閣下は世に名高い英雄です。劉璋には名君たる能力はありません。張松と内応し、益州を奪い取ってください。","劉備",},
        [1393] = {20183502,"法正","益州は天府の国。その豊かさと要害を頼みとすれば、大業を成すのも、掌を返すようにたやすいことでしょう。","益州は天府の国。その豊かさと要害を頼みとすれば、大業を成すのも、掌を返すようにたやすいことでしょう。","劉備",},
        [1394] = {20183502,"劉備","ご助言感謝するぞ、法正殿。すぐに軍を率いて蜀に入ろう。","ご助言感謝するぞ、法正殿。すぐに軍を率いて蜀に入ろう。","法正",},
        [1395] = {20183503,"劉備","夏侯淵は南側の拠点である走馬谷を守り、張郃は東側の拠点である広石を守っている。","夏侯淵は南側の拠点である走馬谷を守り、張郃は東側の拠点である広石を守っている。","法正",},
        [1396] = {20183503,"劉備","私の目には完璧な布陣に見えるが、破る策はあるだろうか?","私の目には完璧な布陣に見えるが、破る策はあるだろうか?","法正",},
        [1397] = {20183503,"法正","私が見るに、一万の精兵を十隊に分け、交代で広石に夜襲をかければ、張郃は必ず夏侯淵に救援を求めるはず。","私が見るに、一万の精兵を十隊に分け、交代で広石に夜襲をかければ、張郃は必ず夏侯淵に救援を求めるはず。","劉備",},
        [1398] = {20183503,"法正","そこで夏侯淵が兵を半分に分けて送ったら、すかさず走馬谷を襲撃し、敵の陣地に火を放ちます。","そこで夏侯淵が兵を半分に分けて送ったら、すかさず走馬谷を襲撃し、敵の陣地に火を放ちます。","劉備",},
        [1399] = {20183503,"法正","この火に乗じて全力で夏侯淵を攻めれば、必ず破れましょう。","この火に乗じて全力で夏侯淵を攻めれば、必ず破れましょう。","劉備",},
        [1400] = {20183601,"周瑜","食糧が不足していて、大変困っています。","食糧が不足していて、大変困っています。","魯粛",},
        [1401] = {20183601,"周瑜","そこで、魯粛殿にお願いがあります。食料を貸していただけないでしょうか。","そこで、魯粛殿にお願いがあります。食料を貸していただけないでしょうか。","魯粛",},
        [1402] = {20183601,"魯粛","そうかしこまらず。この米倉一つ分、三千石をお譲りしましょう。","そうかしこまらず。この米倉一つ分、三千石をお譲りしましょう。","周瑜",},
        [1403] = {20183601,"周瑜","魯粛殿……なんと言ったらいいか……このご恩、この周瑜の心にしっかりと刻みます。","魯粛殿……なんと言ったらいいか……このご恩、この周瑜の心にしっかりと刻みます。","魯粛",},
        [1404] = {20183602,"孫権","ふん……劉備が再三再四、江夏の半分と俺の手にある荊州の南半分を交換しようと持ち掛けてくる。","ふん……劉備が再三再四、江夏の半分と俺の手にある荊州の南半分を交換しようと持ち掛けてくる。","",},
        [1405] = {20183602,"孫権","耳ざわりの良い提案だが、俺は騙されんぞ。同意することはできん！","耳ざわりの良い提案だが、俺は騙されんぞ。同意することはできん！","",},
        [1406] = {20183602,"魯粛","私の見解とは違いますな。荊州を貸し与えるべきです。","私の見解とは違いますな。荊州を貸し与えるべきです。","孫権",},
        [1407] = {20183602,"孫権","なぜだ?","なぜだ?","魯粛",},
        [1408] = {20183602,"魯粛","まず、荊州を貸すと言っても、実際は南郡、襄陽の２つを貸すだけに過ぎません。","まず、荊州を貸すと言っても、実際は南郡、襄陽の２つを貸すだけに過ぎません。","孫権",},
        [1409] = {20183602,"魯粛","次に、劉備はわが呉をのぞいて唯一、曹操に対抗できる勢力です。敵の敵は味方と申します。","次に、劉備はわが呉をのぞいて唯一、曹操に対抗できる勢力です。敵の敵は味方と申します。","孫権",},
        [1410] = {20183602,"魯粛","これは重大な戦略的価値を持ちます。それをわずかな土地を貸すだけで得られるのです。これほどうまい話はありますまい。","これは重大な戦略的価値を持ちます。それをわずかな土地を貸すだけで得られるのです。これほどうまい話はありますまい。","孫権",},
        [1411] = {20183602,"孫権","なるほど、魯粛の言うことはもっともだ。","なるほど、魯粛の言うことはもっともだ。","魯粛",},
        [1412] = {20183603,"関羽","互いの兵を百歩離れたところに待たせ、一対一で話し合おうというのか。","互いの兵を百歩離れたところに待たせ、一対一で話し合おうというのか。","魯粛",},
        [1413] = {20183603,"関羽","わしと刀一本の間合いで向き合うのが恐ろしくはないのか?","わしと刀一本の間合いで向き合うのが恐ろしくはないのか?","魯粛",},
        [1414] = {20183603,"魯粛","恐れていたらここへは来ません。そもそも昔、曹操に敗れて流浪の身になっていたあなた方に、わが呉が情けをかけて土地を貸し与えたのです。","恐れていたらここへは来ません。そもそも昔、曹操に敗れて流浪の身になっていたあなた方に、わが呉が情けをかけて土地を貸し与えたのです。","関羽",},
        [1415] = {20183603,"魯粛","今やそちらは益州 という大きな土地を手に入れました。荊州のすべては返せなくとも、せめて南の三郡だけでも返すのが筋でしょう。","今やそちらは益州 という大きな土地を手に入れました。荊州のすべては返せなくとも、せめて南の三郡だけでも返すのが筋でしょう。","関羽",},
        [1416] = {20183603,"周倉","天下の土地は、徳のある者こそが住むべきだ。お前たち呉だけが独り占めしていいのか?","天下の土地は、徳のある者こそが住むべきだ。お前たち呉だけが独り占めしていいのか?","魯粛",},
        [1417] = {20183603,"関羽","黙れ周倉！国家の大事である。下がっておれ！","黙れ周倉！国家の大事である。下がっておれ！","魯粛",},
        [1418] = {20183603,"関羽","魯粛殿、あいにくだが、今日はひどく酔ってしまった。","魯粛殿、あいにくだが、今日はひどく酔ってしまった。","魯粛",},
        [1419] = {20183603,"関羽","ここで荊州の事を持ち出せば、手元が狂って古くからの友を傷をつけてしまうかも知れん。","ここで荊州の事を持ち出せば、手元が狂って古くからの友を傷をつけてしまうかも知れん。","魯粛",},
        [1420] = {20183603,"関羽","後日、改めて荊州に来てくれ。その時もう一度話し合おう。","後日、改めて荊州に来てくれ。その時もう一度話し合おう。","魯粛",},
        [1421] = {20183701,"劉備","魯粛殿はかつて手紙で、あなたを百里を治める才能の持ち主だと言っていた。","魯粛殿はかつて手紙で、あなたを百里を治める才能の持ち主だと言っていた。","龐統",},
        [1422] = {20183701,"劉備","孔明も、常々あなたの才能について話してくれる。","孔明も、常々あなたの才能について話してくれる。","龐統",},
        [1423] = {20183701,"龐統","なんとも光栄ですな。この龐統ごときが、魯粛殿と孔明殿の目に留まるとは。","なんとも光栄ですな。この龐統ごときが、魯粛殿と孔明殿の目に留まるとは。","劉備",},
        [1424] = {20183701,"劉備","よければ私の幕下に入り、策を献じてもらえないだろうか?","よければ私の幕下に入り、策を献じてもらえないだろうか?","龐統",},
        [1425] = {20183701,"龐統","ようござんす。劉備さまのご評価に応えるため、この龐統、全力でお仕えしましょう。","ようござんす。劉備さまのご評価に応えるため、この龐統、全力でお仕えしましょう。","劉備",},
        [1426] = {20183702,"龐統","荊州は荒れ果て、人も物も流出しております。","荊州は荒れ果て、人も物も流出しております。","劉備",},
        [1427] = {20183702,"龐統","東には孫権、北には曹操があり、大きく発展することは難しいでしょうな。","東には孫権、北には曹操があり、大きく発展することは難しいでしょうな。","劉備",},
        [1428] = {20183702,"龐統","一方、益州の人口は百万を数え、土地は肥沃で、物も豊かです。ここを奪って基盤とすれば、大業に大きく近づけるでしょうぜ。","一方、益州の人口は百万を数え、土地は肥沃で、物も豊かです。ここを奪って基盤とすれば、大業に大きく近づけるでしょうぜ。","劉備",},
        [1429] = {20183702,"劉備","曹操が性急だからこそ、私は寛大に対処した。冷酷だからこそ、私は情けをかけた。ずる賢いからこそ、私は正直に動いた。","曹操が性急だからこそ、私は寛大に対処した。冷酷だからこそ、私は情けをかけた。ずる賢いからこそ、私は正直に動いた。","龐統",},
        [1430] = {20183702,"劉備","何もかも正反対の策を採ってきたからこそ、今日があるのだ。もし今益州を獲れば、天下の信用を失うのではないか?","何もかも正反対の策を採ってきたからこそ、今日があるのだ。もし今益州を獲れば、天下の信用を失うのではないか?","龐統",},
        [1431] = {20183702,"龐統","今は戦乱の時代ですぜ。なんでも杓子定規ではいけません。臨機応変が肝要なんです。","今は戦乱の時代ですぜ。なんでも杓子定規ではいけません。臨機応変が肝要なんです。","劉備",},
        [1432] = {20183702,"龐統","弱きを飲み込み、暗愚を攻め、国を奪って正しく治め、義をもって報いるのは、古来より正しい行いですぜ。","弱きを飲み込み、暗愚を攻め、国を奪って正しく治め、義をもって報いるのは、古来より正しい行いですぜ。","劉備",},
        [1433] = {20183702,"龐統","事態が落ち着いた後で、劉璋はある程度の土地に封じてやればいいんです。信義に背くなんてこたぁない。","事態が落ち着いた後で、劉璋はある程度の土地に封じてやればいいんです。信義に背くなんてこたぁない。","劉備",},
        [1434] = {20183702,"劉備","分かった。では孔明と関羽に荊州を守らせるから、益州に同行してほしい。","分かった。では孔明と関羽に荊州を守らせるから、益州に同行してほしい。","龐統",},
        [1435] = {20183801,"陶謙","張昭殿は才能と学問があり、知謀に長けていると聞いた。","張昭殿は才能と学問があり、知謀に長けていると聞いた。","張昭",},
        [1436] = {20183801,"陶謙","ワシはお主を茂才に推薦したいと思うがどうじゃ?","ワシはお主を茂才に推薦したいと思うがどうじゃ?","張昭",},
        [1437] = {20183801,"張昭","陶謙殿の申し出はありがたいのですが、この張昭は仕官するつもりはありませぬ。","陶謙殿の申し出はありがたいのですが、この張昭は仕官するつもりはありませぬ。","陶謙",},
        [1438] = {20183801,"陶謙","なんじゃと！?お主のためを思って言ってやったのに、さてはワシを愚弄しておるな。牢屋に繋がれるのが怖くないのか?","なんじゃと！?お主のためを思って言ってやったのに、さてはワシを愚弄しておるな。牢屋に繋がれるのが怖くないのか?","張昭",},
        [1439] = {20183802,"劉表","わしは孫策に宛てて手紙を書いた。","わしは孫策に宛てて手紙を書いた。","禰衡",},
        [1440] = {20183802,"劉表","禰衡、読んでみてどう思うか?率直に申してみよ。","禰衡、読んでみてどう思うか?率直に申してみよ。","禰衡",},
        [1441] = {20183802,"禰衡","どう思うと言われましても……ぷぷっ！","どう思うと言われましても……ぷぷっ！","劉表",},
        [1442] = {20183802,"禰衡","これは孫策の家の子供に読ませるつもりですか?","これは孫策の家の子供に読ませるつもりですか?","劉表",},
        [1443] = {20183802,"禰衡","それとも、張昭にでも読ませたいのですか?どちらにしてもひどいですな！","それとも、張昭にでも読ませたいのですか?どちらにしてもひどいですな！","劉表",},
        [1444] = {20183803,"張昭","孫権さまは馬に乗って虎を射るのがお好きなようですが、今のように、虎は鞍まで飛びかかってきます。","孫権さまは馬に乗って虎を射るのがお好きなようですが、今のように、虎は鞍まで飛びかかってきます。","孫権",},
        [1445] = {20183803,"張昭","再び同じことが起こってしまったら、どう防ぐおつもりですか?","再び同じことが起こってしまったら、どう防ぐおつもりですか?","孫権",},
        [1446] = {20183803,"張昭","君主とは英雄を御し、賢者たちを上手く用いるもの。自ら原野を駆け、猛獣と戦うのはいかがなものでしょう。","君主とは英雄を御し、賢者たちを上手く用いるもの。自ら原野を駆け、猛獣と戦うのはいかがなものでしょう。","孫権",},
        [1447] = {20183803,"張昭","もし万一があって、天下の笑い者になってもよろしいのですか?","もし万一があって、天下の笑い者になってもよろしいのですか?","孫権",},
        [1448] = {20183803,"孫権","そうだな……若気の至りで配慮が足りなかった。まったく恥ずかしい限りだ。","そうだな……若気の至りで配慮が足りなかった。まったく恥ずかしい限りだ。","張昭",},
        [1449] = {20183901,"曹操","禰衡は太鼓を打つのが得意と聞いた。鼓史に命じよう。","禰衡は太鼓を打つのが得意と聞いた。鼓史に命じよう。","",},
        [1450] = {20183901,"曹操","大きな宴を開いた時には、鼓史たちの演奏をじっくり聞いてやろう。","大きな宴を開いた時には、鼓史たちの演奏をじっくり聞いてやろう。","",},
        [1451] = {20183902,"曹操","鼓史たちは皆、専用の服を着て太鼓を打つ。","鼓史たちは皆、専用の服を着て太鼓を打つ。","禰衡",},
        [1452] = {20183902,"曹操","禰衡、なぜお前は着替えず謁見しに来たのだ。軽率とは思わなかったのか?","禰衡、なぜお前は着替えず謁見しに来たのだ。軽率とは思わなかったのか?","禰衡",},
        [1453] = {20183902,"禰衡","そうでしたな！ではここで着替えます。(言うとその場で服を脱ぎ、素っ裸になって太鼓を打った。それからおもむろに服を着た)","そうでしたな！ではここで着替えます。(言うとその場で服を脱ぎ、素っ裸になって太鼓を打った。それからおもむろに服を着た)","曹操",},
        [1454] = {20183902,"曹操","くそっ……禰衡のヤツめ、辱めてやろうと思ったのに、一枚上手だったか。","くそっ……禰衡のヤツめ、辱めてやろうと思ったのに、一枚上手だったか。","禰衡",},
        [1455] = {20183903,"劉表","曹操が、禰衡を送りつけてきよった。よほど手に余ったと見える。","曹操が、禰衡を送りつけてきよった。よほど手に余ったと見える。","",},
        [1456] = {20183903,"劉表","しかしこの禰衡という奴、とんでもない無礼者だな。少しばかり才能があるとて、この劉表がここまで侮辱されるいわれはない！","しかしこの禰衡という奴、とんでもない無礼者だな。少しばかり才能があるとて、この劉表がここまで侮辱されるいわれはない！","",},
        [1457] = {20183903,"劉表","もはやわしの手にも余る。江夏太守の黄祖のもとに送ってやろう。あやつは気が短いから、どうなるか楽しみだ。","もはやわしの手にも余る。江夏太守の黄祖のもとに送ってやろう。あやつは気が短いから、どうなるか楽しみだ。","",},
        [1458] = {20183904,"黄祖","船の上の宴での不遜な物言い……わしはもう耐えられん。","船の上の宴での不遜な物言い……わしはもう耐えられん。","",},
        [1459] = {20183904,"黄祖","ちょっと叱ってやったらムキになって罵りおって、目上への敬意がなっておらん。","ちょっと叱ってやったらムキになって罵りおって、目上への敬意がなっておらん。","",},
        [1460] = {20183904,"黄祖","いい加減、我慢の限界だ。禰衡は殺してしまえ！","いい加減、我慢の限界だ。禰衡は殺してしまえ！","",},
        [1461] = {20184001,"袁紹","わしは冀州を占領し、青州、并州の二州を奪い、易京の戦いで公孫瓚を倒し、河北を統一した。","わしは冀州を占領し、青州、并州の二州を奪い、易京の戦いで公孫瓚を倒し、河北を統一した。","",},
        [1462] = {20184001,"袁紹","これらは皆、わしの優秀な配下……麴義、顔良、文醜、張郃、高覧の功労である。","これらは皆、わしの優秀な配下……麴義、顔良、文醜、張郃、高覧の功労である。","",},
        [1463] = {20184001,"袁紹","とくに、顔良、文醜の虎狼兄弟！","とくに、顔良、文醜の虎狼兄弟！","",},
        [1464] = {20184001,"袁紹","二人の働きは特に大きい。わが配下で最も勇猛な将軍だ。","二人の働きは特に大きい。わが配下で最も勇猛な将軍だ。","",},
        [1465] = {20184002,"荀攸","丞相、我が方は兵力が少なく非常に不利な状況です。敵の勢力を分断すべきです。","丞相、我が方は兵力が少なく非常に不利な状況です。敵の勢力を分断すべきです。","曹操",},
        [1466] = {20184002,"荀攸","ます延津へ兵を送り、川を渡らせて後方へ向かわせれば、袁紹は必ずこれに応じるでしょう。","ます延津へ兵を送り、川を渡らせて後方へ向かわせれば、袁紹は必ずこれに応じるでしょう。","曹操",},
        [1467] = {20184002,"荀攸","その間に軽騎兵で白馬を攻めれば、孤立した顔良を捕らえられるはずです。","その間に軽騎兵で白馬を攻めれば、孤立した顔良を捕らえられるはずです。","曹操",},
        [1468] = {20184002,"曹操","うむ、よいな。荀攸の策でいこう。","うむ、よいな。荀攸の策でいこう。","荀攸",},
        [1469] = {20184003,"曹操","見ろ。十里も行かない内に、顔良が驚いて反撃してきたぞ。","見ろ。十里も行かない内に、顔良が驚いて反撃してきたぞ。","荀攸",},
        [1470] = {20184003,"荀攸","まさに思うつぼです。先鋒に張遼と関羽を立てれば、必ず退けられます。","まさに思うつぼです。先鋒に張遼と関羽を立てれば、必ず退けられます。","曹操",},
        [1471] = {20184003,"関羽","白馬の包囲を解くなら、わし一人で十分だ。ひとつ馬に鞭打って、顔良の首を取ってご覧に入れよう。","白馬の包囲を解くなら、わし一人で十分だ。ひとつ馬に鞭打って、顔良の首を取ってご覧に入れよう。","荀攸",},
        [1472] = {20184004,"文醜","白馬の戦いで、顔良を斬ったのはお前だな?","白馬の戦いで、顔良を斬ったのはお前だな?","関羽",},
        [1473] = {20184004,"関羽","いかにもわしだ。それがどうした。","いかにもわしだ。それがどうした。","文醜",},
        [1474] = {20184004,"文醜","顔良は俺の兄弟のようなものだ！今日、ここで仇を討ってくれる！","顔良は俺の兄弟のようなものだ！今日、ここで仇を討ってくれる！","関羽",},
        [1475] = {20184004,"関羽","仇だと?笑止な。自分の命を心配するがいい！","仇だと?笑止な。自分の命を心配するがいい！","文醜",},
        [1476] = {20184101,"黄月英","おばさま、今日お屋敷に招いてくださったのは、どういうわけです?","おばさま、今日お屋敷に招いてくださったのは、どういうわけです?","蔡夫人",},
        [1477] = {20184101,"蔡夫人","あなたに会いたかったのよ、月英。そうやって笠で顔を隠していると、醜いと疑われますよ。本当はわが一族いちの美人なのに。","あなたに会いたかったのよ、月英。そうやって笠で顔を隠していると、醜いと疑われますよ。本当はわが一族いちの美人なのに。","黄月英",},
        [1478] = {20184101,"黄月英","ご冗談を、おばさま。わたし、あまり人と会うのが好きではないの。","ご冗談を、おばさま。わたし、あまり人と会うのが好きではないの。","蔡夫人",},
        [1479] = {20184101,"蔡夫人","何を言ってるの。ほら、見なさい。伯父さまの蔡瑁さまが蜀の織物をいくつか送って下さったのよ。","何を言ってるの。ほら、見なさい。伯父さまの蔡瑁さまが蜀の織物をいくつか送って下さったのよ。","黄月英",},
        [1480] = {20184101,"蔡夫人","ずっと気にかけていてくださってるのね……どれが気に入るかしら?","ずっと気にかけていてくださってるのね……どれが気に入るかしら?","黄月英",},
        [1481] = {20184101,"黄月英","わたしは書を読んだり、大工仕事をしたりするのが好きなの。宝の持ち腐れだわ。","わたしは書を読んだり、大工仕事をしたりするのが好きなの。宝の持ち腐れだわ。","蔡夫人",},
        [1482] = {20184101,"蔡夫人","馬鹿なこと言うものじゃないわよ。これはね、簡単には手に入らないの。わざわざ蜀に人をやって探させたものなのよ。","馬鹿なこと言うものじゃないわよ。これはね、簡単には手に入らないの。わざわざ蜀に人をやって探させたものなのよ。","黄月英",},
        [1483] = {20184101,"蔡夫人","ご厚意を無駄にしてはいけないわ。あなたも早く結婚して、きれいな花嫁衣裳を着るの。それが恩返しになるのよ。","ご厚意を無駄にしてはいけないわ。あなたも早く結婚して、きれいな花嫁衣裳を着るの。それが恩返しになるのよ。","黄月英",},
        [1484] = {20184101,"黄月英","わかりました……それなら、おばさまが選んでくださいな。","わかりました……それなら、おばさまが選んでくださいな。","蔡夫人",},
        [1485] = {20184102,"劉表","朝廷から荊州刺史に任命されて荊州に入ったが、まだ着任できずにいる。","朝廷から荊州刺史に任命されて荊州に入ったが、まだ着任できずにいる。","蔡瑁",},
        [1486] = {20184102,"劉表","土豪が幅を利かせ、民衆もまとまらんのだ。蔡瑁、お前の家は襄陽の名門だ。いい策はないか?","土豪が幅を利かせ、民衆もまとまらんのだ。蔡瑁、お前の家は襄陽の名門だ。いい策はないか?","蔡瑁",},
        [1487] = {20184102,"蔡瑁","宗賊の首領どもは横暴で貪欲です。私の配下の者を使って、首領どもに誘いをかければ、手勢を引き連れてやって来るでしょう。","宗賊の首領どもは横暴で貪欲です。私の配下の者を使って、首領どもに誘いをかければ、手勢を引き連れてやって来るでしょう。","劉表",},
        [1488] = {20184102,"蔡瑁","そこで、無道の者は処刑し、そうでない者は慰撫して任用なさいませ。","そこで、無道の者は処刑し、そうでない者は慰撫して任用なさいませ。","劉表",},
        [1489] = {20184102,"蔡瑁","そうやって、荊州の人々に知らせるのです。今の君主には徳があり、弱いものを必ず助けてくれると。","そうやって、荊州の人々に知らせるのです。今の君主には徳があり、弱いものを必ず助けてくれると。","劉表",},
        [1490] = {20184102,"蔡瑁","そうして兵を集めて民を従え、南は江陵に拠り、北は襄陽を守れば、荊州八郡は平定できるでしょう。","そうして兵を集めて民を従え、南は江陵に拠り、北は襄陽を守れば、荊州八郡は平定できるでしょう。","劉表",},
        [1491] = {20184102,"劉表","これは良い策だ。上手く行ったら、お前を朝廷に奏上して褒賞を与えよう！","これは良い策だ。上手く行ったら、お前を朝廷に奏上して褒賞を与えよう！","蔡瑁",},
        [1492] = {20184103,"劉琮","父君がお亡くなりになり、私が国主に立てられたが、兄上はいま江夏にいるし、外戚の叔父上の劉備殿も新野にいる。","父君がお亡くなりになり、私が国主に立てられたが、兄上はいま江夏にいるし、外戚の叔父上の劉備殿も新野にいる。","",},
        [1493] = {20184103,"劉琮","お前たち、もしこの二人が兵を挙げ、私の罪を問いただしたらどう説明する?","お前たち、もしこの二人が兵を挙げ、私の罪を問いただしたらどう説明する?","",},
        [1494] = {20184103,"李珪","若君、よくぞ仰せられました。急いで訃報を江夏にお出しなさいませ。","若君、よくぞ仰せられました。急いで訃報を江夏にお出しなさいませ。","劉琮",},
        [1495] = {20184103,"李珪","兄君を迎えて荊州の主にお立てし、劉備殿にもご協力を仰ぐのです。さすれば北の曹操、南の孫権に対抗できます。これこそ万全の策です。","兄君を迎えて荊州の主にお立てし、劉備殿にもご協力を仰ぐのです。さすれば北の曹操、南の孫権に対抗できます。これこそ万全の策です。","劉琮",},
        [1496] = {20184103,"蔡瑁","貴様と言うヤツは、殿のご遺言に逆らうのか！","貴様と言うヤツは、殿のご遺言に逆らうのか！","",},
        [1497] = {20184103,"李珪","お前こそ陰謀をめぐらし、遺書を偽造して長幼の序を乱すとは何事だ！いまに荊州は蔡氏の手で潰れてしまうぞ。","お前こそ陰謀をめぐらし、遺書を偽造して長幼の序を乱すとは何事だ！いまに荊州は蔡氏の手で潰れてしまうぞ。","蔡瑁",},
        [1498] = {20184103,"李珪","殿が生きておられたら、真っ先に天誅を下しただろう！","殿が生きておられたら、真っ先に天誅を下しただろう！","蔡瑁",},
        [1499] = {20184103,"蔡瑁","うぬぬ……、よくもそんな妄言をっ！?殿は、次男の劉琮さまを荊州の主とするようご遺言されたのだ！","うぬぬ……、よくもそんな妄言をっ！?殿は、次男の劉琮さまを荊州の主とするようご遺言されたのだ！","",},
        [1500] = {20184103,"蔡瑁","誰か、こやつを引きずり出して首を斬れ！","誰か、こやつを引きずり出して首を斬れ！","",},
        [1501] = {20184104,"蔡瑁","姉よ、劉備は三人の大将を外に置き、自らは荊州に居座っている。いずれ必ずわざわいとなろう。","姉よ、劉備は三人の大将を外に置き、自らは荊州に居座っている。いずれ必ずわざわいとなろう。","蔡夫人",},
        [1502] = {20184104,"蔡夫人","荊州では多くの人が劉備と付き合いがあると聞きました。警戒した方が良いですわ。","荊州では多くの人が劉備と付き合いがあると聞きました。警戒した方が良いですわ。","蔡瑁",},
        [1503] = {20184104,"蔡瑁","そこでだが、宿舎を襲って殺してしまい、その後で殿に報告するのはどうだろう。","そこでだが、宿舎を襲って殺してしまい、その後で殿に報告するのはどうだろう。","蔡夫人",},
        [1504] = {20184104,"伊籍","蔡瑁があなたを殺そうと狙っています。城外の東、南、北の三方はすべて兵馬で固められています。","蔡瑁があなたを殺そうと狙っています。城外の東、南、北の三方はすべて兵馬で固められています。","蔡瑁",},
        [1505] = {20184104,"伊籍","西門から出て、早くお逃げください！","西門から出て、早くお逃げください！","蔡瑁",},
        [1506] = {20184104,"蔡瑁","おや劉備殿、どうして席を外されるのか?","おや劉備殿、どうして席を外されるのか?","伊籍",},
        [1507] = {20184104,"劉備","私は運がいい。もし的盧がいなければ、檀渓を飛び越えることはできなかっただろう。危うく死ぬところだった。","私は運がいい。もし的盧がいなければ、檀渓を飛び越えることはできなかっただろう。危うく死ぬところだった。","蔡瑁",},
        [1508] = {20184105,"蔡瑁","殿、曹操が大軍を率いて襄陽に向かって来ます！かくなる上は荊州を曹操に献上するのです。そうすれば、曹操は必ず殿を厚遇するでしょう。","殿、曹操が大軍を率いて襄陽に向かって来ます！かくなる上は荊州を曹操に献上するのです。そうすれば、曹操は必ず殿を厚遇するでしょう。","劉琦",},
        [1509] = {20184105,"劉琦","なぜだ?父の地盤を受け継いだばかりで安定してもいないのに、どうして他の者に譲らなければならぬ?","なぜだ?父の地盤を受け継いだばかりで安定してもいないのに、どうして他の者に譲らなければならぬ?","蔡瑁",},
        [1510] = {20184105,"蔡瑁","いま曹操は朝廷の名をいただいて、南北を征伐しております。よって敵対するのは、反逆者ということになります。","いま曹操は朝廷の名をいただいて、南北を征伐しております。よって敵対するのは、反逆者ということになります。","劉琦",},
        [1511] = {20184105,"蔡瑁","それに殿は新しく領主となられたばかり。領内もおさまっていないうちに、外敵に対抗できますか?ためらってはなりません。","それに殿は新しく領主となられたばかり。領内もおさまっていないうちに、外敵に対抗できますか?ためらってはなりません。","劉琦",},
        [1512] = {20184105,"劉琦","ううむ、それもそうだな。では将軍の言う通り、荊州を曹操に献上しよう。","ううむ、それもそうだな。では将軍の言う通り、荊州を曹操に献上しよう。","蔡瑁",},
        [1513] = {20184106,"曹操","三江口の戦いでは、数で勝りながらも呉に破れた。なぜだ！?","三江口の戦いでは、数で勝りながらも呉に破れた。なぜだ！?","蔡瑁",},
        [1514] = {20184106,"蔡瑁","荊州の水軍は久しく訓練しておらず、青州、徐州の軍はまだ水上戦に慣れていません。だから負けたのです。","荊州の水軍は久しく訓練しておらず、青州、徐州の軍はまだ水上戦に慣れていません。だから負けたのです。","曹操",},
        [1515] = {20184106,"蔡瑁","しかし、今は水辺に砦を築き、青州、徐州の軍はその中で、荊州の軍は外で毎日訓練させていますので、すぐに使えるようになるでしょう。","しかし、今は水辺に砦を築き、青州、徐州の軍はその中で、荊州の軍は外で毎日訓練させていますので、すぐに使えるようになるでしょう。","曹操",},
        [1516] = {20184106,"曹操","水軍都督よ、上手くやれ。わしに手間を掛けさせるなよ！","水軍都督よ、上手くやれ。わしに手間を掛けさせるなよ！","蔡瑁",},
        [1517] = {20184106,"蔡瑁","ご安心ください。水軍はこの蔡瑁が全身全霊をもって訓練しております。周瑜など問題になりません！","ご安心ください。水軍はこの蔡瑁が全身全霊をもって訓練しております。周瑜など問題になりません！","曹操",},
        [1518] = {20184107,"蒋幹","丞相、このたび呉の陣に赴き、周瑜は説得できませんでしたが、一つ秘密を探ってまいりました。","丞相、このたび呉の陣に赴き、周瑜は説得できませんでしたが、一つ秘密を探ってまいりました。","曹操",},
        [1519] = {20184107,"蒋幹","あの蔡瑁と張允は密かに周瑜と結託し、丞相を陥れようとしております！","あの蔡瑁と張允は密かに周瑜と結託し、丞相を陥れようとしております！","曹操",},
        [1520] = {20184107,"曹操","賊どもめ、なんと無礼な！蔡瑁と張允をここへ呼べ！すぐに兵を進めさせるのだ！","賊どもめ、なんと無礼な！蔡瑁と張允をここへ呼べ！すぐに兵を進めさせるのだ！","蒋幹",},
        [1521] = {20184107,"蔡瑁","丞相、水軍はまだ訓練不足ですので、軽々しく進軍はできません。","丞相、水軍はまだ訓練不足ですので、軽々しく進軍はできません。","曹操",},
        [1522] = {20184107,"曹操","熟練すれば、わしの首を周瑜に献上するというのだろう！誰か、蔡瑁と張允を引きずり出して首を斬れ！","熟練すれば、わしの首を周瑜に献上するというのだろう！誰か、蔡瑁と張允を引きずり出して首を斬れ！","蔡瑁",},
        [1523] = {20184107,"蔡瑁","そっ、そんな！丞相、丞相！この蔡瑁の忠誠心に曇りなどございません！二心など決して！","そっ、そんな！丞相、丞相！この蔡瑁の忠誠心に曇りなどございません！二心など決して！","曹操",},
        [1524] = {20184201,"典韋","李永、よくも劉氏を殺したな。この仇は必ず討ってやる！","李永、よくも劉氏を殺したな。この仇は必ず討ってやる！","李永",},
        [1525] = {20184201,"李永","やれるものならやってみるがいい。富春県令である私の家の警備は厳重だぞ。お前のごとき乱暴者、怖くはないわ。","やれるものならやってみるがいい。富春県令である私の家の警備は厳重だぞ。お前のごとき乱暴者、怖くはないわ。","典韋",},
        [1526] = {20184201,"典韋","待ってやがれ。貴様の命、必ず奪ってやる！(典韋は鶏と酒を持って李永の訪問客を装い、","待ってやがれ。貴様の命、必ず奪ってやる！(典韋は鶏と酒を持って李永の訪問客を装い、","李永",},
        [1527] = {20184201,"典韋","門を開けさせると、入り込んで李永を殺した。これにより、典韋は豪傑として知られるようになった)","門を開けさせると、入り込んで李永を殺した。これにより、典韋は豪傑として知られるようになった)","李永",},
        [1528] = {20184202,"張邈","この牙門旗は重すぎて誰にも持ち上げられなかったのだ。まさか片手で上げてしまうとは。おぬしは一体……?","この牙門旗は重すぎて誰にも持ち上げられなかったのだ。まさか片手で上げてしまうとは。おぬしは一体……?","典韋",},
        [1529] = {20184202,"典韋","俺は、典韋という者だ。","俺は、典韋という者だ。","張邈",},
        [1530] = {20184202,"張邈","おお、おぬしが！私は陳留太守の張邈だ。曹操の義挙に加わって反董卓連合軍を結成した。","おお、おぬしが！私は陳留太守の張邈だ。曹操の義挙に加わって反董卓連合軍を結成した。","典韋",},
        [1531] = {20184202,"張邈","噂はかねがね聞いている。ぜひわが軍に加わらんか?","噂はかねがね聞いている。ぜひわが軍に加わらんか?","典韋",},
        [1532] = {20184202,"典韋","董卓は天下を乱す賊。この典韋、ぜひ朝廷のために力を尽くしたい！","董卓は天下を乱す賊。この典韋、ぜひ朝廷のために力を尽くしたい！","張邈",},
        [1533] = {20184202,"張邈","うむ、これは頼もしい！","うむ、これは頼もしい！","典韋",},
        [1534] = {20184203,"曹操","典韋の忠誠心、武勇、まさに我が悪来よ！軍ではお前を讃える格言ができたそうだぞ。","典韋の忠誠心、武勇、まさに我が悪来よ！軍ではお前を讃える格言ができたそうだぞ。","典韋",},
        [1535] = {20184203,"曹操","「幕下に勇士典君あり、掲げる双戟八十斤」とな。","「幕下に勇士典君あり、掲げる双戟八十斤」とな。","典韋",},
        [1536] = {20184203,"典韋","あはは！軍の連中め、俺をからかっておるのでしょう。","あはは！軍の連中め、俺をからかっておるのでしょう。","曹操",},
        [1537] = {20184203,"曹操","謙遜など不要よ！お前は酒が好きだそうだな。誰か、酒と飯を持ってこい！","謙遜など不要よ！お前は酒が好きだそうだな。誰か、酒と飯を持ってこい！","典韋",},
        [1538] = {20184203,"典韋","かたじけない、殿！","かたじけない、殿！","曹操",},
        [1539] = {20184204,"曹操","外が騒がしいな。なにかあったのか?","外が騒がしいな。なにかあったのか?","典韋",},
        [1540] = {20184204,"典韋","殿っ！張繍が反乱を起こしたようです。俺が食い止めますから、早く裏門からお逃げください！","殿っ！張繍が反乱を起こしたようです。俺が食い止めますから、早く裏門からお逃げください！","曹操",},
        [1541] = {20184204,"曹操","張繍だと……?降っておきながら、なぜまた裏切った！","張繍だと……?降っておきながら、なぜまた裏切った！","典韋",},
        [1542] = {20184204,"典韋","敵の数が多すぎる！殿……お急ぎください！","敵の数が多すぎる！殿……お急ぎください！","曹操",},
        [1543] = {20184301,"董卓","李傕 、お前は勇猛で用兵に長けているだけでなく、弁説の才もある。","李傕 、お前は勇猛で用兵に長けているだけでなく、弁説の才もある。","李傕",},
        [1544] = {20184301,"董卓","現在、我軍は孫堅にやられっぱなしなのじゃが、何かいい策はないか?","現在、我軍は孫堅にやられっぱなしなのじゃが、何かいい策はないか?","李傕",},
        [1545] = {20184301,"李傕","孫堅にゃあ若うして優秀な息子がおると聞きましたけぇ、丞相のお嬢を娶せちゃぁどうじゃろう。","孫堅にゃあ若うして優秀な息子がおると聞きましたけぇ、丞相のお嬢を娶せちゃぁどうじゃろう。","董卓",},
        [1546] = {20184301,"李傕","そうすりゃぁ孫堅は、丞相の思うがままじゃ。","そうすりゃぁ孫堅は、丞相の思うがままじゃ。","董卓",},
        [1547] = {20184301,"董卓","面白い！ではその話、お前が縁談を持ちかけに行ってくれ。上手くいけば、褒美をどっさり与えるぞ。","面白い！ではその話、お前が縁談を持ちかけに行ってくれ。上手くいけば、褒美をどっさり与えるぞ。","李傕",},
        [1548] = {20184301,"李傕","かしこまった。ええ知らせを待ってつかぁさい！","かしこまった。ええ知らせを待ってつかぁさい！","董卓",},
        [1549] = {20184302,"李傕","董卓は死んでしもうた。早う西涼に逃げよう。まだ助かる望みがあるかもしれん。","董卓は死んでしもうた。早う西涼に逃げよう。まだ助かる望みがあるかもしれん。","賈詡",},
        [1550] = {20184302,"賈詡","皆さん落ち着いて、私に考えがあります！","皆さん落ち着いて、私に考えがあります！","李傕",},
        [1551] = {20184302,"賈詡","上手くいけば、再び朝廷を奉じて天下を正すことができますし、だめならその時逃げても遅くはないでしょう。","上手くいけば、再び朝廷を奉じて天下を正すことができますし、だめならその時逃げても遅くはないでしょう。","李傕",},
        [1552] = {20184302,"李傕","ワレはいつでも妙案を出してくれるのう、賈詡。早う言え、どがいな策があるんじゃ?","ワレはいつでも妙案を出してくれるのう、賈詡。早う言え、どがいな策があるんじゃ?","賈詡",},
        [1553] = {20184302,"賈詡","皆さんが軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まってしまいます。","皆さんが軍を捨ててばらばらに逃げては、宿駅の小役人にさえ簡単に捕まってしまいます。","李傕",},
        [1554] = {20184302,"賈詡","兵馬を招集しながら長安に攻め入り、董卓殿の仇を討つのがよいでしょう。","兵馬を招集しながら長安に攻め入り、董卓殿の仇を討つのがよいでしょう。","李傕",},
        [1555] = {20184302,"李傕","ええ策じゃ！王允は今、長安で董卓の残党狩りをしとるけぇ、","ええ策じゃ！王允は今、長安で董卓の残党狩りをしとるけぇ、","賈詡",},
        [1556] = {20184302,"李傕","わしらが「王允は西涼人を皆殺しにするつもりじゃ」と流言を広めりゃあ、反発する勢力がすぐ集まるじゃろう。","わしらが「王允は西涼人を皆殺しにするつもりじゃ」と流言を広めりゃあ、反発する勢力がすぐ集まるじゃろう。","賈詡",},
        [1557] = {20184303,"献帝","李傕 や郭汜は虎狼のごとく、この長安を混乱に陥れている。なぜ皇帝である朕がこのような酷い目に遭うのか。","李傕 や郭汜は虎狼のごとく、この長安を混乱に陥れている。なぜ皇帝である朕がこのような酷い目に遭うのか。","賈詡",},
        [1558] = {20184303,"賈詡","陛下、ここ長安は災いの地です。洛陽に戻るのはいかがでしょう。","陛下、ここ長安は災いの地です。洛陽に戻るのはいかがでしょう。","献帝",},
        [1559] = {20184303,"献帝","朕も東の都を懐かしく思うが、あの二人が同意すまい。","朕も東の都を懐かしく思うが、あの二人が同意すまい。","賈詡",},
        [1560] = {20184303,"賈詡","李傕は私の言うことに従いますから問題ありません。郭汜に関しては、私に一計があります。","李傕は私の言うことに従いますから問題ありません。郭汜に関しては、私に一計があります。","献帝",},
        [1561] = {20184303,"賈詡","郭汜の妻は嫉妬深いと聞いているので、反間の計を用いて、李傕 と郭汜を殺し合わせましょう。","郭汜の妻は嫉妬深いと聞いているので、反間の計を用いて、李傕 と郭汜を殺し合わせましょう。","献帝",},
        [1562] = {20184304,"郭汜","李傕！せっかく招かれてやったのに、酒に毒を混ぜて殺そうとするとはどういう了見だ?","李傕！せっかく招かれてやったのに、酒に毒を混ぜて殺そうとするとはどういう了見だ?","李傕",},
        [1563] = {20184304,"郭汜","もし妻が作ってくれた糞の汁を飲んで吐かなければ、この命はなかったぞ。","もし妻が作ってくれた糞の汁を飲んで吐かなければ、この命はなかったぞ。","李傕",},
        [1564] = {20184304,"李傕","死なんかっただけありがたく思え。おどれが先にわしの女房を誘惑したのが悪いんじゃ！","死なんかっただけありがたく思え。おどれが先にわしの女房を誘惑したのが悪いんじゃ！","郭汜",},
        [1565] = {20184304,"郭汜","お前は巫女を妄信し、いつも陣中で太鼓を叩かせて、神に祈らせているな。","お前は巫女を妄信し、いつも陣中で太鼓を叩かせて、神に祈らせているな。","李傕",},
        [1566] = {20184304,"郭汜","わしがいつお前の妻を誘惑した?醜いうえに頭も悪いではないか。","わしがいつお前の妻を誘惑した?醜いうえに頭も悪いではないか。","李傕",},
        [1567] = {20184304,"李傕","よくも言うてくれたな！士たるもん、殺されるとも侮辱は許さん！兵馬を揃えておどれの首を取っちゃるけぇ、待っとれ！","よくも言うてくれたな！士たるもん、殺されるとも侮辱は許さん！兵馬を揃えておどれの首を取っちゃるけぇ、待っとれ！","郭汜",},
        [1568] = {20184305,"郭汜","どうした。この前は互いに命の取り合いをしていたのに、まだわしのことが気になるのか。","どうした。この前は互いに命の取り合いをしていたのに、まだわしのことが気になるのか。","李傕",},
        [1569] = {20184305,"李傕","永遠の敵などおらん。あるのは永遠の利益だけじゃ。今はおどれと力を合わせたほうが得じゃけぇ。","永遠の敵などおらん。あるのは永遠の利益だけじゃ。今はおどれと力を合わせたほうが得じゃけぇ。","郭汜",},
        [1570] = {20184305,"李傕","楊奉と董承が献帝を連れて長安から逃げ出し、弘農へ向こうた。","楊奉と董承が献帝を連れて長安から逃げ出し、弘農へ向こうた。","郭汜",},
        [1571] = {20184305,"李傕","もしも山東に着いて足場を固めりゃあ、天下の諸将にわしらを討伐せよと命令するはずじゃ。そうなりゃぁ一族郎党皆殺しじゃけぇ。","もしも山東に着いて足場を固めりゃあ、天下の諸将にわしらを討伐せよと命令するはずじゃ。そうなりゃぁ一族郎党皆殺しじゃけぇ。","郭汜",},
        [1572] = {20184305,"郭汜","うむ、今、張済の兵は長安におり、軽はずみには動けん。","うむ、今、張済の兵は長安におり、軽はずみには動けん。","李傕",},
        [1573] = {20184305,"郭汜","この際わしとお前の手勢を合わせ、弘農へ攻めいって帝を殺し、天下を山分けにしよう！しかし、李楽や楊奉の兵は多い。何か妙案はないか?","この際わしとお前の手勢を合わせ、弘農へ攻めいって帝を殺し、天下を山分けにしよう！しかし、李楽や楊奉の兵は多い。何か妙案はないか?","李傕",},
        [1574] = {20184305,"李傕","なに、簡単なことよ！弱いモンは殺し、強いモンは軍に引き入れる。","なに、簡単なことよ！弱いモンは殺し、強いモンは軍に引き入れる。","郭汜",},
        [1575] = {20184305,"李傕","敵に遭うたら「決死隊」ゆぅて民兵を前に出せ。さらに服や物を道端に捨てさせ、敵軍の心を惑わすんじゃ。","敵に遭うたら「決死隊」ゆぅて民兵を前に出せ。さらに服や物を道端に捨てさせ、敵軍の心を惑わすんじゃ。","郭汜",},
        [1576] = {20184305,"郭汜","さすが李傕 よ！その策に乗らせてもらうぞ！","さすが李傕 よ！その策に乗らせてもらうぞ！","李傕",},
        [1577] = {20184401,"小喬","お姉ちゃん、お願い！そのウサギさんをあたしにちょうだい?","お姉ちゃん、お願い！そのウサギさんをあたしにちょうだい?","大喬",},
        [1578] = {20184401,"大喬","もう、小喬ったら、いつもわたしのものを欲しがるんだから。だめよ。これはお父さまからいただいた成人のお祝いなの。","もう、小喬ったら、いつもわたしのものを欲しがるんだから。だめよ。これはお父さまからいただいた成人のお祝いなの。","小喬",},
        [1579] = {20184401,"小喬","なによー！くれないなら、もう知らないよ。","なによー！くれないなら、もう知らないよ。","大喬",},
        [1580] = {20184401,"大喬","勝手になさい。こっちも静かに過ごせて清々するわ","勝手になさい。こっちも静かに過ごせて清々するわ","小喬",},
        [1581] = {20184401,"小喬","どーしてもくれないっていうのね?いいもん！お姉ちゃんがくれないなら、あたし、あたし……うぅっ……","どーしてもくれないっていうのね?いいもん！お姉ちゃんがくれないなら、あたし、あたし……うぅっ……","大喬",},
        [1582] = {20184401,"大喬","分かったわ、あげるわよ、だから泣かないの。あんまり泣き虫だと、素敵なお方のお嫁さんになれないわよ?","分かったわ、あげるわよ、だから泣かないの。あんまり泣き虫だと、素敵なお方のお嫁さんになれないわよ?","小喬",},
        [1583] = {20184401,"小喬","(泣きながら笑いつつ)ありがとう、お姉ちゃん！お姉ちゃんは世界で一番のお姉ちゃんだよ。ずっと守ってあげるからね！","(泣きながら笑いつつ)ありがとう、お姉ちゃん！お姉ちゃんは世界で一番のお姉ちゃんだよ。ずっと守ってあげるからね！","大喬",},
        [1584] = {20184402,"小喬","周瑜さま。旦那様を亡くしてから、お姉ちゃんはずっと一人でかわいそう……","周瑜さま。旦那様を亡くしてから、お姉ちゃんはずっと一人でかわいそう……","周瑜",},
        [1585] = {20184402,"周瑜","あなたはよい妹ですね、小喬。ならば、そばでたくさん話をして、慰めてあげてください。","あなたはよい妹ですね、小喬。ならば、そばでたくさん話をして、慰めてあげてください。","小喬",},
        [1586] = {20184402,"小喬","うん、そうするわ！屋敷に呼んで遊んであげたいと思うんだけど、周瑜さまはどう思う?","うん、そうするわ！屋敷に呼んで遊んであげたいと思うんだけど、周瑜さまはどう思う?","周瑜",},
        [1587] = {20184402,"周瑜","任せます。あなたの思い通りにしなさい。","任せます。あなたの思い通りにしなさい。","小喬",},
        [1588] = {20184403,"諸葛亮","……双台を左右に列ね、玉龍と金鳳あり。二喬を東南に従え、朝夕を共に楽しまん。","……双台を左右に列ね、玉龍と金鳳あり。二喬を東南に従え、朝夕を共に楽しまん。","小喬",},
        [1589] = {20184403,"小喬","諸葛先生が詠んだのは、曹操の息子の曹植が作った『銅雀台の賦』ね。","諸葛先生が詠んだのは、曹操の息子の曹植が作った『銅雀台の賦』ね。","諸葛亮",},
        [1590] = {20184403,"諸葛亮","そうです。ご存知なのですね、小喬夫人。","そうです。ご存知なのですね、小喬夫人。","小喬",},
        [1591] = {20184403,"小喬","ええ。でもあたしが間違えてなければ、元は「二つの橋が東西に連なり、大空の虹のごとし」だったはずだよ。","ええ。でもあたしが間違えてなければ、元は「二つの橋が東西に連なり、大空の虹のごとし」だったはずだよ。","諸葛亮",},
        [1592] = {20184403,"小喬","先生は、周瑜さまを焚きつけるためにわざと?","先生は、周瑜さまを焚きつけるためにわざと?","諸葛亮",},
        [1593] = {20184403,"諸葛亮","これほど高い教養を持ち、聡明でいらっしゃるとは……この孔明、感服の至りです。","これほど高い教養を持ち、聡明でいらっしゃるとは……この孔明、感服の至りです。","小喬",},
        [1594] = {20184403,"小喬","曹操の大軍が南下してきているわ。荊州と江東は一蓮托生。ここで手を組まきゃ、お互い曹操のエサだよね。","曹操の大軍が南下してきているわ。荊州と江東は一蓮托生。ここで手を組まきゃ、お互い曹操のエサだよね。","諸葛亮",},
        [1595] = {20184403,"小喬","それくらい、あたしだってわかるわ。諸葛先生、あたしが必ず周瑜さまを説得して、力になってあげるわよ。","それくらい、あたしだってわかるわ。諸葛先生、あたしが必ず周瑜さまを説得して、力になってあげるわよ。","諸葛亮",},
        [1596] = {20184404,"小喬","周瑜さま、どうしたの?眉間にしわなんか寄せちゃって。","周瑜さま、どうしたの?眉間にしわなんか寄せちゃって。","周瑜",},
        [1597] = {20184404,"周瑜","気にしないでください。少し軍務が忙しいだけですよ。","気にしないでください。少し軍務が忙しいだけですよ。","小喬",},
        [1598] = {20184404,"小喬","頑張りすぎなんじゃないの、周瑜さま。一休みしたら?一曲舞ってあげるから。","頑張りすぎなんじゃないの、周瑜さま。一休みしたら?一曲舞ってあげるから。","周瑜",},
        [1599] = {20184404,"周瑜","ふむ……小喬のいつもの踊りはしなやかで美しいが、今日はなぜ『大風歌』なのです?","ふむ……小喬のいつもの踊りはしなやかで美しいが、今日はなぜ『大風歌』なのです?","小喬",},
        [1600] = {20184404,"小喬","この「大風歌」は、漢の高祖劉邦が作ったんだよ。","この「大風歌」は、漢の高祖劉邦が作ったんだよ。","周瑜",},
        [1601] = {20184404,"小喬","項羽に負け続けてた劉邦だけど、少しずつ反対勢力を集め、ついに項羽を倒したの。それで、天下を統一して大漢王朝を立てたんだよ。","項羽に負け続けてた劉邦だけど、少しずつ反対勢力を集め、ついに項羽を倒したの。それで、天下を統一して大漢王朝を立てたんだよ。","周瑜",},
        [1602] = {20184404,"周瑜","ははぁ、読めました。私に劉備と組んで曹操に対抗しろと言うのですね。安心なさい。","ははぁ、読めました。私に劉備と組んで曹操に対抗しろと言うのですね。安心なさい。","小喬",},
        [1603] = {20184404,"周瑜","わざと曖昧な態度を取ってはいますが、すでに殿には説明し、孔明と共に曹操に対抗することを決めましたよ。","わざと曖昧な態度を取ってはいますが、すでに殿には説明し、孔明と共に曹操に対抗することを決めましたよ。","小喬",},
        [1604] = {20184404,"小喬","さすが周瑜さま！頭の回転が早いね。がんばって利口ぶったけど、釈迦に説法だったよ、えへへ！","さすが周瑜さま！頭の回転が早いね。がんばって利口ぶったけど、釈迦に説法だったよ、えへへ！","周瑜",},
        [1605] = {20184501,"孫策","俺は義兵を挙げて浙江を平定に来たのだ。お前はなぜ賊に味方する?","俺は義兵を挙げて浙江を平定に来たのだ。お前はなぜ賊に味方する?","王朗",},
        [1606] = {20184501,"王朗","義だと?白々しい！すでに呉郡を得ておきながら、私の土地をも飲み込もうというのか！今日こそ厳氏の仇を取ってやる！","義だと?白々しい！すでに呉郡を得ておきながら、私の土地をも飲み込もうというのか！今日こそ厳氏の仇を取ってやる！","孫策",},
        [1607] = {20184501,"孫策","思い上がるな、王朗の老いぼれめ！","思い上がるな、王朗の老いぼれめ！","王朗",},
        [1608] = {20184501,"孫策","太史慈、出でよ！ヤツの首を取ってこい！","太史慈、出でよ！ヤツの首を取ってこい！","王朗",},
        [1609] = {20184502,"曹操","お前が会稽太守の地位を失ったような真似はわしにはとてもできんことよ。","お前が会稽太守の地位を失ったような真似はわしにはとてもできんことよ。","王朗",},
        [1610] = {20184502,"王朗","いやはや、分相応に振る舞うというのは、実に難しいものですな。","いやはや、分相応に振る舞うというのは、実に難しいものですな。","曹操",},
        [1611] = {20184502,"曹操","ほう、なぜだ?","ほう、なぜだ?","王朗",},
        [1612] = {20184502,"王朗","当時の私は会稽太守という漢の官吏でありながら、領地を守り民を養うことができませんでした。","当時の私は会稽太守という漢の官吏でありながら、領地を守り民を養うことができませんでした。","曹操",},
        [1613] = {20184502,"王朗","つまりは、必要のない節約をしていたということでしょう。","つまりは、必要のない節約をしていたということでしょう。","曹操",},
        [1614] = {20184502,"王朗","対して殿は、新しい客人に対してこのように思いもよらない大層なもてなしをされる。","対して殿は、新しい客人に対してこのように思いもよらない大層なもてなしをされる。","曹操",},
        [1615] = {20184502,"王朗","これは、節約すべきところをしていないというべきでしょうな。","これは、節約すべきところをしていないというべきでしょうな。","曹操",},
        [1616] = {20184502,"曹操","はははは、弁舌の才があるな。","はははは、弁舌の才があるな。","王朗",},
        [1617] = {20184502,"曹操","お前を召し出し、諫議大夫・参司空軍司に任命したのは間違いではなかったようだ。","お前を召し出し、諫議大夫・参司空軍司に任命したのは間違いではなかったようだ。","王朗",},
        [1618] = {20184503,"王朗","袁紹、孫策、劉備はみな天下を分裂させた賊。私こそが朝廷の正統を守ったのだ。","袁紹、孫策、劉備はみな天下を分裂させた賊。私こそが朝廷の正統を守ったのだ。","",},
        [1619] = {20184503,"王朗","私は御史大夫、司空、司徒を歴任し、『周易伝』、『春秋伝』、『孝経伝』など三十四巻を書き記した。","私は御史大夫、司空、司徒を歴任し、『周易伝』、『春秋伝』、『孝経伝』など三十四巻を書き記した。","",},
        [1620] = {20184503,"王朗","孫娘の王元姫は、司馬懿の次男、司馬昭に嫁ぎ、西晋の武帝・司馬炎を産んだのだ。","孫娘の王元姫は、司馬懿の次男、司馬昭に嫁ぎ、西晋の武帝・司馬炎を産んだのだ。","",},
        [1621] = {20184503,"王朗","私は断じて白ひげの老賊などではない。ましてや時代遅れの堅物でもない！","私は断じて白ひげの老賊などではない。ましてや時代遅れの堅物でもない！","",},
        [1622] = {20184601,"曹操","宦官張譲、天に替わって成敗してくれる。おとなしく命を差し出せ！","宦官張譲、天に替わって成敗してくれる。おとなしく命を差し出せ！","張譲",},
        [1623] = {20184601,"張譲","誰か、誰か早く来なさい！曹操が来ました！私を殺すつもりです！ひえぇ～っ！","誰か、誰か早く来なさい！曹操が来ました！私を殺すつもりです！ひえぇ～っ！","曹操",},
        [1624] = {20184601,"曹操","逃げても無駄だ！","逃げても無駄だ！","張譲",},
        [1625] = {20184602,"張譲","お前たち、早く取り囲みなさい。早く！","お前たち、早く取り囲みなさい。早く！","曹操",},
        [1626] = {20184602,"張譲","曹操は壁を越えて逃げるつもりなのです。逃がしてはなりません！","曹操は壁を越えて逃げるつもりなのです。逃がしてはなりません！","曹操",},
        [1627] = {20184602,"曹操","お前には幸運が味方しているようだな。今回は見逃すが、いつか殺すぞ、震えて眠るが良い！","お前には幸運が味方しているようだな。今回は見逃すが、いつか殺すぞ、震えて眠るが良い！","張譲",},
        [1628] = {20184602,"張譲","追いなさい！追え！","追いなさい！追え！","曹操",},
        [1629] = {20184603,"張譲","最初に大将軍を陥れる計画を立てたのは、蹇碩です。私たちは存じておりません。","最初に大将軍を陥れる計画を立てたのは、蹇碩です。私たちは存じておりません。","何太后",},
        [1630] = {20184603,"張譲","しかし今、大将軍は袁紹の言葉に踊らされ、我らを誅殺しようとしています。濡れ衣です！どうか太后さまのお慈悲を！","しかし今、大将軍は袁紹の言葉に踊らされ、我らを誅殺しようとしています。濡れ衣です！どうか太后さまのお慈悲を！","何太后",},
        [1631] = {20184603,"何太后","心配するな、お前たち。わらわが守ってつかわそう。","心配するな、お前たち。わらわが守ってつかわそう。","張譲",},
        [1632] = {20184604,"何太后","わたしたちは卑しい生まれ。張譲がいなかったら、今のような富貴を楽しめる身分ではなかったはずよ。","わたしたちは卑しい生まれ。張譲がいなかったら、今のような富貴を楽しめる身分ではなかったはずよ。","何進",},
        [1633] = {20184604,"何太后","蹇碩の人でなしは、今すでに誅されたわ。このうえ誰かにそそのかされて、宦官を皆殺しにするおつもり?","蹇碩の人でなしは、今すでに誅されたわ。このうえ誰かにそそのかされて、宦官を皆殺しにするおつもり?","何進",},
        [1634] = {20184604,"何進","わしを殺そうとした蹇碩の処分は当然だ。一族郎党滅ぶべきだ。しかし、他の者までむやみに殺す必要はない。","わしを殺そうとした蹇碩の処分は当然だ。一族郎党滅ぶべきだ。しかし、他の者までむやみに殺す必要はない。","何太后",},
        [1635] = {20184604,"袁紹","甘いことを申してはいけません。ここで根から断たなければ、必ず御身を滅ぼしますぞ。","甘いことを申してはいけません。ここで根から断たなければ、必ず御身を滅ぼしますぞ。","何進",},
        [1636] = {20184604,"何進","ええい、うるさい。わしはもう決めたのだ。口出しするな。","ええい、うるさい。わしはもう決めたのだ。口出しするな。","袁紹",},
        [1637] = {20184605,"董太后","何進の妹は、元々わらわが引き上げてやったのです。なのに、今やその子が皇帝となり、臣下を腹心で固めて権力をほしいままにしている。","何進の妹は、元々わらわが引き上げてやったのです。なのに、今やその子が皇帝となり、臣下を腹心で固めて権力をほしいままにしている。","張譲",},
        [1638] = {20184605,"董太后","わらわはどうすればよいのか?","わらわはどうすればよいのか?","張譲",},
        [1639] = {20184605,"張譲","太后さまが朝廷にお出ましになり、幼帝に代わって政務を行い、協皇子さまを王になさいませ。","太后さまが朝廷にお出ましになり、幼帝に代わって政務を行い、協皇子さまを王になさいませ。","",},
        [1640] = {20184605,"張譲","さらに国舅董重さまに位を授けて軍事を掌握させ、我らを重用していただければ、あなたの思い通りになりましょう。","さらに国舅董重さまに位を授けて軍事を掌握させ、我らを重用していただければ、あなたの思い通りになりましょう。","",},
        [1641] = {20184605,"董太后","そうか。では、協皇子を陳留王に封じ、董重を驃騎将軍に命じ、お前には政務を預けよう。","そうか。では、協皇子を陳留王に封じ、董重を驃騎将軍に命じ、お前には政務を預けよう。","張譲",},
        [1642] = {20184701,"曹操","あなたは何者だ?これほど美しい人間をわしは知らん。","あなたは何者だ?これほど美しい人間をわしは知らん。","鄒氏",},
        [1643] = {20184701,"鄒氏","張済の妻の鄒氏と申します。","張済の妻の鄒氏と申します。","曹操",},
        [1644] = {20184701,"曹操","わしを知っておるか?","わしを知っておるか?","鄒氏",},
        [1645] = {20184701,"鄒氏","もちろんです。丞相の威名は常々うかがっております。今晩こうしてお会いできて幸運にございます。","もちろんです。丞相の威名は常々うかがっております。今晩こうしてお会いできて幸運にございます。","曹操",},
        [1646] = {20184701,"曹操","ご夫人に免じて、特別に張繍の降伏を認めよう。一族郎党滅ぼしてくれるところであった。","ご夫人に免じて、特別に張繍の降伏を認めよう。一族郎党滅ぼしてくれるところであった。","鄒氏",},
        [1647] = {20184701,"鄒氏","誠に恐れ入ります。お救いいただいたご恩は忘れません。","誠に恐れ入ります。お救いいただいたご恩は忘れません。","曹操",},
        [1648] = {20184701,"曹操","ご夫人と会えたのは天の恵み。今宵は枕を並べようではないか。わしにすべてを任せ、贅沢な暮らしを楽まぬか?","ご夫人と会えたのは天の恵み。今宵は枕を並べようではないか。わしにすべてを任せ、贅沢な暮らしを楽まぬか?","鄒氏",},
        [1649] = {20184701,"鄒氏","丞相のご寵愛に感謝いたします。","丞相のご寵愛に感謝いたします。","曹操",},
        [1650] = {20184702,"鄒氏","丞相。ずっと城内にいては、張繍も疑いましょうし、人々からも噂が出てしまいます。","丞相。ずっと城内にいては、張繍も疑いましょうし、人々からも噂が出てしまいます。","曹操",},
        [1651] = {20184702,"曹操","それもそうだな。では明日は城外に移り、野営地でご夫人と休むとしよう。","それもそうだな。では明日は城外に移り、野営地でご夫人と休むとしよう。","鄒氏",},
        [1652] = {20184702,"曹操","さらに本陣の幕の外に典韋を立たせ、守らせよう。","さらに本陣の幕の外に典韋を立たせ、守らせよう。","鄒氏",},
        [1653] = {20184702,"曹操","典韋よ、決して他の者を中へ入れるなよ。","典韋よ、決して他の者を中へ入れるなよ。","鄒氏",},
        [1654] = {20184702,"典韋","はっ、丞相。","はっ、丞相。","曹操",},
        [1655] = {20184801,"李典","あそこに見える金の兜は孫権だ。左は宋謙、右は賈華で、どちらも方天画戟を使う。","あそこに見える金の兜は孫権だ。左は宋謙、右は賈華で、どちらも方天画戟を使う。","楽進",},
        [1656] = {20184801,"李典","金兜の孫権を捕らえれば、八十三万の味方の恨みを晴らすことができるぞ。","金兜の孫権を捕らえれば、八十三万の味方の恨みを晴らすことができるぞ。","楽進",},
        [1657] = {20184801,"楽進","よし、孫権がどれだけの実力か試してやる！","よし、孫権がどれだけの実力か試してやる！","李典",},
        [1658] = {20184802,"孫権","お前と張遼が戦っている時、一騎の敵将が横合いを突いて俺の首を取りに来た。","お前と張遼が戦っている時、一騎の敵将が横合いを突いて俺の首を取りに来た。","太史慈",},
        [1659] = {20184802,"孫権","宋謙と賈華が画戟で防いでくれなかったら、俺の首は胴から離れていただろう。","宋謙と賈華が画戟で防いでくれなかったら、俺の首は胴から離れていただろう。","太史慈",},
        [1660] = {20184802,"孫権","そやつ、手にした刀を電光のように振り下ろし、宋謙、賈華の双戟を断ち割ったのだ！","そやつ、手にした刀を電光のように振り下ろし、宋謙、賈華の双戟を断ち割ったのだ！","太史慈",},
        [1661] = {20184802,"孫権","お前はそやつが何者か知っているか?","お前はそやつが何者か知っているか?","太史慈",},
        [1662] = {20184802,"太史慈","そいつはおそらく楽進。魏の五将軍の一人、楽進でしょう！","そいつはおそらく楽進。魏の五将軍の一人、楽進でしょう！","孫権",},
        [1663] = {20184803,"曹操","興平元年、楽進は呂布と濮陽で戦い、張超と雍丘で戦い、さらに橋蕤と苦県で戦った。","興平元年、楽進は呂布と濮陽で戦い、張超と雍丘で戦い、さらに橋蕤と苦県で戦った。","",},
        [1664] = {20184803,"曹操","建安三年、張繍を安衆で攻め、呂布を下邳で包囲して配下の将軍を破った。次の年には眭固を射犬で攻め、劉備を小沛で攻めた。","建安三年、張繍を安衆で攻め、呂布を下邳で包囲して配下の将軍を破った。次の年には眭固を射犬で攻め、劉備を小沛で攻めた。","",},
        [1665] = {20184803,"曹操","建安五年、于禁と共に黄河を渡って獲嘉へ進撃し、袁紹を官渡で攻めて配下の淳于瓊を斬った！","建安五年、于禁と共に黄河を渡って獲嘉へ進撃し、袁紹を官渡で攻めて配下の淳于瓊を斬った！","",},
        [1666] = {20184803,"曹操","建安九年、袁紹の子袁譚、袁尚兄弟を黎陽で攻め、その大将厳敬を斬った！","建安九年、袁紹の子袁譚、袁尚兄弟を黎陽で攻め、その大将厳敬を斬った！","",},
        [1667] = {20184803,"曹操","楽進はわしと共に各地を転戦し、大小の戦に参加すること20回以上、一度も負けたことはない。真の猛将だ！","楽進はわしと共に各地を転戦し、大小の戦に参加すること20回以上、一度も負けたことはない。真の猛将だ！","",},
        [1668] = {20184901,"董卓","お前は何をしにここへ来たのだ?","お前は何をしにここへ来たのだ?","李儒",},
        [1669] = {20184901,"李儒","はい。私がちょうど門前まで来たところ、董卓さまが怒って裏庭に入られ、呂布殿を探しているとおっしゃる声が聞こえました。","はい。私がちょうど門前まで来たところ、董卓さまが怒って裏庭に入られ、呂布殿を探しているとおっしゃる声が聞こえました。","董卓",},
        [1670] = {20184901,"李儒","何事かと思っていると、今度は呂布殿が走ってきて、董卓さまに殺されると言うではないですか！","何事かと思っていると、今度は呂布殿が走ってきて、董卓さまに殺されると言うではないですか！","董卓",},
        [1671] = {20184901,"李儒","私はあなたをおなだめ申し上げるために、急いで来たのです。","私はあなたをおなだめ申し上げるために、急いで来たのです。","董卓",},
        [1672] = {20184901,"董卓","無駄じゃ！逆賊は許さん！わしの寵姫に手を出しおって、必ず殺してやる！","無駄じゃ！逆賊は許さん！わしの寵姫に手を出しおって、必ず殺してやる！","李儒",},
        [1673] = {20184901,"李儒","何を仰るのです。昔、楚の荘王は絶纓の会で、愛姫に手を出した蒋雄を咎めませんでした。","何を仰るのです。昔、楚の荘王は絶纓の会で、愛姫に手を出した蒋雄を咎めませんでした。","董卓",},
        [1674] = {20184901,"李儒","後に秦の兵に苦しめられた時、彼は死力を尽くして救ったと言います。","後に秦の兵に苦しめられた時、彼は死力を尽くして救ったと言います。","董卓",},
        [1675] = {20184901,"李儒","貂蝉はただの娘に過ぎませんが、呂布殿は腹心の猛将です。","貂蝉はただの娘に過ぎませんが、呂布殿は腹心の猛将です。","董卓",},
        [1676] = {20184901,"李儒","これを機に貂蝉を与えれば、呂布殿は大恩を感じ、命を掛けて董卓さまに尽くすでしょう。","これを機に貂蝉を与えれば、呂布殿は大恩を感じ、命を掛けて董卓さまに尽くすでしょう。","董卓",},
        [1677] = {20184901,"李儒","よくお考えください、董卓さま。","よくお考えください、董卓さま。","董卓",},
        [1678] = {20184901,"董卓","うむむ……お前の言う通りだ。考え直そう。","うむむ……お前の言う通りだ。考え直そう。","李儒",},
        [1679] = {20184902,"呂布","董卓の暴虐非道に入れ知恵したのは李儒だ！ヤツを捕らえに行く者はいないか?","董卓の暴虐非道に入れ知恵したのは李儒だ！ヤツを捕らえに行く者はいないか?","",},
        [1680] = {20184902,"李粛","私が捕らえて参りましょう！","私が捕らえて参りましょう！","呂布",},
        [1681] = {20184902,"王允","李儒の家の下僕が、李儒を縛って届けに来た。","李儒の家の下僕が、李儒を縛って届けに来た。","呂布",},
        [1682] = {20184902,"呂布","よし、縛ったまま、市中に引ったてて首を斬れ！","よし、縛ったまま、市中に引ったてて首を斬れ！","王允",},
        [1683] = {20185001,"呂布","この呂布の娘を酷い目に遭わせるヤツは誰だ！バラバラに引き裂いてくれる！","この呂布の娘を酷い目に遭わせるヤツは誰だ！バラバラに引き裂いてくれる！","呂霊雎",},
        [1684] = {20185001,"呂霊雎","ほ～ら、父上はこう言っているぞ?それでも手を出せるヤツはいるか?","ほ～ら、父上はこう言っているぞ?それでも手を出せるヤツはいるか?","呂布",},
        [1685] = {20185001,"呂霊雎","それでも私をいじめたければ、前に出よ！","それでも私をいじめたければ、前に出よ！","呂布",},
        [1686] = {20185002,"呂霊雎","父上、私は嫁になど行きたくありませぬ！","父上、私は嫁になど行きたくありませぬ！","呂布",},
        [1687] = {20185002,"呂霊雎","愛し合うより、殺し合いたい。父上のような大英雄になりたい！","愛し合うより、殺し合いたい。父上のような大英雄になりたい！","呂布",},
        [1688] = {20185002,"呂布","何を言う。女は嫁に行って家を守り、夫を助け子を育てるものだ。","何を言う。女は嫁に行って家を守り、夫を助け子を育てるものだ。","呂霊雎",},
        [1689] = {20185002,"呂布","それがこんな跳ねっ返りに育ってしまうとは……ああ、ちゃんと面倒を見ず、刀や槍で遊ばせていたのが良くなかった。","それがこんな跳ねっ返りに育ってしまうとは……ああ、ちゃんと面倒を見ず、刀や槍で遊ばせていたのが良くなかった。","呂霊雎",},
        [1690] = {20185101,"程昱","ワシはいつも不思議な夢を見るのじゃ。それは自分が泰山にいて太陽を捧げ持ち、その後、黄巾が天下に乱を起こすというものじゃ。","ワシはいつも不思議な夢を見るのじゃ。それは自分が泰山にいて太陽を捧げ持ち、その後、黄巾が天下に乱を起こすというものじゃ。","曹操",},
        [1691] = {20185101,"程昱","思うに、これはワシがいずれ名君を補佐することの暗示なのではないだろうか。","思うに、これはワシがいずれ名君を補佐することの暗示なのではないだろうか。","曹操",},
        [1692] = {20185101,"曹操","わっはっはっは、面白いことを言う。","わっはっはっは、面白いことを言う。","程昱",},
        [1693] = {20185101,"曹操","泰山で太陽を捧げ持つ夢を見ると言うなら、名前の上に「日」の字を付けたらどうだ。","泰山で太陽を捧げ持つ夢を見ると言うなら、名前の上に「日」の字を付けたらどうだ。","程昱",},
        [1694] = {20185101,"曹操","そして、名を程昱と改めたらいい。","そして、名を程昱と改めたらいい。","程昱",},
        [1695] = {20185101,"程昱","ふむ、程昱か……丞相のご意見はごもっともじゃ。よし！","ふむ、程昱か……丞相のご意見はごもっともじゃ。よし！","曹操",},
        [1696] = {20185102,"程昱","荀彧、ワシの泰山と太陽の夢は知っておるな。","荀彧、ワシの泰山と太陽の夢は知っておるな。","荀彧",},
        [1697] = {20185102,"荀彧","もちろん。あなたから何度も聞かされていますから。","もちろん。あなたから何度も聞かされていますから。","程昱",},
        [1698] = {20185102,"程昱","それが、丞相から改名させられた後、ぴたりと見なくなったのじゃ。不思議なことじゃのう。","それが、丞相から改名させられた後、ぴたりと見なくなったのじゃ。不思議なことじゃのう。","荀彧",},
        [1699] = {20185102,"荀彧","はははは、きっと改名が良かったのでしょうね。","はははは、きっと改名が良かったのでしょうね。","程昱",},
        [1700] = {20185103,"曹操","袁紹の手勢は多く、兵馬はよく訓練されている。","袁紹の手勢は多く、兵馬はよく訓練されている。","程昱",},
        [1701] = {20185103,"曹操","誰か、これを破る策はないか?","誰か、これを破る策はないか?","程昱",},
        [1702] = {20185103,"程昱","ワシに一計があります。十面埋伏の計をもって、袁紹を破ってみせますぞ。","ワシに一計があります。十面埋伏の計をもって、袁紹を破ってみせますぞ。","曹操",},
        [1703] = {20185103,"曹操","何だ?十面埋伏とは","何だ?十面埋伏とは","程昱",},
        [1704] = {20185103,"程昱","まず、わが軍を黄河の岸まで退かせ、伏兵を十隊忍ばせるのですじゃ。","まず、わが軍を黄河の岸まで退かせ、伏兵を十隊忍ばせるのですじゃ。","曹操",},
        [1705] = {20185103,"程昱","次の日に、中軍の先鋒が、敵陣を攻めると見せかけてすぐ後退し、袁紹を河岸までおびき寄せますのじゃ","次の日に、中軍の先鋒が、敵陣を攻めると見せかけてすぐ後退し、袁紹を河岸までおびき寄せますのじゃ","曹操",},
        [1706] = {20185103,"程昱","中軍の反撃に合わせ、伏兵十隊が次々と攻撃を仕掛ければ、袁紹軍は必ずや壊滅するじゃろう。","中軍の反撃に合わせ、伏兵十隊が次々と攻撃を仕掛ければ、袁紹軍は必ずや壊滅するじゃろう。","曹操",},
        [1707] = {20185103,"曹操","面白い。よい計だ！","面白い。よい計だ！","程昱",},
        [1708] = {20185201,"蘇飛","甘寧の水上戦の実力は抜きん出ています。孫権を破って夏口を奪回したほどです。","甘寧の水上戦の実力は抜きん出ています。孫権を破って夏口を奪回したほどです。","黄祖",},
        [1709] = {20185201,"蘇飛","他と代えがたい逸材でしょう。ぜひとも厚遇するべきです！","他と代えがたい逸材でしょう。ぜひとも厚遇するべきです！","黄祖",},
        [1710] = {20185201,"黄祖","ふん。江賊出身のゴロツキが、わしらのような名門と比べられるか。","ふん。江賊出身のゴロツキが、わしらのような名門と比べられるか。","",},
        [1711] = {20185201,"黄祖","重用など、できるはずがない！","重用など、できるはずがない！","",},
        [1712] = {20185201,"蘇飛","しかし、あまり冷遇すると、恨まれますぞ！","しかし、あまり冷遇すると、恨まれますぞ！","黄祖",},
        [1713] = {20185202,"凌統","甘寧はどうして呉へ降ってきのだ！","甘寧はどうして呉へ降ってきのだ！","呂蒙",},
        [1714] = {20185202,"凌統","この前の夏口の戦いで、ヤツは俺の父上、破賊校尉凌操を射殺したんだぞ！","この前の夏口の戦いで、ヤツは俺の父上、破賊校尉凌操を射殺したんだぞ！","呂蒙",},
        [1715] = {20185202,"凌統","そんなヤツと組むなら、オレが辞めてやる！","そんなヤツと組むなら、オレが辞めてやる！","呂蒙",},
        [1716] = {20185202,"呂蒙","まあ、待て。やつは水上戦の天才で、殿はそれを非常に買っておるのだ。","まあ、待て。やつは水上戦の天才で、殿はそれを非常に買っておるのだ。","凌統",},
        [1717] = {20185202,"呂蒙","乱世では、天下の事のみを重んじるべきだ。過去の恨みに捕らわれてどうする！","乱世では、天下の事のみを重んじるべきだ。過去の恨みに捕らわれてどうする！","凌統",},
        [1718] = {20185203,"呂蒙","お前が凌操を射殺したのは事実、凌統が恨むのは当然だろう。","お前が凌操を射殺したのは事実、凌統が恨むのは当然だろう。","甘寧",},
        [1719] = {20185203,"呂蒙","殿はそんなお前たちの不和を知り、私に調停を命じられたのだ。","殿はそんなお前たちの不和を知り、私に調停を命じられたのだ。","甘寧",},
        [1720] = {20185203,"呂蒙","この前、凌統と腹を割って話した。やつは過去の恨みは捨て、天下の大事を取ると言ってくれたよ。","この前、凌統と腹を割って話した。やつは過去の恨みは捨て、天下の大事を取ると言ってくれたよ。","甘寧",},
        [1721] = {20185203,"呂蒙","これまでの多くの無礼を、どうかお許し願いたい。","これまでの多くの無礼を、どうかお許し願いたい。","甘寧",},
        [1722] = {20185203,"甘寧","オレもすべて水に流すぞ。殿のために忠義を尽くす！","オレもすべて水に流すぞ。殿のために忠義を尽くす！","呂蒙",},
        [1723] = {20185301,"楊奉","徐晃っ、徐晃はどこだ?","徐晃っ、徐晃はどこだ?","徐晃",},
        [1724] = {20185301,"楊奉","何をモタモタしておる！早く、天子の御車を守らんか！","何をモタモタしておる！早く、天子の御車を守らんか！","徐晃",},
        [1725] = {20185301,"徐晃","ここにおります。楊奉殿は天子を守って、洛陽へお戻りください。","ここにおります。楊奉殿は天子を守って、洛陽へお戻りください。","楊奉",},
        [1726] = {20185301,"徐晃","郭汜の将、崔勇は私が食い止めます！","郭汜の将、崔勇は私が食い止めます！","楊奉",},
        [1727] = {20185302,"献帝","汝が朕を窮地から救ったこと、多大なる功である！","汝が朕を窮地から救ったこと、多大なる功である！","楊奉",},
        [1728] = {20185302,"楊奉","ははっ、臣の職責なれば。","ははっ、臣の職責なれば。","献帝",},
        [1729] = {20185302,"献帝","賊将の首を斬った者は誰か?","賊将の首を斬った者は誰か?","楊奉",},
        [1730] = {20185302,"楊奉","河東郡楊県の出で、姓は徐、名は晃、字を公明と申す者でございます。","河東郡楊県の出で、姓は徐、名は晃、字を公明と申す者でございます。","献帝",},
        [1731] = {20185302,"献帝","うむ！徐晃の此度の功をもって、都亭侯に封じる。","うむ！徐晃の此度の功をもって、都亭侯に封じる。","楊奉",},
        [1732] = {20185303,"徐晃","関羽殿。丞相の命を受けて参った。樊城、襄陽の包囲を解いていただこう！","関羽殿。丞相の命を受けて参った。樊城、襄陽の包囲を解いていただこう！","関羽",},
        [1733] = {20185303,"関羽","お前とわしは同郷のよしみこそあるが、今は別の君主に仕える身。残念だが、兵を交えることは避けられまい。","お前とわしは同郷のよしみこそあるが、今は別の君主に仕える身。残念だが、兵を交えることは避けられまい。","徐晃",},
        [1734] = {20185303,"徐晃","同郷だが……悪く思われるな。これは国家のことゆえ。","同郷だが……悪く思われるな。これは国家のことゆえ。","関羽",},
        [1735] = {20185303,"徐晃","皆の者、聞け！関羽の首を取った者には、莫大な恩賞があるぞ！","皆の者、聞け！関羽の首を取った者には、莫大な恩賞があるぞ！","関羽",},
        [1736] = {20185304,"徐晃","丞相自ら七里先まで出迎えに来ていただけるとは、この徐晃、身に余る光栄です。","丞相自ら七里先まで出迎えに来ていただけるとは、この徐晃、身に余る光栄です。","曹操",},
        [1737] = {20185304,"曹操","お前の勇敢さには及ばぬわ。二重三重に堀を巡らせていた敵陣を突破し、賊を包囲して多くの首を斬ったではないか。","お前の勇敢さには及ばぬわ。二重三重に堀を巡らせていた敵陣を突破し、賊を包囲して多くの首を斬ったではないか。","徐晃",},
        [1738] = {20185304,"曹操","わし自身30年以上兵を用い、また歴史上の達人も数多く知っているが、これほどの長距離を駆けて敵に攻め入った者はおらぬ。","わし自身30年以上兵を用い、また歴史上の達人も数多く知っているが、これほどの長距離を駆けて敵に攻め入った者はおらぬ。","徐晃",},
        [1739] = {20185304,"曹操","まして樊城、襄陽の防御は、歴史上の莒や即墨のそれ以上だ。だから将軍の功績は、孫武や穰苴にも勝るものだ。","まして樊城、襄陽の防御は、歴史上の莒や即墨のそれ以上だ。だから将軍の功績は、孫武や穰苴にも勝るものだ。","徐晃",},
        [1740] = {20185304,"徐晃","お褒めをいただき、光栄にございます。これからも力の限りを尽くす所存です。","お褒めをいただき、光栄にございます。これからも力の限りを尽くす所存です。","曹操",},
        [1741] = {20185401,"馬超","妹をいじめたヤツなどいない。いるのは、妹がいじめたヤツだけだ！","妹をいじめたヤツなどいない。いるのは、妹がいじめたヤツだけだ！","馬雲禄",},
        [1742] = {20185401,"馬雲禄","やめてよ、兄貴！それ以上言われたら、お嫁に行けなくなっちゃう！","やめてよ、兄貴！それ以上言われたら、お嫁に行けなくなっちゃう！","馬超",},
        [1743] = {20185401,"馬超","ハッハッハ！お前はカラッと爽やかな性格で、出るところは出て締まるところは締まっている。もちろん美人で、槍の腕前も抜群だ！","ハッハッハ！お前はカラッと爽やかな性格で、出るところは出て締まるところは締まっている。もちろん美人で、槍の腕前も抜群だ！","馬雲禄",},
        [1744] = {20185401,"馬超","どのみち並の男では釣り合わんから、問題ない！","どのみち並の男では釣り合わんから、問題ない！","馬雲禄",},
        [1745] = {20185402,"張飛","馬超の妹が兄を訪ねて来たんだが、途中で張苞の許嫁を助けてくれたんだ。","馬超の妹が兄を訪ねて来たんだが、途中で張苞の許嫁を助けてくれたんだ。","趙雲",},
        [1746] = {20185402,"張飛","この馬超の妹がよ、肉付きもいいし、えらい別嬪でな。しかもまだ嫁入り前ときてやがる。","この馬超の妹がよ、肉付きもいいし、えらい別嬪でな。しかもまだ嫁入り前ときてやがる。","趙雲",},
        [1747] = {20185402,"張飛","趙雲、おめーもまだ嫁がねえだろ。紹介してやりてえんだが、どうだ?","趙雲、おめーもまだ嫁がねえだろ。紹介してやりてえんだが、どうだ?","趙雲",},
        [1748] = {20185402,"趙雲","なっ……なななな何を言うのだ！そっ、そそそそそんなことは許されぬ！","なっ……なななな何を言うのだ！そっ、そそそそそんなことは許されぬ！","張飛",},
        [1749] = {20185402,"張飛","なーにが許されぬだよ、お堅え野郎だな。豪傑のくせに顔を真っ赤にしやがって！","なーにが許されぬだよ、お堅え野郎だな。豪傑のくせに顔を真っ赤にしやがって！","趙雲",},
        [1750] = {20185501,"袁紹","今や奸臣どもが乱を企て、陛下はあちこちを漂泊されている。","今や奸臣どもが乱を企て、陛下はあちこちを漂泊されている。","沮授",},
        [1751] = {20185501,"袁紹","わしの家は代々、漢王室の恩顧を受けてきた家柄だ。力と命の限りを尽くして漢の天下を復活させたい。","わしの家は代々、漢王室の恩顧を受けてきた家柄だ。力と命の限りを尽くして漢の天下を復活させたい。","沮授",},
        [1752] = {20185501,"袁紹","しかし斉の桓公も、管仲がいなければ覇業は成らなかった。勾践も、范蠡がいなければ越の国を守れなかった。","しかし斉の桓公も、管仲がいなければ覇業は成らなかった。勾践も、范蠡がいなければ越の国を守れなかった。","沮授",},
        [1753] = {20185501,"袁紹","そこでわしはお前と力を合わせ、国家を安定させていきたいと思っておる。わしのために、お前はどんな計を授けてくれるのか?","そこでわしはお前と力を合わせ、国家を安定させていきたいと思っておる。わしのために、お前はどんな計を授けてくれるのか?","沮授",},
        [1754] = {20185501,"沮授","将軍は20歳にもならない内から朝廷に重用され、名声は全国におよびました。","将軍は20歳にもならない内から朝廷に重用され、名声は全国におよびました。","袁紹",},
        [1755] = {20185501,"沮授","董卓が皇帝を廃した時、将軍は忠義心からただ一人騎馬で出奔し、奴を恐怖させました。","董卓が皇帝を廃した時、将軍は忠義心からただ一人騎馬で出奔し、奴を恐怖させました。","袁紹",},
        [1756] = {20185501,"沮授","その後、黄河を北に渡り、勃海の民を帰順させました。","その後、黄河を北に渡り、勃海の民を帰順させました。","袁紹",},
        [1757] = {20185501,"沮授","一郡の軍を擁し、冀州の人馬を集め、その威によって黄河以北を治め、名声を天下にとどろかせました。","一郡の軍を擁し、冀州の人馬を集め、その威によって黄河以北を治め、名声を天下にとどろかせました。","袁紹",},
        [1758] = {20185501,"沮授","まずその兵を東へ向け、黄巾を排除し、黒山の張燕を滅ぼします。次に兵を北へ転じて、公孫瓚を下し、匈奴を平定します。","まずその兵を東へ向け、黄巾を排除し、黒山の張燕を滅ぼします。次に兵を北へ転じて、公孫瓚を下し、匈奴を平定します。","袁紹",},
        [1759] = {20185501,"沮授","こうして黄河以北を掃討し、四州の地盤を併合して英雄、才人を集め、百万の人馬を擁して、長安に陛下を迎えるのです。","こうして黄河以北を掃討し、四州の地盤を併合して英雄、才人を集め、百万の人馬を擁して、長安に陛下を迎えるのです。","袁紹",},
        [1760] = {20185501,"沮授","そして洛陽にある宗廟を甦らせ、天下に向けて号令を発し、従わぬ者を討伐なされませ。","そして洛陽にある宗廟を甦らせ、天下に向けて号令を発し、従わぬ者を討伐なされませ。","袁紹",},
        [1761] = {20185501,"沮授","このような方法で勝負をかければ、誰が抗うことができましょう。数年後には、大業を成し遂げることも不可能ではありません。","このような方法で勝負をかければ、誰が抗うことができましょう。数年後には、大業を成し遂げることも不可能ではありません。","袁紹",},
        [1762] = {20185502,"沮授","星の動きを見ますと、太白星が柳と鬼の間を逆行し、入牛と斗の間に光が差し込んでいます。","星の動きを見ますと、太白星が柳と鬼の間を逆行し、入牛と斗の間に光が差し込んでいます。","袁紹",},
        [1763] = {20185502,"沮授","おそらく賊による略奪が起こるでしょう。","おそらく賊による略奪が起こるでしょう。","袁紹",},
        [1764] = {20185502,"沮授","烏巣の兵糧所は守備が薄いと感じます。すぐに精鋭、猛将を派遣して間道や山道を巡回させ、曹操の目論見を避けるべきです。","烏巣の兵糧所は守備が薄いと感じます。すぐに精鋭、猛将を派遣して間道や山道を巡回させ、曹操の目論見を避けるべきです。","袁紹",},
        [1765] = {20185502,"袁紹","黙れ黙れ、罪人め！妄言で皆を惑わすな！","黙れ黙れ、罪人め！妄言で皆を惑わすな！","沮授",},
        [1766] = {20185502,"袁紹","今すぐその口を封じてやる！誰か！こいつを閉じ込めておけ！","今すぐその口を封じてやる！誰か！こいつを閉じ込めておけ！","沮授",},
        [1767] = {20185502,"沮授","ああ……我が軍はすでに死んだ。私の屍はどこに晒されるのでしょうな。","ああ……我が軍はすでに死んだ。私の屍はどこに晒されるのでしょうな。","袁紹",},
        [1768] = {20185503,"曹操","袁紹は敗走したぞ。勝負あったのだ。","袁紹は敗走したぞ。勝負あったのだ。","沮授",},
        [1769] = {20185503,"曹操","なのに沮授よ、どうしてお前は降らぬ?","なのに沮授よ、どうしてお前は降らぬ?","沮授",},
        [1770] = {20185503,"沮授","降伏など、いたしません！","降伏など、いたしません！","曹操",},
        [1771] = {20185503,"曹操","袁紹は無謀で、貴公の言葉を聞き入れなかった。何を迷うことがあるのだ。","袁紹は無謀で、貴公の言葉を聞き入れなかった。何を迷うことがあるのだ。","沮授",},
        [1772] = {20185503,"曹操","わしがもっと早く貴公を得ていれば、天下を心配することもなかったであろうに。","わしがもっと早く貴公を得ていれば、天下を心配することもなかったであろうに。","沮授",},
        [1773] = {20185601,"曹仁","今、漢王室に主は無い。董卓が権力を握って、君を欺き、民を害し、天下には怒りの声が満ちている。","今、漢王室に主は無い。董卓が権力を握って、君を欺き、民を害し、天下には怒りの声が満ちている。","曹洪",},
        [1774] = {20185601,"曹仁","そんな時、曹操が白旗に忠義の二文字を書き、義兵を募り始めた。","そんな時、曹操が白旗に忠義の二文字を書き、義兵を募り始めた。","曹洪",},
        [1775] = {20185601,"曹仁","曹洪よ、曹操は我らの従兄だ。曹操のもとへ馳せ参じよう！","曹洪よ、曹操は我らの従兄だ。曹操のもとへ馳せ参じよう！","曹洪",},
        [1776] = {20185601,"曹洪","無論だ、曹仁！俺もそのつもりだった。","無論だ、曹仁！俺もそのつもりだった。","曹仁",},
        [1777] = {20185602,"曹仁","以前はお主の意見を聞かずに破れ、大将二人と襄陽を失った。二の舞は避けたい。この先どうすればいい?","以前はお主の意見を聞かずに破れ、大将二人と襄陽を失った。二の舞は避けたい。この先どうすればいい?","満寵",},
        [1778] = {20185602,"満寵","関羽は勇将でありながら智謀にも長け、決して軽んじることはできません。ここは、ただ堅く守るべきかと。","関羽は勇将でありながら智謀にも長け、決して軽んじることはできません。ここは、ただ堅く守るべきかと。","曹仁",},
        [1779] = {20185602,"曹仁","では、長安に急使を出し、救援を求めよう。「関羽が襄陽を陥れ、樊城を包囲している。危急の時ゆえ、大将の派遣を求む」と。","では、長安に急使を出し、救援を求めよう。「関羽が襄陽を陥れ、樊城を包囲している。危急の時ゆえ、大将の派遣を求む」と。","満寵",},
        [1780] = {20185602,"曹仁","その間、俺たちはなんとしても城を守りきるぞ！","その間、俺たちはなんとしても城を守りきるぞ！","満寵",},
        [1781] = {20185603,"曹仁","樊城の周りの水の勢いは、ますますひどくなっている。城壁も崩れ始めた。男女を問わず土を運ばせているが間に合わん。","樊城の周りの水の勢いは、ますますひどくなっている。城壁も崩れ始めた。男女を問わず土を運ばせているが間に合わん。","満寵",},
        [1782] = {20185603,"曹仁","こうなっては仕方ない。敵が来ないうちに、夜に乗じて船で逃げたらどうだ。城は失うが、命は助かるだろう。","こうなっては仕方ない。敵が来ないうちに、夜に乗じて船で逃げたらどうだ。城は失うが、命は助かるだろう。","満寵",},
        [1783] = {20185603,"満寵","いけません。山から出た水は、長くは留まりません。十日もすれば自然に退いていくでしょう。","いけません。山から出た水は、長くは留まりません。十日もすれば自然に退いていくでしょう。","曹仁",},
        [1784] = {20185603,"満寵","関羽はまだこの城を攻めませんが、すでに配下の将を郟県に差し向けています。","関羽はまだこの城を攻めませんが、すでに配下の将を郟県に差し向けています。","曹仁",},
        [1785] = {20185603,"満寵","軽々しく攻めてこないのは、我が軍が背後を襲うのを恐れての事。今、城を捨てれば、我が国は黄河より南を失うことになります。","軽々しく攻めてこないのは、我が軍が背後を襲うのを恐れての事。今、城を捨てれば、我が国は黄河より南を失うことになります。","曹仁",},
        [1786] = {20185603,"曹仁","おお、まさしく！貴公の教えがなければ、大事を誤るところだった。","おお、まさしく！貴公の教えがなければ、大事を誤るところだった。","満寵",},
        [1787] = {20185603,"曹仁","俺は魏王の命を受け、この城を守っているのだ。捨てるなどと言うヤツは斬って捨てるぞ！","俺は魏王の命を受け、この城を守っているのだ。捨てるなどと言うヤツは斬って捨てるぞ！","満寵",},
        [1788] = {20185603,"曹仁","樊城は絶対死守だ！城壁に弩弓をありったけ並べ、昼夜を通して警戒を怠るな！","樊城は絶対死守だ！城壁に弩弓をありったけ並べ、昼夜を通して警戒を怠るな！","満寵",},
        [1789] = {20185604,"曹仁","関羽は俺の矢を受け、徐晃に敗れて麦城に籠城した。","関羽は俺の矢を受け、徐晃に敗れて麦城に籠城した。","",},
        [1790] = {20185604,"曹仁","惜しむらくは、ヤツを生け捕りにできなかったことだ！","惜しむらくは、ヤツを生け捕りにできなかったことだ！","",},
        [1791] = {20185604,"曹仁","呉の呂蒙ごときに手柄を攫われるとは、本当に残念だ。","呉の呂蒙ごときに手柄を攫われるとは、本当に残念だ。","",},
        [1792] = {20185701,"呉国太","男も女も大きくなれば結婚するのが古今の習わしです。しかし、わたしは母親なのですから、娘はわたしのものです。","男も女も大きくなれば結婚するのが古今の習わしです。しかし、わたしは母親なのですから、娘はわたしのものです。","孫権",},
        [1793] = {20185701,"呉国太","劉備を招いて婿にすることを、なぜわたしに申さなかったのです?","劉備を招いて婿にすることを、なぜわたしに申さなかったのです?","孫権",},
        [1794] = {20185701,"孫権","どこでその話を?","どこでその話を?","呉国太",},
        [1795] = {20185701,"呉国太","やはり、知られたくないことなのですね。ならば、なぜしたのです。","やはり、知られたくないことなのですね。ならば、なぜしたのです。","孫権",},
        [1796] = {20185701,"呉国太","城内の者はみな知っていますよ。わたしを騙そうというのですか！","城内の者はみな知っていますよ。わたしを騙そうというのですか！","孫権",},
        [1797] = {20185701,"孫権","落ち着いてください。これは周瑜の計略なのです。","落ち着いてください。これは周瑜の計略なのです。","呉国太",},
        [1798] = {20185701,"孫権","荊州を取り戻すため、婚礼の名目で劉備を招いて捕らえ、荊州と交換しようというのです。","荊州を取り戻すため、婚礼の名目で劉備を招いて捕らえ、荊州と交換しようというのです。","呉国太",},
        [1799] = {20185701,"孫権","従わねば、劉備を斬ります。本当の縁組みではありません。","従わねば、劉備を斬ります。本当の縁組みではありません。","呉国太",},
        [1800] = {20185701,"呉国太","周瑜は六郡八十一州の大都督でありながら、わたしの娘を美人の計に使おうというのですか！","周瑜は六郡八十一州の大都督でありながら、わたしの娘を美人の計に使おうというのですか！","孫権",},
        [1801] = {20185701,"呉国太","劉備を殺せば、わたしの娘は未亡人です。どうやって再婚させるのです?娘の一生を台無しにするつもりですか！なんと浅はかな！","劉備を殺せば、わたしの娘は未亡人です。どうやって再婚させるのです?娘の一生を台無しにするつもりですか！なんと浅はかな！","孫権",},
        [1802] = {20185701,"呉国太","たとえこの計略で荊州を得ても、天下の笑い者になりましょう。許されるはずがない！","たとえこの計略で荊州を得ても、天下の笑い者になりましょう。許されるはずがない！","孫権",},
        [1803] = {20185701,"呉国太","いいでしょう。明日、甘露寺で会う約束があるなら、同行します。","いいでしょう。明日、甘露寺で会う約束があるなら、同行します。","孫権",},
        [1804] = {20185701,"呉国太","わたしの意に叶わなければ、お前たちの好きになさい。しかし、意に叶う相手なら、わたしが娘を嫁がせます。","わたしの意に叶わなければ、お前たちの好きになさい。しかし、意に叶う相手なら、わたしが娘を嫁がせます。","孫権",},
        [1805] = {20185702,"孫権","母上は頑として譲らず、本当に妹を劉備に嫁がせてしまった。まさか嘘が真実になるとはな……","母上は頑として譲らず、本当に妹を劉備に嫁がせてしまった。まさか嘘が真実になるとはな……","周瑜",},
        [1806] = {20185702,"周瑜","僕も同じ気持ちです。計略が、まさかこんな形でひっくり返されるとは……","僕も同じ気持ちです。計略が、まさかこんな形でひっくり返されるとは……","孫権",},
        [1807] = {20185702,"周瑜","しかし、すでに嘘が真実になったなら、それを基に計略を用いるしかありません。","しかし、すでに嘘が真実になったなら、それを基に計略を用いるしかありません。","孫権",},
        [1808] = {20185702,"周瑜","劉備は梟雄の性質で、関羽、張飛、趙雲といった将軍を従え、さらに諸葛亮の入れ知恵もあれば、長く人の下に従っていることはないしょう。","劉備は梟雄の性質で、関羽、張飛、趙雲といった将軍を従え、さらに諸葛亮の入れ知恵もあれば、長く人の下に従っていることはないしょう。","孫権",},
        [1809] = {20185702,"周瑜","僕の考えでは、まずヤツを呉で軟禁状態に置き、豪華な館に多くの美女を侍らせて、その志を挫きます。","僕の考えでは、まずヤツを呉で軟禁状態に置き、豪華な館に多くの美女を侍らせて、その志を挫きます。","孫権",},
        [1810] = {20185702,"周瑜","そうして関羽、張飛との情、諸葛亮との契りから遠ざけ、その後、兵をもって攻め入れば目的は達せられましょう。","そうして関羽、張飛との情、諸葛亮との契りから遠ざけ、その後、兵をもって攻め入れば目的は達せられましょう。","孫権",},
        [1811] = {20185702,"周瑜","今、ヤツを自由にするのは、蛟龍が雨雲を得るようなもの。天に昇る前に、殿、ぜひご一考を。","今、ヤツを自由にするのは、蛟龍が雨雲を得るようなもの。天に昇る前に、殿、ぜひご一考を。","孫権",},
        [1812] = {20185702,"孫権","周瑜の計略は、俺の考えとも一致する。劉備は貧しい暮らしから這い上がり、天下を駆ける男。豪勢な暮らしには慣れていまい。","周瑜の計略は、俺の考えとも一致する。劉備は貧しい暮らしから這い上がり、天下を駆ける男。豪勢な暮らしには慣れていまい。","周瑜",},
        [1813] = {20185702,"孫権","豪華な館と金、絹を与えて楽しませれば、自然と部下との仲も疎遠になるだろう。","豪華な館と金、絹を与えて楽しませれば、自然と部下との仲も疎遠になるだろう。","周瑜",},
        [1814] = {20185702,"孫権","そうしてヤツらが反目し合うようになれば、荊州を取り戻すことは造作もない。","そうしてヤツらが反目し合うようになれば、荊州を取り戻すことは造作もない。","周瑜",},
        [1815] = {20185703,"孫尚香","旦那さま、何を悩んでいるの?","旦那さま、何を悩んでいるの?","劉備",},
        [1816] = {20185703,"劉備","私はなんという不孝者だろう。この身一つで異郷をさすらい、生きて両親に仕えることもできず、また祖先を祭ることもできないとは。","私はなんという不孝者だろう。この身一つで異郷をさすらい、生きて両親に仕えることもできず、また祖先を祭ることもできないとは。","孫尚香",},
        [1817] = {20185703,"劉備","新年が近づき、そのことを思い出してしまってな。悲しくてならないのだよ。","新年が近づき、そのことを思い出してしまってな。悲しくてならないのだよ。","孫尚香",},
        [1818] = {20185703,"孫尚香","ウソおっしゃい。あたし知ってるわよ！本当はさっき来た趙雲に荊州が危険だと言われたから、帰りたいと思ってるんでしょう。","ウソおっしゃい。あたし知ってるわよ！本当はさっき来た趙雲に荊州が危険だと言われたから、帰りたいと思ってるんでしょう。","劉備",},
        [1819] = {20185703,"劉備","お見通しというわけか。ならば隠し立てはするまい。ここで私が行かずに荊州を失えば、天下の笑い者だろう。","お見通しというわけか。ならば隠し立てはするまい。ここで私が行かずに荊州を失えば、天下の笑い者だろう。","孫尚香",},
        [1820] = {20185703,"劉備","だが、お前を捨てても置けない。だから悩んでいるのだ。","だが、お前を捨てても置けない。だから悩んでいるのだ。","孫尚香",},
        [1821] = {20185703,"孫尚香","悩むことなんてないわ。あたしも一緒に行く。もうあなたに付いていくと決めたのよ。","悩むことなんてないわ。あたしも一緒に行く。もうあなたに付いていくと決めたのよ。","劉備",},
        [1822] = {20185703,"劉備","お前がそう思ってくれても、呉国太さまと孫権殿が許しはしまい。もしこの劉備を哀れに思うなら、しばしの別れを承知してくれ。","お前がそう思ってくれても、呉国太さまと孫権殿が許しはしまい。もしこの劉備を哀れに思うなら、しばしの別れを承知してくれ。","孫尚香",},
        [1823] = {20185703,"孫尚香","安心して、旦那さま。あたしがお母様に頼みこめば、きっと行かせてくれるわ。","安心して、旦那さま。あたしがお母様に頼みこめば、きっと行かせてくれるわ。","劉備",},
        [1824] = {20185703,"劉備","呉国太さまが認めても、孫権殿が認めまい。","呉国太さまが認めても、孫権殿が認めまい。","孫尚香",},
        [1825] = {20185703,"孫尚香","だったら、こういうのはどう?あたしとあなたで正月の拝礼をする時に、川岸で祖先を祭ると言って、そのまま出発しちゃうの。","だったら、こういうのはどう?あたしとあなたで正月の拝礼をする時に、川岸で祖先を祭ると言って、そのまま出発しちゃうの。","劉備",},
        [1826] = {20185703,"劉備","そうしてくれるなら、生涯この恩は忘れぬ！だが、計画が漏れぬよう気を付けてな。","そうしてくれるなら、生涯この恩は忘れぬ！だが、計画が漏れぬよう気を付けてな。","孫尚香",},
        [1827] = {20185704,"趙雲","奥方、なぜそんなに急いで呉に帰られるのです?","奥方、なぜそんなに急いで呉に帰られるのです?","孫尚香",},
        [1828] = {20185704,"孫尚香","家から手紙が来て、お母様が重病だからお見舞いに行くのよ。","家から手紙が来て、お母様が重病だからお見舞いに行くのよ。","趙雲",},
        [1829] = {20185704,"趙雲","そうですか。ではなぜ阿斗さままで連れていかれるのです?","そうですか。ではなぜ阿斗さままで連れていかれるのです?","孫尚香",},
        [1830] = {20185704,"張飛","簡単な話じゃねえか、姐御。阿斗様を置いて一人で呉に見舞いに行ってくれりゃいいんだ。","簡単な話じゃねえか、姐御。阿斗様を置いて一人で呉に見舞いに行ってくれりゃいいんだ。","孫尚香",},
        [1831] = {20185801,"劉宏","冀州鉅鹿郡の張角が蜂起し、天下八州がこれに呼応して、世の中が揺れている。","冀州鉅鹿郡の張角が蜂起し、天下八州がこれに呼応して、世の中が揺れている。","盧植",},
        [1832] = {20185801,"劉宏","聞けば盧植、お主は文武を兼ね備えているとのこと。特別に北中郎将に任命する。","聞けば盧植、お主は文武を兼ね備えているとのこと。特別に北中郎将に任命する。","盧植",},
        [1833] = {20185801,"劉宏","北軍五校の将兵を率い、冀州に赴き黄巾軍を平定せよ。","北軍五校の将兵を率い、冀州に赴き黄巾軍を平定せよ。","盧植",},
        [1834] = {20185801,"盧植","承りました。盧植、ただちに軍を出発させ申す！","承りました。盧植、ただちに軍を出発させ申す！","劉宏",},
        [1835] = {20185802,"何進","董卓の上奏では、天下の乱れが収まらんのは、黄門の常侍・張譲らがお上を侮っているからだという。","董卓の上奏では、天下の乱れが収まらんのは、黄門の常侍・張譲らがお上を侮っているからだという。","盧植",},
        [1836] = {20185802,"何進","そこで董卓が軍を率いて洛陽に入り、張譲たちを除いてくれるという。まさに渡りに船！国の幸い！天下の幸いよ！","そこで董卓が軍を率いて洛陽に入り、張譲たちを除いてくれるという。まさに渡りに船！国の幸い！天下の幸いよ！","盧植",},
        [1837] = {20185802,"盧植","いや、董卓の人となりはよく知っています。ヤツは善人面をしていますが、心は残忍。ひとたび禁裏に入れれば、必ず禍を起こしましょう。","いや、董卓の人となりはよく知っています。ヤツは善人面をしていますが、心は残忍。ひとたび禁裏に入れれば、必ず禍を起こしましょう。","何進",},
        [1838] = {20185802,"盧植","止めるのです。上京させてはなりません。これ以上天下を乱さぬために。","止めるのです。上京させてはなりません。これ以上天下を乱さぬために。","何進",},
        [1839] = {20185802,"何進","お前は疑い深すぎる。天下の大事を相談するには向いとらんな。","お前は疑い深すぎる。天下の大事を相談するには向いとらんな。","盧植",},
        [1840] = {20185901,"孫策","山賊が宣城に夜襲をかけてきたので、周泰が急いで孫権を馬に載せ、身を挺して守ってくれたのだ。","山賊が宣城に夜襲をかけてきたので、周泰が急いで孫権を馬に載せ、身を挺して守ってくれたのだ。","華佗",},
        [1841] = {20185901,"孫策","必死に抵抗して血路を開いたが、全身に十二ヶ所も深い傷を負った。傷が腫れあがって明日の命も知れん。","必死に抵抗して血路を開いたが、全身に十二ヶ所も深い傷を負った。傷が腫れあがって明日の命も知れん。","華佗",},
        [1842] = {20185901,"孫策","華佗よ、どうか助けてやってくれ。","華佗よ、どうか助けてやってくれ。","華佗",},
        [1843] = {20185901,"華佗","医者たるもの、医術によって人を救うのが務めです。見せてください。","医者たるもの、医術によって人を救うのが務めです。見せてください。","孫策",},
        [1844] = {20185901,"華佗","ああ、これなら簡単です。","ああ、これなら簡単です。","孫策",},
        [1845] = {20185902,"孫権","周泰は勇敢に戦って曹操を退けた。その功をもって、濡須督、平虜将軍に任命する。","周泰は勇敢に戦って曹操を退けた。その功をもって、濡須督、平虜将軍に任命する。","周泰",},
        [1846] = {20185902,"孫権","祝の印だ。この孫権より貴公に一杯振る舞おう。","祝の印だ。この孫権より貴公に一杯振る舞おう。","周泰",},
        [1847] = {20185902,"周泰","はっ、ありがたき幸せ。","はっ、ありがたき幸せ。","孫権",},
        [1848] = {20185902,"孫権","それを飲んだら、服を脱いでもらえるか。","それを飲んだら、服を脱いでもらえるか。","周泰",},
        [1849] = {20185902,"周泰","えっ、服を脱ぐ?","えっ、服を脱ぐ?","孫権",},
        [1850] = {20185902,"孫権","軍中に周泰に対する不満があると聞いた。俺は今日、皆に知らしめたい。","軍中に周泰に対する不満があると聞いた。俺は今日、皆に知らしめたい。","周泰",},
        [1851] = {20185902,"孫権","周泰の体にある数十もの刀傷は、どうしてついたものかと！","周泰の体にある数十もの刀傷は、どうしてついたものかと！","周泰",},
        [1852] = {20185902,"孫権","俺はこやつを肉親同様だと思っている。まさに兵馬を任せるに値する男よ！","俺はこやつを肉親同様だと思っている。まさに兵馬を任せるに値する男よ！","周泰",},
        [1853] = {20186001,"曹操","今日は大宴会だ。山海の珍味はほとんど揃っているぞ。","今日は大宴会だ。山海の珍味はほとんど揃っているぞ。","左慈",},
        [1854] = {20186001,"曹操","足りないのは、呉の松江で獲れる鱸魚くらいだな。","足りないのは、呉の松江で獲れる鱸魚くらいだな。","左慈",},
        [1855] = {20186001,"左慈","そんなものは簡単に手に入るぞ。","そんなものは簡単に手に入るぞ。","曹操",},
        [1856] = {20186001,"左慈","銅のたらいに水を張ってくれ。この竹竿にエサを付けて、そこから釣り上げてやろう。(しばらくして、本当に鱸魚が釣れた)","銅のたらいに水を張ってくれ。この竹竿にエサを付けて、そこから釣り上げてやろう。(しばらくして、本当に鱸魚が釣れた)","曹操",},
        [1857] = {20186001,"曹操","素晴らしい！だが、一匹では宴会の客たちに振る舞うには足りん。もう一匹釣れるか。","素晴らしい！だが、一匹では宴会の客たちに振る舞うには足りん。もう一匹釣れるか。","左慈",},
        [1858] = {20186001,"左慈","お安い御用だ。(しばらく糸を垂らすと、また一匹釣り上げた)","お安い御用だ。(しばらく糸を垂らすと、また一匹釣り上げた)","曹操",},
        [1859] = {20186001,"曹操","ううむ、左慈の道術にはこのわしも唸らされるわ。","ううむ、左慈の道術にはこのわしも唸らされるわ。","左慈",},
        [1860] = {20186002,"左慈","貴軍をねぎらってやろうと、ささやかな贈り物を持ってきたぞ。","貴軍をねぎらってやろうと、ささやかな贈り物を持ってきたぞ。","劉表",},
        [1861] = {20186002,"劉表","お前のような道士一人で、我が軍一万と客人千人をどうやってねぎらう?","お前のような道士一人で、我が軍一万と客人千人をどうやってねぎらう?","左慈",},
        [1862] = {20186002,"左慈","ここに酒が一斗、干し肉が一束ある。","ここに酒が一斗、干し肉が一束ある。","劉表",},
        [1863] = {20186002,"劉表","その程度で、どうしたら足りるというのだ?","その程度で、どうしたら足りるというのだ?","左慈",},
        [1864] = {20186002,"左慈","兵一人あたり酒三杯と干し肉一切れをお配りしよう。(一人ずつ酒と干し肉を配っていく)","兵一人あたり酒三杯と干し肉一切れをお配りしよう。(一人ずつ酒と干し肉を配っていく)","劉表",},
        [1865] = {20186002,"劉表","馬鹿な……！酒は少しも減らないし、干し肉もなくならない。どうやったのだ?","馬鹿な……！酒は少しも減らないし、干し肉もなくならない。どうやったのだ?","左慈",},
        [1866] = {20186002,"左慈","秘密だよ、はっはっは……","秘密だよ、はっはっは……","劉表",},
        [1867] = {20186003,"孫策","秘密だよ、はっはっは……","道士左慈、また妖術で人々をだましているな！","左慈",},
        [1868] = {20186003,"孫策","俺は于吉だって斬れるのだ。お前も斬ってやる！","俺は于吉だって斬れるのだ。お前も斬ってやる！","左慈",},
        [1869] = {20186003,"左慈","はっはっは、どうした?私はゆっくり歩いているぞ。","はっはっは、どうした?私はゆっくり歩いているぞ。","孫策",},
        [1870] = {20186003,"左慈","斬りたければ斬るがいい。私に追いつけるものならな。","斬りたければ斬るがいい。私に追いつけるものならな。","孫策",},
        [1871] = {20186003,"孫策","おのれ……っ！これも妖術か！全力で走っても追いつけん！","おのれ……っ！これも妖術か！全力で走っても追いつけん！","左慈",},
        [1872] = {20186101,"袁紹","そろそろ後継者を決めねばならんが、長男の袁譚は剛情で殺伐としている。次男の袁熙は軟弱で大成しないだろう。","そろそろ後継者を決めねばならんが、長男の袁譚は剛情で殺伐としている。次男の袁熙は軟弱で大成しないだろう。","審配",},
        [1873] = {20186101,"袁紹","末っ子の袁尚が美男子でふさわしいと思うが、どうするべきか?","末っ子の袁尚が美男子でふさわしいと思うが、どうするべきか?","審配",},
        [1874] = {20186101,"審配","では袁譚さまを、殿の亡き兄上の養子に出されてはいかがでしょう?それなら長幼の序を乱したと言う者もおりますまい。","では袁譚さまを、殿の亡き兄上の養子に出されてはいかがでしょう?それなら長幼の序を乱したと言う者もおりますまい。","袁紹",},
        [1875] = {20186101,"袁紹","よい判断だ。袁譚を都督に封じ、青州を守らせよう。","よい判断だ。袁譚を都督に封じ、青州を守らせよう。","審配",},
        [1876] = {20186102,"袁譚","俺こそが袁家の長男で、正統な後継者だ。お前ら、父上に何を吹き込んだ?","俺こそが袁家の長男で、正統な後継者だ。お前ら、父上に何を吹き込んだ?","審配",},
        [1877] = {20186102,"審配","将軍は亡くなられる前に、袁尚さまこそ大業を継ぐにふさわしいと申されました。","将軍は亡くなられる前に、袁尚さまこそ大業を継ぐにふさわしいと申されました。","袁譚",},
        [1878] = {20186102,"袁譚","ふん！嘘も休み休み言え。明らかにお前が父上をそそのかしたのだろう！","ふん！嘘も休み休み言え。明らかにお前が父上をそそのかしたのだろう！","審配",},
        [1879] = {20186102,"審配","あなたは将軍に嫌われていたのです。私のせいではない。","あなたは将軍に嫌われていたのです。私のせいではない。","袁譚",},
        [1880] = {20186102,"袁譚","そんなはずがあるものか！","そんなはずがあるものか！","審配",},
        [1881] = {20186103,"袁譚","前回曹操に敗れたのは、我が軍の装備が悪かったせいだ。今ヤツらは戦う気をなくし、撤退している。","前回曹操に敗れたのは、我が軍の装備が悪かったせいだ。今ヤツらは戦う気をなくし、撤退している。","袁尚",},
        [1882] = {20186103,"袁譚","今のうちに追撃して叩くべきだ。そのために、装備と兵力を増強せねばならん。","今のうちに追撃して叩くべきだ。そのために、装備と兵力を増強せねばならん。","袁尚",},
        [1883] = {20186103,"袁尚","残念ですが、これ以上は無理です。こちらも余力がないので……","残念ですが、これ以上は無理です。こちらも余力がないので……","袁譚",},
        [1884] = {20186103,"袁譚","ふざけるな！あまり調子に乗っていると、曹操と手を組んで、お前を討ってやるからな！","ふざけるな！あまり調子に乗っていると、曹操と手を組んで、お前を討ってやるからな！","袁尚",},
        [1885] = {20186201,"曹植","軽やかなること飛び立つコウノトリのごとく、優美なること天に昇る龍のごとし。","軽やかなること飛び立つコウノトリのごとく、優美なること天に昇る龍のごとし。","張譲",},
        [1886] = {20186201,"曹植","秋の菊のようにきらびやかで、春の松のように華々しい。","秋の菊のようにきらびやかで、春の松のように華々しい。","張譲",},
        [1887] = {20186201,"曹植","おぼろげなること雲のかかる月のごとく、揺らめくこと風に舞う雪のごとし。","おぼろげなること雲のかかる月のごとく、揺らめくこと風に舞う雪のごとし。","曹操",},
        [1888] = {20186201,"甄姫","あら、曹植さま。それって、もしかしてわたしのことかしら?","あら、曹植さま。それって、もしかしてわたしのことかしら?","張譲",},
        [1889] = {20186202,"曹丕","脅えることはない。私は丞相・曹操の息子だ。あなたと家族は必ず保護してやろう。","脅えることはない。私は丞相・曹操の息子だ。あなたと家族は必ず保護してやろう。","甄姫",},
        [1890] = {20186202,"甄姫","ありがとうございます……若君さまのお傍に置いていただきたく存じます。","ありがとうございます……若君さまのお傍に置いていただきたく存じます。","曹丕",},
        [1891] = {20186203,"甄姫","人々の噂が黄金のように固かった絆をも溶かし、今生の別れを迎えることになりました。","人々の噂が黄金のように固かった絆をも溶かし、今生の別れを迎えることになりました。","",},
        [1892] = {20186203,"甄姫","あなたが私の元を去った時のことを思い、ただ一人嘆くばかりです。","あなたが私の元を去った時のことを思い、ただ一人嘆くばかりです。","",},
        [1893] = {20186203,"甄姫","愛し合うことと、分かり合うことは違うのですね。たとえ来世があったとしても、もう二度と会いたくありません。","愛し合うことと、分かり合うことは違うのですね。たとえ来世があったとしても、もう二度と会いたくありません。","",},
        [1894] = {20186301,"諸葛瑾","このたび、劉備殿は蜀の地を得られたそうで、心よりお祝い申し上げる。","このたび、劉備殿は蜀の地を得られたそうで、心よりお祝い申し上げる。","諸葛亮",},
        [1895] = {20186301,"諸葛亮","これはこれは、ご丁寧に。","これはこれは、ご丁寧に。","諸葛瑾",},
        [1896] = {20186301,"諸葛瑾","さてそこで、わが呉が貸している荊州は、いつ返していただけるのかな?","さてそこで、わが呉が貸している荊州は、いつ返していただけるのかな?","諸葛亮",},
        [1897] = {20186301,"諸葛亮","はいはい、それについてはいずれ……","はいはい、それについてはいずれ……","諸葛瑾",},
        [1898] = {20186302,"孫権","このロバの首にはどうして「諸葛瑾」と書かれた札が掛けられてるのだ?このロバはお前なのか?諸葛瑾。","このロバの首にはどうして「諸葛瑾」と書かれた札が掛けられてるのだ?このロバはお前なのか?諸葛瑾。","諸葛恪",},
        [1899] = {20186302,"諸葛恪","あ～、いくつか文字が足りないようですねえ。書き加えさせてください。","あ～、いくつか文字が足りないようですねえ。書き加えさせてください。","孫権",},
        [1900] = {20186302,"孫権","ん?何を書いた?「諸葛瑾……のロバ」?なるほどな、よいぞ！上手いものよ。","ん?何を書いた?「諸葛瑾……のロバ」?なるほどな、よいぞ！上手いものよ。","諸葛恪",},
        [1901] = {20186302,"諸葛恪","今からこのロバは我が家の物になりました！連れて帰りますね！","今からこのロバは我が家の物になりました！連れて帰りますね！","孫権",},
        [1902] = {20186303,"孫権","俺は諸葛瑾を大将軍、左都護、予州牧に封じる。どうだ?","俺は諸葛瑾を大将軍、左都護、予州牧に封じる。どうだ?","諸葛瑾",},
        [1903] = {20186303,"諸葛瑾","身に余る光栄です。しかし予州はいま魏の国の領土で、我らの手にはありませんが?","身に余る光栄です。しかし予州はいま魏の国の領土で、我らの手にはありませんが?","孫権",},
        [1904] = {20186303,"孫権","だからこそよ。よく働き、この地を手に入れた暁にはお前に与えようというのだ！","だからこそよ。よく働き、この地を手に入れた暁にはお前に与えようというのだ！","諸葛瑾",},
        [1905] = {20186401,"黄承彦","我が娘ながら、月英は花のように美しいだけでなく、聡明で賢い。幸運にも娶ることができるのはどこの若者だろうな。","我が娘ながら、月英は花のように美しいだけでなく、聡明で賢い。幸運にも娶ることができるのはどこの若者だろうな。","黄月英",},
        [1906] = {20186401,"黄月英","諸葛孔明さまは学識があって人柄も良く、素敵な方だそうです。お父さま、ぜひ縁談を申し込んでくださいな。","諸葛孔明さまは学識があって人柄も良く、素敵な方だそうです。お父さま、ぜひ縁談を申し込んでくださいな。","黄承彦",},
        [1907] = {20186401,"黄月英","でも、わたしは色黒で、髪も黄みがかった醜い女だと言ってください。その時の反応を見れば、彼の人柄が窺い知れますから。","でも、わたしは色黒で、髪も黄みがかった醜い女だと言ってください。その時の反応を見れば、彼の人柄が窺い知れますから。","黄承彦",},
        [1908] = {20186401,"黄承彦","うむ、よい考えだ。孔明がもし美人を欲しがる男なら、この縁談はなしにしよう。","うむ、よい考えだ。孔明がもし美人を欲しがる男なら、この縁談はなしにしよう。","黄月英",},
        [1909] = {20186402,"諸葛亮","月英が発明したからくり仕掛けの犬や虎、木偶人形は本当に不思議な道具だね。感服したよ。","月英が発明したからくり仕掛けの犬や虎、木偶人形は本当に不思議な道具だね。感服したよ。","黄月英",},
        [1910] = {20186402,"黄月英","ではひとつ宿題です。わたしたちが婚礼を挙げる時、かごには乗らず、馬にも乗らず、船にも乗りません。その方法はありますか?","ではひとつ宿題です。わたしたちが婚礼を挙げる時、かごには乗らず、馬にも乗らず、船にも乗りません。その方法はありますか?","諸葛亮",},
        [1911] = {20186402,"諸葛亮","ふむ……歯ごたえのある宿題だね。少し考えさせておくれ。","ふむ……歯ごたえのある宿題だね。少し考えさせておくれ。","黄月英",},
        [1912] = {20186403,"諸葛亮","月英が出してくれた難題のおかげで、木牛流馬が発明できた。軍の輸送問題が片付いたよ。","月英が出してくれた難題のおかげで、木牛流馬が発明できた。軍の輸送問題が片付いたよ。","黄月英",},
        [1913] = {20186403,"諸葛亮","まさか、これを見越していたのかい?","まさか、これを見越していたのかい?","黄月英",},
        [1914] = {20186403,"黄月英","考えすぎですよ。でもお力になれて、わたしも嬉しいです。","考えすぎですよ。でもお力になれて、わたしも嬉しいです。","諸葛亮",},
        [1915] = {20186403,"黄月英","この羽扇をお贈りします。戦や政で、きっとお役に立ちますよ。","この羽扇をお贈りします。戦や政で、きっとお役に立ちますよ。","諸葛亮",},
        [1916] = {20186501,"潘璋","前方から張遼が突撃してくる。お前ら、敵前逃亡なんかしたら、ただじゃおかねえぞ！","前方から張遼が突撃してくる。お前ら、敵前逃亡なんかしたら、ただじゃおかねえぞ！","",},
        [1917] = {20186501,"潘璋","あそこに転がってる死体は、二人とも脱走兵だ。オレが殺した。ああなりたくなけりゃ、オレと一緒に突撃しろ！","あそこに転がってる死体は、二人とも脱走兵だ。オレが殺した。ああなりたくなけりゃ、オレと一緒に突撃しろ！","",},
        [1918] = {20186501,"孫権","ははは…潘璋の勇猛な戦いをもって、偏将軍に封じ、半州に駐屯させよう。","ははは…潘璋の勇猛な戦いをもって、偏将軍に封じ、半州に駐屯させよう。","潘璋",},
        [1919] = {20186502,"関羽","何者だ?まさかその程度の兵力で、わしの退路を断つつもりか?死にたくなくば道を開けよ。","何者だ?まさかその程度の兵力で、わしの退路を断つつもりか?死にたくなくば道を開けよ。","潘璋",},
        [1920] = {20186502,"潘璋","やはり自惚れ野郎だな、関羽よ。あんたは呉の実力を甘く見ている。","やはり自惚れ野郎だな、関羽よ。あんたは呉の実力を甘く見ている。","関羽",},
        [1921] = {20186502,"関羽","このわしも舐められたものよ。前の戦で兵力を減らしていなければ、こんな場所でお前と話すことなどなかった。","このわしも舐められたものよ。前の戦で兵力を減らしていなければ、こんな場所でお前と話すことなどなかった。","潘璋",},
        [1922] = {20186502,"潘璋","話は終わりだ。我らの罠に落ちろ！","話は終わりだ。我らの罠に落ちろ！","関羽",},
        [1923] = {20186601,"孫策","国を築くことにおいて、お前は俺におよばないが、大業を受け継ぎ守ることにおいては、俺のほうがおよばない。","国を築くことにおいて、お前は俺におよばないが、大業を受け継ぎ守ることにおいては、俺のほうがおよばない。","孫権",},
        [1924] = {20186601,"孫権","兄上がいれば、大業の達成になんの憂いがありましょう！俺が必ず兄上の力になります。","兄上がいれば、大業の達成になんの憂いがありましょう！俺が必ず兄上の力になります。","孫策",},
        [1925] = {20186601,"孫策","お前は小さいころから才知に長けていた。これから、呉の諸将はすべてお前の配下だ。","お前は小さいころから才知に長けていた。これから、呉の諸将はすべてお前の配下だ。","孫権",},
        [1926] = {20186602,"張昭","我が主は「孫と劉が力を合わせ曹に抗う」という先生の提案に全面的に同意し、非常に称賛しておりました。","我が主は「孫と劉が力を合わせ曹に抗う」という先生の提案に全面的に同意し、非常に称賛しておりました。","諸葛亮",},
        [1927] = {20186602,"張昭","どうでしょうな、このまま江東に留まられ、我らと共に大業を成すというのは?","どうでしょうな、このまま江東に留まられ、我らと共に大業を成すというのは?","諸葛亮",},
        [1928] = {20186602,"諸葛亮","孫将軍は優れた君主ですが、私の知恵を使うことはできても、私のすべてを活かすことはできないでしょう。","孫将軍は優れた君主ですが、私の知恵を使うことはできても、私のすべてを活かすことはできないでしょう。","張昭",},
        [1929] = {20186602,"諸葛亮","従って留まることはできません。","従って留まることはできません。","張昭",},
        [1930] = {20186603,"諸葛亮","貴国の軍事力が曹操軍に対抗できるなら、早く断交されるのがいいでしょう。できないのであれば、潔く降伏した方がよろしい。","貴国の軍事力が曹操軍に対抗できるなら、早く断交されるのがいいでしょう。できないのであれば、潔く降伏した方がよろしい。","孫権",},
        [1931] = {20186603,"孫権","そう言うのなら、劉備はどうして曹操に降伏しないのだ?","そう言うのなら、劉備はどうして曹操に降伏しないのだ?","諸葛亮",},
        [1932] = {20186603,"諸葛亮","我が主劉備は漢王室の末裔。その人柄に感服して我らの旗の下に付いた者は数え切れぬほどおります。どうして降伏できましょうか?","我が主劉備は漢王室の末裔。その人柄に感服して我らの旗の下に付いた者は数え切れぬほどおります。どうして降伏できましょうか?","孫権",},
        [1933] = {20186603,"孫権","俺にも江東の広大な領土と十万の精鋭がある。他人に屈することなどできるはずがあるまい。やってやるまでだ！","俺にも江東の広大な領土と十万の精鋭がある。他人に屈することなどできるはずがあるまい。やってやるまでだ！","諸葛亮",},
        [1934] = {20186701,"文醜","俺たちはいつも二人一緒に名を出されるが、果たしてどちらが強いだろうな。","俺たちはいつも二人一緒に名を出されるが、果たしてどちらが強いだろうな。","顔良",},
        [1935] = {20186701,"顔良","それならば、私は徐晃を打ち負かしたことがあるぞ！","それならば、私は徐晃を打ち負かしたことがあるぞ！","文醜",},
        [1936] = {20186701,"文醜","俺は張遼を弓で射落としてやった！それに趙雲のヤツと引き分けたこともある！","俺は張遼を弓で射落としてやった！それに趙雲のヤツと引き分けたこともある！","顔良",},
        [1937] = {20186701,"顔良","ううむ、お互い功を張り合っても仕方ない。私たち兄弟で一勝負してみるか！","ううむ、お互い功を張り合っても仕方ない。私たち兄弟で一勝負してみるか！","文醜",},
        [1938] = {20186702,"沮授","殿、顔良は勇猛でよく戦いますが、短気なので単独で作戦に向かわせるのは悪手です。","殿、顔良は勇猛でよく戦いますが、短気なので単独で作戦に向かわせるのは悪手です。","袁紹",},
        [1939] = {20186702,"袁紹","お前たちが顔良の何を知っている?あやつはわしの一番の大将だ。お前たちのような者の考えがおよぶところではないわ。","お前たちが顔良の何を知っている?あやつはわしの一番の大将だ。お前たちのような者の考えがおよぶところではないわ。","沮授",},
        [1940] = {20186703,"劉備","私の弟、関羽は、身の丈九尺五寸、ひげの長さ一尺八寸、緑の戦袍を着て、黄驃馬に乗り、青龍偃月刀を使う。","私の弟、関羽は、身の丈九尺五寸、ひげの長さ一尺八寸、緑の戦袍を着て、黄驃馬に乗り、青龍偃月刀を使う。","顔良",},
        [1941] = {20186703,"劉備","もし会ったら、すぐ戻って来るように伝えて欲しい。","もし会ったら、すぐ戻って来るように伝えて欲しい。","顔良",},
        [1942] = {20186703,"顔良","相わかった！ついでに、会った時には手合わせしてみよう。","相わかった！ついでに、会った時には手合わせしてみよう。","劉備",},
        [1943] = {20186801,"趙昂","馬超が攻勢を強めてきた。城内の食糧も尽きかけ、事態は差し迫っている。刺史の韋康様は降伏するおつもりのようだ。","馬超が攻勢を強めてきた。城内の食糧も尽きかけ、事態は差し迫っている。刺史の韋康様は降伏するおつもりのようだ。","王異",},
        [1944] = {20186801,"趙昂","私はやめるようお諫めしたが、聞きいれてもらえなかった。","私はやめるようお諫めしたが、聞きいれてもらえなかった。","王異",},
        [1945] = {20186801,"王異","中原からの救援が間に合わないと決まったわけではありません。わたしたちは将兵を励まし、心を一つにして国に報いるべきです。","中原からの救援が間に合わないと決まったわけではありません。わたしたちは将兵を励まし、心を一つにして国に報いるべきです。","趙昂",},
        [1946] = {20186801,"王異","決して逆賊に従ってはなりません。","決して逆賊に従ってはなりません。","趙昂",},
        [1947] = {20186802,"趙昂","馬超は我らの土地を占領したうえ、降伏した韋康様を殺した。この仇は必ず打たねば！","馬超は我らの土地を占領したうえ、降伏した韋康様を殺した。この仇は必ず打たねば！","王異",},
        [1948] = {20186802,"趙昂","だが今、わが子趙月が奴の人質となっている。こちらが兵を興せば当然殺されるだろう。一体どうすれば?","だが今、わが子趙月が奴の人質となっている。こちらが兵を興せば当然殺されるだろう。一体どうすれば?","王異",},
        [1949] = {20186802,"王異","忠義こそ最も大切な人の道。我が君の恥を雪ぐためならば、この命を捨てても惜しくはありませぬ。","忠義こそ最も大切な人の道。我が君の恥を雪ぐためならば、この命を捨てても惜しくはありませぬ。","趙昂",},
        [1950] = {20186802,"王異","ましてや子など惜しみましょうか?","ましてや子など惜しみましょうか?","趙昂",},
        [1951] = {20186802,"趙昂","ああ妻よ、お前の度胸には男も顔負けだ！","ああ妻よ、お前の度胸には男も顔負けだ！","王異",},
        [1952] = {20186901,"関定","これは我が次男、関平にございます。","これは我が次男、関平にございます。","劉備",},
        [1953] = {20186901,"関定","幼少より武芸を習っております。ぜひ関将軍の配下に加えていただければと思いますが、いかがでしょうか?","幼少より武芸を習っております。ぜひ関将軍の配下に加えていただければと思いますが、いかがでしょうか?","劉備",},
        [1954] = {20186901,"劉備","そうだな。何歳になった?","そうだな。何歳になった?","関定",},
        [1955] = {20186901,"関定","十八でございます。","十八でございます。","劉備",},
        [1956] = {20186901,"劉備","ご老人のご厚意、痛み入る。我が弟にはまだ子がない。ご子息を子として迎えては如何かな?","ご老人のご厚意、痛み入る。我が弟にはまだ子がない。ご子息を子として迎えては如何かな?","関定",},
        [1957] = {20186901,"関定","願ってもないご提案にございます。関平、早く父上と伯父殿にご挨拶を。","願ってもないご提案にございます。関平、早く父上と伯父殿にご挨拶を。","関平",},
        [1958] = {20186902,"関羽","息子よ、よく聞け。力と勇気だけでは人として足りぬ。知恵と知略を使うことができてこそ、大事を成し、大器と成れるのだ。","息子よ、よく聞け。力と勇気だけでは人として足りぬ。知恵と知略を使うことができてこそ、大事を成し、大器と成れるのだ。","関平",},
        [1959] = {20186902,"関平","父上の仰るとおりです。ですから、呉国の遣わした使者・諸葛瑾を一時の感情で殺してはなりません！","父上の仰るとおりです。ですから、呉国の遣わした使者・諸葛瑾を一時の感情で殺してはなりません！","関羽",},
        [1960] = {20186902,"関羽","そうだな。孔明のためにも、奴を殺しはせぬ。安心しろ。","そうだな。孔明のためにも、奴を殺しはせぬ。安心しろ。","関平",},
        [1961] = {20187001,"諸葛亮","先帝はあなたにかなり目をかけていたはずですが、どうして逆らうのです?","先帝はあなたにかなり目をかけていたはずですが、どうして逆らうのです?","孟獲",},
        [1962] = {20187001,"孟獲","両川はもともと、わしらが代々暮らしてきた土地だ。その縄張りを侵しておきながら、わしらが逆らっただと?どっちが賊かわかんねえ話よ！","両川はもともと、わしらが代々暮らしてきた土地だ。その縄張りを侵しておきながら、わしらが逆らっただと?どっちが賊かわかんねえ話よ！","諸葛亮",},
        [1963] = {20187001,"諸葛亮","でも、現実にあなたは捕えられているんですよ。どうします?服従します?","でも、現実にあなたは捕えられているんですよ。どうします?服従します?","孟獲",},
        [1964] = {20187001,"孟獲","するものか！狭い山道でうっかりしてて、おめえの計略にはまっただけよ。","するものか！狭い山道でうっかりしてて、おめえの計略にはまっただけよ。","諸葛亮",},
        [1965] = {20187001,"諸葛亮","仕方ないですねえ。ならば、釈放してあげましょう。それでも逆らいます?","仕方ないですねえ。ならば、釈放してあげましょう。それでも逆らいます?","孟獲",},
        [1966] = {20187001,"孟獲","放すがよい！また軍馬を整え、雌雄を決してやる。再び捕えることができたら、その時は服してやろう。","放すがよい！また軍馬を整え、雌雄を決してやる。再び捕えることができたら、その時は服してやろう。","諸葛亮",},
        [1967] = {20187002,"孟獲","おのれ孔明！わしの女房を捕らえるとは、それがおめえのやり方か！","おのれ孔明！わしの女房を捕らえるとは、それがおめえのやり方か！","諸葛亮",},
        [1968] = {20187002,"諸葛亮","怒らないでくださいよ。令夫人と引き換えに、わが軍の張嶷、馬忠の二将軍を返してほしいだけです。","怒らないでくださいよ。令夫人と引き換えに、わが軍の張嶷、馬忠の二将軍を返してほしいだけです。","孟獲",},
        [1969] = {20187002,"孟獲","ふん、おめえとなら交換してやってもいいぜ！","ふん、おめえとなら交換してやってもいいぜ！","諸葛亮",},
        [1970] = {20187002,"祝融","いい加減にしなよ、六回も捕えられて。今度はあたしまで捕まっちまったよ、まったくもう！","いい加減にしなよ、六回も捕えられて。今度はあたしまで捕まっちまったよ、まったくもう！","孟獲",},
        [1971] = {20187003,"諸葛亮","帰って、もう一度兵馬を整えてきたらいかがです?いま一度、勝負をつけましょうよ。","帰って、もう一度兵馬を整えてきたらいかがです?いま一度、勝負をつけましょうよ。","孟獲",},
        [1972] = {20187003,"孟獲","うっうっ……敵を七度擒にして七度も放すなんて、聞いたことねえ。この孟獲、いくら野蛮で無学でも、恥や礼節はわきまえておるわ……！","うっうっ……敵を七度擒にして七度も放すなんて、聞いたことねえ。この孟獲、いくら野蛮で無学でも、恥や礼節はわきまえておるわ……！","諸葛亮",},
        [1973] = {20187003,"祝融","泣かないでよ、みっともない。","泣かないでよ、みっともない。","孟獲",},
        [1974] = {20187003,"諸葛亮","では、服従します?","では、服従します?","孟獲",},
        [1975] = {20187003,"孟獲","はっ、寛大なお計らいに、子々孫々まで感謝いたします。もはや服さねぇわけにゃいかねえです！うおおーん……","はっ、寛大なお計らいに、子々孫々まで感謝いたします。もはや服さねぇわけにゃいかねえです！うおおーん……","諸葛亮",},
        [1976] = {20187101,"曹操","董卓は陛下をお助けすると見せながら上意に逆らい、悪事の限りを尽くしておりました。","董卓は陛下をお助けすると見せながら上意に逆らい、悪事の限りを尽くしておりました。","献帝",},
        [1977] = {20187101,"曹操","董卓が死んだからには、私が必ずや陛下を補佐いたします。","董卓が死んだからには、私が必ずや陛下を補佐いたします。","献帝",},
        [1978] = {20187101,"曹操","ついては、我が娘を陛下に嫁がせ、皇后とするのは如何でしょう?","ついては、我が娘を陛下に嫁がせ、皇后とするのは如何でしょう?","献帝",},
        [1979] = {20187101,"献帝","うむ。丞相の言うとおりで良かろう。","うむ。丞相の言うとおりで良かろう。","曹操",},
        [1980] = {20187102,"献帝","卿の父祖は、どのようなお方であるか?","卿の父祖は、どのようなお方であるか?","劉備",},
        [1981] = {20187102,"劉備","臣は中山靖王の子孫、孝景皇帝閣下の玄孫、劉雄の孫、劉弘の子でございます。","臣は中山靖王の子孫、孝景皇帝閣下の玄孫、劉雄の孫、劉弘の子でございます。","献帝",},
        [1982] = {20187102,"献帝","氏譜を調べよ。ああ、劉備は朕の叔父ではないか。","氏譜を調べよ。ああ、劉備は朕の叔父ではないか。","劉備",},
        [1983] = {20187102,"献帝","曹操に権力を握られ、朕は国事もままならぬ。だが今、英雄の叔父を得ることができた。望みができたぞ！","曹操に権力を握られ、朕は国事もままならぬ。だが今、英雄の叔父を得ることができた。望みができたぞ！","劉備",},
        [1984] = {20187103,"献帝","朕が即位してこの方、周りは奸臣ばかりであった。最初は董卓の災いを被り、次に李傕・郭汜の乱、そして曹操。","朕が即位してこの方、周りは奸臣ばかりであった。最初は董卓の災いを被り、次に李傕・郭汜の乱、そして曹操。","",},
        [1985] = {20187103,"献帝","国を背負う重臣と思いきや、専ら権力を弄し、勝手な振る舞いをする者ばかり。","国を背負う重臣と思いきや、専ら権力を弄し、勝手な振る舞いをする者ばかり。","",},
        [1986] = {20187103,"献帝","それを見るにつけ、朕はまるで針の筵に座らされているような心地がする。","それを見るにつけ、朕はまるで針の筵に座らされているような心地がする。","",},
        [1987] = {20187103,"献帝","朝廷の公卿は、漢の禄を食んでいながら、一人として国を救う者はおらぬのか?","朝廷の公卿は、漢の禄を食んでいながら、一人として国を救う者はおらぬのか?","",},
        [1988] = {20187103,"献帝","朕は血書を書き、帯に隠して董承に託した。忠義の士を集め、奸臣を討ち、再び天下を休らかにせよとな！","朕は血書を書き、帯に隠して董承に託した。忠義の士を集め、奸臣を討ち、再び天下を休らかにせよとな！","",},
        [1989] = {20187201,"韓当","強風で総軍の大将旗が折れてしまいました。これは吉兆にあらず、引き返しましょう！","強風で総軍の大将旗が折れてしまいました。これは吉兆にあらず、引き返しましょう！","孫堅",},
        [1990] = {20187201,"孫堅","我が軍は連戦連勝、もうすぐ襄陽を攻め落とせるのに、なぜ撤退せねばならぬのだ?","我が軍は連戦連勝、もうすぐ襄陽を攻め落とせるのに、なぜ撤退せねばならぬのだ?","韓当",},
        [1991] = {20187201,"韓当","どうも不吉な予感がするのです。用心するがよいかと。","どうも不吉な予感がするのです。用心するがよいかと。","孫堅",},
        [1992] = {20187202,"孫策","江東にこれほど大きな領地を得られたのも、韓当がわが軍に多くの勝利をもたらし、数々の戦功を立てたお陰だ。","江東にこれほど大きな領地を得られたのも、韓当がわが軍に多くの勝利をもたらし、数々の戦功を立てたお陰だ。","韓当",},
        [1993] = {20187202,"韓当","当初から孫堅将軍に付き従い南北を歴戦して参りました。これからも力を尽くし、呉国の繁栄をお助けします！","当初から孫堅将軍に付き従い南北を歴戦して参りました。これからも力を尽くし、呉国の繁栄をお助けします！","孫策",},
        [1994] = {20187203,"韓当","聞くところによると、俺と周泰、呂蒙のうち誰が最も優れているか、と尋ねる者がおるそうだな?","聞くところによると、俺と周泰、呂蒙のうち誰が最も優れているか、と尋ねる者がおるそうだな?","",},
        [1995] = {20187203,"韓当","武力や戦績では、周泰が強い。智謀では呂蒙が優れていよう。","武力や戦績では、周泰が強い。智謀では呂蒙が優れていよう。","",},
        [1996] = {20187203,"韓当","だが経験では、誰も俺に敵うまい！","だが経験では、誰も俺に敵うまい！","",},
        [1997] = {20187301,"曹丕","柏霊筠を司馬懿の側室として与えよう。我が好意に背かぬようにな。","柏霊筠を司馬懿の側室として与えよう。我が好意に背かぬようにな。","張春華",},
        [1998] = {20187301,"張春華","側室ですって?明らかに曹丕が我が家を監視するために密偵を送り込もうとしているのではありませんか。","側室ですって?明らかに曹丕が我が家を監視するために密偵を送り込もうとしているのではありませんか。","司馬懿",},
        [1999] = {20187301,"張春華","受け入れることなどできませぬ。","受け入れることなどできませぬ。","司馬懿",},
        [2000] = {20187301,"司馬懿","お前の言うとおりだな。命に背くことになるが、潔く陛下に死罪を賜ろうではないか。","お前の言うとおりだな。命に背くことになるが、潔く陛下に死罪を賜ろうではないか。","張春華",},
        [2001] = {20187301,"張春華","まあ！まったく小憎らしい！はいはい、あなたには負けましたわ。その女を迎えなさいな！","まあ！まったく小憎らしい！はいはい、あなたには負けましたわ。その女を迎えなさいな！","司馬懿",},
        [2002] = {20187302,"司馬懿","お前は私のために三男一女を育ててくれた。本当に苦労をかけたな。","お前は私のために三男一女を育ててくれた。本当に苦労をかけたな。","張春華",},
        [2003] = {20187302,"張春華","どうせあなたには大切にしてもらえません。子供たちが孝行してくれればそれで良いですわ。","どうせあなたには大切にしてもらえません。子供たちが孝行してくれればそれで良いですわ。","司馬懿",},
        [2004] = {20187302,"司馬懿","な、何を言う。お前のような怖い嫁……いや女傑を、大切にしないわけがなかろう?","な、何を言う。お前のような怖い嫁……いや女傑を、大切にしないわけがなかろう?","張春華",},
        [2005] = {20187302,"張春華","あらそうですか、それならいいですけどね！","あらそうですか、それならいいですけどね！","司馬懿",},
        [2006] = {20187303,"司馬懿","いくら腹が立ったからといって断食はやめてくれ。体を壊すぞ。まして息子たちまで巻き込んで……謝るから、許してくれ。","いくら腹が立ったからといって断食はやめてくれ。体を壊すぞ。まして息子たちまで巻き込んで……謝るから、許してくれ。","張春華",},
        [2007] = {20187303,"張春華","いいでしょう、怒るのは食事が済んでからにしますわ。","いいでしょう、怒るのは食事が済んでからにしますわ。","司馬懿",},
        [2008] = {20187303,"司馬懿","……決して女房が怖いのではないぞ。二人の息子を飢えさせるのが心配なのだ。","……決して女房が怖いのではないぞ。二人の息子を飢えさせるのが心配なのだ。","張春華",},
        [2009] = {20187401,"甘寧","チッ、孫皎のやつ、あんな小さなことで意地を張りやがって。もう相手にしないぞ。これからは、お前につく。","チッ、孫皎のやつ、あんな小さなことで意地を張りやがって。もう相手にしないぞ。これからは、お前につく。","呂蒙",},
        [2010] = {20187401,"呂蒙","そんなに怒ることでもなかろう。我が君の従弟なんだぞ、少し譲ってやれば良いではないか。","そんなに怒ることでもなかろう。我が君の従弟なんだぞ、少し譲ってやれば良いではないか。","甘寧",},
        [2011] = {20187401,"甘寧","たとえ主君の従弟でも、人を侮辱するのは許せん。我が君には精一杯仕えるが、理屈が通らんのは嫌だ。","たとえ主君の従弟でも、人を侮辱するのは許せん。我が君には精一杯仕えるが、理屈が通らんのは嫌だ。","呂蒙",},
        [2012] = {20187401,"呂蒙","(ため息をつく)お前と来たら、まったく頑固だな。","(ため息をつく)お前と来たら、まったく頑固だな。","甘寧",},
        [2013] = {20187402,"孫権","呂蒙よ。お前は今では要職に就き、大権を握っておるのだ。武術だけでなく、学問を収めぬわけにはいかぬぞ。","呂蒙よ。お前は今では要職に就き、大権を握っておるのだ。武術だけでなく、学問を収めぬわけにはいかぬぞ。","呂蒙",},
        [2014] = {20187402,"呂蒙","わかってはおりますが、軍務に忙しく、とても時間はありませぬ。","わかってはおりますが、軍務に忙しく、とても時間はありませぬ。","孫権",},
        [2015] = {20187402,"孫権","何も学者になるまで研鑽せよとは言うておらぬ。ざっと読んで、過去の事を理解すればよいのだ。","何も学者になるまで研鑽せよとは言うておらぬ。ざっと読んで、過去の事を理解すればよいのだ。","呂蒙",},
        [2016] = {20187402,"孫権","勤めが忙しいと言うが、俺ほどではないだろう?俺は常に書を読み、得るところは大きいと感じておるぞ。","勤めが忙しいと言うが、俺ほどではないだろう?俺は常に書を読み、得るところは大きいと感じておるぞ。","呂蒙",},
        [2017] = {20187402,"呂蒙","分かりました。必ずや精進し、しっかりと学びます！","分かりました。必ずや精進し、しっかりと学びます！","孫権",},
        [2018] = {20187403,"呂蒙","関羽と隣り合う、あなたの責任は重大だ。予想外の事態が起こらないよう、どのような策略を採るおつもりか?","関羽と隣り合う、あなたの責任は重大だ。予想外の事態が起こらないよう、どのような策略を採るおつもりか?","魯粛",},
        [2019] = {20187403,"魯粛","それは……その場で状況を見て判断します。","それは……その場で状況を見て判断します。","呂蒙",},
        [2020] = {20187403,"呂蒙","今、我々は劉備と同盟を結んでいるが、関羽は勇猛な将軍、だからこそ警戒せずにはおられまい。","今、我々は劉備と同盟を結んでいるが、関羽は勇猛な将軍、だからこそ警戒せずにはおられまい。","魯粛",},
        [2021] = {20187403,"呂蒙","それが事前に計略もないとは、どういうことなのです?","それが事前に計略もないとは、どういうことなのです?","魯粛",},
        [2022] = {20187403,"魯粛","呂蒙、あなたの才と知略がそれほどとは思いませんでしたな。もはや「呉下の阿蒙」とは呼べませんね！","呂蒙、あなたの才と知略がそれほどとは思いませんでしたな。もはや「呉下の阿蒙」とは呼べませんね！","呂蒙",},
        [2023] = {20187403,"呂蒙","やっとわかっていただけましたか。「士別れて三日なれば、即ち更に刮目して相待すべし」ですよ。","やっとわかっていただけましたか。「士別れて三日なれば、即ち更に刮目して相待すべし」ですよ。","魯粛",},
        [2024] = {20187501,"馬超","ここにいる百頭余りの良馬から、好きなのを選んでいいぞ…ん?なぜそんなに痩せて醜い鹿毛の馬を選ぶんだ?","ここにいる百頭余りの良馬から、好きなのを選んでいいぞ…ん?なぜそんなに痩せて醜い鹿毛の馬を選ぶんだ?","龐徳",},
        [2025] = {20187501,"龐徳","確かに姿は醜いですが、速さなら、他のどの馬でも追い付けぬほどです。我々は実力重視で行きましょう！","確かに姿は醜いですが、速さなら、他のどの馬でも追い付けぬほどです。我々は実力重視で行きましょう！","馬超",},
        [2026] = {20187501,"馬超","そうか、龐徳は大した相馬眼の持ち主だな。","そうか、龐徳は大した相馬眼の持ち主だな。","龐徳",},
        [2027] = {20187502,"曹操","敵軍の龐徳は涼州の勇将だ。もし我が配下に来れば、必ずわしの大事の力になるであろう。","敵軍の龐徳は涼州の勇将だ。もし我が配下に来れば、必ずわしの大事の力になるであろう。","賈詡",},
        [2028] = {20187502,"賈詡","私に良い考えがあります。これこのように。","私に良い考えがあります。これこのように。","曹操",},
        [2029] = {20187502,"曹操","龐徳将軍。わしが自ら戒めを解いてやろう。ときにお前の主君・張魯は、この戦に敗れればお前を斬ると言ったそうだぞ。","龐徳将軍。わしが自ら戒めを解いてやろう。ときにお前の主君・張魯は、この戦に敗れればお前を斬ると言ったそうだぞ。","龐徳",},
        [2030] = {20187502,"曹操","自軍に帰れば死あるのみ。ならばわしの配下となり、共に大業を成そうではないか。どうだ?","自軍に帰れば死あるのみ。ならばわしの配下となり、共に大業を成そうではないか。どうだ?","龐徳",},
        [2031] = {20187502,"龐徳","先に仁義を失くしたのは彼らだ。ならば、とがめられる筋合いはない。よし、投降しよう！曹操殿にお仕えしようではないか。","先に仁義を失くしたのは彼らだ。ならば、とがめられる筋合いはない。よし、投降しよう！曹操殿にお仕えしようではないか。","曹操",},
        [2032] = {20187503,"関羽","お前の兄は漢中におる。我らはお前を将軍として迎えよう。投降して、我らが配下となれ。","お前の兄は漢中におる。我らはお前を将軍として迎えよう。投降して、我らが配下となれ。","龐徳",},
        [2033] = {20187503,"龐徳","ふん、魏王は百万の精兵を擁し、向かうところ敵なしと天下に覇を唱えている。劉備なぞ、敵ではないわ！","ふん、魏王は百万の精兵を擁し、向かうところ敵なしと天下に覇を唱えている。劉備なぞ、敵ではないわ！","関羽",},
        [2034] = {20187503,"龐徳","さあ殺すなら殺せ、幽鬼となっても賊軍の将になどならん！","さあ殺すなら殺せ、幽鬼となっても賊軍の将になどならん！","関羽",},
        [2035] = {20187601,"張星彩","銀屏姉さまの名前、うちの父さまがつけたってホント?","銀屏姉さまの名前、うちの父さまがつけたってホント?","関銀屏",},
        [2036] = {20187601,"関銀屏","本当ですわよ。張飛叔父様は長年大切にしてきた宝珠も下さったんですの。呂布の紫金冠から取ってきたものなんですって。","本当ですわよ。張飛叔父様は長年大切にしてきた宝珠も下さったんですの。呂布の紫金冠から取ってきたものなんですって。","張星彩",},
        [2037] = {20187601,"張星彩","知ってる！幸運をもたらし、凶を吉に変えてくれるんでしょ?父さまったらひどいわ。わたしにくれてもいいのに！","知ってる！幸運をもたらし、凶を吉に変えてくれるんでしょ?父さまったらひどいわ。わたしにくれてもいいのに！","関銀屏",},
        [2038] = {20187602,"孫権","関羽将軍には娘が一人いるそうだが、諸葛瑾から孫登への嫁入りを申し出てはどうか?","関羽将軍には娘が一人いるそうだが、諸葛瑾から孫登への嫁入りを申し出てはどうか?","諸葛瑾",},
        [2039] = {20187602,"孫権","孫・劉同盟への我々の誠意を示せると思うが……","孫・劉同盟への我々の誠意を示せると思うが……","諸葛瑾",},
        [2040] = {20187602,"諸葛瑾","それはよろしいですね。でも、もし関羽将軍が同意しないときは……我々は曹操と手を結び荊州を攻めましょう。","それはよろしいですね。でも、もし関羽将軍が同意しないときは……我々は曹操と手を結び荊州を攻めましょう。","孫権",},
        [2041] = {20187602,"諸葛瑾","いかが思われますか?","いかが思われますか?","孫権",},
        [2042] = {20187603,"諸葛瑾","我が君は世子・孫登様の奥方に、将軍の姫君を迎えたいと考えておられます。将軍のご意向はいかがです?","我が君は世子・孫登様の奥方に、将軍の姫君を迎えたいと考えておられます。将軍のご意向はいかがです?","関羽",},
        [2043] = {20187603,"関羽","ふん、残念だが、虎の娘を犬の子になどやれん！","ふん、残念だが、虎の娘を犬の子になどやれん！","諸葛瑾",},
        [2044] = {20187701,"鮑三娘","ならず者どもを追い払って戴き、感謝いたします。","ならず者どもを追い払って戴き、感謝いたします。","関索",},
        [2045] = {20187701,"関索","礼には及ばぬさ、娘さん。ちょろいもんだ。","礼には及ばぬさ、娘さん。ちょろいもんだ。","鮑三娘",},
        [2046] = {20187701,"鮑三娘","そういうわけには参りません。恐れながら、この身をもって、命を救ってくださった英雄の御恩に報いたいと思います。","そういうわけには参りません。恐れながら、この身をもって、命を救ってくださった英雄の御恩に報いたいと思います。","関索",},
        [2047] = {20187702,"関索","父上……長い間ほうぼう捜し歩いて、ようやくお会いできました！","父上……長い間ほうぼう捜し歩いて、ようやくお会いできました！","関羽",},
        [2048] = {20187702,"関羽","関索よ……長きにわたる流浪の生活、苦労をかけたな。","関索よ……長きにわたる流浪の生活、苦労をかけたな。","関索",},
        [2049] = {20187702,"関索","なんてことはありません！この関索、父上と共に、蜀漢のために尽くしとうございます！","なんてことはありません！この関索、父上と共に、蜀漢のために尽くしとうございます！","関羽",},
        [2050] = {20187703,"関索","この関索、父上の後を継ぎ、蜀漢と生死を共にして、天下統一を果たす所存です。","この関索、父上の後を継ぎ、蜀漢と生死を共にして、天下統一を果たす所存です。","諸葛亮",},
        [2051] = {20187703,"関索","夫婦共々、丞相に従って南蛮を討ち、南中を守ります。","夫婦共々、丞相に従って南蛮を討ち、南中を守ります。","諸葛亮",},
        [2052] = {20187703,"諸葛亮","さすがは関羽将軍の息子ですねえ。昔のお父上の面影があります。関将軍父子の力を得られるとは、わが蜀漢にとって誠に幸いです。","さすがは関羽将軍の息子ですねえ。昔のお父上の面影があります。関将軍父子の力を得られるとは、わが蜀漢にとって誠に幸いです。","関索",},
        [2053] = {20187801,"陸遜","目下、英雄は各々領土を占め、覇を争っております。そこから抜きん出るには大量の兵力が必要です。","目下、英雄は各々領土を占め、覇を争っております。そこから抜きん出るには大量の兵力が必要です。","孫権",},
        [2054] = {20187801,"陸遜","さらに今、国内には山賊も跳梁しています。我々は軍を拡充し、精鋭の兵たちを育てねばなりませぬ。","さらに今、国内には山賊も跳梁しています。我々は軍を拡充し、精鋭の兵たちを育てねばなりませぬ。","陸遜",},
        [2055] = {20187801,"孫権","まさしくもっともだ。気に入ったぞ。我が姪をお前に与えよう。","まさしくもっともだ。気に入ったぞ。我が姪をお前に与えよう。","陸遜",},
        [2056] = {20187802,"陸遜","関羽は自らの武勇をたのみ、無敵とうぬぼれ、敵を侮っています。","関羽は自らの武勇をたのみ、無敵とうぬぼれ、敵を侮っています。","呂蒙",},
        [2057] = {20187802,"陸遜","もしあなたが病気と聞けば、それほど防備を固めることもないでしょう。我々でよく相談いたしましょう。","もしあなたが病気と聞けば、それほど防備を固めることもないでしょう。我々でよく相談いたしましょう。","呂蒙",},
        [2058] = {20187802,"呂蒙","関羽の勇猛は皆知っている。今は奴をどうこうしようとは考えるな。","関羽の勇猛は皆知っている。今は奴をどうこうしようとは考えるな。","陸遜",},
        [2059] = {20187802,"孫権","お前の後を継ぐ者を選ばねば。誰か意中の者はいるか。","お前の後を継ぐ者を選ばねば。誰か意中の者はいるか。","呂蒙",},
        [2060] = {20187802,"呂蒙","陸遜なら思慮深く、重責に耐えられましょう。今はまだ無名ゆえ、関羽も警戒せぬはず。最良の人選です。","陸遜なら思慮深く、重責に耐えられましょう。今はまだ無名ゆえ、関羽も警戒せぬはず。最良の人選です。","陸遜",},
        [2061] = {20187802,"呂蒙","もしお使いになるなら、まずはその存在を外に知られぬようにしましょう。","もしお使いになるなら、まずはその存在を外に知られぬようにしましょう。","陸遜",},
        [2062] = {20187802,"呂蒙","それから機会を捕えて一挙に打って出れば、必ずや成功します。","それから機会を捕えて一挙に打って出れば、必ずや成功します。","陸遜",},
        [2063] = {20187901,"呉国太","劉備殿とは面識がありませんでしたが、もし明日甘露寺でお会いし、私の意に適う人物であるなら、娘を嫁がせましょう。","劉備殿とは面識がありませんでしたが、もし明日甘露寺でお会いし、私の意に適う人物であるなら、娘を嫁がせましょう。","孫権",},
        [2064] = {20187901,"呉国太","そうでなければ、後はそなたたちに任せます。","そうでなければ、後はそなたたちに任せます。","孫権",},
        [2065] = {20187901,"孫権","もう下手人は手配しておきました。母上様の態度次第です。","もう下手人は手配しておきました。母上様の態度次第です。","呉国太",},
        [2066] = {20187902,"呉国太","うん……家柄も見た目も性格も、悪くない。立派な方ですね。この縁談、私は良いと思います。","うん……家柄も見た目も性格も、悪くない。立派な方ですね。この縁談、私は良いと思います。","孫尚香",},
        [2067] = {20187902,"孫尚香","はい。すべては母上のお心のままに。","はい。すべては母上のお心のままに。","呉国太",},
        [2068] = {20187902,"呉国太","あとは劉備殿が、娘に良くしてくれればいいのだけど。","あとは劉備殿が、娘に良くしてくれればいいのだけど。","孫尚香",},
        [2069] = {20188001,"呂布","張遼！お前は俺の最も頼りとする部下だ。裏切り者のせいで曹操に捕まったが、早く助けに来てくれ！","張遼！お前は俺の最も頼りとする部下だ。裏切り者のせいで曹操に捕まったが、早く助けに来てくれ！","張遼",},
        [2070] = {20188001,"曹操","張遼将軍、呂布の配下にあっても、その才は隠しきれぬ。今後はわしにつかぬか。我が陣営でも指折りの猛将となるだろう。","張遼将軍、呂布の配下にあっても、その才は隠しきれぬ。今後はわしにつかぬか。我が陣営でも指折りの猛将となるだろう。","張遼",},
        [2071] = {20188001,"張遼","わかりました。曹操殿に従い、共に大業を成しましょう。","わかりました。曹操殿に従い、共に大業を成しましょう。","曹操",},
        [2072] = {20188002,"関羽","なにを言おうと無駄なこと、わしは投降などせぬ！","なにを言おうと無駄なこと、わしは投降などせぬ！","張遼",},
        [2073] = {20188002,"張遼","関羽殿。そう言わず、ひとまず投降されよ。この俺がいる限り、悪いようにはしませんから。","関羽殿。そう言わず、ひとまず投降されよ。この俺がいる限り、悪いようにはしませんから。","関羽",},
        [2074] = {20188002,"張遼","劉備殿の消息を得しだい、ここを去ればよろしい。","劉備殿の消息を得しだい、ここを去ればよろしい。","関羽",},
        [2075] = {20188002,"関羽","信じて良いのか?","信じて良いのか?","張遼",},
        [2076] = {20188002,"張遼","もちろんですとも。","もちろんですとも。","関羽",},
        [2077] = {20188003,"張遼","この度の戦い、我が軍の十分な準備に加え、皆が退路を断つ覚悟で臨んだおかげで、大勝利を収めることができた。","この度の戦い、我が軍の十分な準備に加え、皆が退路を断つ覚悟で臨んだおかげで、大勝利を収めることができた。","",},
        [2078] = {20188003,"孫権","張遼は正に猛将だ。わずか八百騎で我が大軍を破るとは、少し侮り過ぎたようだ。","張遼は正に猛将だ。わずか八百騎で我が大軍を破るとは、少し侮り過ぎたようだ。","張遼",},
        [2079] = {20188003,"孫権","子どもが夜泣きしても張遼の名を聞くと泣き止むという。この戦いで、その名は江東に轟いたな。","子どもが夜泣きしても張遼の名を聞くと泣き止むという。この戦いで、その名は江東に轟いたな。","張遼",},
        [2080] = {20188101,"曹植","僕は徳に乏しく、藩侯であるにすぎないが、国に報い、民に恵みを与えたい。後世に残る業績を成し、朽ちない功績を建てたいと思う。","僕は徳に乏しく、藩侯であるにすぎないが、国に報い、民に恵みを与えたい。後世に残る業績を成し、朽ちない功績を建てたいと思う。","楊修",},
        [2081] = {20188101,"曹植","決して空虚な文章で功績を立てたり、詩を書くだけで君子になろうなどとは思っておらん。","決して空虚な文章で功績を立てたり、詩を書くだけで君子になろうなどとは思っておらん。","楊修",},
        [2082] = {20188101,"曹植","我が志が実らなければ、我が道が過ちであるということだ。","我が志が実らなければ、我が道が過ちであるということだ。","楊修",},
        [2083] = {20188101,"曹植","誠の民の言葉を集め、時代の試練を見極め、仁義の本質を評せば、その成果は自ずから一つの学説となろう。","誠の民の言葉を集め、時代の試練を見極め、仁義の本質を評せば、その成果は自ずから一つの学説となろう。","楊修",},
        [2084] = {20188101,"曹植","文章を名山に残せずとも、同好の士に伝えよう。白髪となるまで続けるのに、今日急いで結論を出すこともない。","文章を名山に残せずとも、同好の士に伝えよう。白髪となるまで続けるのに、今日急いで結論を出すこともない。","楊修",},
        [2085] = {20188101,"曹植","恥ずかしげもなくこう言うのは、そなたなら僕をわかってくれると思うからだ。","恥ずかしげもなくこう言うのは、そなたなら僕をわかってくれると思うからだ。","楊修",},
        [2086] = {20188101,"楊修","曹植様に認めていただいて光栄です。士は己を知る者のために死す。決死の覚悟で臨みましょう。","曹植様に認めていただいて光栄です。士は己を知る者のために死す。決死の覚悟で臨みましょう。","曹植",},
        [2087] = {20188102,"司馬懿","丞相は曹丕殿と曹植殿の才能を試そうと、それぞれ鄴城の城門から出るよう命じられた。","丞相は曹丕殿と曹植殿の才能を試そうと、それぞれ鄴城の城門から出るよう命じられた。","楊修",},
        [2088] = {20188102,"司馬懿","そのいっぽうで城門を守る兵士には、通さないようこっそり言いつけてあった。","そのいっぽうで城門を守る兵士には、通さないようこっそり言いつけてあった。","楊修",},
        [2089] = {20188102,"司馬懿","曹植殿に助言したのは貴公ですか?王命を奉じているのだから城を出よ、止めようとする兵は斬ってしまえ、と?","曹植殿に助言したのは貴公ですか?王命を奉じているのだから城を出よ、止めようとする兵は斬ってしまえ、と?","楊修",},
        [2090] = {20188102,"楊修","いかにも。","いかにも。","司馬懿",},
        [2091] = {20188102,"司馬懿","楊修殿、才能を浪費しない方がいい。ご自分では丞相の意を汲んでの行動と思っているかもしれませんが。","楊修殿、才能を浪費しない方がいい。ご自分では丞相の意を汲んでの行動と思っているかもしれませんが。","楊修",},
        [2092] = {20188102,"司馬懿","もしあなたの入れ知恵だと丞相に知れたら、曹植殿が不利になりますよ。","もしあなたの入れ知恵だと丞相に知れたら、曹植殿が不利になりますよ。","楊修",},
        [2093] = {20188103,"曹操","夏侯惇よ、なぜ要塞内の兵士たちは皆旅装を整えておるのだ?","夏侯惇よ、なぜ要塞内の兵士たちは皆旅装を整えておるのだ?","夏侯惇",},
        [2094] = {20188103,"夏侯惇","ああ、行軍主簿の楊修が、魏王に撤退の意向があると言ったのでな。","ああ、行軍主簿の楊修が、魏王に撤退の意向があると言ったのでな。","曹操",},
        [2095] = {20188103,"曹操","なぜそのようなことを?","なぜそのようなことを?","夏侯惇",},
        [2096] = {20188103,"夏侯惇","今夜の合言葉は「鶏肋」だろう?それを聞いて、魏王はほどなく引き揚げるつもりだろうと言うのだ。","今夜の合言葉は「鶏肋」だろう?それを聞いて、魏王はほどなく引き揚げるつもりだろうと言うのだ。","曹操",},
        [2097] = {20188103,"夏侯惇","鶏肋、つまり鶏の肋骨には、食べるほどの肉はついていないが、捨てるには惜しいものだと。","鶏肋、つまり鶏の肋骨には、食べるほどの肉はついていないが、捨てるには惜しいものだと。","曹操",},
        [2098] = {20188103,"夏侯惇","まさに今の戦況と同じだ。攻めても勝てず、退けば笑い者になる。ここに留まっていても無益だから、早く帰ったほうがよい。","まさに今の戦況と同じだ。攻めても勝てず、退けば笑い者になる。ここに留まっていても無益だから、早く帰ったほうがよい。","曹操",},
        [2099] = {20188103,"夏侯惇","だから魏王は明日にも帰還命令を出すだろう。よって出発前に慌てないよう、今のうちに帰り支度をしておくというのだ。","だから魏王は明日にも帰還命令を出すだろう。よって出発前に慌てないよう、今のうちに帰り支度をしておくというのだ。","曹操",},
        [2100] = {20188103,"曹操","楊修のやつめ、小賢しい真似を。勝手な言説で軍を乱すとは！","楊修のやつめ、小賢しい真似を。勝手な言説で軍を乱すとは！","夏侯惇",},
        [2101] = {20188103,"曹操","処刑人、奴を引っ立てて首を斬れ！首級は門外へ晒すのだ！","処刑人、奴を引っ立てて首を斬れ！首級は門外へ晒すのだ！","夏侯惇",},
        [2102] = {20188201,"司馬懿","息子たちよ、いるか?こちらへ来い。","息子たちよ、いるか?こちらへ来い。","司馬昭",},
        [2103] = {20188201,"司馬昭","ここにおります、父上。","ここにおります、父上。","司馬懿",},
        [2104] = {20188201,"司馬懿","私は長い年月魏に仕え、太傅の高官を授かった。臣下として最高の位だ。","私は長い年月魏に仕え、太傅の高官を授かった。臣下として最高の位だ。","司馬昭",},
        [2105] = {20188201,"司馬懿","だが人はみな私に野心があると疑っているので、常に恐れを抱いていた。","だが人はみな私に野心があると疑っているので、常に恐れを抱いていた。","司馬昭",},
        [2106] = {20188201,"司馬懿","私が死んだら、お前たち二人は国政をうまくさばき、皇帝を助けて神州を平定するのだ。慎重の上にも慎重にな！","私が死んだら、お前たち二人は国政をうまくさばき、皇帝を助けて神州を平定するのだ。慎重の上にも慎重にな！","司馬昭",},
        [2107] = {20188202,"司馬昭","上は曹髦から下は曹氏の一族郎党に至るまで、みな司馬家の滅亡を望んでいよう。","上は曹髦から下は曹氏の一族郎党に至るまで、みな司馬家の滅亡を望んでいよう。","",},
        [2108] = {20188202,"司馬昭","成済の刃が曹髦の胸を刺し貫いた時から、俺は二度と戻れぬ道に踏み出してしまった。","成済の刃が曹髦の胸を刺し貫いた時から、俺は二度と戻れぬ道に踏み出してしまった。","",},
        [2109] = {20188202,"司馬昭","もはや、天下を取るしかない。さもなくば、曹髦よりさらに悲惨な末路を辿ることになろう。","もはや、天下を取るしかない。さもなくば、曹髦よりさらに悲惨な末路を辿ることになろう。","",},
        [2110] = {20188202,"司馬昭","「司馬昭の心は、誰もが知っている」と世の人々は言うが――","「司馬昭の心は、誰もが知っている」と世の人々は言うが――","",},
        [2111] = {20188202,"司馬昭","果たして世の人は本当に、俺の本心を知っているかな……?","果たして世の人は本当に、俺の本心を知っているかな……?","",},
        [2112] = {20188301,"劉備","陳羣、君は潁川陳氏の生まれだそうだな。学問も名声もかなりのものだと、以前から聞いている。","陳羣、君は潁川陳氏の生まれだそうだな。学問も名声もかなりのものだと、以前から聞いている。","陳羣",},
        [2113] = {20188301,"劉備","是非欲しい人材だ。我が別駕従事となる気はないか?","是非欲しい人材だ。我が別駕従事となる気はないか?","陳羣",},
        [2114] = {20188301,"陳羣","劉備殿は、英雄の才を持ち、民心を得ておられると聞いております。","劉備殿は、英雄の才を持ち、民心を得ておられると聞いております。","劉備",},
        [2115] = {20188301,"陳羣","劉備殿のお誘い、誠に幸甚でございます。","劉備殿のお誘い、誠に幸甚でございます。","劉備",},
        [2116] = {20188302,"曹操","曹昂は不幸にも、若くして宛城に没した。残る卞氏の生んだ子は4人。丕、彰、植、熊だ。","曹昂は不幸にも、若くして宛城に没した。残る卞氏の生んだ子は4人。丕、彰、植、熊だ。","陳羣",},
        [2117] = {20188302,"曹操","わしが平生愛でているのは曹植だが、どうも浮ついていて誠実さが足りんし、酒を嗜んでは思うように振る舞っている。","わしが平生愛でているのは曹植だが、どうも浮ついていて誠実さが足りんし、酒を嗜んでは思うように振る舞っている。","陳羣",},
        [2118] = {20188302,"曹操","よって太子とはしない。","よって太子とはしない。","陳羣",},
        [2119] = {20188302,"曹操","曹彰は勇猛だが謀ができず、曹熊は病多く、身体が持たぬだろう。","曹彰は勇猛だが謀ができず、曹熊は病多く、身体が持たぬだろう。","陳羣",},
        [2120] = {20188302,"曹操","曹丕のみが、篤実で慎み深く、我が大業を継ぐことができる。","曹丕のみが、篤実で慎み深く、我が大業を継ぐことができる。","陳羣",},
        [2121] = {20188302,"曹操","そちと司馬懿は共に、わしが頼みとする重臣だ。よく補佐してやってくれ。","そちと司馬懿は共に、わしが頼みとする重臣だ。よく補佐してやってくれ。","陳羣",},
        [2122] = {20188302,"陳羣","必ずや。臣下として、全力を尽くします。","必ずや。臣下として、全力を尽くします。","曹操",},
        [2123] = {20188401,"関銀屏","興兄さん、どうかしら、私の剣術は?","興兄さん、どうかしら、私の剣術は?","関興",},
        [2124] = {20188401,"関興","そうだな……兵卒くらいなら倒せるが、敵軍の大将には敵わないだろう。","そうだな……兵卒くらいなら倒せるが、敵軍の大将には敵わないだろう。","関銀屏",},
        [2125] = {20188401,"関銀屏","まあっ、ひどいですわ！興兄さんたら私を見下してるんですわね！","まあっ、ひどいですわ！興兄さんたら私を見下してるんですわね！","関興",},
        [2126] = {20188401,"関興","ははははは、そんなことはないさ。","ははははは、そんなことはないさ。","関銀屏",},
        [2127] = {20188402,"関興","銀屏にも困ったものです。俺に四六時中まとわりついて剣術を習いたがるのですよ。","銀屏にも困ったものです。俺に四六時中まとわりついて剣術を習いたがるのですよ。","関平",},
        [2128] = {20188402,"関平","そう言わず、なるべく付き合ってやってくれ。","そう言わず、なるべく付き合ってやってくれ。","関興",},
        [2129] = {20188402,"関興","わかってますけどね","わかってますけどね","関平",},
        [2130] = {20188402,"関平","これから俺は父上と共に出征するのだ。銀屏を頼んだぞ。","これから俺は父上と共に出征するのだ。銀屏を頼んだぞ。","関興",},
        [2131] = {20188402,"関興","はいはい、ご安心を。ちゃんと面倒見ますよ。","はいはい、ご安心を。ちゃんと面倒見ますよ。","関平",},
        [2132] = {20188403,"関興","賊め、逃げるな！","賊め、逃げるな！","潘璋",},
        [2133] = {20188403,"潘璋","顔は棗のように赤く、緑の袍に金の鎧、関羽と同じいでたちだ。いったい何者だ?","顔は棗のように赤く、緑の袍に金の鎧、関羽と同じいでたちだ。いったい何者だ?","関興",},
        [2134] = {20188403,"関興","関羽の次男、関興！","関羽の次男、関興！","潘璋",},
        [2135] = {20188403,"関興","潘璋、貴様の命と奪われた青龍偃月刀、今日ここでもらい受ける！","潘璋、貴様の命と奪われた青龍偃月刀、今日ここでもらい受ける！","潘璋",},
        [2136] = {20188501,"馬超","わが一族二百人あまりは、曹操に誅殺され、ほとんど滅ぼされてしまいました……","わが一族二百人あまりは、曹操に誅殺され、ほとんど滅ぼされてしまいました……","劉備",},
        [2137] = {20188501,"馬超","残ったのは従弟の馬岱だけです。俺が死んだらあいつに馬氏の祭祀を継がせてやってください。他に言い残すことはありません。","残ったのは従弟の馬岱だけです。俺が死んだらあいつに馬氏の祭祀を継がせてやってください。他に言い残すことはありません。","劉備",},
        [2138] = {20188501,"劉備","相わかった。馬超の頼み、この劉備、必ずや果たそう。","相わかった。馬超の頼み、この劉備、必ずや果たそう。","馬超",},
        [2139] = {20188502,"姜維","逆賊、魏延め！丞相の重用を受けながら、なぜ背いた?","逆賊、魏延め！丞相の重用を受けながら、なぜ背いた?","魏延",},
        [2140] = {20188502,"魏延","姜維、お前には関係ない！楊儀だけ呼んで来い！","姜維、お前には関係ない！楊儀だけ呼んで来い！","姜維",},
        [2141] = {20188502,"楊儀","在りし日の丞相は、お前が背くことなどお見通しだった。用心せよと言われたが、果たしてそのとおりになったな。","在りし日の丞相は、お前が背くことなどお見通しだった。用心せよと言われたが、果たしてそのとおりになったな。","魏延",},
        [2142] = {20188502,"楊儀","馬上で「俺を殺す度胸のある者はいるか」と三回叫んでみろ。そうすればお前の勇気を認め、漢中城を明け渡してやろう。","馬上で「俺を殺す度胸のある者はいるか」と三回叫んでみろ。そうすればお前の勇気を認め、漢中城を明け渡してやろう。","魏延",},
        [2143] = {20188502,"魏延","ハッ！楊儀めが、よく聞け！諸葛亮が生きていたならば、俺も少しは恐れただろう。だが亡き今、天下に俺の敵はいない！","ハッ！楊儀めが、よく聞け！諸葛亮が生きていたならば、俺も少しは恐れただろう。だが亡き今、天下に俺の敵はいない！","楊儀",},
        [2144] = {20188502,"魏延","三回と言わず、三万回でも、叫んでやるわ！","三回と言わず、三万回でも、叫んでやるわ！","楊儀",},
        [2145] = {20188502,"魏延","俺を殺す度胸のある者は?","俺を殺す度胸のある者は?","楊儀",},
        [2146] = {20188502,"馬岱","ここにいるぞ！","ここにいるぞ！","魏延",},
        [2147] = {20188502,"馬岱","丞相が臨終の際に、密計を授けられたのだ。魏延、斬るべし！","丞相が臨終の際に、密計を授けられたのだ。魏延、斬るべし！","魏延",},
        [2148] = {20188502,"魏延","何だとぉっ！?","何だとぉっ！?","馬岱",},
        [2149] = {20188601,"周倉","これは関将軍！周倉と申します、どうぞお見知りおきを。","これは関将軍！周倉と申します、どうぞお見知りおきを。","関羽",},
        [2150] = {20188601,"関羽","ほう。この関羽を知っているのか?","ほう。この関羽を知っているのか?","周倉",},
        [2151] = {20188601,"周倉","かつて黄巾の張宝の配下だった時、ご尊顔を拝しました。","かつて黄巾の張宝の配下だった時、ご尊顔を拝しました。","関羽",},
        [2152] = {20188601,"周倉","賊の一党だったため、つき従うことはできませんでしたが、本日は幸いにもお目通りがかないました。","賊の一党だったため、つき従うことはできませんでしたが、本日は幸いにもお目通りがかないました。","関羽",},
        [2153] = {20188601,"周倉","将軍お願いです。俺を兵卒に加えてください。朝晩おそばにいられれば、死んでも本望です。","将軍お願いです。俺を兵卒に加えてください。朝晩おそばにいられれば、死んでも本望です。","関羽",},
        [2154] = {20188602,"周倉","昨夜は我が君が全身血まみれで前に立つ夢を見た。驚いて訳を尋ねようとしたら目が醒めた。","昨夜は我が君が全身血まみれで前に立つ夢を見た。驚いて訳を尋ねようとしたら目が醒めた。","",},
        [2155] = {20188602,"周倉","いったいどんな吉凶があるというんだ……","いったいどんな吉凶があるというんだ……","",},
        [2156] = {20188602,"馬忠","王甫、周倉、出てこい！関羽親子の首はここにあるぞ！","王甫、周倉、出てこい！関羽親子の首はここにあるぞ！","周倉",},
        [2157] = {20188602,"馬忠","早く麦城を出て投降するのだ！","早く麦城を出て投降するのだ！","周倉",},
        [2158] = {20188602,"周倉","何だと?そ…そんな馬鹿な！","何だと?そ…そんな馬鹿な！","",},
        [2159] = {20188701,"呂布","一体誰だ！?兵を率いて反乱を起こし、下邳を襲うとは！","一体誰だ！?兵を率いて反乱を起こし、下邳を襲うとは！","高順",},
        [2160] = {20188701,"高順","将軍、何か隠しておられることは?","将軍、何か隠しておられることは?","呂布",},
        [2161] = {20188701,"呂布","そういえば、声に河内訛りがあった。","そういえば、声に河内訛りがあった。","高順",},
        [2162] = {20188701,"高順","きっと河内の郝萌に違いありません。袁術にそそのかされ、反乱を起こしたのです。","きっと河内の郝萌に違いありません。袁術にそそのかされ、反乱を起こしたのです。","呂布",},
        [2163] = {20188701,"高順","私が兵を率いて、討伐いたしましょう！","私が兵を率いて、討伐いたしましょう！","呂布",},
        [2164] = {20188702,"曹操","汝は清廉な人柄、かつ人望もあると聞いておる。さらに酒も飲まず、賄賂も受けぬと。","汝は清廉な人柄、かつ人望もあると聞いておる。さらに酒も飲まず、賄賂も受けぬと。","高順",},
        [2165] = {20188702,"曹操","配下は700人余りだが千人と号し、鎧兜や武具はいずれも整っておる。","配下は700人余りだが千人と号し、鎧兜や武具はいずれも整っておる。","高順",},
        [2166] = {20188702,"曹操","攻撃すれば打ち破らぬ者なく、「陥陣営」と呼ばれるそうだな。","攻撃すれば打ち破らぬ者なく、「陥陣営」と呼ばれるそうだな。","高順",},
        [2167] = {20188702,"曹操","だが、今や下邳は落ち、呂布も死んだ。我が陣営に入らぬか?","だが、今や下邳は落ち、呂布も死んだ。我が陣営に入らぬか?","高順",},
        [2168] = {20188702,"高順","ふん！","ふん！","曹操",},
        [2169] = {20188702,"曹操","面白い！俘虜となっても、かくも意気軒高とは。引っ立てて斬ってしまえ！","面白い！俘虜となっても、かくも意気軒高とは。引っ立てて斬ってしまえ！","高順",},
        [2170] = {20188801,"廖化","劉備殿、私はついに蜀漢に戻って参りましたぞ。","劉備殿、私はついに蜀漢に戻って参りましたぞ。","劉備",},
        [2171] = {20188801,"劉備","廖化、お前は関羽の遺志を継ぎ、漢王室の復興のため、死んだと嘘をついてきたのか。","廖化、お前は関羽の遺志を継ぎ、漢王室の復興のため、死んだと嘘をついてきたのか。","廖化",},
        [2172] = {20188801,"劉備","老母を連れて西へと千里を旅するとは。この劉備、感激至極だ。","老母を連れて西へと千里を旅するとは。この劉備、感激至極だ。","廖化",},
        [2173] = {20188802,"諸葛亮","廖化殿は荊州で、はじめ関羽殿の主簿として、着実な仕事をされていました。","廖化殿は荊州で、はじめ関羽殿の主簿として、着実な仕事をされていました。","",},
        [2174] = {20188802,"諸葛亮","のちに樊城の曹軍を攻めたときは、先陣を務められた。","のちに樊城の曹軍を攻めたときは、先陣を務められた。","",},
        [2175] = {20188802,"諸葛亮","つまり高い教養があるうえ、武将としての能力も申し分ないのです。","つまり高い教養があるうえ、武将としての能力も申し分ないのです。","",},
        [2176] = {20188802,"諸葛亮","まったく、「蜀は人材が乏しいから廖化を先鋒にした」なんて、ひどい誤解ですね。","まったく、「蜀は人材が乏しいから廖化を先鋒にした」なんて、ひどい誤解ですね。","",},
        [2177] = {20188802,"諸葛亮","廖化殿は、地味でも凡将でもないのですよ。","廖化殿は、地味でも凡将でもないのですよ。","",},
        [2178] = {20188901,"曹操","麋竺よ。わしが上奏し、嬴郡の太守に推薦してやろう。どうだ?","麋竺よ。わしが上奏し、嬴郡の太守に推薦してやろう。どうだ?","糜竺",},
        [2179] = {20188901,"糜竺","せっかくのご厚意ですが、私は劉備殿に従うと心を決めております。","せっかくのご厚意ですが、私は劉備殿に従うと心を決めております。","曹操",},
        [2180] = {20188901,"糜竺","辞退する罪をお許しください。","辞退する罪をお許しください。","曹操",},
        [2181] = {20188901,"曹操","あのわらじ売りの大耳賊のどこが良いのだ。まぁいい。気が変わったら、いつでも言え。","あのわらじ売りの大耳賊のどこが良いのだ。まぁいい。気が変わったら、いつでも言え。","糜竺",},
        [2182] = {20188902,"糜竺","関羽殿が敗れ、亡くなったのは、麋芳が城を明け渡して呉に投降したせいです。","関羽殿が敗れ、亡くなったのは、麋芳が城を明け渡して呉に投降したせいです。","劉備",},
        [2183] = {20188902,"糜竺","愚弟の罪は私の罪、慚愧に堪えません。処罰を請おうと出頭しました。どうか罰をお与えください。","愚弟の罪は私の罪、慚愧に堪えません。処罰を請おうと出頭しました。どうか罰をお与えください。","劉備",},
        [2184] = {20188902,"劉備","やめてくれ。立つのだ。","やめてくれ。立つのだ。","糜竺",},
        [2185] = {20188902,"劉備","兄弟とはいえ、麋芳の罪は、お前とは関係ない。糜竺、自分を責めないでくれ。","兄弟とはいえ、麋芳の罪は、お前とは関係ない。糜竺、自分を責めないでくれ。","糜竺",},
        [2186] = {20188902,"劉備","罰しようなどと思うはずがないのだ。","罰しようなどと思うはずがないのだ。","糜竺",},
        [2187] = {20189001,"曹操","当時、兵を挙げ董卓を討伐した時、秦邵はわしのために兵馬を募ってくれた。","当時、兵を挙げ董卓を討伐した時、秦邵はわしのために兵馬を募ってくれた。","曹真",},
        [2188] = {20189001,"曹操","不幸にも予州牧の黄琬に殺されてしまったが、その恩は忘れておらぬ。","不幸にも予州牧の黄琬に殺されてしまったが、その恩は忘れておらぬ。","曹真",},
        [2189] = {20189001,"曹操","秦真、お前はわしの養子となり、曹丕と共に暮らしてはどうだ?","秦真、お前はわしの養子となり、曹丕と共に暮らしてはどうだ?","曹真",},
        [2190] = {20189001,"曹真","ありがとうございます、曹操様は亡き父のご親友。幸いに存じます。","ありがとうございます、曹操様は亡き父のご親友。幸いに存じます。","曹操",},
        [2191] = {20189002,"曹真","蜀に投降した南安、天水、安定の三郡は、次々に奪い返したが","蜀に投降した南安、天水、安定の三郡は、次々に奪い返したが","",},
        [2192] = {20189002,"曹真","あの諸葛亮がこれで諦めるわけはない。来年必ず陳倉を攻めるだろう。","あの諸葛亮がこれで諦めるわけはない。来年必ず陳倉を攻めるだろう。","",},
        [2193] = {20189002,"曹真","将軍・郝昭と王生に命じ、陳倉を守らせ、城を修築せよ。","将軍・郝昭と王生に命じ、陳倉を守らせ、城を修築せよ。","",},
        [2194] = {20189101,"孫権","徐盛、お前は臨城南阿一体の山賊を討つに功績があった。","徐盛、お前は臨城南阿一体の山賊を討つに功績があった。","徐盛",},
        [2195] = {20189101,"孫権","お前を中郎将に任じ、将兵の監督に当たってもらおう。","お前を中郎将に任じ、将兵の監督に当たってもらおう。","徐盛",},
        [2196] = {20189101,"徐盛","孫権殿のお褒めに預かり、有難く存じます。必ずや全力で務めます。","孫権殿のお褒めに預かり、有難く存じます。必ずや全力で務めます。","孫権",},
        [2197] = {20189102,"曹操","前方が濡須口だ。呉はどの武将が守っておる?","前方が濡須口だ。呉はどの武将が守っておる?","",},
        [2198] = {20189102,"張遼","「江表の虎臣」と呼ばれる徐盛です。孫権がその勇敢さを大いに称えたとか。","「江表の虎臣」と呼ばれる徐盛です。孫権がその勇敢さを大いに称えたとか。","曹操",},
        [2199] = {20189102,"曹操","そんな者など恐れるな。わしには百万の大軍がいる。","そんな者など恐れるな。わしには百万の大軍がいる。","張遼",},
        [2200] = {20189102,"曹操","わしの為に、この濡須口を落としてくれ。","わしの為に、この濡須口を落としてくれ。","張遼",},
        [2201] = {20189103,"孫権","曹丕が大軍を率いて南征し、長江を渡ろうとしている。どうすればよいか?","曹丕が大軍を率いて南征し、長江を渡ろうとしている。どうすればよいか?","",},
        [2202] = {20189103,"徐盛","全軍をあげて、建業の周囲百里に張りぼての城郭を築きましょう。やぐらを建てて藁人形を置き、江上には船を浮かべるのです。","全軍をあげて、建業の周囲百里に張りぼての城郭を築きましょう。やぐらを建てて藁人形を置き、江上には船を浮かべるのです。","孫権",},
        [2203] = {20189103,"徐盛","一夜にして百里の偽城を築けば、敵も退却するでしょう。","一夜にして百里の偽城を築けば、敵も退却するでしょう。","孫権",},
        [2204] = {20189103,"孫権","それも一つの策だな。よし、徐盛の言うとおりにしよう。","それも一つの策だな。よし、徐盛の言うとおりにしよう。","徐盛",},
        [2205] = {20189104,"曹丕","城郭が延々数百里も続いている。魏には何千もの騎兵があるが、これでは用いようがない。","城郭が延々数百里も続いている。魏には何千もの騎兵があるが、これでは用いようがない。","",},
        [2206] = {20189104,"曹丕","天はやはり魏と呉を二つに分かったのだ。孫権のもとにはやはり人材が揃っているな。ここは全軍撤退しかあるまい。","天はやはり魏と呉を二つに分かったのだ。孫権のもとにはやはり人材が揃っているな。ここは全軍撤退しかあるまい。","",},
        [2207] = {20189201,"程普","わしは孫堅殿に従い、宛や鄧の地で黄巾を討ち、陽人で董卓を破った。城攻めや野戦で、傷だらけになったわ。","わしは孫堅殿に従い、宛や鄧の地で黄巾を討ち、陽人で董卓を破った。城攻めや野戦で、傷だらけになったわ。","",},
        [2208] = {20189201,"程普","続いてお仕えした孫策殿が張英や于麋を破り、秣陵県、湖熟、句容、曲阿を転戦した時も、数々の戦功を立てたのだ。","続いてお仕えした孫策殿が張英や于麋を破り、秣陵県、湖熟、句容、曲阿を転戦した時も、数々の戦功を立てたのだ。","",},
        [2209] = {20189201,"程普","しかるに「内政のことは張昭に、外交のことは周瑜に聞け」とは何事か！","しかるに「内政のことは張昭に、外交のことは周瑜に聞け」とは何事か！","",},
        [2210] = {20189201,"程普","周瑜の若僧が大都督で、わしが都督というのは、納得いかん！","周瑜の若僧が大都督で、わしが都督というのは、納得いかん！","",},
        [2211] = {20189202,"孫権","程普と周瑜は烏林で曹操を大敗させてから、不仲ではなくなったそうだな。","程普と周瑜は烏林で曹操を大敗させてから、不仲ではなくなったそうだな。","程普",},
        [2212] = {20189202,"程普","烏林の戦いでは、周瑜殿の軍の運用に敬服しましたな。","烏林の戦いでは、周瑜殿の軍の運用に敬服しましたな。","孫権",},
        [2213] = {20189202,"周瑜","程普は武勇卓越にして戦功は数知れず、誠の江表の虎臣の名に相応しいと思います。","程普は武勇卓越にして戦功は数知れず、誠の江表の虎臣の名に相応しいと思います。","孫権",},
        [2214] = {20189202,"孫権","ははははは、良いぞ。二人は誠に呉の両腕だ。","ははははは、良いぞ。二人は誠に呉の両腕だ。","周瑜",},
        [2215] = {20189301,"陳登","呂布は豺狼です。勇ましくはありますが智謀はなく、行動は常に軽はずみです。早く手を打ったほうが宜しいかと。","呂布は豺狼です。勇ましくはありますが智謀はなく、行動は常に軽はずみです。早く手を打ったほうが宜しいかと。","曹操",},
        [2216] = {20189301,"曹操","わしも平素から呂布は長く手元には置けぬと思っていた。お前たち親子なくして詳しい事は分からぬ。わしと策を練ってくれ。","わしも平素から呂布は長く手元には置けぬと思っていた。お前たち親子なくして詳しい事は分からぬ。わしと策を練ってくれ。","陳登",},
        [2217] = {20189301,"陳登","丞相が動かれるのであれば、こちらも内応致します。","丞相が動かれるのであれば、こちらも内応致します。","曹操",},
        [2218] = {20189301,"曹操","よし、この事は頼むぞ。","よし、この事は頼むぞ。","陳登",},
        [2219] = {20189302,"陳登","胸やけがひどく、顔面が赤くなって、食欲もないのだ。","胸やけがひどく、顔面が赤くなって、食欲もないのだ。","華佗",},
        [2220] = {20189302,"華佗","それはいけません。早くこの薬を飲んでください！","それはいけません。早くこの薬を飲んでください！","陳登",},
        [2221] = {20189302,"陳登","何だこれは！?三升も虫を吐いたぞ。どれも頭が赤くて、もぞもぞうごめいておる。こんなものがなぜわしの体内に?","何だこれは！?三升も虫を吐いたぞ。どれも頭が赤くて、もぞもぞうごめいておる。こんなものがなぜわしの体内に?","華佗",},
        [2222] = {20189302,"華佗","魚の膾を多く食べていますね。恐らくその毒のせいでしょう。","魚の膾を多く食べていますね。恐らくその毒のせいでしょう。","陳登",},
        [2223] = {20189302,"華佗","今日は良くなりましたが、三年後必ず再発します。名医でなくては救えません。","今日は良くなりましたが、三年後必ず再発します。名医でなくては救えません。","陳登",},
        [2224] = {20189302,"華佗","私がそれまで生きていれば、救えますが……","私がそれまで生きていれば、救えますが……","陳登",},
        [2225] = {20189401,"鄧艾","かつては黄巾の乱を平定し、屯田を行って農地を広げました。","かつては黄巾の乱を平定し、屯田を行って農地を広げました。","司馬懿",},
        [2226] = {20189401,"鄧艾","これにより許都に多くの兵糧が蓄えられ、天下を制する元となったのです。","これにより許都に多くの兵糧が蓄えられ、天下を制する元となったのです。","司馬懿",},
        [2227] = {20189401,"鄧艾","今や三方面は既に平定され、残るは淮南のみです。しかし大軍が南征するには輸送だけで兵力の半分を費やします。","今や三方面は既に平定され、残るは淮南のみです。しかし大軍が南征するには輸送だけで兵力の半分を費やします。","司馬懿",},
        [2228] = {20189401,"鄧艾","莫大な経費がかかり、労役の負担も大きい。","莫大な経費がかかり、労役の負担も大きい。","司馬懿",},
        [2229] = {20189401,"鄧艾","陳から蔡にかけての土地は良質なので、許昌周辺の水田を減らして、水路をこの東南方面に引くべきでしょう。","陳から蔡にかけての土地は良質なので、許昌周辺の水田を減らして、水路をこの東南方面に引くべきでしょう。","司馬懿",},
        [2230] = {20189401,"鄧艾","淮北に二万人、淮南に三万人の屯田兵を置き、五分の一ずつ交替で休ませ、","淮北に二万人、淮南に三万人の屯田兵を置き、五分の一ずつ交替で休ませ、","司馬懿",},
        [2231] = {20189401,"鄧艾","常に四万人で、耕作と防衛に当たらせるようにします。","常に四万人で、耕作と防衛に当たらせるようにします。","司馬懿",},
        [2232] = {20189401,"鄧艾","水さえ豊富なら、収穫は常に西の地域の三倍以上になりましょう。","水さえ豊富なら、収穫は常に西の地域の三倍以上になりましょう。","司馬懿",},
        [2233] = {20189401,"鄧艾","諸経費を差し引いても、毎年五百万石を軍糧にできます。","諸経費を差し引いても、毎年五百万石を軍糧にできます。","司馬懿",},
        [2234] = {20189401,"鄧艾","これを続ければ、六、七年のうちに淮河上流に三千万石の糧食を蓄えられます。","これを続ければ、六、七年のうちに淮河上流に三千万石の糧食を蓄えられます。","司馬懿",},
        [2235] = {20189401,"鄧艾","これだけあれば、十万の兵が五年は食べられる計算になります。","これだけあれば、十万の兵が五年は食べられる計算になります。","司馬懿",},
        [2236] = {20189401,"鄧艾","この蓄えをもとに呉へ攻め入れば、勝てぬ戦はないでしょう！","この蓄えをもとに呉へ攻め入れば、勝てぬ戦はないでしょう！","司馬懿",},
        [2237] = {20189401,"司馬懿","素晴らしい！これだけの才能を典農功曹としておくのは惜しい。これより太尉府の属官としよう。","素晴らしい！これだけの才能を典農功曹としておくのは惜しい。これより太尉府の属官としよう。","鄧艾",},
        [2238] = {20189402,"鄧艾","私は、蜀を滅亡させた戦いで最大の功臣だぞ。まごうことなき忠臣なのだ！","私は、蜀を滅亡させた戦いで最大の功臣だぞ。まごうことなき忠臣なのだ！","",},
        [2239] = {20189402,"鄧艾","すべては鍾会の讒言と罠だ。ヤツが謀反を企てているのだ！","すべては鍾会の讒言と罠だ。ヤツが謀反を企てているのだ！","",},
        [2240] = {20189402,"鄧艾","これではまるで戦国の白起将軍ではないか。冤罪だ！","これではまるで戦国の白起将軍ではないか。冤罪だ！","",},
        [2241] = {20189501,"劉備","関羽の兵が破れた。麦城に籠城し、敵に囲まれている。","関羽の兵が破れた。麦城に籠城し、敵に囲まれている。","孟達",},
        [2242] = {20189501,"劉備","しかし蜀からの援軍は、すぐには着けぬ。","しかし蜀からの援軍は、すぐには着けぬ。","孟達",},
        [2243] = {20189501,"劉備","将軍は、すぐに上庸の兵を起こし、危機を救ってくれ。少しでも遅れれば、関羽が敵の手に落ちてしまう。","将軍は、すぐに上庸の兵を起こし、危機を救ってくれ。少しでも遅れれば、関羽が敵の手に落ちてしまう。","孟達",},
        [2244] = {20189501,"孟達","恐れながら申し上げます。私が参っても、荷車一台分の薪を燃やした火に、ひしゃく一杯の水をかけるようなもの。","恐れながら申し上げます。私が参っても、荷車一台分の薪を燃やした火に、ひしゃく一杯の水をかけるようなもの。","劉備",},
        [2245] = {20189501,"孟達","とても救えませぬ。","とても救えませぬ。","劉備",},
        [2246] = {20189502,"張郃","曹丕様も、よくしてくれた桓階や夏侯尚もみな世を去って、孟達は先が不安になったようね。","曹丕様も、よくしてくれた桓階や夏侯尚もみな世を去って、孟達は先が不安になったようね。","司馬懿",},
        [2247] = {20189502,"張郃","だから諸葛亮の誘いに乗って、蜀に寝返ろうとしているわ。","だから諸葛亮の誘いに乗って、蜀に寝返ろうとしているわ。","司馬懿",},
        [2248] = {20189502,"司馬懿","まさか……誰がそんなことを?","まさか……誰がそんなことを?","張郃",},
        [2249] = {20189502,"張郃","孟達と仲の悪い魏興太守の申儀が、ヤツの計画を漏らしてくれたの。","孟達と仲の悪い魏興太守の申儀が、ヤツの計画を漏らしてくれたの。","司馬懿",},
        [2250] = {20189502,"司馬懿","愚かな男だ。かつては劉備を棄て、今日はまた我が魏を棄てるとは、自ら死ににいくようなものではないか！","愚かな男だ。かつては劉備を棄て、今日はまた我が魏を棄てるとは、自ら死ににいくようなものではないか！","張郃",},
        [2251] = {20189502,"司馬懿","私がまず書簡を送り、孟達をなだめよう。同時に密かに軍を急行させ、これを亡ぼすのだ。","私がまず書簡を送り、孟達をなだめよう。同時に密かに軍を急行させ、これを亡ぼすのだ。","張郃",},
        [2252] = {20189601,"蒋幹","私めは幼いころから周瑜とは同窓の間柄です。呉の陣に赴き、舌先三寸で投降させましょう。","私めは幼いころから周瑜とは同窓の間柄です。呉の陣に赴き、舌先三寸で投降させましょう。","曹操",},
        [2253] = {20189601,"曹操","蒋幹は周瑜と親しい仲なのか?","蒋幹は周瑜と親しい仲なのか?","蒋幹",},
        [2254] = {20189601,"蒋幹","丞相、ご安心ください。呉に着けば必ず成功させてみせましょう。","丞相、ご安心ください。呉に着けば必ず成功させてみせましょう。","曹操",},
        [2255] = {20189601,"曹操","うむ、何か必要な物はあるか?","うむ、何か必要な物はあるか?","蒋幹",},
        [2256] = {20189601,"蒋幹","では、従者一人と舟のこぎ手二人を。その他は必要ございません。","では、従者一人と舟のこぎ手二人を。その他は必要ございません。","曹操",},
        [2257] = {20189602,"蒋幹","おお周瑜よ、元気にしておったか！","おお周瑜よ、元気にしておったか！","周瑜",},
        [2258] = {20189602,"周瑜","ご苦労でしたね、蒋幹。遠路、江湖を渡ってきたのですか。いかに曹操に説得役を頼まれたからとはいえ、骨が折れたでしょう。","ご苦労でしたね、蒋幹。遠路、江湖を渡ってきたのですか。いかに曹操に説得役を頼まれたからとはいえ、骨が折れたでしょう。","蒋幹",},
        [2259] = {20189602,"蒋幹","はははは、冗談はよせ。お前と別れて久しい故、懐かしくて来たまでだよ。","はははは、冗談はよせ。お前と別れて久しい故、懐かしくて来たまでだよ。","周瑜",},
        [2260] = {20189602,"周瑜","僕も師曠〔古代の楽人〕ほどの耳は持っておりませんが、弦楽を聞けば、その興趣は分かるというものです。","僕も師曠〔古代の楽人〕ほどの耳は持っておりませんが、弦楽を聞けば、その興趣は分かるというものです。","蒋幹",},
        [2261] = {20189602,"蒋幹","冗談でないとしたら、心外だな。旧友をそのように扱うなら、もう失礼させてもらう。","冗談でないとしたら、心外だな。旧友をそのように扱うなら、もう失礼させてもらう。","周瑜",},
        [2262] = {20189602,"周瑜","とんでもない。ただ曹操の説得役かも知れぬと案じたまでですよ。違うなら、なぜすぐに去ろうとするんです?","とんでもない。ただ曹操の説得役かも知れぬと案じたまでですよ。違うなら、なぜすぐに去ろうとするんです?","蒋幹",},
        [2263] = {20189603,"曹操","汝ら二人に進軍を命じる。","汝ら二人に進軍を命じる。","蔡瑁",},
        [2264] = {20189603,"蔡瑁","お待ち下さい。我が軍はまだ練兵も行き届いておらず、軽々しく進軍はできませぬ。","お待ち下さい。我が軍はまだ練兵も行き届いておらず、軽々しく進軍はできませぬ。","曹操",},
        [2265] = {20189603,"曹操","それを待てぬということだ。練兵が行き届けば、我が首級を周瑜に差し出す腹なのだろう！","それを待てぬということだ。練兵が行き届けば、我が首級を周瑜に差し出す腹なのだろう！","蔡瑁",},
        [2266] = {20189603,"蔡瑁","丞相、それはどのような意味なのですか?","丞相、それはどのような意味なのですか?","曹操",},
        [2267] = {20189603,"曹操","蒋幹が周瑜の天幕から、汝と張允が送った降伏書を見つけたのだ！","蒋幹が周瑜の天幕から、汝と張允が送った降伏書を見つけたのだ！","蔡瑁",},
        [2268] = {20189603,"蔡瑁","まさか！まったく見に覚えがございませぬ！","まさか！まったく見に覚えがございませぬ！","曹操",},
        [2269] = {20189603,"曹操","弁解などいらぬ。引っ立てて斬れ！","弁解などいらぬ。引っ立てて斬れ！","蔡瑁",},
        [2270] = {20189701,"王允","十万の黄巾軍を投降させた時、軍中から張譲、お前の書いた書簡を見つけた。","十万の黄巾軍を投降させた時、軍中から張譲、お前の書いた書簡を見つけた。","張譲",},
        [2271] = {20189701,"王允","そこにはお前が黄巾軍と関係があったことを示す内容が書かれていた。","そこにはお前が黄巾軍と関係があったことを示す内容が書かれていた。","張譲",},
        [2272] = {20189701,"王允","お前は黄巾軍と内通し、謀反を企てていたのではないか?","お前は黄巾軍と内通し、謀反を企てていたのではないか?","張譲",},
        [2273] = {20189701,"張譲","私は朝廷に忠誠を誓っております。黄巾軍と内通し朝廷に背く気など、毛筋ほどもありません。","私は朝廷に忠誠を誓っております。黄巾軍と内通し朝廷に背く気など、毛筋ほどもありません。","王允",},
        [2274] = {20189701,"張譲","さては王允、私に嫉妬し、陥れようとしているのでしょう！","さては王允、私に嫉妬し、陥れようとしているのでしょう！","王允",},
        [2275] = {20189702,"呂布","これはまずいぞ、王允殿！いったん馬で塞外へ逃れてから、良い策を考えよう。","これはまずいぞ、王允殿！いったん馬で塞外へ逃れてから、良い策を考えよう。","王允",},
        [2276] = {20189702,"王允","神の加護を得て国を安んずることが、私の願いだ。叶わぬとなれば、死を待つのみ。","神の加護を得て国を安んずることが、私の願いだ。叶わぬとなれば、死を待つのみ。","呂布",},
        [2277] = {20189702,"王允","危難を前に一人逃げるようなことはできぬ。関東の諸侯によろしく伝えてくれ。国のため努めてくれとな！","危難を前に一人逃げるようなことはできぬ。関東の諸侯によろしく伝えてくれ。国のため努めてくれとな！","呂布",},
        [2278] = {20189702,"呂布","司徒、いいから馬に乗ってくれ！","司徒、いいから馬に乗ってくれ！","王允",},
        [2279] = {20189702,"王允","乗らん！","乗らん！","呂布",},
        [2280] = {20189702,"呂布","そうか…では、御身お大事にな。","そうか…では、御身お大事にな。","王允",},
        [2281] = {20189801,"劉備","お互い漢王室の末裔です。私は北方で何度も敗北し、今こうして荊州に身を寄せてきました。","お互い漢王室の末裔です。私は北方で何度も敗北し、今こうして荊州に身を寄せてきました。","劉表",},
        [2282] = {20189801,"劉表","よく来られたな、劉備殿。もちろん歓迎しよう。","よく来られたな、劉備殿。もちろん歓迎しよう。","劉備",},
        [2283] = {20189801,"劉表","まずは新野に駐屯されては如何かな。","まずは新野に駐屯されては如何かな。","劉備",},
        [2284] = {20189801,"劉備","願ってもないことです。劉表殿のご厚意に感謝します。","願ってもないことです。劉表殿のご厚意に感謝します。","劉表",},
        [2285] = {20189802,"劉表","わしの病はもう治らぬ。死を待つのみだ。そこでわが子のことを劉備殿にお頼みしたい。","わしの病はもう治らぬ。死を待つのみだ。そこでわが子のことを劉備殿にお頼みしたい。","劉備",},
        [2286] = {20189802,"劉表","息子は才に乏しく、後は継げぬだろう。わしが死んだら、劉備殿が荊州を治めるがよい。","息子は才に乏しく、後は継げぬだろう。わしが死んだら、劉備殿が荊州を治めるがよい。","劉備",},
        [2287] = {20189802,"劉備","力の限りを尽くして若君を補佐いたしましょう。それ以上の野心はありませぬ。","力の限りを尽くして若君を補佐いたしましょう。それ以上の野心はありませぬ。","劉表",},
        [2288] = {20189901,"孫権","我が跡継ぎの問題では、波風が絶えぬな。臣下どもが派閥を作って争い、朝廷が安定せぬ。","我が跡継ぎの問題では、波風が絶えぬな。臣下どもが派閥を作って争い、朝廷が安定せぬ。","丁奉",},
        [2289] = {20189901,"孫権","だが、俺ももう年だ。そろそろ決めねばならん。呉は孫亮に託すことに決めたぞ。","だが、俺ももう年だ。そろそろ決めねばならん。呉は孫亮に託すことに決めたぞ。","丁奉",},
        [2290] = {20189901,"孫権","丁奉、お前は全力で孫亮を補佐してくれ。朝廷を安定させ、呉の平穏を保つのだ。","丁奉、お前は全力で孫亮を補佐してくれ。朝廷を安定させ、呉の平穏を保つのだ。","丁奉",},
        [2291] = {20189901,"丁奉","しかと承りました。この丁奉命に代えても、お守りします。","しかと承りました。この丁奉命に代えても、お守りします。","孫権",},
        [2292] = {20189902,"丁奉","孫亮様が帝位を継がれたばかりだが、魏の諸葛誕、胡遵らが東興に攻め入った。","孫亮様が帝位を継がれたばかりだが、魏の諸葛誕、胡遵らが東興に攻め入った。","",},
        [2293] = {20189902,"丁奉","朝廷を安定させ、呉の平穏を保つには、この戦いで必ず勝たねばならん。","朝廷を安定させ、呉の平穏を保つには、この戦いで必ず勝たねばならん。","",},
        [2294] = {20189902,"丁奉","将兵たちよ！功を上げる時が来たぞ！","将兵たちよ！功を上げる時が来たぞ！","",},
        [2295] = {20189902,"丁奉","私と共に敵陣に急行し、鎧を脱ぎ捨て、刀や剣だけ持って奇襲するのだ！","私と共に敵陣に急行し、鎧を脱ぎ捨て、刀や剣だけ持って奇襲するのだ！","",},
        [2296] = {201810001,"羊祜","これは私が最近調合した薬だが、まだ飲んでおらぬ。貴殿が病気と聞き、先にお持ちした。","これは私が最近調合した薬だが、まだ飲んでおらぬ。貴殿が病気と聞き、先にお持ちした。","陸抗",},
        [2297] = {201810001,"孫呉武将","将軍、裏があるやもしれません。飲んではなりませぬぞ。","将軍、裏があるやもしれません。飲んではなりませぬぞ。","陸抗",},
        [2298] = {201810001,"陸抗","羊祜が毒など盛るものか！","羊祜が毒など盛るものか！","",},
        [2299] = {201810001,"陸抗","我々はお互いに、やろうと思えばいくらでもやれるのだ。しかし、今までやらずに来たのは、分をわきまえているからだ。","我々はお互いに、やろうと思えばいくらでもやれるのだ。しかし、今までやらずに来たのは、分をわきまえているからだ。","",},
        [2300] = {201810001,"陸抗","小さな利益を求めても仕方ないとな。","小さな利益を求めても仕方ないとな。","",},
        [2301] = {201810002,"陸抗","国を建て、家業を継ぐためには、器の小さい者や性根の悪い者を用いたり、言うことを聞いたりしてはいけない。","国を建て、家業を継ぐためには、器の小さい者や性根の悪い者を用いたり、言うことを聞いたりしてはいけない。","",},
        [2302] = {201810002,"陸抗","彼らに朝廷の重責を委ね、専制の権威を与えれば、平和で正しい社会が現れるだろうか?絶対にそんなことはない。","彼らに朝廷の重責を委ね、専制の権威を与えれば、平和で正しい社会が現れるだろうか?絶対にそんなことはない。","",},
        [2303] = {201810002,"陸抗","今、官職につき、特殊な才を持つ者は少ないが、彼らのうちさらに少数の者は王室貴族の末裔で、","今、官職につき、特殊な才を持つ者は少ないが、彼らのうちさらに少数の者は王室貴族の末裔で、","",},
        [2304] = {201810002,"陸抗","幼いころより道徳の教育を受けている。","幼いころより道徳の教育を受けている。","",},
        [2305] = {201810002,"陸抗","またそういう教育を受けなくとも、苦労を重ねて自立した者もおり、その資質と才能は任用に値する。","またそういう教育を受けなくとも、苦労を重ねて自立した者もおり、その資質と才能は任用に値する。","",},
        [2306] = {201810002,"陸抗","彼らの才に応じて官職に任命すれば、小賢しい者たちを抑え、罷免することができる。","彼らの才に応じて官職に任命すれば、小賢しい者たちを抑え、罷免することができる。","",},
        [2307] = {201810002,"陸抗","そうして初めて、社会の風潮は清められ、朝廷の政務も汚されなくなるのだ。","そうして初めて、社会の風潮は清められ、朝廷の政務も汚されなくなるのだ。","",},
        [2308] = {1701,"主君","今回、平原県の兄者に書簡を届けに行けば、趙雲将軍に会えるかもしれないぞ！兄者と仲が良いらしいからな。","今回、平原県の兄さんに書簡を届けに行けば、趙雲将軍に会えるかもしれないわよ！兄さんと仲が良いらしいからね。","関銀屏",},
        [2309] = {1701,"関銀屏","まあ、本当に?感激ですわ！","まあ、本当に?感激ですわ！","",},
        [2310] = {1701,"張星彩","むむ～銀屏姉さまったら、浮気性なんだから。","むむ～銀屏姉さまったら、浮気性なんだから。","",},
        [2311] = {1701,"関銀屏","白い鎧に身を包み、銀の槍を燦めかせ、愛馬白龍にまたがって颯爽と乱世を駆ける、一身これ胆の勇将ですもの！","白い鎧に身を包み、銀の槍を燦めかせ、愛馬白龍にまたがって颯爽と乱世を駆ける、一身これ胆の勇将ですもの！","",},
        [2312] = {1701,"関銀屏","趙雲将軍は若くて見た目も素敵なだけでなく、戦いにも優れているのです。憧れるのも当然ですわ！","趙雲将軍は若くて見た目も素敵なだけでなく、戦いにも優れているのです。憧れるのも当然ですわ！","",},
        [2313] = {1701,"張星彩","はいはい、そうね！でも星彩が世界で一番憧れてる人は、ご主君様だもん！","はいはい、そうね！でも星彩が世界で一番憧れてる人は、ご主君様だもん！","",},
        [2314] = {1701,"主君","あまり褒めるなよ、星彩。安心しろ、お前を失望させたりしないよ。","あまり褒めないでよ、星彩。安心して、あなたを失望させたりしないわ。","",},
        [2315] = {1702,"主君","田楷殿、曹操が徐州を攻める件は聞き及んでいるでしょう。北海の孔融殿は既に徐州への救援に同意しました。あなたはどうします?","田楷殿、曹操が徐州を攻める件は聞き及んでいるでしょう。北海の孔融殿は既に徐州への救援に同意しました。あなたはどうします?","田楷",},
        [2316] = {1702,"田楷","徐州と青州は、非常に密接な関係にあります。","徐州と青州は、非常に密接な関係にあります。","",},
        [2317] = {1702,"田楷","道理から言えば、我が青州は助けるべきですが、目下冀州の袁紹と戦っており、うかつに兵力を分散するのは良くないかと……","道理から言えば、我が青州は助けるべきですが、目下冀州の袁紹と戦っており、うかつに兵力を分散するのは良くないかと……","",},
        [2318] = {1702,"田楷","かと言って何もしなければ、徐州を落とした曹操は、すぐに青州を攻めるでしょう……一体どうすれば……","かと言って何もしなければ、徐州を落とした曹操は、すぐに青州を攻めるでしょう……一体どうすれば……","",},
        [2319] = {1702,"主君","優柔不断は最も悪い結果を招きます。行くのか、行かないのか、はっきりしてください！","優柔不断は最も悪い結果を招きます。行くのか、行かないのか、はっきりしてください！","",},
        [2320] = {1702,"田楷","落ち着いてください、若き英雄。曹操の勢いは袁紹よりも大きい。田楷は徐州を支援致します！","落ち着いてください、若き英雄。曹操の勢いは袁紹よりも大きい。田楷は徐州を支援致します！","",},
        [2321] = {1702,"主君","よーし、そうこなくては！","よーし、そうこなくちゃね！","",},
        [2322] = {1703,"主君","兄者、曹操が徐州を攻めようとしています。それで陶謙殿を助けようと北海の孔融殿に出兵を頼みに行ったのですが、北海は黄巾に包囲されているんです。","兄さん、曹操が徐州を攻めようとしています。それで陶謙殿を助けようと北海の孔融殿に出兵を頼みに行ったのですが、北海は黄巾に包囲されているんです。","劉備",},
        [2323] = {1703,"主君","そこで孔融殿から書簡を預かって来ました。兄者に兵を率い、徐州を援助してほしいとのことです。","そこで孔融殿から書簡を預かって来ました。兄さんに兵を率い、徐州を援助してほしいとのことです。","劉備",},
        [2324] = {1703,"劉備","北海の孔融殿がこの劉備を知っていたとは。だが、私の平原県の兵力だけでは足りぬのではないか?","北海の孔融殿がこの劉備を知っていたとは。だが、私の平原県の兵力だけでは足りぬのではないか?","",},
        [2325] = {1703,"主君","慌てないでください、兄者。我々が公孫瓚から三千の兵馬と趙雲将軍を借り、一緒に連れていくのはどうでしょう?","慌てないでください、兄さん。我々が公孫瓚から三千の兵馬と趙雲将軍を借り、一緒に連れていくのはどうですか?","",},
        [2326] = {1703,"劉備","義弟よ、よく考えてくれたな。すぐに公孫瓚のところへ行こう。","義妹よ、よく考えてくれたな。すぐに公孫瓚のところへ行こう。","",},
        [2327] = {1704,"公孫瓚","曹操とそなたには何の怨恨もないはず。なぜ苦労して他人のために兵を出すのだ?","曹操とそなたには何の怨恨もないはず。なぜ苦労して他人のために兵を出すのだ?","劉備",},
        [2328] = {1704,"劉備","孔融殿とはもう約束したのです。違えるわけにはいきません。","孔融殿とはもう約束したのです。違えるわけにはいきません。","",},
        [2329] = {1704,"公孫瓚","劉備よ、いささか仁徳を重んじ過ぎるのではないか。それでは方々に敵を作るぞ。","劉備よ、いささか仁徳を重んじ過ぎるのではないか。それでは方々に敵を作るぞ。","",},
        [2330] = {1704,"公孫瓚","三千の精兵は貸してやるが、趙雲については、本人のお眼鏡に適わねばならんぞ。","三千の精兵は貸してやるが、趙雲については、本人のお眼鏡に適わねばならんぞ。","",},
        [2331] = {1704,"主君","兄者、趙雲将軍のお相手は私にさせてください！さっきからうずうずしているんです。達人との手合わせは久しぶりですからね！","兄さん、趙雲将軍のお相手は私にさせてくださいませんか?さっきからうずうずしているんです。達人との手合わせは久しぶりですからね！","",},
        [2332] = {1705,"趙雲","これが、若き英雄の武芸か……なるほど、先が楽しみだ！","これが、若き英雄の武芸か……なるほど、先が楽しみだ！","プレイヤー",},
        [2333] = {1705,"主君","痛み入ります、趙雲将軍。兄者と共に徐州の救援に行っていただけますか?","痛み入ります、趙雲将軍。兄さんと共に徐州の救援に行っていただけますか?","",},
        [2334] = {1705,"趙雲","もちろんだとも！私との手合わせも合格だ。この常山の趙雲が、約束を違えるものか！","もちろんだとも！私との手合わせも合格だ。この常山の趙雲が、約束を違えるものか！","",},
        [2335] = {1705,"趙雲","ましてや、信頼する劉備殿の義弟の頼みとあってはな。この名将令は、初対面の挨拶代わりだ。すぐに軍馬を用意しよう。","ましてや、信頼する劉備殿の義妹の頼みとあってはな。この名将令は、初対面の挨拶代わりだ。すぐに軍馬を用意しよう。","",},
        [2336] = {1705,"主君","やったぞ！田楷殿と孔融殿の助けを得た上に、趙雲将軍と兄者の軍馬があれば、曹操を撤退させられるに違いない！","やったわ！田楷殿と孔融殿の助けを得た上に、趙雲将軍と兄さんの軍馬があれば、曹操を撤退させられるに違いないわ！","",},
        [2337] = {1705,"張星彩","すごいすごい、ご主君様っ！天下に解決できないことなんてないのね！","すごいすごい、ご主君様っ！天下に解決できないことなんてないのね！","",},
        [2338] = {1705,"主君","すべてうまくいったよ！星彩、銀屏、早く徐州へ帰ろう！","すべてうまくいったわ！星彩、銀屏、早く徐州へ帰るわよ！","",},
        [2339] = {1801,"曹操","呂布、わしとお前の間にはなんの怨恨もないはずだ。なのになぜわしが出兵した隙をついて、兗州、濮陽の二城を奪った?","呂布、わしとお前の間にはなんの怨恨もないはずだ。なのになぜわしが出兵した隙をついて、兗州、濮陽の二城を奪った?","呂布",},
        [2340] = {1801,"呂布","陳宮と張邈の二人が俺を兗州牧として迎えたいと言ったのだ。俺の実力だ！漢の領土はお前だけのものではない！","陳宮と張邈の二人が俺を兗州牧として迎えたいと言ったのだ。俺の実力だ！漢の領土はお前だけのものではない！","",},
        [2341] = {1801,"曹操","恐れを知らぬヤツめ！わしが濮陽城に突撃した暁には、自ら首を落としてやる！","恐れを知らぬヤツめ！わしが濮陽城に突撃した暁には、自ら首を落としてやる！","",},
        [2342] = {1801,"呂布","この戦神・呂布様が、お前など恐れるものか！濮陽に来たら、生きては帰れぬぞ！","この戦神・呂布様が、お前など恐れるものか！濮陽に来たら、生きては帰れぬぞ！","",},
        [2343] = {1802,"張星彩","ご主君様、曹操と呂布が戦いを始めたわ！","ご主君様、曹操と呂布が戦いを始めたわ！","プレイヤー",},
        [2344] = {1802,"張星彩","さっき曹操の使者が来たの。徐州から撤兵するから、代わりに濮陽を助けに来てほしいって。","さっき曹操の使者が来たの。徐州から撤兵するから、代わりに濮陽を助けに来てほしいって。","プレイヤー",},
        [2345] = {1802,"主君","呂布が曹操の城を二つも奪うなんて！いつの間に、それだけの兵力を持つようになったんだ?","呂布が曹操の城を二つも奪うなんて！いつの間に、それだけの兵力を持つようになったのかしら?","",},
        [2346] = {1802,"張星彩","李傕と郭汜が長安で乱を起こした後、呂布は袁術のもとに身を寄せたの。","李傕と郭汜が長安で乱を起こした後、呂布は袁術のもとに身を寄せたの。","",},
        [2347] = {1802,"張星彩","でも袁術は心が狭いし、呂布も野心バリバリだったから、長く続かなかったのね。","でも袁術は心が狭いし、呂布も野心バリバリだったから、長く続かなかったのね。","",},
        [2348] = {1802,"張星彩","それでその後、曹操が徐州を攻めに行ったとき、陳留太守の張邈と陳宮が呂布を迎え入れて、兗州牧にしたんだって。","それでその後、曹操が徐州を攻めに行ったとき、陳留太守の張邈と陳宮が呂布を迎え入れて、兗州牧にしたんだって。","",},
        [2349] = {1802,"主君","私が撤退を求めたときは避けたくせに、自分から助けを求めてくるとはな……釈然としないが、仕方ない。もう一度助けてやろう！","私が撤退を求めたときは避けたくせに、自分から助けを求めてくるなんてね……釈然としないけど、仕方ない。もう一度助けてやるわ！","",},
        [2350] = {1803,"張邈","濮陽を騒がせに来たのは誰だ?曹操が遣わした間諜か?","濮陽を騒がせに来たのは誰だ?曹操が遣わした間諜か?","プレイヤー",},
        [2351] = {1803,"張星彩","ご主君様、張邈は清流派の名士で「八厨」の一人よ。戦うなら気を付けてね。","ご主君様、張邈は清流派の名士で「八厨」の一人よ。戦うなら気を付けてね。","",},
        [2352] = {1803,"主君","八厨というと、厨房の料理人か?うまい料理を作ってくれるのか?","八厨って、厨房の料理人?おいしい料理を作ってくれるの?","",},
        [2353] = {1803,"張星彩","ぶっぶー、違うわよ！「八厨」っていうのは、私財をなげうって人々を救った八人の名士の呼び名。","ぶっぶー、違うわよ！「八厨」っていうのは、私財をなげうって人々を救った八人の名士の呼び名。","",},
        [2354] = {1803,"張星彩","張邈は若いころ、自腹を切って貧しい人を救ったり、人助けをするのが好きだったんだって。","張邈は若いころ、自腹を切って貧しい人を救ったり、人助けをするのが好きだったんだって。","",},
        [2355] = {1803,"張星彩","その男気に惚れて、たくさんの名士がついてきたの。曹操や袁紹も友達だったらしいわ。","その男気に惚れて、たくさんの名士がついてきたの。曹操や袁紹も友達だったらしいわ。","",},
        [2356] = {1803,"張星彩","そんな感じで、張邈の名声はどんどん上がっていったから、朝廷は官職を授けて陳留太守としたのよ。","そんな感じで、張邈の名声はどんどん上がっていったから、朝廷は官職を授けて陳留太守としたのよ。","",},
        [2357] = {1803,"張邈","ふん！我が輩が包丁を持っているからと言って、料理人だと思うなよ！こいつは伝家の宝刀なのだ！","ふん！我が輩が包丁を持っているからと言って、料理人だと思うなよ！こいつは伝家の宝刀なのだ！","",},
        [2358] = {1803,"主君","張邈、なかなかの実力だったが、私には一歩及ばなかったようだな。","張邈、なかなかの実力だったけど、私には一歩及ばなかったようね。","",},
        [2359] = {1804,"張邈","この私に勝つとは……将軍には恐れ入った！この宝刀を差し上げよう！","この私に勝つとは……将軍には恐れ入った！この宝刀を差し上げよう！","プレイヤー",},
        [2360] = {1804,"主君","遠慮しておくよ。さっきの戦いで曲がってるじゃないか。取っておいて葱でも切るんだな！","遠慮しておくわ。さっきの戦いで曲がってるじゃない。取っておいて葱でも切るのね！","",},
        [2361] = {1804,"張邈","ぶはは！「八厨」と呼ばれたこの名士の張邈が、嫌われる日が来るとはな！","ぶはは！「八厨」と呼ばれたこの名士の張邈が、嫌われる日が来るとはな！","",},
        [2362] = {1805,"于禁","我が君は城外でお待ちを。我々が先に入城致しましょう！","我が君は城外でお待ちを。我々が先に入城致しましょう！","曹操",},
        [2363] = {1805,"曹操","ダメだ！わしが行かねば、誰が前に進めよう！","ダメだ！わしが行かねば、誰が前に進めよう！","于禁",},
        [2364] = {1805,"主君","曹操はどうした?瞬きする間にいなくなったぞ。さっきは誰かと話す声が聞こえたのだが……","曹操はどうしたの?瞬きする間にいなくなったわよ。さっきは誰かと話す声が聞こえたのに……","張星彩",},
        [2365] = {1805,"張星彩","ご主君様、曹操は陳宮と呂布の奸計に乗って、兵を率いて城内に突撃したわ！","ご主君様、曹操は陳宮と呂布の奸計に乗って、兵を率いて城内に突撃したわ！","",},
        [2366] = {1805,"張星彩","城内は炎が上がって、煙が立ちこめてる。曹操はどうなっちゃったのかしら。さっき楽進や李典、典韋たちが必死で捜し回っていたけど……","城内は炎が上がって、煙が立ちこめてる。曹操はどうなっちゃったのかしら。さっき楽進や李典、典韋たちが必死で捜し回っていたけど……","",},
        [2367] = {1805,"主君","よし。呂布を討って、曹操を救おう！","よし。呂布を討って、曹操を救うわよ！","",},
        [2368] = {1806,"呂布","はは、この一戦は愉快だった！若き英雄よ、俺たちは身内同然だ。","はは、この一戦は愉快だった！若き英雄よ、俺たちは身内同然だ。","プレイヤー",},
        [2369] = {1806,"呂布","お前がいなければ、俺と貂蝉の今はないからな！今日はお前に免じて、曹操を放してやろう！","お前がいなければ、俺と貂蝉の今はないからな！今日はお前に免じて、曹操を放してやろう！","プレイヤー",},
        [2370] = {1806,"主君","ありがとう。さすがの豪傑だな！","ありがとう。さすがの豪傑ね！","呂布",},
        [2371] = {1806,"曹操","さっきは城門から燃えた梁が落ちてきて、わしの腕に当たった。腕だけではなく、髪や髭も燃えてしまったわ！","さっきは城門から燃えた梁が落ちてきて、わしの腕に当たった。腕だけではなく、髪や髭も燃えてしまったわ！","プレイヤー",},
        [2372] = {1806,"曹操","若き英雄が駆け付け、呂布を討ってくれねば、危ないところだった！この名将令は感謝の印に贈ろう！","若き英雄が駆け付け、呂布を討ってくれねば、危ないところだった！この名将令は感謝の印に贈ろう！","",},
        [2373] = {1806,"主君","曹操兄、約束どおり、徐州から撤兵していただきたい！","曹操兄さん、約束どおり、徐州から撤兵してもらえるかしら！","",},
        [2374] = {1806,"曹操","安心しろ。とっくに撤兵させるよう使者を送っておる。前回会わなかったことはこの通りだ。許してくれ。","安心しろ。とっくに撤兵させるよう使者を送っておる。前回会わなかったことはこの通りだ。許してくれ。","",},
        [2375] = {1806,"主君","もちろん。約束を守ってくれるなら、細かいことには拘りません。では失礼、徐州の兄者のところへ帰ります。","もちろんです。約束を守ってくださるなら、細かいことには拘りませんわ。では失礼、徐州の兄さんのところへ帰ります。","",},
        [2376] = {1806,"曹操","気を付けるのだぞ、若き英雄よ！この度は凡夫の謀にはまったが、この濮陽城は、いずれ奪い返してみせる！","気を付けるのだぞ、若き英雄よ！この度は凡夫の謀にはまったが、この濮陽城は、いずれ奪い返してみせる！","",},
        [2377] = {1901,"陶謙","誰か、早く徐州の印綬をここへ！","誰か、早く徐州の印綬をここへ！","劉備",},
        [2378] = {1901,"劉備","どういうことですか、陶謙殿?","どういうことですか、陶謙殿?","",},
        [2379] = {1901,"陶謙","徐州をお譲りしたいと思うのです、劉備殿。","徐州をお譲りしたいと思うのです、劉備殿。","",},
        [2380] = {1901,"陶謙","あなたの堂々たる姿に優れた弁舌、高い徳と才能を持ち、さらに漢王室の末裔であられる。","あなたの堂々たる姿に優れた弁舌、高い徳と才能を持ち、さらに漢王室の末裔であられる。","",},
        [2381] = {1901,"劉備","それはお受けできませぬ。私は確かに漢王室の末裔ですが、功績や徳などわずかなもの。なぜ袁紹殿にお譲りにならぬのです?","それはお受けできませぬ。私は確かに漢王室の末裔ですが、功績や徳などわずかなもの。なぜ袁紹殿にお譲りにならぬのです?","",},
        [2382] = {1901,"陶謙","袁紹など、無能で歯牙にもかけておりませぬわ。貴殿とは比べ物にならない！","袁紹など、無能で歯牙にもかけておりませぬわ。貴殿とは比べ物にならない！","",},
        [2383] = {1901,"劉備","この度、私が徐州を救いに来たのは、大義のためです。私心などまったくありませぬ。","この度、私が徐州を救いに来たのは、大義のためです。私心などまったくありませぬ。","",},
        [2384] = {1901,"劉備","陶謙殿のお申し出、なにとぞご容赦ください。","陶謙殿のお申し出、なにとぞご容赦ください。","",},
        [2385] = {1902,"陶謙","若き英雄よ、そなたは若くして才に溢れ、大志を抱いておる。一つ陶謙の頼みを聞いてくれぬか。","若き英雄よ、そなたは若くして才に溢れ、大志を抱いておる。一つ陶謙の頼みを聞いてくれぬか。","プレイヤー",},
        [2386] = {1902,"主君","わかりました、何なりと仰ってください。","ええ、何なりと仰ってください。","",},
        [2387] = {1902,"陶謙","いま天下は乱れておる。ワシも歳を取り、明日をも知れぬ身となった。","いま天下は乱れておる。ワシも歳を取り、明日をも知れぬ身となった。","",},
        [2388] = {1902,"陶謙","そこで、この徐州太守の位を劉備殿に譲ろうとしたのじゃが、どうしても受けてくれぬのじゃ。そなたが説得してくれぬか。","そこで、この徐州太守の位を劉備殿に譲ろうとしたのじゃが、どうしても受けてくれぬのじゃ。そなたが説得してくれぬか。","",},
        [2389] = {1902,"主君","なぜご子息に譲らないんですか?","なぜご子息にお譲りなさらないのですか?","",},
        [2390] = {1902,"陶謙","はぁ、それが、長男の商、次男の応とも不才で、とてもこの任には堪えぬのじゃ。","はぁ、それが、長男の商、次男の応とも不才で、とてもこの任には堪えぬのじゃ。","",},
        [2391] = {1902,"陶謙","一体どうすればよいやら……(言い終わると涙を流す)","一体どうすればよいやら……(言い終わると涙を流す)","",},
        [2392] = {1902,"主君","わかりました、陶謙殿。今すぐ兄者を説得してきます。","わかりました、陶謙殿。今すぐ兄さんを説得してきます。","",},
        [2393] = {1903,"関羽","義弟よ、兄者はお前が説得に来ると知って、わしと張飛に止めよと命じられた。","義妹よ、兄者はお前が説得に来ると知って、わしと張飛に止めよと命じられた。","プレイヤー",},
        [2394] = {1903,"主君","関羽兄者、申し訳ない。この機会に一度お手合わせを願いたい。","関羽兄さん、申し訳ない。この機会に一度お手合わせを願うわ。","",},
        [2395] = {1904,"張星彩","どうしちゃったの、父さま！ご主君様の邪魔をするなんて?","どうしちゃったの、父さま！ご主君様の邪魔をするなんて?","張飛",},
        [2396] = {1904,"張飛","兄貴の命令は絶対でな。悪く思うんじゃねえぞ。","兄貴の命令は絶対でな。悪く思うんじゃねえぞ。","",},
        [2397] = {1904,"張星彩","思うわよ！ご主君様を傷つけたら許さないんだから！","思うわよ！ご主君様を傷つけたら許さないんだから！","",},
        [2398] = {1904,"張飛","分かった分かった、可愛い娘の頼みだ。寸止めで勘弁してやるぜ！","分かった分かった、可愛い娘の頼みだ。寸止めで勘弁してやるぜ！","",},
        [2399] = {1905,"主君","兄者、この徐州は常に各勢力に狙われています。北には曹操が兗州を占め、兵も糧食も豊富です。","兄さん、この徐州は常に各勢力に狙われています。北には曹操が兗州を占め、兵も糧食も豊富です。","劉備",},
        [2400] = {1905,"主君","南には袁術が虎視眈々と狙っています。もし兄者が徐州を取らねば、民は窮地に立たされます。","南には袁術が虎視眈々と狙っています。もし兄さんが徐州を取らねば、民は窮地に立たされますわ。","劉備",},
        [2401] = {1905,"劉備","そうだな、義弟の言うとおりだ。私の考えが古かったようだ。だが、私一人で、このような大任に当たれるだろうか?","そうだな、義妹の言うとおりだ。私の考えが古かったようだ。だが、私一人で、このような大任に当たれるだろうか?","",},
        [2402] = {1905,"主君","徐州の孫乾、麋竺、陳登が補佐してくれるでしょう。もちろん、我ら兄弟もいます。どうか引き受けてください！","徐州の孫乾、麋竺、陳登が補佐するでしょう。もちろん、我ら兄妹もいます。決して辞退してはなりませんわ！","",},
        [2403] = {1905,"劉備","分かった。ならば義弟の言うことを聞こう。","分かった。ならば義妹の言うことを聞こう。","",},
        [2404] = {1905,"劉備","義弟よ、この名将令は陶謙殿がお前にと、亡くなる前に託された。曹操を撤兵させ、徐州の急を救ったことへの感謝の印だそうだ。","義妹よ、この名将令は陶謙殿がお前にと、亡くなる前に託された。曹操を撤兵させ、徐州の急を救ったことへの感謝の印だそうだ。","",},
        [2405] = {1905,"主君","ありがとうございます、兄者！遠慮なく受け取ります！","ありがとうございます、兄さん！遠慮なく受け取りますわ！","",},
        [2406] = {2001,"主君","兄者も徐州に落ち着かれたし、ここで私はお別れします。長安が今どうなってるか見たいので。","兄さんも徐州に落ち着かれたし、ここで私はお別れします。長安が今どうなってるか見たいので。","",},
        [2407] = {2001,"主君","なんてことだ……！董卓を倒しに来た時、あれほど賑やかだった長安が……！星彩、何が起こったか知っているか?","なんてこと……！董卓を倒しに来た時、あれほど賑やかだった長安が……！星彩、何が起こったか知っている?","張星彩",},
        [2408] = {2001,"張星彩","李傕と郭汜が長安を占領した後、天子を擁して朝政を握ったの。でもお互いに疑りあって、内乱になって。","李傕と郭汜が長安を占領した後、天子を擁して朝政を握ったの。でもお互いに疑りあって、内乱になって。","",},
        [2409] = {2001,"張星彩","それが数ヶ月も続いたせいで、死人が何万人も出るし、長安はほとんど廃墟になっちゃったのよ。","それが数ヶ月も続いたせいで、死人が何万人も出るし、長安はほとんど廃墟になっちゃったのよ。","",},
        [2410] = {2001,"関銀屏","ふん、悪人同士の醜い争いですわね！最初は賈詡のやつが、李傕・郭汜をけん制して、百官や天子を守るって言ってませんでした?","ふん、悪人同士の醜い争いですわね！最初は賈詡のやつが、李傕・郭汜をけん制して、百官や天子を守るって言ってませんでした?","",},
        [2411] = {2001,"主君","行こう、李傕と郭汜を見つけ出して倒すんだ。賈詡を探して天子をお助けしよう。","行きましょう、李傕と郭汜を見つけ出して倒すのよ。賈詡を探して天子をお助けしましょう。","",},
        [2412] = {2002,"献帝","若き英雄よ、そなたは賈詡を誤解しておる。賈詡が間に入って動いてくれねば、李傕と郭汜はとっくに朕を殺していた。","若き英雄よ、そなたは賈詡を誤解しておる。賈詡が間に入って動いてくれねば、李傕と郭汜はとっくに朕を殺していた。","プレイヤー",},
        [2413] = {2002,"主君","そうでしたか。賈詡、皇帝に免じて許してやろう。","そうでしたか。賈詡、皇帝に免じて許してあげるわ。","",},
        [2414] = {2002,"賈詡","陛下、李傕と郭汜は今や私を殺す気です。私は長安を離れねばなりません。","陛下、李傕と郭汜は今や私を殺す気です。私は長安を離れねばなりません。","",},
        [2415] = {2002,"献帝","賈詡も行ってしまった。これからは誰が朕を守ってくれるのだ?こんな惨めな皇帝はおらぬな……","賈詡も行ってしまった。これからは誰が朕を守ってくれるのだ?こんな惨めな皇帝はおらぬな……","",},
        [2416] = {2002,"主君","陛下、ここ長安は災いの地です。洛陽に戻るのはいかがでしょう。","陛下は東の洛陽にお帰りになるのが宜しいかと。災いの多い長安を離れるのです。","",},
        [2417] = {2002,"献帝","朕も洛陽はずっと恋しく思っているが、李傕、郭汜が同意すまい。","朕も洛陽はずっと恋しく思っているが、李傕、郭汜が同意すまい。","",},
        [2418] = {2002,"主君","ご心配なさらず。郭汜はすでに私が追い払いましたし、今すぐ李傕のところへも行って参ります。","ご心配なさらず。郭汜はすでに私が追い払いましたし、今すぐ李傕のところへも行って参ります。","",},
        [2419] = {2002,"主君","たとえ同意しなくても、させて御覧に入れましょう。","たとえ同意しなくても、させて御覧に入れますわ。","",},
        [2420] = {2003,"関銀屏","楊奉さんは漢朝の臣でしょう?どうして陛下をお守りせずに、悪人の手助けばかりなさいますの?","楊奉さんは漢朝の臣でしょう?どうして陛下をお守りせずに、悪人の手助けばかりなさいますの?","楊奉",},
        [2421] = {2003,"関銀屏","李傕は欲張りで頭が悪いだけじゃなく、妖術好きと来ていますわ。","李傕は欲張りで頭が悪いだけじゃなく、妖術好きと来ていますわ。","",},
        [2422] = {2003,"関銀屏","将兵に褒美を与えないで、巫女ばかり可愛がってるのでしょう?こんな最低の男について行っても、良いことはありませんわよ?","将兵に褒美を与えないで、巫女ばかり可愛がってるのでしょう?こんな最低の男について行っても、良いことはありませんわよ?","",},
        [2423] = {2003,"楊奉","その通りだ！奴に従って命を懸けたのに、俺の功労は巫女にも劣るというのか?待っていろ、兵馬を集めて、反乱を起こしてやる。","その通りだ！奴に従って命を懸けたのに、俺の功労は巫女にも劣るというのか?待っていろ、兵馬を集めて、反乱を起こしてやる。","",},
        [2424] = {2003,"関銀屏","やりましたわ！大将を一人反逆させました！楊奉の軍馬があれば、ご主君様は枕を高くして寝られますわね。","やりましたわ！大将を一人反逆させました！楊奉の軍馬があれば、ご主君様は枕を高くして寝られますわね。","プレイヤー",},
        [2425] = {2003,"主君","楊奉自身は大したことなさそうだが、部下で大斧を持った大将は中々の人物のようだ。あれは誰だ?","楊奉自身は大したことなさそうだけど、部下で大斧を持った大将は中々の人物のようね。あれは誰?","",},
        [2426] = {2003,"関銀屏","あれは河東郡楊県の人で、姓は徐、名は晃、字は公明という武将ですわ。","あれは河東郡楊県の人で、姓は徐、名は晃、字は公明という武将ですわ。","",},
        [2427] = {2003,"主君","なんと！あの名将、徐晃だったのか。楊奉の配下に置いておくのは惜しいな。","へえ！あの名将、徐晃だったのね。楊奉の配下に置いておくのは惜しいわね。","",},
        [2428] = {2004,"献帝","朕はなんと哀れな皇帝だ。先には董卓に権力を握られ、洛陽を焼かれ、無理やり長安へ連れていかれた。","朕はなんと哀れな皇帝だ。先には董卓に権力を握られ、洛陽を焼かれ、無理やり長安へ連れていかれた。","董承",},
        [2429] = {2004,"献帝","奴が死んだと思えば、今度は李傕、郭汜に長安を乱された……","奴が死んだと思えば、今度は李傕、郭汜に長安を乱された……","董承",},
        [2430] = {2004,"献帝","あちこちを流れ彷徨い、散々に苦難を味わってようやく洛陽へ戻れば、今度は韓暹が功をかさに着てやりたい放題。","あちこちを流れ彷徨い、散々に苦難を味わってようやく洛陽へ戻れば、今度は韓暹が功をかさに着てやりたい放題。","",},
        [2431] = {2004,"献帝","その上、飢饉に遭い、朕に従ってくれる臣下は食事も満足にできない有様、ああ！","その上、飢饉に遭い、朕に従ってくれる臣下は食事も満足にできない有様、ああ！","",},
        [2432] = {2004,"董承","陛下、私に良い考えがあります。兗州牧の曹操に密かに詔を出して呼び寄せ、王室を補佐させるのです。","陛下、私に良い考えがあります。兗州牧の曹操に密かに詔を出して呼び寄せ、王室を補佐させるのです。","",},
        [2433] = {2004,"董承","曹操は兵も糧食も豊富なので、今後の心配はなくなります。","曹操は兵も糧食も豊富なので、今後の心配はなくなります。","",},
        [2434] = {2004,"主君","そんなの簡単です。曹操は私の昔馴染み、行って話をしてきましょう。","そんなの簡単です。曹操は私の昔馴染み、行って話をしてきましょう。","",},
        [2435] = {2004,"董承","ありがたい申し出だが、楊奉が納得せんだろう。奴の配下の大将、徐晃は手強いぞ。","ありがたい申し出だが、楊奉が納得せんだろう。奴の配下の大将、徐晃は手強いぞ。","",},
        [2436] = {2004,"主君","楊奉、韓暹は取るに足りませんが、徐晃はなかなかの将です。会って話をしてきましょう。","楊奉、韓暹は取るに足りませんが、徐晃はなかなかの将ですわ。会って話をしてきましょう。","",},
        [2437] = {2005,"主君","徐晃将軍。あなたの勇気と知略は世に稀に見るものだ。なぜ楊の配下に甘んじ、韓に使われている?","徐晃将軍。あなたの勇気と知略は世に稀に見るものよ。なぜ楊の配下に甘んじ、韓に使われているの?","徐晃",},
        [2438] = {2005,"徐晃","もとより、楊奉、韓暹は大事を成す者ではないと知っている。だが長年付き従っているので、棄てるに忍びないのだ。","もとより、楊奉、韓暹は大事を成す者ではないと知っている。だが長年付き従っているので、棄てるに忍びないのだ。","",},
        [2439] = {2005,"主君","良禽は木を選んで棲む、賢臣は主を選んで仕う。","良禽は木を選んで棲む、賢臣は主を選んで仕う。","",},
        [2440] = {2005,"主君","兗州牧の曹操は当世の英雄で、賢者や勇士を好み、礼遇しますよ。帰順してはどうです?","兗州牧の曹操は当世の英雄で、賢者や勇士を好み、礼遇しますよ。帰順してはどうです?","",},
        [2441] = {2005,"徐晃","うむ。若き英雄の一言で夢から覚めた気分だ。感謝する。この名将令は礼の印だ。(徐晃、去る)","うむ。若き英雄の一言で夢から覚めた気分だ。感謝する。この名将令は礼の印だ。(徐晃、去る)","",},
        [2442] = {2005,"主君","皇帝が東の洛陽に帰られたとはいえ、こう毎年天災があっては……","皇帝が東の洛陽に帰られたとはいえ、こう毎年天災があってね……","関銀屏",},
        [2443] = {2005,"主君","どの州も郡も帰属せず、お守りする軍隊もない。それでは長続きしないだろう。","お仕えする州郡も、お守りする軍隊もない。それでは長続きしないわね、きっと。","関銀屏",},
        [2444] = {2005,"関銀屏","この皇帝は本当に内向きですわね。やっぱりご主君様がいいですわ。自由気ままで、誰にも侮られないですもの。","この皇帝は本当に内向きですわね。やっぱりご主君様がいいですわ。自由気ままで、誰にも侮られないですもの。","",},
        [2445] = {2005,"主君","まあ、仕方ないさ。国を背負う皇帝なんだからな。最強を目指す私とは、生き方が違って当たり前だ。","まあ、仕方ないね。国を背負う皇帝なんだもの。最強を目指す私とは、生き方が違って当たり前よ。","",},
        [2446] = {2005,"関銀屏","ご主君様、次はどこへ行きます?","ご主君様、次はどこへ行きます?","",},
        [2447] = {2005,"主君","そうだな……ついて来てくれ。面白いものを見せてやる。","そうね……ついて来て。面白いものを見せてあげるわ。","",},
        [2448] = {2101,"張星彩","ご主君様、わたしたち、どこへ向かってるの?","ご主君様、わたしたち、どこへ向かってるの?","プレイヤー",},
        [2449] = {2101,"主君","袁紹と曹操は、現時点での二大勢力。皇帝が洛陽に帰ったことをどう思ってるのか、見に行ってみよう。","袁紹と曹操は、現時点での二大勢力。皇帝が洛陽に帰ったことをどう思ってるか、見に行こうと思っているの。","",},
        [2450] = {2101,"張星彩","ははーん、何か思惑があるのね。もしかして、袁紹と曹操の見方はもう予想がついてるんじゃ?","ははーん、何か思惑があるのね。もしかして、袁紹と曹操の見方はもう予想がついてるんじゃ?","",},
        [2451] = {2101,"主君","秘密だ。","秘密よ。","",},
        [2452] = {2101,"関銀屏","うふふ、私たちは野次馬ですもの。事が大きくなったほうが面白いですわね！","うふふ、私たちは野次馬ですもの。事が大きくなったほうが面白いですわね！","",},
        [2453] = {2102,"沮授","将軍は宰相の家系に生まれ、忠義で天下を救うお方です。","将軍は宰相の家系に生まれ、忠義で天下を救うお方です。","袁紹",},
        [2454] = {2102,"沮授","今や流浪の身になっている皇帝陛下をお迎えになり、鄴城に都を建てるべきです。","今や流浪の身になっている皇帝陛下をお迎えになり、鄴城に都を建てるべきです。","袁紹",},
        [2455] = {2102,"沮授","天子を擁して諸侯に号令し、兵馬を蓄えて不義の臣下を討つのです。それに誰が逆らえましょう！","天子を擁して諸侯に号令し、兵馬を蓄えて不義の臣下を討つのです。それに誰が逆らえましょう！","",},
        [2456] = {2102,"袁紹","郭図はどう思う?","郭図はどう思う?","",},
        [2457] = {2102,"郭図","天子を迎えるなど、とんでもない！漢王室は衰退して既に久しく、再興など容易ではありません！","天子を迎えるなど、とんでもない！漢王室は衰退して既に久しく、再興など容易ではありません！","",},
        [2458] = {2102,"郭図","我々が天子を迎えれば、何をするにも上意を仰がねばなりません。","我々が天子を迎えれば、何をするにも上意を仰がねばなりません。","",},
        [2459] = {2102,"郭図","命に従えば権力を失い、従わざれば詔に反したという罪を負うでしょう。良い策ではございません。","命に従えば権力を失い、従わざれば詔に反したという罪を負うでしょう。良い策ではございません。","",},
        [2460] = {2102,"袁紹","当初、董卓は洛陽で殺戮を行い、わしを殺そうとした。","当初、董卓は洛陽で殺戮を行い、わしを殺そうとした。","",},
        [2461] = {2102,"袁紹","だがこの袁紹は単騎、渤海より身を立て、天下の英雄に董卓を討てと号令し、十八路諸侯を統率した。","だがこの袁紹は単騎、渤海より身を立て、天下の英雄に董卓を討てと号令し、十八路諸侯を統率した。","",},
        [2462] = {2102,"袁紹","今では冀州の主となり、黒山の黄巾を掃討し、河北統一も目前だ。そのわしがなぜ天子を迎え、あれこれ指図を受ける必要がある?","今では冀州の主となり、黒山の黄巾を掃討し、河北統一も目前だ。そのわしがなぜ天子を迎え、あれこれ指図を受ける必要がある?","",},
        [2463] = {2102,"沮授","将軍、勝利を得るには時機を逸してはなりません。我々が手を挙げねば、他の者に奪われてしまいますぞ。","将軍、勝利を得るには時機を逸してはなりません。我々が手を挙げねば、他の者に奪われてしまいますぞ。","",},
        [2464] = {2102,"袁紹","それ以上言わずともよい、沮授。わしの心はもう決まっておる！","それ以上言わずともよい、沮授。わしの心はもう決まっておる！","",},
        [2465] = {2103,"荀彧","かつて晋の文公は周の襄王を受け入れたことで、諸侯を服従させました。漢の高祖は楚の義帝の葬儀を行ったことで、天下の民心を集めました。","かつて晋の文公は周の襄王を受け入れたことで、諸侯を服従させました。漢の高祖は楚の義帝の葬儀を行ったことで、天下の民心を集めました。","曹操",},
        [2466] = {2103,"荀彧","今、天子は落魄の身。将軍が天子を奉じて衆望を集めるのは、またとない良策にございます。","今、天子は落魄の身。将軍が天子を奉じて衆望を集めるのは、またとない良策にございます。","",},
        [2467] = {2103,"荀彧","早く動かねば、他の者が先んじてしまいます。","早く動かねば、他の者が先んじてしまいます。","",},
        [2468] = {2103,"董昭","今、洛陽では諸将の思惑が入り乱れ、こちらに服従するとは限りません。","今、洛陽では諸将の思惑が入り乱れ、こちらに服従するとは限りません。","",},
        [2469] = {2103,"董昭","大事を成すには、天子を迎えるだけでなく、同時に許昌に遷都するのが上策でございます。","大事を成すには、天子を迎えるだけでなく、同時に許昌に遷都するのが上策でございます。","",},
        [2470] = {2103,"曹操","正にわしの考えておったことだ。遅きに失してはならん。今すぐ天子に奏上し、許都へ参るぞ。","正にわしの考えておったことだ。遅きに失してはならん。今すぐ天子に奏上し、許都へ参るぞ。","",},
        [2471] = {2104,"曹操","臣、国の御恩を被り、常日頃より国に報いたく存じております。今、李傕、郭汜の二人の賊は非道の限りを尽くしておりますが、","臣、国の御恩を被り、常日頃より国に報いたく存じております。今、李傕、郭汜の二人の賊は非道の限りを尽くしておりますが、","献帝",},
        [2472] = {2104,"曹操","臣は精兵二十万余りを有し、逆賊を討つのは容易いこと。陛下の御身をお守りするのが、天下の大事でございます！","臣は精兵二十万余りを有し、逆賊を討つのは容易いこと。陛下の御身をお守りするのが、天下の大事でございます！","献帝",},
        [2473] = {2104,"献帝","うむ。苦労をかけるが頼むぞ！","うむ。苦労をかけるが頼むぞ！","",},
        [2474] = {2104,"曹操","陛下、洛陽は久しく荒れ果て、修繕もできませぬ。糧食の輸送も困難です。しかし許都には城郭や宮殿があり……","陛下、洛陽は久しく荒れ果て、修繕もできませぬ。糧食の輸送も困難です。しかし許都には城郭や宮殿があり……","",},
        [2475] = {2104,"曹操","財や糧食、領民や物資も十分に備えがございます。陛下には許都へお移り願いたく、お聞き届け下さいますよう。","財や糧食、領民や物資も十分に備えがございます。陛下には許都へお移り願いたく、お聞き届け下さいますよう。","",},
        [2476] = {2104,"献帝","長い流浪の果てに、ようやく安定したばかりだ。再び遷都すれば、人々の責めを受けるのではないか?","長い流浪の果てに、ようやく安定したばかりだ。再び遷都すれば、人々の責めを受けるのではないか?","",},
        [2477] = {2104,"曹操","いいえ陛下、事は遅れてはなりませぬ。早く許都へおいで下され！","いいえ陛下、事は遅れてはなりませぬ。早く許都へおいで下され！","",},
        [2478] = {2104,"献帝","わかった、卿の言うとおりにしよう。許都へ遷都だ！","わかった、卿の言うとおりにしよう。許都へ遷都だ！","",},
        [2479] = {2105,"主君","うん、思ったとおりだ。曹操こそ天子を迎え入れる真の英雄だな。","わあ、私の思ったとおりね。曹操こそ天子を迎える真の英雄だわ。","関銀屏",},
        [2480] = {2105,"関銀屏","袁紹は先見の明がないですわね。沮授の言うことを聞かないなんて。","袁紹は先見の明がないですわね。沮授の言うことを聞かないなんて。","",},
        [2481] = {2105,"張星彩","ご主君様、曹操は天子を擁して諸侯に号令するのね。","ご主君様、曹操は天子を擁して諸侯に号令するのね。","",},
        [2482] = {2105,"張星彩","これからは曹操たちが大権を握ることになる。漢王室はもはや名ばかりの存在だし、帝もそのうち……　","これからは曹操たちが大権を握ることになる。漢王室はもはや名ばかりの存在だし、帝もそのうち……　","",},
        [2483] = {2105,"主君","心配性だな、星彩は。目下の局面では、曹操は時局を安定させたいだけだ。","心配性ね、星彩は。目下の局面では、曹操は時局を安定させたいだけよ。","",},
        [2484] = {2105,"主君","天子を殺そうとするわけがない。帝はきっと長生きするよ。","天子を殺そうとするわけがない。帝はきっと長生きするわよ。","",},
        [2485] = {2105,"張星彩","うん、きっとその通りね！","うん、きっとその通りね！","",},
        [2486] = {2105,"主君","帝から揚威将軍に封じられた。職務を尽くして、帝を許都まで護送するぞ。さあ行こう、許都へ！","帝から揚威将軍に封じられたわ。職務を尽くして、帝を許都まで護送するわよ。さあ行きましょう、許都へ！","",},
        [2487] = {2106,"曹操","戯志才の亡きあと、わしと謀をする者がなくなった。そちの郷里、潁川には才ある者が多い。","戯志才の亡きあと、わしと謀をする者がなくなった。そちの郷里、潁川には才ある者が多い。","荀彧",},
        [2488] = {2106,"曹操","荀彧よ、後を継いでくれる者に心当たりはないか?","荀彧よ、後を継いでくれる者に心当たりはないか?","荀彧",},
        [2489] = {2106,"荀彧","はい。潁川の郭嘉は知略に優れ、天下の才子でございます。","はい。潁川の郭嘉は知略に優れ、天下の才子でございます。","",},
        [2490] = {2106,"主君","あの有名な郭嘉殿ですか！曹操兄、私を使者にしてください！","あの有名な郭嘉殿ですか！曹操兄さん、私を使者にしてください！","",},
        [2491] = {2107,"荀彧","郭嘉、こちらが前に話した若き英雄です。","郭嘉、こちらが前に話した若き英雄です。","郭嘉",},
        [2492] = {2107,"荀彧","十常侍の乱を平定し、鳳儀亭で董卓を斬り、李傕、郭汜を破って、天子を洛陽にお送りしたお方です。","十常侍の乱を平定し、鳳儀亭で董卓を斬り、李傕、郭汜を破って、天子を洛陽にお送りしたお方です。","郭嘉",},
        [2493] = {2107,"荀彧","天子は詔を発し、彼を揚威将軍に封じましたが、功名や俸禄に興味はなく、職名だけを受け、軍を率いようとはしないのです。","天子は詔を発し、彼を揚威将軍に封じましたが、功名や俸禄に興味はなく、職名だけを受け、軍を率いようとはしないのです。","",},
        [2494] = {2107,"郭嘉","ほほう。こちらの若き英雄が、あの揚威将軍だったとは。お名前はつとに伺っております。","ほほう。こちらの若き英雄が、あの揚威将軍だったとは。お名前はつとに伺っております。","プレイヤー",},
        [2495] = {2107,"郭嘉","将軍は万物に造詣深く、胸襟の広い方と聞いております。","将軍は万物に造詣深く、胸襟の広い方と聞いております。","",},
        [2496] = {2107,"郭嘉","正に当世の豪傑で、敬服しております。郭嘉より、この名将令を将軍に差し上げたい！","正に当世の豪傑で、敬服しております。郭嘉より、この名将令を将軍に差し上げたい！","",},
        [2497] = {2107,"主君","これは有難い。頂戴いたします。","これは有難い。頂戴いたしますわ。","",},
        [2498] = {35,"関銀屏","星彩、私、三国の秘密をたくさん知ってますのよ。知りたいですか?","星彩、私、三国の秘密をたくさん知ってますのよ。知りたいですか?","張星彩",},
        [2499] = {35,"張星彩","もちろん！教えて教えて、どんな秘密なの?","もちろん！教えて教えて、どんな秘密なの?","",},
        [2500] = {35,"関銀屏","そうですわね～……例えば、曹操は若い頃、袁紹と一緒に花嫁泥棒をしたとか、","そうですわね～……例えば、曹操は若い頃、袁紹と一緒に花嫁泥棒をしたとか、","",},
        [2501] = {35,"関銀屏","徐庶は若い頃、任侠を好む剣の使い手だったとか……","徐庶は若い頃、任侠を好む剣の使い手だったとか……","",},
        [2502] = {35,"張星彩","すごいすごい！名将たちにもそんな面白い物語があったのね。どこに行けば、そういう物語が見られるの?","すごいすごい！名将たちにもそんな面白い物語があったのね。どこに行けば、そういう物語が見られるの?","",},
        [2503] = {35,"関銀屏","名将伝の中ですわ！挑戦すればより多くの名将と知り合えるし、彼らの背後にある物語を深堀りできるんですわよ。","名将伝の中ですわ！挑戦すればより多くの名将と知り合えるし、彼らの背後にある物語を深堀りできるんですわよ。","",},
        [2504] = {35,"張星彩","ご主君様、じゃあ早く一つ目の「名将伝」に挑戦しましょうよ！","ご主君様、じゃあ早く一つ目の「名将伝」に挑戦しましょうよ！","",},
        [2505] = {36,"関銀屏","ご主君様、名将伝を初回クリアすると、大量の元宝をご褒美にもらえますの。元宝こそ、本当の宝ですわよ。","ご主君様、名将伝を初回クリアすると、大量の元宝をご褒美にもらえますの。元宝こそ、本当の宝ですわよ。","張星彩",},
        [2506] = {36,"張星彩","「名将伝」に挑戦すると、魂の勾玉ももらえるの。魂の勾玉は武将商店で、レア度の高い武将と交換できるよ！","「名将伝」に挑戦すると、魂の勾玉ももらえるの。魂の勾玉は武将商店で、レア度の高い武将と交換できるよ！","",},
        [2507] = {36,"関銀屏","ご主君様、毎日忘れず名将伝に挑戦してくださいましね！","ご主君様、毎日忘れず名将伝に挑戦してくださいましね！","",},
        [2508] = {37,"主君","武将(青)と武将(緑)がたくさん手に入ったけど、レア度が低すぎて使い途がなさそうだ。","武将(青)と武将(緑)がたくさん手に入ったけど、レア度が低すぎて使い途がなさそう。","張星彩",},
        [2509] = {37,"張星彩","武将(緑)以上ならみんな#分解#できるよ。回収すると、とっても役に立つアイテムが手に入るよ。","武将(緑)以上ならみんな#分解#できるよ。回収すると、とっても役に立つアイテムが手に入るよ。","",},
        [2510] = {37,"張星彩","これから星彩が、どうやって武将を#分解#するか、教えてあげるね！","これから星彩が、どうやって武将を#分解#するか、教えてあげるね！","",},
        [2511] = {38,"張星彩","ご主君様、分かった?武将を分解すると魂の勾玉がもらえて、武将商店で武将や貴重なアイテムが買えるの！","ご主君様、分かった?武将を分解すると魂の勾玉がもらえて、武将商店で武将や貴重なアイテムが買えるの！","プレイヤー",},
        [2512] = {38,"主君","分かったぞ！魂の勾玉で武将や突破丹、武将経験値が買えるんだな。よし、青武将と緑武将は全部回収して、魂の勾玉に換えるぞ！","分かったわ！魂の勾玉で武将や突破丹、武将経験値が買えるのね。よし、武将(青)と武将(緑)は全部回収して、魂の勾玉に換えるわよ！","",},
        [2513] = {38,"張星彩","育成した武将(紫)と武将(オレンジ)も、必要なければ回収できるよ。しかも、育成に使った素材は全部返してもらえるの！","育成した武将(紫)と武将(オレンジ)も、必要なければ回収できるよ。しかも、育成に使った素材は全部返してもらえるの！","",},
        [2514] = {38,"主君","それは大奮発じゃない！育成に使った資源を#すべて返還#してもらえるなんて！","それは大奮発じゃない！育成に使った資源を#すべて返還#してもらえるなんて！","",},
        [2515] = {38,"張星彩","でしょ～！武将のほか、装備や神器もみんな回収できるよ。だから忘れずに回収に来てね！","でしょ～！武将のほか、装備や神器もみんな回収できるよ。だから忘れずに回収に来てね！","",},
        [2516] = {38,"主君","ありがとう星彩、忘れないで回収に来るさ！","ありがとう星彩、忘れないで回収に来るわ！","",},
        [2517] = {39,"関銀屏","ご主君様、西南の辺境では#南蛮の侵攻#がよくありますの。","ご主君様、西南の辺境では#南蛮の侵攻#がよくありますの。","プレイヤー",},
        [2518] = {39,"関銀屏","彼らは狡猾で、私たちが主戦場で戦闘や掃討をしている時を狙って侵攻してきますのよ。","彼らは狡猾で、私たちが主戦場で戦闘や掃討をしている時を狙って侵攻してきますのよ。","プレイヤー",},
        [2519] = {39,"主君","なんだって……南蛮の奴らはそんなに狡猾なのか！","なんですって……南蛮の奴ら、そんなに狡猾なの！","",},
        [2520] = {39,"関銀屏","でも大丈夫。南蛮を討つのは良い機会でもあるんですわ！戦って勝てば神器進化石が手に入るんですもの。","でも大丈夫。南蛮を討つのは良い機会でもあるんですわ！戦って勝てば神器進化石が手に入るんですもの。","",},
        [2521] = {39,"主君","神器とはなんだ?凄そうな名前だが……","神器ってなに?凄そうな名前だけど……","",},
        [2522] = {39,"関銀屏","どの武将も自分の#専属神器#を持っているんですわ。","どの武将も自分の#専属神器#を持っているんですわ。","",},
        [2523] = {39,"関銀屏","例えばわたしの父、関羽の神兵は青龍偃月刀、大喬さんの神兵は蓮華幽傘というふうに。","例えばわたしの父、関羽の神兵は青龍偃月刀、大喬さんの神兵は蓮華幽傘というふうに。","",},
        [2524] = {39,"関銀屏","ご主君様、たくさん南蛮を討てば、神器商店へ行って神器を買えるんですわよ。","ご主君様、たくさん南蛮を討てば、神器商店へ行って神器を買えるんですわよ。","",},
        [2525] = {39,"主君","そうか！神器を得るのが待ちきれくなってきた！行こう、南蛮を討つぞ！","そうなの！神器を得るのが待ちきれくなってきたわ！行きましょう、南蛮を討つのよ！","",},
        [2526] = {40,"張星彩","やったー！Lv５８に上がったわよ！Lv５８になると#錦袋#が装着できるの！","やったー！Lv５８に上がったわよ！Lv５８になると#錦袋#が装着できるの！","プレイヤー",},
        [2527] = {40,"主君","#錦袋#?何の役に立つんだ?","#錦袋#?何の役に立つの?","",},
        [2528] = {40,"張星彩","錦袋は武将の特性を強化できるの。例えばアタッカーの関銀屏の錦袋を装着すれば攻撃力がアップするし。","錦袋は武将の特性を強化できるの。例えばアタッカーの関銀屏の錦袋を装着すれば攻撃力がアップするし。","",},
        [2529] = {40,"張星彩","デバッファーの貂蝉の錦袋を装着すれば怒気を減らせるわ。","デバッファーの貂蝉の錦袋を装着すれば怒気を減らせるわ。","",},
        [2530] = {40,"張星彩","簡単に言うと、錦袋があれば、より多くの特性を選んで組み合わせられるの。装着すると武将がさらに強くなるよ！","簡単に言うと、錦袋があれば、より多くの特性を選んで組み合わせられるの。装着すると武将がさらに強くなるよ！","",},
        [2531] = {40,"主君","そうか。すごいものなんだな！","そう。すごいものなのね！","",},
        [2532] = {40,"張星彩","そうよ、ご主君様！星彩が錦袋を着けてあげるから試してみて。","そうよ、ご主君様！星彩が錦袋を着けてあげるから試してみて。","",},
        [2533] = {41,"張星彩","きゃ～、すごい！関銀屏の錦袋を着けたら、ご主君様の攻撃力が一気に1000ポイントも上がったわ！","きゃ～、すごい！関銀屏の錦袋を着けたら、ご主君様の攻撃力が一気に1000ポイントも上がったわ！","プレイヤー",},
        [2534] = {41,"主君","だが、これでもまだ武将(紫)の錦袋だ。武将(オレンジ)の錦袋は、もっとすごいのか?","だが、これでもまだ武将(紫)の錦袋よ。。武将(オレンジ)の錦袋は、もっとすごいの?","",},
        [2535] = {41,"張星彩","さすがはご主君様、するどい！錦袋のレア度が高いほど、特性もすごくなるのよ。","さすがはご主君様、するどい！錦袋のレア度が高いほど、特性もすごくなるのよ。","",},
        [2536] = {41,"主君","本当にすごいんだな、錦袋って。どこで手に入れられるんだ?","本当にすごいのね、錦袋って。どこで手に入るの?","",},
        [2537] = {41,"張星彩","錦袋は官職印と同じで、オークションで手に入るよ。","錦袋は官職印と同じで、オークションで手に入るよ。","",},
        [2538] = {41,"張星彩","軍団ボス、軍団試練、軍団クイズといったイベントでも、一定確率で錦袋が出るよ。","軍団ボス、軍団試練、軍団クイズといったイベントでも、一定確率で錦袋が出るよ。","",},
        [2539] = {41,"主君","そうか、軍団の活動やオークションにたくさん参加するだけで、錦袋がもらえるんだな。","そっか、軍団の活動やオークションにたくさん参加するだけで、錦袋がもらえるのね。","",},
        [2540] = {41,"張星彩","そうよ！錦袋図鑑を見て、錦袋をどう組み合わせればもっと強くなれるか、研究するのもいいよ。","そうよ！錦袋図鑑を見て、錦袋をどう組み合わせればもっと強くなれるか、研究するのもいいよ。","",},
        [2541] = {41,"主君","わかった、ありがとう、星彩！","わかったわ、ありがとう、星彩！","",},
        [2542] = {41,"張星彩","どういたしまして！星彩はずっとご主君様のお手伝いをしたいわ！","どういたしまして！星彩はずっとご主君様のお手伝いをしたいわ！","",},
        [2543] = {42,"関銀屏","ご主君様はもうLv70になりましたわ。実力は天下に轟き、戦力の高さと言ったら神獣も共に戦いたいと思うレベルですわよ！","ご主君様はもうLv70になりましたわ。実力は天下に轟き、戦力の高さと言ったら神獣も共に戦いたいと思うレベルですわよ！","プレイヤー",},
        [2544] = {42,"主君","神獣とは……青龍、白虎、朱雀、玄武の類か?","神獣っていうと……青龍、白虎、朱雀、玄武の類?","",},
        [2545] = {42,"関銀屏","そうですわ。私たち神州の大地には、古来より神獣の様々な伝説が伝えられてるのですわ。","そうですわ。私たち神州の大地には、古来より神獣の様々な伝説が伝えられてるのですわ。","",},
        [2546] = {42,"関銀屏","天命を受けた人や真の強者だけが彼らの姿を見られるのです。私の父、関羽はかつて青龍の庇護を受けたのですわ。","天命を受けた人や真の強者だけが彼らの姿を見られるのです。私の父、関羽はかつて青龍の庇護を受けたのですわ。","",},
        [2547] = {42,"主君","道理で関羽兄者はあんなに強いわけだ。神獣の庇護を受けていたとは！ああ、私も自分の神獣が欲しい！","道理で関羽兄さんはあんなに強いわけね。神獣の庇護を受けていたとは！ああ、私も自分の神獣が欲しいわ！","",},
        [2548] = {42,"関銀屏","焦らないでくださいまし。私たちがご主君様のために最初の神獣を用意しましたの。今から銀屏と見に行きましょう。","焦らないでくださいまし。私たちがご主君様のために最初の神獣を用意しましたの。今から銀屏と見に行きましょう。","",},
        [2549] = {43,"関銀屏","ご主君様、これがあなたの最初の神獣ですわよ！","ご主君様、これがあなたの最初の神獣ですわよ！","プレイヤー",},
        [2550] = {43,"主君","なんと、パンダの親子か！親パンダは強そうだし、子パンダは萌え萌えで可愛いな！","パ、パンダの親子?！……ま、まあ、親パンダは強そうだし、子パンダは萌え系で可愛いわね。","",},
        [2551] = {43,"関銀屏","ご主君様、神獣は戦闘の時、7番目の出陣枠にいますわ。強い武将が一人増えるのと同じですのよ。","ご主君様、神獣は戦闘の時、7番目の出陣枠にいますわ。強い武将が一人増えるのと同じですのよ。","",},
        [2552] = {43,"主君","銀屏、パンダは青品質に過ぎないんだろう?もしもっと強い神獣が欲しかったら、どこへ行けばいいんだ?","銀屏、パンダは青品質に過ぎないんでしょう?もしもっと強い神獣が欲しかったら、どこへ行けばいいの?","",},
        [2553] = {43,"関銀屏","神獣商店で#獣魂#を神獣に交換できるんですわ。","神獣商店で#獣魂#を神獣に交換できるんですわ。","",},
        [2554] = {43,"主君","じゃあ、「獣魂」はどこで手に入るんだ?","じゃあ、「獣魂」はどこで手に入るの?","",},
        [2555] = {43,"関銀屏","獣魂は神樹で手に入りますわ。銀屏が連れて行ってあげます。","獣魂は神樹で手に入りますわ。銀屏が連れて行ってあげます。","",},
        [2556] = {44,"関銀屏","ご主君様、ほら、これが神樹ですわ。","ご主君様、ほら、これが神樹ですわ。","プレイヤー",},
        [2557] = {44,"関銀屏","ここには一本の神樹があって、この樹は天地の霊気を吸収してできているんですの。","ここには一本の神樹があって、この樹は天地の霊気を吸収してできているんですの。","",},
        [2558] = {44,"関銀屏","神獣を引き寄せる獣魂を凝縮して生み出せるんですわよ。神樹のレベルが高いほど、生み出せる獣魂は多いんですわ。","神獣を引き寄せる獣魂を凝縮して生み出せるんですわよ。神樹のレベルが高いほど、生み出せる獣魂は多いんですわ。","",},
        [2559] = {44,"主君","分かった！神樹のレベルが上がると、より多くの獣魂が得られるんだな！","分かった！神樹のレベルが上がると、より多くの獣魂が得られるのね！","",},
        [2560] = {44,"主君","獣魂があれば、神獣商店でレア度の高い神獣に交換できるのか。","獣魂があれば、神獣商店でレア度の高い神獣に交換できるのね。","",},
        [2561] = {44,"関銀屏","本当にすごいですわ、ご主君様。すぐに分かってしまうんですもの。ですから忘れずに、神樹をレベルアップさせてくださいましね！","本当にすごいですわ、ご主君様。すぐに分かってしまうんですもの。ですから忘れずに、神樹をレベルアップさせてくださいましね！","",},
        [2562] = {44,"主君","もちろんだ。世界最強の神獣を手に入れるぞ！","もちろんよ。世界最強の神獣を手に入れるわよ！","",},
        [2563] = {44,"関銀屏","頑張ってくださいまし、ご主君様。三国を制覇する日も近いですわね！","頑張ってくださいまし、ご主君様。三国を制覇する日も近いですわね！","",},
        [2564] = {2201,"曹操","劉備が徐州に駐屯し、自ら州事に任じている。最近では呂布が敗北して身を寄せ、劉備は呂布を小沛においている。","劉備が徐州に駐屯し、自ら州事に任じている。最近では呂布が敗北して身を寄せ、劉備は呂布を小沛においている。","荀彧",},
        [2565] = {2201,"曹操","二人が協力して兵を向けてきたら、大きな災いとなりかねん。諸侯に妙案はないか?","二人が協力して兵を向けてきたら、大きな災いとなりかねん。諸侯に妙案はないか?","荀彧",},
        [2566] = {2201,"荀彧","我が方は許都に都を定めたばかり。再度の出兵は難しいでしょう。","我が方は許都に都を定めたばかり。再度の出兵は難しいでしょう。","",},
        [2567] = {2201,"荀彧","私に一計があります。「二虎競食の計」と言い、劉備に呂布を討たせるのです。","私に一計があります。「二虎競食の計」と言い、劉備に呂布を討たせるのです。","",},
        [2568] = {2201,"曹操","今、密偵から報告が来た。この策は劉備に拒否された。劉備は呂布を殺さなかったそうだ。","今、密偵から報告が来た。この策は劉備に拒否された。劉備は呂布を殺さなかったそうだ。","",},
        [2569] = {2201,"荀彧","では、もう一つの計を用いましょう。「駆虎呑狼の計」と言います。劉備に袁術を討てという皇帝の詔を発して――","では、もう一つの計を用いましょう。「駆虎呑狼の計」と言います。劉備に袁術を討てという皇帝の詔を発して――","",},
        [2570] = {2201,"荀彧","裏で密かにその情報を袁術に教えるのです。こうして両者が争えば、呂布にはきっと異心が生まれます。","裏で密かにその情報を袁術に教えるのです。こうして両者が争えば、呂布にはきっと異心が生まれます。","",},
        [2571] = {2201,"曹操","良い策だ！お前の言うとおりにしよう。","良い策だ！お前の言うとおりにしよう。","",},
        [2572] = {2201,"主君","まずいな、曹操が兄者に対抗しようとしているぞ！兄者のところへ行って、奸計に乗らないよう忠告しなくては！","まずいわ、曹操が兄さんに対抗しようとしている！兄さんのところへ行って、奸計に乗らないよう忠告しないと！","",},
        [2573] = {2202,"袁術","おのれ、劉備！わらじ編みの分際で、徐州を占拠しただけでなく、このオレを討ちに来るとは、生意気な！","おのれ、劉備！わらじ編みの分際で、徐州を占拠しただけでなく、このオレを討ちに来るとは、生意気な！","劉備",},
        [2574] = {2202,"劉備","天子の詔を受け、逆臣を討ちに来たのだ。抵抗するなら容赦はせぬぞ。","天子の詔を受け、逆臣を討ちに来たのだ。抵抗するなら容赦はせぬぞ。","",},
        [2575] = {2202,"主君","兄者、袁術はあまりに傲慢だ。私が奴の意気を削いでやりましょう。","兄さん、袁術はあまりに傲慢よ。私が奴の意気を削いでやりますわ。","",},
        [2576] = {2203,"主君","兄者、袁術を討ってはなりません。これは曹操の計略です。","兄さん、袁術を討ってはいけないわ。これは曹操の計略です。","",},
        [2577] = {2203,"劉備","計略だとしても、皇帝の命には逆らえぬ。関羽、張飛、誰が城を守る?","計略だとしても、皇帝の命には逆らえぬ。関羽、張飛、誰が城を守る?","",},
        [2578] = {2203,"張飛","城ならオレが守るぜ！","城ならオレが守るぜ！","",},
        [2579] = {2203,"劉備","お前では無理だ。酔うと見境なく兵士を鞭打つし、すべてが軽はずみで人の言うことを聞かない。私は安心できぬぞ。","お前では無理だ。酔うと見境なく兵士を鞭打つし、すべてが軽はずみで人の言うことを聞かない。私は安心できぬぞ。","",},
        [2580] = {2203,"張飛","わかったよ。これからは、酒を飲むのも兵士を鞭打つのもやめる。何事も人の話を聞く。それでいいだろう。","わかったよ。これからは、酒を飲むのも兵士を鞭打つのもやめる。何事も人の話を聞く。それでいいだろう。","",},
        [2581] = {2203,"主君","張飛兄者、しっかり城を守ってくださいよ。私は兄者たちと袁術を討ってきます。","張飛兄さん、しっかり城を守ってくださいね。私は兄さんたちと袁術を討ってきます。","",},
        [2582] = {2204,"主君","くそっ、呂布め！我々が出兵している間に徐州を奪うとは。","なんなの、呂布っ！我々が出兵している間に徐州を奪うなんて。","劉備",},
        [2583] = {2204,"劉備","怒るな。呂布はもう二人の夫人と眷属をすべて送り返してきたし、糧食や絹織物も贈ってきた。","怒るな。呂布はもう二人の夫人と眷属をすべて送り返してきたし、糧食や絹織物も贈ってきた。","",},
        [2584] = {2204,"劉備","この件は張飛にも非がある。既に和解は果たした。彼は徐州、私は小沛を取る。","この件は張飛にも非がある。既に和解は果たした。彼は徐州、私は小沛を取る。","",},
        [2585] = {2204,"劉備","今、私と呂布が争えば、曹操の思うつぼではないか。彼と袁術に漁夫の利を得させることになる。","今、私と呂布が争えば、曹操の思うつぼではないか。彼と袁術に漁夫の利を得させることになる。","",},
        [2586] = {2204,"主君","そうは言っても、怒りが収まりません。待っていてください、兄者。呂布のところへ行き、目にもの見せてやります。","そうは言っても、怒りが収まりませんわ。待っていてください、兄さん。呂布のところへ行き、目にもの見せてやります。","",},
        [2587] = {2205,"紀霊","劉備はどこだ?我が君、袁術の命を受け、十万の兵と共に捕えに来たぞ！","劉備はどこだ?我が君、袁術の命を受け、十万の兵と共に捕えに来たぞ！","プレイヤー",},
        [2588] = {2205,"主君","妄言はよせ、紀霊！我が兄者は黄巾討伐の功臣だ。お前なんか、百万の黄巾軍に比べれば何ほどのこともない。一撃も与えられるものか！","妄言はよすのね、紀霊！我が兄は黄巾討伐の功臣よ。お前なんか、百万の黄巾に比べれば何ほどのこともないわ。一撃も与えられはしない！","",},
        [2589] = {2205,"呂布","若き英雄よ、まずは俺の話を聞け！","若き英雄よ、まずは俺の話を聞け！","",},
        [2590] = {2205,"主君","よくも兄者の徐州を奪ったな、報いを受けろ！","よくも兄さんの徐州を奪ったわね、報いを受けなさい！","",},
        [2591] = {2206,"呂布","フン、相変わらずだな。その威勢の良さは好きだが、双方で殺し合うのはやめるがいい。","フン、相変わらずだな。その威勢の良さは好きだが、双方で殺し合うのはやめるがいい。","プレイヤー",},
        [2592] = {2206,"呂布","ここから百五十歩離れた轅門に立てた画戟が見えるか?もし俺が一矢であの戟の枝刃に射当てたら、双方兵を収められよ。","ここから百五十歩離れた轅門に立てた画戟が見えるか?もし俺が一矢であの戟の枝刃に射当てたら、双方兵を収められよ。","",},
        [2593] = {2206,"呂布","当たらねば、仕方ない。各々陣営に戻って、戦支度をすればよかろう。","当たらねば、仕方ない。各々陣営に戻って、戦支度をすればよかろう。","",},
        [2594] = {2206,"呂布","やっ！見ろ、天は双方に兵を収めろと命じたぞ。","やっ！見ろ、天は双方に兵を収めろと命じたぞ。","",},
        [2595] = {2206,"主君","うおっ、見事に命中した！まるで弓張月から流れ星が飛んだようだったな。……わかった、呂布兄が争いをやめろというなら、仕方ない。","うわっ、見事に命中した！まるで弓張月から流れ星が飛んだみたい。……わかったわ、呂布兄さんが争いをやめろというなら、仕方ない。","",},
        [2596] = {2206,"主君","紀霊、もしまた攻めてきたら、その時は遠慮しないからな！","紀霊、もしまた攻めてきたら、その時は遠慮しないわよ！","",},
        [2597] = {2206,"紀霊","おうとも！すぐに撤兵する！","おうとも！すぐに撤兵する！","",},
        [2598] = {2206,"呂布","劉備殿、この名将令は詫びの印だ。徐州は、この呂布がお返ししよう。","劉備殿、この名将令は詫びの印だ。徐州は、この呂布がお返ししよう。","",},
        [2599] = {2206,"劉備","いえ、私はもともと呂布殿に譲ろうと思っていたのです。私が小沛に移って、互いに協力して敵をけん制するほうがいいでしょう。","いえ、私はもともと呂布殿に譲ろうと思っていたのです。私が小沛に移って、互いに協力して敵をけん制するほうがいいでしょう。","",},
        [2600] = {2206,"劉備","義弟よ、この名将令は、とりあえずお前が受け取ってくれ。","義妹よ、この名将令は、とりあえずお前が受け取ってくれ。","",},
        [2601] = {2206,"主君","ありがとうございます、兄者。袁術に攻め入られることがないよう、これからも守ります！","ありがとうございます、兄さん。袁術に攻め入られることがないよう、これからも守りますわ！","",},
        [2602] = {2301,"紀霊","我が君、これは呂布が轅門に戟を射て、撤退を迫ったのです。私の意図したところではありません。","我が君、これは呂布が轅門に戟を射て、撤退を迫ったのです。私の意図したところではありません。","袁術",},
        [2603] = {2301,"袁術","オレは呂布にあれほど多くの贈り物をしたというのに、受け取っておいて何もなさぬというのか。しかも劉備に加担するとは！","オレは呂布にあれほど多くの贈り物をしたというのに、受け取っておいて何もなさぬというのか。しかも劉備に加担するとは！","",},
        [2604] = {2301,"袁術","誰かある、自ら大軍を率いて劉備を討つぞ！同時に呂布も成敗する。","誰かある、自ら大軍を率いて劉備を討つぞ！同時に呂布も成敗する。","",},
        [2605] = {2301,"紀霊","軽はずみな行動はいけません。呂布は人並外れて勇猛で、徐州の地も得ています。劉備と結託したら、もっと面倒な事になります。","軽はずみな行動はいけません。呂布は人並外れて勇猛で、徐州の地も得ています。劉備と結託したら、もっと面倒な事になります。","",},
        [2606] = {2301,"紀霊","呂布には年頃の娘があるそうです。我が君のご子息と、縁談を持ちかけてはいかがでしょうか。","呂布には年頃の娘があるそうです。我が君のご子息と、縁談を持ちかけてはいかがでしょうか。","",},
        [2607] = {2301,"袁術","良い策だ。オレと呂布が縁戚になり、さらに利益を与えてやれば、逆らえる者などおらぬだろう。帝位も遠くないな。","良い策だ。オレと呂布が縁戚になり、さらに利益を与えてやれば、逆らえる者などおらぬだろう。帝位も遠くないな。","",},
        [2608] = {2302,"呂布","袁術が使者を寄こして縁談を持ちかけてきたぞ。おぬしら、どう思う?","袁術が使者を寄こして縁談を持ちかけてきたぞ。おぬしら、どう思う?","厳氏",},
        [2609] = {2302,"厳氏","袁術殿は淮南を久しく治め、兵も糧食も豊富、いずれ皇帝になられるお方。子息は一人だけですので、嫁がせれば将来は皇后です。","袁術殿は淮南を久しく治め、兵も糧食も豊富、いずれ皇帝になられるお方。子息は一人だけですので、嫁がせれば将来は皇后です。","",},
        [2610] = {2302,"陳宮","天下は今、諸侯が相争っています。今我が君と袁術が縁戚となれば、諸侯の妬みを買うでしょう。誰かが略奪に動いたらどうなさいます。","天下は今、諸侯が相争っています。今我が君と袁術が縁戚となれば、諸侯の妬みを買うでしょう。誰かが略奪に動いたらどうなさいます。","",},
        [2611] = {2302,"陳宮","まずは霊雎殿を寿春に送って別邸に住まわせ、それから吉日を選んで縁談を進めれば、万全でしょう。","まずは霊雎殿を寿春に送って別邸に住まわせ、それから吉日を選んで縁談を進めれば、万全でしょう。","",},
        [2612] = {2302,"呂布","はは、では呂霊雎を嫁がせよう。誰か、早く袁術に返事をさせよ。縁談を承諾するとな。","はは、では呂霊雎を嫁がせよう。誰か、早く袁術に返事をさせよ。縁談を承諾するとな。","",},
        [2613] = {2303,"陳珪","将軍の訃報に接し、お悔やみ申し上げます。","将軍の訃報に接し、お悔やみ申し上げます。","呂布",},
        [2614] = {2303,"呂布","なぜ、そのようなことを申す?","なぜ、そのようなことを申す?","",},
        [2615] = {2303,"陳珪","袁術には帝位を狙う意がありますが、皇帝を称することは即ち謀反です。逆賊と縁を結べば、その時は天下に受け入れられますまい。","袁術には帝位を狙う意がありますが、皇帝を称することは即ち謀反です。逆賊と縁を結べば、その時は天下に受け入れられますまい。","",},
        [2616] = {2303,"主君","間に合った！呂布、娘を袁術の息子に嫁がせると聞いたが、それはやめておけ！","間に合った！呂布、娘を袁術の子息に嫁がせると聞いたけど、それはダメよ！","",},
        [2617] = {2303,"呂布","若き英雄まで、そのようなことを申すのか?","若き英雄まで、そのようなことを申すのか?","",},
        [2618] = {2303,"主君","李傕、郭汜の後、袁術に身を寄せて追い出されそうになったことを忘れたか?","李傕、郭汜の乱の後、袁術のもとに身を寄せて追い出されそうになったことを忘れた?","",},
        [2619] = {2303,"主君","あのような小人に付き従えば、将来災いを招くのは確実だぞ。","あのような小人に付き従えば、将来災いを招くのは確実だわ。","",},
        [2620] = {2303,"呂布","そうか、言われてみれば！陳宮に惑わされたわ！誰か、早く霊雎を連れ戻せ！","そうか、言われてみれば！陳宮に惑わされたわ！誰か、早く霊雎を連れ戻せ！","",},
        [2621] = {2303,"主君","早い方がいい、私が行く！","早い方がいい、私が行くわ！","",},
        [2622] = {2304,"貂蝉","呂布さま、若き英雄が霊雎を連れ戻してくれました。","呂布さま、若き英雄が霊雎を連れ戻してくれました。","プレイヤー",},
        [2623] = {2304,"主君","貂蝉さんの頼みですから、力を尽くすのは当然です。","貂蝉さんの頼みだもの、力を尽くすのは当然です。","",},
        [2624] = {2304,"貂蝉","縁談は本人の気持ちが一番大事だと思いますわ。霊雎が嫁ぎたくないのに無理強いすれば、一生が台無しです。","縁談は本人の気持ちが一番大事だと思いますわ。霊雎が嫁ぎたくないのに無理強いすれば、一生が台無しです。","",},
        [2625] = {2304,"呂布","うむ、貂蝉の言うとおりだ。","うむ、貂蝉の言うとおりだ。","",},
        [2626] = {2304,"主君","呂布兄は当世の豪傑なんだし、娘を駒にする必要もないだろう。","呂布兄さんは当世の豪傑なんだし、娘を駒にする必要もないでしょう。","",},
        [2627] = {2304,"呂布","その通りだ。この呂布は江淮一帯に覇を唱え……いや、天下に恐れるものはない！","その通りだ。この呂布は江淮一帯に覇を唱え……いや、天下に恐れるものはない！","",},
        [2628] = {2304,"呂布","その呂布の娘だ、本人の意思がなければ嫁がせん！霊雎の気持を聞いてくる。","その呂布の娘だ、本人の意思がなければ嫁がせん！霊雎の気持を聞いてくる。","",},
        [2629] = {2305,"呂布","若き英雄よ、この名将令は取っておいてくれ。お前がいなければ、大事を誤るところだった！","若き英雄よ、この名将令は取っておいてくれ。お前がいなければ、大事を誤るところだった！","プレイヤー",},
        [2630] = {2305,"主君","これはこれは！感謝する。","それはどうも！感謝するわ。","",},
        [2631] = {2305,"呂霊雎","父上、私は強い英雄に嫁ぎたい。袁術の息子など嫌です！誰が臆病者の妻になど。","父上、私は強い英雄に嫁ぎたい。袁術の息子など嫌です！誰が臆病者の妻になど。","",},
        [2632] = {2305,"呂布","わかった！決して無理やり嫁がせるようなことはしないぞ、霊雎よ。どの英雄に嫁ぎたいのだ?","わかった！決して無理やり嫁がせるようなことはしないぞ、霊雎よ。どの英雄に嫁ぎたいのだ?","",},
        [2633] = {2305,"呂霊雎","聞くところでは、天子が新しく揚威将軍に封じた方がいるそうですね。","聞くところでは、天子が新しく揚威将軍に封じた方がいるそうですね。","",},
        [2634] = {2305,"呂霊雎","若くて才能があって、武芸に優れているとか。そのお方に嫁ぎたい！","若くて才能があって、武芸に優れているとか。嫁ぐよりも、そのお方と姉妹になって、戦場を駆け巡りたい！","",},
        [2635] = {2305,"主君","なにっ、私に嫁ぎたいだって?(それじゃ、貂蝉さんが義理の母になるじゃないか、それはダメだ！)","ええっ、私の姉妹になりたいの?(そうすると、貂蝉さんが義理の母になるじゃない、それはだめ！)","",},
        [2636] = {2305,"主君","ご、ご厚意は有難いですが、私はまだ若く、結婚など考えておりません。では！","ご、ご厚意は有難いですが、急用ができましたので失礼します。では！","",},
        [2637] = {2305,"呂霊雎","逃がさんぞ、揚威将軍！この世の果てまでも追いかけて、お前に嫁いでみせる！","逃がさんぞ、揚威将軍！この世の果てまでも追いかけて、姉妹になってみせる！","",},
        [2638] = {2401,"呂布","劉備！先日は轅門に戟を射て、貴様の大難を救ってやったのに、なぜ山賊のふりをして、百五十頭の良馬を奪った?恩を仇で返す気か?","劉備！先日は轅門に戟を射て、貴様の大難を救ってやったのに、なぜ山賊のふりをして、百五十頭の良馬を奪った?恩を仇で返す気か?","劉備",},
        [2639] = {2401,"劉備","貴殿の馬を盗むなどとんでもない。馬が足りなかったので、配下に命じてあちこちで買わせたのです。","貴殿の馬を盗むなどとんでもない。馬が足りなかったので、配下に命じてあちこちで買わせたのです。","",},
        [2640] = {2401,"張飛","オレがお前の馬を奪ったんだよ。兄貴に罪を着せるんじゃねえ。","オレがお前の馬を奪ったんだよ。兄貴に罪を着せるんじゃねえ。","",},
        [2641] = {2401,"呂布","このギョロ目玉め、何度も俺を侮りおって！","このギョロ目玉め、何度も俺を侮りおって！","",},
        [2642] = {2401,"張飛","オレが馬を盗んだら怒るくせに、兄貴から徐州を盗んだ件はだんまりか?ええ?","オレが馬を盗んだら怒るくせに、兄貴から徐州を盗んだ件はだんまりか?ええ?","",},
        [2643] = {2401,"呂布","ふん、ならば実力で勝負だ。すぐに軍を率いて、馬を奪い返すぞ！","ふん、ならば実力で勝負だ。すぐに軍を率いて、馬を奪い返すぞ！","",},
        [2644] = {2402,"関銀屏","ご主君様、大変ですわ！呂布が兵馬を率いて、小沛に攻め入ったのです。その兵力は父上の数倍ですわ、どうしましょう！","ご主君様、大変ですわ！呂布が兵馬を率いて、小沛に攻め入ったのです。その兵力は父上の数倍ですわ、どうしましょう！","プレイヤー",},
        [2645] = {2402,"張星彩","どうせまたうちの父さまがやらかしたんでしょ！もともと無鉄砲な上に、お酒でも入ってたのね。ご主君様、どうしよう?","どうせまたうちの父さまがやらかしたんでしょ！もともと無鉄砲な上に、お酒でも入ってたのね。ご主君様、どうしよう?","",},
        [2646] = {2402,"主君","大丈夫だ、星彩、銀屏。私が何とかする！","大丈夫よ、星彩、銀屏。私が何とかする！","",},
        [2647] = {2402,"主君","呂布兄、私が馬を取り戻せたら、撤兵してもらえないか?できなかったら出兵するということで。","呂布兄さん、私が馬を取り戻せたら、撤兵してくれないかしら。それができないときは出兵するということでいかが?","",},
        [2648] = {2402,"呂布","いいだろう。若き英雄に免じて、そうしよう。","いいだろう。若き英雄に免じて、そうしよう。","",},
        [2649] = {2403,"関羽","義弟よ、何故よそ者の味方をして兄者に矛を向けるのだ?","義妹よ、何故よそ者の味方をして兄者に矛を向けるのだ?","プレイヤー",},
        [2650] = {2403,"主君","そう責めないでください、関羽兄者。考えがあるんです！","そう責めないでよ、関羽兄さん。考えがあるの！","",},
        [2651] = {2404,"張飛","身内を裏切って、呂布の野郎を助けるなら、兄弟の縁もこれまでだぞ！","身内を裏切って、呂布の野郎を助けるなら、兄妹の縁もこれまでだぞ！","プレイヤー",},
        [2652] = {2404,"主君","張飛兄者、誤解しないで、話を聞いてください。","張飛兄さん、誤解しないで、話を聞いて。","",},
        [2653] = {2404,"張飛","誰がてめえの言い訳など聞くかい！おらぁ！俺の矛を食らいやがれ！","誰がてめえの言い訳など聞くかい！おらぁ！俺の矛を食らいやがれ！","",},
        [2654] = {2405,"主君","張飛兄者、誤解です。これはただの時間稼ぎ。私は兄者たちを助けに来たんです！","張飛兄さん、誤解よ。これはただの時間稼ぎ。私はみんなを助けに来たのよ。","張飛",},
        [2655] = {2405,"主君","呂布のそばには陳宮がいます。奴がいると兄者のためになりません。","呂布のそばには陳宮がいます。あいつがいると兄さんにとって不利です。","",},
        [2656] = {2405,"主君","呂布が兵の準備に手間取っている隙に、一緒にこの城を出ましょう。小沛など失っても構いません！","呂布が兵の準備に手間取っている隙に、一緒にこの城を出ましょう。小沛など失っても構いません！","",},
        [2657] = {2405,"張飛","そうだったか。また先走っちまったみたいだな。悪く思わないでくれよ。","そうだったか。また先走っちまったみたいだな。悪く思わないでくれよ。","",},
        [2658] = {2405,"張星彩","父さまはお酒でしくじってばかり！もう少しで皆を死なせるところだったんだからね！もう飲んじゃダメ！","父さまはお酒でしくじってばかり！もう少しで皆を死なせるところだったんだからね！もう飲んじゃダメ！","",},
        [2659] = {2405,"主君","城を棄てて許都へ行き、曹操のもとに身を寄せましょう。","城を棄てて許都へ行き、曹操のもとに身を寄せましょう。","",},
        [2660] = {2405,"主君","曹操の憎む相手と言えば、呂布が筆頭です。前に兗州を攻められ、あやうく命を落としかけましたから。","曹操の憎む相手と言えば、呂布が筆頭です。前に兗州を攻められ、あやうく殺されかけましたから。","",},
        [2661] = {2405,"劉備","ああ、お前の言うとおりだ。","ああ、お前の言うとおりだ。","",},
        [2662] = {2405,"張飛","すまねえ、すっかり誤解してたぜ。名将令をやるから、勘弁してくれ。","すまねえ、すっかり誤解してたぜ。名将令をやるから、勘弁してくれ。","",},
        [2663] = {2501,"主君","兄者たちはもう許都に落ち着いたな。星彩、銀屏、しばらく遊びに行ってなかったな。ちょっと出かけてみようか?","兄さんたちはもう許都に落ち着いたわね。星彩、銀屏、しばらく遊びに行ってなかったわね。ちょっと出かけてみる?","張星彩",},
        [2664] = {2501,"張星彩","そうね、最近はずっと、父さまたちを助けてくれてたもんね。本当にお疲れ様。","そうね、最近はずっと、父さまたちを助けてくれてたもんね。本当にお疲れ様。","",},
        [2665] = {2501,"関銀屏","どこへ行きます?長い間中原にいましたから、目新しい所へ行きたいですわね。江東なんかどうです?","どこへ行きます?長い間中原にいましたから、目新しい所へ行きたいですわね。江東なんかどうです?","",},
        [2666] = {2501,"主君","そうだな、行ってみようか。そういえば孫堅が亡くなってから、孫策は袁術のもとに身を寄せて、江南に引きこもっているそうだ。","そうね、行ってみようか。そういえば孫堅が亡くなってから、孫策は袁術のもとに身を寄せて、江南に引きこもっているそうよ。","",},
        [2667] = {2501,"主君","手伝えることがあるかもしれない。","手伝えることがあるかもしれないわ。","",},
        [2668] = {2501,"関銀屏","決まりですわね！江東の小覇王。格好良いし、覇気もある。早く会いたいですわ！","決まりですわね！江東の小覇王。格好良いし、覇気もある。早く会いたいですわ！","",},
        [2669] = {2502,"主君","お久しぶりです、孫策兄。何かお悩みのようですね?","お久しぶりです、孫策兄さん。何かお悩みのようですね?","孫策",},
        [2670] = {2502,"孫策","悩むも何も、袁術のことだ。奴の言うことはコロコロ変わって信用ならん。俺を最初九江太守にすると約束したのに、結局自分の配下を任命した。","悩むも何も、袁術のことだ。奴の言うことはコロコロ変わって信用ならん。俺を最初九江太守にすると約束したのに、結局自分の配下を任命した。","",},
        [2671] = {2502,"孫策","その後も、俺を廬江太守にすると言って現太守の陸康を討たせたくせに、またしても自分の配下の劉勲を任命したのだ。","その後も、俺を廬江太守にすると言って現太守の陸康を討たせたくせに、またしても自分の配下の劉勲を任命したのだ。","",},
        [2672] = {2502,"孫策","まったく情けない限りだよ。父の孫堅はあれほどの英雄だったのに、俺は他人の家に身を寄せ、志を継ぐことができぬのだ。","まったく情けない限りだよ。父の孫堅はあれほどの英雄だったのに、俺は他人の家に身を寄せ、志を継ぐことができぬのだ。","",},
        [2673] = {2502,"主君","確かに袁術は信用できない。だったらお父上の昔の部下を集め、機を見て江東を手に入れ、基礎を築いたらどうです?","確かに袁術は信用できない。だったらお父上の昔の部下を集め、機を見て江東を手に入れ、基礎を築いたらどうです?","",},
        [2674] = {2502,"孫策","そんなことができるはずがない。袁術が俺を手放さぬだろう！","そんなことができるはずがない。袁術が俺を手放さぬだろう！","",},
        [2675] = {2502,"主君","袁術はずっと皇帝になりたがっています。お父上が残した伝国の玉璽を贈れば、きっと同意するでしょう。","袁術はずっと皇帝になりたがっていますわ。お父上が残した伝国の玉璽を贈れば、きっと同意するでしょう。","",},
        [2676] = {2502,"孫策","なるほど、その手があったか！ちょうど叔父の丹陽太守、呉景が揚州刺史の劉繇に攻められている。この機会を利用しよう。","なるほど、その手があったか！ちょうど叔父の丹陽太守、呉景が揚州刺史の劉繇に攻められている。この機会を利用しよう。","",},
        [2677] = {2502,"主君","その意気です。もし袁術が同意しなくても、力ずくでさせてやりますよ。","その意気です。もし袁術が同意しなくても、力ずくでさせてやりますわ。","",},
        [2678] = {2503,"袁術","この袁術は、やがて帝位に就く男だぞ。その態度はなんだ！","この袁術は、やがて帝位に就く男だぞ。その態度はなんだ！","プレイヤー",},
        [2679] = {2503,"関銀屏","あらあら～ご主君様に負けたくせに、よくもそんな大層なことが言えますわね～","あらあら～ご主君様に負けたくせに、よくもそんな大層なことが言えますわね～","",},
        [2680] = {2503,"主君","もう一度倒して、その減らず口を閉じさせてやる！","もう一度倒して、その減らず口を閉じさせてやるわ！","",},
        [2681] = {2504,"孫策","今回首尾良く江東に帰って来られたのも、君のお陰だ。心から感謝する。","今回首尾良く江東に帰って来られたのも、君のお陰だ。心から感謝する。","プレイヤー",},
        [2682] = {2504,"主君","なんの、遠慮はいりません。天は人を助く者を助くと言うではないですか。","なんの、遠慮はいらないですわ。天は人を助く者を助くと言うではないですか。","",},
        [2683] = {2504,"主君","「一人の好漢には三人の仲間が必要だ」と言うでしょう。","「一人の好漢には三人の仲間が必要だ」と言うでしょう。","",},
        [2684] = {2504,"主君","江東で基礎を築きたいなら、まずは現地の士族から人材を何人か集めないとならないでしょう。","江東で基礎を築きたいなら、まずは現地の士族から人材を何人か集めないとならないですわ。","",},
        [2685] = {2504,"主君","呉郡の四大名家は張、朱、陸、顧で、張家の張昭が筆頭です。非常に優れた才能の持ち主だそうです。","呉郡の四大名家は張、朱、陸、顧で、張家の張昭が筆頭です。非常に優れた才能の持ち主だそうです。","",},
        [2686] = {2504,"主君","まず張昭を得て、賢人を礼遇すれば、他の名家の者たちも自然に身を寄せるでしょう。","まず張昭を得て、賢人を礼遇すれば、他の名家の者たちも自然に身を寄せるでしょう。","",},
        [2687] = {2505,"関銀屏","ご主君様、前から来るあの方は誰ですの?優雅で垢抜けていて、端正なお顔立ち。ステキな美男子ですわ！","ご主君様、前から来るあの方は誰ですの?優雅で垢抜けていて、端正なお顔立ち。ステキな美男子ですわ！","プレイヤー",},
        [2688] = {2505,"主君","本当だ。非凡な風格がある。孫策兄、ご存知ですか?","本当だわ。ただならぬ風格が漂ってる。孫策兄さん、ご存知ですか?","",},
        [2689] = {2505,"孫策","ああ、俺の幼馴染みの周瑜だ。字は公瑾。寿春にいた頃に出会ってすぐ意気投合して、ずっと親しく付き合っている。","ああ、俺の幼馴染みの周瑜だ。字は公瑾。寿春にいた頃に出会ってすぐ意気投合して、ずっと親しく付き合っている。","",},
        [2690] = {2505,"孫策","父上が董卓討伐に向かった時、わが一族は周瑜の屋敷に住んで、家族同然の付き合いをしていたんだ。","父上が董卓討伐に向かった時、わが一族は周瑜の屋敷に住んで、家族同然の付き合いをしていたんだ。","",},
        [2691] = {2505,"主君","ああ、名将・周瑜殿でしたか。それはいい、まずはお手合わせ願いたい！","ああ、名将・周瑜殿でしたか。それはいい、まずはお手合わせ願いたいわ！","",},
        [2692] = {2506,"孫策","周瑜、こちらが前に話した若き英雄だ。既に天子から揚威将軍に封じられている。","周瑜、こちらが前に話した若き英雄だ。既に天子から揚威将軍に封じられている。","周瑜",},
        [2693] = {2506,"孫策","今回袁術のもとを抜け出し、父上の旧領を取り戻せたのも、彼のお陰だ。","今回袁術のもとを抜け出し、父上の旧領を取り戻せたのも、彼のお陰だ。","周瑜",},
        [2694] = {2506,"周瑜","揚威将軍でしたか。お名前はかねがね伺っております。","揚威将軍でしたか。お名前はかねがね伺っております。","",},
        [2695] = {2506,"孫策","俺たち三人は年齢も近いし、気も合う。義兄弟の契りを結ばないか?","俺たち三人は年齢も近いし、気も合う。義兄弟の契りを結ばないか?","",},
        [2696] = {2506,"主君","残念ですが、私は既に劉備、関羽、張飛と義兄弟の契りを結んでおります。","残念ですけど、私は既に劉備、関羽、張飛と義兄妹の契りを結んでおりますわ。","",},
        [2697] = {2506,"主君","私が証人になりますから、お二方で結ぶのはどうでしょう。","私が証人になりますから、お二方で結ぶのはどうでしょう。","",},
        [2698] = {2506,"孫策","天地よ照覧あれ、この孫策は周瑜と今日、ここで義兄弟の契りを交わす。","天地よ照覧あれ、この孫策は周瑜と今日、ここで義兄弟の契りを交わす。","",},
        [2699] = {2506,"孫策","誠をもって相対し、肝胆相照らす。父の遺志を継ぎ、共に天下を取らん。","誠をもって相対し、肝胆相照らす。父の遺志を継ぎ、共に天下を取らん。","",},
        [2700] = {2506,"周瑜","天地よ照覧あれ、この周瑜は孫策と今日、ここで義兄弟の契りを交わす。","天地よ照覧あれ、この周瑜は孫策と今日、ここで義兄弟の契りを交わす。","",},
        [2701] = {2506,"周瑜","誠をもって相対し、肝胆相照らす。主君のため力を尽くし、共に大事を成さん。","誠をもって相対し、肝胆相照らす。主君のため力を尽くし、共に大事を成さん。","",},
        [2702] = {2506,"孫策","この名将令を受け取ってくれ。証人となってくれたお礼だ。","この名将令を受け取ってくれ。証人となってくれたお礼だ。","",},
        [2703] = {2601,"主君","孫策兄。この江東の戦いは、どこから手を付けます?","孫策兄さん。この江東の戦い、どこから手を付けます?","孫策",},
        [2704] = {2601,"孫策","まずは曲阿に駐屯する揚州刺史、劉繇から攻めてはどうだろう。","まずは曲阿に駐屯する揚州刺史、劉繇から攻めてはどうだろう。","",},
        [2705] = {2601,"主君","揚州刺史なのに、なぜ曲阿にいるんです?揚州の州都は寿春でしょう。","揚州刺史なのに、なぜ曲阿にいるんです?揚州の州都は寿春でしょう。","",},
        [2706] = {2601,"孫策","知らないのも無理はないな。朝廷は劉繇を揚州刺史に任じたが、この群雄割拠のご時世だ。州都の寿春は、袁術に占領されているのさ。","知らないのも無理はないな。朝廷は劉繇を揚州刺史に任じたが、この群雄割拠のご時世だ。州都の寿春は、袁術に占領されているのさ。","",},
        [2707] = {2601,"孫策","俺の叔父で丹陽太守の呉景と従兄の孫賁が、劉繇を助けて曲阿に治所を設けてやった。","俺の叔父で丹陽太守の呉景と従兄の孫賁が、劉繇を助けて曲阿に治所を設けてやった。","",},
        [2708] = {2601,"孫策","それなのに奴は体制を整えた途端、叔父を追い払って領地を独り占めした。恩を仇で返したのだ。","それなのに奴は体制を整えた途端、叔父を追い払って領地を独り占めした。恩を仇で返したのだ。","",},
        [2709] = {2601,"主君","なんて恩知らずな。孫策兄は休んでいてください、私が討ってきます！","なんて恩知らずな。孫策兄さんは休んでいてください、私が討ってきます！","",},
        [2710] = {2602,"主君","揚州刺史、劉繇など、取るに足らんさ。","揚州刺史、劉繇など、取るに足らないわ。","張星彩",},
        [2711] = {2602,"張星彩","もちろんよ！劉繇なんか敵じゃないわ！","もちろんよ！劉繇なんか敵じゃないわ！","",},
        [2712] = {2602,"主君","だが配下は、なかなかの人物だったはず。思い出したぞ。孔融の書簡を届けた太史慈だ。なぜ劉繇などに従っているんだ！","だが配下は、なかなかの人物だったはずよ。思い出したわ。孔融の書簡を届けた太史慈よ。なぜ劉繇などに従っているの！","",},
        [2713] = {2602,"張星彩","すごい記憶力ね。その通り、太史慈よ。射れば百発百中っていう、神がかった弓の使い手よ。","すごい記憶力ね。その通り、太史慈よ。射れば百発百中っていう、神がかった弓の使い手よ。","",},
        [2714] = {2602,"張星彩","劉繇とは同郷で、互いに助け合っているんですって。","劉繇とは同郷で、互いに助け合っているんですって。","",},
        [2715] = {2602,"主君","そうだったか。この太史慈を配下にできれば、猛将が一人増えることになるな！","そうだったの。この太史慈を配下にできれば、猛将が一人増えることになるわね！","",},
        [2716] = {2603,"厳白虎","何者だ?わしは東呉の徳王、厳白虎だぞ。どうだ、ビビったか！","何者だ?わしは東呉の徳王、厳白虎だぞ。どうだ、ビビったか！","プレイヤー",},
        [2717] = {2603,"主君","厳白虎?それがどうした！私なんか左に青龍、右に白虎、腰に老牛、胸に龍頭の入れ墨が入ってるぞ！邪魔する奴は人でも仏でも斬るぞ！","厳白虎?それが何よ！私なんか左に青龍、右に白虎、腰に老牛、胸に龍頭の入れ墨が入ってるのよ！邪魔する奴は人でも仏でも斬るわよ！","",},
        [2718] = {2603,"厳白虎","こわっぱめ、ホラ吹きもいい加減にしろ！","こわっぱめ、ホラ吹きもいい加減にしろ！","",},
        [2719] = {2603,"張星彩","厳白虎は元山賊で、自ら呉の徳王と名乗って呉郡を占領してるの。戦うなら気を付けてね！","厳白虎は元山賊で、自ら呉の徳王と名乗って呉郡を占領してるの。戦うなら気を付けてね！","",},
        [2720] = {2603,"主君","ちっぽけな山賊のくせに呉郡を占領するとは、尻尾を巻いて逃げ出すまで打ちのめしてやる！いくぞ！","ちっぽけな山賊のくせに呉郡を占領するとは、尻尾を巻いて逃げ出すまで打ちのめしてやるわ！いくわよ！","",},
        [2721] = {2604,"王朗","孫策の小童よ。負けたからには、殺そうが切り刻もうが自由だ。だがこの王朗、絶対にお前になど従わん。","孫策の小童よ。負けたからには、殺そうが切り刻もうが自由だ。だがこの王朗、絶対にお前になど従わん。","孫策",},
        [2722] = {2604,"孫策","従いたくないならかまわん。この孫策、誰にも無理強いしたことはない！","従いたくないならかまわん。この孫策、誰にも無理強いしたことはない！","",},
        [2723] = {2604,"孫策","曹操は皇帝に上奏し、お前を許昌に召そうとしているのだ。送り届けようではないか。","曹操は皇帝に上奏し、お前を許昌に召そうとしているのだ。送り届けようではないか。","",},
        [2724] = {2604,"主君","孫策兄は本当に度量が広いですね。敬服しますよ。","孫策兄さんは本当に度量が広いですね。敬服します。","",},
        [2725] = {2604,"孫策","ははは、なに、それほどでもない。","ははは、なに、それほどでもない。","",},
        [2726] = {2605,"主君","あとは太史慈だけだ。孫策兄、私が迎え討ちましょう。","あとは太史慈だけね。孫策兄さん、私が迎え討ちましょう。","孫策",},
        [2727] = {2605,"孫策","待て待て。太史慈とは前に、神亭で一騎討ちをしたことがある。俺が奴の背負った手戟を奪い、奴が俺の兜を奪ったところで引き分けた。","待て待て。太史慈とは前に、神亭で一騎討ちをしたことがある。俺が奴の背負った手戟を奪い、奴が俺の兜を奪ったところで引き分けた。","",},
        [2728] = {2605,"孫策","まだ勝負はついていないのだ。そう簡単には譲れん。","まだ勝負はついていないのだ。そう簡単には譲れん。","",},
        [2729] = {2605,"主君","では一度手合わせし、勝ったほうが太史慈と戦うというのはどうです?","では一度手合わせし、勝ったほうが太史慈と戦うというのはどうです?","",},
        [2730] = {2605,"孫策","おお！いいだろう。では手合わせ致そう！","おお！いいだろう。では手合わせ致そう！","",},
        [2731] = {2606,"太史慈","揚威将軍はやはり強いな。敬服の至りだ。この名将令は将軍に差し上げよう。","揚威将軍はやはり強いな。敬服の至りだ。この名将令は将軍に差し上げよう。","プレイヤー",},
        [2732] = {2606,"主君","有難い！","有難いわ！","",},
        [2733] = {2606,"孫策","太史慈は快男子だな！劉繇は愚かにも大将として用いなかったから大敗したのだ。これからは俺と共に戦ってくれ。","太史慈は快男子だな！劉繇は愚かにも大将として用いなかったから大敗したのだ。これからは俺と共に戦ってくれ。","",},
        [2734] = {2606,"太史慈","主君の劉繇が敗れて、将兵の心は離れています。俺が行って残党を集め、孫策殿の手助けをしたいのですが。","主君の劉繇が敗れて、将兵の心は離れています。俺が行って残党を集め、孫策殿の手助けをしたいのですが。","",},
        [2735] = {2606,"孫策","いいだろう。その誠意は俺も望むところだ。明日中に帰って参れ。(太史慈は約束して去る)","いいだろう。その誠意は俺も望むところだ。明日中に帰って参れ。(太史慈は約束して去る)","",},
        [2736] = {2606,"主君","孫策兄、太史慈がこのまま戻って来ないとは思わないんですか。","孫策兄さん、太史慈がこのまま戻って来ないとは思わないんですか。","",},
        [2737] = {2606,"孫策","太史慈は信義の男だ。裏切りはしないさ。","太史慈は信義の男だ。裏切りはしないさ。","",},
        [2738] = {2606,"主君","実をいうと、曹操が太史慈の勇名を聞いて、書簡を送ったそうですよ。中には薬草の当帰が入っていたとか。","実をいうと、曹操が太史慈の勇名を聞いて、書簡を送ったそうですよ。中には薬草の当帰が入っていたとか。","",},
        [2739] = {2606,"主君","自分に帰属せよという誘いですね。だけど結局は、孫策兄が迎え入れることになりましたね！","自分に帰属せよという誘いですね。だけど結局は、孫策兄さんが迎え入れることになりましたね！","",},
        [2740] = {2606,"孫策","この孫策、疑わしい者は用いず、用いれば疑わず。太史慈は我が呉の大将になるだろうな。ははは！","この孫策、疑わしい者は用いず、用いれば疑わず。太史慈は我が呉の大将になるだろうな。ははは！","",},
        [2741] = {2701,"関銀屏","あの人、何をやっているのでしょうか。武術の修練ですの?","あの人、何をやっているのでしょうか。武術の修練ですの?","華佗",},
        [2742] = {2701,"華佗","(五禽戯を練習しつつ、独り言を言う)虎の突き、鹿の蹴り、熊の力、猿の引っかき、鳥の羽ばたき……","(五禽戯を練習しつつ、独り言を言う)虎の突き、鹿の蹴り、熊の力、猿の引っかき、鳥の羽ばたき……","",},
        [2743] = {2701,"張星彩","あっ、師匠だわ！師匠！ここでお会いできるとは思いませんでした！","あっ、師匠だわ！師匠！ここでお会いできるとは思いませんでした！","",},
        [2744] = {2701,"華佗","ははは、我が愛弟子、星彩か！ちょうど江東へ来たところだよ。今まであちらこちらを渡り歩き、医術を施していたんだ。","ははは、我が愛弟子、星彩か！ちょうど江東へ来たところだよ。今まであちらこちらを渡り歩き、医術を施していたんだ。","",},
        [2745] = {2701,"張星彩","師匠、こちらがご主君様です。ご主君様、こちらが師匠の華佗先生。","師匠、こちらがご主君様です。ご主君様、こちらが師匠の華佗先生。","",},
        [2746] = {2701,"華佗","へえ、この方が揚威将軍か。お若いね、先が楽しみだ。(さすが左慈が「天命を受けた人」というだけのことはある)","へえ、この方が揚威将軍か。お若いね、先が楽しみだ。(さすが左慈が「天命を受けた人」というだけのことはある)","",},
        [2747] = {2701,"主君","お会いできて光栄です。あの名医、華佗先生でしたか！","お会いできて光栄ですわ。あの名医、華佗先生でしたか！","",},
        [2748] = {2701,"華佗","星彩、この五禽戯を取っておきなさい。主君に差し上げれば、若さと寿命を延ばすことができる。","星彩、この五禽戯を取っておきなさい。主君に差し上げれば、若さと寿命を延ばすことができる。","",},
        [2749] = {2701,"華佗","私は用事で会稽に行かねばならぬ。先に失礼するよ。","私は用事で会稽に行かねばならぬ。先に失礼するよ。","",},
        [2750] = {2701,"関銀屏","星彩、師匠は本当にお若いですわね……あら、もう行ってしまいましたの！本当に変な人！","星彩、師匠は本当にお若いですわね……あら、もう行ってしまいましたの！本当に変な人！","",},
        [2751] = {2701,"張星彩","師匠はいつもああなの。ご主君様も姉さまも悪く思わないでね。","師匠はいつもああなの。ご主君様も姉さまも悪く思わないでね。","",},
        [2752] = {2702,"張星彩","見て！どうして宣城が山賊に囲まれちゃってるの！山賊がこんなに大勢いるなんて！","見て！どうして宣城が山賊に囲まれちゃってるの！山賊がこんなに大勢いるなんて！","プレイヤー",},
        [2753] = {2702,"主君","宣城は、孫策の弟の孫権が守っていたはずじゃ?！早く助けに行こう！","宣城は、孫策の弟の孫権が守っていたはずじゃ?！早く助けに行きましょう！","",},
        [2754] = {2702,"関銀屏","いやですわ、山賊がこんなに大勢！！ようし、全員ぶちのめしますわよ！","いやですわ、山賊がこんなに大勢！！ようし、全員ぶちのめしますわよ！","",},
        [2755] = {2703,"周泰","救援……痛み入る……！各々方……孫権将軍を……お守りしてくれ……！(言い終わると、重傷を負った周泰は倒れ、意識を失う)","救援……痛み入る……！各々方……孫権将軍を……お守りしてくれ……！(言い終わると、重傷を負った周泰は倒れ、意識を失う)","プレイヤー",},
        [2756] = {2703,"孫権","周泰！俺をかばって、これほどの傷を負って……！","周泰！俺をかばって、これほどの傷を負って……！","",},
        [2757] = {2703,"主君","周泰は誠の忠義の士だ。こころから尊敬するよ。星彩、早く治療してやってくれ！","周泰は誠の忠義の士。こころから尊敬するわ。星彩、早く治療してあげて！","",},
        [2758] = {2703,"張星彩","ひどい怪我だわ！全身に十二ヶ所も傷を負ってる。しかも重傷よ！","ひどい怪我だわ！全身に十二ヶ所も傷を負ってる。しかも重傷よ！","",},
        [2759] = {2703,"張星彩","とりあえず、金針で経穴を封じて、一命はとりとめたけど。これ以上の治療は、師匠の華佗先生に来てもらわないと。","とりあえず、金針で経穴を封じて、一命はとりとめたけど。これ以上の治療は、師匠の華佗先生に来てもらわないと。","",},
        [2760] = {2703,"孫権","有難い！その方は、今どこにおられるのだ?","有難い！その方は、今どこにおられるのだ?","",},
        [2761] = {2703,"張星彩","ここに来る前にお会いしたとき、会稽に行くって言ってたけど……","ここに来る前にお会いしたとき、会稽に行くって言ってたけど……","",},
        [2762] = {2703,"孫権","よし、早く会稽へ使者を！周泰を治療してもらうのだ！","よし、早く会稽へ使者を！周泰を治療してもらうのだ！","",},
        [2763] = {2704,"華佗","揚威将軍はやはり強いですね！この名将令を差し上げましょう。","揚威将軍はやはり強いですね！この名将令を差し上げましょう。","プレイヤー",},
        [2764] = {2704,"主君","前に五禽戯をいただいて、今度は名将令もいただくとは。感謝に堪えません。","前に五禽戯をいただいて、今度は名将令もいただくとは。感謝に堪えません。","",},
        [2765] = {2704,"華佗","将軍はこの乱世を終わらせる天命を受けた人ですからね。","将軍はこの乱世を終わらせる天命を受けた人ですからね。","",},
        [2766] = {2704,"華佗","私は五禽戯を研究しましたが、是非将軍にはお勤め頂きたい。きっと寿命を延ばし、体を強くします。","私は五禽戯を研究しましたが、是非将軍にはお勤め頂きたい。きっと寿命を延ばし、体を強くします。","",},
        [2767] = {2704,"主君","華佗先生のお顔がこんなに若いのも、そのお陰なのですか?","華佗先生のお顔がこんなにお若いのも、そのお陰なんですか?","",},
        [2768] = {2704,"華佗","そのとおり。","そのとおり。","",},
        [2769] = {2704,"主君","それは素晴らしい。きっと朝晩一回ずつ練習します。先生のようにいつまでも若くいたいですからね！","それは素晴らしい。必ず朝晩一回ずつ練習しますわ。先生のようにいつまでも若くいたいですから！","",},
        [2770] = {2704,"主君","星彩、銀屏も一緒にやろう。きっと良い嫁ぎ先を見つけてやれるぞ！","星彩、銀屏も一緒にやりましょう。きっと良い嫁ぎ先を見つけてやれるわよ！","",},
        [2771] = {2704,"張星彩","それはいいよ、星彩はずっとご主君様と一緒にいるんだから！","それはいいよ、星彩はずっとご主君様と一緒にいるんだから！","",},
        [2772] = {2704,"関銀屏","私もですわ！ずっとご主君様のために戦います！","私もですわ！ずっとご主君様のために戦います！","",},
        [2773] = {2704,"主君","はははは、分かった分かった！ところで陳留にまた黄巾賊の残党が現れたらしい。討伐に行こう！","あはは、分かったわよ。ところで陳留にまた黄巾賊の残党が現れたようね。討伐に行きましょう！","",},
        [2774] = {2801,"主君","この陳留も中原の真ん中なのに、飢えた民だらけだ。どうして民の生活はこんなに荒れているんだ?","この陳留も中原の真ん中なのに、飢えた民だらけね。どうして民の生活はこんなに荒れているの?","張星彩",},
        [2775] = {2801,"張星彩","この二年ほど、蝗害がひどくて、田畑がイナゴに食い荒らされてしまったの。","この二年ほど、蝗害がひどくて、田畑がイナゴに食い荒らされてしまったの。","プレイヤー",},
        [2776] = {2801,"張星彩","函谷関より東では穀物一斛が五十貫もして、人々が食い合うことさえ起こってるみたいよ。","函谷関より東では穀物一斛が五十貫もして、人々が食い合うことさえ起こってるみたいよ。","プレイヤー",},
        [2777] = {2801,"主君","ここは曹操の勢力範囲だ。食糧をもらいに行こう。被災民を救うんだ。","ここは曹操の勢力範囲よ。食糧をもらいに行きましょう。被災民を救うのよ。","張星彩",},
        [2778] = {2802,"主君","曹操兄。この陳留は被災民で溢れています。なぜ食糧を出して被災民を助けないんですか?","曹操兄さん。この陳留は被災民で溢れています。なぜ食糧を出して被災民を助けないんですか?","曹操",},
        [2779] = {2802,"曹操","もちろん、わしも頭を悩ませておる。だが最近のイナゴの害が酷く、ましてや呂布と戦ったばかり。軍にもあまり糧食はないのだ。","もちろん、わしも頭を悩ませておる。だが最近のイナゴの害が酷く、ましてや呂布と戦ったばかり。軍にもあまり糧食はないのだ。","プレイヤー",},
        [2780] = {2802,"荀彧","それですが……黄巾賊の残党の何儀や黄劭が、あちこちで略奪を働き、金や食糧を多く持っていると聞きます。","それですが……黄巾賊の残党の何儀や黄劭が、あちこちで略奪を働き、金や食糧を多く持っていると聞きます。","プレイヤー",},
        [2781] = {2802,"荀彧","この賊を討って食糧を奪えば、軍も養えますし、朝廷も民衆も喜びます。天意にかなうことでしょう。","この賊を討って食糧を奪えば、軍も養えますし、朝廷も民衆も喜びます。天意にかなうことでしょう。","プレイヤー",},
        [2782] = {2802,"主君","いい考えですね。私が先鋒になって、黄巾賊を掃討しましょう。朗報を待っていてください。","いい考えですね。私が先鋒になって、黄巾賊を掃討しましょう。朗報を待っててください。","荀彧",},
        [2783] = {2803,"黄劭","俺は截天夜叉の盟友、黄劭だ！お前ら、何者だァ?","俺は截天夜叉の盟友、黄劭だ！お前ら、何者だァ?","関銀屏",},
        [2784] = {2803,"関銀屏","ご主君様は、当世の天子自ら封じられた揚威将軍ですわよ。黄巾の賊め、大人しくお縄を頂戴なさい！","ご主君様は、当世の天子自ら封じられた揚威将軍ですわよ。黄巾の賊め、大人しくお縄を頂戴なさい！","黄劭",},
        [2785] = {2803,"黄劭","揚威将軍だとォ、聞いたこともねえな！誰だろうが、わが黄巾天軍に攻めかかってくる奴はぶちのめしてやる。跪いて許しを請え！","揚威将軍だとォ、聞いたこともねえな！誰だろうが、わが黄巾天軍に攻めかかってくる奴はぶちのめしてやる。跪いて許しを請え！","関銀屏",},
        [2786] = {2804,"黄劭","揚威将軍を存じ上げず、大変失礼しました。将軍、どうか命ばかりはお許しください！","揚威将軍を存じ上げず、大変失礼しました。将軍、どうか命ばかりはお許しください！","関銀屏",},
        [2787] = {2804,"関銀屏","あーら！今さら命乞いですの?ちょっとばかり遅いんじゃありません?","あーら！今さら命乞いですの?ちょっとばかり遅いんじゃありません?","黄劭",},
        [2788] = {2804,"主君","いいだろう、命は助けてやってもいい。山寨にある食糧や金帛を全部出せばな。","いいでしょう、命は助けてあげる。山寨にある食糧や金帛を全部出せばね。","黄劭",},
        [2789] = {2804,"黄劭","いえ実は、我らの山寨に大男がやって来て、手下の数百人が擒にされてしまったのです。食糧や金帛が欲しければ、そいつと戦って勝たねばなりません。","いえ実は、我らの山寨に大男がやって来て、手下の数百人が擒にされてしまったのです。食糧や金帛が欲しければ、そいつと戦って勝たねばなりません。","プレイヤー",},
        [2790] = {2804,"関銀屏","どういう輩なのかしら。ちょっと見物に行ってさしあげましょう！","どういう輩なのかしら。ちょっと見物に行ってさしあげましょう！","黄劭",},
        [2791] = {2805,"関銀屏","見てくださいな。この大男が、わたしたちの獲物を横取りしてますのよ。","見てくださいな。この大男が、わたしたちの獲物を横取りしてますのよ。","プレイヤー",},
        [2792] = {2805,"主君","ううむ。見る限り身の丈八尺、腰回りは十囲はあるな。何とも、すごいものだ。","へえ。見る限り身の丈八尺、腰回りは十囲はあるわね。すごい！","関銀屏",},
        [2793] = {2805,"関銀屏","んもう！ご主君様ったら、お優しい言葉遣いですわね。","んもう！ご主君様ったら、お優しい言葉遣いですわね。","プレイヤー",},
        [2794] = {2805,"関銀屏","単なるデブじゃありませんの。ちょっと、そこのおデブさん、早く黄巾の残党を渡しなさい！","単なるデブじゃありませんの。ちょっと、そこのおデブさん、早く黄巾の残党を渡しなさい！","プレイヤー",},
        [2795] = {2805,"許褚","ブヒッ！馬鹿め、これは筋肉だ！","ブヒッ！馬鹿め、これは筋肉だ！","プレイヤー",},
        [2796] = {2805,"許褚","その証拠におらはこの百八十斤の流星錘を自在に振り回せるんだぞう！おらに勝てたら何でも渡してやるぞう。どうだぁ！","その証拠におらはこの百八十斤の流星錘を自在に振り回せるんだぞう！おらに勝てたら何でも渡してやるぞう。どうだぁ！","プレイヤー",},
        [2797] = {2805,"主君","銀屏、私の後ろに回れ。奴の流星錘で怪我をするなよ！","銀屏、私の後ろに回って。奴の流星錘で怪我をしないでね！","許褚",},
        [2798] = {2806,"許褚","ブヒィィィっ！この許褚、完敗するなんてぇ！この名将令はあんたにあげるんだぞう。だからおらもお供をさせてくれだぞう！","ブヒィィィっ！この許褚、完敗するなんてぇ！この名将令はあんたにあげるんだぞう。だからおらもお供をさせてくれだぞう！","プレイヤー",},
        [2799] = {2806,"主君","私は各地を転戦する身だ。曹操兄に従うほうがいいだろう。","いえ、私は各地を転戦する身だから。曹操兄さんに従うほうがいいでしょう。","許褚",},
        [2800] = {2806,"主君","曹操兄、私から大将を推薦します。こちらは許褚、武芸は典韋に勝るとも劣りません。","曹操兄さん、私から大将を推薦します。こちらは許褚、武芸は典韋に勝るとも劣りません。","曹操",},
        [2801] = {2806,"曹操","ははは、虎のごとき猛将を二人得るとは幸いだ。","ははは、虎のごとき猛将を二人得るとは幸いだ。","プレイヤー",},
        [2802] = {2806,"曹操","左に典韋、右に許褚、もはや、この世でわしを傷つけられる者は一人もおらぬだろう！","左に典韋、右に許褚、もはや、この世でわしを傷つけられる者は一人もおらぬだろう！","プレイヤー",},
        [2803] = {2901,"曹操","若き英雄よ、張繍が賈詡を参謀に用い、劉表と結託して宛城に駐屯しておる。お主はこれをどう見る?","若き英雄よ、張繍が賈詡を参謀に用い、劉表と結託して宛城に駐屯しておる。お主はこれをどう見る?","プレイヤー",},
        [2804] = {2901,"主君","まずは使者を送り、張繍に降伏を説くのがいいでしょう。","まずは使者を送り、張繍に降伏を説くのがいいでしょう。","曹操",},
        [2805] = {2901,"主君","賈詡は判断に優れ、時機を見る目があります。私が投降を勧めましょう。一兵も使わず、宛城を手に入れられます。","賈詡は判断に優れ、時機を見る目があります。私が投降を勧めましょう。一兵も使わず、宛城を手に入れられます。","曹操",},
        [2806] = {2901,"曹操","それは素晴らしい。若き英雄よ、恩に着るぞ！","それは素晴らしい。若き英雄よ、恩に着るぞ！","プレイヤー",},
        [2807] = {2902,"曹操","ご夫人の名は?","ご夫人の名は?","鄒氏",},
        [2808] = {2902,"鄒氏","張済の妻の鄒氏と申します。","張済の妻の鄒氏と申します。","曹操",},
        [2809] = {2902,"曹操","わしを知っておるか?","わしを知っておるか?","鄒氏",},
        [2810] = {2902,"鄒氏","もちろんです。丞相の威名は常々うかがっております。今晩こうしてお会いできて幸運にございます。","もちろんです。丞相の威名は常々うかがっております。今晩こうしてお会いできて幸運にございます。","曹操",},
        [2811] = {2902,"曹操","ご夫人に免じて、特別に張繍の降伏を認めよう。一族郎党滅ぼしてくれるところであった。","ご夫人に免じて、特別に張繍の降伏を認めよう。一族郎党滅ぼしてくれるところであった。","鄒氏",},
        [2812] = {2902,"曹操","ご夫人と会えたのは天の恵み。今宵は枕を並べようではないか。わしにすべてを任せ、贅沢な暮らしを楽まぬか?","ご夫人と会えたのは天の恵み。今宵は枕を並べようではないか。わしにすべてを任せ、贅沢な暮らしを楽まぬか?","鄒氏",},
        [2813] = {2902,"鄒氏","丞相のお言葉に従います。","丞相のお言葉に従います。","曹操",},
        [2814] = {2903,"張繍","曹操め、叔母を辱めるとは、俺を侮るにもほどがある！奴を殺さぬ限り、この恨みは晴らせぬ！","曹操め、叔母を辱めるとは、俺を侮るにもほどがある！奴を殺さぬ限り、この恨みは晴らせぬ！","賈詡",},
        [2815] = {2903,"賈詡","これは秘密の策なんですけど……明日、曹操が天幕から出てくるのを待って、こう言うのです。","これは秘密の策なんですけど……明日、曹操が天幕から出てくるのを待って、こう言うのです。","張繍",},
        [2816] = {2903,"賈詡","投降する兵が多く逃亡者も出たので、中軍に編入させてほしいと。","投降する兵が多く逃亡者も出たので、中軍に編入させてほしいと。","張繍",},
        [2817] = {2903,"張繍","だが曹操の手下の典韋は並外れた猛者だぞ。どう対処する?","だが曹操の手下の典韋は並外れた猛者だぞ。どう対処する?","賈詡",},
        [2818] = {2903,"賈詡","簡単な事です。酒を飲ませ、酔わせるのです。その隙に、人をやって戟を盗ませれば、怖くありません。","簡単な事です。酒を飲ませ、酔わせるのです。その隙に、人をやって戟を盗ませれば、怖くありません。","張繍",},
        [2819] = {2903,"張繍","それは妙計だ！必ず曹操を斬ってやるぞ。","それは妙計だ！必ず曹操を斬ってやるぞ。","賈詡",},
        [2820] = {2904,"主君","変だな、城内がなぜこんなに混乱している?張繍はもう曹操に投降したはずじゃないのか?","変ね、城内はなぜこんなに混乱しているの?張繍はもう曹操に投降したはずじゃ?","関銀屏",},
        [2821] = {2904,"関銀屏","大変ですわ！","大変ですわ！","プレイヤー",},
        [2822] = {2904,"関銀屏","張繍は一旦投降したのですけど、曹操が叔母の鄒氏に手を出したのを知り考えを変え、策を講じて対抗しようとしているのですわ！","張繍は一旦投降したのですけど、曹操が叔母の鄒氏に手を出したのを知り考えを変え、策を講じて対抗しようとしているのですわ！","プレイヤー",},
        [2823] = {2904,"主君","そうだったのか！早く曹操兄を助けに行かないと！","そうだったのね！早く曹操兄さんを助けに行かないと！","関銀屏",},
        [2824] = {2905,"張星彩","一歩遅かったわ！典韋が曹操を助けるために重傷を負っちゃった！","一歩遅かったわ！典韋が曹操を助けるために重傷を負っちゃった！","プレイヤー",},
        [2825] = {2905,"主君","星彩、早く典韋を見てやってくれ。","星彩、早く典韋を見てやって！","張星彩",},
        [2826] = {2905,"張星彩","残念だけど、星彩には無理よ。傷が深すぎる！","残念だけど、星彩には無理よ。傷が深すぎる！","プレイヤー",},
        [2827] = {2905,"典韋","俺が陣門に立ちはだかって、丞相を裏から馬で逃がした……だが追撃の兵は多く……このままでは危うい。","俺が陣門に立ちはだかって、丞相を裏から馬で逃がした……だが追撃の兵は多く……このままでは危うい。","プレイヤー",},
        [2828] = {2905,"典韋","揚威将軍、そなたは丞相とも懇意だ！どうかお助けしてくれ。でないと、俺は死んでも死に切れん！","揚威将軍、そなたは丞相とも懇意だ！どうかお助けしてくれ。でないと、俺は死んでも死に切れん！","プレイヤー",},
        [2829] = {2905,"主君","典韋将軍、ご安心を。曹操殿は必ず助け出します！","典韋将軍、ご安心を。曹操殿は必ず助け出します！","典韋",},
        [2830] = {2905,"典韋","早くお逃げください、我が君……(典韋は低く呻くと、息絶える)","早くお逃げください、我が君……(典韋は低く呻くと、息絶える)","プレイヤー",},
        [2831] = {3001,"張星彩","ご主君様、ずっと追いかけてきたのに、なぜ曹操の姿が見えないのかしら?まさか、違う道を逃げたとか?","ご主君様、ずっと追いかけてきたのに、なぜ曹操の姿が見えないのかしら?まさか、違う道を逃げたとか?","プレイヤー",},
        [2832] = {3001,"主君","そうだな。逃げる時に道を間違え、違う方向へ行ったのかもしれん。だが慌てるな、向こうから来るのは曹操の手下の于禁のようだ。","そうね。逃げる時に道を間違えて、違う方向へ行ったのかも。でも慌てないで、向こうから来るのは曹操の手下の于禁みたいよ。","張星彩",},
        [2833] = {3001,"于禁","揚威将軍、宛城がまた乱れておるようだな。丞相の姿は見たか?","揚威将軍、宛城がまた乱れておるようだな。丞相の姿は見たか?","プレイヤー",},
        [2834] = {3001,"主君","見ていない。今しがた宛城を出てきたところだが、張繍が突然歯向かい、典韋将軍も戦死して、どこへ逃げたか分からないのだ。","見ていないわ。今しがた宛城を出てきたところだけど、張繍が突然歯向かって、典韋将軍も戦死して、どこへ逃げたか分からないのよ。","于禁",},
        [2835] = {3001,"于禁","そうだったのか！では俺もすぐ兗州の兵を整えて救援に行こう！","そうだったのか！では俺もすぐ兗州の兵を整えて救援に行こう！","プレイヤー",},
        [2836] = {3001,"主君","それが良いだろう。張繍勢は数も多い。私たちは兵を二つに分け、引き続き丞相を探そう！","それが良いわね。張繍勢は数も多い。私たちは兵を二つに分け、引き続き丞相を探しましょう！","于禁",},
        [2837] = {3002,"曹昂","父上の馬はもう走れません。既に三本の矢を受け、今また目に一本当たりました。私の馬に乗って、早くお逃げください。","父上の馬はもう走れません。既に三本の矢を受け、今また目に一本当たりました。私の馬に乗って、早くお逃げください。","曹操",},
        [2838] = {3002,"曹操","わしがお前の馬に乗ったら、お前はどうするのだ?","わしがお前の馬に乗ったら、お前はどうするのだ?","曹昂",},
        [2839] = {3002,"曹昂","ご安心ください。私は武芸に優れております。残って後ろの敵を断ちましょう！","ご安心ください。私は武芸に優れております。残って後ろの敵を断ちましょう！","曹操",},
        [2840] = {3002,"曹昂","(そう言うと馬の尻を叩き、馬は曹操を乗せて走り去る。後ろからは矢が雨のごとく降り注ぎ、曹昂は力の限り戦う)","(そう言うと馬の尻を叩き、馬は曹操を乗せて走り去る。後ろからは矢が雨のごとく降り注ぎ、曹昂は力の限り戦う)","曹操",},
        [2841] = {3003,"丁夫人","あなたは私の息子を殺しました。それならもはや、あなたのもとに留まる理由はありません！(丁夫人は曹昂の戦死を聞き、怒って実家に帰った)","あなたは私の息子を殺しました。それならもはや、あなたのもとに留まる理由はありません！(丁夫人は曹昂の戦死を聞き、怒って実家に帰った)","曹操",},
        [2842] = {3003,"曹操","済まなかった……夫人よ、馬車に乗ってわしと共に帰ろう、どうだ?","済まなかった……夫人よ、馬車に乗ってわしと共に帰ろう、どうだ?","丁夫人",},
        [2843] = {3003,"丁夫人","嫌です！","嫌です！","曹操",},
        [2844] = {3003,"曹操","そんなことは言わず、わしと共に帰ろう。","そんなことは言わず、わしと共に帰ろう。","丁夫人",},
        [2845] = {3003,"丁夫人","いやです！","いやです！","曹操",},
        [2846] = {3003,"曹操","わしは生涯、あまり後悔したことはない。だが死後に霊魂があり、母親はどこかと曹昂から尋ねられたら、何と答えれば良いのだ！","わしは生涯、あまり後悔したことはない。だが死後に霊魂があり、母親はどこかと曹昂から尋ねられたら、何と答えれば良いのだ！","丁夫人",},
        [2847] = {3004,"夏侯惇","揚威将軍っ！丞相への厚情には感謝するが、なぜ俺の配下の青州兵を殺したのだぁっ！?","揚威将軍っ！丞相への厚情には感謝するが、なぜ俺の配下の青州兵を殺したのだぁっ！?","プレイヤー",},
        [2848] = {3004,"主君","そんな質問をするとは失望しましたよ、夏侯将軍。そちらの青州軍は、元はと言えば私が服従させた黄巾の残党です。","そんな質問をするとは失望したわよ、夏侯将軍。あなたの青州軍は、元はと言えば私が服従させた黄巾の残党じゃないの。","夏侯惇",},
        [2849] = {3004,"主君","宛城から丞相を助けた時、途中でそいつらが民家を略奪するのを見たから、成敗したんです！不服ですか?","宛城から丞相を助けた時、途中でそいつらが民家を略奪するのを見たから、成敗したのよ！不服なの?","夏侯惇",},
        [2850] = {3004,"夏侯惇","ぐっ……そうか、俺がうかつだった、そのような事があったとは！わが軍の軍紀が緩んでいたせいだっ！","ぐっ……そうか、俺がうかつだった、そのような事があったとは！わが軍の軍紀が緩んでいたせいだっ！","プレイヤー",},
        [2851] = {3005,"曹操","于禁よ、青州兵から話は聞いた。宛城が乱れた隙に謀反を企てたそうだな。罪を認めよ！","于禁よ、青州兵から話は聞いた。宛城が乱れた隙に謀反を企てたそうだな。罪を認めよ！","于禁",},
        [2852] = {3005,"主君","曹操兄、それはとんでもない濡れ衣です！","曹操兄さん、それはとんでもない濡れ衣です！","曹操",},
        [2853] = {3005,"曹操","なんだと?","なんだと?","プレイヤー",},
        [2854] = {3005,"主君","あの青州の黄巾が、そもそも黄巾賊だったのは覚えているでしょう。","あの青州の黄巾が、そもそも黄巾賊だったのは覚えているでしょう。","曹操",},
        [2855] = {3005,"主君","奴らの一部が賊の性根のまま、乱に乗じて民家を略奪したのです。私と于禁将軍は、そいつらに仕置きをしてやったに過ぎない。","奴らの一部が賊の性根のまま、乱に乗じて民家を略奪したのよ。私と于禁将軍は、そいつらに仕置きをしてやったに過ぎないわ。","曹操",},
        [2856] = {3005,"曹操","そうだったか。危うく誤解するところだったぞ、于禁。なぜ早くわしに言わん?","そうだったか。危うく誤解するところだったぞ、于禁。なぜ早くわしに言わん?","于禁",},
        [2857] = {3005,"于禁","情勢は急を告げ、敵が追ってきておりましたので、まずは敵を倒したのです。","情勢は急を告げ、敵が追ってきておりましたので、まずは敵を倒したのです。","曹操",},
        [2858] = {3005,"于禁","丞相は道理の分かるお方。流言は智者のところで止まると申しますから、信じておりました。","丞相は道理の分かるお方。流言は智者のところで止まると申しますから、信じておりました。","曹操",},
        [2859] = {3005,"曹操","将軍は危急の時も兵を整え塁を築き、他人にどう言われようと労苦もいとわず、敗北を勝利に変えた。","将軍は危急の時も兵を整え塁を築き、他人にどう言われようと労苦もいとわず、敗北を勝利に変えた。","プレイヤー",},
        [2860] = {3005,"曹操","いにしえの名将もこれに勝るものはない！","いにしえの名将もこれに勝るものはない！","プレイヤー",},
        [2861] = {3005,"主君","さすが曹操兄は、話がわかるな！","さすが曹操兄さん、話がわかるわね！","曹操",},
        [2862] = {3005,"曹操","宛城の役で、わしは長子と甥を失ったが、深い痛手ではない。ただ典韋のために泣こう！","宛城の役で、わしは長子と甥を失ったが、深い痛手ではない。ただ典韋のために泣こう！","プレイヤー",},
        [2863] = {3101,"袁術","昔、漢の高祖は泗水の亭長に過ぎなかったが、天下を取った。だがそれから四百年、命運は尽き、世は乱れた。","昔、漢の高祖は泗水の亭長に過ぎなかったが、天下を取った。だがそれから四百年、命運は尽き、世は乱れた。","",},
        [2864] = {3101,"袁術","我が家系は四代続けて三公を出し、民心を集めておる。天意に従い、オレも皇帝の位につくべきだ。皆の者はどう思う?","我が家系は四代続けて三公を出し、民心を集めておる。天意に従い、オレも皇帝の位につくべきだ。皆の者はどう思う?","",},
        [2865] = {3101,"閣象","いけません。昔、周の后稷は徳を積み功を上げ、文王に至っては広く天下の支持を得ながら、なお殷に服しました。","いけません。昔、周の后稷は徳を積み功を上げ、文王に至っては広く天下の支持を得ながら、なお殷に服しました。","袁術",},
        [2866] = {3101,"閣象","袁術殿の家柄は高貴なれど、周ほどではなく、漢王室は衰退したとはいえ、殷の紂王のごとき暴虐ではありません。絶対になりませぬ。","袁術殿の家柄は高貴なれど、周ほどではなく、漢王室は衰退したとはいえ、殷の紂王のごとき暴虐ではありません。絶対になりませぬ。","袁術",},
        [2867] = {3101,"袁術","オレの袁という姓は陳の国から出ている。陳国は舜の末裔だ。五行では舜は火徳だから、土徳の漢のあとに続くにはふさわしい。","オレの袁という姓は陳の国から出ている。陳国は舜の末裔だ。五行では舜は火徳だから、土徳の漢のあとに続くにはふさわしい。","",},
        [2868] = {3101,"袁術","予言にも「漢に代わる者は当塗高なり」という。オレの字は「公路」で、「路」も「塗」も同じく「みち」という意味だ。","予言にも「漢に代わる者は当塗高なり」という。オレの字は「公路」で、「路」も「塗」も同じく「みち」という意味だ。","",},
        [2869] = {3101,"袁術","伝国の玉璽もある。皇帝とならずば天の道に背くのだ。オレの意は決まったぞ。これ以上言うものは斬る！","伝国の玉璽もある。皇帝とならずば天の道に背くのだ。オレの意は決まったぞ。これ以上言うものは斬る！","",},
        [2870] = {3102,"張星彩","ご主君様、袁術がついにぶちかましたわよ。寿春で皇帝を称したんですって！","ご主君様、袁術がついにぶちかましたわよ。寿春で皇帝を称したんですって！","プレイヤー",},
        [2871] = {3102,"関銀屏","袁術はずっと邪念を持っていたのですわ。でも、まさか皇帝を称するなんて！片腹痛いとはこのことですわね、おーっほっほっ！","袁術はずっと邪念を持っていたのですわ。でも、まさか皇帝を称するなんて！片腹痛いとはこのことですわね、おーっほっほっ！","プレイヤー",},
        [2872] = {3102,"主君","まったくだ。袁術のような安っぽい皇帝がその地位に居続けられるなら、私は素っ裸で洛陽の城の上を三周回ってやる！","まったくよ。袁術のような安っぽい皇帝がその地位に居続けられるなら、私は素っ裸で洛陽の城の上を三周回ってやるわ！","関銀屏",},
        [2873] = {3102,"張星彩","ご主君様、今はとても寒いわ。そんなことしたら凍え死ぬわよ！","ご主君様、今はとても寒いわ。そんなことしたら凍え死ぬわよ！","プレイヤー",},
        [2874] = {3102,"関銀屏","馬鹿ですわね星彩、冗談をマジメに返答してどうしますの！","馬鹿ですわね星彩、冗談をマジメに返答してどうしますの！","プレイヤー",},
        [2875] = {3102,"主君","(困って大笑い)行こう行こう、どこかで味方を得て、一緒に袁術を討つんだ！","(苦笑い)行きましょう、味方を探して、一緒に袁術を討つのよ！","関銀屏",},
        [2876] = {3103,"主君","袁術、恐れ多くも自ら皇帝を称するとは！とても天下の諸侯には受け入れられぬ。","袁術、恐れ多くも自ら皇帝を称するとは！とても天下の諸侯には受け入れられぬ。","袁術",},
        [2877] = {3103,"主君","私はすでに曹操、劉備、呂布と同盟を組んだ。三つの大軍が寿春にむかっている。お前は袋叩きに遭うぞ！","私は曹操、劉備、呂布と同盟を結んだわ。三つの大軍が寿春にむかっている。あんたなんか袋叩きに遭うわよ！","袁術",},
        [2878] = {3103,"袁術","馬鹿め！玉璽があれば、天下は朕のもの。邪魔する者は誰であろうとぶっ潰すのみよ！","馬鹿め！玉璽があれば、天下は朕のもの。邪魔する者は誰であろうとぶっ潰すのみよ！","プレイヤー",},
        [2879] = {3104,"関銀屏","袁術のやつ、やはり笑わせてくれますわね！見てごらんなさい、勝てないとなったら逃げていきますわ！","袁術のやつ、やはり笑わせてくれますわね！見てごらんなさい、勝てないとなったら逃げていきますわ！","プレイヤー",},
        [2880] = {3104,"関銀屏","私たちも名将令が一つ手に入ったし、無駄足にはなりませんでしたわ。","私たちも名将令が一つ手に入ったし、無駄足にはなりませんでしたわ。","プレイヤー",},
        [2881] = {3104,"主君","袁術は破ったが、奴の勢力範囲はまだ大きい。何か方法を考えないと。","袁術は破ったけど、奴の勢力範囲はまだ大きいわ。何か方法を考えないと。","関銀屏",},
        [2882] = {3104,"関銀屏","袁術の勢力範囲は、南陽、汝南、それに江淮一帯。","袁術の勢力範囲は、南陽、汝南、それに江淮一帯。","プレイヤー",},
        [2883] = {3104,"関銀屏","地盤は広く、人口も多く、食糧も豊富ですの。何とかして勢力を削がないといけませんわね。","地盤は広く、人口も多く、食糧も豊富ですの。何とかして勢力を削がないといけませんわね。","プレイヤー",},
        [2884] = {3104,"主君","なんの！他はともかく江東一帯は、私が奴の勢いを消してやる！","よし！他はともかく江東一帯の袁術の勢力は、私が滅ぼしてやるわ！","関銀屏",},
        [2885] = {3104,"関銀屏","そもそも玉璽は、孫堅さんが洛陽で見つけて、江東へ持っていったもの。それを袁術に渡した孫策さんは、内心どう思っているのでしょう。","そもそも玉璽は、孫堅さんが洛陽で見つけて、江東へ持っていったもの。それを袁術に渡した孫策さんは、内心どう思っているのでしょう。","プレイヤー",},
        [2886] = {3104,"主君","よし。では江東へ行って、孫策と旧交を温めるとしようか！","よし。じゃあ江東へ行って、孫策と旧交を温めるとしましょうか！","関銀屏",},
        [2887] = {3201,"孫策","若き英雄か、久しぶりだな。よく江東へ来てくれた。","若き英雄か、久しぶりだな。よく江東へ来てくれた。","プレイヤー",},
        [2888] = {3201,"孫策","何度も助けてもらい、家族も同然！精一杯もてなしをさせてもらうぞ！誰か、酒と料理を！","何度も助けてもらい、家族も同然！精一杯もてなしをさせてもらうぞ！誰か、酒と料理を！","プレイヤー",},
        [2889] = {3201,"主君","それは有難い！ではとっておきの秘蔵酒を出してもらうかな。もったいないなんて言うのはナシですよ！","やったあ！じゃあとっておきの秘蔵酒を出してもらおうかしら。もったいないなんて言わせないわよ！","孫策",},
        [2890] = {3201,"孫策","もちろんだとも、さあ、一杯おつぎしよう！","もちろんだとも、さあ、一杯おつぎしよう！","プレイヤー",},
        [2891] = {3201,"主君","(酒が回ってきたところで)孫策兄、実は一つ忠告したいことがあるのです！","(酒が回ってきたところで)孫策兄さん、実は一つ忠告したいことがあるの！","孫策",},
        [2892] = {3201,"孫策","おうとも！お互い身内も同然だ、なんでも言ってくれ！","おうとも！お互い身内も同然だ、なんでも言ってくれ！","プレイヤー",},
        [2893] = {3201,"主君","袁術は勝手に天子を称し、周りはみな離れていっています。","袁術は勝手に天子を称し、周りはみな離れていっています。","孫策",},
        [2894] = {3201,"主君","必ずや世間の攻撃の的になるでしょう。あなたも断交したほうがよいかと思います。","必ずや世間の攻撃の的になるでしょう。あなたも断交したほうがよいかと思いますわ。","孫策",},
        [2895] = {3201,"孫策","ははは、英雄の見解はだいたい一致するな。俺も書簡で諫めたことがあるが、袁術は聞き入れなかったのだ。","ははは、英雄の見解はだいたい一致するな。俺も書簡で諫めたことがあるが、袁術は聞き入れなかったのだ。","プレイヤー",},
        [2896] = {3201,"孫策","いま君の話を聞いて、ますます縁を切ろうという気になった。","いま君の話を聞いて、ますます縁を切ろうという気になった。","プレイヤー",},
        [2897] = {3201,"主君","そうだったのですか。やはり孫策兄は見る目がある！","そうだったんですか。やっぱり孫策兄さんは見る目がありますね！","孫策",},
        [2898] = {3201,"孫策","これから俺は廬江を攻め、江東六郡を統一しようと思っている。君も力を貸してくれないか?","これから俺は廬江を攻め、江東六郡を統一しようと思っている。君も力を貸してくれないか?","プレイヤー",},
        [2899] = {3201,"主君","もちろんです、何も恐れはしません！","もちろんよ、何も恐れはしませんわ！","孫策",},
        [2900] = {3201,"孫策","いいぞ！さすがは若き英雄、それでこそ豪傑だ。","いいぞ！さすがは若き英雄、それでこそ豪傑だ。","プレイヤー",},
        [2901] = {3202,"孫策","廬江太守の劉勲は、許昌へ逃げ、曹操のもとに身を寄せた。","廬江太守の劉勲は、許昌へ逃げ、曹操のもとに身を寄せた。","プレイヤー",},
        [2902] = {3202,"主君","黄祖にも逃げられた。孫策兄に仇討ちをさせてやれなかったのは残念でした！","黄祖にも逃げられた。孫策兄さんに仇討ちをさせてあげられなくて、残念でした！","孫策",},
        [2903] = {3202,"孫策","急がずともよい。江東を統一したら、遅かれ早かれ奴の首を打ち、父の墓前に供えてやるのだからな！","急がずともよい。江東を統一したら、遅かれ早かれ奴の首を打ち、父の墓前に供えてやるのだからな！","プレイヤー",},
        [2904] = {3202,"主君","あとは豫章を残すのみです。一気呵成に、すべて制圧しましょう！","あとは豫章を残すのみですわ。一気呵成に、すべて制圧しましょう！","孫策",},
        [2905] = {3203,"主君","孫策兄、今日はことのほかご機嫌ですね！","孫策兄さん、今日はことのほかご機嫌ですね！","孫策",},
        [2906] = {3203,"孫策","ははは、機嫌も良くなるというものだ！虞翻を説得して降伏させ、この豫章を一兵も失わず落としたのだからな。","ははは、機嫌も良くなるというものだ！虞翻を説得して降伏させ、この豫章を一兵も失わず落としたのだからな。","プレイヤー",},
        [2907] = {3203,"主君","孫策兄の配下は実に多士済々……ん、あそこにいる少年は誰です?","孫策兄さんの配下は本当に多士済々……ん、あそこにいる少年は誰かしら?","孫策",},
        [2908] = {3203,"主君","堂々たる姿をしていて、弁舌も素晴らしい。恐らく良家の出でしょうが……","堂々たる姿をしていて、弁舌も素晴らしいわ。恐らく良家の出でしょうけど……","孫策",},
        [2909] = {3203,"孫策","ほう、君がそんなに褒めるとは。どれ、誰だか見てみよう。","ほう、君がそんなに褒めるとは。どれ、誰だか見てみよう。","プレイヤー",},
        [2910] = {3204,"孫策","俺は孫策だ。そなたは何者だ?","俺は孫策だ。そなたは何者だ?","陸遜",},
        [2911] = {3204,"陸遜","はい、わたくしは陸遜、字を伯言と申します。呉郡の者です。","はい、わたくしは陸遜、字を伯言と申します。呉郡の者です。","孫策",},
        [2912] = {3204,"孫策","呉郡には陸、顧、朱、張の四大名家があると聞いている。そなたは陸家の出身か?","呉郡には陸、顧、朱、張の四大名家があると聞いている。そなたは陸家の出身か?","陸遜",},
        [2913] = {3204,"陸遜","そのとおりにございます。","そのとおりにございます。","孫策",},
        [2914] = {3204,"孫策","なるほど、謙虚で品のある読書人の風格だ。将来きっと大成するだろう。結婚はしているのか?","なるほど、謙虚で品のある読書人の風格だ。将来きっと大成するだろう。結婚はしているのか?","陸遜",},
        [2915] = {3204,"陸遜","恥ずかしながら。まだでございます。","恥ずかしながら。まだでございます。","孫策",},
        [2916] = {3204,"孫策","ははは、それなら俺の長女の婿に決めたぞ！娘が十五になったら娶りに来るがいい。","ははは、それなら俺の長女の婿に決めたぞ！娘が十五になったら娶りに来るがいい。","陸遜",},
        [2917] = {3205,"孫策","若き英雄よ、うちの新しい婿をどう思う?","若き英雄よ、うちの新しい婿をどう思う?","プレイヤー",},
        [2918] = {3205,"主君","名家の生まれらしく、慎み深い性格で姿も美しく、詩文の教養も高い。前途有望ですね。","名家の生まれらしく、慎み深い性格で姿も美しく、詩文の教養も高いわ。前途有望ですね。","孫策",},
        [2919] = {3205,"孫策","ははは、そう言ってくれるか！今日は本当に嬉しい日だ。さあさあ、酒の入ったところで、一緒に狩りへ行こう！","ははは、そう言ってくれるか！今日は本当に嬉しい日だ。さあさあ、酒の入ったところで、一緒に狩りへ行こう！","プレイヤー",},
        [2920] = {3205,"主君","孫策兄、遠出するなら、従者を多めに連れて行ったほうが良いと思います。","孫策兄さん、遠出するなら、従者を多めに連れて行ったほうが良いと思いますよ。","孫策",},
        [2921] = {3205,"孫策","心配いらん、俺は小覇王・孫策だぞ。今や六郡を平定し、江東はすべて俺の領土だ。手出しする者などいるはずがない！","心配いらん、俺は小覇王・孫策だぞ。今や六郡を平定し、江東はすべて俺の領土だ。手出しする者などいるはずがない！","プレイヤー",},
        [2922] = {3205,"孫策","君は名将令が欲しいのだろう。部下に命じて探し集めて、差し上げよう！","君は名将令が欲しいのだろう。部下に命じて探し集めて、差し上げよう！","プレイヤー",},
        [2923] = {3205,"主君","それは痛み入ります！孫策兄はやっぱり豪気だなあ。","わあ、ありがたいわ！孫策兄さんはやっぱり豪気ね。","孫策",},
        [2924] = {3301,"孫策","若き英雄よ、実はひとつ頼みがあるのだが……","若き英雄よ、実はひとつ頼みがあるのだが……","プレイヤー",},
        [2925] = {3301,"主君","それは、喬公の二人の娘、大喬、小喬のことですか?","それって、喬公の二人の娘、大喬、小喬のことですか?","孫策",},
        [2926] = {3301,"孫策","さすが、何でもお見通しだな。","さすが、何でもお見通しだな。","プレイヤー",},
        [2927] = {3301,"主君","うちの星彩は小喬と知り合いです。とっくに聞いていました。","うちの星彩は小喬と知り合いです。とっくに聞いていましたわ。","孫策",},
        [2928] = {3301,"孫策","そうだったか。実は周瑜と二人でいる時、偶然その姉妹に出会って、見初めてしまったのだ。","そうだったか。実は周瑜と二人でいる時、偶然その姉妹に出会って、見初めてしまったのだ。","プレイヤー",},
        [2929] = {3301,"孫策","天女と見紛うほどの美しさだった。","天女と見紛うほどの美しさだった。","プレイヤー",},
        [2930] = {3301,"孫策","だが、皖城を落とした、いわば仇敵の俺に娘を嫁がせることを、喬公は渋っている。","だが、皖城を落とした、いわば仇敵の俺に娘を嫁がせることを、喬公は渋っている。","プレイヤー",},
        [2931] = {3301,"孫策","そこで君の出番なのだ。天子から揚威将軍に封じられた君が媒酌をしてくれれば、喬公も断るまい。","そこで君の出番なのだ。天子から揚威将軍に封じられた君が媒酌をしてくれれば、喬公も断るまい。","プレイヤー",},
        [2932] = {3301,"主君","わかりました、やってみましょう。必ず祝いの酒を飲みましょうね！","わかりました、やってみましょう。必ず祝いの酒を飲みましょうね！","孫策",},
        [2933] = {3302,"主君","おい星彩、なぜお前が?","ちょっと星彩、なぜあなたが?","張星彩",},
        [2934] = {3302,"張星彩","喬公は、大喬・小喬姉妹との勝負に勝てなければ、この縁談は受けないって。","喬公は、大喬・小喬姉妹との勝負に勝てなければ、この縁談は受けないって。","プレイヤー",},
        [2935] = {3302,"主君","大丈夫だ、私一人で充分だよ。これまでもそうだっただろう?","大丈夫、私一人で充分よ。これまでもそうだったでしょう?","張星彩",},
        [2936] = {3302,"張星彩","そうね！じゃあ星彩は見学してるわ。見事に勝ってみせてね！","そうね！じゃあ星彩は見学してるわ。見事に勝ってみせてね！","プレイヤー",},
        [2937] = {3303,"関銀屏","孫尚香さん、それだけの武芸の腕をお持ちなんですもの、将来は大英雄に嫁がないといけませんわね！","孫尚香さん、それだけの武芸の腕をお持ちなんですもの、将来は大英雄に嫁がないといけませんわね！","孫尚香",},
        [2938] = {3303,"孫尚香","あなたもそんなくだらないことを言うの?嫁ぐなんてつまらない。あたしは戦場で敵を殺したいのよ！","あなたもそんなくだらないことを言うの?嫁ぐなんてつまらない。あたしは戦場で敵を殺したいのよ！","関銀屏",},
        [2939] = {3303,"関銀屏","あら、考えてもごらんなさい。英雄に嫁いでこそ、戦場へ行く機会があるのですわよ！","あら、考えてもごらんなさい。英雄に嫁いでこそ、戦場へ行く機会があるのですわよ！","孫尚香",},
        [2940] = {3303,"関銀屏","普通の男に嫁いだら、夫の世話や子供の教育をするだけですわ。","普通の男に嫁いだら、夫の世話や子供の教育をするだけですわ。","孫尚香",},
        [2941] = {3303,"孫尚香","ふーん、一理あるわね。母上に言いに行くわ。一生、英雄以外には嫁がないって！","ふーん、一理あるわね。母上に言いに行くわ。一生、英雄以外には嫁がないって！","関銀屏",},
        [2942] = {3304,"張星彩","大喬さんって、なんてきれいなの。まるで牡丹の花みたい！","大喬さんって、なんてきれいなの。まるで牡丹の花みたい！","大喬",},
        [2943] = {3304,"大喬","ふふっ、ありがとう、星彩さん。あなたもとっても可愛いわよ。","ふふっ、ありがとう、星彩さん。あなたもとっても可愛いわよ。","張星彩",},
        [2944] = {3304,"関銀屏","二人ともなにをおっしゃってますの?この関銀屏の美脚こそ、天下第一ですわ！","二人ともなにをおっしゃってますの?この関銀屏の美脚こそ、天下第一ですわ！","張星彩",},
        [2945] = {3304,"張星彩","……姉さま！","……姉さま！","関銀屏",},
        [2946] = {3304,"関銀屏","はいはい、褒めあいっこはここまでですわ。大喬さん、ご主君様からお尋ねですわよ。孫策さんに嫁ぐ気はありまして?","はいはい、褒めあいっこはここまでですわ。大喬さん、ご主君様からお尋ねですわよ。孫策さんに嫁ぐ気はありまして?","大喬",},
        [2947] = {3304,"大喬","は、はい……孫策さまのもとに嫁ぎたい……です……","は、はい……孫策さまのもとに嫁ぎたい……です……","張星彩",},
        [2948] = {3304,"関銀屏","おほほ、良かったですわ。デキる男と美貌の女性、お似合いの夫婦ですわね！","おほほ、良かったですわ。デキる男と美貌の女性、お似合いの夫婦ですわね！","大喬",},
        [2949] = {3305,"小喬","噂を聞いていた揚威将軍に、やっと会えたわ。小喬の無遠慮を許してね！","噂を聞いていた揚威将軍に、やっと会えたわ。小喬の無遠慮を許してね！","プレイヤー",},
        [2950] = {3305,"主君","すまない。どういう意味かな?","ごめん。どういう意味?","小喬",},
        [2951] = {3305,"小喬","実はあたし、周瑜さまとは前から知り合いなの。あたしのウサちゃんも、周瑜さまからの贈り物なんだー。","実はあたし、周瑜さまとは前から知り合いなの。あたしのウサちゃんも、周瑜さまからの贈り物なんだー。","プレイヤー",},
        [2952] = {3305,"小喬","それで、お姉ちゃんと孫策さまを引き合わせたのも、今回の勝負も、全部あたしの考えなの！","それで、お姉ちゃんと孫策さまを引き合わせたのも、今回の勝負も、全部あたしの考えなの！","プレイヤー",},
        [2953] = {3305,"関銀屏","あらあら、そんな大胆な策を用いていたなんて。小喬さんったら、意外とお転婆ですわね！","あらあら、そんな大胆な策を用いていたなんて。小喬さんったら、意外とお転婆ですわね！","小喬",},
        [2954] = {3305,"小喬","今回の勝負をお膳立てしたのは、噂の揚威将軍にお会いしたかったからよ！","今回の勝負をお膳立てしたのは、噂の揚威将軍にお会いしたかったからよ！","関銀屏",},
        [2955] = {3305,"小喬","あたしはずっと江東にいたけど、揚威将軍の名前は耳にしてたの。","あたしはずっと江東にいたけど、揚威将軍の名前は耳にしてたの。","プレイヤー",},
        [2956] = {3305,"小喬","今日はじめて会ったけど、噂以上に強くてカッコよくて、最高だったわ！","今日はじめて会ったけど、噂以上に強くてカッコよくて、最高だったわ！","プレイヤー",},
        [2957] = {3305,"小喬","周瑜さまに聞いたけど、名将令を集めてるんでしょ?ここに一つあるから、差し上げます！","周瑜さまに聞いたけど、名将令を集めてるんでしょ?ここに一つあるから、差し上げます！","プレイヤー",},
        [2958] = {3305,"主君","それは有難い！姉妹お二方ともお望みなら、早く婚礼をすると良い。私にも祝いの酒を飲ませてくれ！","それは有難いわ！姉妹お二方ともお望みなら、早く婚礼をすればいいじゃない。私にも祝いの酒を飲ませてよ！","小喬",},
        [2959] = {3401,"周瑜","今日は礼を言いに来たんだよ、揚威将軍。あなたがいなければ、この縁組もここまで順調に行かなかった。","今日は礼を言いに来たんだよ、揚威将軍。あなたがいなければ、この縁組もここまで順調に行かなかった。","プレイヤー",},
        [2960] = {3401,"主君","いやいや、古来より佳人は江東から出るという。大喬小喬は花にも月にもたとえられる絶世の美女だ。誠に羨ましい。","いえいえ、古来より佳人は江東から出るといいますわ。大喬小喬は花にも月にもたとえられる絶世の美女。女の私からしても、誠に羨ましいです。","周瑜",},
        [2961] = {3401,"周瑜","からかうのは良くないよ、揚威将軍……","からかうのは良くないよ、揚威将軍……","プレイヤー",},
        [2962] = {3401,"主君","さあ、周瑜殿に酒を！敬意を表して私が先に飲み干そう。乾杯！","さあ、周瑜殿に酒を！敬意を表して私が先に飲み干すわ。乾杯！","周瑜",},
        [2963] = {3401,"周瑜","将軍、くれぐれも飲みすぎには注意することだね。","将軍、くれぐれも飲みすぎには注意することだね。","プレイヤー",},
        [2964] = {3401,"主君","この程度で酔いはしないさ！おおっ、あれは太史慈将軍ではないか。一つお手合わせ願いたい！","この程度で酔いはしないわよ！おおっ、あれは太史慈将軍じゃない。一つお手合わせ願おうかしら！","周瑜",},
        [2965] = {3402,"関銀屏","太史慈将軍、ご主君様は酔って、将軍と武芸を競いたいと申しておりますわ。","太史慈将軍、ご主君様は酔って、将軍と武芸を競いたいと申しておりますわ。","太史慈",},
        [2966] = {3402,"太史慈","銀屏殿、ご安心を。ご主人様を傷つけないよう、寸止めにしますから！","銀屏殿、ご安心を。ご主人様を傷つけないよう、寸止めにしますから！","関銀屏",},
        [2967] = {3402,"関銀屏","それは誤解ですわ、将軍。ご主君様は酔うとますます強くなりますの。","それは誤解ですわ、将軍。ご主君様は酔うとますます強くなりますの。","太史慈",},
        [2968] = {3402,"関銀屏","将軍が怪我するんじゃないかと心配で、注意しに来たのですわ！","将軍が怪我するんじゃないかと心配で、注意しに来たのですわ！","太史慈",},
        [2969] = {3402,"太史慈","……ほほう！お心遣い、感謝しましょう！","……ほほう！お心遣い、感謝しましょう！","関銀屏",},
        [2970] = {3403,"張星彩","ご主君様、酔い覚ましの薬湯を持ってきたわ！これを飲めば、うんと気分が良くなるはずよ。","ご主君様、酔い覚ましの薬湯を持ってきたわ！これを飲めば、うんと気分が良くなるはずよ。","プレイヤー",},
        [2971] = {3403,"主君","ありがとう星彩、だいぶ良くなったよ。昨日はつい調子に乗って、飲みすぎてしまった……","ありがとう星彩、だいぶ良くなったわ。昨日はつい調子に乗って、飲みすぎちゃった……","張星彩",},
        [2972] = {3403,"張星彩","ご主君様、門の外に何人かの英雄が面会に来てるわ。もう長いこと待ってるわよ。","ご主君様、門の外に何人かの英雄が面会に来てるわ。もう長いこと待ってるわよ。","プレイヤー",},
        [2973] = {3403,"主君","そうか、誰が来てるんだ?","そう、誰が来てるの?","張星彩",},
        [2974] = {3403,"張星彩","氏名帖があるから、見てみて！","氏名帖があるから、見てみて！","プレイヤー",},
        [2975] = {3403,"主君","へえ、魯粛と呂蒙か。周瑜に知らせてくれ、一緒に会ってみよう。","わあ、魯粛と呂蒙だって！周瑜にも知らせて、一緒に会ってみよう。","張星彩",},
        [2976] = {3404,"魯粛","魯粛です。お初にお目にかかります。","魯粛です。お初にお目にかかります。","プレイヤー",},
        [2977] = {3404,"主君","魯粛殿、お名前はかねがね！周瑜殿から聞いていたが、二人は古い知り合いだそうですね！","魯粛殿、お名前はかねがね！周瑜殿から聞いていたけど、二人は古い知り合いだそうですね！","魯粛",},
        [2978] = {3404,"周瑜","ええ。私が居巣の県長をしていた時、魯粛殿が普段から好んで施しをされると聞いて、糧食の援助をお願いしたのです。","ええ。私が居巣の県長をしていた時、魯粛殿が普段から好んで施しをされると聞いて、糧食の援助をお願いしたのです。","魯粛",},
        [2979] = {3404,"周瑜","彼は二つ返事で、倉一つ分、千斛もの米をお与えくださいました。","彼は二つ返事で、倉一つ分、千斛もの米をお与えくださいました。","魯粛",},
        [2980] = {3404,"主君","なかなかに豪快ですね、魯粛殿。民の間でも施しを好む福の神だと言われていますが、本当なのですね！","なかなかに豪快ですわね、魯粛殿。民の間でも施しを好む福の神だと言われていますが、本当なのですね！","魯粛",},
        [2981] = {3404,"魯粛","ははは！将軍も今後困ったことがあったら来てください。できる限り力になりましょう。","ははは！将軍も今後困ったことがあったら来てください。できる限り力になりましょう。","プレイヤー",},
        [2982] = {3405,"呂蒙","呂蒙と申します。お初にお目にかかります。","呂蒙と申します。お初にお目にかかります。","プレイヤー",},
        [2983] = {3405,"主君","呂蒙殿は若いのに、ご立派ですね。やはり英雄は若い時から英雄ですね！","呂蒙殿は若いのに、堂々としてますね。やはり英雄は若い時から英雄なのですね！","呂蒙",},
        [2984] = {3405,"呂蒙","お褒めに預かり光栄です！","お褒めに預かり光栄です！","プレイヤー",},
        [2985] = {3405,"主君","ところで、なぜ錨と竹簡を持っているんです?","ところで、なぜ錨と竹簡を持っているんです?","呂蒙",},
        [2986] = {3405,"呂蒙","錨は私が自ら作り出した武器です。竹簡は兵法書です。熟読し、学問で国に報いるようにと魯粛殿から勧められました。","錨は私が自ら作り出した武器です。竹簡は兵法書です。熟読し、学問で国に報いるようにと魯粛殿から勧められました。","プレイヤー",},
        [2987] = {3405,"主君","そうかそうか！呂蒙殿の将来は限りなく有望だな。江東はやはり多士済々だ！","そうだったの！呂蒙殿の将来は限りなく有望ね。江東はやはり多士済々じゃない！","呂蒙",},
        [2988] = {3406,"周瑜","揚威将軍、これを見てくれ。","揚威将軍、これを見てくれ。","プレイヤー",},
        [2989] = {3406,"主君","これは、曹操からの書簡じゃないか！","これは、曹操からの書簡じゃないの！","周瑜",},
        [2990] = {3406,"周瑜","ああ。曹操は僕を説得し、自分の陣営に入れようとしているのだ。","ああ。曹操は僕を説得し、自分の陣営に入れようとしているのだ。","プレイヤー",},
        [2991] = {3406,"主君","無理もない。周瑜殿は文武に造詣が深く、当世の英才ですからね。曹操ならば取り込もうとするでしょう。","無理もないわ。周瑜殿は文武に造詣が深く、当世の英才ですもの。曹操ならば取り込もうとするでしょうね。","周瑜",},
        [2992] = {3406,"周瑜","そうは言っても、私と孫策殿の仲を裂けるものではありませんよ。","そうは言っても、私と孫策殿の仲を裂けるものではありませんよ。","プレイヤー",},
        [2993] = {3406,"周瑜","今、曹操は天子を擁して諸侯に号令し、野心を隠しません。いずれ一戦を交えることになるでしょうね。","今、曹操は天子を擁して諸侯に号令し、野心を隠しません。いずれ一戦を交えることになるでしょうね。","プレイヤー",},
        [2994] = {3406,"主君","安心してください、まだ時期尚早です。冀州の袁紹、淮南の袁術、更に呂布。","安心してください、まだ時期尚早ですわ。冀州の袁紹、淮南の袁術、更に呂布。","周瑜",},
        [2995] = {3406,"主君","曹操は三面を敵に囲まれ、対応に苦慮している。ましてや江東との間には、長江があります！","曹操は三面を敵に囲まれ、対応に苦慮しています。ましてや江東との間には、長江があります！","周瑜",},
        [2996] = {3406,"周瑜","将軍は実に視野が広い。天下を見渡しておられますね。今回、我々は大いに助かりました。","将軍は実に視野が広い。天下を見渡しておられますね。今回、我々は大いに助かりました。","プレイヤー",},
        [2997] = {3406,"周瑜","せめてもの礼に、この名将令を差し上げたいと思います。","せめてもの礼に、この名将令を差し上げたいと思います。","プレイヤー",},
        [2998] = {3501,"夏侯淵","丞相、この乱世を収めるには、まずは張繍を降伏させ、それから河北を平定する必要がございます。","丞相、この乱世を収めるには、まずは張繍を降伏させ、それから河北を平定する必要がございます。","曹操",},
        [2999] = {3501,"曹操","張繍とは、何者だ?","張繍とは、何者だ?","夏侯淵",},
        [3000] = {3501,"主君","私がお答えしましょう。張繍は驃騎将軍、張済の甥で、名将令を持ち宛城を根城にしています。","私がお答えしますわ。張繍は驃騎将軍、張済の甥で、名将令を持ち宛城を根城にしています。","曹操",},
        [3001] = {3501,"主君","人呼んで「北の槍術王」。虎の頭がついた金の槍を持ち、その「百鳥朝凰槍」は天下に轟いています。","人呼んで「北の槍術王」。虎の頭がついた金の槍を持ち、その「百鳥朝凰槍」は天下に轟いていますわ。","曹操",},
        [3002] = {3501,"主君","この者は当世の英才です。配下に収めることができれば、きっと役に立つでしょう。","この者は当世の英才ですわ。配下に収めることができれば、きっと役に立つでしょうね。","曹操",},
        [3003] = {3501,"曹操","うむ、もっともだな。お前の言うとおり、即刻軍を南下させよう。","うむ、もっともだな。お前の言うとおり、即刻軍を南下させよう。","プレイヤー",},
        [3004] = {3502,"曹操","于禁よ、三軍は集結したか?","于禁よ、三軍は集結したか?","于禁",},
        [3005] = {3502,"于禁","はい、丞相。既に集結し、いつでも出立可能です。","はい、丞相。既に集結し、いつでも出立可能です。","曹操",},
        [3006] = {3502,"曹操","よし、では伝令だ。即刻、出立する。一刻の猶予もならん。","よし、では伝令だ。即刻、出立する。一刻の猶予もならん。","于禁",},
        [3007] = {3503,"曹操","郭嘉、この一帯の麦は既に熟しているのに、なぜ皆は刈らぬのだ?","郭嘉、この一帯の麦は既に熟しているのに、なぜ皆は刈らぬのだ?","郭嘉",},
        [3008] = {3503,"郭嘉","おそらく我々が南征しここまで兵を出しているので、民衆が恐れているのでしょう。","おそらく我々が南征しここまで兵を出しているので、民衆が恐れているのでしょう。","曹操",},
        [3009] = {3503,"曹操","そうか。すぐに村の長老と、各所で辺境を守る官吏に伝えよ。","そうか。すぐに村の長老と、各所で辺境を守る官吏に伝えよ。","郭嘉",},
        [3010] = {3503,"曹操","わしは天子の詔を奉じて、逆賊を討ち、民の害を除きに来たのだ。","わしは天子の詔を奉じて、逆賊を討ち、民の害を除きに来たのだ。","郭嘉",},
        [3011] = {3503,"曹操","今は麦の熟す時だが、やむを得ず出兵した。大小の将校に関わらず、麦を踏みつけた者は即斬首としている。","今は麦の熟す時だが、やむを得ず出兵した。大小の将校に関わらず、麦を踏みつけた者は即斬首としている。","郭嘉",},
        [3012] = {3503,"曹操","軍法は甚だ厳しい。民は恐れずとも良い……とな。","軍法は甚だ厳しい。民は恐れずとも良い……とな。","郭嘉",},
        [3013] = {3503,"郭嘉","賢明な行いでございます、丞相。","賢明な行いでございます、丞相。","曹操",},
        [3014] = {3504,"曹操","畑の中にいた鳩にわしの馬が驚き、麦畑に入ってしまった。麦を踏みつけたからには、わしも軍法の処罰を受けねばならぬ。","畑の中にいた鳩にわしの馬が驚き、麦畑に入ってしまった。麦を踏みつけたからには、わしも軍法の処罰を受けねばならぬ。","プレイヤー",},
        [3015] = {3504,"主君","どうして丞相を罪に問えるのです?","どうして丞相を罪に問えるというのです?","曹操",},
        [3016] = {3504,"曹操","わしは自ら法を定めながら、それを犯したのだ。守らなければ人を服従させることなどできんではないか。","わしは自ら法を定めながら、それを犯したのだ。守らなければ人を服従させることなどできんではないか。","プレイヤー",},
        [3017] = {3504,"郭嘉","いにしえの「春秋」の言葉にも「法は尊きに加えず」と言います。丞相は大軍を統べるお方。自刃など滅相もないことです。","いにしえの「春秋」の言葉にも「法は尊きに加えず」と言います。丞相は大軍を統べるお方。自刃など滅相もないことです。","曹操",},
        [3018] = {3504,"曹操","そうか……「春秋」にそんな言葉があるなら、死ぬのはやめておくとしよう。","そうか……「春秋」にそんな言葉があるなら、死ぬのはやめておくとしよう。","郭嘉",},
        [3019] = {3504,"曹操","三軍に伝えよ。丞相は麦を踏んだ。本来なら斬首のところだが、髪を切って代わりとする。","三軍に伝えよ。丞相は麦を踏んだ。本来なら斬首のところだが、髪を切って代わりとする。","郭嘉",},
        [3020] = {3601,"曹操","わしは呂布を攻めたい。袁紹は怖くないが、劉表と張繍に背後を突かれまいかが気掛かりだ。","わしは呂布を攻めたい。袁紹は怖くないが、劉表と張繍に背後を突かれまいかが気掛かりだ。","荀攸",},
        [3021] = {3601,"荀攸","あの二人は敗れたばかりで軽々しくは動けぬでしょう。勇猛な呂布が袁術と結託すれば、淮河と泗水を取るのは難しくなります。","あの二人は敗れたばかりで軽々しくは動けぬでしょう。勇猛な呂布が袁術と結託すれば、淮河と泗水を取るのは難しくなります。","曹操",},
        [3022] = {3601,"郭嘉","左様にございます。反逆を起こしたばかりで、人心がまだ従っていない隙に、素早く討つのがよろしいかと存じます。","左様にございます。反逆を起こしたばかりで、人心がまだ従っていない隙に、素早く討つのがよろしいかと存じます。","曹操",},
        [3023] = {3601,"曹操","確かにその通りだ。夏侯惇はおるか！","確かにその通りだ。夏侯惇はおるか！","郭嘉",},
        [3024] = {3601,"夏侯惇","ここにぃ！","ここにぃ！","曹操",},
        [3025] = {3601,"曹操","すぐに夏侯淵、呂虔、李典と5万の兵を率いて先陣を切れ。わしも続けて出発する。簡雍も随行するぞ。","すぐに夏侯淵、呂虔、李典と5万の兵を率いて先陣を切れ。わしも続けて出発する。簡雍も随行するぞ。","夏侯惇",},
        [3026] = {3602,"高順","呂布殿。曹操が劉備救援の兵を出し、こちらを攻撃しようとしています。","呂布殿。曹操が劉備救援の兵を出し、こちらを攻撃しようとしています。","呂布",},
        [3027] = {3602,"高順","既に夏侯惇に命じ、夏侯淵・呂虔・李典とともに五万の兵を率いさせ、先発させています。","既に夏侯惇に命じ、夏侯淵・呂虔・李典とともに五万の兵を率いさせ、先発させています。","呂布",},
        [3028] = {3602,"呂布","恐るるに足らん！侯成、郝萌、曹性に二百騎あまりを率いらせ、お前に呼応するよう命じておる。","恐るるに足らん！侯成、郝萌、曹性に二百騎あまりを率いらせ、お前に呼応するよう命じておる。","高順",},
        [3029] = {3602,"呂布","沛城の外、三十里で曹操軍を迎え撃て。俺も後から合流する！","沛城の外、三十里で曹操軍を迎え撃て。俺も後から合流する！","高順",},
        [3030] = {3602,"高順","ははっ、すぐに参ります。","ははっ、すぐに参ります。","呂布",},
        [3031] = {3603,"夏侯惇","前方の敵将は何者だぁっ！?","前方の敵将は何者だぁっ！?","高順",},
        [3032] = {3603,"高順","呂布配下の中郎将、高順だ。","呂布配下の中郎将、高順だ。","夏侯惇",},
        [3033] = {3603,"夏侯惇","この俺に戦いを挑む気があるかぁっ！","この俺に戦いを挑む気があるかぁっ！","高順",},
        [3034] = {3603,"高順","見くびっているな。かかって来い！","見くびっているな。かかって来い！","夏侯惇",},
        [3035] = {3604,"夏侯惇","高順はどこへ逃げたぁっ！?","高順はどこへ逃げたぁっ！?","",},
        [3036] = {3604,"主君","逃げた敵は深追いしてはいけない。それより陣中の小人が密かに放つ矢に気を付けて。","逃げた敵は深追いしてはいけないわ。それより敵がひそかに矢を放ってくるかもしれないから気を付けて。","夏侯惇",},
        [3037] = {3604,"夏侯惇","ぐおっ！?矢が、俺の左目にぃっ！！どいつが放ったぁっ！","ぐおっ！?矢が、俺の左目にぃっ！！どいつが放ったぁっ！","",},
        [3038] = {3604,"夏侯惇","父母からもらった体、棄てるわけにはいかぬぅっ！！","父母からもらった体、棄てるわけにはいかぬぅっ！！","",},
        [3039] = {3604,"夏侯惇","(すぐに矢を抜くと、眼球もろとも抜けてしまったため、眼球を口に入れ、食べてしまう)","(すぐに矢を抜くと、眼球もろとも抜けてしまったため、眼球を口に入れ、食べてしまう)","",},
        [3040] = {3604,"夏侯惇","曹性とかいう雑魚が放った矢かぁっ！おのれ、首を取ってやるっ！！","曹性とかいう雑魚が放った矢かぁっ！おのれ、首を取ってやるっ！！","",},
        [3041] = {3605,"高順","味方の大軍が到着した。夏侯惇、貴様はもう囲まれている。大人しく降伏しろ！","味方の大軍が到着した。夏侯惇、貴様はもう囲まれている。大人しく降伏しろ！","夏侯惇",},
        [3042] = {3605,"主君","夏侯惇将軍、助けに来ましたよ。私と一緒に敵陣を抜けましょう！","夏侯惇将軍、助けに来たわよ。私と一緒に敵陣を抜けましょう！","夏侯惇",},
        [3043] = {3605,"夏侯惇","おおっ！自らの命も顧みず救い出してくれるとは、有難いぃっ！","おおっ！自らの命も顧みず救い出してくれるとは、有難いぃっ！","プレイヤー",},
        [3044] = {3701,"主君","順調な戦いでした。徐州と小沛の二城を取りましたよ！","順調な戦いでしたわ。徐州と小沛の二城を取りましたよ！","曹操",},
        [3045] = {3701,"主君","呂布は下邳に退却し、これを死守しています。","呂布は下邳に退却し、これを死守していますわ。","曹操",},
        [3046] = {3701,"曹操","三軍に褒賞を与えよう。各部休んで軍を整えた後、下邳を奪うのだ！","三軍に褒賞を与えよう。各部休んで軍を整えた後、下邳を奪うのだ！","プレイヤー",},
        [3047] = {3702,"呂布","貂蝉、早く、なみなみと注いでくれ。","貂蝉、早く、なみなみと注いでくれ。","貂蝉",},
        [3048] = {3702,"貂蝉","呂布さま、少し飲み過ぎですよ。","呂布さま、少し飲み過ぎですよ。","呂布",},
        [3049] = {3702,"陳宮","今は戦いのもっとも大事な時ですぞ。敵を軽んじ酒色に溺れてはなりません。","今は戦いのもっとも大事な時ですぞ。敵を軽んじ酒色に溺れてはなりません。","呂布",},
        [3050] = {3702,"呂布","慌てるな、陳宮。曹操は我が下邳を二ヶ月も攻めているが何の成果もない。きっと撤兵するだろう。","慌てるな、陳宮。曹操は我が下邳を二ヶ月も攻めているが何の成果もない。きっと撤兵するだろう。","陳宮",},
        [3051] = {3702,"呂布","さあ、候成も共に飲むぞ。","さあ、候成も共に飲むぞ。","陳宮",},
        [3052] = {3702,"侯成","はっ。","はっ。","呂布",},
        [3053] = {3703,"曹操","下邳を攻め始めて、もう2か月だ。兵も疲れておる。","下邳を攻め始めて、もう2か月だ。兵も疲れておる。","荀彧",},
        [3054] = {3703,"曹操","いったん呂布は捨て置き、都へ帰ろうと思う。しばらく休戦だ。どう思う?","いったん呂布は捨て置き、都へ帰ろうと思う。しばらく休戦だ。どう思う?","荀彧",},
        [3055] = {3703,"荀彧","下邳を落とす機会はまだあります。呂布は酒色に溺れて戦う気を失くしており、陳宮は智謀あれど、聞き入れません。","下邳を落とす機会はまだあります。呂布は酒色に溺れて戦う気を失くしており、陳宮は智謀あれど、聞き入れません。","曹操",},
        [3056] = {3703,"主君","私に一計があります。下邳城をすぐさま落として見せましょう。","私に一計があります。下邳城をすぐさま落として見せますわ。","曹操",},
        [3057] = {3703,"郭嘉","まさか沂河、泗河の水を決壊させるおつもりですか?","まさか沂河、泗河の水を決壊させるおつもりですか?","プレイヤー",},
        [3058] = {3703,"主君","そのまさかです！","そのまさかよ！","郭嘉",},
        [3059] = {3703,"曹操","面白い策だ。各軍の兵士に命じて両河の水を決壊させよ。","面白い策だ。各軍の兵士に命じて両河の水を決壊させよ。","プレイヤー",},
        [3060] = {3704,"陳宮","報告します！曹操が沂水と泗水の堤防を切りました。下邳を水没させるつもりです！","報告します！曹操が沂水と泗水の堤防を切りました。下邳を水没させるつもりです！","呂布",},
        [3061] = {3704,"陳宮","東門には水が来ていませんが、その他の各門はすべて水没しました。","東門には水が来ていませんが、その他の各門はすべて水没しました。","呂布",},
        [3062] = {3704,"呂布","何を恐れることがあろう。俺には赤兎馬がある。水を渡るのも平地の如しだ！","何を恐れることがあろう。俺には赤兎馬がある。水を渡るのも平地の如しだ！","陳宮",},
        [3063] = {3704,"呂布","もうしばらく、女たちと美酒を堪能してから戦っても遅くはない。はははは。","もうしばらく、女たちと美酒を堪能してから戦っても遅くはない。はははは。","陳宮",},
        [3064] = {3704,"貂蝉","やめてください、皆の前ですよ。","やめてください、皆の前ですよ。","呂布",},
        [3065] = {3801,"主君","曹操殿、呂布の武将、候成が赤兎馬を盗んで献上してきましたよ。","曹操兄さん、呂布の武将、候成が赤兎馬を盗んで献上してきましたわ。","曹操",},
        [3066] = {3801,"主君","その者が言うには、下邳を守っている武将の宋憲、魏続が白旗を合図に、門を開けるつもりだということです。","その者が言うには、下邳を守っている武将の宋憲、魏続が白旗を合図に、門を開けるつもりだということです。","曹操",},
        [3067] = {3801,"曹操","そうか。すぐ兵に命じ、書簡数十枚を弓矢に付けて城内に打ち込め。","そうか。すぐ兵に命じ、書簡数十枚を弓矢に付けて城内に打ち込め。","プレイヤー",},
        [3068] = {3801,"曹操","書簡にはこう書くのだ。「将軍曹操、帝の詔を奉じ、呂布を征伐す」","書簡にはこう書くのだ。「将軍曹操、帝の詔を奉じ、呂布を征伐す」","プレイヤー",},
        [3069] = {3801,"曹操","我が軍に逆らう者あらば、城が陥落した日に一族を誅殺する！","我が軍に逆らう者あらば、城が陥落した日に一族を誅殺する！","プレイヤー",},
        [3070] = {3801,"曹操","また、呂布を捕えて献じた者、またはその首級を献じた者に、官位と褒賞を与える。将校から庶民までだ！","また、呂布を捕えて献じた者、またはその首級を献じた者に、官位と褒賞を与える。将校から庶民までだ！","プレイヤー",},
        [3071] = {3802,"呂布","なんの騒ぎだ?この地を揺るがすような叫び声は?","なんの騒ぎだ?この地を揺るがすような叫び声は?","",},
        [3072] = {3802,"陳宮","昨夜、侯成が赤兎馬を盗み、曹操に投降したのです。奴は文書を弓につけて城内に打ちこんできました。","昨夜、侯成が赤兎馬を盗み、曹操に投降したのです。奴は文書を弓につけて城内に打ちこんできました。","呂布",},
        [3073] = {3802,"陳宮","そして夜明けと共に、城を攻めてきたのです！","そして夜明けと共に、城を攻めてきたのです！","呂布",},
        [3074] = {3802,"呂布","フン、来たか。俺の戟を持て。皆殺しにしてくれるわ！","フン、来たか。俺の戟を持て。皆殺しにしてくれるわ！","陳宮",},
        [3075] = {3803,"曹操","やはり、呂布の武功は軍を抜いているか……誰かあやつを捕えてくる者はおらぬか?","やはり、呂布の武功は軍を抜いているか……誰かあやつを捕えてくる者はおらぬか?","",},
        [3076] = {3803,"主君","私が行こう。呂布を生け捕りにして参ります！","私が行きます。呂布を生け捕りにして参りますわ！","曹操",},
        [3077] = {3803,"曹操","よし、任せた。白門楼で良い知らせを待っておるぞ。","よし、任せた。白門楼で良い知らせを待っておるぞ。","プレイヤー",},
        [3078] = {3803,"曹操","それから貂蝉も忘れるな。わしのために捕えてこい。","それから貂蝉も忘れるな。わしのために捕えてこい。","プレイヤー",},
        [3079] = {3804,"主君","丞相、呂布と張遼、陳宮たちをみな生け捕りにして来ました！","丞相、呂布と張遼、陳宮たちをみな生け捕りにして来ましたよ！","曹操",},
        [3080] = {3804,"曹操","誠に見事な武芸、世に稀なる英才だ！","誠に見事な武芸、世に稀なる英才だ！","プレイヤー",},
        [3081] = {3804,"曹操","伝令だ。呂布を縛り首にし、首をさらせ！","伝令だ。呂布を縛り首にし、首をさらせ！","プレイヤー",},
        [3082] = {3804,"曹操","陳宮は生死に臨んでも二心なし、男として大したものだ。だが仕える者を間違え、才を無駄にしたな。厚く葬ってやれ。","陳宮は生死に臨んでも二心なし、男として大したものだ。だが仕える者を間違え、才を無駄にしたな。厚く葬ってやれ。","プレイヤー",},
        [3083] = {3804,"曹操","皆の者、張遼をどのように処置すべきか?","皆の者、張遼をどのように処置すべきか?","プレイヤー",},
        [3084] = {3804,"主君","張遼は忠義の士、真心の人。残して用いるべきです。","張遼は忠義の士、真心の人です。残して用いるべきです。","曹操",},
        [3085] = {3804,"曹操","よい。わしも、張遼の忠義は知っておる。からかっただけだ。","よい。わしも、張遼の忠義は知っておる。からかっただけだ。","プレイヤー",},
        [3086] = {3804,"曹操","伝令だ。張遼を中郎将とし、関内公の爵位を与える。臧覇を帰順させよ。","伝令だ。張遼を中郎将とし、関内公の爵位を与える。臧覇を帰順させよ。","プレイヤー",},
        [3087] = {3901,"劉備","私の兄弟子にあたる公孫瓚が、袁紹によって幽州で包囲されているらしい。どうしたら良いだろうか。","私の兄弟子にあたる公孫瓚が、袁紹によって幽州で包囲されているらしい。どうしたら良いだろうか。","",},
        [3088] = {3901,"主君","慌てないでください、兄者。私が兵を率いて救援に行きましょう。","慌てないでください、兄さん。私が兵を率いて救援に行きますわ。","劉備",},
        [3089] = {3901,"劉備","おお、そうか。すまぬが頼む。","おお、そうか。すまぬが頼む。","プレイヤー",},
        [3090] = {3902,"袁紹","ははははっ！白馬将軍と呼ばれたお前も、このザマとはな！","ははははっ！白馬将軍と呼ばれたお前も、このザマとはな！","公孫瓚",},
        [3091] = {3902,"公孫瓚","喜ぶのは早いぞ。援軍が来れば、内と外から挟み撃ちだ。必ずお前の首を取ってやる。","喜ぶのは早いぞ。援軍が来れば、内と外から挟み撃ちだ。必ずお前の首を取ってやる。","袁紹",},
        [3092] = {3902,"袁紹","そんなもの、どこから来るのだ。援軍を頼む書簡なら、わしがとっくに始末したぞ。","そんなもの、どこから来るのだ。援軍を頼む書簡なら、わしがとっくに始末したぞ。","公孫瓚",},
        [3093] = {3902,"袁紹","おとなしく捕まるのだな！はははははっ！！","おとなしく捕まるのだな！はははははっ！！","公孫瓚",},
        [3094] = {3902,"公孫瓚","だとしても、こちらは既に城と堀を築いておる。高さ十丈の易京楼を建て、三十万石の兵糧も蓄えた。","だとしても、こちらは既に城と堀を築いておる。高さ十丈の易京楼を建て、三十万石の兵糧も蓄えた。","袁紹",},
        [3095] = {3902,"公孫瓚","お前といえども攻め落とせまい！","お前といえども攻め落とせまい！","袁紹",},
        [3096] = {3903,"袁紹","おい、そこの武将！何をしに来た?","おい、そこの武将！何をしに来た?","プレイヤー",},
        [3097] = {3903,"主君","知れたことだ。白馬将軍、公孫瓚を救いに来た！","知れたこと。白馬将軍、公孫瓚を救いに来たのよ！","袁紹",},
        [3098] = {3903,"袁紹","なにを小癪な！将兵どもよ、そやつを止めろ！","なにを小癪な！将兵どもよ、そやつを止めろ！","プレイヤー",},
        [3099] = {3903,"主君","止められるものなら止めてみるがいい！(敵陣に突っ込み、公孫瓚に会う。)","止められるものなら止めてみなさいよ！(敵陣に突っ込み、公孫瓚に会う。)","袁紹",},
        [3100] = {3904,"主君","公孫瓚殿、袁紹軍は地面を三尺も掘り、今にもこの易京楼を攻め落とす勢いです。","公孫瓚殿、袁紹軍は地面を三尺も掘り、今にもこの易京楼を攻め落とす勢いですわ。","公孫瓚",},
        [3101] = {3904,"主君","時間がありません。私と共に突撃し、ここを出ましょう。","時間がありません。私と共に突撃し、ここを出ましょう。","公孫瓚",},
        [3102] = {3904,"公孫瓚","いや、私を救う必要はない…","いや、私を救う必要はない…","プレイヤー",},
        [3103] = {3904,"主君","なぜ、そのようなことを?","なぜ、そのようなことを?","公孫瓚",},
        [3104] = {3904,"公孫瓚","我が軍は大半が失われ、大勢は決した……自分でも天命は分かっておる。","我が軍は大半が失われ、大勢は決した……自分でも天命は分かっておる。","プレイヤー",},
        [3105] = {3904,"公孫瓚","この名将令は差し上げよう。早くこの場を去られよ。乱世の平定はそなたに頼んだぞ。","この名将令は差し上げよう。早くこの場を去られよ。乱世の平定はそなたに頼んだぞ。","プレイヤー",},
        [3106] = {3904,"主君","くっ……！公孫瓚殿、御身お大事に！","くっ……！公孫瓚殿、御身お大事に！","公孫瓚",},
        [3107] = {4001,"献帝","卿の父祖は、どのようなお方であるか?","卿の父祖は、どのようなお方であるか?","劉備",},
        [3108] = {4001,"劉備","臣は中山靖王の子孫、孝景皇帝閣下の玄孫、劉雄の孫、劉弘の子でございます。","臣は中山靖王の子孫、孝景皇帝閣下の玄孫、劉雄の孫、劉弘の子でございます。","献帝",},
        [3109] = {4001,"献帝","劉雄の孫、劉弘の子…、すると卿は朕の叔父ではないか。","劉雄の孫、劉弘の子…、すると卿は朕の叔父ではないか。","劉備",},
        [3110] = {4001,"献帝","勅令を下す。劉備を左将軍、宜城亭侯とする。","勅令を下す。劉備を左将軍、宜城亭侯とする。","劉備",},
        [3111] = {4001,"劉備","皇帝陛下の御恩に感謝いたします。","皇帝陛下の御恩に感謝いたします。","献帝",},
        [3112] = {4002,"荀彧","天子が劉備を叔父と認めたこと、曹操殿に益なしかと存じます。","天子が劉備を叔父と認めたこと、曹操殿に益なしかと存じます。","曹操",},
        [3113] = {4002,"曹操","問題はない。皇叔とされたとて、わしが天子の詔として令を発すれば、奴も服さぬわけにはいくまい。","問題はない。皇叔とされたとて、わしが天子の詔として令を発すれば、奴も服さぬわけにはいくまい。","荀彧",},
        [3114] = {4002,"曹操","ましてや、奴は許都にとどめている。名は天子に近いといえど、実際はわしの手の内だ。何を恐れることがある?","ましてや、奴は許都にとどめている。名は天子に近いといえど、実際はわしの手の内だ。何を恐れることがある?","荀彧",},
        [3115] = {4002,"曹操","わしの懸念は、太尉の楊彪が袁術の親戚ということだ。もし袁紹、袁術と内応したら、その害は深いぞ。すぐに除かねば。","わしの懸念は、太尉の楊彪が袁術の親戚ということだ。もし袁紹、袁術と内応したら、その害は深いぞ。すぐに除かねば。","荀彧",},
        [3116] = {4002,"程昱","今や、我が君の名声は日ごとに増しております。なぜこの機に、覇を称えようとなさらぬのです?","今や、我が君の名声は日ごとに増しております。なぜこの機に、覇を称えようとなさらぬのです?","曹操",},
        [3117] = {4002,"曹操","朝廷には重臣が数多く、うかつな真似はできぬ。ここは天子に狩りをして頂き、動静を見極めようではないか。","朝廷には重臣が数多く、うかつな真似はできぬ。ここは天子に狩りをして頂き、動静を見極めようではないか。","程昱",},
        [3118] = {4003,"曹操","既に最高の馬や鷹、犬を選び、弓矢も整っております。兵も城外に集めました。","既に最高の馬や鷹、犬を選び、弓矢も整っております。兵も城外に集めました。","献帝",},
        [3119] = {4003,"曹操","どうか天子も、狩りにお出かけ下さいますよう。","どうか天子も、狩りにお出かけ下さいますよう。","献帝",},
        [3120] = {4003,"献帝","狩りだと?朕は無益な殺生は好まぬ……","狩りだと?朕は無益な殺生は好まぬ……","曹操",},
        [3121] = {4003,"曹操","古代の帝王は春に採集し、夏に苗を植え、秋に猟をし、冬に狩りをしました。四季おりおりに城外へ出て、武を天下に示したのです。","古代の帝王は春に採集し、夏に苗を植え、秋に猟をし、冬に狩りをしました。四季おりおりに城外へ出て、武を天下に示したのです。","献帝",},
        [3122] = {4003,"曹操","今は天下が乱れている時。狩りによって武を示されるべきです。","今は天下が乱れている時。狩りによって武を示されるべきです。","献帝",},
        [3123] = {4003,"献帝","うむ……では卿の言うとおりにしよう。(皇帝は従わなざるを得ない)","うむ……では卿の言うとおりにしよう。(皇帝は従わなざるを得ない)","曹操",},
        [3124] = {4003,"献帝","だが、朕は劉備と関羽、それに張飛も呼んで、一緒に行きたい。","だが、朕は劉備と関羽、それに張飛も呼んで、一緒に行きたい。","曹操",},
        [3125] = {4004,"献帝","朕は劉備の狩りが見たいぞ。","朕は劉備の狩りが見たいぞ。","劉備",},
        [3126] = {4004,"劉備","はは。この劉備、御意に従います(すぐに1本の矢を放ち、野ウサギを射る)","はは。この劉備、御意に従います(すぐに1本の矢を放ち、野ウサギを射る)","献帝",},
        [3127] = {4004,"献帝","曹操も、やってみてはどうか?","曹操も、やってみてはどうか?","劉備",},
        [3128] = {4004,"曹操","御意。(そう言うと、天子の弓矢を借りて鹿を討ち、群臣、諸将が誤って万歳を叫ぶ。","御意。(そう言うと、天子の弓矢を借りて鹿を討ち、群臣、諸将が誤って万歳を叫ぶ。","献帝",},
        [3129] = {4004,"曹操","曹操は馬を後ろ足で立ち上がらせて自分だと示し、天子が万歳を受けるのを遮る)。","曹操は馬を後ろ足で立ち上がらせて自分だと示し、天子が万歳を受けるのを遮る)。","献帝",},
        [3130] = {4004,"関羽","曹操め、天子をないがしろにするとは。今のうちに除かねば、後の災いとなるでしょう(劉備に耳打ちする)","曹操め、天子をないがしろにするとは。今のうちに除かねば、後の災いとなるでしょう(劉備に耳打ちする)","劉備",},
        [3131] = {4004,"劉備","今は抑えよ、関羽。軽々しく口にしてはならん。","今は抑えよ、関羽。軽々しく口にしてはならん。","関羽",},
        [3132] = {4101,"献帝","そなたに下賜した帯に、朕の血書が挟んである。","そなたに下賜した帯に、朕の血書が挟んである。","董承",},
        [3133] = {4101,"献帝","曹操は朕を擁して許県に遷都し、諸侯に号令している。","曹操は朕を擁して許県に遷都し、諸侯に号令している。","董承",},
        [3134] = {4101,"献帝","車騎将軍、董承よ。朕を助け、曹操を亡ぼせ。","車騎将軍、董承よ。朕を助け、曹操を亡ぼせ。","董承",},
        [3135] = {4101,"董承","密詔……はっ、承りました。","密詔……はっ、承りました。","献帝",},
        [3136] = {4102,"董承","帝は血で密詔をしたため、曹操を除けと命じられた。私を助けてくれぬか?","帝は血で密詔をしたため、曹操を除けと命じられた。私を助けてくれぬか?","王子服",},
        [3137] = {4102,"王子服","それは…","それは…","董承",},
        [3138] = {4102,"董承","郭汜はかつて数百の兵力で李傕の数万を破った。呂不韋は子楚を得たのち、富貴に至った。","郭汜はかつて数百の兵力で李傕の数万を破った。呂不韋は子楚を得たのち、富貴に至った。","王子服",},
        [3139] = {4102,"董承","事を成せば、わしもそなたも大いに出世できるとは思わんか。","事を成せば、わしもそなたも大いに出世できるとは思わんか。","王子服",},
        [3140] = {4102,"王子服","しかし、私ではこの重責に堪えぬのではないかと。手勢の数も足りんし。","しかし、私ではこの重責に堪えぬのではないかと。手勢の数も足りんし。","董承",},
        [3141] = {4102,"董承","曹操を殺せば、奴の強大な兵力が手に入るのだぞ。それでも足りぬと?","曹操を殺せば、奴の強大な兵力が手に入るのだぞ。それでも足りぬと?","王子服",},
        [3142] = {4102,"王子服","都には共に謀をなす者がまだいるのか?","都には共に謀をなす者がまだいるのか?","董承",},
        [3143] = {4102,"董承","いるとも。長水校尉の種輯、議郎の呉碩もわしの腹心だ。","いるとも。長水校尉の種輯、議郎の呉碩もわしの腹心だ。","王子服",},
        [3144] = {4103,"董承","曹操は天子の威光を借りて諸侯に号令しておる。","曹操は天子の威光を借りて諸侯に号令しておる。","劉備",},
        [3145] = {4103,"董承","わしは帝から血で書かれた密詔を受け取り、密かに奴を誅殺する準備をしておる。","わしは帝から血で書かれた密詔を受け取り、密かに奴を誅殺する準備をしておる。","劉備",},
        [3146] = {4103,"董承","王子服は既に同意した。揚威将軍と劉備殿のお二人も加わらぬか?","王子服は既に同意した。揚威将軍と劉備殿のお二人も加わらぬか?","劉備",},
        [3147] = {4103,"劉備","お話はわかりました。よく考えさせて下され...","お話はわかりました。よく考えさせて下され...","董承",},
        [3148] = {4103,"主君","私も同じくだ。よく考えたい。","私も同じくよ。よく考えたいわ。","董承",},
        [3149] = {4201,"張飛","兄貴はどうして最近、裏庭で野菜を育ててるんですかい?水やりまで自分でやっちまって。","兄貴はどうして最近、裏庭で野菜を育ててるんですかい?水やりまで自分でやっちまって。","劉備",},
        [3150] = {4201,"関羽","まったくです。天下の大事を気にかけず、百姓のようなまねごとをして。どうしたことですか?","まったくです。天下の大事を気にかけず、百姓のようなまねごとをして。どうしたことですか?","劉備",},
        [3151] = {4201,"劉備","今は言うな。お前たち二人の知らぬことなのだ。","今は言うな。お前たち二人の知らぬことなのだ。","関羽",},
        [3152] = {4202,"許褚","ブヒッ、丞相のご命令だぞう！すぐに来るんだぞう。","ブヒッ、丞相のご命令だぞう！すぐに来るんだぞう。","劉備",},
        [3153] = {4202,"劉備","何か急なことでも?","何か急なことでも?","許褚",},
        [3154] = {4202,"許褚","知らないぞう。ただ、来いと言ってたぞう。","知らないぞう。ただ、来いと言ってたぞう。","劉備",},
        [3155] = {4202,"主君","関羽と張飛の兄者がいない。私が一緒に参りましょう。","関羽兄さんも張飛兄さんもいないから、私がお供しましょう。","劉備",},
        [3156] = {4203,"曹操","この先に風景の美しい亭がある。","この先に風景の美しい亭がある。","劉備",},
        [3157] = {4203,"曹操","美酒佳肴を用意した。旬の青梅もある。","美酒佳肴を用意した。旬の青梅もある。","劉備",},
        [3158] = {4203,"曹操","我ら三人で大いに飲もうではないか。","我ら三人で大いに飲もうではないか。","劉備",},
        [3159] = {4203,"劉備","さすが曹操殿。じつに風雅ですな。","さすが曹操殿。じつに風雅ですな。","曹操",},
        [3160] = {4203,"主君","せっかくのお申し出です。曹操兄にご案内頂きましょう。","せっかくのお申し出ですわ。曹操兄さんにご案内頂きましょう。","曹操",},
        [3161] = {4204,"曹操","英雄たるもの、胸に大志、腹に良き謀を抱きかねばならん。宇宙の機を蔵し、天地の志を呑み、かつ吐くのだ。","英雄たるもの、胸に大志、腹に良き謀を抱きかねばならん。宇宙の機を蔵し、天地の志を呑み、かつ吐くのだ。","劉備",},
        [3162] = {4204,"劉備","誰がそのような者になれましょうか?","誰がそのような者になれましょうか?","曹操",},
        [3163] = {4204,"曹操","今、天下の英雄と言えるはそなたと、この曹操のみだ！","今、天下の英雄と言えるはそなたと、この曹操のみだ！","劉備",},
        [3164] = {4204,"劉備","申し訳ない。一瞬の雷鳴に驚き、このようになりました。","申し訳ない。一瞬の雷鳴に驚き、このようになりました。","曹操",},
        [3165] = {4204,"劉備","(劉備は曹操の言葉を聞いて驚き、手に持った箸を地面に落としたため、慌てて言い訳をする。)","(劉備は曹操の言葉を聞いて驚き、手に持った箸を地面に落としたため、慌てて言い訳をする。)","曹操",},
        [3166] = {4204,"曹操","そなたのような男でも雷を恐れるのか?","そなたのような男でも雷を恐れるのか?","劉備",},
        [3167] = {4204,"劉備","たとえ聖人とて、激しい雷や風に顔色を変えます。私が恐れずにいられましょうか?","たとえ聖人とて、激しい雷や風に顔色を変えます。私が恐れずにいられましょうか?","曹操",},
        [3168] = {4204,"曹操","劉備、揚威将軍を見よ。少しも恐れず、飲み続けておるぞ。","劉備、揚威将軍を見よ。少しも恐れず、飲み続けておるぞ。","劉備",},
        [3169] = {4204,"主君","あの程度の雷、恐れるわけないですよ。","あの程度の雷など、恐れるに足りませんわ。","曹操",},
        [3170] = {4204,"曹操","うむ、堂々たる将軍だ。まさに当世の英雄だな！","うむ、堂々たる将軍だ。まさに当世の英雄だな！","プレイヤー",},
        [3171] = {4301,"曹操","袁紹が公孫瓚を破り、奴の軍まで手に入れた。名声も勢いも増しておる。","袁紹が公孫瓚を破り、奴の軍まで手に入れた。名声も勢いも増しておる。","劉備",},
        [3172] = {4301,"曹操","だが奴は驕りが過ぎる。皆に背かれ、帝位を袁紹に譲ろうとしておる。玉璽を携えて淮南を棄て、河北に身を寄せようとしておるようだ。","だが奴は驕りが過ぎる。皆に背かれ、帝位を袁紹に譲ろうとしておる。玉璽を携えて淮南を棄て、河北に身を寄せようとしておるようだ。","劉備",},
        [3173] = {4301,"劉備","二人が協力すれば、取り返しがつきませぬな。","二人が協力すれば、取り返しがつきませぬな。","曹操",},
        [3174] = {4301,"曹操","正にそのとおりだ。","正にそのとおりだ。","劉備",},
        [3175] = {4301,"劉備","袁術が袁紹に身を寄せるなら、必ず徐州を通ります。私が軍をお借りして遮れば、袁術を生け捕れるでしょう。","袁術が袁紹に身を寄せるなら、必ず徐州を通ります。私が軍をお借りして遮れば、袁術を生け捕れるでしょう。","曹操",},
        [3176] = {4301,"曹操","よし、急ぎ五万の人馬を率いて行け。","よし、急ぎ五万の人馬を率いて行け。","劉備",},
        [3177] = {4302,"関羽","兄者はなぜ夜中に兵器や馬の準備をしているのです?しかも、そんなに慌てて。","兄者はなぜ夜中に兵器や馬の準備をしているのです?しかも、そんなに慌てて。","劉備",},
        [3178] = {4302,"劉備","私は籠の中の鳥、網の中の魚。今回出立すれば、魚が大海に入り、鳥が大空に上るようなものだ。もう籠や網の束縛を受けるつもりはない。","私は籠の中の鳥、網の中の魚。今回出立すれば、魚が大海に入り、鳥が大空に上るようなものだ。もう籠や網の束縛を受けるつもりはない。","関羽",},
        [3179] = {4302,"劉備","それで、善は急げというわけだ。","それで、善は急げというわけだ。","関羽",},
        [3180] = {4303,"程昱","丞相はなぜ、劉備に五万の兵馬を与えて行かせたのです?","丞相はなぜ、劉備に五万の兵馬を与えて行かせたのです?","曹操",},
        [3181] = {4303,"曹操","無論、徐州へ行かせて、袁術を遮るためだ。","無論、徐州へ行かせて、袁術を遮るためだ。","程昱",},
        [3182] = {4303,"程昱","劉備が予州牧だった時、ワシは殺すよう進言しましたが、丞相はお聞きになりませんでした。","劉備が予州牧だった時、ワシは殺すよう進言しましたが、丞相はお聞きになりませんでした。","曹操",},
        [3183] = {4303,"程昱","今日また奴に兵を与えるとは、龍を海に放ち、虎を山に帰すようなものです。おそらくは二度と戻らぬでしょうな。","今日また奴に兵を与えるとは、龍を海に放ち、虎を山に帰すようなものです。おそらくは二度と戻らぬでしょうな。","曹操",},
        [3184] = {4303,"郭嘉","程昱殿に同意いたします。古人も『一日敵を放せば、万世の患』と申します。丞相のご明察を。","程昱殿に同意いたします。古人も『一日敵を放せば、万世の患』と申します。丞相のご明察を。","曹操",},
        [3185] = {4303,"曹操","わかった。許褚、兵を率いて行け。劉備を連れ戻してくるのだ。","わかった。許褚、兵を率いて行け。劉備を連れ戻してくるのだ。","郭嘉",},
        [3186] = {4303,"許褚","ブヒッ、すぐ行きますぞう！","ブヒッ、すぐ行きますぞう！","曹操",},
        [3187] = {4304,"関羽","曹操の兵が追い付いてきましたぞ！","曹操の兵が追い付いてきましたぞ！","劉備",},
        [3188] = {4304,"劉備","慌てるな。伝令だ。兵と鎧兜の準備をせよ。","慌てるな。伝令だ。兵と鎧兜の準備をせよ。","関羽",},
        [3189] = {4304,"許褚","劉備はどこにいるんだぞう！?","劉備はどこにいるんだぞう！?","劉備",},
        [3190] = {4304,"劉備","許褚将軍、何の御用事で?","許褚将軍、何の御用事で?","許褚",},
        [3191] = {4304,"許褚","丞相のご命令だぞう！将軍は戻ってこい。別に相談があるんだぞう！","丞相のご命令だぞう！将軍は戻ってこい。別に相談があるんだぞう！","劉備",},
        [3192] = {4304,"劉備","武将が外にある時は、主君の命を受けぬこともあります。私は直接丞相に対面し、お言葉を得て来たのです。","武将が外にある時は、主君の命を受けぬこともあります。私は直接丞相に対面し、お言葉を得て来たのです。","許褚",},
        [3193] = {4304,"劉備","他に用事がないなら、許褚殿はすぐに戻られよ。私の返事を丞相に申しあげて下され。","他に用事がないなら、許褚殿はすぐに戻られよ。私の返事を丞相に申しあげて下され。","許褚",},
        [3194] = {4304,"許褚","そんなこと言うなら、すこし手荒な真似をしないとダメなんだぞう！","そんなこと言うなら、すこし手荒な真似をしないとダメなんだぞう！","劉備",},
        [3195] = {4304,"主君","私がお相手しよう。かかってこい！","私がお相手するわ。かかってきなさい！","許褚",},
        [3196] = {4401,"劉備","徐州刺史、袁術の状況はどうだ?","徐州刺史、袁術の状況はどうだ?","車胄",},
        [3197] = {4401,"車胄","袁術は豪奢な生活に溺れており、部下の雷薄、陳蘭は皆嵩山に身を投じました。","袁術は豪奢な生活に溺れており、部下の雷薄、陳蘭は皆嵩山に身を投じました。","劉備",},
        [3198] = {4401,"車胄","すでに勢いは衰え、袁紹に帝位を譲るという書簡を出しました。人馬や宮廷での御用の品々をまとめ、もうすぐ徐州に着くところです。","すでに勢いは衰え、袁紹に帝位を譲るという書簡を出しました。人馬や宮廷での御用の品々をまとめ、もうすぐ徐州に着くところです。","劉備",},
        [3199] = {4401,"劉備","良いところに来たということだな。誰か、先陣を切りたい者はいるか?","良いところに来たということだな。誰か、先陣を切りたい者はいるか?","車胄",},
        [3200] = {4401,"主君","ぜひ私に！逆賊、袁術を斬るご命令をください！","ぜひ私に！逆賊、袁術を斬るご命令をください！","劉備",},
        [3201] = {4401,"劉備","よし、すぐに人馬を携えて行くがよい。","よし、すぐに人馬を携えて行くがよい。","プレイヤー",},
        [3202] = {4402,"紀霊","袁術様の先鋒隊だぞ。邪魔しようとするのは誰だ。","袁術様の先鋒隊だぞ。邪魔しようとするのは誰だ。","プレイヤー",},
        [3203] = {4402,"主君","揚威将軍よ。命を受け、あなたたちの首を取りにきたわ。","揚威将軍よ。命を受け、あなたたちの首を取りにきたわ。","紀霊",},
        [3204] = {4402,"紀霊","ほざけ！","ほざけ！","プレイヤー",},
        [3205] = {4403,"劉備","悪逆非道の輩め。詔を受け、お前を討ちに来た！先鋒隊の紀霊は、既に我らが斬ったぞ。","悪逆非道の輩め。詔を受け、お前を討ちに来た！先鋒隊の紀霊は、既に我らが斬ったぞ。","袁術",},
        [3206] = {4403,"劉備","大人しく縄に付くがよい。","大人しく縄に付くがよい。","袁術",},
        [3207] = {4403,"袁術","おのれ！むしろ編みの小童が、朕を馬鹿にする気か！","おのれ！むしろ編みの小童が、朕を馬鹿にする気か！","劉備",},
        [3208] = {4404,"袁術","炊事係はどこだ！炊事係！","炊事係はどこだ！炊事係！","",},
        [3209] = {4404,"炊事係","ここにおります。何なりとお申し付けを。","ここにおります。何なりとお申し付けを。","袁術",},
        [3210] = {4404,"袁術","飯がまずくて飲み込めん。それに喉が渇いた！蜂蜜水を持ってまいれ。","飯がまずくて飲み込めん。それに喉が渇いた！蜂蜜水を持ってまいれ。","炊事係",},
        [3211] = {4404,"炊事係","恐れながら、殿。揚威将軍と劉備に敗れ、山賊の強奪にも遭い、もはや糧食も尽きております。","恐れながら、殿。揚威将軍と劉備に敗れ、山賊の強奪にも遭い、もはや糧食も尽きております。","袁術",},
        [3212] = {4404,"炊事係","血が流れるばかりで、蜂蜜水などございません！","血が流れるばかりで、蜂蜜水などございません！","袁術",},
        [3213] = {4404,"袁術","馬鹿者！","馬鹿者！","炊事係",},
        [3214] = {4404,"袁術","くそおっ！朕は四世三公の名門の出だぞ！","くそおっ！朕は四世三公の名門の出だぞ！","炊事係",},
        [3215] = {4404,"袁術","ここまで落ちぶれることなどあってはならん！(そう言うと、地面に倒れ、血を一斗余り吐いて死ぬ)","ここまで落ちぶれることなどあってはならん！(そう言うと、地面に倒れ、血を一斗余り吐いて死ぬ)","炊事係",},
        [3216] = {4501,"曹操","これはこれは。帯に密詔を忍ばせるとは！陰謀を企て、私を除くおつもりか?","これはこれは。帯に密詔を忍ばせるとは！陰謀を企て、私を除くおつもりか?","献帝",},
        [3217] = {4501,"曹操","逆賊、董承は一族財産没収の上、皆殺しだ。陛下の董妃も死を免れませぬぞ！","逆賊、董承は一族財産没収の上、皆殺しだ。陛下の董妃も死を免れませぬぞ！","献帝",},
        [3218] = {4501,"献帝","密詔など知らぬ。董承一人がやったことだ。朕とは関係ない！","密詔など知らぬ。董承一人がやったことだ。朕とは関係ない！","曹操",},
        [3219] = {4501,"曹操","そうだと宜しいのですがな。","そうだと宜しいのですがな。","献帝",},
        [3220] = {4502,"曹操","劉備は紀霊を斬って袁術を破り、朱霊と路昭を許都に帰らせた。","劉備は紀霊を斬って袁術を破り、朱霊と路昭を許都に帰らせた。","車胄",},
        [3221] = {4502,"曹操","表面上は軍馬を残して徐州を守っているようだが、とうに謀反の心があるに違いない。","表面上は軍馬を残して徐州を守っているようだが、とうに謀反の心があるに違いない。","車胄",},
        [3222] = {4502,"曹操","徐州刺史よ、策を考えて奴を殺せ。","徐州刺史よ、策を考えて奴を殺せ。","車胄",},
        [3223] = {4502,"車胄","はっ！仰せのとおりに。","はっ！仰せのとおりに。","曹操",},
        [3224] = {4503,"車胄","陳登、劉備には謀反の心がある。","陳登、劉備には謀反の心がある。","陳登",},
        [3225] = {4503,"車胄","丞相は私に、奴を殺すよう密命を下された。何か策はないか?","丞相は私に、奴を殺すよう密命を下された。何か策はないか?","陳登",},
        [3226] = {4503,"陳登","容易い事です。今、劉備は城を出て民を安撫していますが、もうすぐ戻ります。","容易い事です。今、劉備は城を出て民を安撫していますが、もうすぐ戻ります。","車胄",},
        [3227] = {4503,"陳登","城門の内に伏兵を忍ばせておいて、迎えるふりをして斬ってしまえばよいのです。","城門の内に伏兵を忍ばせておいて、迎えるふりをして斬ってしまえばよいのです。","車胄",},
        [3228] = {4503,"陳登","私が城の上から弓矢で後続の軍を討ちましょう。きっと成功します！","私が城の上から弓矢で後続の軍を討ちましょう。きっと成功します！","車胄",},
        [3229] = {4503,"車胄","よし、お前の策どおりにしよう。","よし、お前の策どおりにしよう。","陳登",},
        [3230] = {4504,"陳登","関羽殿、大変です！曹操が車冑に劉備殿の暗殺を命じました。","関羽殿、大変です！曹操が車冑に劉備殿の暗殺を命じました。","関羽",},
        [3231] = {4504,"陳登","劉備殿が城外からお戻りになるとき、城門の内に伏兵を置いて斬るつもりです。","劉備殿が城外からお戻りになるとき、城門の内に伏兵を置いて斬るつもりです。","関羽",},
        [3232] = {4504,"関羽","わかった。その策を逆手に取ろう。わしが夜陰に乗じて曹操軍の振りをし、徐州へ先回りする。車冑を誘き出して、討とう。","わかった。その策を逆手に取ろう。わしが夜陰に乗じて曹操軍の振りをし、徐州へ先回りする。車冑を誘き出して、討とう。","陳登",},
        [3233] = {4504,"陳登","はい、車冑が城を出たら、私が城から矢を射て挟み撃ちにしましょう。","はい、車冑が城を出たら、私が城から矢を射て挟み撃ちにしましょう。","関羽",},
        [3234] = {4505,"関羽","車冑が曹操の命を受け、兄者を殺そうとしていると、陳登が知らせてくれました。","車冑が曹操の命を受け、兄者を殺そうとしていると、陳登が知らせてくれました。","劉備",},
        [3235] = {4505,"関羽","そこでわしと陳登が先回りし、逆に車冑を斬りました。","そこでわしと陳登が先回りし、逆に車冑を斬りました。","劉備",},
        [3236] = {4505,"劉備","何ということを。もし曹操がやって来たら、どうするのだ。","何ということを。もし曹操がやって来たら、どうするのだ。","関羽",},
        [3237] = {4505,"関羽","わしと張飛で迎え討ちましょう。","わしと張飛で迎え討ちましょう。","劉備",},
        [3238] = {4505,"劉備","曹操の腹心を殺したのだから、追及は免れぬのではないか。","曹操の腹心を殺したのだから、追及は免れぬのではないか。","関羽",},
        [3239] = {4505,"陳登","劉備殿、私に一計があります。徐州に入られるとよい。曹操を退散させてみせましょう。","劉備殿、私に一計があります。徐州に入られるとよい。曹操を退散させてみせましょう。","劉備",},
        [3240] = {4601,"陳登","袁紹は冀州、青州、幽州、并州の諸郡に居座り兵百万を携え、文官、武将も大勢抱えています。曹操はやはりこれを恐れています。","袁紹は冀州、青州、幽州、并州の諸郡に居座り兵百万を携え、文官、武将も大勢抱えています。曹操はやはりこれを恐れています。","劉備",},
        [3241] = {4601,"陳登","なぜ使者を遣わして、彼に助けを求めないのですか。","なぜ使者を遣わして、彼に助けを求めないのですか。","劉備",},
        [3242] = {4601,"劉備","求められるわけがない。袁紹殿とは付き合いもないうえ、従弟の袁術を滅ぼしてしまったのだ。","求められるわけがない。袁紹殿とは付き合いもないうえ、従弟の袁術を滅ぼしてしまったのだ。","陳登",},
        [3243] = {4601,"陳登","ですが、袁紹と代々親しくしておる者がございます。その者から書簡を送れば、助けを得られるでしょう。","ですが、袁紹と代々親しくしておる者がございます。その者から書簡を送れば、助けを得られるでしょう。","劉備",},
        [3244] = {4601,"劉備","なるほど、それはどなただろう。","なるほど、それはどなただろう。","陳登",},
        [3245] = {4601,"陳登","劉備殿が普段から敬っている者です。お忘れですか?","劉備殿が普段から敬っている者です。お忘れですか?","劉備",},
        [3246] = {4601,"劉備","鄭玄先生のことか。","鄭玄先生のことか。","陳登",},
        [3247] = {4601,"陳登","左様です。","左様です。","劉備",},
        [3248] = {4602,"袁紹","鄭玄尚書から書簡が来た。出兵して劉備を助け、曹操を攻めよと言うが、迷っておる。兵を出すべきか、出さざるべきか?","鄭玄尚書から書簡が来た。出兵して劉備を助け、曹操を攻めよと言うが、迷っておる。兵を出すべきか、出さざるべきか?","",},
        [3249] = {4602,"沮授","出すべきです。公は多数で少数に勝ち、強きをもって弱きを攻めるのが宜しいでしょう。漢の賊、曹操を討ち、王室を助けるのです。","出すべきです。公は多数で少数に勝ち、強きをもって弱きを攻めるのが宜しいでしょう。漢の賊、曹操を討ち、王室を助けるのです。","袁紹",},
        [3250] = {4602,"袁紹","うむ、わしもそう考えておった。","うむ、わしもそう考えておった。","沮授",},
        [3251] = {4602,"袁紹","田豊、荀諶、許攸を軍師とし、顔良、文醜を将軍とせよ。兵三十万を率いて、黎陽へ出兵する。","田豊、荀諶、許攸を軍師とし、顔良、文醜を将軍とせよ。兵三十万を率いて、黎陽へ出兵する。","沮授",},
        [3252] = {4602,"袁紹","陳琳に曹操討伐の檄文を書かせよ。奴の罪状を天下に知らしめるのだ！","陳琳に曹操討伐の檄文を書かせよ。奴の罪状を天下に知らしめるのだ！","沮授",},
        [3253] = {4603,"孔融","曹操殿の罪状をあげつらった檄文が、許都じゅうに広まってますよ。","曹操殿の罪状をあげつらった檄文が、許都じゅうに広まってますよ。","曹操",},
        [3254] = {4603,"曹操","知っておる。先から頭痛がしておったが、あの檄文を見たら、体中に冷や汗が出てな。頭痛も治ってしまったわ！","知っておる。先から頭痛がしておったが、あの檄文を見たら、体中に冷や汗が出てな。頭痛も治ってしまったわ！","孔融",},
        [3255] = {4603,"孔融","頭痛も吹き飛ぶ見事な悪口でしたねえ。それはともかく、袁紹の勢力は強大です。戦いは避け、和睦するべきでしょう。","頭痛も吹き飛ぶ見事な悪口でしたねえ。それはともかく、袁紹の勢力は強大です。戦いは避け、和睦するべきでしょう。","曹操",},
        [3256] = {4603,"荀彧","袁紹は取るに足らない小者です。なぜ和する必要があるのです?","袁紹は取るに足らない小者です。なぜ和する必要があるのです?","孔融",},
        [3257] = {4603,"孔融","袁紹は配下に人材が多いでしょ。許攸、郭図、審配、逢紀などは智謀の士ですし。","袁紹は配下に人材が多いでしょ。許攸、郭図、審配、逢紀などは智謀の士ですし。","荀彧",},
        [3258] = {4603,"孔融","田豊と沮授は忠臣ですし、顔良と文醜は全軍随一の勇者。高覧、張郃、淳于瓊らも名だたる名将です。","田豊と沮授は忠臣ですし、顔良と文醜は全軍随一の勇者。高覧、張郃、淳于瓊らも名だたる名将です。","荀彧",},
        [3259] = {4603,"孔融","とても小者だとは侮れませんよ。","とても小者だとは侮れませんよ。","荀彧",},
        [3260] = {4603,"荀彧","袁紹の兵は多くても整っておりません。","袁紹の兵は多くても整っておりません。","孔融",},
        [3261] = {4603,"荀彧","田豊は剛情で上の命令に逆らいます。許攸は貪欲で浅はか、審配は独断的で計画性がなく、逢紀は向こう見ずに行動します。","田豊は剛情で上の命令に逆らいます。許攸は貪欲で浅はか、審配は独断的で計画性がなく、逢紀は向こう見ずに行動します。","孔融",},
        [3262] = {4603,"荀彧","彼らは互いに相容れず、必ず内紛を起こすでしょう。顔良、文醜は蛮勇があるのみで、戦えばすぐ捕らえられます。","彼らは互いに相容れず、必ず内紛を起こすでしょう。顔良、文醜は蛮勇があるのみで、戦えばすぐ捕らえられます。","孔融",},
        [3263] = {4603,"荀彧","その他の平凡な輩は、百万いたとて取るに足りませぬ。","その他の平凡な輩は、百万いたとて取るに足りませぬ。","孔融",},
        [3264] = {4603,"曹操","すべて荀彧の考えの内だということだな。","すべて荀彧の考えの内だということだな。","荀彧",},
        [3265] = {4603,"曹操","劉岱と後陣の王忠に命じ、五万の兵を出せ。わしの旗印を掲げ、劉備を攻めるのだ。","劉岱と後陣の王忠に命じ、五万の兵を出せ。わしの旗印を掲げ、劉備を攻めるのだ。","荀彧",},
        [3266] = {4604,"劉備","袁紹は当初黎陽で屯田を行ったが、謀臣と不和になり、まだその地を取っていない。","袁紹は当初黎陽で屯田を行ったが、謀臣と不和になり、まだその地を取っていない。","陳登",},
        [3267] = {4604,"劉備","曹操はどこにいるのだろう。黎陽の軍中に旗印はないと聞くが、なぜここにあるのだ?","曹操はどこにいるのだろう。黎陽の軍中に旗印はないと聞くが、なぜここにあるのだ?","陳登",},
        [3268] = {4604,"陳登","曹操には様々な計略があります。きっと河北を重視し、自ら指揮を執りに行ったのでしょう。この旗印は目くらましです。","曹操には様々な計略があります。きっと河北を重視し、自ら指揮を執りに行ったのでしょう。この旗印は目くらましです。","劉備",},
        [3269] = {4604,"劉備","なるほどな……誰か、虚実を探りに行くものはいないか?","なるほどな……誰か、虚実を探りに行くものはいないか?","陳登",},
        [3270] = {4604,"関羽","わしが行きましょう。","わしが行きましょう。","劉備",},
        [3271] = {4604,"主君","関羽兄者の助太刀に、私も一緒に行きます。","関羽兄さんの助太刀に、私も一緒に行きます。","劉備",},
        [3272] = {4605,"王忠","丞相はここにおられる。なぜ投降しない?","丞相はここにおられる。なぜ投降しない?","関羽",},
        [3273] = {4605,"関羽","おられるなら出陣願いたい。わしから話がある。","おられるなら出陣願いたい。わしから話がある。","王忠",},
        [3274] = {4605,"王忠","馬鹿め。そんなに軽々しく会われるものか！","馬鹿め。そんなに軽々しく会われるものか！","関羽",},
        [3275] = {4605,"関羽","やはりな。丞相はここにはおらぬのだろう。そんな策に乗るものか。","やはりな。丞相はここにはおらぬのだろう。そんな策に乗るものか。","王忠",},
        [3276] = {4605,"主君","生け捕りにしてやる。待っていろ！","生け捕りにしてやるわ。待ってなさい！","王忠",},
        [3277] = {4606,"主君","王忠は我らが生け捕りにしました。","王忠は私たちが生け捕りにしました。","劉備",},
        [3278] = {4606,"劉備","うむ、よくやってくれた。","うむ、よくやってくれた。","プレイヤー",},
        [3279] = {4606,"張飛","よっしゃ！ならオレは劉岱を生け捕りにしてくるぜ！","よっしゃ！ならオレは劉岱を生け捕りにしてくるぜ！","劉備",},
        [3280] = {4606,"劉備","劉岱はかつて兗州刺史だった。虎牢関で董卓を討った時も、一つの鎮を治める諸侯だった。","劉岱はかつて兗州刺史だった。虎牢関で董卓を討った時も、一つの鎮を治める諸侯だった。","張飛",},
        [3281] = {4606,"劉備","今は先鋒を勤めているが、決して侮ってはならぬぞ。","今は先鋒を勤めているが、決して侮ってはならぬぞ。","張飛",},
        [3282] = {4606,"張飛","心配するなよ、兄貴！あんな奴ら取るに足らねえってんだ！","心配するなよ、兄貴！あんな奴ら取るに足らねえってんだ！","劉備",},
        [3283] = {4606,"劉備","命まで奪って、我が大事を誤らねばよいが……","命まで奪って、我が大事を誤らねばよいが……","張飛",},
        [3284] = {4606,"主君","ご心配でしたら、私が一緒に行きましょう。","ご心配でしたら、私が一緒に行きますわ。","劉備",},
        [3285] = {4606,"劉備","おお。揚威将軍が行ってくれるなら、安心だ。","おお。揚威将軍が行ってくれるなら、安心だ。","プレイヤー",},
        [3286] = {4607,"張飛","劉岱も大したことねえな。オレに生け捕られるなんてよ！","劉岱も大したことねえな。オレに生け捕られるなんてよ！","",},
        [3287] = {4607,"張飛","ガハハハハ～ッ！","ガハハハハ～ッ！","",},
        [3288] = {4608,"張飛","兄貴、王忠と劉岱を連れてきたぜ！","兄貴、王忠と劉岱を連れてきたぜ！","劉備",},
        [3289] = {4608,"劉岱","フン！殺そうが肉を削ごうが、好きにしろ！","フン！殺そうが肉を削ごうが、好きにしろ！","劉備",},
        [3290] = {4608,"劉備","これは失礼した。すぐに縄を解こう。","これは失礼した。すぐに縄を解こう。","劉岱",},
        [3291] = {4608,"劉備","車冑は私を殺そうとしたので、斬らざるを得なかったのです。丞相は私に謀反の心ありと誤解され、お二方を使わしたのです。","車冑は私を殺そうとしたので、斬らざるを得なかったのです。丞相は私に謀反の心ありと誤解され、お二方を使わしたのです。","劉岱",},
        [3292] = {4608,"劉備","私は丞相の大恩を蒙り、御恩に報いたいと思っていたところ。謀反などとんでもない。","私は丞相の大恩を蒙り、御恩に報いたいと思っていたところ。謀反などとんでもない。","劉岱",},
        [3293] = {4608,"劉備","お二方が許都に戻られたら、この劉備のために弁解して頂ければ幸いです。","お二方が許都に戻られたら、この劉備のために弁解して頂ければ幸いです。","劉岱",},
        [3294] = {4608,"王忠","命を助けて下さり、お礼申し上げる。","命を助けて下さり、お礼申し上げる。","劉備",},
        [3295] = {4608,"劉岱","ワシもです。丞相の件、我ら両家の一族にかけて、劉備殿にお誓い申す。","ワシもです。丞相の件、我ら両家の一族にかけて、劉備殿にお誓い申す。","劉備",},
        [3296] = {4608,"劉備","かたじけない。すぐにお二人に馬を準備します。兵馬もお返ししましょう。","かたじけない。すぐにお二人に馬を準備します。兵馬もお返ししましょう。","劉岱",},
        [3297] = {4609,"張飛","待ちやがれ！","待ちやがれ！","劉岱",},
        [3298] = {4609,"張飛","兄貴は何をしてるんだ！賊を捕らえたのに、何故また放す?","兄貴は何をしてるんだ！賊を捕らえたのに、何故また放す?","劉岱",},
        [3299] = {4609,"劉岱","ど、ど、どうする気だ?","ど、ど、どうする気だ?","張飛",},
        [3300] = {4609,"関羽","張飛、無礼な真似はやめろ！","張飛、無礼な真似はやめろ！","張飛",},
        [3301] = {4609,"関羽","兄者が放すと決めたのだ。なぜお前が命令を守らん?","兄者が放すと決めたのだ。なぜお前が命令を守らん?","張飛",},
        [3302] = {4609,"張飛","でも兄貴！今、放してやったら、また来るぜ！","でも兄貴！今、放してやったら、また来るぜ！","関羽",},
        [3303] = {4609,"関羽","ならば、その時に斬っても遅くはなかろう。","ならば、その時に斬っても遅くはなかろう。","張飛",},
        [3304] = {4609,"王忠","そのとおりです。たとえ丞相に一族を皆殺しにすると言われても、もう来ません。どうか将軍、寛大なご処置を。","そのとおりです。たとえ丞相に一族を皆殺しにすると言われても、もう来ません。どうか将軍、寛大なご処置を。","張飛",},
        [3305] = {4609,"張飛","フン、わかったよ。今日のところは見逃してやらあ！たとえ曹操本人が来ても、鎧も残らぬほどぶっ潰してやるぜ！","フン、わかったよ。今日のところは見逃してやらあ！たとえ曹操本人が来ても、鎧も残らぬほどぶっ潰してやるぜ！","王忠",},
        [3306] = {4609,"劉岱","王忠、早く行こう。あの張飛とやらの気が変わらないうちに。","王忠、早く行こう。あの張飛とやらの気が変わらないうちに。","王忠",},
        [3307] = {4610,"曹操","王忠、劉岱、この愚か者ども。よくも何もせず帰ってきたな！即刻斬れ。","王忠、劉岱、この愚か者ども。よくも何もせず帰ってきたな！即刻斬れ。","",},
        [3308] = {4610,"劉岱","丞相、命ばかりはお助けを！","丞相、命ばかりはお助けを！","曹操",},
        [3309] = {4610,"王忠","ひいぃっ！","ひいぃっ！","曹操",},
        [3310] = {4610,"孔融","まあまあ。この二人に劉備の相手はそもそも無理だったんですから。斬ってしまったら、将兵たちの心が離れますよ。","まあまあ。この二人に劉備の相手はそもそも無理だったんですから。斬ってしまったら、将兵たちの心が離れますよ。","曹操",},
        [3311] = {4610,"曹操","ふん……死罪は免じてやるが、罰は逃れられぬぞ！","ふん……死罪は免じてやるが、罰は逃れられぬぞ！","孔融",},
        [3312] = {4610,"曹操","この二人の官職と俸禄を取り上げ、追い出してしまえ！","この二人の官職と俸禄を取り上げ、追い出してしまえ！","孔融",},
        [3313] = {4610,"劉岱","孔融殿のお取りなしに感謝する。丞相、命を助けて頂き、有難く存じます！","孔融殿のお取りなしに感謝する。丞相、命を助けて頂き、有難く存じます！","曹操",},
        [3314] = {4610,"王忠","わしもです！","わしもです！","曹操",},
        [3315] = {4701,"劉備","曹操は、密詔の一件で、董承、王子服らを斬った。","曹操は、密詔の一件で、董承、王子服らを斬った。","プレイヤー",},
        [3316] = {4701,"劉備","お腹に5か月の子どもがいた董貴妃も見逃しはしなかった。","お腹に5か月の子どもがいた董貴妃も見逃しはしなかった。","プレイヤー",},
        [3317] = {4701,"劉備","私もこの件には関係がある。曹操はまた兵を上げて攻めて来るだろう。","私もこの件には関係がある。曹操はまた兵を上げて攻めて来るだろう。","プレイヤー",},
        [3318] = {4701,"主君","ご心配なく。私が冀州へ行って、もう一度袁紹の援助を求めて来ましょう。","ご心配なく。私が冀州へ行って、もう一度袁紹の援助を求めて来ましょう。","劉備",},
        [3319] = {4702,"主君","袁紹殿、なぜそんなに憔悴しているんですか。服装も乱れてるし。","袁紹殿、なぜそんなに憔悴しているんですか。服装も乱れてるし。","袁紹",},
        [3320] = {4702,"袁紹","ああああっ、わしはもう死ぬ！","ああああっ、わしはもう死ぬ！","プレイヤー",},
        [3321] = {4702,"主君","どうしたんです、いったい?","どうしたの、いったい！","袁紹",},
        [3322] = {4702,"袁紹","わしは五人の子をもうけたが、意に沿うのは末子だけだった。それなのに、その子は今、疥癬で命も危ない。","わしは五人の子をもうけたが、意に沿うのは末子だけだった。それなのに、その子は今、疥癬で命も危ない。","プレイヤー",},
        [3323] = {4702,"袁紹","他の事に構う気になどなれんのだ！","他の事に構う気になどなれんのだ！","プレイヤー",},
        [3324] = {4702,"主君","今、曹操は劉備殿を討つため東征しています。この隙に乗じて義兵を起こせば、天子と万民を救うことができます。","今、曹操は劉備殿を討つため東征しています。この隙に乗じて義兵を起こせば、天子と万民を救うことができます。","袁紹",},
        [3325] = {4702,"主君","これは得難い機会ですよ。袁紹殿にしか決断できないことです。","これは得難い機会ですよ。袁紹殿にしか決断できないことです。","袁紹",},
        [3326] = {4702,"袁紹","それどころではないと言っておるだろう！","それどころではないと言っておるだろう！","プレイヤー",},
        [3327] = {4702,"主君","天下のほうが大事です！","天下のほうが大事よ！","袁紹",},
        [3328] = {4703,"主君","最初は不利を恐れていましたが、説得が功を奏し救援を約束してくれました。","最初は不利を恐れていましたが、説得が功を奏し救援を約束してくれました。","劉備",},
        [3329] = {4703,"主君","曹操が東征してこちらに向かった隙をつき、袁紹が許昌を襲撃します。敵を分散させる、「魏を囲みて趙を救う」の策です。","曹操が東征してこちらに向かった隙をつき、袁紹が許昌を襲撃します。敵を分散させる、「魏を囲みて趙を救う」の策です。","劉備",},
        [3330] = {4703,"主君","袁紹は、策が失敗した場合、身を寄せてもよいと言っておりました。","袁紹は、策が失敗した場合、身を寄せてもよいと言っていましたわ。","劉備",},
        [3331] = {4703,"劉備","うむ。","うむ。","プレイヤー",},
        [3332] = {4801,"張繍","今は袁紹のほうが曹操より強い。なのに袁紹からの書面を破り、使者を追い返してしまって。もし袁紹が攻めて来たらどうするのだ?","今は袁紹のほうが曹操より強い。なのに袁紹からの書面を破り、使者を追い返してしまって。もし袁紹が攻めて来たらどうするのだ?","賈詡",},
        [3333] = {4801,"賈詡","曹操に従うのが良いでしょう。","曹操に従うのが良いでしょう。","張繍",},
        [3334] = {4801,"張繍","俺には曹操との確執がある。簡単には受け入れ難い策だが……","俺には曹操との確執がある。簡単には受け入れ難い策だが……","賈詡",},
        [3335] = {4801,"賈詡","曹操に従うほうが良い理由は三つあります。曹操は天子の詔を奉じて、天下を征伐しています。これが一つ目。","曹操に従うほうが良い理由は三つあります。曹操は天子の詔を奉じて、天下を征伐しています。これが一つ目。","張繍",},
        [3336] = {4801,"賈詡","袁紹は強く、少人数で従っても重用されないでしょう。曹操は弱くても、我が軍が帰順すれば必ず喜びます。これが二つ目。","袁紹は強く、少人数で従っても重用されないでしょう。曹操は弱くても、我が軍が帰順すれば必ず喜びます。これが二つ目。","張繍",},
        [3337] = {4801,"賈詡","最後に曹操には覇王の志があります。私怨は忘れ、世間に徳を示すでしょう。これが三つ目です。将軍、躊躇しないほうがいいですよ。","最後に曹操には覇王の志があります。私怨は忘れ、世間に徳を示すでしょう。これが三つ目です。将軍、躊躇しないほうがいいですよ。","張繍",},
        [3338] = {4802,"張繍","このたび私と賈詡が許都に参りましたのは、曹操殿に投降するためでございます。","このたび私と賈詡が許都に参りましたのは、曹操殿に投降するためでございます。","曹操",},
        [3339] = {4802,"賈詡","どうか丞相、これまでの遺恨は水に流していただけますよう。","どうか丞相、これまでの遺恨は水に流していただけますよう。","曹操",},
        [3340] = {4802,"曹操","ささいな間違いを、気にすることはない。","ささいな間違いを、気にすることはない。","賈詡",},
        [3341] = {4802,"曹操","今後、張繍を揚武将軍とし、賈詡を執金吾としよう。","今後、張繍を揚武将軍とし、賈詡を執金吾としよう。","賈詡",},
        [3342] = {4901,"孫乾","袁紹が曹操を迎え討つなら、小沛に援兵は出してくれないでしょう。","袁紹が曹操を迎え討つなら、小沛に援兵は出してくれないでしょう。","劉備",},
        [3343] = {4901,"劉備","では、どうすれば?","では、どうすれば?","孫乾",},
        [3344] = {4901,"張飛","兄貴、心配はいらねえ。曹操の兵は遠征で疲れてるはずだ。機に乗じて最初に根城を奪っちまえば、曹操を破れるぜ。","兄貴、心配はいらねえ。曹操の兵は遠征で疲れてるはずだ。機に乗じて最初に根城を奪っちまえば、曹操を破れるぜ。","劉備",},
        [3345] = {4901,"劉備","張飛よ、お前は蛮勇のみと思っていたが、なかなか良い策だ、兵法にも合っている。","張飛よ、お前は蛮勇のみと思っていたが、なかなか良い策だ、兵法にも合っている。","張飛",},
        [3346] = {4901,"劉備","そう言えば前に劉岱を捕えた時も、よく謀を用いていたな。","そう言えば前に劉岱を捕えた時も、よく謀を用いていたな。","張飛",},
        [3347] = {4902,"荀彧","風はどの方角から吹いてきました?折れた旗の色は?(突風が吹いて、牙旗が折れてしまう。)","風はどの方角から吹いてきました?折れた旗の色は?(突風が吹いて、牙旗が折れてしまう。)","曹操",},
        [3348] = {4902,"曹操","南東からの風が、角に掲げた青と赤の牙旗を折ってしまった。","南東からの風が、角に掲げた青と赤の牙旗を折ってしまった。","荀彧",},
        [3349] = {4902,"荀彧","となれば、間違いありません。今夜、劉備が夜襲をかけてくるでしょう。","となれば、間違いありません。今夜、劉備が夜襲をかけてくるでしょう。","曹操",},
        [3350] = {4903,"張飛","どうもおかしい。曹操はなんでこんな簡単に陣営の奇襲を許した?営内に人馬も少ねえぞ。","どうもおかしい。曹操はなんでこんな簡単に陣営の奇襲を許した?営内に人馬も少ねえぞ。","",},
        [3351] = {4903,"張飛","やっちまった、こいつは罠か！","やっちまった、こいつは罠か！","",},
        [3352] = {4903,"徐晃","どこへ逃げる気だ！お前はもう包囲されてるぞ！","どこへ逃げる気だ！お前はもう包囲されてるぞ！","張飛",},
        [3353] = {4903,"主君","張飛の兄者、一緒に突破しましょう！","張飛兄さん、一緒に突破しましょう！","張飛",},
        [3354] = {4903,"張飛","揚威将軍、命がけで助けに来てくれるとはありがてえ！","揚威将軍、命がけで助けに来てくれるとはありがてえ！","プレイヤー",},
        [3355] = {4904,"張飛","やっと突破できたか。早く小沛へ帰ろうぜ！","やっと突破できたか。早く小沛へ帰ろうぜ！","プレイヤー",},
        [3356] = {4904,"主君","ダメだ。今戻っても小沛は持ちこたえられない。先に芒碭山へ逃げてください。","ダメよ。今戻っても小沛は持ちこたえられない。先に芒碭山へ逃げてください。","張飛",},
        [3357] = {4904,"張飛","でもよ、劉備の兄貴はどうする?","でもよ、劉備の兄貴はどうする?","プレイヤー",},
        [3358] = {4904,"主君","大丈夫、私が救い出しに行きます。ご安心を。","大丈夫、私が救い出しに行きます。安心して。","張飛",},
        [3359] = {4905,"劉備","いかん、小沛城内から火の手が……ここは棄てるしかないのか……","いかん、小沛城内から火の手が……ここは棄てるしかないのか……","",},
        [3360] = {4905,"劉備","徐州、下邳へ逃れよう。","徐州、下邳へ逃れよう。","",},
        [3361] = {4905,"夏侯淵","逃げるだと?俺の弓がそうさせると思うか！","逃げるだと?俺の弓がそうさせると思うか！","劉備",},
        [3362] = {4905,"主君","劉備の兄者、早くお逃げください。夏侯渊は私が食い止めます。","劉備兄さん、早くお逃げください！夏侯渊は私が食い止めます。","夏侯淵",},
        [3363] = {4906,"劉備","張飛が曹操の軍営を攻めに行ったが、まだ帰らぬ。どうすればよい?","張飛が曹操の軍営を攻めに行ったが、まだ帰らぬ。どうすればよい?","プレイヤー",},
        [3364] = {4906,"主君","ご安心を。既に私が救い出し、芒碭山へ逃げてもらいました。","ご安心を。既に私が救い出し、芒碭山へ逃げていただきましたわ。","劉備",},
        [3365] = {4906,"主君","これから、どうしますか。","これから、どうしますか。","劉備",},
        [3366] = {4906,"劉備","袁紹が言っていたな。うまく行かねば、身を寄せてもよいと。","袁紹が言っていたな。うまく行かねば、身を寄せてもよいと。","プレイヤー",},
        [3367] = {4906,"劉備","しばらく世話になり、別の方策を考えよう。","しばらく世話になり、別の方策を考えよう。","プレイヤー",},
        [3368] = {4906,"主君","はい、ではお先に向かってください。私は徐州で別れた関羽の兄者を助けに行ってきます。","はい、ではお先に向かってください。私は徐州で別れた関羽兄さんを助けに行ってきます。","劉備",},
        [3369] = {5001,"曹操","顔良はなんという勇将だ！","顔良はなんという勇将だ！","",},
        [3370] = {5001,"曹操","何度かの手合わせだけで、我が軍の宋憲、魏続の二人の大将を続けざまに斬るとはな！","何度かの手合わせだけで、我が軍の宋憲、魏続の二人の大将を続けざまに斬るとはな！","",},
        [3371] = {5001,"曹操","奴を止めるには、どうすべきか……","奴を止めるには、どうすべきか……","",},
        [3372] = {5001,"程昱","顔良に匹敵するものを一人推挙致しますぞ。","顔良に匹敵するものを一人推挙致しますぞ。","曹操",},
        [3373] = {5001,"曹操","何者だ?","何者だ?","程昱",},
        [3374] = {5001,"程昱","関羽に他なりませぬ。","関羽に他なりませぬ。","曹操",},
        [3375] = {5001,"曹操","関羽か……だが、あの男は功を立てれば、去ってしまうぞ。","関羽か……だが、あの男は功を立てれば、去ってしまうぞ。","程昱",},
        [3376] = {5001,"程昱","劉備が生きていれば、袁紹に身を投じているでしょうな。関羽が袁紹の兵を破れば、袁紹は劉備を疑って殺すはず。","劉備が生きていれば、袁紹に身を投じているでしょうな。関羽が袁紹の兵を破れば、袁紹は劉備を疑って殺すはず。","曹操",},
        [3377] = {5001,"程昱","劉備が死ねば、関羽はどこに行くというのです?","劉備が死ねば、関羽はどこに行くというのです?","曹操",},
        [3378] = {5001,"曹操","ふむ……よし、すぐに使いを出して、関羽を出陣させよう。","ふむ……よし、すぐに使いを出して、関羽を出陣させよう。","程昱",},
        [3379] = {5002,"曹操","ふもとの陣中にいる顔良だが、相次いで二人の将軍を倒す勇猛ぶりだ。関羽に頼めないかと思ってな。","ふもとの陣中にいる顔良だが、相次いで二人の将軍を倒す勇猛ぶりだ。関羽に頼めないかと思ってな。","関羽",},
        [3380] = {5002,"関羽","ほう。どのような将軍ですか。","ほう。どのような将軍ですか。","曹操",},
        [3381] = {5002,"曹操","河北の人馬にて旗幟鮮明。槍や刀をひしめかせ、士気も高い。手強いぞ。","河北の人馬にて旗幟鮮明。槍や刀をひしめかせ、士気も高い。手強いぞ。","関羽",},
        [3382] = {5002,"関羽","揚威将軍、どう思う?","揚威将軍、どう思う?","曹操",},
        [3383] = {5002,"主君","そうですね……あれは、ただの見掛け倒しかと。","そうね……あれは、ただの見掛け倒しだと思うわ。","関羽",},
        [3384] = {5002,"曹操","麾蓋の下で刺繍の袍に金の兜、刀を持ち馬上にいるのが、顔良だ。","麾蓋の下で刺繍の袍に金の兜、刀を持ち馬上にいるのが、顔良だ。","関羽",},
        [3385] = {5002,"関羽","あれでは、自分の首をさらしているようにしか見えませぬな。","あれでは、自分の首をさらしているようにしか見えませぬな。","曹操",},
        [3386] = {5002,"曹操","侮ってはならんぞ。","侮ってはならんぞ。","関羽",},
        [3387] = {5002,"関羽","非才ながら、わしが万の軍勢の中からその首を取り、丞相に献じましょう。","非才ながら、わしが万の軍勢の中からその首を取り、丞相に献じましょう。","曹操",},
        [3388] = {5002,"張遼","軍内で戯言は許されませぬぞ。必ずややって頂こう。","軍内で戯言は許されませぬぞ。必ずややって頂こう。","関羽",},
        [3389] = {5002,"主君","私と関羽の兄者で行く。すぐに戻って来ましょう！","私と関羽兄さんで行くわ。すぐ戻るから、見てなさい！","張遼",},
        [3390] = {5003,"関羽","顔良の首です、お納め下され。(そう言うと、馬の首の下に括り付けていた顔良の首級を曹操に献じる。)","顔良の首です、お納め下され。(そう言うと、馬の首の下に括り付けていた顔良の首級を曹操に献じる。)","曹操",},
        [3391] = {5003,"曹操","将軍はまさに神の如しだな！","将軍はまさに神の如しだな！","関羽",},
        [3392] = {5003,"関羽","敵の大軍に飛び込んで将軍の首を取ってくるくらい、袋の中から物を取り出すよりたやすいことです。","敵の大軍に飛び込んで将軍の首を取ってくるくらい、袋の中から物を取り出すよりたやすいことです。","曹操",},
        [3393] = {5004,"袁紹","我が大将、顔良を斬ったのは何者だ?","我が大将、顔良を斬ったのは何者だ?","",},
        [3394] = {5004,"沮授","真っ赤な顔に長い髭で大刀を振るう。劉備の弟分、関羽に違いありません。","真っ赤な顔に長い髭で大刀を振るう。劉備の弟分、関羽に違いありません。","袁紹",},
        [3395] = {5004,"袁紹","貴様の弟が我が大将を斬ったのだぞ。内通していたに違いない。その報いは受けてもらうぞ！","貴様の弟が我が大将を斬ったのだぞ。内通していたに違いない。その報いは受けてもらうぞ！","沮授",},
        [3396] = {5004,"劉備","一方の言うことだけを聞いて、今までの交情を絶ってしまわれるのですか?","一方の言うことだけを聞いて、今までの交情を絶ってしまわれるのですか?","袁紹",},
        [3397] = {5004,"劉備","徐州で別れて以来、義弟の関羽の生死は知りませぬ。","徐州で別れて以来、義弟の関羽の生死は知りませぬ。","袁紹",},
        [3398] = {5004,"劉備","天下に容貌の似た者はたくさんいます。真っ赤な顔に長い髭だからといって、それが関羽とは限りますまい?袁紹殿はお分かりにならぬか?","天下に容貌の似た者はたくさんいます。真っ赤な顔に長い髭だからといって、それが関羽とは限りますまい?袁紹殿はお分かりにならぬか?","袁紹",},
        [3399] = {5004,"袁紹","むむむ……確かに。沮授！お前のせいで、危うく罪のない者を殺すところだったぞ。","むむむ……確かに。沮授！お前のせいで、危うく罪のない者を殺すところだったぞ。","劉備",},
        [3400] = {5005,"文醜","顔良は俺の兄弟も同然。曹操めに殺され、この恨みを晴らさずにいられようか?","顔良は俺の兄弟も同然。曹操めに殺され、この恨みを晴らさずにいられようか?","",},
        [3401] = {5005,"袁紹","お前以外に顔良の仇を討てる者はおらぬ。十万の兵を与えよう。黄河を渡り、曹操めを追撃せよ！","お前以外に顔良の仇を討てる者はおらぬ。十万の兵を与えよう。黄河を渡り、曹操めを追撃せよ！","文醜",},
        [3402] = {5005,"沮授","なりませぬ。今は延津に残り、官渡に兵を分けるのが良策です。軽々しく黄河を渡れば、何か変事があった時、全員が帰れなくなります。","なりませぬ。今は延津に残り、官渡に兵を分けるのが良策です。軽々しく黄河を渡れば、何か変事があった時、全員が帰れなくなります。","袁紹",},
        [3403] = {5005,"袁紹","お前は全軍の士気を削ぎ、いたずらに日を引き延ばして大事を妨げるつもりか！兵は拙速を貴ぶと聞いたことはないのか?","お前は全軍の士気を削ぎ、いたずらに日を引き延ばして大事を妨げるつもりか！兵は拙速を貴ぶと聞いたことはないのか?","沮授",},
        [3404] = {5005,"沮授","上に立つ者は志ばかり大きく、下は功を焦るばかりだ。","上に立つ者は志ばかり大きく、下は功を焦るばかりだ。","袁紹",},
        [3405] = {5005,"沮授","悠々たる黄河よ、私はもう生きて戻れぬだろう！(言い終わると、扉を乱暴に閉めて出て行く。)","悠々たる黄河よ、私はもう生きて戻れぬだろう！(言い終わると、扉を乱暴に閉めて出て行く。)","袁紹",},
        [3406] = {5005,"劉備","私は大恩を受けながら、報いることができておりません。文醜将軍と同行させていただきたい。","私は大恩を受けながら、報いることができておりません。文醜将軍と同行させていただきたい。","袁紹",},
        [3407] = {5005,"劉備","一つは袁紹殿の徳に報いるため、もう一つは関羽の消息を探るため。","一つは袁紹殿の徳に報いるため、もう一つは関羽の消息を探るため。","袁紹",},
        [3408] = {5005,"袁紹","よし、文醜は七万の軍を率いて先に行け。劉備殿は三万を率いて後から続くのだ。","よし、文醜は七万の軍を率いて先に行け。劉備殿は三万を率いて後から続くのだ。","劉備",},
        [3409] = {5006,"曹操","文醜は河北の名将だ。誰か生け捕りにできる者はおらぬか?","文醜は河北の名将だ。誰か生け捕りにできる者はおらぬか?","",},
        [3410] = {5006,"主君","顔良も斬っているのです、文醜など恐れません。私と関羽の兄者で、すぐに行って戻ってきましょう。","顔良も斬っているのです、文醜など恐れません。私と関羽兄さんで行って、すぐ戻ってきましょう。","曹操",},
        [3411] = {5006,"関羽","揚威将軍、行くぞ！","揚威将軍、行くぞ！","プレイヤー",},
        [3412] = {5007,"関羽","これが文醜の首です、お納め下され。(そう言うと、馬の首の下に括り付けていた文醜の首級を曹操に献じる。)","これが文醜の首です、お納め下され。(そう言うと、馬の首の下に括り付けていた文醜の首級を曹操に献じる。)","",},
        [3413] = {5007,"曹操","袁紹の二人の大将を続けざまに斬るとは。関羽と揚威将軍は当世の英雄だな。","袁紹の二人の大将を続けざまに斬るとは。関羽と揚威将軍は当世の英雄だな。","関羽",},
        [3414] = {5008,"袁紹","今度は関羽が文醜まで殺したぞ。この大耳賊め、なぜこんなことを！","今度は関羽が文醜まで殺したぞ。この大耳賊め、なぜこんなことを！","劉備",},
        [3415] = {5008,"劉備","私に何の罪があると?","私に何の罪があると?","袁紹",},
        [3416] = {5008,"袁紹","弟分を使って、また一人わしの大将を殺した。罪がないなどとなぜ言える?","弟分を使って、また一人わしの大将を殺した。罪がないなどとなぜ言える?","劉備",},
        [3417] = {5008,"劉備","死ぬ前に一言だけよろしいか。曹操は普段から私を警戒しています。","死ぬ前に一言だけよろしいか。曹操は普段から私を警戒しています。","袁紹",},
        [3418] = {5008,"劉備","今、私が袁紹殿のところにいるのを知り、私が袁紹殿を助けるのを恐れて、関羽に二人の将軍を殺させたのです。","今、私が袁紹殿のところにいるのを知り、私が袁紹殿を助けるのを恐れて、関羽に二人の将軍を殺させたのです。","袁紹",},
        [3419] = {5008,"劉備","袁紹殿が知れば怒るのは当然ですから、袁紹殿の手で私を殺させようとしたのです。よくお考えください。","袁紹殿が知れば怒るのは当然ですから、袁紹殿の手で私を殺させようとしたのです。よくお考えください。","袁紹",},
        [3420] = {5008,"袁紹","む……確かに劉備の言うことはもっともだ。お前たち、わしの賢君の名を害するところだったぞ。","む……確かに劉備の言うことはもっともだ。お前たち、わしの賢君の名を害するところだったぞ。","劉備",},
        [3421] = {5008,"劉備","袁紹殿の寛大さに、御恩をお返しすることもできませんが、腹心に密書を持たせて、関羽のところへ遣わしたく存じます。","袁紹殿の寛大さに、御恩をお返しすることもできませんが、腹心に密書を持たせて、関羽のところへ遣わしたく存じます。","袁紹",},
        [3422] = {5008,"劉備","私の消息を知らせれば、関羽は夜を徹して駆け付け、袁紹殿を補佐するでしょう。","私の消息を知らせれば、関羽は夜を徹して駆け付け、袁紹殿を補佐するでしょう。","袁紹",},
        [3423] = {5008,"劉備","共に曹操を誅し、顔良、文醜両将軍の仇を討つのです。いかがですか?","共に曹操を誅し、顔良、文醜両将軍の仇を討つのです。いかがですか?","袁紹",},
        [3424] = {5008,"袁紹","うむ！関羽を得られれば、顔良、文醜の十倍以上だ。","うむ！関羽を得られれば、顔良、文醜の十倍以上だ。","劉備",},
        [3425] = {5008,"劉備","すぐに書簡を書き、使者に持たせましょう。","すぐに書簡を書き、使者に持たせましょう。","袁紹",},
        [3426] = {5101,"張遼","関羽殿、陣中で劉備殿の消息を得たそうですな。お祝いを述べに参った。","関羽殿、陣中で劉備殿の消息を得たそうですな。お祝いを述べに参った。","関羽",},
        [3427] = {5101,"関羽","いや、喜ぶにはまだ早い。ご無事はわかったが、まだ会えておらぬのだ。","いや、喜ぶにはまだ早い。ご無事はわかったが、まだ会えておらぬのだ。","張遼",},
        [3428] = {5101,"張遼","関羽殿と劉備殿の交わりは、俺と関羽殿に比べてどうかな?","関羽殿と劉備殿の交わりは、俺と関羽殿に比べてどうかな?","関羽",},
        [3429] = {5101,"関羽","わしと張遼殿は親友だ。だが劉備の兄者とは親友以上に兄弟、かつ君臣の間柄だ。同列には語れまい。","わしと張遼殿は親友だ。だが劉備の兄者とは親友以上に兄弟、かつ君臣の間柄だ。同列には語れまい。","張遼",},
        [3430] = {5101,"張遼","今、劉備殿は河北におる。そちらへ行かれる気か?","今、劉備殿は河北におる。そちらへ行かれる気か?","関羽",},
        [3431] = {5101,"関羽","昔の誓いに背くわけにはいかぬ！張遼殿、丞相へ言付けを頼む。","昔の誓いに背くわけにはいかぬ！張遼殿、丞相へ言付けを頼む。","張遼",},
        [3432] = {5102,"曹操","関羽、何をそんなに急ぐのだ?","関羽、何をそんなに急ぐのだ?","関羽",},
        [3433] = {5102,"関羽","前に丞相にも申し上げたはず。我が君が河北におられるとわかったからには、急いで参らねばなりません。","前に丞相にも申し上げたはず。我が君が河北におられるとわかったからには、急いで参らねばなりません。","曹操",},
        [3434] = {5102,"関羽","何度もお屋敷をお訪ねしましたが、お会いできませんでしたので、書状でお別れを申し上げました。","何度もお屋敷をお訪ねしましたが、お会いできませんでしたので、書状でお別れを申し上げました。","曹操",},
        [3435] = {5102,"関羽","戴いた官位や金品は丞相にお返しします。かつてのお言葉をお忘れにならぬよう。","戴いた官位や金品は丞相にお返しします。かつてのお言葉をお忘れにならぬよう。","曹操",},
        [3436] = {5102,"曹操","わしは天下の信を得ねばならぬ。前言を翻すことなどできん。","わしは天下の信を得ねばならぬ。前言を翻すことなどできん。","関羽",},
        [3437] = {5102,"曹操","将軍が途中で困らぬよう、路銀を受け取ってもらいたい。(そう言うと、盆にのせた黄金を差し出す。)","将軍が途中で困らぬよう、路銀を受け取ってもらいたい。(そう言うと、盆にのせた黄金を差し出す。)","関羽",},
        [3438] = {5102,"関羽","いや、これまでに賜った恩賞で充分です。その路銀は将兵に分けてやってください。","いや、これまでに賜った恩賞で充分です。その路銀は将兵に分けてやってください。","曹操",},
        [3439] = {5102,"曹操","大きな功に報いるには、ほんのわずかだぞ。なぜ辞退するのだ?","大きな功に報いるには、ほんのわずかだぞ。なぜ辞退するのだ?","関羽",},
        [3440] = {5102,"関羽","いいえ、わずかな労です。歯牙に掛けるほどでもありませぬ。","いいえ、わずかな労です。歯牙に掛けるほどでもありませぬ。","曹操",},
        [3441] = {5102,"曹操","関羽は天下の義士だ。わしに運がなく、引き留められぬのは無念よ！この錦の袍で、少しばかりの気持とさせてくれ。","関羽は天下の義士だ。わしに運がなく、引き留められぬのは無念よ！この錦の袍で、少しばかりの気持とさせてくれ。","関羽",},
        [3442] = {5102,"関羽","ありがたく賜ります。いつかお会いすることもあるでしょう。","ありがたく賜ります。いつかお会いすることもあるでしょう。","曹操",},
        [3443] = {5103,"孔秀","将軍はどこへ行かれる?","将軍はどこへ行かれる?","関羽",},
        [3444] = {5103,"関羽","丞相の下を辞し、河北へ兄者を訪ねるところだ。","丞相の下を辞し、河北へ兄者を訪ねるところだ。","孔秀",},
        [3445] = {5103,"孔秀","河北の袁紹は、丞相の敵だ。将軍がここを通られるなら、丞相の手形をお持ちでしょうな?","河北の袁紹は、丞相の敵だ。将軍がここを通られるなら、丞相の手形をお持ちでしょうな?","関羽",},
        [3446] = {5103,"関羽","慌ただしい出立で、頂いておらぬ。","慌ただしい出立で、頂いておらぬ。","孔秀",},
        [3447] = {5103,"孔秀","手形がないなら、使者を出して丞相にお伺いを立てねば、通せませぬ。","手形がないなら、使者を出して丞相にお伺いを立てねば、通せませぬ。","関羽",},
        [3448] = {5103,"関羽","伺いを立てていては、わしの行程に遅れが出る。","伺いを立てていては、わしの行程に遅れが出る。","孔秀",},
        [3449] = {5103,"孔秀","法に定められております。そうしないわけには参りませぬ。","法に定められております。そうしないわけには参りませぬ。","関羽",},
        [3450] = {5103,"関羽","ここを通さないつもりか?","ここを通さないつもりか?","孔秀",},
