        [3498] = {5110,"張遼","関羽殿、これからどこへ向かわれる?","関羽殿、これからどこへ向かわれる?","夏侯惇",},
        [3499] = {5110,"関羽","兄者はもう袁紹のところにはおらぬと聞いた。なれば天下を尋ね回るしかない。","兄者はもう袁紹のところにはおらぬと聞いた。なれば天下を尋ね回るしかない。","張遼",},
        [3500] = {5110,"張遼","劉備殿の行方が分からぬなら、しばらく丞相のところへ戻られるのはいかがかな?","劉備殿の行方が分からぬなら、しばらく丞相のところへ戻られるのはいかがかな?","関羽",},
