        [3543] = {5303,"周倉","それから部下を全員投降させて、山寨を占領しました。","それから部下を全員投降させて、山寨を占領しました。","劉備",},
        [3544] = {5303,"周倉","俺もその武将と闘ったんですが、あっさり返り討ちにあって、槍を三回浴びたんです。だから知らせに来ました。","俺もその武将と闘ったんですが、あっさり返り討ちにあって、槍を三回浴びたんです。だから知らせに来ました。","劉備",},
        [3545] = {5303,"劉備","その者はどんな姿だった?名はなんという?","その者はどんな姿だった?名はなんという?","周倉",},
        [3546] = {5303,"周倉","ものすごく頑健でした。名前は分からないや。","ものすごく頑健でした。名前は分からないや。","劉備",},
        [3547] = {5303,"関羽","では、急いで見に行ってみよう。","では、急いで見に行ってみよう。","劉備",},
        [3548] = {5303,"主君","私も同行します！","私も同行します！","劉備",},
        [3549] = {5304,"劉備","お前は、趙雲ではないか?","お前は、趙雲ではないか?","",},
        [3550] = {5304,"趙雲","なんと、ここでお会いするとは……劉備殿とお別れした後、公孫瓚は結局戦いに敗れて自刃してしまいました。","なんと、ここでお会いするとは……劉備殿とお別れした後、公孫瓚は結局戦いに敗れて自刃してしまいました。","劉備",},
        [3551] = {5304,"趙雲","袁紹からは何度も招かれましたが、奴は人を用いる器のない男ゆえ、行きませんでした。","袁紹からは何度も招かれましたが、奴は人を用いる器のない男ゆえ、行きませんでした。","劉備",},
        [3552] = {5304,"趙雲","その後、徐州の劉備殿のところへ行こうと思いましたが、","その後、徐州の劉備殿のところへ行こうと思いましたが、","劉備",},
        [3553] = {5304,"趙雲","徐州が攻め落とされ、関羽殿も曹操に帰順し、劉備殿も袁紹のところにいると聞いたのです。","徐州が攻め落とされ、関羽殿も曹操に帰順し、劉備殿も袁紹のところにいると聞いたのです。","劉備",},
        [3554] = {5304,"趙雲","劉備殿のもとに馳せ参じて、袁紹に咎められてはいけないと、あちこち流浪しておりました。","劉備殿のもとに馳せ参じて、袁紹に咎められてはいけないと、あちこち流浪しておりました。","劉備",},
        [3555] = {5304,"趙雲","偶然この場所を通りかかり、裴元紹が私の馬を奪おうとしたので、これを斬って、この場所に落ち着こうかと。","偶然この場所を通りかかり、裴元紹が私の馬を奪おうとしたので、これを斬って、この場所に落ち着こうかと。","劉備",},
        [3556] = {5304,"趙雲","張飛殿が古城にいると聞いたので、訪ねていくつもりでした。本当だろうかと思っていましたが……","張飛殿が古城にいると聞いたので、訪ねていくつもりでした。本当だろうかと思っていましたが……","劉備",},
        [3557] = {5304,"趙雲","いま幸いにして劉備殿にお会いできました！","いま幸いにして劉備殿にお会いできました！","劉備",},
        [3558] = {5304,"劉備","初めてお前に会った時から、別れがたいと感じていた。会えて良かったぞ。","初めてお前に会った時から、別れがたいと感じていた。会えて良かったぞ。","趙雲",},
        [3559] = {5304,"趙雲","この趙雲、あちらこちらで主君を探してきましたが、劉備殿のようなお方は他におりません。","この趙雲、あちらこちらで主君を探してきましたが、劉備殿のようなお方は他におりません。","劉備",},
        [3560] = {5304,"趙雲","お仕えできれば、肝脳地に塗るとも、悔いはありません。","お仕えできれば、肝脳地に塗るとも、悔いはありません。","劉備",},
        [3561] = {5305,"主君","これで、関羽と張飛の兄者とも出会えましたし。","これで、関羽兄さん、張飛兄さんとも出会えましたね。","劉備",},
        [3562] = {5305,"主君","それに、周倉、廖化、関平、趙雲将軍も来てくれました。","それに、周倉、廖化、関平、趙雲将軍も来てくれました。","劉備",},
        [3563] = {5305,"主君","これだけの助けがあれば、きっと乱世を平定できるでしょう。","これだけの助けがあれば、きっと乱世を平定できるでしょう。","劉備",},
        [3564] = {5305,"主君","私はしばらくお別れして、呉を回ってきます。","私はしばらくお別れして、呉を回ってきます。","劉備",},
        [3565] = {5305,"劉備","揚威将軍、体を大切にな。また会おう！","揚威将軍、体を大切にな。また会おう！","プレイヤー",},
        [3566] = {5401,"孫策","許貢が密かに丞相に書簡を送ったと聞いた。","許貢が密かに丞相に書簡を送ったと聞いた。","",},
        [3567] = {5401,"孫策","俺の勇猛ぶりが項羽に似ていると書いたらしい。","俺の勇猛ぶりが項羽に似ていると書いたらしい。","",},
        [3568] = {5401,"孫策","朝廷は恩寵を与えて都に召喚したほうがよい。外に置いておけば、災いになると。","朝廷は恩寵を与えて都に召喚したほうがよい。外に置いておけば、災いになると。","",},
        [3569] = {5401,"孫策","よくも俺を死地に追いやろうとしたな！","よくも俺を死地に追いやろうとしたな！","",},
        [3570] = {5401,"孫策","許貢一家を皆殺しにしろ！","許貢一家を皆殺しにしろ！","",},
        [3571] = {5402,"孫策","貴様ら、何者だ?","貴様ら、何者だ?","",},
        [3572] = {5402,"許貢の食客","我らは許貢の食客だ。主人のために仇討ちに参った！","我らは許貢の食客だ。主人のために仇討ちに参った！","孫策",},
        [3573] = {5402,"孫策","馬鹿め。賊を殺せ！","馬鹿め。賊を殺せ！","許貢の食客",},
        [3574] = {5402,"程普","孫策殿、お助けに参った！","孫策殿、お助けに参った！","孫策",},
        [3575] = {5403,"孫策","華佗先生はおられるか?","華佗先生はおられるか?","華佗の従者",},
        [3576] = {5403,"華佗の従者","先生は中原に出かけておられます。呉には弟子が何人か残っているだけです。","先生は中原に出かけておられます。呉には弟子が何人か残っているだけです。","孫策",},
        [3577] = {5403,"孫策","では、仕方ない。早く傷を見てくれ。","では、仕方ない。早く傷を見てくれ。","華佗の従者",},
        [3578] = {5403,"華佗の従者","矢に毒が塗ってあったようで、それが骨まで届いています。","矢に毒が塗ってあったようで、それが骨まで届いています。","孫策",},
        [3579] = {5403,"華佗の従者","治すには、百日は静養が必要です。激しく怒ったりして刺激すると、傷が治りにくくなります。","治すには、百日は静養が必要です。激しく怒ったりして刺激すると、傷が治りにくくなります。","孫策",},
        [3580] = {5403,"孫策","百日?冗談ではない！すぐにでも治したいのに！","百日?冗談ではない！すぐにでも治したいのに！","華佗の従者",},
        [3581] = {5404,"陳震","このたび袁紹様の使者としてやって参りました。袁紹様は呉と同盟を結び、南北呼応して、共に曹操を攻めたいとお考えです。","このたび袁紹様の使者としてやって参りました。袁紹様は呉と同盟を結び、南北呼応して、共に曹操を攻めたいとお考えです。","孫策",},
        [3582] = {5404,"孫策","はからずも同じことを考えていたとは。俺は許昌を取りたい。","はからずも同じことを考えていたとは。俺は許昌を取りたい。","陳震",},
        [3583] = {5404,"孫策","さあさあ、城楼へ上がってくれ。宴を楽しもう！","さあさあ、城楼へ上がってくれ。宴を楽しもう！","陳震",},
        [3584] = {5405,"孫策","宴に来た客や諸将がひそひそ話をして、続々と楼台を降りていった。何事だ?","宴に来た客や諸将がひそひそ話をして、続々と楼台を降りていった。何事だ?","張昭",},
        [3585] = {5405,"張昭","とある道士が、楼の下を通ったので、諸将が礼拝をしようとしているのです。","とある道士が、楼の下を通ったので、諸将が礼拝をしようとしているのです。","孫策",},
        [3586] = {5405,"孫策","どんな妖術使いだ?早く捕まえてこい！","どんな妖術使いだ?早く捕まえてこい！","張昭",},
        [3587] = {5405,"張昭","この者は姓は于、名は吉といい、東方に寓居し、呉郡・会稽一帯を行き来しています。","この者は姓は于、名は吉といい、東方に寓居し、呉郡・会稽一帯を行き来しています。","孫策",},
        [3588] = {5405,"張昭","護符の灰を溶かした水を使い、万病を治すそうで、効かぬものはないとか。","護符の灰を溶かした水を使い、万病を治すそうで、効かぬものはないとか。","孫策",},
        [3589] = {5405,"張昭","当世では神仙と呼ばれています。侮ってはなりません。","当世では神仙と呼ばれています。侮ってはなりません。","孫策",},
        [3590] = {5405,"孫策","バカな、早く捕らえてこい！命令に背くなら斬るぞ！","バカな、早く捕らえてこい！命令に背くなら斬るぞ！","張昭",},
        [3591] = {5406,"孫策","腐れ道士め、人心を惑わすとは！","腐れ道士め、人心を惑わすとは！","于吉",},
        [3592] = {5406,"于吉","貧道は琅琊宮の道士だが、順帝の御代、山に入って薬草を採っていたところ、曲陽泉のほとりで神書を手に入れたのだ。","貧道は琅琊宮の道士だが、順帝の御代、山に入って薬草を採っていたところ、曲陽泉のほとりで神書を手に入れたのだ。","孫策",},
        [3593] = {5406,"于吉","その本の名は『太平青領道』と言う。","その本の名は『太平青領道』と言う。","孫策",},
        [3594] = {5406,"于吉","百巻余りあり、すべて病気を治す方術が書かれている。","百巻余りあり、すべて病気を治す方術が書かれている。","孫策",},
        [3595] = {5406,"于吉","貧道はこれを得てより、ただ天に代わって徳を施し、万人を救おうと務めておるだけ。","貧道はこれを得てより、ただ天に代わって徳を施し、万人を救おうと務めておるだけ。","孫策",},
        [3596] = {5406,"于吉","人々から毫も物は受け取っておらぬ。これで人心を惑わしたなどといえようか?","人々から毫も物は受け取っておらぬ。これで人心を惑わしたなどといえようか?","孫策",},
        [3597] = {5406,"孫策","嘘をつけ。ならば服や食べ物はどうやって手に入れているのだ。","嘘をつけ。ならば服や食べ物はどうやって手に入れているのだ。","于吉",},
        [3598] = {5406,"孫策","お前など、黄巾賊の張角と同じだ。今誅殺せねば、後の憂いとなる！","お前など、黄巾賊の張角と同じだ。今誅殺せねば、後の憂いとなる！","于吉",},
        [3599] = {5406,"張昭","おやめください！于吉殿は江東に数十年もいて、罪を犯したことはありません。どうぞお慈悲を。","おやめください！于吉殿は江東に数十年もいて、罪を犯したことはありません。どうぞお慈悲を。","孫策",},
        [3600] = {5406,"孫策","こんな妖術使い、豚や犬を殺すのと何の違いがある！","こんな妖術使い、豚や犬を殺すのと何の違いがある！","張昭",},
        [3601] = {5406,"陳震","孫策殿、どうか慎重にお考えを。","孫策殿、どうか慎重にお考えを。","孫策",},
        [3602] = {5406,"孫策","ええい、うるさい！誰か、こいつを牢に入れろ。","ええい、うるさい！誰か、こいつを牢に入れろ。","陳震",},
        [3603] = {5407,"孫策","お前の妖術は風を起こし雨を降らせるそうだな。今は日照りだ。恵みの雨を降らせることができれば、死を免じてやろう。","お前の妖術は風を起こし雨を降らせるそうだな。今は日照りだ。恵みの雨を降らせることができれば、死を免じてやろう。","于吉",},
        [3604] = {5407,"孫策","午の刻までに降らねば、即刻斬る。","午の刻までに降らねば、即刻斬る。","于吉",},
        [3605] = {5407,"于吉","承知した。すぐに祭壇を設けて、祈祷を行おう。","承知した。すぐに祭壇を設けて、祈祷を行おう。","孫策",},
        [3606] = {5407,"主君","誰が祈祷したのだ。街中は河になって、渓流には水が満ち、田畑は水没したぞ。","誰が祈祷したの?街中は河になって、渓流には水が満ち、田畑は水没したわよ。","于吉",},
        [3607] = {5407,"孫策","俺は恵みの雨を降らせよと言ったのだ。江東を水没させるとは、謀反を起こす気か?","俺は恵みの雨を降らせよと言ったのだ。江東を水没させるとは、謀反を起こす気か?","于吉",},
        [3608] = {5407,"于吉","わはははっ！私は雨を呼び、風を起こせるのだ。貴様らごときでは、どうすることもできぬわ！","わはははっ！私は雨を呼び、風を起こせるのだ。貴様らごときでは、どうすることもできぬわ！","孫策",},
        [3609] = {5407,"主君","やはり妖術使いか。乱世をさらに乱すつもりだな?この揚威将軍が抹殺してやる！","やっぱり妖術使いだったのね！乱世をさらに乱すつもりなら、この揚威将軍が成敗してやるわ！","于吉",},
        [3610] = {5408,"孫策","おかげで助かった。これで江東は水害から救われたぞ。","おかげで助かった。これで江東は水害から救われたぞ。","プレイヤー",},
        [3611] = {5408,"主君","礼には及びません。江東を周遊している間、この道士が護符の水で病気を治すなどと、人心を惑わすのを以前より聞いていました。","礼には及びません。江東を周遊している間、この道士が護符の水で病気を治すなどと、人心を惑わすのを以前より聞いていました。","孫策",},
        [3612] = {5408,"主君","今日はまた方術を使い、江東の人々を危険に晒した。私は民のために害を除いたまでです。","今日はまた方術を使い、江東の人々を危険に晒した。私は民のために害を除いたまでですわ。","孫策",},
        [3613] = {5408,"孫策","揚威将軍の力添え、感謝にたえない。","揚威将軍の力添え、感謝にたえない。","プレイヤー",},
        [3614] = {5501,"張昭","昨晩は嵐でしたが、明け方になってみると、于吉の首がなくなっていたのです！","昨晩は嵐でしたが、明け方になってみると、于吉の首がなくなっていたのです！","孫策",},
        [3615] = {5501,"孫策","妖術使いめ、やはり人ではなかったか！","妖術使いめ、やはり人ではなかったか！","張昭",},
        [3616] = {5501,"張昭","于吉の妖術が、この後も孫策殿に危害を加えないか心配です。","于吉の妖術が、この後も孫策殿に危害を加えないか心配です。","孫策",},
        [3617] = {5501,"孫策","俺は幼い頃から父と共に戦に出、山ほど人を殺めてきた。今さら奴の妖術など怖がると思うか?","俺は幼い頃から父と共に戦に出、山ほど人を殺めてきた。今さら奴の妖術など怖がると思うか?","張昭",},
        [3618] = {5502,"孫策","むぅ……最近どうも気持ちが塞ぐ……","むぅ……最近どうも気持ちが塞ぐ……","大喬",},
        [3619] = {5502,"大喬","孫策さま、何かお悩みでしたら、わたしに打ち明けてください。","孫策さま、何かお悩みでしたら、わたしに打ち明けてください。","孫策",},
        [3620] = {5502,"孫策","あの于吉とかいう妖しい道士を斬ってから、心が落ち着かぬのだ。","あの于吉とかいう妖しい道士を斬ってから、心が落ち着かぬのだ。","大喬",},
        [3621] = {5502,"孫策","昨晩、灯火の影にあの道士がいて、寝台の前に立った。廟で香を焚いた時も、ヤツの顔が見えた。","昨晩、灯火の影にあの道士がいて、寝台の前に立った。廟で香を焚いた時も、ヤツの顔が見えた。","大喬",},
        [3622] = {5502,"孫策","頭の中にも絶えず奴が現れるのだ。心を乱さずにいられるか！","頭の中にも絶えず奴が現れるのだ。心を乱さずにいられるか！","大喬",},
        [3623] = {5502,"大喬","孫策さまはきっとお疲れなのです。しばらく静養なされば、きっと良くなりますわ。","孫策さまはきっとお疲れなのです。しばらく静養なされば、きっと良くなりますわ。","孫策",},
        [3624] = {5502,"孫策","俺がせっかちなのは知っているだろう。休む必要などない。戦場に出るのが一番の休養なのだ。","俺がせっかちなのは知っているだろう。休む必要などない。戦場に出るのが一番の休養なのだ。","大喬",},
        [3625] = {5502,"孫策","軍を起こすぞ。城外に陣を張り、諸将を集め、袁紹を助けて曹操を挟み撃ちにする策を練る。","軍を起こすぞ。城外に陣を張り、諸将を集め、袁紹を助けて曹操を挟み撃ちにする策を練る。","大喬",},
        [3626] = {5502,"大喬","むやみに動いてはだめです。お体の具合が良くなってから兵を挙げても、遅くはないです。","むやみに動いてはだめです。お体の具合が良くなってから兵を挙げても、遅くはないです。","孫策",},
        [3627] = {5502,"孫策","すまぬ、もう決めたのだ。","すまぬ、もう決めたのだ。","大喬",},
        [3628] = {5503,"孫策","于吉め、早く出てこい！","于吉め、早く出てこい！","",},
        [3629] = {5503,"孫策","早く出てこい。我が軍営で騒ぎを起こしてよいと思っているのか。","早く出てこい。我が軍営で騒ぎを起こしてよいと思っているのか。","",},
        [3630] = {5503,"孫策","わあああああ！","わあああああ！","",},
        [3631] = {5503,"主君","孫策兄、天幕の中でなぜ一人騒いでいるんです?","孫策兄さん、天幕の中でなぜ一人騒いでいるんです?","孫策",},
        [3632] = {5503,"孫策","見えなかったのか?あの于吉という道士が髪を振り乱して俺の天幕の中にいる！","見えなかったのか?あの于吉という道士が髪を振り乱して俺の天幕の中にいる！","プレイヤー",},
        [3633] = {5503,"主君","ここに于吉が来るものですか。あの道士は、この前私が斬ったではないですか。","ここに于吉が来るものですか。あの道士は、この前私が斬ったではないですか。","孫策",},
        [3634] = {5503,"主君","きっと疲れているんです。数か月前に許貢の食客に襲われた傷もまだ癒えていないし。気にしすぎて幻覚を見たんですよ。","きっと疲れているんです。数か月前に許貢の食客に襲われた傷もまだ癒えていないし。気にしすぎて幻覚を見たんですよ。","孫策",},
        [3635] = {5503,"主君","大丈夫ですから！","大丈夫ですから！","孫策",},
        [3636] = {5503,"孫策","俺はなぜこんなに憔悴しているのだ！(主人公の言葉を聞いた後、鏡に自分を映す。)","俺はなぜこんなに憔悴しているのだ！(主人公の言葉を聞いた後、鏡に自分を映す。)","プレイヤー",},
        [3637] = {5503,"孫策","見ろ！あの于吉が俺の鏡の中に立っているではないか。","見ろ！あの于吉が俺の鏡の中に立っているではないか。","プレイヤー",},
        [3638] = {5503,"孫策","うわあああっ！(孫策は鏡を叩いて叫ぶ。傷口が開き、地面に倒れる。)","うわあああっ！(孫策は鏡を叩いて叫ぶ。傷口が開き、地面に倒れる。)","プレイヤー",},
        [3639] = {5503,"主君","大丈夫ですか?！孫策兄、目を醒ましてください！","大丈夫ですか?！孫策兄さん、目を醒ましてください！","孫策",},
        [3640] = {5504,"孫策","天下は乱れている。だが、わが呉の勢力と三江の要害をもってすれば、この先希望はある。","天下は乱れている。だが、わが呉の勢力と三江の要害をもってすれば、この先希望はある。","孫権",},
        [3641] = {5504,"孫策","弟よ、江東の軍を率いて戦に勝ち、天下の覇権を争う能力では、お前は俺に及ばない。","弟よ、江東の軍を率いて戦に勝ち、天下の覇権を争う能力では、お前は俺に及ばない。","孫権",},
        [3642] = {5504,"孫策","だが、才能のある者を登用し、江東の領土を守っていく能力なら、お前の方が上だ。","だが、才能のある者を登用し、江東の領土を守っていく能力なら、お前の方が上だ。","孫権",},
        [3643] = {5504,"孫策","国の礎を築いた父と兄の労苦をしかと心に留め、この孫呉の舵を取れ！","国の礎を築いた父と兄の労苦をしかと心に留め、この孫呉の舵を取れ！","孫権",},
        [3644] = {5504,"孫権","俺はまだ年若く、任に堪えません。どうしたら良いのでしょう?","俺はまだ年若く、任に堪えません。どうしたら良いのでしょう?","孫策",},
        [3645] = {5504,"孫策","弟よ、お前の才は俺よりずっと上だ。大事を任せるに足る。内政で困ったら、張昭に聞くがよい。","弟よ、お前の才は俺よりずっと上だ。大事を任せるに足る。内政で困ったら、張昭に聞くがよい。","孫権",},
        [3646] = {5504,"孫策","外政で困ったら、周瑜に聞くのだ。","外政で困ったら、周瑜に聞くのだ。","孫権",},
        [3647] = {5504,"孫策","揚威将軍はいるか?","揚威将軍はいるか?","孫権",},
        [3648] = {5504,"主君","はい、ここに。","はい、ここに。","孫策",},
        [3649] = {5504,"孫策","俺は英雄の助けを得て幸いだった。もし今後、弟が困っていたら、どうか助けてやってほしい。","俺は英雄の助けを得て幸いだった。もし今後、弟が困っていたら、どうか助けてやってほしい。","プレイヤー",},
        [3650] = {5504,"主君","はい、天の理に背かぬことであれば、必ず全力でお助けしましょう。","はい、天の理に背かぬことであれば、必ず全力でお助けしましょう。","孫策",},
        [3651] = {5504,"孫策","それから、周瑜に伝えてくれ。平素の交情を忘れず、弟を補佐してやってくれと。(言い終わると、目を閉じて息を引き取る。)","それから、周瑜に伝えてくれ。平素の交情を忘れず、弟を補佐してやってくれと。(言い終わると、目を閉じて息を引き取る。)","プレイヤー",},
        [3652] = {5601,"孫権","父と兄の跡を継いだものの、これからどうすればよいか……","父と兄の跡を継いだものの、これからどうすればよいか……","周瑜",},
        [3653] = {5601,"周瑜","古来より、人を得る者は栄え、人を失う者は亡ぶ、と申します。","古来より、人を得る者は栄え、人を失う者は亡ぶ、と申します。","孫権",},
        [3654] = {5601,"周瑜","今大切なのは、高い見識を持った人物を補佐として求めることで、そうすれば江東は安定するでしょう。","今大切なのは、高い見識を持った人物を補佐として求めることで、そうすれば江東は安定するでしょう。","孫権",},
        [3655] = {5601,"孫権","兄の遺言だ。内政は張昭に、外政は周瑜にすべて頼るようにと。","兄の遺言だ。内政は張昭に、外政は周瑜にすべて頼るようにと。","周瑜",},
        [3656] = {5601,"周瑜","張昭は賢者で、大任を負うに十分だが、私は才なく、重責に堪えられません。ある者を補佐として、将軍に推薦したく。","張昭は賢者で、大任を負うに十分だが、私は才なく、重責に堪えられません。ある者を補佐として、将軍に推薦したく。","孫権",},
        [3657] = {5601,"孫権","何者だ?","何者だ?","周瑜",},
        [3658] = {5601,"周瑜","姓は鲁、名は粛、字は子敬。臨淮郡東川県の出身です。","姓は鲁、名は粛、字は子敬。臨淮郡東川県の出身です。","孫権",},
        [3659] = {5601,"周瑜","この者は兵法に通じ、機略に富んでいます。","この者は兵法に通じ、機略に富んでいます。","孫権",},
        [3660] = {5601,"周瑜","父を早くに無くし、母に孝行を尽くしていますが、家は裕福で、財を投じて貧者を救済しております。","父を早くに無くし、母に孝行を尽くしていますが、家は裕福で、財を投じて貧者を救済しております。","孫権",},
        [3661] = {5601,"周瑜","僕が居巣長を務めていた時、数百人で淮河を渡って、糧食に困ったことがありました。","僕が居巣長を務めていた時、数百人で淮河を渡って、糧食に困ったことがありました。","孫権",},
        [3662] = {5601,"周瑜","その時、魯粛の家で蔵二つ分の米を蓄え、各三千斛あると聞いて、助けを求めたのです。","その時、魯粛の家で蔵二つ分の米を蓄え、各三千斛あると聞いて、助けを求めたのです。","孫権",},
        [3663] = {5601,"周瑜","魯粛はすぐに蔵1つを丸ごと贈ってくれました。それほどの器量の大きさです。","魯粛はすぐに蔵1つを丸ごと贈ってくれました。それほどの器量の大きさです。","孫権",},
        [3664] = {5601,"周瑜","平素から剣術や騎馬を好み、曲阿に住んでいます。祖母が亡くなった時は東城に埋葬しました。","平素から剣術や騎馬を好み、曲阿に住んでいます。祖母が亡くなった時は東城に埋葬しました。","孫権",},
        [3665] = {5601,"周瑜","友人の劉子揚が魯粛を誘い、巣湖の鄭宝に身を寄せようとしていますが、魯粛はまだ躊躇しております。","友人の劉子揚が魯粛を誘い、巣湖の鄭宝に身を寄せようとしていますが、魯粛はまだ躊躇しております。","孫権",},
        [3666] = {5601,"周瑜","早く召し抱えたほうが良いでしょう。","早く召し抱えたほうが良いでしょう。","孫権",},
        [3667] = {5601,"孫権","それはよい。周瑜の言うとおりにしよう。","それはよい。周瑜の言うとおりにしよう。","周瑜",},
        [3668] = {5602,"周瑜","孫権殿は、あなたを慕っているようだよ。","孫権殿は、あなたを慕っているようだよ。","魯粛",},
        [3669] = {5602,"魯粛","最近、劉子揚に誘われて、巣湖へ行こうかと思っていたのですが。","最近、劉子揚に誘われて、巣湖へ行こうかと思っていたのですが。","周瑜",},
        [3670] = {5602,"周瑜","かつて、馬援は漢の光武帝に言った。今の世では、君主が臣下を選ぶだけでなく、臣下もまた君主を選ぶのだと。","かつて、馬援は漢の光武帝に言った。今の世では、君主が臣下を選ぶだけでなく、臣下もまた君主を選ぶのだと。","魯粛",},
        [3671] = {5602,"周瑜","わが孫権将軍は、賢者を礼遇し、才ある者を受入れること、世に類を見ないほどです。","わが孫権将軍は、賢者を礼遇し、才ある者を受入れること、世に類を見ないほどです。","魯粛",},
        [3672] = {5602,"周瑜","他のことなど考えず、僕と一緒に呉へ身を投じませんか。","他のことなど考えず、僕と一緒に呉へ身を投じませんか。","魯粛",},
        [3673] = {5602,"魯粛","わかりました。孫権殿に会いましょう。","わかりました。孫権殿に会いましょう。","周瑜",},
        [3674] = {5603,"孫権","今、漢王朝は危機に瀕し、世は乱れている。","今、漢王朝は危機に瀕し、世は乱れている。","魯粛",},
        [3675] = {5603,"孫権","俺は父と兄の跡を継いで、斉の桓公、晋の文公のような覇者になりたいと考えている。どうすればよいか。","俺は父と兄の跡を継いで、斉の桓公、晋の文公のような覇者になりたいと考えている。どうすればよいか。","魯粛",},
        [3676] = {5603,"魯粛","昔、漢の高祖は楚の義帝を奉じようとしましたが、項羽に妨げられてできませんでした。","昔、漢の高祖は楚の義帝を奉じようとしましたが、項羽に妨げられてできませんでした。","孫権",},
        [3677] = {5603,"魯粛","今は曹操が項羽のようなものです。おそらく将軍は桓公や文公のようにはなれません。","今は曹操が項羽のようなものです。おそらく将軍は桓公や文公のようにはなれません。","孫権",},
        [3678] = {5603,"魯粛","私が思うに漢王朝の復興はできませんし、曹操もすぐには除くことはできません。","私が思うに漢王朝の復興はできませんし、曹操もすぐには除くことはできません。","孫権",},
        [3679] = {5603,"魯粛","ですから将軍は、江東に足場を築き、天下のほころびを見極めることです。","ですから将軍は、江東に足場を築き、天下のほころびを見極めることです。","孫権",},
        [3680] = {5603,"魯粛","今、北方には難事が多い。その機に乗じて黄祖を除き、劉表を討って、長江流域を領有するのです。","今、北方には難事が多い。その機に乗じて黄祖を除き、劉表を討って、長江流域を領有するのです。","孫権",},
        [3681] = {5603,"魯粛","それから帝王を名乗り、天下を狙う。つまり、高祖の成したことをなすのです！","それから帝王を名乗り、天下を狙う。つまり、高祖の成したことをなすのです！","孫権",},
        [3682] = {5603,"孫権","まったく、そなたの言うとおりだ。はははは。","まったく、そなたの言うとおりだ。はははは。","魯粛",},
        [3683] = {5604,"魯粛","一人、孫権殿に推薦したいものがおります。","一人、孫権殿に推薦したいものがおります。","孫権",},
        [3684] = {5604,"孫権","何者だ?","何者だ?","魯粛",},
        [3685] = {5604,"魯粛","博学多才で、継母にも孝行を尽くしています。姓は诸葛、名は瑾、字は子瑜といい、琅琊郡南陽県の出身です。","博学多才で、継母にも孝行を尽くしています。姓は诸葛、名は瑾、字は子瑜といい、琅琊郡南陽県の出身です。","孫権",},
        [3686] = {5604,"孫権","よし、早く引き合わせてくれ。","よし、早く引き合わせてくれ。","魯粛",},
        [3687] = {5605,"孫権","諸葛瑾。今の天下を、そなたはどう見る?","諸葛瑾。今の天下を、そなたはどう見る?","諸葛瑾",},
        [3688] = {5605,"諸葛瑾","袁紹と誼を通じてはなりません。しばらくは曹操に従うとみせ、時機が来たら討ちましょう。","袁紹と誼を通じてはなりません。しばらくは曹操に従うとみせ、時機が来たら討ちましょう。","孫権",},
        [3689] = {5605,"孫権","ふむ。簡潔な答えだがしっかりと要を得ている。筋が通っているな。","ふむ。簡潔な答えだがしっかりと要を得ている。筋が通っているな。","諸葛瑾",},
        [3690] = {5606,"主君","私は江東を周遊しているのですが、私からも推薦したい者がおります。","私は江東を周遊しているのですが、私からも推薦したい者がおります。","孫権",},
        [3691] = {5606,"孫権","何者だ?","何者だ?","プレイヤー",},
        [3692] = {5606,"主君","姓は顧、名は雍、字は元歎。中郎、蔡邕の弟子です。寡黙で酒は飲まず、厳格で正義を重んじる人間です。","姓は顧、名は雍、字は元歎。中郎、蔡邕の弟子です。寡黙で酒は飲まず、厳格で正義を重んじる人です。","孫権",},
        [3693] = {5606,"孫権","うむ、その者を会稽郡の丞として、俺の太守の職を代行させよう。","うむ、その者を会稽郡の丞として、俺の太守の職を代行させよう。","プレイヤー",},
        [3694] = {5607,"陳震","孫策は死にましたが、孫権が後を継ぎ、深く民心を得ています。","孫策は死にましたが、孫権が後を継ぎ、深く民心を得ています。","袁紹",},
        [3695] = {5607,"陳震","曹操は孫権を討虜将軍に封じ、味方につけました。","曹操は孫権を討虜将軍に封じ、味方につけました。","袁紹",},
        [3696] = {5607,"袁紹","何だと！","何だと！","陳震",},
        [3697] = {5607,"袁紹","おのれ！冀州、青州、幽州、并州の兵七十余万を出し、許昌を攻め落とすぞ！","おのれ！冀州、青州、幽州、并州の兵七十余万を出し、許昌を攻め落とすぞ！","陳震",},
        [3698] = {5701,"沮授","我が軍は数の上で有利といえど、勇猛さでは敵軍にかないません。そして、敵軍は精鋭揃いですが、糧食は我が軍ほどありません。","我が軍は数の上で有利といえど、勇猛さでは敵軍にかないません。そして、敵軍は精鋭揃いですが、糧食は我が軍ほどありません。","袁紹",},
        [3699] = {5701,"沮授","つまり、敵軍は短期決戦には有利ですが、我が軍は、長期戦で守るのに向いています。","つまり、敵軍は短期決戦には有利ですが、我が軍は、長期戦で守るのに向いています。","袁紹",},
        [3700] = {5701,"沮授","時間を引き延ばすことができれば、敵軍は戦わずして敗れるでしょう。","時間を引き延ばすことができれば、敵軍は戦わずして敗れるでしょう。","袁紹",},
        [3701] = {5701,"袁紹","なんだと！?お前までわしの力を軽視するというのか！?田豊と同じように！","なんだと！?お前までわしの力を軽視するというのか！?田豊と同じように！","沮授",},
        [3702] = {5701,"袁紹","沮授を陣中に監禁しておけ。曹操を破った後で、田豊と共に裁いてやる！","沮授を陣中に監禁しておけ。曹操を破った後で、田豊と共に裁いてやる！","沮授",},
        [3703] = {5702,"曹操","わしは天子の前で、そなたを大将軍に封じるよう申し上げたのに、なぜ謀反を起こすのだ?","わしは天子の前で、そなたを大将軍に封じるよう申し上げたのに、なぜ謀反を起こすのだ?","袁紹",},
        [3704] = {5702,"袁紹","貴様は漢の丞相と称してはいるが、実際には逆賊だ！その罪の数々、王莽や董卓にも劣らぬ。それなのに、逆に人を謀反人呼ばわりか！","貴様は漢の丞相と称してはいるが、実際には逆賊だ！その罪の数々、王莽や董卓にも劣らぬ。それなのに、逆に人を謀反人呼ばわりか！","曹操",},
        [3705] = {5702,"曹操","今日こそ、詔を奉じて、貴様を討ってやる！","今日こそ、詔を奉じて、貴様を討ってやる！","袁紹",},
        [3706] = {5702,"袁紹","こちらこそ、玉帯の密詔を奉じて、賊を討つのだ！","こちらこそ、玉帯の密詔を奉じて、賊を討つのだ！","曹操",},
        [3707] = {5702,"曹操","張遼、出よ！","張遼、出よ！","袁紹",},
        [3708] = {5702,"袁紹","なんの！張郃、迎え討て！","なんの！張郃、迎え討て！","曹操",},
        [3709] = {5703,"袁紹","わっはっは！曹操軍は大敗し、官渡まで退却したぞ。","わっはっは！曹操軍は大敗し、官渡まで退却したぞ。","",},
        [3710] = {5703,"袁紹","三軍に命じて、官渡の砦に迫るのだ。","三軍に命じて、官渡の砦に迫るのだ。","",},
        [3711] = {5703,"審配","お待ち下さい。まず、十万の兵を出して官渡を守らせ、曹操の砦の前に土の山を築かせるのです。","お待ち下さい。まず、十万の兵を出して官渡を守らせ、曹操の砦の前に土の山を築かせるのです。","袁紹",},
        [3712] = {5703,"審配","兵士に命じ、上から砦の中に矢を放たせましょう。","兵士に命じ、上から砦の中に矢を放たせましょう。","袁紹",},
        [3713] = {5703,"審配","曹操が砦を棄てて逃げたら、我が軍はこの隘路を得て、許昌を破ることができます。","曹操が砦を棄てて逃げたら、我が軍はこの隘路を得て、許昌を破ることができます。","袁紹",},
        [3714] = {5703,"袁紹","むむ……審配の言うとおりだ。","むむ……審配の言うとおりだ。","審配",},
        [3715] = {5704,"曹操","袁紹軍は土山を築き、突撃の機会を伺っておる。さらに審配の弓矢隊が隘路を塞ぎ、前に進めぬ。","袁紹軍は土山を築き、突撃の機会を伺っておる。さらに審配の弓矢隊が隘路を塞ぎ、前に進めぬ。","",},
        [3716] = {5704,"曹操","さて、どう切り抜けたものか。","さて、どう切り抜けたものか。","",},
        [3717] = {5704,"主君","曹操兄、ご心配なく！","曹操兄さん、ご心配なく！","曹操",},
        [3718] = {5704,"主君","江東から急ぎ駆け付けました。袁紹軍を破る手伝いを致しましょう！","江東から急ぎ駆け付けました。袁紹軍を破る手伝いを致しますわ！","曹操",},
        [3719] = {5704,"曹操","おお、助かった！勝利はすべて、揚威将軍にかかっておる。","おお、助かった！勝利はすべて、揚威将軍にかかっておる。","プレイヤー",},
        [3720] = {5705,"曹操","この官渡を守るのも八月から始め、もう九月の終わりだ。軍も疲弊し、糧食も続かぬ。","この官渡を守るのも八月から始め、もう九月の終わりだ。軍も疲弊し、糧食も続かぬ。","プレイヤー",},
        [3721] = {5705,"曹操","かくなるうえは官渡を棄て、許昌へ退却しようと思う。","かくなるうえは官渡を棄て、許昌へ退却しようと思う。","プレイヤー",},
        [3722] = {5705,"主君","私は、まだ許昌へは帰れないと思いますが……荀彧殿の意見を聞いてみては。","私は、まだ許昌へ帰るべきではないと思いますけど……荀彧殿の意見を聞いてみては?","曹操",},
        [3723] = {5705,"曹操","……荀彧、お前はどう見る?","……荀彧、お前はどう見る?","プレイヤー",},
        [3724] = {5705,"荀彧","袁紹は全軍を官渡に集め、勝負を決しようとしています。我が君は、最弱の軍勢で、最強の軍勢に対抗しなければなりません。","袁紹は全軍を官渡に集め、勝負を決しようとしています。我が君は、最弱の軍勢で、最強の軍勢に対抗しなければなりません。","曹操",},
        [3725] = {5705,"荀彧","ここを制さねば、敵に乗じられ、天下の大勢が決まります。","ここを制さねば、敵に乗じられ、天下の大勢が決まります。","曹操",},
        [3726] = {5705,"荀彧","ですが袁紹軍は数が多くても、使いこなせてはいません。","ですが袁紹軍は数が多くても、使いこなせてはいません。","曹操",},
        [3727] = {5705,"荀彧","英明な我が君なら、必ず勝てることでしょう。我が軍は数では不利ですが、かつて高祖劉邦が項羽と戦ったときに比べれば、さほどではありません。","英明な我が君なら、必ず勝てることでしょう。我が軍は数では不利ですが、かつて高祖劉邦が項羽と戦ったときに比べれば、さほどではありません。","曹操",},
        [3728] = {5705,"荀彧","今はこの地を守り、袁紹を前進させないことです。","今はこの地を守り、袁紹を前進させないことです。","曹操",},
        [3729] = {5705,"荀彧","袁紹軍が窮乏してきたら、軍内の情勢も変わります。その時が奇策を用いる機会です。逃してはなりません。我が君のご明察を願います。","袁紹軍が窮乏してきたら、軍内の情勢も変わります。その時が奇策を用いる機会です。逃してはなりません。我が君のご明察を願います。","曹操",},
        [3730] = {5705,"曹操","よし、直ちに将兵に命じる。全力で死守せよ！","よし、直ちに将兵に命じる。全力で死守せよ！","荀彧",},
        [3731] = {5801,"曹操","斥候より報告だ。袁紹軍は三十里余り退いたが、大将の韓猛が糧食を携えて応援に来るらしい。","斥候より報告だ。袁紹軍は三十里余り退いたが、大将の韓猛が糧食を携えて応援に来るらしい。","荀攸",},
        [3732] = {5801,"荀攸","韓猛は匹夫の勇です。軽騎兵数千を誰か一人に率いさせ、道中を攻撃すれば、糧食を絶つことができ、袁紹軍は自ずから乱れるでしょう。","韓猛は匹夫の勇です。軽騎兵数千を誰か一人に率いさせ、道中を攻撃すれば、糧食を絶つことができ、袁紹軍は自ずから乱れるでしょう。","曹操",},
        [3733] = {5801,"曹操","そうか、誰か行くものはおらぬか?","そうか、誰か行くものはおらぬか?","荀攸",},
        [3734] = {5801,"主君","私が行きましょう！","私が行きましょう！","曹操",},
        [3735] = {5801,"曹操","よし、頼んだぞ！","よし、頼んだぞ！","プレイヤー",},
        [3736] = {5802,"袁紹","ぐぬぬ……糧食も奪われた。どうすればよい?","ぐぬぬ……糧食も奪われた。どうすればよい?","",},
        [3737] = {5802,"審配","行軍には糧食が重要です。注意深く守らねばなりません。烏巣は糧食を蓄える場所ですから、重点的に兵を置いて守るべきです。","行軍には糧食が重要です。注意深く守らねばなりません。烏巣は糧食を蓄える場所ですから、重点的に兵を置いて守るべきです。","袁紹",},
        [3738] = {5802,"袁紹","もう策は決めたぞ。お前は鄴都へ戻って補給を監督し、糧食が欠乏しないようにせよ。","もう策は決めたぞ。お前は鄴都へ戻って補給を監督し、糧食が欠乏しないようにせよ。","審配",},
        [3739] = {5802,"袁紹","大将の淳于瓊は、眭元進、韓莒子、呂威璜、趙叡らと二万の兵を率いて烏巣を守るのだ。","大将の淳于瓊は、眭元進、韓莒子、呂威璜、趙叡らと二万の兵を率いて烏巣を守るのだ。","審配",},
        [3740] = {5803,"許攸","曹操の書簡を手に入れました。","曹操の書簡を手に入れました。","袁紹",},
        [3741] = {5803,"許攸","曹操は官渡に軍を置き、長い間我が軍と対峙しています。","曹操は官渡に軍を置き、長い間我が軍と対峙しています。","袁紹",},
        [3742] = {5803,"許攸","許昌は空になっているでしょう。軍の一部を割いて許昌へ夜襲をかければ、許昌を落とし、曹操を捕えることができます。","許昌は空になっているでしょう。軍の一部を割いて許昌へ夜襲をかければ、許昌を落とし、曹操を捕えることができます。","袁紹",},
        [3743] = {5803,"許攸","今は曹操の糧食も尽きています。この機に乗じて両方から攻めるのです。","今は曹操の糧食も尽きています。この機に乗じて両方から攻めるのです。","袁紹",},
        [3744] = {5803,"袁紹","曹操には奸計が多い。その書簡も敵を誘い込む計略ではないのか。","曹操には奸計が多い。その書簡も敵を誘い込む計略ではないのか。","許攸",},
        [3745] = {5803,"許攸","今、この策を取らねば、後の害になりましょう。","今、この策を取らねば、後の害になりましょう。","袁紹",},
        [3746] = {5803,"袁紹","お前は冀州にいた頃、民間から賄賂を受け取り、子や甥にも多くの税を取り立てさせ、自分の懐に入れていただろう。","お前は冀州にいた頃、民間から賄賂を受け取り、子や甥にも多くの税を取り立てさせ、自分の懐に入れていただろう。","許攸",},
        [3747] = {5803,"袁紹","卑しい者が！どのツラ下げて、わしに策を献じるか！","卑しい者が！どのツラ下げて、わしに策を献じるか！","許攸",},
        [3748] = {5803,"袁紹","お前は曹操と旧交がある。奴から賄賂でも受け、間諜となり、我が軍を騙そうと言うのだろう！","お前は曹操と旧交がある。奴から賄賂でも受け、間諜となり、我が軍を騙そうと言うのだろう！","許攸",},
        [3749] = {5803,"袁紹","本来なら斬るべきだが、しばらくは首を預けておいてやる！とっとと下がれ、今後、謁見はならん！","本来なら斬るべきだが、しばらくは首を預けておいてやる！とっとと下がれ、今後、謁見はならん！","許攸",},
        [3750] = {5803,"許攸","なんと……忠言耳に逆う、か。こんな小物と大事をはかることなどできん！","なんと……忠言耳に逆う、か。こんな小物と大事をはかることなどできん！","袁紹",},
        [3751] = {5804,"曹操","旧友よ、こんな深夜にやって来て、どうしたのだ?","旧友よ、こんな深夜にやって来て、どうしたのだ?","許攸",},
        [3752] = {5804,"許攸","わしは袁紹に仕えていたが、何を言っても聞き入れられず、計略にも従ってくれぬ。","わしは袁紹に仕えていたが、何を言っても聞き入れられず、計略にも従ってくれぬ。","曹操",},
        [3753] = {5804,"許攸","仕える主君を間違えたのだ。そこで奴を棄て、旧友に会いに来た。召し抱えてもらえないだろうか。","仕える主君を間違えたのだ。そこで奴を棄て、旧友に会いに来た。召し抱えてもらえないだろうか。","曹操",},
        [3754] = {5804,"曹操","そうであったか。遠くから来てくれて、助かったぞ！袁紹を破る計を教えてくれぬか。","そうであったか。遠くから来てくれて、助かったぞ！袁紹を破る計を教えてくれぬか。","許攸",},
        [3755] = {5804,"許攸","今、軍の糧食はいかほど残っている?","今、軍の糧食はいかほど残っている?","曹操",},
        [3756] = {5804,"曹操","うむ。一年は持つ。","うむ。一年は持つ。","許攸",},
        [3757] = {5804,"許攸","みくびられたものよ。わしは誠意をもって投じて来たのに、欺こうとするとは。","みくびられたものよ。わしは誠意をもって投じて来たのに、欺こうとするとは。","曹操",},
        [3758] = {5804,"許攸","本当のことを申せ！糧食は既に尽きたはずだ！","本当のことを申せ！糧食は既に尽きたはずだ！","曹操",},
        [3759] = {5804,"曹操","ふっ……ハッハッハ…許攸の目は欺けぬか。旧交を忘れず来てくれたのなら、今すぐ策を教えてくれ。","ふっ……ハッハッハ…許攸の目は欺けぬか。旧交を忘れず来てくれたのなら、今すぐ策を教えてくれ。","許攸",},
        [3760] = {5804,"許攸","いいだろう。この策を用いれば、三日と経たず、袁紹の百万の軍を戦わずして破れる。聞き入れてもらえるかな?","いいだろう。この策を用いれば、三日と経たず、袁紹の百万の軍を戦わずして破れる。聞き入れてもらえるかな?","曹操",},
        [3761] = {5804,"曹操","ぜひ聞かせてくれ。","ぜひ聞かせてくれ。","許攸",},
        [3762] = {5804,"許攸","袁紹は軍の糧食や物資をすべて烏巣に蓄えておる。今は、淳于瓊に守らせているが、奴は酒に目がない。","袁紹は軍の糧食や物資をすべて烏巣に蓄えておる。今は、淳于瓊に守らせているが、奴は酒に目がない。","曹操",},
        [3763] = {5804,"許攸","そこでこちらから精兵を選び、袁紹の武将・蒋奇が糧食の護衛に来たと偽って、派遣するのだ。","そこでこちらから精兵を選び、袁紹の武将・蒋奇が糧食の護衛に来たと偽って、派遣するのだ。","曹操",},
        [3764] = {5804,"許攸","隙を見て糧食や物資を燃やしてしまえば、袁紹軍は三日ともたず自ら崩れていこう。","隙を見て糧食や物資を燃やしてしまえば、袁紹軍は三日ともたず自ら崩れていこう。","曹操",},
        [3765] = {5805,"張遼","袁紹が糧食を蓄えている場所が、無防備でありましょうか?丞相、軽々しく行ってはなりませぬ。恐らく許攸の計略です。","袁紹が糧食を蓄えている場所が、無防備でありましょうか?丞相、軽々しく行ってはなりませぬ。恐らく許攸の計略です。","曹操",},
        [3766] = {5805,"曹操","いや、許攸がここへ来たのは、天が袁紹を敗北させようとしているのだ。","いや、許攸がここへ来たのは、天が袁紹を敗北させようとしているのだ。","張遼",},
        [3767] = {5805,"曹操","今、我が軍に糧食の供給がなければ、持ちこたえるのは難しい。許攸の計略を用いねば、座して手を束ねるのみだ。","今、我が軍に糧食の供給がなければ、持ちこたえるのは難しい。許攸の計略を用いねば、座して手を束ねるのみだ。","張遼",},
        [3768] = {5805,"曹操","もしわしを騙す気なら、なぜ我が砦に残っている?しかも、わしも砦を奪おうと思っていた。","もしわしを騙す気なら、なぜ我が砦に残っている?しかも、わしも砦を奪おうと思っていた。","張遼",},
        [3769] = {5805,"曹操","許攸の策を実行すべきだ。諸侯は疑うな。","許攸の策を実行すべきだ。諸侯は疑うな。","張遼",},
        [3770] = {5805,"張遼","袁紹がこの隙に襲撃してくるのも防がねばなりません。","袁紹がこの隙に襲撃してくるのも防がねばなりません。","曹操",},
        [3771] = {5805,"曹操","そこは既に考えてある。","そこは既に考えてある。","張遼",},
        [3772] = {5805,"曹操","荀攸、賈詡、曹洪は許攸と共に砦を守れ。","荀攸、賈詡、曹洪は許攸と共に砦を守れ。","張遼",},
        [3773] = {5805,"曹操","夏侯惇、夏侯淵は、それぞれ軍を率いて左に潜み、曹仁、李典は右に潜んで、不慮の事態に備えよ。","夏侯惇、夏侯淵は、それぞれ軍を率いて左に潜み、曹仁、李典は右に潜んで、不慮の事態に備えよ。","張遼",},
        [3774] = {5805,"曹操","張遼、許褚は前に、徐晃、于禁は後ろ、わしと揚威将軍は中央だ。全部で五千の人馬を出す。","張遼、許褚は前に、徐晃、于禁は後ろ、わしと揚威将軍は中央だ。全部で五千の人馬を出す。","張遼",},
        [3775] = {5805,"曹操","袁紹軍の旗印を掲げよ。兵士は皆わら束と薪を背負い、人や馬の口を封じろ。夕暮れに、烏巣に向けて出立する！","袁紹軍の旗印を掲げよ。兵士は皆わら束と薪を背負い、人や馬の口を封じろ。夕暮れに、烏巣に向けて出立する！","張遼",},
        [3776] = {5806,"袁紹","ええい！この騒ぎはなんだ?","ええい！この騒ぎはなんだ?","",},
        [3777] = {5806,"張郃","曹操の夜襲よ。烏巣から出火したわ！","曹操の夜襲よ。烏巣から出火したわ！","袁紹",},
        [3778] = {5806,"袁紹","何だと！","何だと！","張郃",},
        [3779] = {5901,"張郃","私も高覧と共に烏巣の救援に向かうわ。","私も高覧と共に烏巣の救援に向かうわ。","",},
        [3780] = {5901,"郭図","いけません！曹操軍が糧食を奪う気なら、曹操自身も必ず行っているでしょう。ならば、本陣はガラ空きのはず。","いけません！曹操軍が糧食を奪う気なら、曹操自身も必ず行っているでしょう。ならば、本陣はガラ空きのはず。","張郃",},
        [3781] = {5901,"郭図","ですからまず本陣を攻撃すれば、曹操はすぐに引き返すでしょう。兵法に言う「魏を囲んで趙を救う」の計です。","ですからまず本陣を攻撃すれば、曹操はすぐに引き返すでしょう。兵法に言う「魏を囲んで趙を救う」の計です。","張郃",},
        [3782] = {5901,"張郃","いいえ、違うわ！謀に長けた曹操のこと、外に出撃するなら、内も必ず防備はしているはずよ。","いいえ、違うわ！謀に長けた曹操のこと、外に出撃するなら、内も必ず防備はしているはずよ。","郭図",},
        [3783] = {5901,"張郃","今、曹操の陣営を攻め落とせず、淳于瓊たちも捕えられれば、私たちも皆同じ目に遭うわよ。","今、曹操の陣営を攻め落とせず、淳于瓊たちも捕えられれば、私たちも皆同じ目に遭うわよ。","郭図",},
        [3784] = {5901,"郭図","曹操は糧食を奪うことのみ考えています。本陣に兵を残すことはありません！","曹操は糧食を奪うことのみ考えています。本陣に兵を残すことはありません！","張郃",},
        [3785] = {5901,"袁紹","いいかげんにしろ！張郃、高覧は五千の軍を率い、官渡へ行って曹操陣営を攻撃せよ。","いいかげんにしろ！張郃、高覧は五千の軍を率い、官渡へ行って曹操陣営を攻撃せよ。","郭図",},
        [3786] = {5901,"袁紹","蒋奇は一万の兵を率い、烏巣の救援に向かうのだ。","蒋奇は一万の兵を率い、烏巣の救援に向かうのだ。","郭図",},
        [3787] = {5902,"袁紹","なぜ烏巣を失ったのだ?何があったのだ?","なぜ烏巣を失ったのだ?何があったのだ?","郭図",},
        [3788] = {5902,"郭図","淳于瓊が酒に酔って寝てしまい、敵に抵抗できなかったのです。","淳于瓊が酒に酔って寝てしまい、敵に抵抗できなかったのです。","袁紹",},
        [3789] = {5902,"袁紹","曹操陣営を襲撃した張郃、高覧はなぜ戻らぬ?","曹操陣営を襲撃した張郃、高覧はなぜ戻らぬ?","郭図",},
        [3790] = {5902,"郭図","張郃と高覧は、わが軍の敗走を見て、内心喜んでいることでしょうな。(郭図は別の思惑があって言っている。)","張郃と高覧は、わが軍の敗走を見て、内心喜んでいることでしょうな。(郭図は別の思惑があって言っている。)","袁紹",},
        [3791] = {5902,"袁紹","なぜそのようなことを?","なぜそのようなことを?","郭図",},
        [3792] = {5902,"郭図","あの二人はもともと曹操に投降するつもりでしたので。","あの二人はもともと曹操に投降するつもりでしたので。","袁紹",},
        [3793] = {5902,"郭図","今回、敵陣の攻撃に差し向けられましたが、わざと手を抜き、味方の被害を増やしたのです。","今回、敵陣の攻撃に差し向けられましたが、わざと手を抜き、味方の被害を増やしたのです。","袁紹",},
        [3794] = {5902,"袁紹","何だと！?早く使いを出して、張郃、高覧を本陣に帰らせろ！","何だと！?早く使いを出して、張郃、高覧を本陣に帰らせろ！","郭図",},
        [3795] = {5903,"高覧","郭図は良からぬ考えを抱いておる。","郭図は良からぬ考えを抱いておる。","張郃",},
        [3796] = {5903,"高覧","袁紹は郭図の讒言を信じるような男だ。いずれ曹操に敗れるだろう。ならば我らは座して死を待つ必要があるか?それより曹操に投降したほうがいい。","袁紹は郭図の讒言を信じるような男だ。いずれ曹操に敗れるだろう。ならば我らは座して死を待つ必要があるか?それより曹操に投降したほうがいい。","張郃",},
        [3797] = {5903,"張郃","奇遇ね。私も、ずっとそう考えていたわ。","奇遇ね。私も、ずっとそう考えていたわ。","高覧",},
        [3798] = {5904,"夏侯惇","張郃、高覧の二人が投降してきた。だが、本心かどうかは分からん。","張郃、高覧の二人が投降してきた。だが、本心かどうかは分からん。","曹操",},
        [3799] = {5904,"曹操","わかった。恩をもって遇しよう。たとえ異心があっても、変えることもできる。","わかった。恩をもって遇しよう。たとえ異心があっても、変えることもできる。","夏侯惇",},
        [3800] = {5904,"曹操","砦の門を開け、二人を中に入れよ。","砦の門を開け、二人を中に入れよ。","夏侯惇",},
        [3801] = {5905,"張郃","袁紹は愚昧です。私たちは丞相殿の配下に入りとうございます。","袁紹は愚昧です。私たちは丞相殿の配下に入りとうございます。","曹操",},
        [3802] = {5905,"曹操","袁紹が、お二方の言に従っておれば、敗戦には至らなかったろう。","袁紹が、お二方の言に従っておれば、敗戦には至らなかったろう。","張郃",},
        [3803] = {5905,"曹操","今、お二方が投降したのは、微子が殷を去り、韓信が漢に帰ったようなものだ。","今、お二方が投降したのは、微子が殷を去り、韓信が漢に帰ったようなものだ。","張郃",},
        [3804] = {5905,"曹操","はははは！運が向いてきたぞ！","はははは！運が向いてきたぞ！","張郃",},
        [3805] = {6001,"主君","官渡の戦いを経ても、袁紹は降伏していません。","官渡の戦いを経ても、袁紹は降伏していません。","曹操",},
        [3806] = {6001,"主君","四州の兵を集め、二、三十万を得て、倉亭で陣を張っています。","四州の兵を集め、二、三十万を得て、倉亭で陣を張っています。","曹操",},
        [3807] = {6001,"曹操","すぐに三軍に命じ、倉亭へ急行せよ。","すぐに三軍に命じ、倉亭へ急行せよ。","プレイヤー",},
        [3808] = {6002,"曹操","袁紹の軍勢は多く、兵馬も強力だ。初戦で袁尚が三回戦い、我が将、史渙を破った。","袁紹の軍勢は多く、兵馬も強力だ。初戦で袁尚が三回戦い、我が将、史渙を破った。","",},
        [3809] = {6002,"曹操","諸侯に袁紹を破る策はあるか?","諸侯に袁紹を破る策はあるか?","",},
        [3810] = {6002,"程昱","ワシに一計がございますじゃ。その名も「十面埋伏」。","ワシに一計がございますじゃ。その名も「十面埋伏」。","曹操",},
        [3811] = {6002,"曹操","何だ?十面埋伏とは。","何だ?十面埋伏とは。","程昱",},
        [3812] = {6002,"程昱","まず、わが軍を黄河の岸まで退かせ、伏兵を十隊忍ばせるのですじゃ。","まず、わが軍を黄河の岸まで退かせ、伏兵を十隊忍ばせるのですじゃ。","曹操",},
        [3813] = {6002,"程昱","左側一隊は夏侯惇、二隊は張遼、三隊は李典、四隊は楽進、五隊は夏侯淵。","左側一隊は夏侯惇、二隊は張遼、三隊は李典、四隊は楽進、五隊は夏侯淵。","曹操",},
        [3814] = {6002,"程昱","右側一隊は曹洪、二隊は張郃、三隊は徐晃、四隊は于禁、五隊は高覧。","右側一隊は曹洪、二隊は張郃、三隊は徐晃、四隊は于禁、五隊は高覧。","曹操",},
        [3815] = {6002,"程昱","次の日に、許褚と揚威将軍が中軍の先鋒となり、敵陣を襲う振りをしてすぐ後退し、袁紹を河岸までおびき寄せますのじゃ。","次の日に、許褚と揚威将軍が中軍の先鋒となり、敵陣を襲う振りをしてすぐ後退し、袁紹を河岸までおびき寄せますのじゃ。","曹操",},
        [3816] = {6002,"程昱","中軍の反撃に合わせ、伏兵十隊が次々と攻撃を仕掛ければ、袁紹軍は必ずや壊滅するじゃろう。","中軍の反撃に合わせ、伏兵十隊が次々と攻撃を仕掛ければ、袁紹軍は必ずや壊滅するじゃろう。","曹操",},
        [3817] = {6002,"主君","なるほど、実に巧妙ですね！","なるほど、実に巧妙ですわ！","程昱",},
        [3818] = {6002,"曹操","よし、すぐに取り掛かれ。","よし、すぐに取り掛かれ。","プレイヤー",},
        [3819] = {6101,"曹操","倉亭の戦いで勝利するため、程昱の言葉を聞いて、一年余り軍の鋭気を養った。今こそ兵を起こし、袁紹を討つべきであろう。","倉亭の戦いで勝利するため、程昱の言葉を聞いて、一年余り軍の鋭気を養った。今こそ兵を起こし、袁紹を討つべきであろう。","程昱",},
        [3820] = {6101,"曹操","夏侯惇と満寵は汝南を守り、劉表を抑えよ。曹仁、荀彧を許都に残して守りを固め、わしは大軍を率いて再び官渡に駐屯する。","夏侯惇と満寵は汝南を守り、劉表を抑えよ。曹仁、荀彧を許都に残して守りを固め、わしは大軍を率いて再び官渡に駐屯する。","程昱",},
        [3821] = {6101,"程昱","まずは袁紹を破り、それから荊州を取れば、南北いずれも一挙に手に入れられますじゃ。","まずは袁紹を破り、それから荊州を取れば、南北いずれも一挙に手に入れられますじゃ。","曹操",},
        [3822] = {6102,"袁紹","もう年も越してだいぶになる。許都を攻めるぞ。","もう年も越してだいぶになる。許都を攻めるぞ。","審配",},
        [3823] = {6102,"審配","昨年官渡と倉亭で敗北し、軍内の士気は振るいません。やはりまだ防備を固めて、軍民の力を蓄えるべきです。","昨年官渡と倉亭で敗北し、軍内の士気は振るいません。やはりまだ防備を固めて、軍民の力を蓄えるべきです。","袁紹",},
        [3824] = {6102,"郭図","申し上げます！曹操が官渡に兵を進め、冀州を攻めようとしています。","申し上げます！曹操が官渡に兵を進め、冀州を攻めようとしています。","袁紹",},
        [3825] = {6102,"袁紹","兵が城に迫り、壕近くまで来れば、そこから防いでも遅い。わしが自ら大軍を率いて迎え討とう。","兵が城に迫り、壕近くまで来れば、そこから防いでも遅い。わしが自ら大軍を率いて迎え討とう。","郭図",},
        [3826] = {6102,"袁尚","父上はまだ病が治っておらず、遠征は無理です。私が兵を率いて迎え討ちましょう。","父上はまだ病が治っておらず、遠征は無理です。私が兵を率いて迎え討ちましょう。","袁紹",},
        [3827] = {6102,"袁紹","おおっ！やってくれるか！","おおっ！やってくれるか！","袁尚",},
        [3828] = {6102,"袁紹","すぐ使いを出して青州から袁譚を、幽州から袁煕を、并州から高幹を呼び、四方向から同時に曹操を破るのだ。","すぐ使いを出して青州から袁譚を、幽州から袁煕を、并州から高幹を呼び、四方向から同時に曹操を破るのだ。","袁尚",},
        [3829] = {6103,"袁尚","曹操軍など何ほどでもない。","曹操軍など何ほどでもない。","",},
        [3830] = {6103,"袁尚","あっさりと史渙を斬ってやったぞ。他に、戦いを受けて立つものはおらぬのか?","あっさりと史渙を斬ってやったぞ。他に、戦いを受けて立つものはおらぬのか?","",},
        [3831] = {6103,"張遼","お前など怖れるものか。俺が相手だ！","お前など怖れるものか。俺が相手だ！","袁尚",},
        [3832] = {6103,"主君","張遼殿が出るまでもありません。私が相手になりましょう！","張遼殿が出るには及びません。私が相手になりますわ！","張遼",},
        [3833] = {6104,"主君","敗将、袁尚！どこへ逃げる気だ！","敗将、袁尚！どこへ逃げる気なの！","",},
        [3834] = {6104,"張遼","ははは、口ほどにもない相手だったな。","ははは、口ほどにもない相手だったな。","プレイヤー",},
        [3835] = {6104,"張遼","揚威将軍、追うことはない。袁紹軍はほぼ壊滅状態だ。袁尚には、尻尾を巻いて冀州に逃げ帰らせておけばよい。","揚威将軍、追うことはない。袁紹軍はほぼ壊滅状態だ。袁尚には、尻尾を巻いて冀州に逃げ帰らせておけばよい。","プレイヤー",},
        [3836] = {6105,"審配","袁尚さまは史渙を斬ってから、自らの勇におぼれてしまわれたようです。","袁尚さまは史渙を斬ってから、自らの勇におぼれてしまわれたようです。","袁紹",},
        [3837] = {6105,"審配","袁譚さまの兵が来るのを待たずに、自ら数万の兵を率いて黍陽へ行かれ、曹操軍の先陣と相対しました。","袁譚さまの兵が来るのを待たずに、自ら数万の兵を率いて黍陽へ行かれ、曹操軍の先陣と相対しました。","袁紹",},
        [3838] = {6105,"審配","三合と戦わぬうちに大敗し、すでに軍を率いて冀州へ逃げ戻っておられます。","三合と戦わぬうちに大敗し、すでに軍を率いて冀州へ逃げ戻っておられます。","袁紹",},
        [3839] = {6105,"袁紹","何だと！(袁紹はこの報告を聞いて驚愕し、病気が再発。大量に吐血して倒れる。)","何だと！(袁紹はこの報告を聞いて驚愕し、病気が再発。大量に吐血して倒れる。)","審配",},
        [3840] = {6105,"劉夫人","大変っ！早く袁紹殿を部屋へお連れして、早く！","大変っ！早く袁紹殿を部屋へお連れして、早く！","袁紹",},
        [3841] = {6106,"劉夫人","袁尚に後を継がせてよろしいですか?","袁尚に後を継がせてよろしいですか?","袁紹",},
        [3842] = {6106,"袁紹","よい…","よい…","",},
        [3843] = {6106,"審配","かしこまりました。すぐに遺言書を作ります。","かしこまりました。すぐに遺言書を作ります。","袁紹",},
        [3844] = {6106,"劉夫人","あ、あなた！(袁紹はうめき声をあげると、一斗余り吐血して死ぬ。)","あ、あなた！(袁紹はうめき声をあげると、一斗余り吐血して死ぬ。)","袁紹",},
        [3845] = {6201,"張春華","曹操があなたの才知を見こんで、出仕せよと使いの者を寄こしたのですが。","曹操があなたの才知を見こんで、出仕せよと使いの者を寄こしたのですが。","司馬懿",},
        [3846] = {6201,"司馬懿","漢の政権は既に曹操に握られている。それに曹操は宦官の家系だ。","漢の政権は既に曹操に握られている。それに曹操は宦官の家系だ。","張春華",},
        [3847] = {6201,"司馬懿","節を曲げて、奴の配下になどなりたくない。中風で、起き上がれないと言い訳しよう。","節を曲げて、奴の配下になどなりたくない。中風で、起き上がれないと言い訳しよう。","張春華",},
        [3848] = {6201,"司馬懿","使者は帰すように。","使者は帰すように。","張春華",},
        [3849] = {6201,"張春華","はい。","はい。","司馬懿",},
        [3850] = {6202,"張春華","まずいですわ！","まずいですわ！","司馬懿",},
        [3851] = {6202,"張春華","曹操はあなたが中風だと信じず、深夜に再び使者を寄こしました。","曹操はあなたが中風だと信じず、深夜に再び使者を寄こしました。","司馬懿",},
        [3852] = {6202,"司馬懿","慌てるな、客間でしばらく使者を足留めしておいてくれ。私は寝室に戻って臥せっていよう。","慌てるな、客間でしばらく使者を足留めしておいてくれ。私は寝室に戻って臥せっていよう。","張春華",},
        [3853] = {6202,"司馬懿","ぴくりともせず、使者に本当に中風のように見せてやるぞ。","ぴくりともせず、使者に本当に中風のように見せてやるぞ。","張春華",},
        [3854] = {6202,"張春華","分かりましたわ。","分かりましたわ。","司馬懿",},
        [3855] = {6301,"孫権","偶然、象を手に入れた。曹操殿に献上いたそう。","偶然、象を手に入れた。曹操殿に献上いたそう。","曹操",},
        [3856] = {6301,"孫権","俺からの気持ちだ。どうか収めてくれ。","俺からの気持ちだ。どうか収めてくれ。","曹操",},
        [3857] = {6301,"曹操","ハッハッハ…孫権殿、ご好意に感謝する。","ハッハッハ…孫権殿、ご好意に感謝する。","孫権",},
        [3858] = {6301,"孫権","おっと、そうであった。曹操殿、俺は才が足りず、象の重さが分からなかった。ですが貴公の陣営には賢者や策士が数多い。","おっと、そうであった。曹操殿、俺は才が足りず、象の重さが分からなかった。ですが貴公の陣営には賢者や策士が数多い。","曹操",},
        [3859] = {6301,"孫権","象の重さを量ることができたら、必ず俺に教えてほしいのだ。","象の重さを量ることができたら、必ず俺に教えてほしいのだ。","曹操",},
        [3860] = {6301,"曹操","承知いたした。必ず……","承知いたした。必ず……","孫権",},
        [3861] = {6302,"曹操","孫権め……象を贈るにことよせて、わしを貶める気だな。","孫権め……象を贈るにことよせて、わしを貶める気だな。","",},
        [3862] = {6302,"曹操","もし象の重さを量れねば、わしの面子は丸つぶれだ。","もし象の重さを量れねば、わしの面子は丸つぶれだ。","",},
        [3863] = {6302,"曹操","お前たち、早く方法を考えよ。","お前たち、早く方法を考えよ。","",},
        [3864] = {6302,"夏侯惇","大きな秤を作ればいいのでは?","大きな秤を作ればいいのでは?","曹操",},
        [3865] = {6302,"張遼","却下。一体どれだけ大きな秤を作ればよいのです。しかも象は生きている。秤の上でおとなしくしていますか?","却下。一体どれだけ大きな秤を作ればよいのです。しかも象は生きている。秤の上でおとなしくしていますか?","曹操",},
        [3866] = {6302,"張遼","よって、象を殺してバラバラにして量るしかありませぬな。","よって、象を殺してバラバラにして量るしかありませぬな。","曹操",},
        [3867] = {6302,"曹操","馬鹿か！戦ばかりの武将では話にならん！","馬鹿か！戦ばかりの武将では話にならん！","張遼",},
        [3868] = {6302,"曹操","重さを量るために生きている象を殺せだと?惜しいではないか！","重さを量るために生きている象を殺せだと?惜しいではないか！","張遼",},
        [3869] = {6303,"曹操","誰か、象の重さを量る方法を思いつく者はおらんのか?","誰か、象の重さを量る方法を思いつく者はおらんのか?","",},
        [3870] = {6303,"曹沖","ぼくに考えがあります。きっと重さを量れるよ。","ぼくに考えがあります。きっと重さを量れるよ。","曹操",},
        [3871] = {6303,"曹操","ふむ、どうする?","ふむ、どうする?","曹沖",},
        [3872] = {6303,"曹沖","簡単なことだよ。まず象を船に乗せ、水面が触れたところに目印をつけるんだ。","簡単なことだよ。まず象を船に乗せ、水面が触れたところに目印をつけるんだ。","曹操",},
        [3873] = {6303,"曹沖","次に船から象を下ろして別の荷物を積み、水面を先ほどの目印の位置にする。","次に船から象を下ろして別の荷物を積み、水面を先ほどの目印の位置にする。","曹操",},
        [3874] = {6303,"曹沖","最後にこの荷物の重さを計れば、象の重さが分かると思うよ。","最後にこの荷物の重さを計れば、象の重さが分かると思うよ。","曹操",},
        [3875] = {6303,"曹操","おおっ！やはり我が曹沖は賢いな。","おおっ！やはり我が曹沖は賢いな。","曹沖",},
        [3876] = {6303,"曹操","はははは！","はははは！","曹沖",},
        [3877] = {6401,"曹操","兵と共に冀州へ来たが、袁譚と袁尚が城を堅く守り、袁煕と高幹は城から三十里のところで砦を固めている。連日攻めても落ちぬ。","兵と共に冀州へ来たが、袁譚と袁尚が城を堅く守り、袁煕と高幹は城から三十里のところで砦を固めている。連日攻めても落ちぬ。","郭嘉",},
        [3878] = {6401,"曹操","さて、どうするべきか……?","さて、どうするべきか……?","郭嘉",},
        [3879] = {6401,"郭嘉","袁家では兄を差し置いて弟に後を継がせたため、兄弟の間で権勢が拮抗し、派閥ができています。","袁家では兄を差し置いて弟に後を継がせたため、兄弟の間で権勢が拮抗し、派閥ができています。","曹操",},
        [3880] = {6401,"郭嘉","急いで攻めれば兄弟同士で助け合うでしょうが、時間を置けば彼ら同士で争うでしょう。","急いで攻めれば兄弟同士で助け合うでしょうが、時間を置けば彼ら同士で争うでしょう。","曹操",},
        [3881] = {6401,"郭嘉","まずは兵を南の荊州へ向け、劉表を討つのです。袁家の兄弟の内紛を待ち、その後で討てば、一挙に形勢は定まるでしょう。","まずは兵を南の荊州へ向け、劉表を討つのです。袁家の兄弟の内紛を待ち、その後で討てば、一挙に形勢は定まるでしょう。","曹操",},
        [3882] = {6401,"曹操","その言や良し！","その言や良し！","郭嘉",},
        [3883] = {6401,"曹操","賈詡を太守に命じ、黍陽を守らせよ。曹洪は兵を率いて官渡を守れ。わしは大軍を率いて荊州に兵を進める。","賈詡を太守に命じ、黍陽を守らせよ。曹洪は兵を率いて官渡を守れ。わしは大軍を率いて荊州に兵を進める。","郭嘉",},
        [3884] = {6402,"袁譚","俺は長子なのに、父の後を継げぬとはどういうことだ！袁尚は継母の子なのに、官位を継いだぞ。まったく納得いかん。","俺は長子なのに、父の後を継げぬとはどういうことだ！袁尚は継母の子なのに、官位を継いだぞ。まったく納得いかん。","郭図",},
        [3885] = {6402,"郭図","我が君は城外に兵を置いたまま、袁尚と審配を酒宴にお招きなさい。その席に刺客を忍ばせて殺してしまえば、万事片がつきます。","我が君は城外に兵を置いたまま、袁尚と審配を酒宴にお招きなさい。その席に刺客を忍ばせて殺してしまえば、万事片がつきます。","袁譚",},
        [3886] = {6403,"審配","袁譚がご主君と単独で会おうなど、郭図の計に違いありませぬ。","袁譚がご主君と単独で会おうなど、郭図の計に違いありませぬ。","袁尚",},
        [3887] = {6403,"審配","行けば必ず奸計に遭うでしょう。いっそ、この機会に乗じて攻めるのがよろしいかと。","行けば必ず奸計に遭うでしょう。いっそ、この機会に乗じて攻めるのがよろしいかと。","袁尚",},
        [3888] = {6403,"袁尚","すぐに兵を率いて行くぞ！(袁尚は五万の兵を率いて城外へ出る。)","すぐに兵を率いて行くぞ！(袁尚は五万の兵を率いて城外へ出る。)","審配",},
        [3889] = {6403,"袁譚","なぜ兵を率いて来たのだ?","なぜ兵を率いて来たのだ?","袁尚",},
        [3890] = {6403,"袁尚","父上が私を後継ぎに立てたことで、謀反を起こそうというのではないか?","父上が私を後継ぎに立てたことで、謀反を起こそうというのではないか?","袁譚",},
        [3891] = {6403,"袁譚","なにをっ！どうせお前が爵位を奪おうと父上を毒殺したのだろう！今度は兄を殺す気か！","なにをっ！どうせお前が爵位を奪おうと父上を毒殺したのだろう！今度は兄を殺す気か！","袁尚",},
        [3892] = {6404,"郭嘉","袁譚と呂曠、呂翔が投降してきました。","袁譚と呂曠、呂翔が投降してきました。","曹操",},
        [3893] = {6404,"曹操","ふん、想像通りだな。","ふん、想像通りだな。","郭嘉",},
        [3894] = {6404,"曹操","だが、袁譚の奴、将軍の印綬を二つ作り、密かに二人の呂に贈っている。内応して、今後、謀反を起こすに違いない。","だが、袁譚の奴、将軍の印綬を二つ作り、密かに二人の呂に贈っている。内応して、今後、謀反を起こすに違いない。","郭嘉",},
        [3895] = {6404,"曹操","だが呂曠と呂翔はその事をわしに報告してきた。袁尚を破ったあと、必ず袁譚は斬ってやる。","だが呂曠と呂翔はその事をわしに報告してきた。袁尚を破ったあと、必ず袁譚は斬ってやる。","郭嘉",},
        [3896] = {6404,"郭嘉","さすがは丞相殿。","さすがは丞相殿。","曹操",},
        [3897] = {6405,"曹操","馮礼の計に従い、三百人の兵士に地下洞を掘らせて、城に攻め入ったが……","馮礼の計に従い、三百人の兵士に地下洞を掘らせて、城に攻め入ったが……","",},
        [3898] = {6405,"曹操","企てが漏れたか。審配の精兵が大石で突門を閉じ、馮礼と三百の兵を生き埋めにしおった。","企てが漏れたか。審配の精兵が大石で突門を閉じ、馮礼と三百の兵を生き埋めにしおった。","",},
        [3899] = {6405,"曹操","他にどのような計略がある?","他にどのような計略がある?","",},
        [3900] = {6405,"許攸","わしに一計があります。漳河の水を引き込んで、城を水攻めにするのはいかがです?","わしに一計があります。漳河の水を引き込んで、城を水攻めにするのはいかがです?","曹操",},
        [3901] = {6405,"曹操","やってみる価値はあるな。許攸の計で攻めてみよう。","やってみる価値はあるな。許攸の計で攻めてみよう。","許攸",},
        [3902] = {6406,"袁尚","曹操軍が城外で塹壕を掘っている！","曹操軍が城外で塹壕を掘っている！","審配",},
        [3903] = {6406,"審配","私が見てきましょう。","私が見てきましょう。","袁尚",},
        [3904] = {6406,"審配","これは漳河の水を引き入れて、城を水攻めにしようというのでしょう。","これは漳河の水を引き入れて、城を水攻めにしようというのでしょう。","袁尚",},
        [3905] = {6406,"審配","堀が深ければ水を引き入れられますが、こんなに浅くては使い物になりませんな！","堀が深ければ水を引き入れられますが、こんなに浅くては使い物になりませんな！","袁尚",},
        [3906] = {6407,"許攸","審配は油断しました。我が軍は今夜、兵士を十倍に増やし全力で掘っています。明日になれば広さも深さも二丈になります。","審配は油断しました。我が軍は今夜、兵士を十倍に増やし全力で掘っています。明日になれば広さも深さも二丈になります。","曹操",},
        [3907] = {6407,"許攸","漳河の水を引けば、城内の水は深さ数尺にもなります。糧食も尽きており、兵士はみな飢え死にでしょう。","漳河の水を引けば、城内の水は深さ数尺にもなります。糧食も尽きており、兵士はみな飢え死にでしょう。","曹操",},
        [3908] = {6407,"曹操","よし！敵将の審栄が投降してきたぞ。西門を開け、我が兵を入れよ。","よし！敵将の審栄が投降してきたぞ。西門を開け、我が兵を入れよ。","許攸",},
        [3909] = {6407,"曹操","すぐ全軍に命令だ。冀州に突入する！","すぐ全軍に命令だ。冀州に突入する！","許攸",},
        [3910] = {6408,"曹操","卿は袁氏に忠実だからな。こうせざるを得んのだ。どうだ、わしに投降するか?","卿は袁氏に忠実だからな。こうせざるを得んのだ。どうだ、わしに投降するか?","審配",},
        [3911] = {6408,"審配","侮るなよ！","侮るなよ！","曹操",},
        [3912] = {6408,"審配","私は、生きてるうちは袁氏の臣下、死せば袁氏の霊となる。お前らのような阿諛追従の輩とは違う！早く斬れ！","私は、生きてるうちは袁氏の臣下、死せば袁氏の霊となる。お前らのような阿諛追従の輩とは違う！早く斬れ！","曹操",},
        [3913] = {6408,"曹操","愚物め、斬れ！","愚物め、斬れ！","審配",},
        [3914] = {6501,"曹丕","そこで泣いているご婦人二人は、何者だ?(曹丕が袁家の屋敷に入り、泣いている二人の夫人を見て尋ねる。)","そこで泣いているご婦人二人は、何者だ?(曹丕が袁家の屋敷に入り、泣いている二人の夫人を見て尋ねる。)","",},
        [3915] = {6501,"劉夫人","わたしは、袁将軍の妻、劉氏です。","わたしは、袁将軍の妻、劉氏です。","曹丕",},
        [3916] = {6501,"曹丕","では、こちらは?","では、こちらは?","",},
        [3917] = {6501,"劉夫人","次男、袁熙の妻、甄氏です。袁熙は幽州へ赴任しましたが、甄氏は遠くへ行きたくなくて、ここに残ったのです。","次男、袁熙の妻、甄氏です。袁熙は幽州へ赴任しましたが、甄氏は遠くへ行きたくなくて、ここに残ったのです。","曹丕",},
        [3918] = {6501,"曹丕","そうか、私は丞相・曹操の息子だ。お前の家族は保護してやろう。心配するな。","そうか、私は丞相・曹操の息子だ。お前の家族は保護してやろう。心配するな。","",},
        [3919] = {6502,"許攸","わしがいなければ、この城内には入れなかっただろう?","わしがいなければ、この城内には入れなかっただろう?","曹操",},
        [3920] = {6502,"曹操","はははは、さあ、一緒に入城しようではないか。","はははは、さあ、一緒に入城しようではないか。","許攸",},
        [3921] = {6502,"曹操","まずは袁紹の屋敷へ行くぞ。","まずは袁紹の屋敷へ行くぞ。","許攸",},
        [3922] = {6502,"曹操","誰が先に入ったのだ?","誰が先に入ったのだ?","許攸",},
        [3923] = {6502,"徐晃","はっ。実は曹丕様が先に中に……","はっ。実は曹丕様が先に中に……","曹操",},
        [3924] = {6502,"曹操","勝手なことを！出てこい、曹丕！","勝手なことを！出てこい、曹丕！","徐晃",},
        [3925] = {6502,"曹丕","玉の肌に花の貌……まさに絶世の美女だ。甄氏を私の妻にしたい！","玉の肌に花の貌……まさに絶世の美女だ。甄氏を私の妻にしたい！","",},
        [3926] = {6502,"劉夫人","曹丕様のおかげで、わたしども一家みな助かりました。甄氏を曹丕様の嫁に差し上げとうございます。","曹丕様のおかげで、わたしども一家みな助かりました。甄氏を曹丕様の嫁に差し上げとうございます。","曹操",},
        [3927] = {6502,"曹操","まったく……好きにせい。","まったく……好きにせい。","",},
        [3928] = {6503,"曹操","かつて、わしと袁紹には兄弟の交わりがあったが、既に袁紹は死んだ。運命とは、わからぬものだな。","かつて、わしと袁紹には兄弟の交わりがあったが、既に袁紹は死んだ。運命とは、わからぬものだな。","",},
        [3929] = {6503,"曹操","黄金と絹、糧食を袁紹の妻・劉氏に与えよ。今、河北の民は戦乱で傷ついている。今年は租税を免ずる。","黄金と絹、糧食を袁紹の妻・劉氏に与えよ。今、河北の民は戦乱で傷ついている。今年は租税を免ずる。","",},
        [3930] = {6504,"許攸","お前たち、わしがいなければこの城門をくぐることはできなかっただろう?","お前たち、わしがいなければこの城門をくぐることはできなかっただろう?","許褚",},
        [3931] = {6504,"許褚","なに言うぞうっ！おらたちだって命を懸けて、血の戦いをくぐってきたんだぞうっ！お前だけの手柄じゃない！","なに言うぞうっ！おらたちだって命を懸けて、血の戦いをくぐってきたんだぞうっ！お前だけの手柄じゃない！","許攸",},
        [3932] = {6504,"許攸","フン、この猪武者が。お前らごとき匹夫、取るに足りんわ！","フン、この猪武者が。お前らごとき匹夫、取るに足りんわ！","許褚",},
        [3933] = {6504,"許褚","ブヒッ！おらを怒らせたってことは、命はいらんってことだな！","ブヒッ！おらを怒らせたってことは、命はいらんってことだな！","許攸",},
        [3934] = {6505,"許褚","許攸はすごく無礼だったので、おらが罰を与えてやったんだぞう。(そう言って、許攸の首を差し出す。)","許攸はすごく無礼だったので、おらが罰を与えてやったんだぞう。(そう言って、許攸の首を差し出す。)","曹操",},
        [3935] = {6505,"曹操","許攸はわしの昔馴染み。冗談を言ったまでだ。なぜ殺した！","許攸はわしの昔馴染み。冗談を言ったまでだ。なぜ殺した！","許褚",},
        [3936] = {6505,"許褚","こいつはあまりに失礼だったんだぞう！おらたちのことも舐め腐るし……だから、我慢ならずに殺してやったんだぞう！","こいつはあまりに失礼だったんだぞう！おらたちのことも舐め腐るし……だから、我慢ならずに殺してやったんだぞう！","曹操",},
        [3937] = {6505,"曹操","なんと乱暴なことを！お前が自ら手厚く葬ってやれ。","なんと乱暴なことを！お前が自ら手厚く葬ってやれ。","許褚",},
        [3938] = {6601,"曹操","すでに冀州を平定した。袁譚、袁尚、袁熙などの消息はないのか?","すでに冀州を平定した。袁譚、袁尚、袁熙などの消息はないのか?","",},
        [3939] = {6601,"主君","袁尚に戦う気はなく、まっすぐ幽州の袁煕のところへ逃げたようです。","袁尚に戦う気はなく、まっすぐ幽州の袁煕のところへ逃げたようです。","曹操",},
        [3940] = {6601,"主君","袁譚は兵を率いて、甘陵、安平、渤海、河間などを奪い、冀州を奪い返そうとしているとのこと。","袁譚は兵を率いて、甘陵、安平、渤海、河間などを奪い、冀州を奪い返そうとしているとのことですわ。","曹操",},
        [3941] = {6601,"曹操","大軍を出し、袁譚を制圧せよ！","大軍を出し、袁譚を制圧せよ！","プレイヤー",},
        [3942] = {6602,"主君","袁譚は劉表に書簡で増援を頼んだようですが、劉表に兵を出す気はなさそうです。我々に敵わないとみて、南皮へ逃げたようですね。","袁譚は劉表に書簡で増援を頼んだようですが、劉表に兵を出す気はなさそうです。我々に敵わないとみて、南皮へ逃げたようですね。","曹操",},
        [3943] = {6602,"曹操","逃がすものか、追え！","逃がすものか、追え！","プレイヤー",},
        [3944] = {6603,"主君","私と曹洪殿で突撃すると、袁譚が待ち構えていました。袁譚は乱戦の中で討ち取りました。","私と曹洪殿で突撃すると、袁譚が待ち構えていました。袁譚は乱戦の中で討ち取りましたわ。","曹操",},
        [3945] = {6603,"主君","郭図は陣が乱れると、慌てて場内に逃げ込もうとしました。私が弓をつがえて濠のところで討つと、人馬ともに落ちていったのです。","郭図は陣が乱れると、慌てて場内に逃げ込もうとしました。私が弓をつがえて濠のところで討つと、人馬ともに落ちていったんです。","曹操",},
        [3946] = {6603,"曹操","よし！揚威将軍、さすがだ！","よし！揚威将軍、さすがだ！","プレイヤー",},
        [3947] = {6603,"曹操","袁譚も既に死した。次は袁尚と袁煕を討つ！","袁譚も既に死した。次は袁尚と袁煕を討つ！","プレイヤー",},
        [3948] = {6604,"曹操","今、袁尚は袁煕に身を寄せておる。これには、どんな策を用いればよい?","今、袁尚は袁煕に身を寄せておる。これには、どんな策を用いればよい?","郭嘉",},
        [3949] = {6604,"郭嘉","袁氏から投降してきた焦触、張南らに攻めさせればよいかと存じます。","袁氏から投降してきた焦触、張南らに攻めさせればよいかと存じます。","曹操",},
        [3950] = {6604,"曹操","よし。焦触、張南、呂曠、呂翔、馬延、それぞれ自軍を率い、三路に分かれて幽州に攻め入れ。","よし。焦触、張南、呂曠、呂翔、馬延、それぞれ自軍を率い、三路に分かれて幽州に攻め入れ。","郭嘉",},
        [3951] = {6604,"曹操","李典、楽進は張燕と合流し、并州を討て。高幹を攻めるのだ。","李典、楽進は張燕と合流し、并州を討て。高幹を攻めるのだ。","郭嘉",},
        [3952] = {6605,"曹洪","高幹はもう後がなく劉表に投じようとしたが、途中で都尉の王琰に殺された。その首が送られてきたぞ。","高幹はもう後がなく劉表に投じようとしたが、途中で都尉の王琰に殺された。その首が送られてきたぞ。","曹操",},
        [3953] = {6605,"主君","袁煕、袁尚は軍が壊滅し、再起の望みもありません。今は、遠い砂漠に逃げているようです。","袁煕、袁尚は軍が壊滅し、再起の望みもありません。今は、遠い砂漠に逃げているようですわ。","曹操",},
        [3954] = {6605,"主君","私が西を攻めれば、その隙に劉備や劉表が許都を攻めるでしょう。","私が西を攻めれば、その隙に劉備や劉表が許都を攻めるでしょう。","曹操",},
        [3955] = {6605,"主君","私が救援に行っても間に合いませんから、被害も深刻になりましょう。軍を戻して、深追いせぬほうが良いかと。","私の救援も間に合わぬでしょうから、被害も深刻になりますわ。軍を戻して、深追いせぬほうが良いかと。","曹操",},
        [3956] = {6605,"郭嘉","揚威将軍の言葉は間違いです。曹操殿は天下に覇を称えているとはいえ、砂漠の者どもは遠いからと安心して、無防備でおります。","揚威将軍の言葉は間違いです。曹操殿は天下に覇を称えているとはいえ、砂漠の者どもは遠いからと安心して、無防備でおります。","曹操",},
        [3957] = {6605,"郭嘉","その無防備に乗じて急襲すれば、必ず打ち破れるでしょう。","その無防備に乗じて急襲すれば、必ず打ち破れるでしょう。","曹操",},
        [3958] = {6605,"郭嘉","しかも袁紹と烏桓の間には恩義があり、袁尚と袁煕の兄弟はまだ生きている。除かぬわけには参りません。","しかも袁紹と烏桓の間には恩義があり、袁尚と袁煕の兄弟はまだ生きている。除かぬわけには参りません。","曹操",},
        [3959] = {6605,"郭嘉","劉表は口だけの人物で、自分でも劉備を使うほどの才がないことは分かっています。","劉表は口だけの人物で、自分でも劉備を使うほどの才がないことは分かっています。","曹操",},
        [3960] = {6605,"郭嘉","重用すれば抑えが利かなくなるでしょうし、軽く扱えば役に立ちませぬから。","重用すれば抑えが利かなくなるでしょうし、軽く扱えば役に立ちませぬから。","曹操",},
        [3961] = {6605,"郭嘉","ゆえに、国を留守にして遠征されても、ご心配には及びませぬ。","ゆえに、国を留守にして遠征されても、ご心配には及びませぬ。","曹操",},
        [3962] = {6605,"曹操","郭嘉の言うことはもっともだ。大小三軍と馬車数千台に、前進を命じる。","郭嘉の言うことはもっともだ。大小三軍と馬車数千台に、前進を命じる。","郭嘉",},
        [3963] = {6606,"曹操","わしが砂漠を平定しようとしたために、そなたを遠路辛い目に遭わせ、病にしてしまった。","わしが砂漠を平定しようとしたために、そなたを遠路辛い目に遭わせ、病にしてしまった。","郭嘉",},
        [3964] = {6606,"曹操","悔やんでも悔やみきれぬ。(郭嘉が、風土が合わず、馬車で病に臥せっているのを見る。)","悔やんでも悔やみきれぬ。(郭嘉が、風土が合わず、馬車で病に臥せっているのを見る。)","郭嘉",},
        [3965] = {6606,"郭嘉","あぁ……丞相の大恩に、万に一つも報いられずこの身朽ちようとは。","あぁ……丞相の大恩に、万に一つも報いられずこの身朽ちようとは。","曹操",},
        [3966] = {6606,"曹操","北方の道は険しい。軍を戻そうと思うがどうだ?","北方の道は険しい。軍を戻そうと思うがどうだ?","郭嘉",},
        [3967] = {6606,"郭嘉","兵は動きの速さが肝心です。今、千里を越えて襲撃しても、兵器や軍糧が多く不利です。","兵は動きの速さが肝心です。今、千里を越えて襲撃しても、兵器や軍糧が多く不利です。","曹操",},
        [3968] = {6606,"郭嘉","軽騎兵を急がせて、まだ防護の不十分な隙に攻撃するのが良いでしょう。","軽騎兵を急がせて、まだ防護の不十分な隙に攻撃するのが良いでしょう。","曹操",},
        [3969] = {6606,"曹操","わかった。そなたは易州で病を治せ。わしが自ら案内役を探そう。","わかった。そなたは易州で病を治せ。わしが自ら案内役を探そう。","郭嘉",},
        [3970] = {6607,"曹操","郭嘉が死ぬとは、天はわしに死ねと言うのか！(曹操が再び易州に来ると、郭嘉は病で世を去っている。)","郭嘉が死ぬとは、天はわしに死ねと言うのか！(曹操が再び易州に来ると、郭嘉は病で世を去っている。)","",},
        [3971] = {6607,"曹操","諸侯の中で、最も年若い郭嘉に後を託そうと思っていたのに、こうも早く逝ってしまうとは……あぁ！胸が張り裂けそうだ！","諸侯の中で、最も年若い郭嘉に後を託そうと思っていたのに、こうも早く逝ってしまうとは……あぁ！胸が張り裂けそうだ！","",},
        [3972] = {6607,"主君","郭嘉殿の遺書がここにあります。書かれているとおりにすれば、遼東は平定できると。","郭嘉殿の遺書がここにあります。書かれているとおりにすれば、遼東は平定できるかと。","曹操",},
        [3973] = {6607,"夏侯惇","遺書には、速やかに遼東を攻めよと書かれているのだな?","遺書には、速やかに遼東を攻めよと書かれているのだな?","プレイヤー",},
        [3974] = {6607,"主君","いいえ。皆の力を煩わさずとも、何日かたてば、公孫康が自ら袁尚と袁煕の首を届けに来ると。","いいえ。皆の力を煩わさずとも、何日かたてば、公孫康が自ら袁尚と袁煕の首を届けに来ると。","夏侯惇",},
        [3975] = {6608,"袁尚","遼東の軍は数万、曹操と覇を争うには十分だ。","遼東の軍は数万、曹操と覇を争うには十分だ。","袁熙",},
        [3976] = {6608,"袁尚","とりあえず身を投じ、後で公孫康を殺して、領地を奪おう。気力を養い中原に打って出れば、河北を取り返せる。","とりあえず身を投じ、後で公孫康を殺して、領地を奪おう。気力を養い中原に打って出れば、河北を取り返せる。","袁熙",},
        [3977] = {6608,"袁熙","おう！私もそう思っていた！","おう！私もそう思っていた！","袁尚",},
        [3978] = {6609,"夏侯惇","遼東から公孫康の使者が来て、宿駅で袁煕と袁尚を斬ったと、二人の首を届けてきた！","遼東から公孫康の使者が来て、宿駅で袁煕と袁尚を斬ったと、二人の首を届けてきた！","曹操",},
        [3979] = {6609,"曹操","やはりな。郭嘉の思ったとおりだ！","やはりな。郭嘉の思ったとおりだ！","夏侯惇",},
        [3980] = {6609,"夏侯惇","どういうことだ?","どういうことだ?","曹操",},
        [3981] = {6609,"主君","遺書にこう書いてあったんです。","遺書にこう書いてあったんです。","夏侯惇",},
        [3982] = {6609,"主君","「袁煕、袁尚が遼東に投じたと聞きましたが、決して追撃しないでください。」","「袁煕、袁尚が遼東に投じたと聞きましたが、決して追撃しないでください。」","夏侯惇",},
        [3983] = {6609,"主君","「公孫康は長らく袁家に併合されることを恐れており、袁家兄弟が身を投じれば、きっと疑います。」","「公孫康は長らく袁家に併合されることを恐れており、袁家兄弟が身を投じれば、きっと疑います。」","夏侯惇",},
        [3984] = {6609,"主君","「ですが、兵を出して攻撃すれば、力を合わせて迎え討つでしょう。急いではなりません。」","「ですが、兵を出して攻撃すれば、力を合わせて迎え討つでしょう。急いではなりません。」","夏侯惇",},
        [3985] = {6609,"主君","「放っておけば、公孫康と袁氏は互いに争うことは目に見えています」と。","「放っておけば、公孫康と袁氏は互いに争うことは目に見えています」と。","夏侯惇",},
        [3986] = {6609,"曹操","天が郭嘉を生み、並み居る英雄の中でも一番の豪傑だった。文武の両道に通じていた！","天が郭嘉を生み、並み居る英雄の中でも一番の豪傑だった。文武の両道に通じていた！","プレイヤー",},
