        [3990] = {6701,"曹操","南方は勢いが盛んだ。攻めるのは難しいのではないか。","南方は勢いが盛んだ。攻めるのは難しいのではないか。","程昱",},
        [3991] = {6701,"荀攸","丞相ほどのご威光があれば、服さぬものはおりませぬ！","丞相ほどのご威光があれば、服さぬものはおりませぬ！","曹操",},
