        [3995] = {6702,"曹操","これは何の予兆だ?(人々が金の光のところに集まり、銅の雀を掘り出す。)","これは何の予兆だ?(人々が金の光のところに集まり、銅の雀を掘り出す。)","荀攸",},
        [3996] = {6702,"荀攸","昔、舜の母は夢に雀が懐に入るのを見て、、舜を生みました。今、銅雀を得たのも、吉兆でしょう。","昔、舜の母は夢に雀が懐に入るのを見て、、舜を生みました。今、銅雀を得たのも、吉兆でしょう。","曹操",},
        [3997] = {6702,"曹操","これはめでたい！高楼を作って祝うぞ！","これはめでたい！高楼を作って祝うぞ！","荀攸",},
        [3998] = {6702,"曹植","高楼を建てるなら、三棟建てねばなりません。中央に高い楼を建て、名を銅雀とします。","高楼を建てるなら、三棟建てねばなりません。中央に高い楼を建て、名を銅雀とします。","曹操",},
        [3999] = {6702,"曹植","左の一棟は玉龍、右の一棟は金鳳という名にするのです。","左の一棟は玉龍、右の一棟は金鳳という名にするのです。","曹操",},
        [4000] = {6702,"曹植","それから二本の渡り廊下を作り、空中を横切るようにすれば、壮観でしょう。","それから二本の渡り廊下を作り、空中を横切るようにすれば、壮観でしょう。","曹操",},
        [4001] = {6702,"曹操","我が息子の言葉は素晴らしい。そのような高楼ができれば、わしも十分楽しめるな！","我が息子の言葉は素晴らしい。そのような高楼ができれば、わしも十分楽しめるな！","曹植",},
        [4002] = {6703,"曹操","わしは袁紹の兵、五、六十万を得、許都へ凱旋した。","わしは袁紹の兵、五、六十万を得、許都へ凱旋した。","",},
        [4003] = {6703,"曹操","南征して劉表を討とうと思うが、諸侯はどう考える?","南征して劉表を討とうと思うが、諸侯はどう考える?","",},
        [4004] = {6703,"主君","大軍は北征から帰ったばかりなのに、また動かすのは良くありません。","大軍は北征から帰ったばかりなのに、また動かすのは良くありません。","曹操",},
        [4005] = {6703,"主君","半年ほど待って鋭気を養えば、劉表、孫権を一気に制圧できるでしょう。","半年ほど待って鋭気を養えば、劉表、孫権を一気に制圧できるでしょう。","曹操",},
        [4006] = {6703,"曹操","揚威将軍の言うとおりだ。兵を分けて屯田させ、今後の出兵に備えよ。","揚威将軍の言うとおりだ。兵を分けて屯田させ、今後の出兵に備えよ。","プレイヤー",},
        [4007] = {6801,"曹操","最近、よく蔡邕のことを思い出すのだ。","最近、よく蔡邕のことを思い出すのだ。","",},
        [4008] = {6801,"曹操","娘、文姫のことも、よく思い出す。博学多才で音律にも精通しておった。","娘、文姫のことも、よく思い出す。博学多才で音律にも精通しておった。","",},
        [4009] = {6801,"曹操","別れて何年にもなるが、文姫は今どこにおるのだ?","別れて何年にもなるが、文姫は今どこにおるのだ?","",},
        [4010] = {6801,"夏侯惇","そうだな、蔡文姫の行方を探ってみよう。","そうだな、蔡文姫の行方を探ってみよう。","曹操",},
        [4011] = {6802,"夏侯惇","蔡文姫の居所が分かったぞ！","蔡文姫の居所が分かったぞ！","曹操",},
