        [4013] = {6802,"夏侯惇","中郎将だった父の蔡邕は、董卓の巻き添えをくって獄死した。董卓の残党が乱を起こしたとき、","中郎将だった父の蔡邕は、董卓の巻き添えをくって獄死した。董卓の残党が乱を起こしたとき、","曹操",},
        [4014] = {6802,"夏侯惇","国境の匈奴が混乱に乗じて、中原一帯を荒らし回った。","国境の匈奴が混乱に乗じて、中原一帯を荒らし回った。","曹操",},
        [4015] = {6802,"夏侯惇","蔡文姫は、他の女どもと共に匈奴にさらわれ、後に左賢王に嫁がされたそうだ。","蔡文姫は、他の女どもと共に匈奴にさらわれ、後に左賢王に嫁がされたそうだ。","曹操",},
        [4016] = {6802,"夏侯惇","幸いにも王の寵愛を得て、二人の子をもうけたらしい。","幸いにも王の寵愛を得て、二人の子をもうけたらしい。","曹操",},
        [4017] = {6802,"曹操","そうか。生きていてくれたか。","そうか。生きていてくれたか。","夏侯惇",},
        [4018] = {6803,"曹操","蔡文姫は匈奴に捕らわれ、左賢王に嫁し、辺境に住むこと十二年だ。","蔡文姫は匈奴に捕らわれ、左賢王に嫁し、辺境に住むこと十二年だ。","",},
        [4019] = {6803,"曹操","わしは文姫を許都へ連れ戻したい。何か良い方法はないか?","わしは文姫を許都へ連れ戻したい。何か良い方法はないか?","",},
        [4020] = {6803,"主君","当初、袁尚配下の高幹が救い出そうとしましたが、できませんでした。","当初、袁尚配下の高幹が救い出そうとしましたが、できませんでした。","曹操",},
