        [4022] = {6803,"主君","単于の土地は痩せて貧しい。千両の黄金に白璧と交換すれば、盟約を結べるでしょう。","単于の土地は痩せて貧しい。千両の黄金に白璧と交換すれば、盟約を結べましょう。","曹操",},
        [4023] = {6803,"主君","文姫も取り返せるはずです。","文姫も取り返せるはずです。","曹操",},
        [4024] = {6803,"曹操","楊威将軍の言うとおりだ。単于のところへ行き、この重責を果たしてはくれぬか。","楊威将軍の言うとおりだ。単于のところへ行き、この重責を果たしてはくれぬか。","プレイヤー",},
        [4025] = {6803,"主君","では、すぐ参ります。","では、すぐ参ります。","曹操",},
        [4026] = {6804,"蔡文姫","故郷を離れて十二年、今こうして帰ってこられましたのも、曹操様のおかげです。","故郷を離れて十二年、今こうして帰ってこられましたのも、曹操様のおかげです。","曹操",},
        [4027] = {6804,"蔡文姫","救っていただいたこと、心より感謝申し上げます。","救っていただいたこと、心より感謝申し上げます。","曹操",},
        [4028] = {6804,"曹操","頭を上げよ。感謝など無用だ。","頭を上げよ。感謝など無用だ。","蔡文姫",},
        [4029] = {6804,"曹操","そなたの父、蔡邕はわしの旧友だ。かつて、命を救ってやれなかった。今日そなたを救うのは、その償いだ。","そなたの父、蔡邕はわしの旧友だ。かつて、命を救ってやれなかった。今日そなたを救うのは、その償いだ。","蔡文姫",},
        [4030] = {6805,"曹操","蔡文姫の故郷、陳留にはもう知り合いもおらぬ。","蔡文姫の故郷、陳留にはもう知り合いもおらぬ。","蔡文姫",},
        [4031] = {6805,"曹操","ちょうど、わしの配下の董祀も、音律を解し、史書に通じた才気ある男だ。","ちょうど、わしの配下の董祀も、音律を解し、史書に通じた才気ある男だ。","蔡文姫",},
