        [4033] = {6805,"蔡文姫","丞相のご好意、感謝申し上げます。","丞相のご好意、感謝申し上げます。","曹操",},
        [4034] = {6901,"曹操","伝令を出せ。司馬懿を文学掾として召し抱える。","伝令を出せ。司馬懿を文学掾として召し抱える。","",},
        [4035] = {6901,"曹操","奴がまた前のように、病の振りをして家から出てこないときは……","奴がまた前のように、病の振りをして家から出てこないときは……","",},
        [4036] = {6901,"曹操","捕えて投獄してしまえ！","捕えて投獄してしまえ！","",},
        [4037] = {6902,"張春華","丞相があなたを文学掾として召し抱えるそうよ。仮病ももう見破られていますわ。","丞相があなたを文学掾として召し抱えるそうよ。仮病ももう見破られていますわ。","司馬懿",},
        [4038] = {6902,"張春華","これ以上出仕しないつもりなら、投獄すると脅してきました。","これ以上出仕しないつもりなら、投獄すると脅してきました。","司馬懿",},
        [4039] = {6902,"司馬懿","やれやれ、分かったよ、出仕すれば良いのだろう。","やれやれ、分かったよ、出仕すれば良いのだろう。","張春華",},
        [4040] = {6903,"曹操","わしはしばらく観察したが、司馬懿は胸に大志があり、狼顧の相がある。","わしはしばらく観察したが、司馬懿は胸に大志があり、狼顧の相がある。","曹丕",},
