        [4043] = {6903,"曹丕","はい、肝に銘じます。","はい、肝に銘じます。","曹操",},
        [4044] = {7001,"曹操","わしの頭痛は耐えがたい。あちこちに名医を求めたが、治らぬ。","わしの頭痛は耐えがたい。あちこちに名医を求めたが、治らぬ。","プレイヤー",},
        [4045] = {7001,"曹操","揚威将軍は諸国を周遊しておるのだろう。名医を知らぬか?","揚威将軍は諸国を周遊しておるのだろう。名医を知らぬか?","プレイヤー",},
        [4046] = {7001,"主君","神医、華佗をご存知ではありませんか?","神医、華佗をご存知ではありません?","曹操",},
        [4047] = {7001,"曹操","江東で、周泰を治した者か?","江東で、周泰を治した者か?","プレイヤー",},
        [4048] = {7001,"主君","そうです。","そうです。","曹操",},
        [4049] = {7001,"曹操","名は聞き及んでおるが、その医術は知らぬ。","名は聞き及んでおるが、その医術は知らぬ。","プレイヤー",},
        [4050] = {7001,"主君","伝説の名医、扁鵲や、倉公にも匹敵します。","伝説の名医、扁鵲や、倉公にも匹敵します。","曹操",},
        [4051] = {7001,"曹操","今、どこにおるのだ?","今、どこにおるのだ?","プレイヤー",},
        [4052] = {7001,"主君","今は金城にいます。ここから、そう遠くありません。","今は金城にいます。ここから、そう遠くありませんわ。","曹操",},
        [4053] = {7001,"曹操","なんという幸運！すぐに呼んでくれ！","なんという幸運！すぐに呼んでくれ！","プレイヤー",},
        [4054] = {7002,"曹操","華佗よ、わしは頭が痛くてたまらぬ。早く見てくれ。","華佗よ、わしは頭が痛くてたまらぬ。早く見てくれ。","華佗",},
        [4055] = {7002,"華佗","この頭痛は、風がもとで起きています。病根が頭の中にあるので、このしこりを出さないと、いくら薬湯を飲んでも治りません。","この頭痛は、風がもとで起きています。病根が頭の中にあるので、このしこりを出さないと、いくら薬湯を飲んでも治りません。","曹操",},
        [4056] = {7002,"華佗","まず麻肺湯を飲んでいただき、それから頭を切り開いて風のしこりを取り出せば、根治できます。","まず麻肺湯を飲んでいただき、それから頭を切り開いて風のしこりを取り出せば、根治できます。","曹操",},
        [4057] = {7002,"曹操","お前はわしを殺そうというのか！","お前はわしを殺そうというのか！","華佗",},
        [4058] = {7002,"華佗","曹操殿は、関羽殿が毒矢に当たって右腕を怪我したことはご存知ですか。","曹操殿は、関羽殿が毒矢に当たって右腕を怪我したことはご存知ですか。","曹操",},
        [4059] = {7002,"華佗","その際、骨を削り毒を除きましたが、関羽殿は恐れの色など見せませんでした。","その際、骨を削り毒を除きましたが、関羽殿は恐れの色など見せませんでした。","曹操",},
        [4060] = {7002,"華佗","それに比べれば、曹操殿の病は大したものではありません。何を疑われるのです。","それに比べれば、曹操殿の病は大したものではありません。何を疑われるのです。","曹操",},
        [4061] = {7002,"曹操","腕は削れるだろうが、頭を切り開くことなどできるものか！","腕は削れるだろうが、頭を切り開くことなどできるものか！","華佗",},
        [4062] = {7002,"曹操","お前は関羽と親しいのであろう。さてはこの機に仇を討つ気だな！","お前は関羽と親しいのであろう。さてはこの機に仇を討つ気だな！","華佗",},
        [4063] = {7002,"曹操","誰か、早くこの者を捕えよ！","誰か、早くこの者を捕えよ！","華佗",},
        [4064] = {7002,"主君","このような名医は二人といません。殺してはなりません。","このような名医は二人といません。殺してはなりませんわ。","曹操",},
        [4065] = {7002,"曹操","機に乗じて、わしを害そうとしたのだ。吉平と変わりはない！","機に乗じて、わしを害そうとしたのだ。吉平と変わりはない！","プレイヤー",},
        [4066] = {7002,"曹操","捕えるのだ！","捕えるのだ！","プレイヤー",},
        [4067] = {7003,"華佗","揚威将軍、感謝しますよ。毎日獄卒に化けて見舞いに来て、差し入れまでしてくれて。","揚威将軍、感謝しますよ。毎日獄卒に化けて見舞いに来て、差し入れまでしてくれて。","プレイヤー",},
        [4068] = {7003,"主君","感謝などとんでもない。私のせいで牢獄に入れられたのですから。","感謝などとんでもない。私のせいで牢獄に入れられたのですもの。","華佗",},
        [4069] = {7003,"華佗","世の理は見通せないものです。自分を責めないでください。","世の理は見通せないものです。自分を責めないでください。","プレイヤー",},
        [4070] = {7003,"華佗","死ぬのは恐くないですが、私の医術を記した「青嚢書」を世に伝えられないのが心残りですね。","死ぬのは恐くないですが、私の医術を記した「青嚢書」を世に伝えられないのが心残りですね。","プレイヤー",},
        [4071] = {7003,"華佗","手紙を書きますから、わが家に届けてもらって、「青嚢書」を受け取ってください。将軍には私の医術を継いでもらいたいのです。","手紙を書きますから、わが家に届けてもらって、「青嚢書」を受け取ってください。将軍には私の医術を継いでもらいたいのです。","プレイヤー",},
        [4072] = {7003,"主君","あなたの医術は、ご自分で後継者に伝えてください。私が助けてみせます。","あなたの医術は、ご自分で後継者に伝えてください。私が助けてみせますわ。","華佗",},
        [4073] = {7004,"主君","身代わりを探します。世間には華佗は獄中で死んだと思わせましょう。今後は華佗と名乗ってはなりません。","身代わりを探します。世間には華佗は獄中で死んだと思わせましょう。今後は華佗と名乗ってはなりませんよ。","華佗",},
        [4074] = {7004,"主君","人をやって準備を整えておきました。華佗殿は荊襄に隠居し、これからも医術で世を救ってください。","人をやって準備を整えておきました。華佗殿は荊襄に隠居し、これからも医術で世を救ってくださいね。","華佗",},
        [4075] = {7004,"華佗","ああ、揚威将軍、助けて頂いてかたじけない。このことは肝に命じ、必ずや医術で世に恵みをもたらしましょう。","ああ、揚威将軍、助けて頂いてかたじけない。このことは肝に命じ、必ずや医術で世に恵みをもたらしましょう。","プレイヤー",},
        [4076] = {7004,"華佗","くれぐれも体を大切に！","くれぐれも体を大切に！","プレイヤー",},
        [4077] = {7004,"主君","はい。先生もお気を付けて！","ええ。先生もお気を付けて！","華佗",},
        [4078] = {7101,"劉表","降伏してきた張武と陳孫が、江夏郡で略奪を働き、謀反を企てておるという。","降伏してきた張武と陳孫が、江夏郡で略奪を働き、謀反を企てておるという。","劉備",},
        [4079] = {7101,"劉表","この賊二人の被害は大きいぞ！","この賊二人の被害は大きいぞ！","劉備",},
        [4080] = {7101,"劉備","私が荊州へ来てから、劉表殿にはよくして頂いています。","私が荊州へ来てから、劉表殿にはよくして頂いています。","劉表",},
        [4081] = {7101,"劉備","二人の賊についてはご心配なく。この劉備が討って参りましょう。","二人の賊についてはご心配なく。この劉備が討って参りましょう。","劉表",},
        [4082] = {7101,"劉表","よし！三万の兵を率いて、すぐに行ってくれ。","よし！三万の兵を率いて、すぐに行ってくれ。","劉備",},
        [4083] = {7102,"劉備","先陣にいる江夏の匪賊の中でも、張武は目立っているな。","先陣にいる江夏の匪賊の中でも、張武は目立っているな。","",},
        [4084] = {7102,"劉備","奴の乗っている馬は、素晴らしい駿馬だ。千里を駆ける馬に違いない。","奴の乗っている馬は、素晴らしい駿馬だ。千里を駆ける馬に違いない。","",},
        [4085] = {7102,"主君","どんなに馬が良くても、乗っている人間が問題でしょう。私が行って、張武がどれほどの力か見てやります。","どんなに馬が良くても、乗ってる人間が問題でしょう。私が行って、張武がどれほどの力か見てやります。","劉備",},
        [4086] = {7102,"張飛","よっしゃ！張武は任せたぜ。陳孫はオレがやる！","よっしゃ！張武は任せたぜ。陳孫はオレがやる！","プレイヤー",},
        [4087] = {7102,"主君","はい！","はい！","張飛",},
        [4088] = {7103,"蔡瑁","劉備は三人の大将を外に置き、自らは荊州に居座っている。いずれ必ずわざわいとなろう。","劉備は三人の大将を外に置き、自らは荊州に居座っている。いずれ必ずわざわいとなろう。","蔡夫人",},
        [4089] = {7103,"蔡瑁","そなたから劉表殿に言ってくれ。","そなたから劉表殿に言ってくれ。","蔡夫人",},
        [4090] = {7103,"蔡夫人","わかりました","わかりました","蔡瑁",},
        [4091] = {7104,"蔡夫人","荊州では多くの人が劉備と付き合いがあると聞きました。警戒した方が良いですわ。","荊州では多くの人が劉備と付き合いがあると聞きました。警戒した方が良いですわ。","劉表",},
        [4092] = {7104,"蔡夫人","これ以上城内に住まわせていても、なんの益もありません。他所へやってしまってくださいまし。","これ以上城内に住まわせていても、なんの益もありません。他所へやってしまってくださいまし。","劉表",},
        [4093] = {7104,"劉表","なにを言うか。劉備は仁徳ある人だぞ。","なにを言うか。劉備は仁徳ある人だぞ。","蔡夫人",},
        [4094] = {7104,"蔡夫人","あなたの思っているような人ならいいですけど。","あなたの思っているような人ならいいですけど。","劉表",},
        [4095] = {7105,"蒯越","今乗っておられる馬は、どこから手に入れたのです?","今乗っておられる馬は、どこから手に入れたのです?","劉表",},
        [4096] = {7105,"劉表","劉備から贈られたものだ。","劉備から贈られたものだ。","",},
        [4097] = {7105,"蒯越","そうですか。兄の蒯良は馬の相を見る名人でして、私にも心得がありますが……この馬は目の下に涙槽があり、額に白い点があります。","そうですか。兄の蒯良は馬の相を見る名人でして、私にも心得がありますが……この馬は目の下に涙槽があり、額に白い点があります。","劉表",},
        [4098] = {7105,"蒯越","的盧と言って、乗る者を害する馬ですぞ。","的盧と言って、乗る者を害する馬ですぞ。","劉表",},
        [4099] = {7105,"蒯越","張武はこの馬のせいで死んだのです。殿もこれに乗ってはなりませぬ。","張武はこの馬のせいで死んだのです。殿もこれに乗ってはなりませぬ。","劉表",},
        [4100] = {7106,"劉表","昨日は良馬を頂き、かたじけなかった。だが劉備殿はたびたび征伐に出られるから、馬が必要だろう。謹んでお返ししよう。","昨日は良馬を頂き、かたじけなかった。だが劉備殿はたびたび征伐に出られるから、馬が必要だろう。謹んでお返ししよう。","劉備",},
        [4101] = {7106,"劉備","それは感謝いたします。","それは感謝いたします。","劉表",},
        [4102] = {7106,"劉表","ここに長くおられて、武芸が廃れたのではないか。","ここに長くおられて、武芸が廃れたのではないか。","劉備",},
        [4103] = {7106,"劉表","襄陽の管内の新野県には、兵糧も金もたくさんある。手勢を率いて、新野県に駐屯してはいかがかな?","襄陽の管内の新野県には、兵糧も金もたくさんある。手勢を率いて、新野県に駐屯してはいかがかな?","劉備",},
        [4104] = {7106,"劉備","そうですね。ではすぐに参ります。","そうですね。ではすぐに参ります。","劉表",},
        [4105] = {7107,"伊籍","この馬に、乗ってはなりません。","この馬に、乗ってはなりません。","劉備",},
        [4106] = {7107,"伊籍","昨日蒯越が劉表殿に言っていました。","昨日蒯越が劉表殿に言っていました。","劉備",},
        [4107] = {7107,"伊籍","この馬は的盧といい、乗るものを害すと。だから劉備殿に帰したのです。なぜまた乗っておられるのです?","この馬は的盧といい、乗るものを害すと。だから劉備殿に帰したのです。なぜまた乗っておられるのです?","劉備",},
        [4108] = {7107,"劉備","ご厚意、痛み入ります。しかし人の生死は運命で、馬に害せられることなどありませんよ。","ご厚意、痛み入ります。しかし人の生死は運命で、馬に害せられることなどありませんよ。","伊籍",},
        [4109] = {7107,"伊籍","これは高いご見識、感服いたします。","これは高いご見識、感服いたします。","劉備",},
        [4110] = {7108,"劉備","今、曹操の兵は全て北方に出征し、許昌は空です。荊襄の軍勢で、この隙に襲撃すれば、きっと成功します。","今、曹操の兵は全て北方に出征し、許昌は空です。荊襄の軍勢で、この隙に襲撃すれば、きっと成功します。","劉表",},
        [4111] = {7108,"劉表","わしは九郡を領有すれば十分だ。なぜ他の地を奪う必要がある?","わしは九郡を領有すれば十分だ。なぜ他の地を奪う必要がある?","劉備",},
        [4112] = {7108,"劉表","そなたは許昌で曹操と、青梅を肴に酒を飲んで、英雄を論じたというではないか。","そなたは許昌で曹操と、青梅を肴に酒を飲んで、英雄を論じたというではないか。","劉備",},
        [4113] = {7108,"劉表","当世の名士の名をあげても、曹操はすべて違うと言い、天下の英雄はそなたと曹操だけだと言ったそうだな。","当世の名士の名をあげても、曹操はすべて違うと言い、天下の英雄はそなたと曹操だけだと言ったそうだな。","劉備",},
        [4114] = {7108,"劉表","曹操の権力をもってしても、そなたを差し置いてはおけないのだ。功績を挙げられないと恐れることはないだろう?","曹操の権力をもってしても、そなたを差し置いてはおけないのだ。功績を挙げられないと恐れることはないだろう?","劉備",},
        [4115] = {7108,"劉備","そうですね。私に確固とした基盤があれば、天下の凡庸な輩など、本当に恐るるに足りないのですが。","そうですね。私に確固とした基盤があれば、天下の凡庸な輩など、本当に恐るるに足りないのですが。","劉表",},
        [4116] = {7109,"蔡夫人","昨晩は劉備とお話なさったのですね。","昨晩は劉備とお話なさったのですね。","劉表",},
        [4117] = {7109,"蔡夫人","屏風の後ろで聞いていましたが、人を馬鹿にしています。","屏風の後ろで聞いていましたが、人を馬鹿にしています。","劉表",},
        [4118] = {7109,"蔡夫人","荊州を我が物にしようとしているのですわ。今、除かねば、きっと後で災いになります。","荊州を我が物にしようとしているのですわ。今、除かねば、きっと後で災いになります。","劉表",},
        [4119] = {7109,"劉表","いかん。劉備は仁徳ある人だ。決して殺してはならぬ。","いかん。劉備は仁徳ある人だ。決して殺してはならぬ。","蔡夫人",},
        [4120] = {7110,"蔡夫人","何度も劉表殿に劉備を殺すように勧めたけれど、うんと言わないのです。","何度も劉表殿に劉備を殺すように勧めたけれど、うんと言わないのです。","蔡瑁",},
        [4121] = {7110,"蔡夫人","あなたが宿舎を襲って殺してくださいな。劉表殿には後で報告しましょう。","あなたが宿舎を襲って殺してくださいな。劉表殿には後で報告しましょう。","蔡瑁",},
        [4122] = {7110,"蔡瑁","分かった、すぐに行こう。","分かった、すぐに行こう。","蔡夫人",},
        [4123] = {7111,"伊籍","大変です！蔡瑁があなたを殺そうとしています。早く逃げてください。","大変です！蔡瑁があなたを殺そうとしています。早く逃げてください。","劉備",},
        [4124] = {7111,"劉備","劉表殿に挨拶をしていないのに、行くわけにはいかない。","劉表殿に挨拶をしていないのに、行くわけにはいかない。","伊籍",},
        [4125] = {7111,"伊籍","そんなことを言っていると、蔡瑁に殺されます。","そんなことを言っていると、蔡瑁に殺されます。","劉備",},
        [4126] = {7111,"劉備","やむを得ないか……助けていただき、かたじけない。すぐにここを離れます。","やむを得ないか……助けていただき、かたじけない。すぐにここを離れます。","伊籍",},
        [4127] = {7112,"蔡瑁","劉備は謀反を起こす気です。壁に謀反の詩を書いて、辞去の挨拶もせず逃げました。","劉備は謀反を起こす気です。壁に謀反の詩を書いて、辞去の挨拶もせず逃げました。","劉表",},
        [4128] = {7112,"劉表","どんな詩だ?","どんな詩だ?","蔡瑁",},
        [4129] = {7112,"蔡瑁","「数年徒に幽閉され、山河に空しく向かうのみ。龍は池に住むものにあらず、雷に乗り天に昇らん」と。","「数年徒に幽閉され、山河に空しく向かうのみ。龍は池に住むものにあらず、雷に乗り天に昇らん」と。","劉表",},
        [4130] = {7112,"劉表","わしは劉備と長年一緒にいたが、詩を作るのなど見たことがない。誰かが我らの間を裂こうとしているのだろう。","わしは劉備と長年一緒にいたが、詩を作るのなど見たことがない。誰かが我らの間を裂こうとしているのだろう。","蔡瑁",},
        [4131] = {7113,"蔡瑁","近年豊作が続いています。官吏たちを襄陽に集め、慰労致しましょう。劉表殿もお越しください。","近年豊作が続いています。官吏たちを襄陽に集め、慰労致しましょう。劉表殿もお越しください。","劉表",},
        [4132] = {7113,"劉表","わしは最近、気の病が出て、行けぬ。息子たちに賓客のもてなしをさせたらどうだ。","わしは最近、気の病が出て、行けぬ。息子たちに賓客のもてなしをさせたらどうだ。","蔡瑁",},
        [4133] = {7113,"蔡瑁","若君がたはまだ幼いので、礼儀上よろしくないのではと。","若君がたはまだ幼いので、礼儀上よろしくないのではと。","劉表",},
        [4134] = {7113,"劉表","では新野に行って、劉備に客のもてなしを頼めばよい。","では新野に行って、劉備に客のもてなしを頼めばよい。","蔡瑁",},
        [4135] = {7113,"蔡瑁","かしこまりました。(劉表が自分の計略に乗ったことを内心喜び、使者を出して劉備を襄陽へ呼び寄せる)","かしこまりました。(劉表が自分の計略に乗ったことを内心喜び、使者を出して劉備を襄陽へ呼び寄せる)","劉表",},
        [4136] = {7114,"伊籍","蔡瑁が劉備殿を襄陽へ向かわせようとしているのは計略で、本当は殺そうと企んでいるのです。","蔡瑁が劉備殿を襄陽へ向かわせようとしているのは計略で、本当は殺そうと企んでいるのです。","劉備",},
        [4137] = {7114,"伊籍","東、南、北の三つの城門は軍馬で固められていますが、西門だけは通れます。早くお逃げください！","東、南、北の三つの城門は軍馬で固められていますが、西門だけは通れます。早くお逃げください！","劉備",},
        [4138] = {7114,"劉備","西門ですね、かたじけない！的盧、逃げるぞ。","西門ですね、かたじけない！的盧、逃げるぞ。","伊籍",},
        [4139] = {7115,"蔡瑁","おや劉備殿、どうして席を外されるのか?","おや劉備殿、どうして席を外されるのか?","劉備",},
        [4140] = {7115,"劉備","そなたとの間に恨みはない。なぜ害そうとする?","そなたとの間に恨みはない。なぜ害そうとする?","蔡瑁",},
        [4141] = {7115,"劉備","的盧！どう！(的盧は三丈もの檀渓を一跳び、西岸へ飛び移る。ここから「檀渓を馬で跳ぶ」という言葉ができた。)","的盧！どう！(的盧は三丈もの檀渓を一跳び、西岸へ飛び移る。ここから「檀渓を馬で跳ぶ」という言葉ができた。)","蔡瑁",},
        [4142] = {7201,"牧童","もしや将軍は、黄巾賊を破ったという劉備様ではありませんか?","もしや将軍は、黄巾賊を破ったという劉備様ではありませんか?","劉備",},
        [4143] = {7201,"劉備","そなたのような村の童子が、なぜ私の名前を知っているのだ?","そなたのような村の童子が、なぜ私の名前を知っているのだ?","牧童",},
        [4144] = {7201,"牧童","僕は知りませんでしたが、いつも師匠の傍にいるので、お客様が来ると劉備様のお名前が良く出るのです。","僕は知りませんでしたが、いつも師匠の傍にいるので、お客様が来ると劉備様のお名前が良く出るのです。","劉備",},
        [4145] = {7201,"牧童","身の丈は七尺五寸あって、手を垂らすと膝下まで届き、首を巡らすと自分の耳が見られる福相で、当世の英雄だって。","身の丈は七尺五寸あって、手を垂らすと膝下まで届き、首を巡らすと自分の耳が見られる福相で、当世の英雄だって。","劉備",},
        [4146] = {7201,"牧童","今、将軍のお姿を見るとその通りなので、きっとそうだと思いました。","今、将軍のお姿を見るとその通りなので、きっとそうだと思いました。","劉備",},
        [4147] = {7201,"劉備","そなたの師匠は何者だ?","そなたの師匠は何者だ?","牧童",},
        [4148] = {7201,"牧童","師匠の姓は司馬、名は徽、字は德操。潁川の出身で、道号を水鏡先生といいます。","師匠の姓は司馬、名は徽、字は德操。潁川の出身で、道号を水鏡先生といいます。","劉備",},
        [4149] = {7201,"劉備","師匠は今、どこにおられる?","師匠は今、どこにおられる?","牧童",},
        [4150] = {7201,"牧童","前に見える林の中です。あの庵です。","前に見える林の中です。あの庵です。","劉備",},
        [4151] = {7201,"劉備","私は確かに劉備だ。そなたの師匠に引き合わせてくれないか。","私は確かに劉備だ。そなたの師匠に引き合わせてくれないか。","牧童",},
        [4152] = {7202,"司馬徽","劉備殿はなぜここへ?","劉備殿はなぜここへ?","劉備",},
        [4153] = {7202,"劉備","偶然この地を通りかかり、童子に案内してもらったのです。ご尊顔を拝し、恐悦至極に存じます。","偶然この地を通りかかり、童子に案内してもらったのです。ご尊顔を拝し、恐悦至極に存じます。","司馬徽",},
        [4154] = {7202,"司馬徽","隠すことはありません。お顔を拝見するに、難を逃れておいでになったのでしょう。","隠すことはありません。お顔を拝見するに、難を逃れておいでになったのでしょう。","劉備",},
        [4155] = {7202,"劉備","これは、お恥ずかしい。","これは、お恥ずかしい。","司馬徽",},
        [4156] = {7202,"司馬徽","お噂はかねがね伺っております。なぜ未だに不遇の身でおられるのです?","お噂はかねがね伺っております。なぜ未だに不遇の身でおられるのです?","劉備",},
        [4157] = {7202,"劉備","色々思い通りにいかないことが多く、このような事になりました。","色々思い通りにいかないことが多く、このような事になりました。","司馬徽",},
        [4158] = {7202,"司馬徽","もしくは、将軍の左右に人材がいないからでしょう。","もしくは、将軍の左右に人材がいないからでしょう。","劉備",},
        [4159] = {7202,"劉備","私は不肖の輩ながら、文には孫乾、糜竺、簡雍があり、武には関羽、張飛、趙雲がおります。","私は不肖の輩ながら、文には孫乾、糜竺、簡雍があり、武には関羽、張飛、趙雲がおります。","司馬徽",},
        [4160] = {7202,"劉備","忠義に厚い補佐を得て、大いにその力に頼っています。","忠義に厚い補佐を得て、大いにその力に頼っています。","司馬徽",},
        [4161] = {7202,"司馬徽","関羽、張飛、趙雲らは一騎当千の勇将ですが、使いこなせる人材がいません。孫乾、糜竺らは白面の書生で、世を救う経綸の士ではない。","関羽、張飛、趙雲らは一騎当千の勇将ですが、使いこなせる人材がいません。孫乾、糜竺らは白面の書生で、世を救う経綸の士ではない。","劉備",},
        [4162] = {7202,"劉備","それはごもっともです。私もまた身を屈して山野に賢人を求めていますが、いかんせん、まだ巡り合えておりません。","それはごもっともです。私もまた身を屈して山野に賢人を求めていますが、いかんせん、まだ巡り合えておりません。","司馬徽",},
        [4163] = {7202,"司馬徽","孔子も言っているではないですか。「十室の邑には必ず忠信の人あり」。なぜ人材がいないなどと言うのです?","孔子も言っているではないですか。「十室の邑には必ず忠信の人あり」。なぜ人材がいないなどと言うのです?","劉備",},
        [4164] = {7202,"劉備","私は愚かなため知りません。どうか教えを賜りたい。","私は愚かなため知りません。どうか教えを賜りたい。","司馬徽",},
        [4165] = {7202,"司馬徽","近頃、襄陽のあちこちで子供らが歌っている歌をご存じですか?「八、九年間はじめて衰えんとす。十三年に至り孑遺なし」","近頃、襄陽のあちこちで子供らが歌っている歌をご存じですか?「八、九年間はじめて衰えんとす。十三年に至り孑遺なし」","劉備",},
        [4166] = {7202,"司馬徽","「ついには天命、帰すところあり、泥中の蟠龍、天に向かい飛ぶ」。","「ついには天命、帰すところあり、泥中の蟠龍、天に向かい飛ぶ」。","劉備",},
        [4167] = {7202,"司馬徽","建安八年に劉表殿は前の夫人をなくし、家は乱れた。これが「はじめて衰えんとす」の意味です。","建安八年に劉表殿は前の夫人をなくし、家は乱れた。これが「はじめて衰えんとす」の意味です。","劉備",},
        [4168] = {7202,"司馬徽","「孑遺なし」とは、劉表殿がまもなく世を去られるが、文武ともに衰退し後を継ぐ者がいないということです。","「孑遺なし」とは、劉表殿がまもなく世を去られるが、文武ともに衰退し後を継ぐ者がいないということです。","劉備",},
        [4169] = {7202,"司馬徽","「天命、帰すところあり、龍が天に向かって飛ぶ」とは将軍のことに違いありません。","「天命、帰すところあり、龍が天に向かって飛ぶ」とは将軍のことに違いありません。","劉備",},
        [4170] = {7202,"劉備","失礼ながら、水鏡先生は私を買いかぶっておられる。そのような大役は務まりません。","失礼ながら、水鏡先生は私を買いかぶっておられる。そのような大役は務まりません。","司馬徽",},
        [4171] = {7202,"司馬徽","今、天下の奇才はこの地に集まっています。彼らをお召しになればよいでしょう。","今、天下の奇才はこの地に集まっています。彼らをお召しになればよいでしょう。","劉備",},
        [4172] = {7202,"劉備","そのような人材がいるのですか?どちらに?","そのような人材がいるのですか?どちらに?","司馬徽",},
        [4173] = {7202,"司馬徽","もう夜も遅い。将軍はここに一晩お泊りになると良い。明日お話しましょう。","もう夜も遅い。将軍はここに一晩お泊りになると良い。明日お話しましょう。","劉備",},
        [4174] = {7203,"司馬徽","徐庶、なぜ深夜に来た?","徐庶、なぜ深夜に来た?","徐庶",},
        [4175] = {7203,"徐庶","劉表は善悪をわきまえた人物と聞いたのでわざわざ会いに行きましたが、まるで違っていました。","劉表は善悪をわきまえた人物と聞いたのでわざわざ会いに行きましたが、まるで違っていました。","司馬徽",},
        [4176] = {7203,"徐庶","善い人物がいても使うことができず、悪人がいても追い出すこともできない。なので書簡を残して出てきました。それでここへ来たのです。","善い人物がいても使うことができず、悪人がいても追い出すこともできない。なので書簡を残して出てきました。それでここへ来たのです。","司馬徽",},
        [4177] = {7203,"司馬徽","そなたは王佐の才の持ち主。仕える人物は選ぶべきだ。劉表では釣り合わん。英雄豪傑はもっと目の前にいるはずだぞ。","そなたは王佐の才の持ち主。仕える人物は選ぶべきだ。劉表では釣り合わん。英雄豪傑はもっと目の前にいるはずだぞ。","徐庶",},
        [4178] = {7203,"徐庶","先生のお言葉のとおりです。","先生のお言葉のとおりです。","司馬徽",},
        [4179] = {7204,"劉備","昨夜いらしてたのは、どなたです?","昨夜いらしてたのは、どなたです?","司馬徽",},
        [4180] = {7204,"司馬徽","私の友人です。","私の友人です。","劉備",},
        [4181] = {7204,"劉備","お会いできませんか?","お会いできませんか?","司馬徽",},
        [4182] = {7204,"司馬徽","残念ですが、良い主君を求めて、もうどこかへ行ってしまいました。","残念ですが、良い主君を求めて、もうどこかへ行ってしまいました。","劉備",},
        [4183] = {7204,"劉備","名は何と仰いますか?","名は何と仰いますか?","司馬徽",},
        [4184] = {7204,"司馬徽","ははは...善きかな、善きかな。(笑って答えない)","ははは...善きかな、善きかな。(笑って答えない)","劉備",},
        [4185] = {7204,"劉備","では、伏龍、鳳雛とは、一体誰のことなのです?","では、伏龍、鳳雛とは、一体誰のことなのです?","司馬徽",},
        [4186] = {7204,"司馬徽","善きかな、善きかな。(笑って答えない)","善きかな、善きかな。(笑って答えない)","劉備",},
        [4187] = {7204,"劉備","では、水鏡先生が山を出て助けてくださいませんか。共に漢王朝を支えましょう。","では、水鏡先生が山を出て助けてくださいませんか。共に漢王朝を支えましょう。","司馬徽",},
        [4188] = {7204,"司馬徽","私は山野の閑人です。世の役には立ちませんよ。","私は山野の閑人です。世の役には立ちませんよ。","劉備",},
        [4189] = {7204,"司馬徽","私より遥かに優れた人物が、将軍をお助けするでしょう。そういう人をお尋ねになるとよい。","私より遥かに優れた人物が、将軍をお助けするでしょう。そういう人をお尋ねになるとよい。","劉備",},
        [4190] = {7205,"牧童","数百人を率いた将軍が、こちらへ来ます。","数百人を率いた将軍が、こちらへ来ます。","劉備",},
        [4191] = {7205,"劉備","そこへ来るのは何者だ?","そこへ来るのは何者だ?","牧童",},
        [4192] = {7205,"趙雲","私です、趙雲です。","私です、趙雲です。","劉備",},
        [4193] = {7205,"趙雲","夜に新野県に帰ると、我が君がおられないので、夜を徹して尋ねまわり、ここへ着きました。","夜に新野県に帰ると、我が君がおられないので、夜を徹して尋ねまわり、ここへ着きました。","劉備",},
        [4194] = {7205,"趙雲","早く県へお帰りください。道中、刺客が現れるかもしれません。お気をつけて。","早く県へお帰りください。道中、刺客が現れるかもしれません。お気をつけて。","劉備",},
        [4195] = {7205,"劉備","わかった。すぐに新野に帰ろう。","わかった。すぐに新野に帰ろう。","趙雲",},
        [4196] = {7206,"徐庶","天地覆り、火は消えんとす。屋敷崩れんとするや、一木支え難し。","天地覆り、火は消えんとす。屋敷崩れんとするや、一木支え難し。","劉備",},
        [4197] = {7206,"徐庶","山谷に賢者あり、名君に使えんと欲す。名君、賢者を求むれど、我を知らず。","山谷に賢者あり、名君に使えんと欲す。名君、賢者を求むれど、我を知らず。","劉備",},
        [4198] = {7206,"劉備","この人が、水鏡先生の言われた伏龍、鳳雛だろうか?","この人が、水鏡先生の言われた伏龍、鳳雛だろうか?","徐庶",},
        [4199] = {7206,"徐庶","私は潁上の出身で、姓は単、名は福と申します。かねがね将軍が賢者を招いていると聞き、馳せ参じたく思いましたが、","私は潁上の出身で、姓は単、名は福と申します。かねがね将軍が賢者を招いていると聞き、馳せ参じたく思いましたが、","劉備",},
        [4200] = {7206,"徐庶","あいにくツテもありません。そこで街中で歌を歌い、お耳汚しをした次第です。","あいにくツテもありません。そこで街中で歌を歌い、お耳汚しをした次第です。","劉備",},
        [4201] = {7206,"徐庶","お乗りになっていた馬を、もう一度見せては頂けませぬか。","お乗りになっていた馬を、もう一度見せては頂けませぬか。","劉備",},
        [4202] = {7206,"徐庶","これは的盧ではありませんか?千里を走る馬ですが、主人に害を成します。お乗りになってはいけません。","これは的盧ではありませんか?千里を走る馬ですが、主人に害を成します。お乗りになってはいけません。","劉備",},
        [4203] = {7206,"劉備","この馬は私を助け、檀渓を飛び越えました。命を救ってくれたのです。","この馬は私を助け、檀渓を飛び越えました。命を救ってくれたのです。","徐庶",},
        [4204] = {7206,"徐庶","それは主ではなく、自分を救ったにすぎませんな。この先、必ず一度は主に害を成します。ですが私は、それを払う方法を知っています。","それは主ではなく、自分を救ったにすぎませんな。この先、必ず一度は主に害を成します。ですが私は、それを払う方法を知っています。","劉備",},
        [4205] = {7206,"劉備","どうやって払うかお聞きしたい。","どうやって払うかお聞きしたい。","徐庶",},
        [4206] = {7206,"徐庶","あなたが心中恨みを持つ人に、この馬を贈るのです。馬がその者に害を成した後でこれに乗れば、もう大丈夫です。","あなたが心中恨みを持つ人に、この馬を贈るのです。馬がその者に害を成した後でこれに乗れば、もう大丈夫です。","劉備",},
        [4207] = {7206,"劉備","なるほど……あなたの教えは私には合わないようです。初対面の相手に、己のために他人に害することを教えるとは。","なるほど……あなたの教えは私には合わないようです。初対面の相手に、己のために他人に害することを教えるとは。","徐庶",},
        [4208] = {7206,"徐庶","申し訳ありません。今の教えはあなたを試すための嘘です。徳のある人だとは聞いていましたが、すぐには信じられませんでしたので。","申し訳ありません。今の教えはあなたを試すための嘘です。徳のある人だとは聞いていましたが、すぐには信じられませんでしたので。","劉備",},
        [4209] = {7206,"劉備","私など徳があるとは言えません。先生に教えて頂かないと。","私など徳があるとは言えません。先生に教えて頂かないと。","徐庶",},
        [4210] = {7206,"徐庶","潁上からここへ来るまで、新野の人が歌っているのを聞きました。","潁上からここへ来るまで、新野の人が歌っているのを聞きました。","劉備",},
        [4211] = {7206,"徐庶","「新野牧の劉備殿、この地に来られ、民は富む」と。やはりあなたは徳のあるお方だ。","「新野牧の劉備殿、この地に来られ、民は富む」と。やはりあなたは徳のあるお方だ。","劉備",},
        [4212] = {7206,"劉備","先生のご助力を得られて幸いです。今日からあなたを軍師とし、我が軍の人馬を訓練して頂きたい。","先生のご助力を得られて幸いです。今日からあなたを軍師とし、我が軍の人馬を訓練して頂きたい。","徐庶",},
        [4213] = {7301,"徐庶","曹仁が樊城に兵を集めている。大軍でせめて来るに違いありません。","曹仁が樊城に兵を集めている。大軍でせめて来るに違いありません。","劉備",},
        [4214] = {7301,"劉備","どうやって迎え討てば?","どうやって迎え討てば?","徐庶",},
        [4215] = {7301,"徐庶","向こうが全軍を挙げて来るなら、樊城は空になります。これに乗じて奪えばよいでしょう。","向こうが全軍を挙げて来るなら、樊城は空になります。これに乗じて奪えばよいでしょう。","劉備",},
        [4216] = {7301,"劉備","どんな策がある?","どんな策がある?","徐庶",},
        [4217] = {7301,"徐庶","はい。劉備殿、お耳を拝借。","はい。劉備殿、お耳を拝借。","劉備",},
        [4218] = {7302,"劉備","曹軍が太鼓を鳴らして進軍してきた。陣を敷いている。","曹軍が太鼓を鳴らして進軍してきた。陣を敷いている。","徐庶",},
        [4219] = {7302,"劉備","この陣形を知っているのか?","この陣形を知っているのか?","徐庶",},
        [4220] = {7302,"徐庶","八門金鎖の陣です。八門とは、休、生、傷、杜、景、死、驚、開です。","八門金鎖の陣です。八門とは、休、生、傷、杜、景、死、驚、開です。","劉備",},
        [4221] = {7302,"徐庶","生門、景門、開門から入れば吉ですが、傷、休、驚から入れば傷を負い、杜門、死門から入れば亡びます。","生門、景門、開門から入れば吉ですが、傷、休、驚から入れば傷を負い、杜門、死門から入れば亡びます。","劉備",},
        [4222] = {7302,"徐庶","今、八門の布陣は整っていますが、中軍の主導が弱い。南東角の生門から突入し、西の景門から出れば、陣は必ず乱れます。","今、八門の布陣は整っていますが、中軍の主導が弱い。南東角の生門から突入し、西の景門から出れば、陣は必ず乱れます。","劉備",},
        [4223] = {7302,"劉備","陣形を乱すな。揚威将軍は五百の兵を率い、東南から突撃、西の方角へ出よ。","陣形を乱すな。揚威将軍は五百の兵を率い、東南から突撃、西の方角へ出よ。","徐庶",},
        [4224] = {7302,"主君","お任せを！すぐに兵を率いて、八門金鎖の陣を破ってみせましょう。","お任せを！すぐに兵を率いて、八門金鎖の陣を破ってみせますわ。","劉備",},
        [4225] = {7303,"曹仁","俺の陣を破るとは……！劉備の軍にはよほど有能な者がいるに違いない。","俺の陣を破るとは……！劉備の軍にはよほど有能な者がいるに違いない。","李典",},
        [4226] = {7303,"李典","私はここにいても、樊城が心配です。","私はここにいても、樊城が心配です。","曹仁",},
        [4227] = {7303,"曹仁","今夜、夜襲をかけるぞ！勝てば、再び策を練り、負ければ樊城に撤退する。","今夜、夜襲をかけるぞ！勝てば、再び策を練り、負ければ樊城に撤退する。","李典",},
        [4228] = {7303,"李典","いけません、劉備はきっと準備しています。","いけません、劉備はきっと準備しています。","曹仁",},
        [4229] = {7303,"曹仁","慎重が過ぎる！それで兵を使えるか！","慎重が過ぎる！それで兵を使えるか！","李典",},
        [4230] = {7304,"徐庶","今夜、曹仁はきっと夜討ちに来るでしょう。","今夜、曹仁はきっと夜討ちに来るでしょう。","劉備",},
        [4231] = {7304,"劉備","どうやって対抗すればいい?","どうやって対抗すればいい?","徐庶",},
        [4232] = {7304,"徐庶","既に考えてあります。","既に考えてあります。","劉備",},
        [4233] = {7304,"徐庶","関羽殿は急いで樊城の外へ行き、静かに待ち構えてください。曹仁が出てきたら、素早く城を奪うのです。","関羽殿は急いで樊城の外へ行き、静かに待ち構えてください。曹仁が出てきたら、素早く城を奪うのです。","劉備",},
        [4234] = {7304,"徐庶","そのまま城内に留まってください。趙雲殿と揚威将軍は陣の外に隠れ、やってくる曹仁の大軍を奇襲し、川岸まで追い詰めるのです。","そのまま城内に留まってください。趙雲殿と揚威将軍は陣の外に隠れ、やってくる曹仁の大軍を奇襲し、川岸まで追い詰めるのです。","劉備",},
        [4235] = {7304,"徐庶","張飛殿は川岸に潜んで呼応し、共に樊城まで追い返します。関羽殿は樊城から出撃し、最後に敵を取り囲むのです。","張飛殿は川岸に潜んで呼応し、共に樊城まで追い返します。関羽殿は樊城から出撃し、最後に敵を取り囲むのです。","劉備",},
        [4236] = {7304,"劉備","それはいい考えだ。","それはいい考えだ。","徐庶",},
        [4237] = {7305,"曹仁","いかん、劉備はとっくに準備をしていた。敵を攻め落とせぬばかりか、樊城も失った！","いかん、劉備はとっくに準備をしていた。敵を攻め落とせぬばかりか、樊城も失った！","",},
        [4238] = {7305,"曹仁","やむを得ん、許都まで撤退するしかない！","やむを得ん、許都まで撤退するしかない！","",},
        [4239] = {7306,"曹仁","このたびの敗戦と将兵の損失につき、いかなる処罰も受ける覚悟です！","このたびの敗戦と将兵の損失につき、いかなる処罰も受ける覚悟です！","曹操",},
        [4240] = {7306,"曹操","勝敗は戦の常だ。だが、誰が劉備に策を授けたのだ?","勝敗は戦の常だ。だが、誰が劉備に策を授けたのだ?","曹仁",},
        [4241] = {7306,"曹仁","はっ、単福という者にございます。","はっ、単福という者にございます。","曹操",},
        [4242] = {7306,"曹操","何者だ?","何者だ?","曹仁",},
        [4243] = {7306,"程昱","単福というのは、まことの名ではありませぬぞ。","単福というのは、まことの名ではありませぬぞ。","曹操",},
        [4244] = {7306,"程昱","その者は若い頃から剣を好み、人の仇討ちのため人を殺めましての。髪を振り乱し顔に泥を塗って逃げましたが、役人に捕えられたのですじゃ。","その者は若い頃から剣を好み、人の仇討ちのため人を殺めましての。髪を振り乱し顔に泥を塗って逃げましたが、役人に捕えられたのですじゃ。","曹操",},
        [4245] = {7306,"程昱","名前を聞いても答えないため、役人が縛って車に乗せ、太鼓を叩いて市で引き回し、知っている人はいないかと尋ねました。","名前を聞いても答えないため、役人が縛って車に乗せ、太鼓を叩いて市で引き回し、知っている人はいないかと尋ねました。","曹操",},
        [4246] = {7306,"程昱","人々は知っていましたが何も言わず、仲間がこっそり縄を解いて救い出したのですじゃ。","人々は知っていましたが何も言わず、仲間がこっそり縄を解いて救い出したのですじゃ。","曹操",},
        [4247] = {7306,"程昱","そこで名前を変えて逃げ、以来、心を入れ替えて学問に励んだのですじゃ。","そこで名前を変えて逃げ、以来、心を入れ替えて学問に励んだのですじゃ。","曹操",},
        [4248] = {7306,"程昱","各地に名のある師を訪ね歩き、司馬徽とも良く語り合っておったそうです。この者は潁川出身の徐庶、字は元直。単福は仮の名ですじゃ。","各地に名のある師を訪ね歩き、司馬徽とも良く語り合っておったそうです。この者は潁川出身の徐庶、字は元直。単福は仮の名ですじゃ。","曹操",},
        [4249] = {7306,"曹操","徐庶の才は、お前と比べてどうだ?","徐庶の才は、お前と比べてどうだ?","程昱",},
        [4250] = {7306,"程昱","私めの十倍かと。","私めの十倍かと。","曹操",},
        [4251] = {7306,"曹操","それほどの賢士を劉備に召し抱えられたか！奴に翼を与えたようなものではないか！","それほどの賢士を劉備に召し抱えられたか！奴に翼を与えたようなものではないか！","程昱",},
        [4252] = {7306,"程昱","ご安心くだされ。今は敵方におりますが、丞相が用いたいと思われるなら、呼び寄せることは難しくありませんぞ。","ご安心くだされ。今は敵方におりますが、丞相が用いたいと思われるなら、呼び寄せることは難しくありませんぞ。","曹操",},
        [4253] = {7306,"曹操","ほう。どうやるのだ?","ほう。どうやるのだ?","程昱",},
        [4254] = {7306,"程昱","徐庶は非常に孝行者ですじゃ。幼い頃に父を亡くし、老母しかおりませぬ。徐康という弟も亡くなり、他に老母を養う者がおりませぬ。","徐庶は非常に孝行者ですじゃ。幼い頃に父を亡くし、老母しかおりませぬ。徐康という弟も亡くなり、他に老母を養う者がおりませぬ。","曹操",},
        [4255] = {7306,"程昱","そこで、母親をだまして許昌に連れてこさせ、息子を呼び寄せる手紙を書かせれば、徐庶は必ず来るでしょうぞ。","そこで、母親をだまして許昌に連れてこさせ、息子を呼び寄せる手紙を書かせれば、徐庶は必ず来るでしょうぞ。","曹操",},
        [4256] = {7307,"徐庶","実は私は潁川の出で本名は徐庶、字を元直と申します。難を逃れるため、単福と名を変えておりました。","実は私は潁川の出で本名は徐庶、字を元直と申します。難を逃れるため、単福と名を変えておりました。","劉備",},
        [4257] = {7307,"徐庶","劉表殿が広く人材を招いていると聞いて面会に行ったのですが、無用の者と知りましたので、置き手紙をして辞して来ました。","劉表殿が広く人材を招いていると聞いて面会に行ったのですが、無用の者と知りましたので、置き手紙をして辞して来ました。","劉備",},
        [4258] = {7307,"徐庶","その晩遅く、水鏡先生の庵へ行き、その事を訴えましたところ、","その晩遅く、水鏡先生の庵へ行き、その事を訴えましたところ、","劉備",},
        [4259] = {7307,"徐庶","水鏡先生に、主を見る目がないと叱られました。劉備殿がここにおられるのに、なぜ仕えないのかと。","水鏡先生に、主を見る目がないと叱られました。劉備殿がここにおられるのに、なぜ仕えないのかと。","劉備",},
        [4260] = {7307,"徐庶","そこで私は戯れ歌を作り、街中で歌って劉備殿の気を引きました。幸いにもお見捨てにならず、重用を賜りました。","そこで私は戯れ歌を作り、街中で歌って劉備殿の気を引きました。幸いにもお見捨てにならず、重用を賜りました。","劉備",},
        [4261] = {7307,"徐庶","ですが老母が今、曹操の奸計に遭い、許昌に捕らわれてしまいました。","ですが老母が今、曹操の奸計に遭い、許昌に捕らわれてしまいました。","劉備",},
        [4262] = {7307,"徐庶","危害を加えられるやもしれませぬ。老母から私を呼び寄せる書状が届き、行かないわけには参りませぬ。","危害を加えられるやもしれませぬ。老母から私を呼び寄せる書状が届き、行かないわけには参りませぬ。","劉備",},
        [4263] = {7307,"徐庶","微力ながらお仕えし、ご恩に報いたいと思っておりましたが……後日の再会を期し、今は故郷へ帰ることを、どうぞお許し下さい。","微力ながらお仕えし、ご恩に報いたいと思っておりましたが……後日の再会を期し、今は故郷へ帰ることを、どうぞお許し下さい。","劉備",},
        [4264] = {7307,"劉備","もちろんだ。母と子の情は天の理、私のことなど考えなくてよい。","もちろんだ。母と子の情は天の理、私のことなど考えなくてよい。","徐庶",},
        [4265] = {7307,"劉備","母上と再会したのち、また教えを請うこともあるかもしれないし。","母上と再会したのち、また教えを請うこともあるかもしれないし。","徐庶",},
        [4266] = {7308,"孫乾","徐庶は天下の奇才です。長く新野におり、我が軍中の手の内を知り尽くしております。","徐庶は天下の奇才です。長く新野におり、我が軍中の手の内を知り尽くしております。","劉備",},
        [4267] = {7308,"孫乾","今、曹操に帰属すれば、必ず重用されるでしょう。我が軍が危なくなります。","今、曹操に帰属すれば、必ず重用されるでしょう。我が軍が危なくなります。","劉備",},
        [4268] = {7308,"孫乾","絶対に行かせてはなりません。徐庶が行かなければ、曹操はその母を斬るでしょう。","絶対に行かせてはなりません。徐庶が行かなければ、曹操はその母を斬るでしょう。","劉備",},
        [4269] = {7308,"孫乾","母の死を知れば、徐庶は仇を討とうと思い、全力で曹操を攻めるはずです。","母の死を知れば、徐庶は仇を討とうと思い、全力で曹操を攻めるはずです。","劉備",},
        [4270] = {7308,"劉備","つまり、わざと母を殺めさせよというのか?それはいかん。仁ではない。","つまり、わざと母を殺めさせよというのか?それはいかん。仁ではない。","孫乾",},
        [4271] = {7308,"劉備","そもそも徐庶を行かせぬのも、親子の道を絶つことだ。義ではない。私はたとえ死んだとしても、不仁不義は成さぬ。","そもそも徐庶を行かせぬのも、親子の道を絶つことだ。義ではない。私はたとえ死んだとしても、不仁不義は成さぬ。","孫乾",},
        [4272] = {7309,"劉備","私とは縁がなく、共に過ごせなかったな。新しい主君に良く仕え、功成り名遂げることを祈っている。","私とは縁がなく、共に過ごせなかったな。新しい主君に良く仕え、功成り名遂げることを祈っている。","徐庶",},
        [4273] = {7309,"徐庶","私のような浅学非才な者を、重用して下さった。今、不幸にして道半ばでお別れするのも、ひとえに老母のためです。","私のような浅学非才な者を、重用して下さった。今、不幸にして道半ばでお別れするのも、ひとえに老母のためです。","劉備",},
        [4274] = {7309,"徐庶","たとえどんなに強いられても、決して曹操のために策を献じはいたしません。","たとえどんなに強いられても、決して曹操のために策を献じはいたしません。","劉備",},
        [4275] = {7309,"劉備","徐庶が去るのなら、私も山林に隠居しようかと思う。","徐庶が去るのなら、私も山林に隠居しようかと思う。","徐庶",},
        [4276] = {7309,"徐庶","私が殿と共に天下に覇を唱えようと思いましたのも、その心があったからですが、今は老母のために心が乱れています。","私が殿と共に天下に覇を唱えようと思いましたのも、その心があったからですが、今は老母のために心が乱れています。","劉備",},
        [4277] = {7309,"徐庶","ここにいたとて、お役には立てないでしょう。","ここにいたとて、お役には立てないでしょう。","劉備",},
        [4278] = {7309,"徐庶","ほかに殿を補佐できる賢士を招いて、大業をお図りください。ここで挫けてはなりませぬ。","ほかに殿を補佐できる賢士を招いて、大業をお図りください。ここで挫けてはなりませぬ。","劉備",},
        [4279] = {7309,"劉備","天下の賢士で、徐庶の右に出る者などおるまい。","天下の賢士で、徐庶の右に出る者などおるまい。","徐庶",},
        [4280] = {7309,"徐庶","そのようなお褒めの言葉……私のごとき凡庸な者には似つかわしくありません。","そのようなお褒めの言葉……私のごとき凡庸な者には似つかわしくありません。","劉備",},
        [4281] = {7309,"劉備","またいつ会えるかわからない。徐庶が行ってしまうのは実に忍びない。","またいつ会えるかわからない。徐庶が行ってしまうのは実に忍びない。","徐庶",},
        [4282] = {7309,"徐庶","遠くまで送って頂くには及びません。ここでお別れします！","遠くまで送って頂くには及びません。ここでお別れします！","劉備",},
        [4283] = {7309,"劉備","仕方ない。では、達者で！","仕方ない。では、達者で！","徐庶",},
        [4284] = {7310,"劉備","徐庶、なぜ戻って来たのだ?","徐庶、なぜ戻って来たのだ?","徐庶",},
        [4285] = {7310,"徐庶","先程は心が乱れて忘れておりました。","先程は心が乱れて忘れておりました。","劉備",},
        [4286] = {7310,"徐庶","ここに天下の奇才がおり、襄陽城からほんの二十里の隆中に住んでいます。この者を召し抱えられては?","ここに天下の奇才がおり、襄陽城からほんの二十里の隆中に住んでいます。この者を召し抱えられては?","劉備",},
        [4287] = {7310,"劉備","かたじけない。その者を招いてはもらえないか。","かたじけない。その者を招いてはもらえないか。","徐庶",},
        [4288] = {7310,"徐庶","残念ながら、その者が招きに応じることはないでしょう。","残念ながら、その者が招きに応じることはないでしょう。","劉備",},
        [4289] = {7310,"徐庶","我が君自らお出向きにならないと。ですが、もしこの者を得られれば、周の文王が太公望を、漢の高祖が張良を得たようなものです。","我が君自らお出向きにならないと。ですが、もしこの者を得られれば、周の文王が太公望を、漢の高祖が張良を得たようなものです。","劉備",},
        [4290] = {7310,"劉備","自身と比べて、その才はいかほど?","自身と比べて、その才はいかほど?","徐庶",},
        [4291] = {7310,"徐庶","私と比べるなど、駄馬を麒麟と、カラスを鳳凰と比べるようなもの。","私と比べるなど、駄馬を麒麟と、カラスを鳳凰と比べるようなもの。","劉備",},
        [4292] = {7310,"徐庶","彼自身は管仲、楽毅をもって自ら任じておりますが、私が見るに、この二人とて彼には及びません。","彼自身は管仲、楽毅をもって自ら任じておりますが、私が見るに、この二人とて彼には及びません。","劉備",},
        [4293] = {7310,"徐庶","天下を統べる経世の才の持ち主です。天下に二人とおりません。","天下を統べる経世の才の持ち主です。天下に二人とおりません。","劉備",},
        [4294] = {7310,"劉備","それは素晴らしい……是非、お名前を伺いたい。","それは素晴らしい……是非、お名前を伺いたい。","徐庶",},
        [4295] = {7310,"徐庶","琅琊郡陽都県の人で、姓は諸葛、名は亮、字は孔明と言います。漢の司隷校尉、諸葛豊の子孫です。","琅琊郡陽都県の人で、姓は諸葛、名は亮、字は孔明と言います。漢の司隷校尉、諸葛豊の子孫です。","劉備",},
        [4296] = {7310,"徐庶","その住むところに岡があり、臥龍岡という名のため、臥龍先生と号しています。","その住むところに岡があり、臥龍岡という名のため、臥龍先生と号しています。","劉備",},
        [4297] = {7310,"徐庶","当世並ぶ者なき奇才です。急ぎ、会いに行かれてください。もしこの者の補佐を得られれば、天下が定まらぬわけはありません！","当世並ぶ者なき奇才です。急ぎ、会いに行かれてください。もしこの者の補佐を得られれば、天下が定まらぬわけはありません！","劉備",},
        [4298] = {7310,"劉備","前に水鏡先生から伺ったことがある。","前に水鏡先生から伺ったことがある。","徐庶",},
        [4299] = {7310,"劉備","伏龍、鳳雛のうち一人を得れば、天下を安んずるに足ると。今言っているのは、そのどちらかのことか?","伏龍、鳳雛のうち一人を得れば、天下を安んずるに足ると。今言っているのは、そのどちらかのことか?","徐庶",},
        [4300] = {7310,"徐庶","鳳雛とは襄陽の龐統のことです。伏龍が諸葛孔明です。","鳳雛とは襄陽の龐統のことです。伏龍が諸葛孔明です。","劉備",},
        [4301] = {7310,"劉備","なんと、そのような大賢人がすぐ近くにいたとは……灯台もと暗しとはこのことか。","なんと、そのような大賢人がすぐ近くにいたとは……灯台もと暗しとはこのことか。","徐庶",},
        [4302] = {7311,"徐庶","私は劉備殿にお仕えするつもりだったが、老母が曹操に捕えられたので、仕方なくお傍を離れたのだ。","私は劉備殿にお仕えするつもりだったが、老母が曹操に捕えられたので、仕方なくお傍を離れたのだ。","諸葛亮",},
        [4303] = {7311,"徐庶","去り際に、君を推薦しておいた。","去り際に、君を推薦しておいた。","諸葛亮",},
        [4304] = {7311,"徐庶","近く会いに来られるだろうから、断らないでほしい。君の才能を大いに発揮し、補佐してもらえればありがたい！","近く会いに来られるだろうから、断らないでほしい。君の才能を大いに発揮し、補佐してもらえればありがたい！","諸葛亮",},
        [4305] = {7311,"諸葛亮","なるほど。つまり君は私を祭りの生け贄にしようと言うのですね?","なるほど。つまり君は私を祭りの生け贄にしようと言うのですね?","徐庶",},
        [4306] = {7311,"徐庶","と、とんでもない、そんなつもりでは……！","と、とんでもない、そんなつもりでは……！","諸葛亮",},
        [4307] = {7312,"劉備","漢王室の末裔、涿郡の愚夫です。かねがね先生のお噂を伺い、雷が耳を貫くごとしでした。","漢王室の末裔、涿郡の愚夫です。かねがね先生のお噂を伺い、雷が耳を貫くごとしでした。","諸葛亮",},
        [4308] = {7312,"劉備","前にも二度ほどお目に掛かりに来ましたが、お会いできませんでした。書状を残しておきましたが、ご覧頂けたでしょうか?","前にも二度ほどお目に掛かりに来ましたが、お会いできませんでした。書状を残しておきましたが、ご覧頂けたでしょうか?","諸葛亮",},
        [4309] = {7312,"諸葛亮","お恥ずかしい限りです。何度もお訪ねいただいたのに。","お恥ずかしい限りです。何度もお訪ねいただいたのに。","劉備",},
        [4310] = {7312,"諸葛亮","仕官もせず、南陽の田舎にいて、すっかり怠け者になっておりました。","仕官もせず、南陽の田舎にいて、すっかり怠け者になっておりました。","劉備",},
        [4311] = {7312,"諸葛亮","書状は拝見いたしました。将軍が民や国を憂うお心が良く分かりました。ですが私は若輩者で才もなく、ご期待に応えられぬかと思います。","書状は拝見いたしました。将軍が民や国を憂うお心が良く分かりました。ですが私は若輩者で才もなく、ご期待に応えられぬかと思います。","劉備",},
        [4312] = {7312,"劉備","司馬徽殿や徐庶殿の言葉は、嘘ではないと考えております。","司馬徽殿や徐庶殿の言葉は、嘘ではないと考えております。","諸葛亮",},
        [4313] = {7312,"劉備","どうか卑しい者とお見捨てにならず、曲げて教えを賜りますよう、お願い致します。","どうか卑しい者とお見捨てにならず、曲げて教えを賜りますよう、お願い致します。","諸葛亮",},
        [4314] = {7312,"諸葛亮","彼らは当世の高士ですが、私は一介の農夫、天下の事など語れません。","彼らは当世の高士ですが、私は一介の農夫、天下の事など語れません。","劉備",},
        [4315] = {7312,"諸葛亮","きっと間違えて推挙されたのでしょう。将軍はなぜ、玉を捨て石を求めようとされるのです?","きっと間違えて推挙されたのでしょう。将軍はなぜ、玉を捨て石を求めようとされるのです?","劉備",},
        [4316] = {7312,"劉備","天下の奇才といわれるほどの立派な男が、林の中でただ老いて良いものでしょうか。","天下の奇才といわれるほどの立派な男が、林の中でただ老いて良いものでしょうか。","諸葛亮",},
        [4317] = {7312,"劉備","天下の民草のためにも、私のような愚才を導き、教えを賜りたい。","天下の民草のためにも、私のような愚才を導き、教えを賜りたい。","諸葛亮",},
        [4318] = {7312,"諸葛亮","将軍の志をお聞きかせください。","将軍の志をお聞きかせください。","劉備",},
        [4319] = {7312,"劉備","漢王室は廃れ、奸臣が権力を奪っております。私は身の程知らずにも、天下に大義を取り戻したく思いましたが、","漢王室は廃れ、奸臣が権力を奪っております。私は身の程知らずにも、天下に大義を取り戻したく思いましたが、","諸葛亮",},
        [4320] = {7312,"劉備","智謀に欠け、まだ成就していません。先生が、愚かな私を助けて下されば、これ以上の幸いはありません！","智謀に欠け、まだ成就していません。先生が、愚かな私を助けて下されば、これ以上の幸いはありません！","諸葛亮",},
        [4321] = {7312,"諸葛亮","これは西蜀五十四州の図です。","これは西蜀五十四州の図です。","劉備",},
        [4322] = {7312,"諸葛亮","北の曹操には天の時、南の孫権には地の利を占められています。将軍が覇業を成さんとされるなら、人の和を占めるとよろしいでしょうね。","北の曹操には天の時、南の孫権には地の利を占められています。将軍が覇業を成さんとされるなら、人の和を占めるとよろしいでしょうね。","劉備",},
        [4323] = {7312,"諸葛亮","まずは荊州をとって拠点とし、それから西蜀に基礎を築いて、三国鼎立の形勢とします。それから中原を狙うのです。","まずは荊州をとって拠点とし、それから西蜀に基礎を築いて、三国鼎立の形勢とします。それから中原を狙うのです。","劉備",},
        [4324] = {7312,"劉備","おお……先生の言葉で、突如つかえが取れて、雲を抜け青空を見る思いです。","おお……先生の言葉で、突如つかえが取れて、雲を抜け青空を見る思いです。","諸葛亮",},
        [4325] = {7312,"劉備","ですが荊州の劉表殿、益州の劉璋殿は皆漢王室の宗族です。無理に奪うことはできません。","ですが荊州の劉表殿、益州の劉璋殿は皆漢王室の宗族です。無理に奪うことはできません。","諸葛亮",},
        [4326] = {7312,"諸葛亮","夜に天文を見たところ、劉表の寿命は長くなく、劉璋は大業を成す者ではありませんね。時間が立てば、必ず将軍に帰属するでしょう。","夜に天文を見たところ、劉表の寿命は長くなく、劉璋は大業を成す者ではありませんね。時間が立てば、必ず将軍に帰属するでしょう。","劉備",},
        [4327] = {7312,"劉備","私は名もなく徳も薄い者ですが、どうか卑しいとお見捨てにならず、山を下りて助けて頂きたい。謹んで教えを受けます。","私は名もなく徳も薄い者ですが、どうか卑しいとお見捨てにならず、山を下りて助けて頂きたい。謹んで教えを受けます。","諸葛亮",},
        [4328] = {7312,"諸葛亮","あいにく、私はこの晴耕雨読の暮らしを楽しんでおりまして、俗世に関わる気はありません。ご期待には沿いかねます。","あいにく、私はこの晴耕雨読の暮らしを楽しんでおりまして、俗世に関わる気はありません。ご期待には沿いかねます。","劉備",},
        [4329] = {7312,"劉備","先生が山を下りないとなれば、民はどうしたら良いのです！","先生が山を下りないとなれば、民はどうしたら良いのです！","諸葛亮",},
        [4330] = {7312,"諸葛亮","そこまで仰るなら……将軍がお見捨てにならないなら、微力ながら尽くしましょう。","そこまで仰るなら……将軍がお見捨てにならないなら、微力ながら尽くしましょう。","劉備",},
        [4331] = {7401,"呂蒙","黄祖の配下の者が投降して参りました。","黄祖の配下の者が投降して参りました。","孫権",},
        [4332] = {7401,"孫権","何者だ?詳しく言ってくれ！","何者だ?詳しく言ってくれ！","呂蒙",},
        [4333] = {7401,"呂蒙","甘寧、巴郡臨江の出身です。気概のある遊侠の徒で、いささか学問もあります。","甘寧、巴郡臨江の出身です。気概のある遊侠の徒で、いささか学問もあります。","孫権",},
        [4334] = {7401,"呂蒙","腰に銅の鈴をつけていて、鈴の音を聞くと皆逃げたとか。また蜀江の錦で帆を作ったので、「錦帆賊」と呼ばれたといいます。","腰に銅の鈴をつけていて、鈴の音を聞くと皆逃げたとか。また蜀江の錦で帆を作ったので、「錦帆賊」と呼ばれたといいます。","孫権",},
        [4335] = {7401,"呂蒙","先ごろ前非を悔いて、善行をするようになり、配下を率いて劉表に身を投じました。","先ごろ前非を悔いて、善行をするようになり、配下を率いて劉表に身を投じました。","孫権",},
        [4336] = {7401,"呂蒙","劉表では事を成せぬと、呉に投じようとしましたが、黄祖が夏口に留め置いたのです。","劉表では事を成せぬと、呉に投じようとしましたが、黄祖が夏口に留め置いたのです。","孫権",},
        [4337] = {7401,"呂蒙","呉が黄祖を破った時、黄祖は甘寧の力を得て夏口を奪還しましたが、甘寧の扱いは軽いものでした。","呉が黄祖を破った時、黄祖は甘寧の力を得て夏口を奪還しましたが、甘寧の扱いは軽いものでした。","孫権",},
        [4338] = {7401,"呂蒙","都督の蘇飛が何度も推挙しましたが、黄祖は甘寧を江賊出身だといって重用せず、甘寧の恨みを買ったのです。","都督の蘇飛が何度も推挙しましたが、黄祖は甘寧を江賊出身だといって重用せず、甘寧の恨みを買ったのです。","孫権",},
        [4339] = {7401,"呂蒙","そこで私から、我が君が賢士を求めていることを教え、昔の恨みなど気にしないと言ったのです。","そこで私から、我が君が賢士を求めていることを教え、昔の恨みなど気にしないと言ったのです。","孫権",},
        [4340] = {7401,"呂蒙","それぞれが主のために尽したのですからな。甘寧は喜び、手勢を連れて江を渡って我が君に謁見に参ったのです。どうかお許しを願いたい。","それぞれが主のために尽したのですからな。甘寧は喜び、手勢を連れて江を渡って我が君に謁見に参ったのです。どうかお許しを願いたい。","孫権",},
        [4341] = {7401,"孫権","揚威将軍は、かつて我が兄の孫策を江東の戦禍から救ってくださった。今の事をどう思われる?","揚威将軍は、かつて我が兄の孫策を江東の戦禍から救ってくださった。今の事をどう思われる?","呂蒙",},
        [4342] = {7401,"主君","私の意見を申し上げるなら、甘寧を得れば、必ず黄祖を破れるでしょう。","私の意見を申し上げるなら、甘寧を得れば、必ず黄祖を破れるでしょう。","孫権",},
        [4343] = {7401,"孫権","よし、すぐにその者を連れて参れ。","よし、すぐにその者を連れて参れ。","プレイヤー",},
        [4344] = {7402,"孫権","甘寧が来てくれたのは、大いに俺の意にかなう。恨みなどあるものか。疑いを解いて、ぜひ黄祖を破る策を教えてくれ。","甘寧が来てくれたのは、大いに俺の意にかなう。恨みなどあるものか。疑いを解いて、ぜひ黄祖を破る策を教えてくれ。","甘寧",},
        [4345] = {7402,"甘寧","今、漢王室はますます危うく、曹操はきっと簒奪を狙うでしょう。荊州の南は曹操が必ず奪おうとする地です。","今、漢王室はますます危うく、曹操はきっと簒奪を狙うでしょう。荊州の南は曹操が必ず奪おうとする地です。","孫権",},
        [4346] = {7402,"甘寧","荊州の劉表は優柔不断、その子も暗愚で跡目争いが起きています。ここを早く攻めると良いでしょう。","荊州の劉表は優柔不断、その子も暗愚で跡目争いが起きています。ここを早く攻めると良いでしょう。","孫権",},
        [4347] = {7402,"甘寧","遅れれば、曹操に先を越されます。劉表配下の黄祖はもはや老齢で、財貨をむさぼり、人々の恨みを買っています。","遅れれば、曹操に先を越されます。劉表配下の黄祖はもはや老齢で、財貨をむさぼり、人々の恨みを買っています。","孫権",},
        [4348] = {7402,"甘寧","武具の手入れもせず、軍にも紀律がありません。","武具の手入れもせず、軍にも紀律がありません。","孫権",},
        [4349] = {7402,"甘寧","今、孫権殿が攻めれば、きっと破ることができましょう。それから西へ軍を進め、楚関を占領して巴蜀を伺えば、覇業は定まりましょう。","今、孫権殿が攻めれば、きっと破ることができましょう。それから西へ軍を進め、楚関を占領して巴蜀を伺えば、覇業は定まりましょう。","孫権",},
        [4350] = {7402,"孫権","これは嬉しい献策だ！","これは嬉しい献策だ！","甘寧",},
        [4351] = {7402,"孫権","周瑜を大都督に任じ、水陸軍の統率を命じる。呂蒙と揚威将軍を先陣とし、董襲と甘寧を副将にする。","周瑜を大都督に任じ、水陸軍の統率を命じる。呂蒙と揚威将軍を先陣とし、董襲と甘寧を副将にする。","甘寧",},
        [4352] = {7402,"孫権","俺は自ら十万の大軍を率い、黄祖を討つ。","俺は自ら十万の大軍を率い、黄祖を討つ。","甘寧",},
        [4353] = {7403,"黄祖","間諜に探らせたところ、孫権は十万の大軍を率いてやって来るという。","間諜に探らせたところ、孫権は十万の大軍を率いてやって来るという。","",},
        [4354] = {7403,"黄祖","蘇飛を大将に任ずる。陳就、鄧龍を先鋒に、江夏の兵を総動員して迎え撃て。","蘇飛を大将に任ずる。陳就、鄧龍を先鋒に、江夏の兵を総動員して迎え撃て。","",},
        [4355] = {7404,"主君","蘇飛、陳就、鄧龍は皆斬りました。もはや、黄祖軍は立ち直れないでしょう。","蘇飛、陳就、鄧龍は皆斬りました。もはや、黄祖軍は立ち直れないでしょう。","孫権",},
        [4356] = {7404,"主君","黄祖軍は壊滅状態で、江夏を棄て、荊州へ逃げました。","黄祖軍は壊滅状態で、江夏を棄て、荊州へ逃げました。","孫権",},
        [4357] = {7404,"孫権","追撃だ！逃がすものか！","追撃だ！逃がすものか！","プレイヤー",},
        [4358] = {7405,"黄祖","わしはお前によくしてやったはず。なぜ今になって攻めてくるのだ?","わしはお前によくしてやったはず。なぜ今になって攻めてくるのだ?","甘寧",},
        [4359] = {7405,"甘寧","今さら何を言いやがる！江夏にいた時、数多くの功績を立てたのに賊としてしか扱わなかっただろう！","今さら何を言いやがる！江夏にいた時、数多くの功績を立てたのに賊としてしか扱わなかっただろう！","黄祖",},
        [4360] = {7405,"主君","敗残の将が言い訳とは見苦しい。覚悟！","敗残の将が言い訳とは見苦しいわ。覚悟！","黄祖",},
        [4361] = {7406,"孫権","黄祖は死んだ。その首を江東へ持ち帰り、亡き父の墓前に献じよう。","黄祖は死んだ。その首を江東へ持ち帰り、亡き父の墓前に献じよう。","",},
        [4362] = {7406,"孫権","三軍に大いに褒美を取らせ、甘寧を都尉に昇格させる。","三軍に大いに褒美を取らせ、甘寧を都尉に昇格させる。","",},
        [4363] = {7406,"張昭","孤立した城は守りにくいものです。ひとまず江東に帰りましょう。我が軍が黄祖を破ったと知れば、劉表は必ず仇討ちに来ます。","孤立した城は守りにくいものです。ひとまず江東に帰りましょう。我が軍が黄祖を破ったと知れば、劉表は必ず仇討ちに来ます。","孫権",},
        [4364] = {7406,"張昭","鋭気を養って疲れた敵を待てば、必ず勝てます。その勢いに乗って攻めれば、荊州も手に入りましょう。","鋭気を養って疲れた敵を待てば、必ず勝てます。その勢いに乗って攻めれば、荊州も手に入りましょう。","孫権",},
        [4365] = {7406,"孫権","よし、江夏を棄て、江東に凱旋する。","よし、江夏を棄て、江東に凱旋する。","張昭",},
        [4366] = {7501,"劉備","劉表殿から、相談があるので荊州に来てほしいと使いが来たのだが……","劉表殿から、相談があるので荊州に来てほしいと使いが来たのだが……","諸葛亮",},
        [4367] = {7501,"諸葛亮","黄祖が呉に討たれたので、仇討ちの策をはかりたいのでしょう。私もお供いたします。機会をみて策を施しましょう。","黄祖が呉に討たれたので、仇討ちの策をはかりたいのでしょう。私もお供いたします。機会をみて策を施しましょう。","劉備",},
        [4368] = {7501,"劉備","劉表殿に会ったら、どう受け答えすればいいだろう?","劉表殿に会ったら、どう受け答えすればいいだろう?","諸葛亮",},
        [4369] = {7501,"諸葛亮","まず襄陽の件でお詫びを述べてください。","まず襄陽の件でお詫びを述べてください。","劉備",},
        [4370] = {7501,"諸葛亮","もし呉の征伐を頼まれても、絶対にお引き受けになってはなりません。新野に帰って、兵馬を整えたいと答えてください。","もし呉の征伐を頼まれても、絶対にお引き受けになってはなりません。新野に帰って、兵馬を整えたいと答えてください。","劉備",},
        [4371] = {7502,"劉表","劉備殿が難に遭われたことは承知しておる。","劉備殿が難に遭われたことは承知しておる。","劉備",},
        [4372] = {7502,"劉表","あのとき蔡瑁の首を刎ねてお詫びしようとしたが、家来どもが諫めるのでひとまず許しておいた。どうか悪く思わんでくれ。","あのとき蔡瑁の首を刎ねてお詫びしようとしたが、家来どもが諫めるのでひとまず許しておいた。どうか悪く思わんでくれ。","劉備",},
        [4373] = {7502,"劉備","いえ、あれは蔡将軍のせいではありません。おそらく手下の誰かの仕業でしょう。","いえ、あれは蔡将軍のせいではありません。おそらく手下の誰かの仕業でしょう。","劉表",},
        [4374] = {7502,"劉表","ときに、江夏を失って、黄祖が討たれた。そこで仇討ちの策を練りたいと招いたのだが……","ときに、江夏を失って、黄祖が討たれた。そこで仇討ちの策を練りたいと招いたのだが……","劉備",},
        [4375] = {7502,"劉備","黄祖殿は粗暴で、人をうまく使えない方でしたから、自ら禍いを招いたのでしょう。","黄祖殿は粗暴で、人をうまく使えない方でしたから、自ら禍いを招いたのでしょう。","劉表",},
        [4376] = {7502,"劉備","いま南に兵を出して、その隙に北から曹操に攻め込まれたら、どうします?","いま南に兵を出して、その隙に北から曹操に攻め込まれたら、どうします?","劉表",},
        [4377] = {7502,"劉表","わしも歳のうえ病がちで、政務もままならぬ。劉備殿、どうか助けてくれ。わし亡きあとは、この荊州の主となってくれまいか。","わしも歳のうえ病がちで、政務もままならぬ。劉備殿、どうか助けてくれ。わし亡きあとは、この荊州の主となってくれまいか。","劉備",},
        [4378] = {7502,"劉備","何をおっしゃいます。私のような不束者にそんな大任は担えません。","何をおっしゃいます。私のような不束者にそんな大任は担えません。","劉表",},
        [4379] = {7502,"劉備","ゆっくり手立てを考えたいので、ひとまず失礼いたします。","ゆっくり手立てを考えたいので、ひとまず失礼いたします。","劉表",},
        [4380] = {7503,"諸葛亮","劉表殿から荊州を譲ると言われたのに、なぜお引き受けにならなかったのです。","劉表殿から荊州を譲ると言われたのに、なぜお引き受けにならなかったのです。","劉備",},
        [4381] = {7503,"劉備","劉表殿には実によく面倒をみてもらった。恩人が危ういのにつけこんで領地を奪うことなどできぬ。","劉表殿には実によく面倒をみてもらった。恩人が危ういのにつけこんで領地を奪うことなどできぬ。","諸葛亮",},
        [4382] = {7503,"諸葛亮","やれやれ、わが君は本当に情け深くていらっしゃる……","やれやれ、わが君は本当に情け深くていらっしゃる……","劉備",},
        [4383] = {7504,"劉琦","継母は私を跡継ぎと認めてくれず、いつ殺されるかもしれません。どうか哀れと思し召し、お助けください。","継母は私を跡継ぎと認めてくれず、いつ殺されるかもしれません。どうか哀れと思し召し、お助けください。","劉備",},
        [4384] = {7504,"劉備","しかしそれは劉琦殿のお家の事情。私に申されてもどうにも……","しかしそれは劉琦殿のお家の事情。私に申されてもどうにも……","劉琦",},
        [4385] = {7504,"劉琦","叔父上、なにとぞ、お助けくださいませ！","叔父上、なにとぞ、お助けくださいませ！","劉備",},
        [4386] = {7504,"劉備","では明日、孔明を答礼にうかがわせましょう。きっと妙計を授けてくれるでしょう。","では明日、孔明を答礼にうかがわせましょう。きっと妙計を授けてくれるでしょう。","劉琦",},
        [4387] = {7504,"劉琦","かたじけのうございます！","かたじけのうございます！","劉備",},
        [4388] = {7505,"劉琦","私は継母から目の敵にされております。先生、救いの策を授けてください。","私は継母から目の敵にされております。先生、救いの策を授けてください。","諸葛亮",},
        [4389] = {7505,"諸葛亮","客人の立場で、他家のお家事情に口を挟むわけにいきませんよ。もし外に漏れ聞こえでもしたら、大ごとですよ。","客人の立場で、他家のお家事情に口を挟むわけにいきませんよ。もし外に漏れ聞こえでもしたら、大ごとですよ。","劉琦",},
        [4390] = {7505,"劉琦","せっかくお越しいただいて、むざむざお帰しするわけには参りません。","せっかくお越しいただいて、むざむざお帰しするわけには参りません。","諸葛亮",},
        [4391] = {7505,"劉琦","継母は私を跡継ぎにしたくないのです。先生、なにとぞご助言を。","継母は私を跡継ぎにしたくないのです。先生、なにとぞご助言を。","諸葛亮",},
        [4392] = {7505,"諸葛亮","私の申すべき筋合いのことではありませんね。","私の申すべき筋合いのことではありませんね。","劉琦",},
        [4393] = {7505,"劉琦","継母は私を亡き者にしようと企んでいるのです。先生はお見捨てになるのですか?","継母は私を亡き者にしようと企んでいるのです。先生はお見捨てになるのですか?","諸葛亮",},
        [4394] = {7505,"諸葛亮","……では、一計があります。","……では、一計があります。","劉琦",},
        [4395] = {7505,"劉琦","どうか教えてくださいませ。","どうか教えてくださいませ。","諸葛亮",},
        [4396] = {7505,"諸葛亮","若君は春秋時代の申生と重耳の故事はご存知ですか。国に留まった兄君の申生は殺され、外に逃げた弟君の重耳は助かったのです。","若君は春秋時代の申生と重耳の故事はご存知ですか。国に留まった兄君の申生は殺され、外に逃げた弟君の重耳は助かったのです。","劉琦",},
        [4397] = {7505,"諸葛亮","黄祖が討ち死にし、いま江夏には守る人がおりません。","黄祖が討ち死にし、いま江夏には守る人がおりません。","劉琦",},
        [4398] = {7505,"諸葛亮","若君は父君に乞うて江夏の守備に行かれるべきです。さすれば難を逃れられましょう。","若君は父君に乞うて江夏の守備に行かれるべきです。さすれば難を逃れられましょう。","劉琦",},
        [4399] = {7505,"劉琦","ありがたい！ご助言、心より感謝いたします。","ありがたい！ご助言、心より感謝いたします。","諸葛亮",},
        [4400] = {7506,"劉表","劉琦が江夏の守りにやってくれと申し出てきたのだが、どうしたものか。","劉琦が江夏の守りにやってくれと申し出てきたのだが、どうしたものか。","劉備",},
        [4401] = {7506,"劉備","江夏は重要な土地ですから、近親である若君を守りに置かれるのは何よりでしょう。","江夏は重要な土地ですから、近親である若君を守りに置かれるのは何よりでしょう。","劉表",},
        [4402] = {7506,"劉備","東南をお二人で守られるのであれば、西北の守りはこの劉備が当たります。","東南をお二人で守られるのであれば、西北の守りはこの劉備が当たります。","劉表",},
        [4403] = {7506,"劉表","聞けば近頃、曹操は鄴に玄武池というものを作って、水軍の調練をしているそうだ。南征の野心があるのだろう。心して防衛せねばならん。","聞けば近頃、曹操は鄴に玄武池というものを作って、水軍の調練をしているそうだ。南征の野心があるのだろう。心して防衛せねばならん。","劉備",},
        [4404] = {7506,"劉備","存じております。ご心配なく。ではこれにて、新野に帰ります。","存じております。ご心配なく。ではこれにて、新野に帰ります。","劉表",},
        [4405] = {7507,"主君","孔明殿は最近嫁探しをしているそうだな?","孔明殿は最近嫁探しをしているんですって?","諸葛亮",},
        [4406] = {7507,"主君","私も各地を巡り歩いたが、才華にあふれた娘を知っているよ。会いに行ってみたらどうかな。","私も各地を巡り歩いたけど、才華にあふれた娘を知ってるよ。会いに行ってみたらどう?","諸葛亮",},
        [4407] = {7507,"諸葛亮","どういう方です?","どういう方です?","プレイヤー",},
        [4408] = {7507,"主君","荊州は沔陽の名士、黄承彦の娘で、黄月英というのだが……","荊州は沔陽の名士、黄承彦の娘で、黄月英というんだけど……","諸葛亮",},
        [4409] = {7507,"諸葛亮","ああ、その娘の噂なら聞いております。色黒で黄みがかった髪の醜女だとか。揚威将軍が推薦されるとは、またどうして。","ああ、その娘の噂なら聞いております。色黒で黄みがかった髪の醜女だとか。揚威将軍が推薦されるとは、またどうして。","プレイヤー",},
        [4410] = {7507,"主君","世間の人の言うことなんか当てにならないさ。自分の目で確かめてみるといい。","世間の人の言うことなんか当てにならないわ。自分の目で確かめてみなさいよ。","諸葛亮",},
        [4411] = {7508,"諸葛亮","世間の人はこぞって黄承彦の娘は醜いなどと言っておりますが――","世間の人はこぞって黄承彦の娘は醜いなどと言っておりますが――","黄月英",},
        [4412] = {7508,"諸葛亮","それは根も葉もない噂だと今わかりました。貴女は聡明で美しく、抜群の才華をお持ちですね。学問だけでなく、武芸の腕もたつ。","それは根も葉もない噂だと今わかりました。貴女は聡明で美しく、抜群の才華をお持ちですね。学問だけでなく、武芸の腕もたつ。","黄月英",},
        [4413] = {7508,"諸葛亮","きっと宝玉の価値がわからない人が、いい加減なことを言っているのですね。","きっと宝玉の価値がわからない人が、いい加減なことを言っているのですね。","黄月英",},
        [4414] = {7508,"黄月英","褒めてくださるの?嬉しいです。","褒めてくださるの?嬉しいです。","諸葛亮",},
        [4415] = {7508,"黄月英","この羽扇を差し上げます。大事にしてくださいね。","この羽扇を差し上げます。大事にしてくださいね。","諸葛亮",},
        [4416] = {7508,"諸葛亮","ほう、白羽扇ですね。これをなぜ私に?","ほう、白羽扇ですね。これをなぜ私に?","黄月英",},
        [4417] = {7508,"黄月英","諸葛先生が父と天下の大事を語り合われたおり、ご自身の胸の内にある大計を話すときは、意気軒昂、喜色満面でいらっしゃいましたが――","諸葛先生が父と天下の大事を語り合われたおり、ご自身の胸の内にある大計を話すときは、意気軒昂、喜色満面でいらっしゃいましたが――","諸葛亮",},
        [4418] = {7508,"黄月英","お話が曹操や孫権のことに及ぶと、眉根を寄せて、憂いを露わにしておられました。それで扇子でお顔を隠せるようにと思ったのです。","お話が曹操や孫権のことに及ぶと、眉根を寄せて、憂いを露わにしておられました。それで扇子でお顔を隠せるようにと思ったのです。","諸葛亮",},
        [4419] = {7508,"諸葛亮","なるほど、わかりました。大丈夫たるもの、いかなる時も感情的になってはならない。","なるほど、わかりました。大丈夫たるもの、いかなる時も感情的になってはならない。","黄月英",},
        [4420] = {7508,"諸葛亮","そんなところを人に見られては、大事はなせないということですね。","そんなところを人に見られては、大事はなせないということですね。","黄月英",},
        [4421] = {7508,"諸葛亮","これからは肌身離さず持つことにしましょう。","これからは肌身離さず持つことにしましょう。","黄月英",},
        [4422] = {7509,"主君","黄月英はお気に召したかな?","黄月英はお気に召した?","諸葛亮",},
        [4423] = {7509,"諸葛亮","気に入るどころではありませんよ！聡明で美しく、抜群の才華を持った方。まさに荊州の玉ですね。","気に入るどころではありませんよ！聡明で美しく、抜群の才華を持った方。まさに荊州の玉ですね。","プレイヤー",},
        [4424] = {7509,"主君","それはよかった。嬉しくてたまらないようだが、少し落ち着いてくれ。","それはよかったわ。嬉しくてたまらないようだけど、少し落ち着いて。","諸葛亮",},
        [4425] = {7509,"諸葛亮","ええ、落ち着いていますとも。すぐに縁談を進めます。","ええ、落ち着いていますとも。すぐに縁談を進めます。","プレイヤー",},
        [4426] = {7601,"夏侯惇","近ごろ劉備は新野で日々兵卒の調練をしておるそうだ。捨て置けばのちの禍いになろう。早めに始末せねば。","近ごろ劉備は新野で日々兵卒の調練をしておるそうだ。捨て置けばのちの禍いになろう。早めに始末せねば。","曹操",},
        [4427] = {7601,"曹操","では夏侯惇を都督に命ずる。于禁、李典、夏侯蘭、韓浩を副将とし、十万の兵を率いて博望城まで繰り出し、新野の様子を窺え！","では夏侯惇を都督に命ずる。于禁、李典、夏侯蘭、韓浩を副将とし、十万の兵を率いて博望城まで繰り出し、新野の様子を窺え！","夏侯惇",},
        [4428] = {7601,"荀彧","劉備は傑物です。しかも諸葛亮を軍師に迎えた今、軽々しい戦は禁物です。","劉備は傑物です。しかも諸葛亮を軍師に迎えた今、軽々しい戦は禁物です。","曹操",},
        [4429] = {7601,"夏侯惇","ハンっ！劉備ごときネズミ、俺が生け捕りにしてくれるわぁっ！","ハンっ！劉備ごときネズミ、俺が生け捕りにしてくれるわぁっ！","荀彧",},
        [4430] = {7601,"徐庶","いやいや将軍、劉備は侮れませんぞ。諸葛亮の補佐を得た今の劉備は、虎に翼が生えたようなものです。","いやいや将軍、劉備は侮れませんぞ。諸葛亮の補佐を得た今の劉備は、虎に翼が生えたようなものです。","夏侯惇",},
        [4431] = {7601,"曹操","諸葛亮とは、どんな男だ?","諸葛亮とは、どんな男だ?","徐庶",},
        [4432] = {7601,"徐庶","諸葛亮、字は孔明、道号を臥龍先生と申します。","諸葛亮、字は孔明、道号を臥龍先生と申します。","曹操",},
        [4433] = {7601,"徐庶","天地も動かせるほどの才能を持ち、神算鬼謀を巡らす、当世の奇才です。見くびってはなりません。","天地も動かせるほどの才能を持ち、神算鬼謀を巡らす、当世の奇才です。見くびってはなりません。","曹操",},
        [4434] = {7601,"曹操","おぬしと比べればどうだ?","おぬしと比べればどうだ?","徐庶",},
        [4435] = {7601,"徐庶","私などは比べものになりません。私を蛍の光とすれば、孔明は明月の輝きでしょう。","私などは比べものになりません。私を蛍の光とすれば、孔明は明月の輝きでしょう。","曹操",},
        [4436] = {7601,"夏侯惇","徐庶の話は大げさだ！俺から見れば諸葛亮など塵芥も同然、恐るるに足りん！","徐庶の話は大げさだ！俺から見れば諸葛亮など塵芥も同然、恐るるに足りん！","曹操",},
        [4437] = {7601,"夏侯惇","もし奴と劉備を生け捕りにできなければ、この首を丞相に差し上げようっ！","もし奴と劉備を生け捕りにできなければ、この首を丞相に差し上げようっ！","曹操",},
        [4438] = {7601,"曹操","よし、早く戦勝の報告を持ってこい。わしを安心させよ！","よし、早く戦勝の報告を持ってこい。わしを安心させよ！","夏侯惇",},
        [4439] = {7602,"劉備","曹操が差し向けた夏侯惇が、十万の兵を率いて、新野に攻めて来るそうだ。どう迎え討てばよいだろう?","曹操が差し向けた夏侯惇が、十万の兵を率いて、新野に攻めて来るそうだ。どう迎え討てばよいだろう?","張飛",},
        [4440] = {7602,"張飛","そんなもん、孔明を敵の前に出して迎えればいいじゃねえか。","そんなもん、孔明を敵の前に出して迎えればいいじゃねえか。","劉備",},
        [4441] = {7602,"劉備","おいおい、仲違いするでない。智謀は孔明が頼りだが、武勇はお前たち弟二人を頼りにしているのだぞ。","おいおい、仲違いするでない。智謀は孔明が頼りだが、武勇はお前たち弟二人を頼りにしているのだぞ。","張飛",},
        [4442] = {7602,"張飛","けっ！行こうぜ、関羽の兄貴。","けっ！行こうぜ、関羽の兄貴。","劉備",},
        [4443] = {7603,"劉備","孔明、夏侯惇が十万の兵を率いて攻めて来た。どう迎え討てばよい?","孔明、夏侯惇が十万の兵を率いて攻めて来た。どう迎え討てばよい?","諸葛亮",},
        [4444] = {7603,"諸葛亮","策はありますが、関羽、張飛のお二人が私の指図に従わないのではないですか。","策はありますが、関羽、張飛のお二人が私の指図に従わないのではないですか。","劉備",},
        [4445] = {7603,"諸葛亮","私に軍略を任されていただけるのであれば、わが君の剣と印をお貸しください。","私に軍略を任されていただけるのであれば、わが君の剣と印をお貸しください。","劉備",},
        [4446] = {7603,"劉備","よかろう、では剣と印を授けよう。","よかろう、では剣と印を授けよう。","諸葛亮",},
        [4447] = {7604,"劉備","私の剣と印は孔明に授けてある。みな孔明の命令に従うように。","私の剣と印は孔明に授けてある。みな孔明の命令に従うように。","張飛",},
        [4448] = {7604,"張飛","おもしれえ！どんな命令をするのか、聞くだけ聞いてやろうじゃねえか。","おもしれえ！どんな命令をするのか、聞くだけ聞いてやろうじゃねえか。","劉備",},
        [4449] = {7604,"諸葛亮","博望城の左手に予山という山があり、右手には安林という林があります。ここに兵を埋伏させられます。","博望城の左手に予山という山があり、右手には安林という林があります。ここに兵を埋伏させられます。","張飛",},
        [4450] = {7604,"諸葛亮","関羽殿は一千の軍勢を率いて予山にひそんでください。ただし敵軍が来てもやり過ごすように。","関羽殿は一千の軍勢を率いて予山にひそんでください。ただし敵軍が来てもやり過ごすように。","張飛",},
        [4451] = {7604,"諸葛亮","輜重隊が必ず後に続きますから、南に火の手が上がるのを見たら一気に攻めかかり、糧秣を焼き払ってください。","輜重隊が必ず後に続きますから、南に火の手が上がるのを見たら一気に攻めかかり、糧秣を焼き払ってください。","張飛",},
        [4452] = {7604,"諸葛亮","張飛殿は同じく一千の軍勢を率いて、安林の後ろの谷間に隠れてください。","張飛殿は同じく一千の軍勢を率いて、安林の後ろの谷間に隠れてください。","張飛",},
        [4453] = {7604,"諸葛亮","南に火の手が上がるのを見たらただちに出撃し、博望城の食糧庫に火をかけて焼き払うよう。","南に火の手が上がるのを見たらただちに出撃し、博望城の食糧庫に火をかけて焼き払うよう。","張飛",},
        [4454] = {7604,"諸葛亮","関平と劉封のお二人は各々五百の兵を率い、引火物を用意して、博望坡の後方の両側で待機してください。宵の口になったら火を放つのです。","関平と劉封のお二人は各々五百の兵を率い、引火物を用意して、博望坡の後方の両側で待機してください。宵の口になったら火を放つのです。","張飛",},
        [4455] = {7604,"諸葛亮","樊城から趙雲を呼び寄せて先鋒を命じます。ただしわざと敗走し、敵を誘き寄せます。","樊城から趙雲を呼び寄せて先鋒を命じます。ただしわざと敗走し、敵を誘き寄せます。","張飛",},
        [4456] = {7604,"諸葛亮","わが君にも一軍を率いて後詰めにあたっていただきます。各々この軍略通りに動き、間違いのないように。","わが君にも一軍を率いて後詰めにあたっていただきます。各々この軍略通りに動き、間違いのないように。","張飛",},
        [4457] = {7604,"関羽","我ら全員が出撃して応戦するのはわかったが、軍師は何をされるのか。","我ら全員が出撃して応戦するのはわかったが、軍師は何をされるのか。","諸葛亮",},
        [4458] = {7604,"諸葛亮","私ですか?もちろん、ここで城を守ります。","私ですか?もちろん、ここで城を守ります。","関羽",},
        [4459] = {7604,"張飛","全員を戦に出しておいて、てめえだけ家の中でのうのうと座ってるってのか！","全員を戦に出しておいて、てめえだけ家の中でのうのうと座ってるってのか！","諸葛亮",},
        [4460] = {7604,"諸葛亮","ええ。このとおり、剣も印もあずかっています。命令に背く者は、斬ります！","ええ。このとおり、剣も印もあずかっています。命令に背く者は、斬ります！","張飛",},
        [4461] = {7604,"劉備","「謀を帷幄のうちに運らし、勝つことを千里の外に決す」と言うではないか。命令に従え、張飛。","「謀を帷幄のうちに運らし、勝つことを千里の外に決す」と言うではないか。命令に従え、張飛。","張飛",},
        [4462] = {7604,"張飛","ふん！","ふん！","劉備",},
        [4463] = {7604,"関羽","ひとまず孔明の計略が当たるかどうか見ようではないか。それから文句をつけても遅くはない。","ひとまず孔明の計略が当たるかどうか見ようではないか。それから文句をつけても遅くはない。","劉備",},
        [4464] = {7604,"諸葛亮","わが君は今日は兵を率いて博望山の麓に陣をお取りください。明日の黄昏時に必ず敵軍がやって来ますので、","わが君は今日は兵を率いて博望山の麓に陣をお取りください。明日の黄昏時に必ず敵軍がやって来ますので、","関羽",},
        [4465] = {7604,"諸葛亮","そうしたら陣地を捨ててお逃げください。しかし火の手が上がったら、すぐに引き返して攻撃してください。","そうしたら陣地を捨ててお逃げください。しかし火の手が上がったら、すぐに引き返して攻撃してください。","関羽",},
        [4466] = {7604,"諸葛亮","私は糜竺、糜芳両名と五百の兵をもって、この県城を守ります。","私は糜竺、糜芳両名と五百の兵をもって、この県城を守ります。","関羽",},
        [4467] = {7605,"夏侯惇","なにが「諸葛亮は奇才」だ！このお粗末な用兵はなんだ。","なにが「諸葛亮は奇才」だ！このお粗末な用兵はなんだ。","",},
        [4468] = {7605,"夏侯惇","この程度の兵力を押し立てて俺に対抗しようとは。犬か羊をけしかけて虎や豹と闘おうとするようなものよぉっ！","この程度の兵力を押し立てて俺に対抗しようとは。犬か羊をけしかけて虎や豹と闘おうとするようなものよぉっ！","",},
        [4469] = {7605,"夏侯惇","俺も丞相の前で、劉備と諸葛亮を生け捕ってみせると大言を吐いたが、今にその通りになるなぁっ！","俺も丞相の前で、劉備と諸葛亮を生け捕ってみせると大言を吐いたが、今にその通りになるなぁっ！","",},
        [4470] = {7605,"趙雲","たわごとはそこまでだ！わが長槍をくらうがいい！","たわごとはそこまでだ！わが長槍をくらうがいい！","夏侯惇",},
        [4471] = {7605,"夏侯惇","うぬらも劉備に従っていたところで、亡霊のお供をするだけだぞぉっ！","うぬらも劉備に従っていたところで、亡霊のお供をするだけだぞぉっ！","趙雲",},
        [4472] = {7606,"李典","趙雲は逃げるふりをして我らを誘っている様子。伏兵があるにちがいありません。","趙雲は逃げるふりをして我らを誘っている様子。伏兵があるにちがいありません。","夏侯惇",},
        [4473] = {7606,"夏侯惇","これしきの敵っ！たとえ十面埋伏の計があっても、恐るるに足りんわぁっ！","これしきの敵っ！たとえ十面埋伏の計があっても、恐るるに足りんわぁっ！","李典",},
        [4474] = {7606,"夏侯惇","一気に博望坡まで突き進めえっ！","一気に博望坡まで突き進めえっ！","李典",},
        [4475] = {7607,"李典","敵を侮るのは敗け戦のもとだ。南に行くに従って道は狭くなり、山と川が迫っていて――","敵を侮るのは敗け戦のもとだ。南に行くに従って道は狭くなり、山と川が迫っていて――","于禁",},
        [4476] = {7607,"李典","草木も生い茂っている。これでもし敵の火攻めにあったら、どうなる?","草木も生い茂っている。これでもし敵の火攻めにあったら、どうなる?","于禁",},
        [4477] = {7607,"于禁","もっともだ。先陣の都督を引き留めに行くから、お前はここで後陣を押しとどめてくれ。","もっともだ。先陣の都督を引き留めに行くから、お前はここで後陣を押しとどめてくれ。","李典",},
        [4478] = {7607,"于禁","後陣はゆっくり進め！都督どの、しばし待たれよ！","後陣はゆっくり進め！都督どの、しばし待たれよ！","李典",},
        [4479] = {7607,"于禁","これより南は道が狭く、山と川が迫って、草木が生い茂っています。火計に用心せねば！","これより南は道が狭く、山と川が迫って、草木が生い茂っています。火計に用心せねば！","李典",},
        [4480] = {7607,"夏侯惇","はっ……全軍、止まれぃっ！","はっ……全軍、止まれぃっ！","于禁",},
        [4481] = {7608,"夏侯惇","くっそぉぉぉっ！李典と于禁が忠告してくれたというのにぃっ！後悔先に立たずとはこのことかぁぁっっ！","くっそぉぉぉっ！李典と于禁が忠告してくれたというのにぃっ！後悔先に立たずとはこのことかぁぁっっ！","",},
        [4482] = {7608,"夏侯惇","まったく惨敗だぁっ！残りの兵を集め、速やかに許昌に帰るぞぉっ！","まったく惨敗だぁっ！残りの兵を集め、速やかに許昌に帰るぞぉっ！","",},
        [4483] = {7609,"張飛","まったく、孔明は大したもんだな！","まったく、孔明は大したもんだな！","諸葛亮",},
        [4484] = {7609,"関羽","いやはや、おそれいった。","いやはや、おそれいった。","諸葛亮",},
        [4485] = {7609,"諸葛亮","夏侯惇は敗走しましたが、次は曹操が自ら大軍を率いて来るでしょう。","夏侯惇は敗走しましたが、次は曹操が自ら大軍を率いて来るでしょう。","関羽",},
        [4486] = {7609,"劉備","それでは、どうする?","それでは、どうする?","諸葛亮",},
        [4487] = {7609,"諸葛亮","私に一計がございます。それならば曹操軍にも対抗できます。","私に一計がございます。それならば曹操軍にも対抗できます。","劉備",},
        [4488] = {7609,"劉備","ぜひ教えてほしい。","ぜひ教えてほしい。","諸葛亮",},
        [4489] = {7609,"諸葛亮","新野は狭く、長居すべき所ではありません。噂では近ごろ劉表は病床にあり、危篤のようです。","新野は狭く、長居すべき所ではありません。噂では近ごろ劉表は病床にあり、危篤のようです。","劉備",},
        [4490] = {7609,"諸葛亮","この機会に荊州を取って安住の地とすれば、曹操も防げましょう。","この機会に荊州を取って安住の地とすれば、曹操も防げましょう。","劉備",},
        [4491] = {7609,"劉備","その策はもっともだが、劉表殿は私の恩人だ。国を奪うようなまねはできない。","その策はもっともだが、劉表殿は私の恩人だ。国を奪うようなまねはできない。","諸葛亮",},
        [4492] = {7609,"諸葛亮","ですが、いま荊州を取っておかなければ、後悔しても及びませんよ。","ですが、いま荊州を取っておかなければ、後悔しても及びませんよ。","劉備",},
        [4493] = {7609,"劉備","たとえ死んでも、義に背くようなことはできぬ。","たとえ死んでも、義に背くようなことはできぬ。","諸葛亮",},
        [4494] = {7609,"諸葛亮","そうですか……ではこの話はひとまず置きましょう。","そうですか……ではこの話はひとまず置きましょう。","劉備",},
        [4495] = {7701,"夏侯惇","諸葛亮の計略にかかり、火攻めによって、わが軍は大敗した次第っ……！","諸葛亮の計略にかかり、火攻めによって、わが軍は大敗した次第っ……！","曹操",},
        [4496] = {7701,"曹操","情けないぞ、夏侯惇。幼少より用兵を習っていながら、狭い道には火攻めのあることぐらい気づかなかったのか。","情けないぞ、夏侯惇。幼少より用兵を習っていながら、狭い道には火攻めのあることぐらい気づかなかったのか。","夏侯惇",},
        [4497] = {7701,"夏侯惇","ぐ……李典と于禁が忠告してくれたのだが……もはや手遅れだった。","ぐ……李典と于禁が忠告してくれたのだが……もはや手遅れだった。","曹操",},
        [4498] = {7701,"曹操","そうか。勝敗は兵家の常。あまり気にすることはない。","そうか。勝敗は兵家の常。あまり気にすることはない。","夏侯惇",},
        [4499] = {7701,"夏侯惇","劉備がこれほど手強くなったとは……捨て置いてはやがて禍いとなるぞ。早急に除き去らねば！","劉備がこれほど手強くなったとは……捨て置いてはやがて禍いとなるぞ。早急に除き去らねば！","曹操",},
        [4500] = {7701,"曹操","わしも憂慮しておるのは劉備と孫権だけだ。あとは気にもかけておらん。この機会に江南を平らげるとしよう。","わしも憂慮しておるのは劉備と孫権だけだ。あとは気にもかけておらん。この機会に江南を平らげるとしよう。","夏侯惇",},
        [4501] = {7701,"曹操","五十万の大軍を起こす。曹仁と曹洪を第一隊とし、張遼と張郃を第二隊とする。","五十万の大軍を起こす。曹仁と曹洪を第一隊とし、張遼と張郃を第二隊とする。","夏侯惇",},
        [4502] = {7701,"曹操","夏侯淵 と夏侯惇は第三隊、于禁と李典は第四隊とし、わし自ら第五隊の総大将を務める。各隊それぞれ兵十万を率いよ。","夏侯淵 と夏侯惇は第三隊、于禁と李典は第四隊とし、わし自ら第五隊の総大将を務める。各隊それぞれ兵十万を率いよ。","夏侯惇",},
        [4503] = {7701,"曹操","許褚を折衝将軍とし、三千の兵を与えて先鋒とする。出陣の日取りは、建安十三年秋七月丙午の日を吉日として選んだ。","許褚を折衝将軍とし、三千の兵を与えて先鋒とする。出陣の日取りは、建安十三年秋七月丙午の日を吉日として選んだ。","夏侯惇",},
        [4504] = {7702,"孔融","劉備と劉表は漢王室の一門、軽々しく討つものではありません。また孫権も江東六郡を支配し、","劉備と劉表は漢王室の一門、軽々しく討つものではありません。また孫権も江東六郡を支配し、","曹操",},
        [4505] = {7702,"孔融","長江の要害を擁しているからには、やすやすとは討ち取れないでしょう。今丞相が大義名分のない兵を興せば、天下の人心を失いますよ。","長江の要害を擁しているからには、やすやすとは討ち取れないでしょう。今丞相が大義名分のない兵を興せば、天下の人心を失いますよ。","曹操",},
        [4506] = {7702,"曹操","だまれ、劉備も劉表も孫権も、勅命に逆らう逆臣だ。征伐して何が悪い！","だまれ、劉備も劉表も孫権も、勅命に逆らう逆臣だ。征伐して何が悪い！","孔融",},
        [4507] = {7702,"曹操","もうよい、退がれ！今度わしに逆らったら、斬り捨てるぞ！","もうよい、退がれ！今度わしに逆らったら、斬り捨てるぞ！","孔融",},
        [4508] = {7703,"孔融","やれやれ……","やれやれ……","",},
        [4509] = {7703,"孔融","不仁をもって仁を討つ、か。それじゃあ負けるに決まってる。","不仁をもって仁を討つ、か。それじゃあ負けるに決まってる。","",},
        [4510] = {7704,"郗慮","孔融は平素から丞相を侮っております。また禰衡と仲が良く、お互いを孔子、顔回の再来とたたえ合うほど。","孔融は平素から丞相を侮っております。また禰衡と仲が良く、お互いを孔子、顔回の再来とたたえ合うほど。","曹操",},
        [4511] = {7704,"郗慮","前に禰衡が丞相に恥をかかせましたのも、孔融の差し金だったとか。","前に禰衡が丞相に恥をかかせましたのも、孔融の差し金だったとか。","曹操",},
        [4512] = {7704,"郗慮","昨日も丞相府を出るとき、不仁をもって仁を討つなら、敗れるに決まっている、などとぬかしておりました。","昨日も丞相府を出るとき、不仁をもって仁を討つなら、敗れるに決まっている、などとぬかしておりました。","曹操",},
        [4513] = {7704,"曹操","おのれ、孔融めが！","おのれ、孔融めが！","郗慮",},
        [4514] = {7704,"曹操","直ちに奴を引っ捕らえ、二人の息子ともども、斬ってしまえ！","直ちに奴を引っ捕らえ、二人の息子ともども、斬ってしまえ！","郗慮",},
        [4515] = {7801,"劉表","わしの病はもう治る見込みがない、じきに死ぬだろう。","わしの病はもう治る見込みがない、じきに死ぬだろう。","劉備",},
        [4516] = {7801,"劉表","わが子のことはお頼み申す。だが、あの至らぬ子らでは跡目は継げぬだろう。わしが死んだら、荊州は劉備殿が治めてくれ。","わが子のことはお頼み申す。だが、あの至らぬ子らでは跡目は継げぬだろう。わしが死んだら、荊州は劉備殿が治めてくれ。","劉備",},
        [4517] = {7801,"劉備","力の限りを尽くして若君を補佐いたしましょう。それ以上の野心はありませぬ。","力の限りを尽くして若君を補佐いたしましょう。それ以上の野心はありませぬ。","劉表",},
        [4518] = {7801,"劉備","曹操みずから大軍を率いて攻めてくるようです。私はひとまず新野に帰りますので、後事の話はまた後ほど。","曹操みずから大軍を率いて攻めてくるようです。私はひとまず新野に帰りますので、後事の話はまた後ほど。","劉表",},
        [4519] = {7802,"劉琦","父の危篤を聞いて、江夏の任地から急いで見舞いに帰ってきたのですが……","父の危篤を聞いて、江夏の任地から急いで見舞いに帰ってきたのですが……","蔡瑁",},
        [4520] = {7802,"蔡瑁","父君のご命を受けて重要な江夏の守りに赴かれながら、無断で任地を離れてご帰国とは。もし呉が攻めて来たら、どうなさるおつもりか！","父君のご命を受けて重要な江夏の守りに赴かれながら、無断で任地を離れてご帰国とは。もし呉が攻めて来たら、どうなさるおつもりか！","劉琦",},
        [4521] = {7802,"蔡瑁","今ご主君に会われても、きっとお怒りになって、病をさらに重くするでしょう。それでは親孝行とはいえぬ。速やかに帰られたがよい。","今ご主君に会われても、きっとお怒りになって、病をさらに重くするでしょう。それでは親孝行とはいえぬ。速やかに帰られたがよい。","劉琦",},
        [4522] = {7802,"劉琦","そんな……","そんな……","蔡瑁",},
        [4523] = {7802,"劉琦","では叔父上、父上のことはくれぐれも頼みます。何かあったらすぐに知らせてください！","では叔父上、父上のことはくれぐれも頼みます。何かあったらすぐに知らせてください！","蔡瑁",},
        [4524] = {7803,"蔡夫人","劉表殿が亡くなりました。跡継ぎの件はどうしましょう?","劉表殿が亡くなりました。跡継ぎの件はどうしましょう?","蔡瑁",},
        [4525] = {7803,"蔡瑁","偽の遺言書を作って、次男の劉琮を荊州の主としよう。喪を公表するのはそのあとで。","偽の遺言書を作って、次男の劉琮を荊州の主としよう。喪を公表するのはそのあとで。","蔡夫人",},
        [4526] = {7803,"蔡夫人","わかりましたわ。","わかりましたわ。","蔡瑁",},
        [4527] = {7804,"劉琮","父君がお亡くなりになり、私が国主に立てられたが、兄上はいま江夏にいるし、外戚の叔父上の劉備殿も新野にいる。","父君がお亡くなりになり、私が国主に立てられたが、兄上はいま江夏にいるし、外戚の叔父上の劉備殿も新野にいる。","",},
        [4528] = {7804,"劉琮","もし兄や叔父が兵を挙げて非道を問うてきたら、どう説明するのだ。","もし兄や叔父が兵を挙げて非道を問うてきたら、どう説明するのだ。","",},
        [4529] = {7804,"李珪","若君、よくぞ仰せられました。いそぎ訃報を出して兄君を迎え、荊州の主にお立てしましょう。劉備殿にも輔佐を頼みましょう。","若君、よくぞ仰せられました。いそぎ訃報を出して兄君を迎え、荊州の主にお立てしましょう。劉備殿にも輔佐を頼みましょう。","劉琮",},
        [4530] = {7804,"李珪","さすれば北は曹操を防ぎ、南は孫権にあたれます。これこそ万全の策です。","さすれば北は曹操を防ぎ、南は孫権にあたれます。これこそ万全の策です。","劉琮",},
        [4531] = {7804,"蔡瑁","貴様と言うヤツは、殿のご遺言に逆らうのか！","貴様と言うヤツは、殿のご遺言に逆らうのか！","",},
        [4532] = {7804,"李珪","お前こそ陰謀をめぐらし、遺書を偽造して長幼の序を乱すとは何事だ！いまに荊州は蔡氏の手で潰れてしまうぞ。","お前こそ陰謀をめぐらし、遺書を偽造して長幼の序を乱すとは何事だ！いまに荊州は蔡氏の手で潰れてしまうぞ。","蔡瑁",},
        [4533] = {7804,"李珪","殿が生きておられたら、真っ先に天誅を下しただろう！","殿が生きておられたら、真っ先に天誅を下しただろう！","蔡瑁",},
        [4534] = {7804,"蔡瑁","こやつを引きずり出して、打ち首にせよ！","こやつを引きずり出して、打ち首にせよ！","",},
        [4535] = {7805,"劉琮","曹操が大軍を率いて攻めてくる。どうしたらよいだろう。","曹操が大軍を率いて攻めてくる。どうしたらよいだろう。","",},
        [4536] = {7805,"王粲","劉琮様はご自身を曹操と比べてどう思われます?","劉琮様はご自身を曹操と比べてどう思われます?","劉琮",},
        [4537] = {7805,"劉琮","……敵わない。","……敵わない。","",},
        [4538] = {7805,"王粲","曹操の配下は勇将強兵ぞろいで、当人も智謀に長けています。呂布を下邳の戦いで捕らえ、袁紹を官渡の戦いで破り、","曹操の配下は勇将強兵ぞろいで、当人も智謀に長けています。呂布を下邳の戦いで捕らえ、袁紹を官渡の戦いで破り、","劉琮",},
        [4539] = {7805,"王粲","劉備を隴右に追いつめ、烏桓を白狼山の戦いで破るなど、戦勝の例は枚挙にいとまがありません。","劉備を隴右に追いつめ、烏桓を白狼山の戦いで破るなど、戦勝の例は枚挙にいとまがありません。","劉琮",},
        [4540] = {7805,"王粲","それが今大軍をもって荊州に南下してくるとあらば、とても敵いません。","それが今大軍をもって荊州に南下してくるとあらば、とても敵いません。","劉琮",},
        [4541] = {7805,"王粲","傅巽、蒯越両君の進言どおり、降伏しましょう。ぐずぐずしていると、後悔しますぞ。","傅巽、蒯越両君の進言どおり、降伏しましょう。ぐずぐずしていると、後悔しますぞ。","劉琮",},
        [4542] = {7805,"劉琮","先生のご教示はごもっとも。しかし、母上はどう思われます?","先生のご教示はごもっとも。しかし、母上はどう思われます?","",},
        [4543] = {7805,"蔡夫人","わたしも賛成です。曹操に降りましょう。","わたしも賛成です。曹操に降りましょう。","劉琮",},
        [4544] = {7805,"劉琮","わかりました。では降伏の書を奉ります。","わかりました。では降伏の書を奉ります。","蔡夫人",},
        [4545] = {7806,"劉備","蔡夫人と蔡瑁は、江夏を守る劉琦に訃報も出さず、偽の遺言書を作って劉琮を国主に立てた。","蔡夫人と蔡瑁は、江夏を守る劉琦に訃報も出さず、偽の遺言書を作って劉琮を国主に立てた。","",},
        [4546] = {7806,"劉備","まさかその劉琮が早ばや荊・襄九郡を曹操に献じてしまうとは……！","まさかその劉琮が早ばや荊・襄九郡を曹操に献じてしまうとは……！","",},
        [4547] = {7806,"劉備","劉表殿は危篤のおり、私にご子息の後事を託されたというのに。","劉表殿は危篤のおり、私にご子息の後事を託されたというのに。","",},
        [4548] = {7806,"劉備","今その子を捕らえて領地を奪っては、あの世で劉表殿に会わす顔がないではないか。","今その子を捕らえて領地を奪っては、あの世で劉表殿に会わす顔がないではないか。","",},
        [4549] = {7806,"主君","忌々しいことです。仕方がない、奴らを懲らしめてやりましょう！","忌々しいことです。仕方ありません、彼らを懲らしめてやりましょう！","劉備",},
        [4550] = {7807,"劉備","曹操軍は既に宛城まで来ている。どう当たればいい?","曹操軍は既に宛城まで来ている。どう当たればいい?","諸葛亮",},
        [4551] = {7807,"諸葛亮","戦わず、樊城へ避難しましょう。","戦わず、樊城へ避難しましょう。","劉備",},
        [4552] = {7807,"劉備","わかった。そなたを信じよう。","わかった。そなたを信じよう。","諸葛亮",},
        [4553] = {7901,"劉備","曹操軍は既に博望城まで迫ってきた。孔明、早く防戦の策を講じなければ。","曹操軍は既に博望城まで迫ってきた。孔明、早く防戦の策を講じなければ。","諸葛亮",},
        [4554] = {7901,"諸葛亮","落ち着いてください。前に夏侯惇軍の大半を焼き尽くしたように、今度の曹操軍にも火計を用いましょう。","落ち着いてください。前に夏侯惇軍の大半を焼き尽くしたように、今度の曹操軍にも火計を用いましょう。","劉備",},
        [4555] = {7901,"劉備","どうするのだ?","どうするのだ?","諸葛亮",},
        [4556] = {7901,"諸葛亮","その前にやらねばならぬことがあります。","その前にやらねばならぬことがあります。","劉備",},
        [4557] = {7901,"諸葛亮","新野の領民に布告を出してください。領主に従おうという者は、老幼男女を問わず、一緒に樊城に避難せよ、遅れぬようにと。","新野の領民に布告を出してください。領主に従おうという者は、老幼男女を問わず、一緒に樊城に避難せよ、遅れぬようにと。","劉備",},
        [4558] = {7902,"劉備","敵にはどう当たるのだ?","敵にはどう当たるのだ?","諸葛亮",},
        [4559] = {7902,"諸葛亮","孫乾殿は川岸に舟を用意して避難民を渡し、糜竺殿は官吏の家族を樊城まで護送するよう。","孫乾殿は川岸に舟を用意して避難民を渡し、糜竺殿は官吏の家族を樊城まで護送するよう。","劉備",},
        [4560] = {7902,"諸葛亮","また関羽殿は一千の兵を率いて白河上流に埋伏し、土嚢を築いて川の流れをせき止めてください。","また関羽殿は一千の兵を率いて白河上流に埋伏し、土嚢を築いて川の流れをせき止めてください。","劉備",},
        [4561] = {7902,"諸葛亮","明日真夜中ごろ、下流から馬のいななきや兵の叫び声が聞こえたら、すぐ土嚢の堰を切って水を流し、下流に向かって攻めて来てください。","明日真夜中ごろ、下流から馬のいななきや兵の叫び声が聞こえたら、すぐ土嚢の堰を切って水を流し、下流に向かって攻めて来てください。","劉備",},
        [4562] = {7902,"諸葛亮","張飛殿は一千の兵をつれ、博陵の渡し場で待ち伏せてください。","張飛殿は一千の兵をつれ、博陵の渡し場で待ち伏せてください。","劉備",},
        [4563] = {7902,"諸葛亮","ここは水の流れが最も緩い。水攻めにあった曹操軍はきっとここに逃げてくるでしょう。そこをすかさず討ち取ってください。","ここは水の流れが最も緩い。水攻めにあった曹操軍はきっとここに逃げてくるでしょう。そこをすかさず討ち取ってください。","劉備",},
        [4564] = {7902,"諸葛亮","趙雲殿は三千の兵を四隊に分け、自らは一隊を率いて東門の外で待機し、残る三隊を西門・北門・南門の三手に差し向けるよう。","趙雲殿は三千の兵を四隊に分け、自らは一隊を率いて東門の外で待機し、残る三隊を西門・北門・南門の三手に差し向けるよう。","劉備",},
        [4565] = {7902,"諸葛亮","前もって城内の民家の屋根に、硫黄や煙硝など引火物を隠しておいてください。","前もって城内の民家の屋根に、硫黄や煙硝など引火物を隠しておいてください。","劉備",},
        [4566] = {7902,"諸葛亮","曹操軍は城内に入ったら、きっと民家で休息をとるでしょう。","曹操軍は城内に入ったら、きっと民家で休息をとるでしょう。","劉備",},
        [4567] = {7902,"諸葛亮","明日は暮れ方から大風が吹くはず。風が吹いたら、西門・南門・北門の伏兵に命じて城内に火矢を射込んでください。","明日は暮れ方から大風が吹くはず。風が吹いたら、西門・南門・北門の伏兵に命じて城内に火矢を射込んでください。","劉備",},
        [4568] = {7902,"諸葛亮","城内が火の海と化すのを待って、城外から鬨の声を上げます。","城内が火の海と化すのを待って、城外から鬨の声を上げます。","劉備",},
        [4569] = {7902,"諸葛亮","東門だけを空けて敵兵を逃がし、門の外に出たところを背後から攻めかかってください。","東門だけを空けて敵兵を逃がし、門の外に出たところを背後から攻めかかってください。","劉備",},
        [4570] = {7902,"諸葛亮","夜明けには関羽、張飛両軍と合流し、兵をまとめて樊城に戻ってください。さらに糜芳、劉封の両名に二千の軍を授けます。","夜明けには関羽、張飛両軍と合流し、兵をまとめて樊城に戻ってください。さらに糜芳、劉封の両名に二千の軍を授けます。","劉備",},
        [4571] = {7902,"諸葛亮","半数ずつに赤い旗と青い旗を持たせて、新野の城外三十里にある鵲尾坡に行ってください。","半数ずつに赤い旗と青い旗を持たせて、新野の城外三十里にある鵲尾坡に行ってください。","劉備",},
        [4572] = {7902,"諸葛亮","曹操軍が来るのを見たら、赤旗の軍は左に、青旗の軍は右に移動します。","曹操軍が来るのを見たら、赤旗の軍は左に、青旗の軍は右に移動します。","劉備",},
        [4573] = {7902,"諸葛亮","敵は怪しんで追ってはこないでしょう。お二人は別々に待ち伏せてください。","敵は怪しんで追ってはこないでしょう。お二人は別々に待ち伏せてください。","劉備",},
        [4574] = {7902,"諸葛亮","城内に火の手が上がるのが見えたら、混乱する敵兵を追って討ち、それから白河の上流まで応援に来てください。","城内に火の手が上がるのが見えたら、混乱する敵兵を追って討ち、それから白河の上流まで応援に来てください。","劉備",},
        [4575] = {7902,"劉備","よし、では各々手はず通り動くのだ。","よし、では各々手はず通り動くのだ。","諸葛亮",},
        [4576] = {7903,"許褚","ブヒッ！鵲尾坡の前に敵軍がおって、しきりに青い旗と赤い旗を振っているぞう！","ブヒッ！鵲尾坡の前に敵軍がおって、しきりに青い旗と赤い旗を振っているぞう！","曹仁",},
        [4577] = {7903,"許褚","この先にきっと伏兵がいるのだ！ここで止まるしかないぞう。","この先にきっと伏兵がいるのだ！ここで止まるしかないぞう。","曹仁",},
        [4578] = {7903,"曹仁","いや、それは疑兵の計だ。伏兵などおるまい。進軍を続けよ。","いや、それは疑兵の計だ。伏兵などおるまい。進軍を続けよ。","許褚",},
        [4579] = {7904,"曹仁","城内には人っ子一人おらん。もぬけの殻だ。","城内には人っ子一人おらん。もぬけの殻だ。","",},
        [4580] = {7904,"曹仁","フン。万策尽きて、領民を連れて逃げてしまったか。ひとまず城内で軍を休ませ、明日夜明けとともに出発しよう。","フン。万策尽きて、領民を連れて逃げてしまったか。ひとまず城内で軍を休ませ、明日夜明けとともに出発しよう。","",},
        [4581] = {7904,"曹仁","兵士たちも歩き疲れて、腹が減っていたようだな。争って民家に入って飯を炊き始めたわ。","兵士たちも歩き疲れて、腹が減っていたようだな。争って民家に入って飯を炊き始めたわ。","",},
        [4582] = {7905,"許褚","ブヒィッ！西門、南門、北門の三方から火の手が上がっているぞう！","ブヒィッ！西門、南門、北門の三方から火の手が上がっているぞう！","曹仁",},
        [4583] = {7905,"曹仁","東門だけは火がない、急げ！","東門だけは火がない、急げ！","許褚",},
        [4584] = {7905,"趙雲","曹仁、逃げる気か！","曹仁、逃げる気か！","曹仁",},
        [4585] = {7905,"糜芳","賊め、どこに行く！","賊め、どこに行く！","曹仁",},
        [4586] = {7905,"張飛","待ちやがれ、その首を置いてけってんだ！","待ちやがれ、その首を置いてけってんだ！","曹仁",},
        [4587] = {7906,"曹仁","丞相、こたびの敗戦、面目次第もございません。","丞相、こたびの敗戦、面目次第もございません。","曹操",},
        [4588] = {7906,"曹操","そうか。勝敗は兵家の常。あまり気にすることはない。","そうか。勝敗は兵家の常。あまり気にすることはない。","曹仁",},
        [4589] = {7906,"曹操","だが、諸葛亮とやら、よくもやってくれたな！","だが、諸葛亮とやら、よくもやってくれたな！","曹仁",},
        [4590] = {7906,"曹操","全軍に伝えよ。残らず新野に移動して、陣を構えよ。山捜しをし、白河の渡しを兵で埋めよ。","全軍に伝えよ。残らず新野に移動して、陣を構えよ。山捜しをし、白河の渡しを兵で埋めよ。","曹仁",},
        [4591] = {7906,"曹操","全軍を八路に分け、一斉に樊城を攻めるぞ！","全軍を八路に分け、一斉に樊城を攻めるぞ！","曹仁",},
        [4592] = {8001,"劉曄","丞相は初めて襄陽にいらしたのですから、まずは民心を得なければなりません。劉備が新野の領民をことごとく樊城に移した今、","丞相は初めて襄陽にいらしたのですから、まずは民心を得なければなりません。劉備が新野の領民をことごとく樊城に移した今、","曹操",},
        [4593] = {8001,"劉曄","わが軍がこのまま進撃すれば、この二県の民は皆殺しになってしまいます。まずは使者をやって劉備に降伏を勧めるべきです。","わが軍がこのまま進撃すれば、この二県の民は皆殺しになってしまいます。まずは使者をやって劉備に降伏を勧めるべきです。","曹操",},
        [4594] = {8001,"劉曄","たとえ劉備が降らずとも、こちらが民を大事にする気持ちは伝わりましょう。もし降ってくれば、荊州の地は、戦わずして手に入ります。","たとえ劉備が降らずとも、こちらが民を大事にする気持ちは伝わりましょう。もし降ってくれば、荊州の地は、戦わずして手に入ります。","曹操",},
        [4595] = {8001,"曹操","では、誰を使者にやればいい?","では、誰を使者にやればいい?","劉曄",},
        [4596] = {8001,"劉曄","徐庶は劉備と親しく、ちょうど今従軍しております。彼に行かせればいかがでしょう。","徐庶は劉備と親しく、ちょうど今従軍しております。彼に行かせればいかがでしょう。","曹操",},
        [4597] = {8001,"曹操","徐庶か。そのまま戻って来ないのではないか。","徐庶か。そのまま戻って来ないのではないか。","劉曄",},
        [4598] = {8001,"劉曄","ご心配なさらず。戻らねば、世の笑いものになりましょう。","ご心配なさらず。戻らねば、世の笑いものになりましょう。","曹操",},
        [4599] = {8002,"曹操","当初は樊城を踏み潰すつもりだったが、それでは民が憐れだ。そこで劉備のもとに説得に行ってもらいたい。","当初は樊城を踏み潰すつもりだったが、それでは民が憐れだ。そこで劉備のもとに説得に行ってもらいたい。","徐庶",},
        [4600] = {8002,"曹操","わしに降れば、罪を赦して官位を与えよう。だが頑迷にも降らぬとあらば、領民もろとも皆殺しにすると伝えよ。","わしに降れば、罪を赦して官位を与えよう。だが頑迷にも降らぬとあらば、領民もろとも皆殺しにすると伝えよ。","徐庶",},
        [4601] = {8002,"曹操","貴公の忠義は信じておるから、特別に使者を任せるのだ。わしの期待に背くなよ。","貴公の忠義は信じておるから、特別に使者を任せるのだ。わしの期待に背くなよ。","徐庶",},
        [4602] = {8002,"徐庶","かしこまりました。","かしこまりました。","曹操",},
        [4603] = {8003,"徐庶","曹操が私を使者に立てて投降を勧めるのは、民心を買おうとしてのことです。曹操は兵を八路に分けー","曹操が私を使者に立てて投降を勧めるのは、民心を買おうとしてのことです。曹操は兵を八路に分けー","劉備",},
        [4604] = {8003,"徐庶","白河を埋め尽くす勢いで進撃しようとしています。おそらく樊城は守り切れないでしょう。速やかにお立ち退きください。","白河を埋め尽くす勢いで進撃しようとしています。おそらく樊城は守り切れないでしょう。速やかにお立ち退きください。","劉備",},
        [4605] = {8003,"劉備","徐庶殿、せっかく来てくれたのに、戻らねばならぬのか。","徐庶殿、せっかく来てくれたのに、戻らねばならぬのか。","徐庶",},
        [4606] = {8003,"徐庶","戻らねば、物笑いの種になりましょう。老母が亡くなってから、一生の恨みを抱くばかり。身は曹操のもとにあってもー","戻らねば、物笑いの種になりましょう。老母が亡くなってから、一生の恨みを抱くばかり。身は曹操のもとにあってもー","劉備",},
        [4607] = {8003,"徐庶","誓って彼のために献策はいたしませぬ。劉備殿には臥龍の補佐があれば、必ずや大業を成し遂げましょう。これにておいとまいたします。","誓って彼のために献策はいたしませぬ。劉備殿には臥龍の補佐があれば、必ずや大業を成し遂げましょう。これにておいとまいたします。","劉備",},
        [4608] = {8003,"劉備","そうか……では無理に引き留めはしない。くれぐれも体を大事にな。","そうか……では無理に引き留めはしない。くれぐれも体を大事にな。","徐庶",},
        [4609] = {8004,"徐庶","丞相に申し上げます。劉備に降る意思は全くございません。","丞相に申し上げます。劉備に降る意思は全くございません。","曹操",},
        [4610] = {8004,"曹操","そうか。ならば直ちに兵を進めよ！","そうか。ならば直ちに兵を進めよ！","徐庶",},
        [4611] = {8005,"孫乾","領民が劉備殿に付き従いたい、ともに川を渡りたいと申しております。","領民が劉備殿に付き従いたい、ともに川を渡りたいと申しております。","劉備",},
        [4612] = {8005,"劉備","私一人のために領民に苦難を強いてしまうとは。いっそ私が死ねば……","私一人のために領民に苦難を強いてしまうとは。いっそ私が死ねば……","孫乾",},
        [4613] = {8005,"孫乾","早まってはなりませぬ！ご自分をお責めなさいますな。","早まってはなりませぬ！ご自分をお責めなさいますな。","劉備",},
        [4614] = {8005,"孫乾","曹操軍はそこまで迫っております。急いで川を渡りましょう。","曹操軍はそこまで迫っております。急いで川を渡りましょう。","劉備",},
        [4615] = {8005,"劉備","領民も残さず南岸に渡すのだぞ。そして襄陽に行こう。","領民も残さず南岸に渡すのだぞ。そして襄陽に行こう。","孫乾",},
        [4616] = {8006,"劉備","劉琮殿、私はただ領民の命を救いたいだけで、他意はない。どうか襄陽の門を開けてくれぬか。","劉琮殿、私はただ領民の命を救いたいだけで、他意はない。どうか襄陽の門を開けてくれぬか。","劉琮",},
        [4617] = {8006,"劉琮","騙されぬぞ。門を開けば、こちらに危害が及ぶではないか！","騙されぬぞ。門を開けば、こちらに危害が及ぶではないか！","劉備",},
        [4618] = {8006,"魏延","蔡瑁と張允は売国奴だ！劉備殿こそ仁徳の人。いま民を救わんがために投じてきたのに、なぜ拒むか！","蔡瑁と張允は売国奴だ！劉備殿こそ仁徳の人。いま民を救わんがために投じてきたのに、なぜ拒むか！","劉備",},
        [4619] = {8006,"魏延","ええい、俺が吊り橋を下ろそう。劉備殿、早く兵を率いて城内にお入りください。そして共に売国奴を殺してやりましょう！","ええい、俺が吊り橋を下ろそう。劉備殿、早く兵を率いて城内にお入りください。そして共に売国奴を殺してやりましょう！","劉備",},
        [4620] = {8006,"劉備","いや、領民を驚かせてはならぬ。","いや、領民を驚かせてはならぬ。","魏延",},
        [4621] = {8007,"劉備","民を保護するつもりが、襄陽に入ったらかえって民を苦しめてしまう。やはり入るのはよそう。","民を保護するつもりが、襄陽に入ったらかえって民を苦しめてしまう。やはり入るのはよそう。","",},
        [4622] = {8007,"諸葛亮","江陵こそは荊州の要地。まずは江陵を取って拠点としましょう。","江陵こそは荊州の要地。まずは江陵を取って拠点としましょう。","劉備",},
        [4623] = {8007,"劉備","おお、まさに望むところだ。","おお、まさに望むところだ。","諸葛亮",},
        [4624] = {8007,"劉備","諸将に命じる。領民を引き連れて襄陽を離れ、江陵に向かうのだ。","諸将に命じる。領民を引き連れて襄陽を離れ、江陵に向かうのだ。","諸葛亮",},
        [4625] = {8101,"曹操","蒯越を江陵の太守・樊城侯とし、傅巽、王粲等は関内侯とする。劉琮は青州刺史だ。即日着任の途に就け。","蒯越を江陵の太守・樊城侯とし、傅巽、王粲等は関内侯とする。劉琮は青州刺史だ。即日着任の途に就け。","",},
        [4626] = {8101,"劉琮","私は官位は要りません！父母の郷里を守りたく存じます。","私は官位は要りません！父母の郷里を守りたく存じます。","曹操",},
        [4627] = {8101,"曹操","青州は帝都に近いゆえ、朝廷の官にしてやったのだ。荊州などにいては危害が及ぶぞ。","青州は帝都に近いゆえ、朝廷の官にしてやったのだ。荊州などにいては危害が及ぶぞ。","劉琮",},
        [4628] = {8101,"劉琮","いえ、どうか丞相、任命を撤回していただきますよう。","いえ、どうか丞相、任命を撤回していただきますよう。","曹操",},
        [4629] = {8101,"曹操","ただちに出発するのだ。退がれ。","ただちに出発するのだ。退がれ。","劉琮",},
        [4630] = {8101,"劉琮","はっ……","はっ……","曹操",},
        [4631] = {8101,"曹操","于禁はどこだ?","于禁はどこだ?","",},
        [4632] = {8101,"于禁","ここにぃ！","ここにぃ！","曹操",},
        [4633] = {8101,"曹操","軽騎をつれて劉琮の後を追い、母子ともども殺せ。将来の禍根を断つのだ。","軽騎をつれて劉琮の後を追い、母子ともども殺せ。将来の禍根を断つのだ。","于禁",},
        [4634] = {8101,"于禁","御意。","御意。","曹操",},
        [4635] = {8102,"于禁","丞相の命令で、母子ともども命をいただきに参った！おとなしく首を寄こせ！","丞相の命令で、母子ともども命をいただきに参った！おとなしく首を寄こせ！","蔡夫人",},
        [4636] = {8102,"蔡夫人","なにとぞお見逃しください！","なにとぞお見逃しください！","于禁",},
        [4637] = {8102,"于禁","ならぬ。ものども、殺れッ！","ならぬ。ものども、殺れッ！","蔡夫人",},
        [4638] = {8103,"于禁","劉備は領民を引き連れているため、一日に十数里しか進めません。ここからはわずか三百里余りの距離でしょう。","劉備は領民を引き連れているため、一日に十数里しか進めません。ここからはわずか三百里余りの距離でしょう。","曹操",},
        [4639] = {8103,"曹操","鉄騎兵五千を選りすぐり、一昼夜の間に劉備に追いつけ。","鉄騎兵五千を選りすぐり、一昼夜の間に劉備に追いつけ。","于禁",},
        [4640] = {8103,"曹操","我が本隊もその後について進軍する。","我が本隊もその後について進軍する。","于禁",},
        [4641] = {8104,"劉備","にわかに狂風が起こり、土煙が天まで吹き上げ、日の光を遮った。何かの前触れだろうか?","にわかに狂風が起こり、土煙が天まで吹き上げ、日の光を遮った。何かの前触れだろうか?","",},
        [4642] = {8104,"簡雍","これは大凶の兆しです。おそらくは今夜。早く民を見捨ててお逃げください。","これは大凶の兆しです。おそらくは今夜。早く民を見捨ててお逃げください。","劉備",},
        [4643] = {8104,"劉備","領民たちは新野からここまで私に付き従ってきたのだ。棄てゆくわけにはいかぬ。","領民たちは新野からここまで私に付き従ってきたのだ。棄てゆくわけにはいかぬ。","簡雍",},
        [4644] = {8104,"簡雍","お見捨てにならないとあらば、禍いは目前です。","お見捨てにならないとあらば、禍いは目前です。","劉備",},
        [4645] = {8104,"劉備","この先に見えるのはどこだ?","この先に見えるのはどこだ?","簡雍",},
        [4646] = {8104,"簡雍","この先は当陽県です。景山という山があります。","この先は当陽県です。景山という山があります。","劉備",},
        [4647] = {8104,"劉備","ではこの山に軍をとどめよう。","ではこの山に軍をとどめよう。","簡雍",},
        [4648] = {8105,"糜芳","趙雲が裏切りました！曹操に投じて行ったようです！","趙雲が裏切りました！曹操に投じて行ったようです！","劉備",},
        [4649] = {8105,"劉備","趙雲は旧友だ。裏切るわけがなかろう。","趙雲は旧友だ。裏切るわけがなかろう。","糜芳",},
        [4650] = {8105,"張飛","いいや、兄貴。窮状に陥ったオレたちを見限って、いい思いをしようとしたんじゃねえのか。","いいや、兄貴。窮状に陥ったオレたちを見限って、いい思いをしようとしたんじゃねえのか。","劉備",},
        [4651] = {8105,"劉備","趙雲が私についたのはもともと苦難にあったときだ。鉄のように固い信念を持つあの男が、富貴などに心を動かされるものか。","趙雲が私についたのはもともと苦難にあったときだ。鉄のように固い信念を持つあの男が、富貴などに心を動かされるものか。","張飛",},
        [4652] = {8105,"糜芳","しかし趙雲が西北に駆けていくのをこの目で見たのです。","しかし趙雲が西北に駆けていくのをこの目で見たのです。","劉備",},
        [4653] = {8105,"張飛","オレが捜しに行ってくらあ。見つけたら、ひと槍で突き殺してやる！","オレが捜しに行ってくらあ。見つけたら、ひと槍で突き殺してやる！","劉備",},
        [4654] = {8105,"劉備","疑うのはよさないか。関羽が顔良と文醜を討ったことを忘れたのか。","疑うのはよさないか。関羽が顔良と文醜を討ったことを忘れたのか。","張飛",},
        [4655] = {8105,"劉備","趙雲にはきっとわけがあるはずだ。私を見捨てるはずはない、そう信じている。","趙雲にはきっとわけがあるはずだ。私を見捨てるはずはない、そう信じている。","張飛",},
        [4656] = {8105,"張飛","じゃあ兵をつれて長坂橋で待つ。趙雲が帰ってくるかどうか見ててやるぜ。","じゃあ兵をつれて長坂橋で待つ。趙雲が帰ってくるかどうか見ててやるぜ。","劉備",},
        [4657] = {8106,"趙雲","主君から甘夫人と糜夫人、それに幼主の阿斗様を託されたというのに。","主君から甘夫人と糜夫人、それに幼主の阿斗様を託されたというのに。","",},
        [4658] = {8106,"趙雲","今日、軍中ではぐれてしまった。このままでは主君に会わす顔がない。この命をかけても、両夫人と幼主の行方を捜さねば！","今日、軍中ではぐれてしまった。このままでは主君に会わす顔がない。この命をかけても、両夫人と幼主の行方を捜さねば！","",},
        [4659] = {8107,"趙雲","両夫人を見かけなかったか?","両夫人を見かけなかったか?","簡雍",},
        [4660] = {8107,"簡雍","ご夫人方は車も供の者も失って、阿斗様だけ抱いてお逃げになった。","ご夫人方は車も供の者も失って、阿斗様だけ抱いてお逃げになった。","趙雲",},
        [4661] = {8107,"簡雍","私は馬を飛ばして、丘を越えようとしたのだが、敵将の槍で突かれて落馬したのだ。馬も奪われてしまった。","私は馬を飛ばして、丘を越えようとしたのだが、敵将の槍で突かれて落馬したのだ。馬も奪われてしまった。","趙雲",},
        [4662] = {8107,"簡雍","甘夫人は髪を振り乱し、裸足になって、領民の一群とともに、南に落ち延びてゆかれた。","甘夫人は髪を振り乱し、裸足になって、領民の一群とともに、南に落ち延びてゆかれた。","趙雲",},
        [4663] = {8107,"趙雲","俺の従者の馬を貸すから、戻ってご主君に知らせてくれ。何としてでも両夫人と阿斗様を捜し出すと。","俺の従者の馬を貸すから、戻ってご主君に知らせてくれ。何としてでも両夫人と阿斗様を捜し出すと。","簡雍",},
        [4664] = {8107,"趙雲","見つからぬときは、この戦場で果てるまで！","見つからぬときは、この戦場で果てるまで！","簡雍",},
        [4665] = {8108,"簡雍","混戦の中で趙雲に救われ、主君に報告に戻るよういわれた次第です。","混戦の中で趙雲に救われ、主君に報告に戻るよういわれた次第です。","劉備",},
        [4666] = {8108,"簡雍","趙雲は何としてでもご両夫人と阿斗様を捜し出す、でなければ戦場で果てると申しておりました。","趙雲は何としてでもご両夫人と阿斗様を捜し出す、でなければ戦場で果てると申しておりました。","劉備",},
        [4667] = {8108,"劉備","それでは趙雲が危い。どうしたらいいだろう?","それでは趙雲が危い。どうしたらいいだろう?","簡雍",},
        [4668] = {8108,"主君","心配無用です。私が行って趙雲将軍を助け、ご夫人と阿斗様を捜します。","心配無用です。私が行って趙雲将軍を助け、ご夫人と阿斗様を捜しますわ。","劉備",},
        [4669] = {8108,"劉備","では頼むぞ。くれぐれも気をつけてくれ。","では頼むぞ。くれぐれも気をつけてくれ。","プレイヤー",},
        [4670] = {8109,"趙雲","あの領民たちの中に、もしかして甘夫人がおられるのでは?","あの領民たちの中に、もしかして甘夫人がおられるのでは?","",},
        [4671] = {8109,"甘夫人","趙雲！","趙雲！","趙雲",},
        [4672] = {8109,"趙雲","申し訳ありませぬ！はぐれてしまったのは、私の咎です。糜夫人と阿斗様はいずこに?","申し訳ありませぬ！はぐれてしまったのは、私の咎です。糜夫人と阿斗様はいずこに?","甘夫人",},
        [4673] = {8109,"甘夫人","一緒に追われて、車も供も失くして、領民にまじって歩いていたのです。そうしたらまた敵兵に蹴散らされて――","一緒に追われて、車も供も失くして、領民にまじって歩いていたのです。そうしたらまた敵兵に蹴散らされて――","趙雲",},
        [4674] = {8109,"甘夫人","糜夫人も阿斗様も行方知れずになりました。わたし一人命からがらここに逃れてきたのです。","糜夫人も阿斗様も行方知れずになりました。わたし一人命からがらここに逃れてきたのです。","趙雲",},
        [4675] = {8109,"趙雲","甘夫人、馬にお乗りください。私が血路を開き、長坂城までお送りいたす。","甘夫人、馬にお乗りください。私が血路を開き、長坂城までお送りいたす。","甘夫人",},
        [4676] = {8110,"張飛","趙雲っ！おのれ、兄貴を裏切りやがったなァ！","趙雲っ！おのれ、兄貴を裏切りやがったなァ！","趙雲",},
        [4677] = {8110,"趙雲","バカを申すな！はぐれたご夫人と阿斗様を捜して、引き返しただけだ。","バカを申すな！はぐれたご夫人と阿斗様を捜して、引き返しただけだ。","張飛",},
        [4678] = {8110,"張飛","さっき簡雍の知らせがなかったら、お前を見るなり、たたっ斬るところだったぜ！","さっき簡雍の知らせがなかったら、お前を見るなり、たたっ斬るところだったぜ！","趙雲",},
        [4679] = {8110,"趙雲","ご主君はいずこに?","ご主君はいずこに?","張飛",},
        [4680] = {8110,"張飛","すぐこの先にいるぜ。","すぐこの先にいるぜ。","趙雲",},
        [4681] = {8110,"趙雲","甘夫人を先にお送りしてくれ。俺は糜夫人と阿斗様を捜しに戻る。","甘夫人を先にお送りしてくれ。俺は糜夫人と阿斗様を捜しに戻る。","張飛",},
        [4682] = {8110,"張飛","気をつけろよ。揚威将軍もさっき、ご夫人と阿斗様を捜して、敵陣に突っ込んで行きやがった。","気をつけろよ。揚威将軍もさっき、ご夫人と阿斗様を捜して、敵陣に突っ込んで行きやがった。","趙雲",},
        [4683] = {8110,"趙雲","そうか、なら揚威将軍を探して一緒にご夫人と阿斗様の救出に向かおう。","そうか、なら揚威将軍を探して一緒にご夫人と阿斗様の救出に向かおう。","張飛",},
        [4684] = {8111,"趙雲","駆けつけるのが遅れ、申し訳ございません。","駆けつけるのが遅れ、申し訳ございません。","糜夫人",},
        [4685] = {8111,"糜夫人","ああ将軍、お会いできて、阿斗の命も助かりましたわ。","ああ将軍、お会いできて、阿斗の命も助かりましたわ。","趙雲",},
        [4686] = {8111,"糜夫人","殿がさすらいの半生でただ一人もうけましたこの子を、憐れと思ってやってくださいまし。","殿がさすらいの半生でただ一人もうけましたこの子を、憐れと思ってやってくださいまし。","趙雲",},
        [4687] = {8111,"糜夫人","将軍がこの子を守って、父に会わせてやってくださいましたら、わたくしはここで死んでも悔いはございません。","将軍がこの子を守って、父に会わせてやってくださいましたら、わたくしはここで死んでも悔いはございません。","趙雲",},
        [4688] = {8111,"趙雲","なにを申されるか！奥方様が難に遭われたのは、私のせいです。","なにを申されるか！奥方様が難に遭われたのは、私のせいです。","糜夫人",},
        [4689] = {8111,"趙雲","どうか馬にお乗りくださいませ。私は徒歩で戦い、敵の囲みを切り拓きます。","どうか馬にお乗りくださいませ。私は徒歩で戦い、敵の囲みを切り拓きます。","糜夫人",},
        [4690] = {8111,"糜夫人","なりません！将軍に馬がなければ、この子も危ないでしょう。わたくしはもはやこの深手です。死んでも惜しくはありません。","なりません！将軍に馬がなければ、この子も危ないでしょう。わたくしはもはやこの深手です。死んでも惜しくはありません。","趙雲",},
        [4691] = {8111,"糜夫人","将軍、早くこの子を抱いて、行ってくださいまし。わたくしがいては、足手まといです。","将軍、早くこの子を抱いて、行ってくださいまし。わたくしがいては、足手まといです。","趙雲",},
        [4692] = {8111,"趙雲","鬨の声が近い。敵が迫ってきました。早く馬に！","鬨の声が近い。敵が迫ってきました。早く馬に！","糜夫人",},
        [4693] = {8111,"糜夫人","どうしても行けません。このままではみな敵の手にかかってしまいます。","どうしても行けません。このままではみな敵の手にかかってしまいます。","趙雲",},
        [4694] = {8111,"糜夫人","この子の命は将軍にかかっているのです！(糜夫人は阿斗を趙雲に渡す)","この子の命は将軍にかかっているのです！(糜夫人は阿斗を趙雲に渡す)","趙雲",},
        [4695] = {8111,"趙雲","どうかお聞き入れください！敵が来てからでは遅いのです！","どうかお聞き入れください！敵が来てからでは遅いのです！","糜夫人",},
        [4696] = {8111,"糜夫人","阿斗のことは、頼みました！(糜夫人は阿斗を地面に置き、古井戸に身を投げる)","阿斗のことは、頼みました！(糜夫人は阿斗を地面に置き、古井戸に身を投げる)","趙雲",},
        [4697] = {8111,"趙雲","あっ、糜夫人！(趙雲は夫人が亡くなったのを見て、曹操軍に遺体を盗まれぬよう、土塀を突き崩して井戸を埋めた。","あっ、糜夫人！(趙雲は夫人が亡くなったのを見て、曹操軍に遺体を盗まれぬよう、土塀を突き崩して井戸を埋めた。","糜夫人",},
        [4698] = {8111,"趙雲","鎧の紐を解き、胸当ての下に阿斗を抱きかかえ、槍を手にして馬に飛び乗った)。","鎧の紐を解き、胸当ての下に阿斗を抱きかかえ、槍を手にして馬に飛び乗った)。","糜夫人",},
        [4699] = {8112,"主君","趙雲殿！両夫人と阿斗様は見つかりましたか?","趙雲殿！両夫人と阿斗様は見つかりました?","趙雲",},
        [4700] = {8112,"趙雲","甘夫人は無事に長坂橋を渡られた。","甘夫人は無事に長坂橋を渡られた。","プレイヤー",},
        [4701] = {8112,"趙雲","阿斗様は俺の懐の中だ。糜夫人は……","阿斗様は俺の懐の中だ。糜夫人は……","プレイヤー",},
        [4702] = {8112,"主君","糜夫人は?","糜夫人は?","趙雲",},
        [4703] = {8112,"趙雲","糜夫人は深手を負われて、足手まといになるまいと、古井戸に身を投げてしまわれた……","糜夫人は深手を負われて、足手まといになるまいと、古井戸に身を投げてしまわれた……","プレイヤー",},
        [4704] = {8112,"主君","一命をなげうってお世継ぎを守られたのか……ご夫人ながら勇ましいご決断だ。","一命をなげうってお世継ぎを守られたのね……勇ましいご決断だわ……","趙雲",},
        [4705] = {8112,"主君","ならば趙雲殿、曹操軍の囲みを突破しましょう。糜夫人の思いを無駄にしないためにも！","ならば趙雲殿、曹操軍の囲みを突破しましょう。糜夫人の思いを無駄にしないためにも！","趙雲",},
        [4706] = {8201,"趙雲","後ろから追っ手が来ている。張飛殿、加勢を頼む！","後ろから追っ手が来ている。張飛殿、加勢を頼む！","張飛",},
        [4707] = {8201,"張飛","よっしゃあ！趙雲はこのまま進め。追っ手はオレが引き受けた！","よっしゃあ！趙雲はこのまま進め。追っ手はオレが引き受けた！","趙雲",},
        [4708] = {8202,"趙雲","私の咎は、万死をもっても償えませぬ！糜夫人は深手を負われ、井戸に身をお投げになりました。やむなく土塀を崩して埋めて参りました。","私の咎は、万死をもっても償えませぬ！糜夫人は深手を負われ、井戸に身をお投げになりました。やむなく土塀を崩して埋めて参りました。","劉備",},
        [4709] = {8202,"趙雲","私は若君を懐に抱いて重囲を突破し、揚威将軍の助けで辛くも危機を脱しました。","私は若君を懐に抱いて重囲を突破し、揚威将軍の助けで辛くも危機を脱しました。","劉備",},
        [4710] = {8202,"趙雲","幸い、若君はご無事でございました！(趙雲は両手で阿斗を劉備に渡す)","幸い、若君はご無事でございました！(趙雲は両手で阿斗を劉備に渡す)","劉備",},
        [4711] = {8202,"劉備","揚威将軍と趙雲を失うことになるのならば、こんな子供などいらぬ！(劉備は阿斗を受け取るや、地べたにほおり投げる)","揚威将軍と趙雲を失うことになるのならば、こんな子供などいらぬ！(劉備は阿斗を受け取るや、地べたにほおり投げる)","趙雲",},
        [4712] = {8202,"趙雲","たとえ死んでも、このご恩は忘れませぬ！(趙雲は急いで阿斗を抱きあげる)","たとえ死んでも、このご恩は忘れませぬ！(趙雲は急いで阿斗を抱きあげる)","劉備",},
        [4713] = {8203,"楽進","敵将の張飛が虎髭を逆立て、目をむき、蛇矛を手に馬に乗って、長坂橋に突っ立っています！","敵将の張飛が虎髭を逆立て、目をむき、蛇矛を手に馬に乗って、長坂橋に突っ立っています！","曹操",},
        [4714] = {8203,"楽進","橋の東の木陰には、土煙が舞い上がっています。孔明の罠かもしれません。","橋の東の木陰には、土煙が舞い上がっています。孔明の罠かもしれません。","曹操",},
        [4715] = {8203,"曹操","前に関羽に聞いたことがある。張飛は百万の敵軍がいようと、袋から物をさぐり出すように、たやすく大将の首を取ってくると。","前に関羽に聞いたことがある。張飛は百万の敵軍がいようと、袋から物をさぐり出すように、たやすく大将の首を取ってくると。","楽進",},
        [4716] = {8203,"曹操","今日思いがけず出会ったが、孔明の罠があろうとなかろうと、軽々しく相手してはならぬ。","今日思いがけず出会ったが、孔明の罠があろうとなかろうと、軽々しく相手してはならぬ。","楽進",},
        [4717] = {8204,"張飛","燕人・張飛、ここにあり！オレと勝負するヤツはいねえのか！","燕人・張飛、ここにあり！オレと勝負するヤツはいねえのか！","曹操",},
        [4718] = {8204,"張飛","戦いもしねえ、退きもしねえたぁ、どういう了見だァ！","戦いもしねえ、退きもしねえたぁ、どういう了見だァ！","曹操",},
        [4719] = {8204,"曹操","全軍一斉に西を駆け、長坂橋を迂回せよ！","全軍一斉に西を駆け、長坂橋を迂回せよ！","張飛",},
        [4720] = {8205,"張遼","丞相、落ち着いてください。たかが張飛一人、恐れることはありません。今すぐに軍を引き返せば、劉備を生け捕りにできるのですよ。","丞相、落ち着いてください。たかが張飛一人、恐れることはありません。今すぐに軍を引き返せば、劉備を生け捕りにできるのですよ。","曹操",},
        [4721] = {8205,"曹操","むむ、では張遼と許褚は急ぎ長坂橋にとって返し、様子を探れ。","むむ、では張遼と許褚は急ぎ長坂橋にとって返し、様子を探れ。","張遼",},
        [4722] = {8206,"張遼","張飛は橋を切り落として去ってしまいました。","張飛は橋を切り落として去ってしまいました。","曹操",},
        [4723] = {8206,"曹操","橋を落とすとは、やはり脅えている証拠か。","橋を落とすとは、やはり脅えている証拠か。","張遼",},
        [4724] = {8206,"曹操","すぐに三ヶ所に浮き橋を架けさせよ。今夜のうちに川を渡る。","すぐに三ヶ所に浮き橋を架けさせよ。今夜のうちに川を渡る。","張遼",},
        [4725] = {8206,"李典","これも諸葛亮の計略かもしれません。軽々しく進むのはどうかと。","これも諸葛亮の計略かもしれません。軽々しく進むのはどうかと。","曹操",},
        [4726] = {8206,"曹操","張飛は勇猛なだけの男、謀略などあるまい。","張飛は勇猛なだけの男、謀略などあるまい。","李典",},
        [4727] = {8207,"劉備","前は大河、後ろは敵の追っ手だ。どうすればいい?","前は大河、後ろは敵の追っ手だ。どうすればいい?","プレイヤー",},
        [4728] = {8207,"主君","心配無用、私と趙雲殿がいます！","心配無用、私と趙雲殿がいます！","劉備",},
        [4729] = {8208,"曹操","今度こそ劉備は罠に落ちた虎も同然。ここで捕まえそびれたら、虎を山へ帰すようなものだ。","今度こそ劉備は罠に落ちた虎も同然。ここで捕まえそびれたら、虎を山へ帰すようなものだ。","",},
        [4730] = {8208,"曹操","皆の者、力の限り前進せよ。","皆の者、力の限り前進せよ。","",},
        [4731] = {8208,"関羽","そうはさせぬ、ここで待ち受けておったぞ！","そうはさせぬ、ここで待ち受けておったぞ！","曹操",},
        [4732] = {8208,"関羽","(関羽は江夏から一万の兵馬を借り受け、当陽・長坂の苦戦を知って、この道で待ち伏せしていた)","(関羽は江夏から一万の兵馬を借り受け、当陽・長坂の苦戦を知って、この道で待ち伏せしていた)","曹操",},
        [4733] = {8208,"曹操","ええい！またしても諸葛亮の計略にかかったか！","ええい！またしても諸葛亮の計略にかかったか！","関羽",},
        [4734] = {8208,"曹操","全軍、速やかに後退せよ！","全軍、速やかに後退せよ！","関羽",},
        [4735] = {8301,"曹操","今劉備はすでに江夏に身を寄せている。呉の孫権と結んで、勢いを伸ばすと厄介だ。どのように破ったものか。","今劉備はすでに江夏に身を寄せている。呉の孫権と結んで、勢いを伸ばすと厄介だ。どのように破ったものか。","",},
        [4736] = {8301,"荀攸","今のわが軍の威光を背にして、孫権に檄文送りましょう。共に江夏を攻めて劉備を生け捕り、荊州の領地を分け、末永く同盟を結ぼうと。","今のわが軍の威光を背にして、孫権に檄文送りましょう。共に江夏を攻めて劉備を生け捕り、荊州の領地を分け、末永く同盟を結ぼうと。","曹操",},
        [4737] = {8301,"荀攸","孫権はきっと気圧されて降参し、事は成就するでしょう。","孫権はきっと気圧されて降参し、事は成就するでしょう。","曹操",},
        [4738] = {8301,"曹操","よし、そちの言う通りにしよう。速やかに呉に使者を派遣せよ。","よし、そちの言う通りにしよう。速やかに呉に使者を派遣せよ。","荀攸",},
        [4739] = {8302,"魯粛","荊州はわが国と隣接し、要害は堅固で、人民も富み栄えております。これを手に入れられれば、帝王の基礎を築けます。","荊州はわが国と隣接し、要害は堅固で、人民も富み栄えております。これを手に入れられれば、帝王の基礎を築けます。","孫権",},
        [4740] = {8302,"魯粛","今劉表は死去し、劉備は敗走しました。","今劉表は死去し、劉備は敗走しました。","孫権",},
        [4741] = {8302,"魯粛","私が命を奉じて江夏に弔問に訪れ、劉備を説得し、劉表の旧将たちと力を合わせて、共に曹操を打ち破るようにしましょう。","私が命を奉じて江夏に弔問に訪れ、劉備を説得し、劉表の旧将たちと力を合わせて、共に曹操を打ち破るようにしましょう。","孫権",},
        [4742] = {8302,"魯粛","劉備が喜んで命令に従えば、大事はなったも同然です。","劉備が喜んで命令に従えば、大事はなったも同然です。","孫権",},
        [4743] = {8302,"孫権","よし、すぐに江夏に行ってくれ。","よし、すぐに江夏に行ってくれ。","魯粛",},
        [4744] = {8303,"劉備","曹操軍が破竹の勢いで今また江陵を占領した。今後どう対処したらよいだろう?","曹操軍が破竹の勢いで今また江陵を占領した。今後どう対処したらよいだろう?","諸葛亮",},
        [4745] = {8303,"諸葛亮","曹操の勢力は強大で、急には対抗できません。呉の孫権に後援を頼むほかありませんね。","曹操の勢力は強大で、急には対抗できません。呉の孫権に後援を頼むほかありませんね。","劉備",},
        [4746] = {8303,"諸葛亮","南北でにらみあいをさせ、我らはその間で利益を得るようにすればよろしいかと。","南北でにらみあいをさせ、我らはその間で利益を得るようにすればよろしいかと。","劉備",},
        [4747] = {8303,"劉備","呉は人材が豊富だ。遠謀もあろう。こちらの思い通りになるだろうか?","呉は人材が豊富だ。遠謀もあろう。こちらの思い通りになるだろうか?","諸葛亮",},
        [4748] = {8303,"諸葛亮","曹操は今、百万の大軍を率いて、長江と漢江の間にとぐろを巻いております。呉のほうから使者を出して様子を探りにくるでしょう。","曹操は今、百万の大軍を率いて、長江と漢江の間にとぐろを巻いております。呉のほうから使者を出して様子を探りにくるでしょう。","劉備",},
        [4749] = {8303,"諸葛亮","使者が来ましたら、この私が追い風に乗って呉に赴き、三寸不爛の舌をふるって、南北両軍を戦わせてみせましょう。","使者が来ましたら、この私が追い風に乗って呉に赴き、三寸不爛の舌をふるって、南北両軍を戦わせてみせましょう。","劉備",},
        [4750] = {8303,"諸葛亮","もし南軍が勝てば、曹操を誅して荊州を取り、もし北軍が勝てば、その勢いに乗じて江南を奪えばよいのです。","もし南軍が勝てば、曹操を誅して荊州を取り、もし北軍が勝てば、その勢いに乗じて江南を奪えばよいのです。","劉備",},
        [4751] = {8303,"劉備","そうなれば結構だが。呉から誰か来てくれるだろうか?","そうなれば結構だが。呉から誰か来てくれるだろうか?","諸葛亮",},
        [4752] = {8303,"諸葛亮","きっと今にやってきますよ。","きっと今にやってきますよ。","劉備",},
        [4753] = {8304,"劉備","呉の孫権の使者として魯粛が悔やみに来たそうだ。船はもう着いたらしい。","呉の孫権の使者として魯粛が悔やみに来たそうだ。船はもう着いたらしい。","諸葛亮",},
        [4754] = {8304,"諸葛亮","大事が成就しましたね。","大事が成就しましたね。","劉備",},
        [4755] = {8304,"諸葛亮","先年、孫策が死去したおり、襄陽から弔問の使者を出しましたか?","先年、孫策が死去したおり、襄陽から弔問の使者を出しましたか?","劉備",},
        [4756] = {8304,"劉琦","呉とわが家とは父を殺した仇同士の間柄です。吉事にせよ凶事にせよ、使いをやるわけがありません。","呉とわが家とは父を殺した仇同士の間柄です。吉事にせよ凶事にせよ、使いをやるわけがありません。","諸葛亮",},
        [4757] = {8304,"諸葛亮","それなら魯粛が来たのは、悔やみなどではありませんねえ。軍情を探りに来たのでしょう。","それなら魯粛が来たのは、悔やみなどではありませんねえ。軍情を探りに来たのでしょう。","劉琦",},
        [4758] = {8304,"諸葛亮","もし曹操の動静を尋ねられたら、知らぬ存ぜぬで通してください。重ねて問われましたら、諸葛亮に聞けとおっしゃてください。","もし曹操の動静を尋ねられたら、知らぬ存ぜぬで通してください。重ねて問われましたら、諸葛亮に聞けとおっしゃてください。","劉琦",},
        [4759] = {8304,"劉備","わかった。","わかった。","諸葛亮",},
        [4760] = {8305,"魯粛","劉備殿のご高名はかねがね承っておりました。今日お目通りがかなって、まことに嬉しく存じます。","劉備殿のご高名はかねがね承っておりました。今日お目通りがかなって、まことに嬉しく存じます。","劉備",},
        [4761] = {8305,"魯粛","近ごろ曹操と交戦なされたとか。相手の内情はよくご存知でしょう。曹操の軍勢とはいかばかりでしょうか?","近ごろ曹操と交戦なされたとか。相手の内情はよくご存知でしょう。曹操の軍勢とはいかばかりでしょうか?","劉備",},
        [4762] = {8305,"劉備","申し訳ないが。こちらは将兵も少なく、曹操が来ると聞けばすぐ逃げていたので、内情など何も知らないのです。","申し訳ないが。こちらは将兵も少なく、曹操が来ると聞けばすぐ逃げていたので、内情など何も知らないのです。","魯粛",},
        [4763] = {8305,"魯粛","聞けば劉備殿は諸葛孔明の謀略を用いて、二度までも火攻めにより曹操の肝胆を寒からしめたとか。それでもご存知ないと?","聞けば劉備殿は諸葛孔明の謀略を用いて、二度までも火攻めにより曹操の肝胆を寒からしめたとか。それでもご存知ないと?","劉備",},
        [4764] = {8305,"劉備","詳しい事はわかりません。孔明に聞かなければ。","詳しい事はわかりません。孔明に聞かなければ。","魯粛",},
        [4765] = {8305,"魯粛","孔明殿はどこにおられます?是非お会いしたい。","孔明殿はどこにおられます?是非お会いしたい。","劉備",},
        [4766] = {8305,"劉備","では呼んで引き合わせましょう。","では呼んで引き合わせましょう。","魯粛",},
        [4767] = {8306,"魯粛","先生のご才徳はかねてより承っておりました。今日お目にかかれて幸いです。まず目前の危機についてお尋ねしたいのですが。","先生のご才徳はかねてより承っておりました。今日お目にかかれて幸いです。まず目前の危機についてお尋ねしたいのですが。","諸葛亮",},
        [4768] = {8306,"諸葛亮","曹操のたくらみは、知り抜いております。ただ口惜しいことに力が及ばぬので、難を避けているのです。","曹操のたくらみは、知り抜いております。ただ口惜しいことに力が及ばぬので、難を避けているのです。","魯粛",},
        [4769] = {8306,"魯粛","劉備殿はこの地に留まられるのですか?","劉備殿はこの地に留まられるのですか?","諸葛亮",},
        [4770] = {8306,"諸葛亮","わが君は蒼梧郡の太守、呉臣となじみなので、そこに身を寄せる考えです。","わが君は蒼梧郡の太守、呉臣となじみなので、そこに身を寄せる考えです。","魯粛",},
        [4771] = {8306,"魯粛","呉臣は兵糧も兵力も乏しく、自分の身を保つのも難しいはず。他人を助ける余裕はないのでは?","呉臣は兵糧も兵力も乏しく、自分の身を保つのも難しいはず。他人を助ける余裕はないのでは?","諸葛亮",},
        [4772] = {8306,"諸葛亮","ええ、ですから長居はできませんが、ひとまず身を寄せ、別の道を考えます。","ええ、ですから長居はできませんが、ひとまず身を寄せ、別の道を考えます。","魯粛",},
        [4773] = {8306,"魯粛","孫権将軍は六郡を領しており、軍は優秀で兵糧の貯えも充分。また賢者を重んじ、江東の豪傑が大勢付き従っています。","孫権将軍は六郡を領しており、軍は優秀で兵糧の貯えも充分。また賢者を重んじ、江東の豪傑が大勢付き従っています。","諸葛亮",},
        [4774] = {8306,"魯粛","劉備殿のために考えれば、腹心を使いに出して呉と手を結び、ともに大業を図るに越したことはないでしょう。","劉備殿のために考えれば、腹心を使いに出して呉と手を結び、ともに大業を図るに越したことはないでしょう。","諸葛亮",},
        [4775] = {8306,"諸葛亮","わが君はこれまで孫将軍とは親交がありませんし、おそらく話しても無駄でしょう。使いに出せる腹心もおりませんし。","わが君はこれまで孫将軍とは親交がありませんし、おそらく話しても無駄でしょう。使いに出せる腹心もおりませんし。","魯粛",},
        [4776] = {8306,"魯粛","先生の兄上は、いま呉の参謀になって、再会を待ち望んでおられます。","先生の兄上は、いま呉の参謀になって、再会を待ち望んでおられます。","諸葛亮",},
        [4777] = {8306,"魯粛","ふつつかながら私もお供して、孫将軍の御前で大事を議したく存じます。","ふつつかながら私もお供して、孫将軍の御前で大事を議したく存じます。","諸葛亮",},
        [4778] = {8306,"劉備","孔明は頼りにしているわが軍師です。一刻たりともそばを離れては困る。","孔明は頼りにしているわが軍師です。一刻たりともそばを離れては困る。","魯粛",},
        [4779] = {8306,"魯粛","劉備殿は孔明殿を私と同行させてくれないのですか?","劉備殿は孔明殿を私と同行させてくれないのですか?","劉備",},
        [4780] = {8306,"劉備","それは……","それは……","魯粛",},
        [4781] = {8306,"主君","今回は兄者たちをわずらわすまでもない。私が孔明と一緒に呉に行きましょう。","今回は兄さんたちをわずらわすまでもないわ。私が孔明殿と一緒に呉に行きましょう。","劉備",},
        [4782] = {8306,"劉備","そうか、では頼んだぞ。","そうか、では頼んだぞ。","プレイヤー",},
        [4783] = {8307,"孫権","魯粛よ、江夏で探って来た状況はどうだ?","魯粛よ、江夏で探って来た状況はどうだ?","魯粛",},
        [4784] = {8307,"魯粛","あらかたはわかりました。それではご報告します。","あらかたはわかりました。それではご報告します。","孫権",},
        [4785] = {8307,"孫権","昨日、曹操の使者が檄文を持参して来た。ひとまず送り返したが、まだ何とも定まらぬ。","昨日、曹操の使者が檄文を持参して来た。ひとまず送り返したが、まだ何とも定まらぬ。","魯粛",},
        [4786] = {8307,"魯粛","曹操はなんと?","曹操はなんと?","孫権",},
        [4787] = {8307,"孫権","呉とともに江夏に猟をして、劉備を討ち、その領土を分割して、長く同盟を結びたいと。","呉とともに江夏に猟をして、劉備を討ち、その領土を分割して、長く同盟を結びたいと。","魯粛",},
        [4788] = {8307,"魯粛","わが君の思し召しはいかに?","わが君の思し召しはいかに?","孫権",},
        [4789] = {8307,"孫権","まだ定見はない。","まだ定見はない。","魯粛",},
        [4790] = {8307,"張昭","曹操は百万の大軍を抱え、天子の名を借りて、四方を征伐しており、拒むと王命に背くことになります。","曹操は百万の大軍を抱え、天子の名を借りて、四方を征伐しており、拒むと王命に背くことになります。","孫権",},
        [4791] = {8307,"張昭","それにわが呉にとって、曹操を防ぐ要害となるのは長江ですが――","それにわが呉にとって、曹操を防ぐ要害となるのは長江ですが――","孫権",},
        [4792] = {8307,"張昭","曹操はすでに荊州を手に入れました。","曹操はすでに荊州を手に入れました。","孫権",},
        [4793] = {8307,"張昭","長江の要害は敵味方で分け合ったようなもので、この勢いには対抗できません。愚見では、降伏が最善の策かと存じます。","長江の要害は敵味方で分け合ったようなもので、この勢いには対抗できません。愚見では、降伏が最善の策かと存じます。","孫権",},
        [4794] = {8307,"孫権","しかしな……","しかしな……","張昭",},
        [4795] = {8307,"張昭","迷うことはありません。曹操に降れば、呉の人民は安らかに、江南の六郡も保たれましょう。","迷うことはありません。曹操に降れば、呉の人民は安らかに、江南の六郡も保たれましょう。","孫権",},
        [4796] = {8307,"孫権","だが……","だが……","張昭",},
        [4797] = {8308,"魯粛","他の者は曹操に降伏してもさしつかえありませんが、将軍だけは降伏はなりません。","他の者は曹操に降伏してもさしつかえありませんが、将軍だけは降伏はなりません。","孫権",},
        [4798] = {8308,"孫権","何故だ?","何故だ?","魯粛",},
        [4799] = {8308,"魯粛","私のような者が曹操に降っても、郷里に帰る程度。官位も昇進すれば州や郡の長官にはなれましょう。","私のような者が曹操に降っても、郷里に帰る程度。官位も昇進すれば州や郡の長官にはなれましょう。","孫権",},
        [4800] = {8308,"魯粛","しかし将軍が降ったら、落ち着く先はありません。","しかし将軍が降ったら、落ち着く先はありません。","孫権",},
        [4801] = {8308,"魯粛","官位は諸侯が関の山、許されるのはせいぜい車一台に馬一頭。供の者も数人たらずでしょう。南方の王を称するなどとんでもない！","官位は諸侯が関の山、許されるのはせいぜい車一台に馬一頭。供の者も数人たらずでしょう。南方の王を称するなどとんでもない！","孫権",},
        [4802] = {8308,"魯粛","皆の本心は、自分の保身のみです。お聞き入れになってはなりません。よく考えて決断なさってください。","皆の本心は、自分の保身のみです。お聞き入れになってはなりません。よく考えて決断なさってください。","孫権",},
        [4803] = {8308,"孫権","そうだな。魯粛の意見は、俺の気持ちとも同じだ。さきほどの皆の議論には、全く失望していたのだ。","そうだな。魯粛の意見は、俺の気持ちとも同じだ。さきほどの皆の議論には、全く失望していたのだ。","魯粛",},
        [4804] = {8308,"孫権","魯粛が来てくれたのは、天意だろう。しかし曹操は袁紹の軍を破った上に、近頃また荊州の兵を得た。その勢いにはとても敵うまい。","魯粛が来てくれたのは、天意だろう。しかし曹操は袁紹の軍を破った上に、近頃また荊州の兵を得た。その勢いにはとても敵うまい。","魯粛",},
        [4805] = {8308,"魯粛","江夏から、諸葛瑾の弟、諸葛亮を連れて参りました。情勢をお尋ねになってはいかがでしょう。","江夏から、諸葛瑾の弟、諸葛亮を連れて参りました。情勢をお尋ねになってはいかがでしょう。","孫権",},
        [4806] = {8308,"孫権","臥龍先生がここに来ているのか?","臥龍先生がここに来ているのか?","魯粛",},
        [4807] = {8308,"魯粛","はい、今は宿駅でお休みです。","はい、今は宿駅でお休みです。","孫権",},
        [4808] = {8308,"孫権","そうか……しかし、今日会うには遅いな。明日文官武官を集め、わが呉の俊才を紹介し、評議させよう。","そうか……しかし、今日会うには遅いな。明日文官武官を集め、わが呉の俊才を紹介し、評議させよう。","魯粛",},
        [4809] = {8309,"張昭","わしは江東では末輩ですが、先生は久しく隆中に隠れて、自らを管仲・楽毅に比しておられたとか。それはまことかな?","わしは江東では末輩ですが、先生は久しく隆中に隠れて、自らを管仲・楽毅に比しておられたとか。それはまことかな?","諸葛亮",},
        [4810] = {8309,"諸葛亮","そんなこともありましたねえ。ちょっとした例えですよ。","そんなこともありましたねえ。ちょっとした例えですよ。","張昭",},
        [4811] = {8309,"張昭","近ごろ劉備殿が三たび草廬に訪ねて、幸いにも先生を得られた。","近ごろ劉備殿が三たび草廬に訪ねて、幸いにも先生を得られた。","諸葛亮",},
        [4812] = {8309,"張昭","魚が水を得たかのごとく、一気に荊州を席巻するかと思いきや、たちまち曹操に奪われたというのは、いかなるわけですかな。","魚が水を得たかのごとく、一気に荊州を席巻するかと思いきや、たちまち曹操に奪われたというのは、いかなるわけですかな。","諸葛亮",},
        [4813] = {8309,"諸葛亮","荊州の土地を取るくらい、手のひらを返すよりたやすいことです。","荊州の土地を取るくらい、手のひらを返すよりたやすいことです。","張昭",},
        [4814] = {8309,"諸葛亮","しかしわが主君は仁義に厚く、同族の領地を奪うのは忍びないと、頑なに辞退されたのです。","しかしわが主君は仁義に厚く、同族の領地を奪うのは忍びないと、頑なに辞退されたのです。","張昭",},
        [4815] = {8309,"諸葛亮","そこへ劉琮が、つまらぬ進言を聞き入れてひそかに投降してしまい、曹操を勢いづかせることになりました。","そこへ劉琮が、つまらぬ進言を聞き入れてひそかに投降してしまい、曹操を勢いづかせることになりました。","張昭",},
        [4816] = {8309,"諸葛亮","わが主君は別の思惑があっていま江夏に駐屯していますが、他人にはわかりますまい。","わが主君は別の思惑があっていま江夏に駐屯していますが、他人にはわかりますまい。","張昭",},
        [4817] = {8309,"張昭","それならば先生の話は言行不一致ではないか。","それならば先生の話は言行不一致ではないか。","諸葛亮",},
        [4818] = {8309,"張昭","劉備殿に仕える身となったからには、民のために利害を考え、賊を討伐するのが当然であろう。","劉備殿に仕える身となったからには、民のために利害を考え、賊を討伐するのが当然であろう。","諸葛亮",},
        [4819] = {8309,"張昭","ところが先生は劉備殿に仕えてより、曹操軍がひとたび押し寄せてくれば、風をくらって逃げてばかりおる。","ところが先生は劉備殿に仕えてより、曹操軍がひとたび押し寄せてくれば、風をくらって逃げてばかりおる。","諸葛亮",},
        [4820] = {8309,"張昭","劉表の恩に報いて領民を安んずることもできず、遺児を助けて荊州を保つこともかなわぬ。","劉表の恩に報いて領民を安んずることもできず、遺児を助けて荊州を保つこともかなわぬ。","諸葛亮",},
        [4821] = {8309,"張昭","新野を棄て、樊城に走り、当陽で敗れ、夏口へ逃れた。身を置く場所とてない。","新野を棄て、樊城に走り、当陽で敗れ、夏口へ逃れた。身を置く場所とてない。","諸葛亮",},
        [4822] = {8309,"張昭","劉備殿は先生を得てから、かえって落ち目となったのではないか?これが管仲や楽毅と並ぶ者の働きですかな?","劉備殿は先生を得てから、かえって落ち目となったのではないか?これが管仲や楽毅と並ぶ者の働きですかな?","諸葛亮",},
        [4823] = {8309,"諸葛亮","わが主君は先ごろ汝南で敗れ、劉表のもとに身を寄せられたが、兵力は千にも満たず、将軍といえば関羽・張飛・趙雲くらいのもの。","わが主君は先ごろ汝南で敗れ、劉表のもとに身を寄せられたが、兵力は千にも満たず、将軍といえば関羽・張飛・趙雲くらいのもの。","張昭",},
        [4824] = {8309,"諸葛亮","しばし身を寄せただけで、もとよりここに鎮座するつもりはありませんでした。","しばし身を寄せただけで、もとよりここに鎮座するつもりはありませんでした。","張昭",},
        [4825] = {8309,"諸葛亮","そもそも軍の備えも充分でなく、城は堅固ならず、兵も戦の経験に乏しく、食糧も足りません。","そもそも軍の備えも充分でなく、城は堅固ならず、兵も戦の経験に乏しく、食糧も足りません。","張昭",},
        [4826] = {8309,"諸葛亮","それでも博望の焼き討ち、白河の水攻めによって、夏侯惇や曹仁の心胆を寒からしめたのです。","それでも博望の焼き討ち、白河の水攻めによって、夏侯惇や曹仁の心胆を寒からしめたのです。","張昭",},
        [4827] = {8309,"諸葛亮","劉琮が曹操に降ったことを、劉備様はご存知ありませんでした。","劉琮が曹操に降ったことを、劉備様はご存知ありませんでした。","張昭",},
        [4828] = {8309,"諸葛亮","混乱に乗じて同族の領地を奪うことはできぬと仰せられた。これは大義によるものです。","混乱に乗じて同族の領地を奪うことはできぬと仰せられた。これは大義によるものです。","張昭",},
        [4829] = {8309,"諸葛亮","当陽大敗の折は数十万の領民が劉備様を慕ってついてきました。老人も幼児も見捨てなかったため、一日にわずか十里しか進めませんでした。","当陽大敗の折は数十万の領民が劉備様を慕ってついてきました。老人も幼児も見捨てなかったため、一日にわずか十里しか進めませんでした。","張昭",},
        [4830] = {8309,"諸葛亮","江陵を取るより、民と一緒に敗れるほうがよいと仰せられました。これは仁愛によるものです。","江陵を取るより、民と一緒に敗れるほうがよいと仰せられました。これは仁愛によるものです。","張昭",},
        [4831] = {8309,"張昭","む、むむ……","む、むむ……","諸葛亮",},
        [4832] = {8310,"孫権","先生が新野で劉備殿を助けて曹操と決戦されたのなら、敵軍の内情には通じておろう。","先生が新野で劉備殿を助けて曹操と決戦されたのなら、敵軍の内情には通じておろう。","諸葛亮",},
        [4833] = {8310,"孫権","曹操の兵力はいかほどか?曹操の配下にはどれほどの将軍がおる?","曹操の兵力はいかほどか?曹操の配下にはどれほどの将軍がおる?","諸葛亮",},
        [4834] = {8310,"諸葛亮","歩兵・騎兵・水軍あわせて百万あまりです。加えて智謀の士や、戦慣れした猛将も、千人や二千人どころではありませんね。","歩兵・騎兵・水軍あわせて百万あまりです。加えて智謀の士や、戦慣れした猛将も、千人や二千人どころではありませんね。","孫権",},
        [4835] = {8310,"孫権","曹操は荊州を平らげたあと、さらなる野心を抱いているだろうか?","曹操は荊州を平らげたあと、さらなる野心を抱いているだろうか?","諸葛亮",},
        [4836] = {8310,"諸葛亮","今まさに長江に沿って陣地を構え、軍船を準備しておりますよ。これが呉を攻めようとの計でなくて何でしょう?","今まさに長江に沿って陣地を構え、軍船を準備しておりますよ。これが呉を攻めようとの計でなくて何でしょう?","孫権",},
        [4837] = {8310,"孫権","もし曹操に併呑の野心があるなら、戦うべきか否か……判断してもらえぬか。","もし曹操に併呑の野心があるなら、戦うべきか否か……判断してもらえぬか。","諸葛亮",},
        [4838] = {8310,"諸葛亮","先ごろ天下が大いに乱れました。故に将軍は江東で兵を挙げ、わが主君・劉備様は江南を奪い返し、曹操と天下を争わんとしたのですが――","先ごろ天下が大いに乱れました。故に将軍は江東で兵を挙げ、わが主君・劉備様は江南を奪い返し、曹操と天下を争わんとしたのですが――","孫権",},
        [4839] = {8310,"諸葛亮","今曹操は北方をおおかた平定しました。さらにこのほど荊州を破り、天下に威勢を轟かせております。","今曹操は北方をおおかた平定しました。さらにこのほど荊州を破り、天下に威勢を轟かせております。","孫権",},
        [4840] = {8310,"諸葛亮","いくら英雄でも、これに武力で立ち向かうのは容易ではありません。それで劉備様は今まで逃げているのです。","いくら英雄でも、これに武力で立ち向かうのは容易ではありません。それで劉備様は今まで逃げているのです。","孫権",},
        [4841] = {8310,"諸葛亮","将軍は身の丈に合うようになさいませ。もし呉越の軍勢で中原の兵力と争えると思われるなら、早ばやと曹操と断交なさいませ。","将軍は身の丈に合うようになさいませ。もし呉越の軍勢で中原の兵力と争えると思われるなら、早ばやと曹操と断交なさいませ。","孫権",},
        [4842] = {8310,"諸葛亮","もし敵わないと思われるなら、謀臣の皆様の意見に従い、降伏なさればよろしいでしょう。","もし敵わないと思われるなら、謀臣の皆様の意見に従い、降伏なさればよろしいでしょう。","孫権",},
        [4843] = {8310,"諸葛亮","将軍は表向き曹操に服従しながら、内心では疑いを抱いておられる。ご決断をためらっていれば、禍いは目の前です。","将軍は表向き曹操に服従しながら、内心では疑いを抱いておられる。ご決断をためらっていれば、禍いは目の前です。","孫権",},
        [4844] = {8310,"孫権","……もし先生の言われる通りなら、なぜ劉備殿には降伏を勧めぬ?","……もし先生の言われる通りなら、なぜ劉備殿には降伏を勧めぬ?","諸葛亮",},
        [4845] = {8310,"諸葛亮","むかし斉の田横は、いち処士ながら義を守って自害しました。ましてやわが主君・劉備様は王室の一族。","むかし斉の田横は、いち処士ながら義を守って自害しました。ましてやわが主君・劉備様は王室の一族。","孫権",},
        [4846] = {8310,"諸葛亮","英才は世を覆い、人々に慕われております。事成らぬのも一時の天命。誰かに膝を屈するわけがありません。","英才は世を覆い、人々に慕われております。事成らぬのも一時の天命。誰かに膝を屈するわけがありません。","孫権",},
        [4847] = {8310,"魯粛","よくもまあ……先生の言い方は我らの主君を蔑むにもほどがあります。明日また評議しましょう。","よくもまあ……先生の言い方は我らの主君を蔑むにもほどがあります。明日また評議しましょう。","諸葛亮",},
        [4848] = {8311,"孫権","曹操が平素より目の敵にしていたのは、呂布、劉表、袁紹、袁術、劉備殿と俺であった。","曹操が平素より目の敵にしていたのは、呂布、劉表、袁紹、袁術、劉備殿と俺であった。","諸葛亮",},
        [4849] = {8311,"孫権","今では皆滅んでしまい、劉備殿と俺だけが残った。俺もこの呉の領土を持ちながら、人の下につくわけにはいかん。","今では皆滅んでしまい、劉備殿と俺だけが残った。俺もこの呉の領土を持ちながら、人の下につくわけにはいかん。","諸葛亮",},
        [4850] = {8311,"孫権","俺の心は決まっている。劉備殿のほかに曹操に当たれる者はいまい。だが劉備殿は最近戦に敗れたばかり。対抗するのは難しかろう?","俺の心は決まっている。劉備殿のほかに曹操に当たれる者はいまい。だが劉備殿は最近戦に敗れたばかり。対抗するのは難しかろう?","諸葛亮",},
        [4851] = {8311,"諸葛亮","劉備様は敗れはしましたが、なお関羽殿が精兵一万を率いております。劉琦殿が率いる江夏の兵士の数も一万は下りません。","劉備様は敗れはしましたが、なお関羽殿が精兵一万を率いております。劉琦殿が率いる江夏の兵士の数も一万は下りません。","孫権",},
        [4852] = {8311,"諸葛亮","曹操軍は遠征のため疲れています。また劉備様を追って、軽騎兵を一昼夜三百里走らせました。","曹操軍は遠征のため疲れています。また劉備様を追って、軽騎兵を一昼夜三百里走らせました。","孫権",},
        [4853] = {8311,"諸葛亮","これは強弩の末、魯縞を穿つあたわず、ということわざの通りです。それに北方育ちで、水上の戦には慣れていません。","これは強弩の末、魯縞を穿つあたわず、ということわざの通りです。それに北方育ちで、水上の戦には慣れていません。","孫権",},
        [4854] = {8311,"諸葛亮","荊州の民は曹操に従ってはいますが、その勢力を恐れただけのこと、本意ではありません。","荊州の民は曹操に従ってはいますが、その勢力を恐れただけのこと、本意ではありません。","孫権",},
        [4855] = {8311,"諸葛亮","今もし将軍がわが主君・劉備様と心を一つにし力を合わせれば、必ずや曹操の軍を打ち破れましょう。","今もし将軍がわが主君・劉備様と心を一つにし力を合わせれば、必ずや曹操の軍を打ち破れましょう。","孫権",},
        [4856] = {8311,"諸葛亮","敗れた敵は北に帰ります。そうすれば荊州と呉は強い勢力を保って、天下三分の形が成るでしょう。","敗れた敵は北に帰ります。そうすれば荊州と呉は強い勢力を保って、天下三分の形が成るでしょう。","孫権",},
        [4857] = {8311,"諸葛亮","成否の分かれ目はまさに今日、将軍のご決断にかかっております。","成否の分かれ目はまさに今日、将軍のご決断にかかっております。","孫権",},
        [4858] = {8311,"孫権","先生の言葉で、胸のつかえが下りた。俺は決意した、もう迷わんぞ。即日兵を起こし、曹操を討ち滅ぼさん！","先生の言葉で、胸のつかえが下りた。俺は決意した、もう迷わんぞ。即日兵を起こし、曹操を討ち滅ぼさん！","諸葛亮",},
        [4859] = {8312,"張昭","ああ、孔明の罠にかかってしまわれた！","ああ、孔明の罠にかかってしまわれた！","孫権",},
        [4860] = {8312,"張昭","曹操はむかし兵力が乏しかったときでさえ、袁紹を破ったのです。","曹操はむかし兵力が乏しかったときでさえ、袁紹を破ったのです。","孫権",},
        [4861] = {8312,"張昭","まして今は百万の軍を率いて南征してきています。敵を侮ってはなりません。","まして今は百万の軍を率いて南征してきています。敵を侮ってはなりません。","孫権",},
        [4862] = {8312,"張昭","奴の弁舌にそそのかされて、軽率に兵を動かしては、薪を背負って火の中へ飛びこむようなものですぞ。","奴の弁舌にそそのかされて、軽率に兵を動かしては、薪を背負って火の中へ飛びこむようなものですぞ。","孫権",},
        [4863] = {8312,"孫権","しかし……","しかし……","張昭",},
        [4864] = {8312,"顧雍","劉備は曹操に敗れたので、わが呉の兵を借りて刃向かおうとしているのです。","劉備は曹操に敗れたので、わが呉の兵を借りて刃向かおうとしているのです。","孫権",},
        [4865] = {8312,"顧雍","利用されてはなりません。どうか張昭殿のお諫めをお聞き下さい。","利用されてはなりません。どうか張昭殿のお諫めをお聞き下さい。","孫権",},
        [4866] = {8312,"孫権","だが……","だが……","顧雍",},
        [4867] = {8312,"魯粛","どうかお聞き入れなさいますな。張昭らは妻子とわが身の保全を図っているだけです。","どうかお聞き入れなさいますな。張昭らは妻子とわが身の保全を図っているだけです。","孫権",},
        [4868] = {8312,"魯粛","ためらっておられては、道を誤ることになりましょう。","ためらっておられては、道を誤ることになりましょう。","孫権",},
        [4869] = {8312,"孫権","しばし退がっておれ。よくよく思案してみよう。","しばし退がっておれ。よくよく思案してみよう。","魯粛",},
        [4870] = {8313,"呉国太","何を気に病んでいるのです?眠れず、食も進まぬとは。","何を気に病んでいるのです?眠れず、食も進まぬとは。","孫権",},
        [4871] = {8313,"孫権","今曹操が漢江に兵をとどめて、江南を攻めようとしています。文官武官に問うても、降伏せよと言う者もあれば、戦えと言う者もあります","今曹操が漢江に兵をとどめて、江南を攻めようとしています。文官武官に問うても、降伏せよと言う者もあれば、戦えと言う者もあります","呉国太",},
        [4872] = {8313,"孫権","戦おうとしても多勢に無勢ですし、降伏しても曹操が無事にはおくまいと思いまして……","戦おうとしても多勢に無勢ですし、降伏しても曹操が無事にはおくまいと思いまして……","呉国太",},
        [4873] = {8313,"呉国太","そなたはわが姉上、武烈皇后の遺言をお忘れですか?","そなたはわが姉上、武烈皇后の遺言をお忘れですか?","孫権",},
        [4874] = {8313,"呉国太","「内事が決せねば張昭に問え。外事が決せねば周瑜に問え」。そう言われたでしょう?","「内事が決せねば張昭に問え。外事が決せねば周瑜に問え」。そう言われたでしょう?","孫権",},
        [4875] = {8313,"呉国太","すぐに周瑜を呼んでお尋ねなさい。","すぐに周瑜を呼んでお尋ねなさい。","孫権",},
        [4876] = {8401,"張昭","周瑜殿は呉の一大事をご存知か?","周瑜殿は呉の一大事をご存知か?","周瑜",},
        [4877] = {8401,"周瑜","いいえ。","いいえ。","張昭",},
        [4878] = {8401,"張昭","曹操が百万の大軍を率いて、漢江に陣を構えておるのだ。昨日檄文が届いた。","曹操が百万の大軍を率いて、漢江に陣を構えておるのだ。昨日檄文が届いた。","周瑜",},
        [4879] = {8401,"張昭","ご主君を誘って一緒に江夏に猟に出ようという。こちらを併呑する腹だろうが、まだ表には出しておらぬ。","ご主君を誘って一緒に江夏に猟に出ようという。こちらを併呑する腹だろうが、まだ表には出しておらぬ。","周瑜",},
        [4880] = {8401,"張昭","わしらは呉に禍いをもたらさぬよう、ご主君に降伏を勧めた。","わしらは呉に禍いをもたらさぬよう、ご主君に降伏を勧めた。","周瑜",},
        [4881] = {8401,"張昭","ところが魯粛が江夏から劉備の軍師、諸葛亮を連れて来たのだ。あやつは自分らの恨みを晴らそうと、弁舌でご主君を焚きつけておる。","ところが魯粛が江夏から劉備の軍師、諸葛亮を連れて来たのだ。あやつは自分らの恨みを晴らそうと、弁舌でご主君を焚きつけておる。","周瑜",},
        [4882] = {8401,"張昭","魯粛はすっかり惑わされて聞かん。ここは周瑜殿の決断を仰ぐほかない。","魯粛はすっかり惑わされて聞かん。ここは周瑜殿の決断を仰ぐほかない。","周瑜",},
        [4883] = {8401,"周瑜","皆の意見は同じか?","皆の意見は同じか?","張昭",},
        [4884] = {8401,"顧雍","ここにおる者は同意見だ。","ここにおる者は同意見だ。","周瑜",},
        [4885] = {8401,"周瑜","私も降伏に賛成だ。ひとまずお引き取りを。明朝早く主君に会って、決定しよう。","私も降伏に賛成だ。ひとまずお引き取りを。明朝早く主君に会って、決定しよう。","顧雍",},
        [4886] = {8402,"程普","周瑜殿はこの呉が他人の手に渡るかもしれぬことをご存知か?","周瑜殿はこの呉が他人の手に渡るかもしれぬことをご存知か?","周瑜",},
        [4887] = {8402,"周瑜","いいえ。","いいえ。","程普",},
        [4888] = {8402,"程普","わしらは孫堅様に従って呉の国を興して以来、大小数百もの戦を経て、ようやく六郡の領地を勝ち取ったのだ。","わしらは孫堅様に従って呉の国を興して以来、大小数百もの戦を経て、ようやく六郡の領地を勝ち取ったのだ。","周瑜",},
        [4889] = {8402,"程普","ところがご主君は謀臣たちの言うことを聞いて、曹操に降伏すると言われる。なんと情けない、口惜しくてならん！","ところがご主君は謀臣たちの言うことを聞いて、曹操に降伏すると言われる。なんと情けない、口惜しくてならん！","周瑜",},
        [4890] = {8402,"程普","わしらは死んでもそんな屈辱は受けん。どうか周瑜殿、ご主君に開戦を勧めてくれ。わしらは決死の覚悟で戦う。","わしらは死んでもそんな屈辱は受けん。どうか周瑜殿、ご主君に開戦を勧めてくれ。わしらは決死の覚悟で戦う。","周瑜",},
        [4891] = {8402,"周瑜","将軍方の意見は同じか?","将軍方の意見は同じか?","程普",},
        [4892] = {8402,"黄蓋","無論じゃ！この首を斬られようとも、誓って曹操に降参などせぬ！","無論じゃ！この首を斬られようとも、誓って曹操に降参などせぬ！","周瑜",},
        [4893] = {8402,"周瑜","私も曹操と決戦するつもりだ。降伏などせんよ。ひとまずお引き取りを。主君に会って、決定しよう。","私も曹操と決戦するつもりだ。降伏などせんよ。ひとまずお引き取りを。主君に会って、決定しよう。","黄蓋",},
        [4894] = {8403,"諸葛瑾","弟の諸葛亮が漢江より参って、劉備殿とわが呉が同盟を結び、共に曹操を討とうと申していますが、文官武官の意見が一致しない。","弟の諸葛亮が漢江より参って、劉備殿とわが呉が同盟を結び、共に曹操を討とうと申していますが、文官武官の意見が一致しない。","周瑜",},
        [4895] = {8403,"諸葛瑾","私は余計な口出しはせず、周瑜殿のご判断を待っておるのですが。","私は余計な口出しはせず、周瑜殿のご判断を待っておるのですが。","周瑜",},
        [4896] = {8403,"周瑜","貴殿のお考えは?","貴殿のお考えは?","諸葛瑾",},
        [4897] = {8403,"諸葛瑾","降伏ならば安全だが、戦えば危うい。","降伏ならば安全だが、戦えば危うい。","周瑜",},
        [4898] = {8403,"周瑜","なるほど。私にも考えがある。明日御前で評議しよう。","なるほど。私にも考えがある。明日御前で評議しよう。","諸葛瑾",},
        [4899] = {8404,"魯粛","曹操が大軍を率いて侵攻してきました。和を乞うか戦うか、主君は決めかね、将軍に任せようとのことです。将軍のご意見は?","曹操が大軍を率いて侵攻してきました。和を乞うか戦うか、主君は決めかね、将軍に任せようとのことです。将軍のご意見は?","周瑜",},
        [4900] = {8404,"周瑜","曹操が天子の名を借りている以上、その戦に刃向かうことはできませんね。また勢力は強大で侮れない。","曹操が天子の名を借りている以上、その戦に刃向かうことはできませんね。また勢力は強大で侮れない。","魯粛",},
        [4901] = {8404,"周瑜","戦えば必ず敗れましょうが、降伏すればひとまず安全です。心は決まっています。明日主君に会って、投降の使者を出すようにしますよ。","戦えば必ず敗れましょうが、降伏すればひとまず安全です。心は決まっています。明日主君に会って、投降の使者を出すようにしますよ。","魯粛",},
        [4902] = {8404,"魯粛","周瑜殿のお言葉とも思えません！この呉は三代にもわたって国の基礎を固めてきたのに、むざむざ他人の手に渡すのですか?","周瑜殿のお言葉とも思えません！この呉は三代にもわたって国の基礎を固めてきたのに、むざむざ他人の手に渡すのですか?","周瑜",},
        [4903] = {8404,"魯粛","孫策様も、外事は将軍に託すと遺言されたのに。","孫策様も、外事は将軍に託すと遺言されたのに。","周瑜",},
        [4904] = {8404,"魯粛","国を保つべく、今まさに大きな信頼が将軍に寄せられているのです！それなのに、臆病者の意見に耳を傾けるのですか?","国を保つべく、今まさに大きな信頼が将軍に寄せられているのです！それなのに、臆病者の意見に耳を傾けるのですか?","周瑜",},
        [4905] = {8404,"周瑜","江東六郡の無数の民を、私のせいで戦乱に巻き込むわけにいかない。だから降伏するというのだ。","江東六郡の無数の民を、私のせいで戦乱に巻き込むわけにいかない。だから降伏するというのだ。","魯粛",},
        [4906] = {8404,"魯粛","いいえ。将軍の勇敢さと、呉の要害があれば、曹操の思い通りにはなりません！","いいえ。将軍の勇敢さと、呉の要害があれば、曹操の思い通りにはなりません！","周瑜",},
        [4907] = {8404,"諸葛亮","ふふっ……","ふふっ……","周瑜",},
        [4908] = {8404,"周瑜","先生、何を笑っているのだ?","先生、何を笑っているのだ?","諸葛亮",},
        [4909] = {8404,"諸葛亮","ああ、これは失礼しました。ただ、魯粛殿が時勢をご存知ないのがおかしくて。","ああ、これは失礼しました。ただ、魯粛殿が時勢をご存知ないのがおかしくて。","周瑜",},
        [4910] = {8405,"魯粛","先生は私が時勢を知らないとお笑いになるのですか?","先生は私が時勢を知らないとお笑いになるのですか?","諸葛亮",},
        [4911] = {8405,"諸葛亮","ええ、周瑜殿が曹操に降ろうと言われるのは、至極理にかなっています。","ええ、周瑜殿が曹操に降ろうと言われるのは、至極理にかなっています。","魯粛",},
        [4912] = {8405,"周瑜","さすが孔明殿、時勢を見通しているね。","さすが孔明殿、時勢を見通しているね。","諸葛亮",},
        [4913] = {8405,"魯粛","なぜそのようなことを言われるのです、孔明先生?","なぜそのようなことを言われるのです、孔明先生?","諸葛亮",},
        [4914] = {8405,"諸葛亮","曹操は用兵に長けていて、天下に敵う者はおりません。","曹操は用兵に長けていて、天下に敵う者はおりません。","魯粛",},
        [4915] = {8405,"諸葛亮","以前は呂布、袁紹、袁術、劉表だけが敵対できたのですが、今では皆滅ぼし、名実ともに天下無敵です。","以前は呂布、袁紹、袁術、劉表だけが敵対できたのですが、今では皆滅ぼし、名実ともに天下無敵です。","魯粛",},
        [4916] = {8405,"諸葛亮","時勢を考えない劉備様だけが強いて争おうとしておりますが、将来の存亡はわかりません。","時勢を考えない劉備様だけが強いて争おうとしておりますが、将来の存亡はわかりません。","魯粛",},
        [4917] = {8405,"諸葛亮","将軍が曹操に降れば妻子も恙無く、良い暮らしもできましょう。国が変わったとしても、それは天命というもの。惜しむことはありません。","将軍が曹操に降れば妻子も恙無く、良い暮らしもできましょう。国が変わったとしても、それは天命というもの。惜しむことはありません。","魯粛",},
        [4918] = {8405,"魯粛","わが主君に、逆賊曹操に膝を屈せよと言われるのですか！","わが主君に、逆賊曹操に膝を屈せよと言われるのですか！","諸葛亮",},
        [4919] = {8405,"諸葛亮","いえいえ、私に一計がございます。ただ一艘の小舟に二人の者を乗せ、川上に送ってやればよろしい。","いえいえ、私に一計がございます。ただ一艘の小舟に二人の者を乗せ、川上に送ってやればよろしい。","魯粛",},
        [4920] = {8405,"諸葛亮","曹操がこの二人を手に入れれば、百万の大軍も鎧を脱いで北に帰るでしょう。","曹操がこの二人を手に入れれば、百万の大軍も鎧を脱いで北に帰るでしょう。","魯粛",},
        [4921] = {8405,"周瑜","たった二人で、曹操の大軍を退かせるというのか?その二人とは?","たった二人で、曹操の大軍を退かせるというのか?その二人とは?","諸葛亮",},
        [4922] = {8405,"諸葛亮","江東の喬公の二人の娘、大喬と小喬です。","江東の喬公の二人の娘、大喬と小喬です。","周瑜",},
        [4923] = {8406,"周瑜","曹操軍を退ける計に、江東の二喬を使うだと?","曹操軍を退ける計に、江東の二喬を使うだと?","諸葛亮",},
        [4924] = {8406,"諸葛亮","隆中にいた頃に聞いた話では、曹操は漳河のほとりに楼台を築いて、銅雀台と名づけたとか。","隆中にいた頃に聞いた話では、曹操は漳河のほとりに楼台を築いて、銅雀台と名づけたとか。","周瑜",},
        [4925] = {8406,"諸葛亮","たいそう壮麗で、ここに天下の美女を集めているそうです。","たいそう壮麗で、ここに天下の美女を集めているそうです。","周瑜",},
        [4926] = {8406,"諸葛亮","曹操はもともと好色漢です。江東の喬公に二人の娘があり、大喬と小喬という、花も恥じらう絶世の美女だと聞いて――","曹操はもともと好色漢です。江東の喬公に二人の娘があり、大喬と小喬という、花も恥じらう絶世の美女だと聞いて――","周瑜",},
        [4927] = {8406,"諸葛亮","誓いを立てたそうですよ。一つには天下を平らげて帝王となる。","誓いを立てたそうですよ。一つには天下を平らげて帝王となる。","周瑜",},
        [4928] = {8406,"諸葛亮","もう一つには江東の二喬を得て、銅雀台に置いて晩年の楽しみとする。そうすれば死んでも悔いはないと。","もう一つには江東の二喬を得て、銅雀台に置いて晩年の楽しみとする。そうすれば死んでも悔いはないと。","周瑜",},
        [4929] = {8406,"諸葛亮","今曹操が百万の軍勢を率いて、虎視眈々と江南を狙っているのも、実を言えばこの二人の娘のため。","今曹操が百万の軍勢を率いて、虎視眈々と江南を狙っているのも、実を言えばこの二人の娘のため。","周瑜",},
        [4930] = {8406,"諸葛亮","ですから将軍、今すぐ千金で彼女らを購い、曹操にくれてやってはどうでしょう。","ですから将軍、今すぐ千金で彼女らを購い、曹操にくれてやってはどうでしょう。","周瑜",},
        [4931] = {8406,"諸葛亮","范蠡が西施を献じた計と同じです。曹操は娘二人を手に入れたら、大満足で兵を引き揚げるでしょう。","范蠡が西施を献じた計と同じです。曹操は娘二人を手に入れたら、大満足で兵を引き揚げるでしょう。","周瑜",},
        [4932] = {8406,"周瑜","曹操が二喬を欲しがっているという証拠は?","曹操が二喬を欲しがっているという証拠は?","諸葛亮",},
        [4933] = {8406,"主君","曹操の三男の曹植は詩文の才能があって、曹操が命じて「銅雀台の賦」という賦を作らせています。","曹操の三男の曹植は詩文の才能があって、曹操が命じて「銅雀台の賦」という賦を作らせています。","周瑜",},
        [4934] = {8406,"主君","天子となって、二喬を娶りたいという曹操の野望を歌ったものです。","天子となって、二喬を娶りたいという曹操の野望を歌ったものですわ。","周瑜",},
        [4935] = {8406,"主君","今日その賦を持って来ていますから、どうぞご覧ください。","今日その賦を持って来ていますから、どうぞご覧くださいませ。","周瑜",},
        [4936] = {8406,"周瑜","おのれ曹操、人を舐めるにもほどがある……！","おのれ曹操、人を舐めるにもほどがある……！","プレイヤー",},
        [4937] = {8406,"諸葛亮","むかし匈奴がたびたび辺境を犯したさい、漢の天子は公主を単于に贈って和親をはかったのです。","むかし匈奴がたびたび辺境を犯したさい、漢の天子は公主を単于に贈って和親をはかったのです。","周瑜",},
        [4938] = {8406,"諸葛亮","民間の娘二人くらい、惜しまずとも良いでしょう。","民間の娘二人くらい、惜しまずとも良いでしょう。","周瑜",},
        [4939] = {8406,"周瑜","先生は知らぬだろうが……大喬とは先君・孫策の夫人。そして小喬は、この周瑜の妻なのだ！","先生は知らぬだろうが……大喬とは先君・孫策の夫人。そして小喬は、この周瑜の妻なのだ！","諸葛亮",},
        [4940] = {8406,"諸葛亮","ああっ、なんと！知らぬこととは申せ、失言でした。ご無礼をお許しください。","ああっ、なんと！知らぬこととは申せ、失言でした。ご無礼をお許しください。","周瑜",},
        [4941] = {8406,"周瑜","曹操め、何としても倒さねば……！明日主君にお会いして、挙兵の意見を伝えよう。","曹操め、何としても倒さねば……！明日主君にお会いして、挙兵の意見を伝えよう。","諸葛亮",},
        [4942] = {8407,"孫権","連日評議しているが、ある者は降伏せよといい、ある者は開戦せよというので、心が定まらぬ。よって周瑜に決断を任せたい。","連日評議しているが、ある者は降伏せよといい、ある者は開戦せよというので、心が定まらぬ。よって周瑜に決断を任せたい。","周瑜",},
        [4943] = {8407,"周瑜","降伏を勧めたのは誰ですか?","降伏を勧めたのは誰ですか?","孫権",},
        [4944] = {8407,"孫権","張昭ら、皆その意見だ。","張昭ら、皆その意見だ。","周瑜",},
        [4945] = {8407,"周瑜","では張昭殿、降伏すべしという理由を伺いましょう。","では張昭殿、降伏すべしという理由を伺いましょう。","孫権",},
        [4946] = {8407,"張昭","曹操は天子を擁し、朝廷の名を借りて四方を征伐しておる。","曹操は天子を擁し、朝廷の名を借りて四方を征伐しておる。","周瑜",},
        [4947] = {8407,"張昭","このたび荊州も手に入れて、威勢はますます盛んだ。わが呉が曹操を防ぐ頼みの綱は、長江だけだが――","このたび荊州も手に入れて、威勢はますます盛んだ。わが呉が曹操を防ぐ頼みの綱は、長江だけだが――","周瑜",},
        [4948] = {8407,"張昭","いま曹操は幾千もの軍船を揃え、水陸の両方から進んでくる。","いま曹操は幾千もの軍船を揃え、水陸の両方から進んでくる。","周瑜",},
        [4949] = {8407,"張昭","これでは防げまい。とりあえず降伏を申し入れ、その善後策を講じるほかはあるまい。","これでは防げまい。とりあえず降伏を申し入れ、その善後策を講じるほかはあるまい。","周瑜",},
        [4950] = {8407,"周瑜","なるほど、いかにも腐れ儒者の議論だな。呉は開国してもう三代になる。これをむざむざ棄ててしまうのか?","なるほど、いかにも腐れ儒者の議論だな。呉は開国してもう三代になる。これをむざむざ棄ててしまうのか?","張昭",},
        [4951] = {8407,"孫権","では、何とする?","では、何とする?","周瑜",},
        [4952] = {8407,"周瑜","曹操は漢の丞相とは名ばかりの逆賊です。ですが将軍は武勇に優れ、父君と兄君のご偉業によって江東を領しておられます。","曹操は漢の丞相とは名ばかりの逆賊です。ですが将軍は武勇に優れ、父君と兄君のご偉業によって江東を領しておられます。","孫権",},
        [4953] = {8407,"周瑜","兵は精鋭、食糧も豊か。国家のために逆賊を討つべきで、降るいわれはありません。","兵は精鋭、食糧も豊か。国家のために逆賊を討つべきで、降るいわれはありません。","孫権",},
        [4954] = {8407,"周瑜","曹操はいくつも過ちを犯しています。北方もまだ平定していないのに、南征に出て久しい。","曹操はいくつも過ちを犯しています。北方もまだ平定していないのに、南征に出て久しい。","孫権",},
        [4955] = {8407,"周瑜","これが一つ。水上の戦に慣れない北方の軍を、馬を捨てて船に乗せ、わが呉と争おうとしている。これが二つ。","これが一つ。水上の戦に慣れない北方の軍を、馬を捨てて船に乗せ、わが呉と争おうとしている。これが二つ。","孫権",},
        [4956] = {8407,"周瑜","今は真冬で、馬のまぐさも整わない。これが三つ。遠くから駆り出された兵は風土に馴染まず、病気の者が多い。これが四つ。","今は真冬で、馬のまぐさも整わない。これが三つ。遠くから駆り出された兵は風土に馴染まず、病気の者が多い。これが四つ。","孫権",},
        [4957] = {8407,"周瑜","曹操軍がこうした数々の過ちを犯しているからには、数は多くとも、敗北は必至。","曹操軍がこうした数々の過ちを犯しているからには、数は多くとも、敗北は必至。","孫権",},
        [4958] = {8407,"周瑜","曹操を生け捕りにするのは、まさに今です。私に数万人の精兵をいただければ、夏口まで進軍して、打ち破ってみせましょう！","曹操を生け捕りにするのは、まさに今です。私に数万人の精兵をいただければ、夏口まで進軍して、打ち破ってみせましょう！","孫権",},
        [4959] = {8407,"孫権","よし、周瑜を大都督に任じる。","よし、周瑜を大都督に任じる。","周瑜",},
        [4960] = {8407,"孫権","程普は副都督、魯粛は賛軍校尉として、兵を挙げて曹操を討て。これ以上降伏を言う者は、斬り捨てる！","程普は副都督、魯粛は賛軍校尉として、兵を挙げて曹操を討て。これ以上降伏を言う者は、斬り捨てる！","周瑜",},
        [4961] = {8501,"周瑜","今日御前において評議が定まった。曹操を破る良策を伺いたいのだが。","今日御前において評議が定まった。曹操を破る良策を伺いたいのだが。","諸葛亮",},
        [4962] = {8501,"諸葛亮","孫将軍のお心が、まだ落ち着かないでしょう。策を定めるにはまだ早いかと。","孫将軍のお心が、まだ落ち着かないでしょう。策を定めるにはまだ早いかと。","周瑜",},
        [4963] = {8501,"周瑜","落ち着かないとは?","落ち着かないとは?","諸葛亮",},
        [4964] = {8501,"諸葛亮","曹操軍が多勢で、とても敵うまいと内心不安を抱えておられましょう。","曹操軍が多勢で、とても敵うまいと内心不安を抱えておられましょう。","周瑜",},
        [4965] = {8501,"諸葛亮","周瑜殿が両軍の数を説明して疑念を晴らしてさしあげねば、大事は成就しませんよ。","周瑜殿が両軍の数を説明して疑念を晴らしてさしあげねば、大事は成就しませんよ。","周瑜",},
        [4966] = {8501,"周瑜","なるほど、もっともだね。","なるほど、もっともだね。","諸葛亮",},
        [4967] = {8502,"孫権","周瑜が夜中にやって来たのには、きっとわけがあるのだろうな。","周瑜が夜中にやって来たのには、きっとわけがあるのだろうな。","周瑜",},
        [4968] = {8502,"周瑜","明日は軍勢の手配りをしますが、わが君が案じておられることはありませんか?","明日は軍勢の手配りをしますが、わが君が案じておられることはありませんか?","孫権",},
        [4969] = {8502,"孫権","曹操の軍勢が多いので、衆寡敵せぬのではないかと心配だ。ほかに疑念はない。","曹操の軍勢が多いので、衆寡敵せぬのではないかと心配だ。ほかに疑念はない。","周瑜",},
        [4970] = {8502,"周瑜","その説明のためにやって参りました。","その説明のためにやって参りました。","孫権",},
        [4971] = {8502,"周瑜","曹操の檄文に「水陸の大軍百万」とあるので不安になっておいででしょうが、内実はご存知ありますまい。","曹操の檄文に「水陸の大軍百万」とあるので不安になっておいででしょうが、内実はご存知ありますまい。","孫権",},
        [4972] = {8502,"周瑜","実のところは、連戦続きで疲れた兵に、投降してきた本心もわからぬ兵ばかりで、数は多くとも恐るるに足りません。","実のところは、連戦続きで疲れた兵に、投降してきた本心もわからぬ兵ばかりで、数は多くとも恐るるに足りません。","孫権",},
        [4973] = {8502,"周瑜","五万の兵を授けていただければ、たやすく破ってみせましょう。","五万の兵を授けていただければ、たやすく破ってみせましょう。","孫権",},
        [4974] = {8502,"孫権","周瑜の言葉で疑念が晴れた。張昭などは無策で、いたく失望していたのだ。俺の心をわかってくれるのは、おぬしと魯粛だけだ。","周瑜の言葉で疑念が晴れた。張昭などは無策で、いたく失望していたのだ。俺の心をわかってくれるのは、おぬしと魯粛だけだ。","周瑜",},
        [4975] = {8503,"周瑜","孔明はとうに主君の心中を見抜いていました。","孔明はとうに主君の心中を見抜いていました。","魯粛",},
        [4976] = {8503,"周瑜","私より一枚上手です。いずれわが呉の患いとなるに違いありません。今のうちに殺しておかねば。","私より一枚上手です。いずれわが呉の患いとなるに違いありません。今のうちに殺しておかねば。","魯粛",},
        [4977] = {8503,"魯粛","いけません。曹操を破る前に策士を殺してしまっては、自分の首を絞めるようなものです。","いけません。曹操を破る前に策士を殺してしまっては、自分の首を絞めるようなものです。","周瑜",},
        [4978] = {8503,"周瑜","あの者が劉備を補佐していては、必ずやわが呉の患いとなるだろう。","あの者が劉備を補佐していては、必ずやわが呉の患いとなるだろう。","魯粛",},
        [4979] = {8503,"魯粛","諸葛瑾は彼の実の兄です。ともに呉に仕えさせるよう、孔明を説得してもらえれば良いではありませんか。","諸葛瑾は彼の実の兄です。ともに呉に仕えさせるよう、孔明を説得してもらえれば良いではありませんか。","周瑜",},
        [4980] = {8503,"周瑜","ふふっ、それもそうだね。","ふふっ、それもそうだね。","魯粛",},
        [4981] = {8504,"周瑜","孔明どのは王佐の才がありながら、なぜ劉備ごときに仕えているのだ?","孔明どのは王佐の才がありながら、なぜ劉備ごときに仕えているのだ?","諸葛瑾",},
        [4982] = {8504,"周瑜","幸いいま呉に来ておられる。劉備を見捨てて、わが呉に仕えるよう口添えしてもらえませんか。","幸いいま呉に来ておられる。劉備を見捨てて、わが呉に仕えるよう口添えしてもらえませんか。","諸葛瑾",},
        [4983] = {8504,"周瑜","そうなれば主君は良き補佐を得られ、ご兄弟も離ればなれにならない。結構ではありませんか。","そうなれば主君は良き補佐を得られ、ご兄弟も離ればなれにならない。結構ではありませんか。","諸葛瑾",},
        [4984] = {8504,"諸葛瑾","私は呉に来て以来、これという手柄もなくて恥じ入るばかりでした。都督のご下命とあらば、微力を尽くしましょう。","私は呉に来て以来、これという手柄もなくて恥じ入るばかりでした。都督のご下命とあらば、微力を尽くしましょう。","周瑜",},
        [4985] = {8505,"諸葛瑾","お前は呉に仕える気はないのか?","お前は呉に仕える気はないのか?","諸葛亮",},
        [4986] = {8505,"諸葛亮","おや、周瑜殿の使いで説得にいらしたのですね。","おや、周瑜殿の使いで説得にいらしたのですね。","諸葛瑾",},
        [4987] = {8505,"諸葛瑾","伯夷・叔斉の故事は知っているな?","伯夷・叔斉の故事は知っているな?","諸葛亮",},
        [4988] = {8505,"諸葛瑾","伯夷と叔斉は首陽山の麓で飢え死にしたが、兄弟二人いつも一緒だった。","伯夷と叔斉は首陽山の麓で飢え死にしたが、兄弟二人いつも一緒だった。","諸葛亮",},
        [4989] = {8505,"諸葛瑾","お前とは実の兄弟でありながら、別々の主人に仕えて、朝晩会うこともできぬ。伯夷・叔斉に比べて、恥ずかしいことと思わぬか。","お前とは実の兄弟でありながら、別々の主人に仕えて、朝晩会うこともできぬ。伯夷・叔斉に比べて、恥ずかしいことと思わぬか。","諸葛亮",},
        [4990] = {8505,"諸葛亮","兄上が仰るのは「情」でしょう。弟が守っているのは「義」です。そもそも兄上も私も漢朝に生まれた者ではありませんか。","兄上が仰るのは「情」でしょう。弟が守っているのは「義」です。そもそも兄上も私も漢朝に生まれた者ではありませんか。","諸葛瑾",},
        [4991] = {8505,"諸葛亮","わが主君・劉備様は正しく漢王室の血筋です。","わが主君・劉備様は正しく漢王室の血筋です。","諸葛瑾",},
        [4992] = {8505,"諸葛亮","兄上こそ、弟とともに劉備様にお仕えになれば、漢朝の臣として恥じることなく、兄弟一緒におれます。情も義も果たせましょう。","兄上こそ、弟とともに劉備様にお仕えになれば、漢朝の臣として恥じることなく、兄弟一緒におれます。情も義も果たせましょう。","諸葛瑾",},
        [4993] = {8505,"諸葛亮","兄上のお考えはいかがです?","兄上のお考えはいかがです?","諸葛瑾",},
        [4994] = {8505,"諸葛瑾","お前の気持ちはよくわかった。これにて失礼しよう。","お前の気持ちはよくわかった。これにて失礼しよう。","諸葛亮",},
        [4995] = {8506,"周瑜","かつて曹操は少ない兵で、袁紹の大軍を打ち破った。許攸の計りごとで、前もって烏巣にあった兵糧を焼き払ったからだ。","かつて曹操は少ない兵で、袁紹の大軍を打ち破った。許攸の計りごとで、前もって烏巣にあった兵糧を焼き払ったからだ。","諸葛亮",},
        [4996] = {8506,"周瑜","今曹操の兵は八十三万、わがほうは五、六万。","今曹操の兵は八十三万、わがほうは五、六万。","諸葛亮",},
        [4997] = {8506,"周瑜","まったく比較になりません。そこで敵の兵糧を断たねばならぬのですが、探らせたところ、曹操の兵糧はすべて聚鉄山に貯えてあるそうです。","まったく比較になりません。そこで敵の兵糧を断たねばならぬのですが、探らせたところ、曹操の兵糧はすべて聚鉄山に貯えてあるそうです。","諸葛亮",},
        [4998] = {8506,"周瑜","先生は長らく漢江のあたりにおられ、地理には詳しいでしょう。","先生は長らく漢江のあたりにおられ、地理には詳しいでしょう。","諸葛亮",},
        [4999] = {8506,"周瑜","お手数ですが夜半に聚鉄山を襲い、敵の糧道を断っていただきたい。これも互いの主君のためです。異存ありますまい?","お手数ですが夜半に聚鉄山を襲い、敵の糧道を断っていただきたい。これも互いの主君のためです。異存ありますまい?","諸葛亮",},
        [5000] = {8506,"諸葛亮","ええ。もちろん。","ええ。もちろん。","周瑜",},
        [5001] = {8507,"主君","周瑜は孔明殿が仕官に応じないから、曹操の手を借りて亡き者にするつもりではないか。将来邪魔にならないように。","周瑜は孔明殿が説得に応じないから、曹操の手を借りて亡き者にするつもりじゃないかしら。将来邪魔にならないように。","諸葛亮",},
        [5002] = {8507,"諸葛亮","わかっています。でもここで断れば、笑いものにするでしょう。別に考えがあるから、承知したのですよ。","わかっています。でもここで断れば、笑いものにするでしょう。別に考えがあるから、承知したのですよ。","プレイヤー",},
        [5003] = {8507,"主君","さすがは孔明殿。","さすが孔明殿。","諸葛亮",},
        [5004] = {8508,"魯粛","先生、この作戦に勝算はあるのですか?","先生、この作戦に勝算はあるのですか?","諸葛亮",},
        [5005] = {8508,"諸葛亮","私は水上戦、歩兵戦、騎兵戦、戦車戦どれも極めていますから、きっと手柄を立てて見せますよ。","私は水上戦、歩兵戦、騎兵戦、戦車戦どれも極めていますから、きっと手柄を立てて見せますよ。","魯粛",},
        [5006] = {8508,"諸葛亮","一芸しかない魯粛殿や周瑜殿とはわけが違います。","一芸しかない魯粛殿や周瑜殿とはわけが違います。","魯粛",},
        [5007] = {8508,"魯粛","む、私や周瑜殿が一芸しかないとは、どういう意味です?","む、私や周瑜殿が一芸しかないとは、どういう意味です?","諸葛亮",},
        [5008] = {8508,"諸葛亮","江南の童歌でも言っておりますよ。「魯粛は待ち伏せ、関守り、周瑜は舟乗り、水戦」と。","江南の童歌でも言っておりますよ。「魯粛は待ち伏せ、関守り、周瑜は舟乗り、水戦」と。","魯粛",},
        [5009] = {8508,"諸葛亮","魯粛殿は陸戦は得意ですが、待ち伏せして関を守るだけ。周瑜殿は水上戦は上手ですが、陸戦になるとからっきしですね。","魯粛殿は陸戦は得意ですが、待ち伏せして関を守るだけ。周瑜殿は水上戦は上手ですが、陸戦になるとからっきしですね。","魯粛",},
        [5010] = {8509,"魯粛","孔明の考えを探ったところ、周瑜殿は水上戦は得意だが、陸戦はできないと申しておりました。","孔明の考えを探ったところ、周瑜殿は水上戦は得意だが、陸戦はできないと申しておりました。","周瑜",},
        [5011] = {8509,"周瑜","ほう、この周瑜に陸上戦ができないとおっしゃいましたか……","ほう、この周瑜に陸上戦ができないとおっしゃいましたか……","魯粛",},
        [5012] = {8509,"周瑜","いいでしょう、孔明には出陣させません。私みずから一万の騎兵を率いて、聚鉄山の糧道を断ちましょう。","いいでしょう、孔明には出陣させません。私みずから一万の騎兵を率いて、聚鉄山の糧道を断ちましょう。","魯粛",},
        [5013] = {8510,"劉備","孔明は呉に出かけていったきり、何の音沙汰もない。どうしているだろう?","孔明は呉に出かけていったきり、何の音沙汰もない。どうしているだろう?","",},
        [5014] = {8510,"糜竺","様子をうかがって参りましたところ、呉の者が言うには孔明とともに曹操を破る計りごとがあるので、しばらくは帰せないと。","様子をうかがって参りましたところ、呉の者が言うには孔明とともに曹操を破る計りごとがあるので、しばらくは帰せないと。","劉備",},
        [5015] = {8510,"糜竺","周瑜殿は、劉備殿にお目にかかって、ともに策を練りたいと申しておりました。","周瑜殿は、劉備殿にお目にかかって、ともに策を練りたいと申しておりました。","劉備",},
        [5016] = {8510,"関羽","周瑜は謀略に長けた者と聞きます。","周瑜は謀略に長けた者と聞きます。","劉備",},
        [5017] = {8510,"関羽","孔明からそんな便りも届いておらず、何か企みがあるのでしょう。軽々しく行ってはなりません。","孔明からそんな便りも届いておらず、何か企みがあるのでしょう。軽々しく行ってはなりません。","劉備",},
        [5018] = {8510,"劉備","今は呉と同盟を結んで、曹操を破ろうというときだ。","今は呉と同盟を結んで、曹操を破ろうというときだ。","関羽",},
        [5019] = {8510,"劉備","周瑜が会いたいというのに行かなければ、同盟の意義に背こう。双方が疑りあっていては、事が運ばない。","周瑜が会いたいというのに行かなければ、同盟の意義に背こう。双方が疑りあっていては、事が運ばない。","関羽",},
        [5020] = {8510,"関羽","兄者がそうお考えならば、わしもお供いたしましょう。","兄者がそうお考えならば、わしもお供いたしましょう。","劉備",},
        [5021] = {8510,"張飛","兄貴、オレも連れて行ってくれ！","兄貴、オレも連れて行ってくれ！","劉備",},
        [5022] = {8510,"劉備","関羽だけついて来てくれればいい。孔明にはもう一人の義弟もついているしな。","関羽だけついて来てくれればいい。孔明にはもう一人の義妹もついているしな。","張飛",},
        [5023] = {8511,"魯粛","劉備殿がみえました。","劉備殿がみえました。","周瑜",},
        [5024] = {8511,"周瑜","何隻の船を連れてきた?","何隻の船を連れてきた?","魯粛",},
        [5025] = {8511,"魯粛","一隻だけです。従者が二十人あまり。","一隻だけです。従者が二十人あまり。","周瑜",},
        [5026] = {8511,"周瑜","ふふ……彼の命運もここまでというわけだね。兵を待ち伏せさせておけ。","ふふ……彼の命運もここまでというわけだね。兵を待ち伏せさせておけ。","魯粛",},
        [5027] = {8512,"周瑜","劉備殿、どうぞ上座へ。","劉備殿、どうぞ上座へ。","劉備",},
        [5028] = {8512,"劉備","いえ、周瑜殿は天下に名高い将軍。私のような不束者が、そう丁重にもてなされては恐れ入ります。","いえ、周瑜殿は天下に名高い将軍。私のような不束者が、そう丁重にもてなされては恐れ入ります。","周瑜",},
        [5029] = {8512,"周瑜","またまた、ご謙遜を。","またまた、ご謙遜を。","劉備",},
        [5030] = {8512,"周瑜","ところで劉備殿の後ろに立っておられるのは、どなたですかな。","ところで劉備殿の後ろに立っておられるのは、どなたですかな。","劉備",},
        [5031] = {8512,"劉備","私の義弟で、関羽と申します。","私の義弟で、関羽と申します。","周瑜",},
        [5032] = {8512,"周瑜","なんと、先ごろ顔良と文醜を討ち取ったという、あの……?","なんと、先ごろ顔良と文醜を討ち取ったという、あの……?","劉備",},
        [5033] = {8512,"劉備","いかにも。","いかにも。","周瑜",},
        [5034] = {8512,"周瑜","ほう……ご高名はかねてより承っております。さあ、関羽殿にも一献差し上げましょう。","ほう……ご高名はかねてより承っております。さあ、関羽殿にも一献差し上げましょう。","劉備",},
        [5035] = {8513,"魯粛","劉備をここまで誘き寄せておきながら、なぜ手を下さなかったのです?","劉備をここまで誘き寄せておきながら、なぜ手を下さなかったのです?","周瑜",},
        [5036] = {8513,"周瑜","かの猛将・関羽が、片時も劉備のそばを離れなかった。手を出そうものなら、先にこちらが殺されていただろう。","かの猛将・関羽が、片時も劉備のそばを離れなかった。手を出そうものなら、先にこちらが殺されていただろう。","魯粛",},
        [5037] = {8513,"魯粛","そういうわけでしたか……","そういうわけでしたか……","周瑜",},
        [5038] = {8514,"諸葛亮","わが君は今日、危うかったことにお気づきでしたか?","わが君は今日、危うかったことにお気づきでしたか?","劉備",},
        [5039] = {8514,"劉備","いや、何かあったのか?","いや、何かあったのか?","諸葛亮",},
        [5040] = {8514,"主君","もし関羽の兄者がいなかったら、兄者は周瑜に殺されてしまうところだったのですよ……","もし関羽兄さんがいなかったら、兄さんは周瑜に殺されてしまうところだったのよ……","劉備",},
        [5041] = {8514,"劉備","そ、そうか、危ないところだった。そういうことなら、孔明たちも早く一緒に帰ろう。","そ、そうか、危ないところだった。そういうことなら、孔明たちも早く一緒に帰ろう。","プレイヤー",},
        [5042] = {8514,"諸葛亮","いえ、私は虎の口にいようとも大丈夫です。わが君は兵と軍船の支度をしてお待ちください。","いえ、私は虎の口にいようとも大丈夫です。わが君は兵と軍船の支度をしてお待ちください。","劉備",},
        [5043] = {8514,"諸葛亮","十一月二十日の甲子の日が過ぎたら、趙雲殿に命じて小舟を出し、長江の南岸で私を待つようお申しつけください。","十一月二十日の甲子の日が過ぎたら、趙雲殿に命じて小舟を出し、長江の南岸で私を待つようお申しつけください。","劉備",},
        [5044] = {8514,"諸葛亮","くれぐれもお忘れなきよう。","くれぐれもお忘れなきよう。","劉備",},
        [5045] = {8514,"諸葛亮","東南の風が吹き始めましたら、必ず帰ります。","東南の風が吹き始めましたら、必ず帰ります。","劉備",},
        [5046] = {8514,"劉備","わかった。孔明も義弟も気をつけてくれ。","わかった。孔明も義妹も気をつけてくれ。","諸葛亮",},
        [5047] = {8601,"魯粛","曹操の使者が書状を持って来ました。","曹操の使者が書状を持って来ました。","周瑜",},
        [5048] = {8601,"周瑜","見せてくれ。","見せてくれ。","魯粛",},
        [5049] = {8601,"魯粛","どうぞ。","どうぞ。","周瑜",},
        [5050] = {8601,"周瑜","……使者の首を刎ねろ！","……使者の首を刎ねろ！","魯粛",},
        [5051] = {8601,"魯粛","どうしてまた?！","どうしてまた?！","周瑜",},
        [5052] = {8601,"周瑜","表書きに「漢の大丞相より周都督へ付する」とある。大したものだ、まるで臣下扱いだ！","表書きに「漢の大丞相より周都督へ付する」とある。大したものだ、まるで臣下扱いだ！","魯粛",},
        [5053] = {8601,"魯粛","しかし、国と国が争っても、使者は斬らないのが掟です。","しかし、国と国が争っても、使者は斬らないのが掟です。","周瑜",},
        [5054] = {8601,"周瑜","だからこそ、使者を血祭りにして威を示すのだよ！その首を供の者に持たせて帰そう。","だからこそ、使者を血祭りにして威を示すのだよ！その首を供の者に持たせて帰そう。","魯粛",},
        [5055] = {8601,"周瑜","甘寧を先鋒とし、韓当を左翼、蒋欽を右翼の大将とします。私は本陣を率いましょう。","甘寧を先鋒とし、韓当を左翼、蒋欽を右翼の大将とします。私は本陣を率いましょう。","魯粛",},
        [5056] = {8601,"周瑜","明日未明のうちに食事をすませ、船を出し、陣太鼓を鳴らして進みましょう！","明日未明のうちに食事をすませ、船を出し、陣太鼓を鳴らして進みましょう！","魯粛",},
        [5057] = {8602,"曹操","周瑜が書状を破り捨て、使者を斬っただと！","周瑜が書状を破り捨て、使者を斬っただと！","",},
        [5058] = {8602,"曹操","蔡瑁、張允を先陣とし、わし自ら後陣を率いる。船団を組み、三江口を目指せ。","蔡瑁、張允を先陣とし、わし自ら後陣を率いる。船団を組み、三江口を目指せ。","",},
        [5059] = {8603,"曹操","蔡勲は討ち死にし、わが軍の犠牲者はおびただしい。","蔡勲は討ち死にし、わが軍の犠牲者はおびただしい。","蔡瑁",},
        [5060] = {8603,"曹操","呉はあれしきの小勢なのに、なぜ敗れる?おぬしらが本気でかからないからだろう！","呉はあれしきの小勢なのに、なぜ敗れる?おぬしらが本気でかからないからだろう！","蔡瑁",},
        [5061] = {8603,"蔡瑁","荊州の水軍は、長らく訓練しておらず、青州・徐州の兵は、もとより水上戦に慣れておりません。ゆえに敗れた次第です。","荊州の水軍は、長らく訓練しておらず、青州・徐州の兵は、もとより水上戦に慣れておりません。ゆえに敗れた次第です。","曹操",},
        [5062] = {8603,"蔡瑁","まずは水上の陣地、「水寨」を設けましょう。","まずは水上の陣地、「水寨」を設けましょう。","曹操",},
        [5063] = {8603,"蔡瑁","青州・徐州の兵をその内側に、荊州の兵を外側におき、毎日調練すれば、使えるようになりましょう。","青州・徐州の兵をその内側に、荊州の兵を外側におき、毎日調練すれば、使えるようになりましょう。","曹操",},
        [5064] = {8603,"曹操","水軍都督よ、上手くやれ。わしに手間を掛けさせるなよ！","水軍都督よ、上手くやれ。わしに手間を掛けさせるなよ！","蔡瑁",},
        [5065] = {8604,"主君","昨夜高い所に登って見渡すと、西の空が一面真っ赤に見えました。よくよく見ればそれは曹操軍の陣営の篝火でした。","昨夜高い所に登って見渡すと、西の空が一面真っ赤に見えました。よくよく見ればそれは曹操軍の陣営の篝火でした。","周瑜",},
        [5066] = {8604,"主君","それで小舟に乗って曹操の水寨を偵察してきましたが、実に見事な水軍の運用法でした。","それで小舟に乗って曹操の水寨を偵察してきましたが、実に見事な水軍の運用法でした。","周瑜",},
        [5067] = {8604,"周瑜","水軍の都督は誰だ?","水軍の都督は誰だ?","プレイヤー",},
        [5068] = {8604,"主君","蔡瑁と張允です。","蔡瑁と張允です。","周瑜",},
        [5069] = {8604,"諸葛亮","長く江北にいた二人ですから、水上戦は心得たものでしょう。先に一計を案じてこの二人を除かないと、曹操は破れませんねえ。","長く江北にいた二人ですから、水上戦は心得たものでしょう。先に一計を案じてこの二人を除かないと、曹操は破れませんねえ。","プレイヤー",},
        [5070] = {8605,"曹操","昨日の初戦で出鼻を挫かれた上に、今度は陣の中を覗かれてしまうとは。敵を破るいい計略はないか?","昨日の初戦で出鼻を挫かれた上に、今度は陣の中を覗かれてしまうとは。敵を破るいい計略はないか?","",},
        [5071] = {8605,"蒋幹","私めは幼いころから周瑜とは同窓の間柄です。呉の陣に赴き、弁舌をもって説き伏せ、投降させましょう。","私めは幼いころから周瑜とは同窓の間柄です。呉の陣に赴き、弁舌をもって説き伏せ、投降させましょう。","曹操",},
        [5072] = {8605,"曹操","蒋幹は周瑜と親しい仲なのか?","蒋幹は周瑜と親しい仲なのか?","蒋幹",},
        [5073] = {8605,"蒋幹","丞相、ご安心ください。呉に着けば必ず成功させてみせましょう。","丞相、ご安心ください。呉に着けば必ず成功させてみせましょう。","曹操",},
        [5074] = {8605,"曹操","うむ、何か必要な物はあるか?","うむ、何か必要な物はあるか?","蒋幹",},
        [5075] = {8605,"蒋幹","では、従者一人と舟のこぎ手二人を。その他は必要ございません。","では、従者一人と舟のこぎ手二人を。その他は必要ございません。","曹操",},
        [5076] = {8605,"曹操","よし。","よし。","蒋幹",},
        [5077] = {8606,"魯粛","旧友の蒋幹と申す者が小舟に乗って訪ねてきました。","旧友の蒋幹と申す者が小舟に乗って訪ねてきました。","周瑜",},
        [5078] = {8606,"周瑜","ふっ、説客が来たか。","ふっ、説客が来たか。","魯粛",},
        [5079] = {8606,"主君","それはちょうどいい。曹操の疑心を利用して、反間計を仕掛けられますね。","それはちょうどいい。曹操の疑心を利用して、反間計を仕掛けられますね。","周瑜",},
        [5080] = {8606,"周瑜","なぜそのようなことを?","なぜそのようなことを?","プレイヤー",},
        [5081] = {8606,"主君","ちょっとお耳を拝借。よいですか、曹操は……","ちょっとお耳を拝借……まずはじめにこのしかけをですね……","周瑜",},
        [5082] = {8606,"周瑜","ふっ、その計略通りにしよう。","ふっ、その計略通りにしよう。","プレイヤー",},
        [5083] = {8607,"蒋幹","おお周瑜よ、元気にしておったか！","おお周瑜よ、元気にしておったか！","周瑜",},
        [5084] = {8607,"周瑜","ご苦労だね、蒋幹。はるばる長江を渡って、曹操の説客として来たのか?","ご苦労だね、蒋幹。はるばる長江を渡って、曹操の説客として来たのか?","蒋幹",},
        [5085] = {8607,"蒋幹","な、なにを言う！だ、だいぶご無沙汰していたから、昔語りでもしようとやって来たのだ。疑うとはひどいぞ。","な、なにを言う！だ、だいぶご無沙汰していたから、昔語りでもしようとやって来たのだ。疑うとはひどいぞ。","周瑜",},
        [5086] = {8607,"周瑜","僕も師曠ほどの耳は持っていないが、弦楽を聞けば、その興趣は分かるというものだ。","僕も師曠ほどの耳は持っていないが、弦楽を聞けば、その興趣は分かるというものだ。","蒋幹",},
        [5087] = {8607,"蒋幹","冗談でないとしたら、心外だな。旧友をそのように扱うなら、もう失礼させてもらう。","冗談でないとしたら、心外だな。旧友をそのように扱うなら、もう失礼させてもらう。","周瑜",},
        [5088] = {8607,"周瑜","とんでもない。ただ曹操の説得役かも知れぬと案じたまでだよ。違うなら、なぜすぐに去ろうとするのだ?","とんでもない。ただ曹操の説得役かも知れぬと案じたまでだよ。違うなら、なぜすぐに去ろうとするのだ?","蒋幹",},
        [5089] = {8607,"周瑜","本陣で一緒に飲もう。","本陣で一緒に飲もう。","蒋幹",},
        [5090] = {8608,"蒋幹","もう勘弁してくれ。これ以上は飲めない……","もう勘弁してくれ。これ以上は飲めない……","周瑜",},
        [5091] = {8608,"周瑜","そうか、いいだろう、宴はここまでだ。","そうか、いいだろう、宴はここまでだ。","蒋幹",},
        [5092] = {8608,"周瑜","昔はよく君と一つ寝床で寝たものだ。久々に、今宵は枕を並べて寝るとしよう。","昔はよく君と一つ寝床で寝たものだ。久々に、今宵は枕を並べて寝るとしよう。","蒋幹",},
        [5093] = {8608,"蒋幹","わかったよ。周瑜の仰せのままに……","わかったよ。周瑜の仰せのままに……","周瑜",},
        [5094] = {8609,"曹操","首尾はどうであった?","首尾はどうであった?","蒋幹",},
        [5095] = {8609,"蒋幹","周瑜はなかなか、口先だけで動かせるような男ではありませんでした。","周瑜はなかなか、口先だけで動かせるような男ではありませんでした。","曹操",},
        [5096] = {8609,"曹操","ふん。成功どころか、行って馬鹿を見たわけか。","ふん。成功どころか、行って馬鹿を見たわけか。","蒋幹",},
        [5097] = {8609,"蒋幹","いえ、周瑜の説得はできませんでしたが、丞相のために一つ秘密を探ってまいりました。","いえ、周瑜の説得はできませんでしたが、丞相のために一つ秘密を探ってまいりました。","曹操",},
        [5098] = {8609,"曹操","ほう?早く申せ。","ほう?早く申せ。","蒋幹",},
        [5099] = {8609,"蒋幹","ではどうか、お人払いを。","ではどうか、お人払いを。","曹操",},
        [5100] = {8610,"曹操","これで、ここにいるのはわしとおぬしだけになった。話してくれ。","これで、ここにいるのはわしとおぬしだけになった。話してくれ。","蒋幹",},
        [5101] = {8610,"蒋幹","昨夜周瑜は泥酔し、私は酔ったふりをして一緒に寝ていました。","昨夜周瑜は泥酔し、私は酔ったふりをして一緒に寝ていました。","曹操",},
        [5102] = {8610,"蒋幹","真夜中に起きて、幕舎の机にあった文書をこっそり見てみると、どれも往来の書簡で――","真夜中に起きて、幕舎の机にあった文書をこっそり見てみると、どれも往来の書簡で――","曹操",},
        [5103] = {8610,"蒋幹","その中に「蔡瑁・張允謹んで封ず」という一通があったのです。","その中に「蔡瑁・張允謹んで封ず」という一通があったのです。","曹操",},
        [5104] = {8610,"蒋幹","書簡の内容はこうです。「我々が曹操に降ったのは、俸禄のためではなく、時の勢いでやむを得なかったのです。","書簡の内容はこうです。「我々が曹操に降ったのは、俸禄のためではなく、時の勢いでやむを得なかったのです。","曹操",},
        [5105] = {8610,"蒋幹","今は曹操軍を水寨に閉じ込めましたから、頃合いを見計らって、逆賊・曹操の首をそちらに献じましょう――","今は曹操軍を水寨に閉じ込めましたから、頃合いを見計らって、逆賊・曹操の首をそちらに献じましょう――","曹操",},
        [5106] = {8610,"蒋幹","「いずれ使いが来たら、ご報告申しあげます。お疑いなきよう。とりいそぎお返事まで」と。","「いずれ使いが来たら、ご報告申しあげます。お疑いなきよう。とりいそぎお返事まで」と。","曹操",},
        [5107] = {8610,"曹操","おのれ、恩知らずの蔡瑁・張允めが！","おのれ、恩知らずの蔡瑁・張允めが！","蒋幹",},
        [5108] = {8611,"曹操","汝ら二人に進軍を命じる。","汝ら二人に進軍を命じる。","蔡瑁",},
        [5109] = {8611,"蔡瑁","お待ち下さい。我が軍はまだ練兵も行き届いておらず、軽々しく進軍はできませぬ。","お待ち下さい。我が軍はまだ練兵も行き届いておらず、軽々しく進軍はできませぬ。","曹操",},
        [5110] = {8611,"曹操","それを待てぬということだ。練兵が行き届けば、我が首級を周瑜に差し出す腹なのだろう！","それを待てぬということだ。練兵が行き届けば、我が首級を周瑜に差し出す腹なのだろう！","蔡瑁",},
        [5111] = {8611,"蔡瑁","何をおっしゃいます?！","何をおっしゃいます?！","曹操",},
        [5112] = {8611,"曹操","二人を引きずり出して、斬れ！","二人を引きずり出して、斬れ！","蔡瑁",},
        [5113] = {8612,"曹操","誰かおらぬか。蔡瑁と張允はもう斬ったのか?","誰かおらぬか。蔡瑁と張允はもう斬ったのか?","",},
        [5114] = {8612,"蒋幹","丞相に申し上げます。二人とも既に処刑されました。","丞相に申し上げます。二人とも既に処刑されました。","曹操",},
        [5115] = {8612,"曹操","……しまった、計略にかかったかもしれぬ。","……しまった、計略にかかったかもしれぬ。","魯粛",},
        [5116] = {8612,"蒋幹","丞相のお考えが、私には解せませぬ。","丞相のお考えが、私には解せませぬ。","曹操",},
        [5117] = {8612,"曹操","今さら悔やんでも後の祭りか……","今さら悔やんでも後の祭りか……","魯粛",},
        [5118] = {8701,"周瑜","孔明を生かしておいては危うい。必ず斬ってやらねば！","孔明を生かしておいては危うい。必ず斬ってやらねば！","魯粛",},
        [5119] = {8701,"魯粛","孔明を殺せば、曹操に笑われるのが落ちです。","孔明を殺せば、曹操に笑われるのが落ちです。","周瑜",},
        [5120] = {8701,"周瑜","いや、殺されても文句が言えないような方法で、公正に斬る！","いや、殺されても文句が言えないような方法で、公正に斬る！","魯粛",},
        [5121] = {8701,"魯粛","どのような理由で?","どのような理由で?","周瑜",},
        [5122] = {8701,"周瑜","今は教えられない。明日になればわかるだろう。","今は教えられない。明日になればわかるだろう。","魯粛",},
        [5123] = {8702,"周瑜","いよいよ曹操軍との決戦の時が迫ってきたが、水上の戦にはまずどんな武器が必要だろうか。","いよいよ曹操軍との決戦の時が迫ってきたが、水上の戦にはまずどんな武器が必要だろうか。","諸葛亮",},
        [5124] = {8702,"諸葛亮","長江でしたら、まず弓矢でしょう。","長江でしたら、まず弓矢でしょう。","周瑜",},
        [5125] = {8702,"周瑜","私も先生と同意見です。ただ、いま陣中では矢が不足しています。そこで大変お手数なのですが、十万本の矢を作ってもらえませんか。","私も先生と同意見です。ただ、いま陣中では矢が不足しています。そこで大変お手数なのですが、十万本の矢を作ってもらえませんか。","諸葛亮",},
        [5126] = {8702,"周瑜","大事に関わることゆえ、ぜひお引き受けいただきたい。","大事に関わることゆえ、ぜひお引き受けいただきたい。","諸葛亮",},
        [5127] = {8702,"諸葛亮","尽力いたしましょう。都督殿の頼みですから。その十万本の矢は、いつまでに必要ですか。","尽力いたしましょう。都督殿の頼みですから。その十万本の矢は、いつまでに必要ですか。","周瑜",},
        [5128] = {8702,"周瑜","そうだね、できれば十日以内に。","そうだね、できれば十日以内に。","諸葛亮",},
        [5129] = {8702,"諸葛亮","十日?おやおや、曹操軍がいつ攻めてくるかもわからないのに、それでは長すぎるでしょう。","十日?おやおや、曹操軍がいつ攻めてくるかもわからないのに、それでは長すぎるでしょう。","周瑜",},
        [5130] = {8702,"周瑜","では、何日でできると?","では、何日でできると?","諸葛亮",},
        [5131] = {8702,"諸葛亮","そうですねえ……三日あれば結構です。三日で十万本の矢を揃えましょう。","そうですねえ……三日あれば結構です。三日で十万本の矢を揃えましょう。","周瑜",},
        [5132] = {8702,"周瑜","……陣中で戯れ言は許されないぞ。","……陣中で戯れ言は許されないぞ。","諸葛亮",},
        [5133] = {8702,"諸葛亮","もちろんです。なんなら誓文を書きましょうか。三日以内にできなければ厳罰に服します、と。","もちろんです。なんなら誓文を書きましょうか。三日以内にできなければ厳罰に服します、と。","周瑜",},
        [5134] = {8702,"周瑜","そこまで言われるなら……戦が終われば、改めてお礼をしよう。","そこまで言われるなら……戦が終われば、改めてお礼をしよう。","諸葛亮",},
        [5135] = {8702,"諸葛亮","今日はもう間に合いませんので、明日から始めましょう。三日目に、江岸に五百人の兵士を寄こしてください。矢を運ばせますので。","今日はもう間に合いませんので、明日から始めましょう。三日目に、江岸に五百人の兵士を寄こしてください。矢を運ばせますので。","周瑜",},
        [5136] = {8703,"魯粛","孔明殿は出まかせを言ったのでは?","孔明殿は出まかせを言ったのでは?","周瑜",},
        [5137] = {8703,"周瑜","自殺願望があるようにしか思えませんね。","自殺願望があるようにしか思えませんね。","魯粛",},
        [5138] = {8703,"周瑜","私が詰め腹を切らさなくても、皆の面前で証文を書いたからには、翼が生えようと逃げられないでしょう。","私が詰め腹を切らさなくても、皆の面前で証文を書いたからには、翼が生えようと逃げられないでしょう。","魯粛",},
        [5139] = {8703,"周瑜","念の為、職人たちに言い含めてわざと仕事を遅らせ、必要な資材も揃わぬようにさせるか。","念の為、職人たちに言い含めてわざと仕事を遅らせ、必要な資材も揃わぬようにさせるか。","魯粛",},
        [5140] = {8703,"周瑜","そうすれば絶対に間に合わない。言い逃れはできないだろう。今の様子を探って来てくれないか。","そうすれば絶対に間に合わない。言い逃れはできないだろう。今の様子を探って来てくれないか。","魯粛",},
        [5141] = {8704,"諸葛亮","だから口止めしたでしょう、私に危害が及ぶから、周瑜殿には話さないようにと。","だから口止めしたでしょう、私に危害が及ぶから、周瑜殿には話さないようにと。","魯粛",},
        [5142] = {8704,"諸葛亮","魯粛殿がしゃべってしまったから、今日はひどい目に遭いましたよ。","魯粛殿がしゃべってしまったから、今日はひどい目に遭いましたよ。","魯粛",},
        [5143] = {8704,"諸葛亮","三日以内に十万本の矢を作れだなんて、もともと無理難題なのですよ。助けてください、魯粛殿。","三日以内に十万本の矢を作れだなんて、もともと無理難題なのですよ。助けてください、魯粛殿。","魯粛",},
        [5144] = {8704,"魯粛","わざわざ難しくしたのはご自身でしょうに。今さら私にどう助けろと言うのですか。","わざわざ難しくしたのはご自身でしょうに。今さら私にどう助けろと言うのですか。","諸葛亮",},
        [5145] = {8704,"諸葛亮","はい、では二十隻の船と、一隻あたり三十人の兵士をお貸しください。","はい、では二十隻の船と、一隻あたり三十人の兵士をお貸しください。","魯粛",},
        [5146] = {8704,"諸葛亮","船には青い布の幕を張り、藁束を千あまり両側に立てて並べてください。","船には青い布の幕を張り、藁束を千あまり両側に立てて並べてください。","魯粛",},
        [5147] = {8704,"諸葛亮","私に考えがあります。","私に考えがあります。","魯粛",},
        [5148] = {8704,"諸葛亮","三日目には必ず十万本の矢をご用意しましょう。ですが今度こそ周瑜殿には内密に。知られたら、失敗するかもしれませんからね。","三日目には必ず十万本の矢をご用意しましょう。ですが今度こそ周瑜殿には内密に。知られたら、失敗するかもしれませんからね。","魯粛",},
        [5149] = {8704,"魯粛","意図はよくわかりませんが、とにかく承知しました。","意図はよくわかりませんが、とにかく承知しました。","諸葛亮",},
        [5150] = {8705,"魯粛","呼ばれたので来ましたが、どういうご用でしょう?","呼ばれたので来ましたが、どういうご用でしょう?","諸葛亮",},
        [5151] = {8705,"諸葛亮","はい。これから矢を取りに行くので、お招きしました。","はい。これから矢を取りに行くので、お招きしました。","魯粛",},
        [5152] = {8705,"魯粛","矢?どこにあるのです?","矢?どこにあるのです?","諸葛亮",},
        [5153] = {8705,"諸葛亮","行ってみればわかります。","行ってみればわかります。","魯粛",},
        [5154] = {8706,"魯粛","もう夜明け前です。船はどうして曹操の水寨に向かっているのですか?","もう夜明け前です。船はどうして曹操の水寨に向かっているのですか?","諸葛亮",},
        [5155] = {8706,"諸葛亮","まあまあ、慌てずに。……よし、では、船の舳先を西に、艫を東に向け、一列に並べよ。太鼓を打ち鳴らし、鬨の声をあげなさい。","まあまあ、慌てずに。……よし、では、船の舳先を西に、艫を東に向け、一列に並べよ。太鼓を打ち鳴らし、鬨の声をあげなさい。","魯粛",},
        [5156] = {8706,"魯粛","なっ……敵軍が一斉に出て来たら、どうするんです?","なっ……敵軍が一斉に出て来たら、どうするんです?","諸葛亮",},
        [5157] = {8706,"諸葛亮","曹操はこんな濃霧の中、出てはきませんよ。こちらは酒でも酌み交わして楽しみ、霧が晴れたら帰るとしましょう。","曹操はこんな濃霧の中、出てはきませんよ。こちらは酒でも酌み交わして楽しみ、霧が晴れたら帰るとしましょう。","魯粛",},
        [5158] = {8707,"于禁","丞相、呉の船が太鼓を鳴らし鬨の声をあげています。","丞相、呉の船が太鼓を鳴らし鬨の声をあげています。","曹操",},
        [5159] = {8707,"曹操","この濃霧の中、急に攻め寄せたのは、伏兵があるに違いない。軽々しく動いてはならぬ。射手を繰りだして、一斉に矢を射よ。","この濃霧の中、急に攻め寄せたのは、伏兵があるに違いない。軽々しく動いてはならぬ。射手を繰りだして、一斉に矢を射よ。","于禁",},
        [5160] = {8707,"曹操","張遼、徐晃は各々三千人の弩弓隊をつれて、急ぎ江岸まで応援に来い。","張遼、徐晃は各々三千人の弩弓隊をつれて、急ぎ江岸まで応援に来い。","于禁",},
        [5161] = {8707,"張遼","はっ。","はっ。","曹操",},
        [5162] = {8708,"諸葛亮","曹丞相、矢はありがたく頂戴しました！","曹丞相、矢はありがたく頂戴しました！","",},
        [5163] = {8708,"曹操","なんだと！早く、あの船を追え！","なんだと！早く、あの船を追え！","諸葛亮",},
        [5164] = {8708,"于禁","船足が速く、流れも急で、もう二十里あまり離れています。追いつけません。","船足が速く、流れも急で、もう二十里あまり離れています。追いつけません。","曹操",},
        [5165] = {8708,"曹操","おのれ、孔明に計られたわ！","おのれ、孔明に計られたわ！","于禁",},
        [5166] = {8709,"諸葛亮","船に射込まれた矢は、一隻あたり五、六千本というところでしょうか。","船に射込まれた矢は、一隻あたり五、六千本というところでしょうか。","魯粛",},
        [5167] = {8709,"諸葛亮","これで労力はほとんど使わず、十万本あまりの矢を得ました。明日曹操軍が攻めて来たら、この矢で射返しましょう。","これで労力はほとんど使わず、十万本あまりの矢を得ました。明日曹操軍が攻めて来たら、この矢で射返しましょう。","魯粛",},
        [5168] = {8709,"魯粛","先生は神のようだ……！今日の霧をどうして知っておられたのです?","先生は神のようだ……！今日の霧をどうして知っておられたのです?","諸葛亮",},
        [5169] = {8709,"諸葛亮","将たる者、天文・地理に通じ、奇門・陰陽を知り、陣形を読み、兵法に明らかでないといけません。でなければ凡才でしょう。","将たる者、天文・地理に通じ、奇門・陰陽を知り、陣形を読み、兵法に明らかでないといけません。でなければ凡才でしょう。","魯粛",},
        [5170] = {8709,"諸葛亮","三日前に今日の霧が予測できたので、あえて期日を三日と切ったのです。","三日前に今日の霧が予測できたので、あえて期日を三日と切ったのです。","魯粛",},
        [5171] = {8709,"諸葛亮","周瑜殿は十日で仕上げるように言いながら、職人も材料も手に入らぬようにして、私に罪を着せて殺すつもりだったのでしょう?","周瑜殿は十日で仕上げるように言いながら、職人も材料も手に入らぬようにして、私に罪を着せて殺すつもりだったのでしょう?","魯粛",},
        [5172] = {8709,"諸葛亮","ですが、残念ながら私には天命があります。周瑜殿にどうこうできるものではありませんよ。","ですが、残念ながら私には天命があります。周瑜殿にどうこうできるものではありませんよ。","魯粛",},
        [5173] = {8709,"魯粛","まったく恐れ入りました。","まったく恐れ入りました。","諸葛亮",},
        [5174] = {8710,"周瑜","先生の神算鬼謀には恐れ入った。","先生の神算鬼謀には恐れ入った。","諸葛亮",},
        [5175] = {8710,"諸葛亮","なぁに。ほんの偽計、小細工です。別に不思議でもありません。","なぁに。ほんの偽計、小細工です。別に不思議でもありません。","周瑜",},
        [5176] = {8710,"周瑜","実は昨夜、主君から使いがあり、早く進軍せよと督促されたのですが、まだ妙案が思いつかない。どうかお教え願えないか。","実は昨夜、主君から使いがあり、早く進軍せよと督促されたのですが、まだ妙案が思いつかない。どうかお教え願えないか。","諸葛亮",},
        [5177] = {8710,"諸葛亮","そんなものありませんよ。私のような凡人に。","そんなものありませんよ。私のような凡人に。","周瑜",},
        [5178] = {8710,"周瑜","先日、曹操の水寨を見たが、実に整然としていて、うかつに攻めかかることもできません。","先日、曹操の水寨を見たが、実に整然としていて、うかつに攻めかかることもできません。","諸葛亮",},
        [5179] = {8710,"周瑜","一つ計略を思いついたのだが、その是非を判断してもらいたいのです。","一つ計略を思いついたのだが、その是非を判断してもらいたいのです。","諸葛亮",},
        [5180] = {8710,"諸葛亮","あ、ではお口を開かず、お互い心に秘めた計略を手のひらに書いて、見せ合ってみませんか。","あ、ではお口を開かず、お互い心に秘めた計略を手のひらに書いて、見せ合ってみませんか。","周瑜",},
        [5181] = {8710,"周瑜","こちらは「火」の字――火計だ。","こちらは「火」の字――火計だ。","諸葛亮",},
        [5182] = {8710,"諸葛亮","こちらも「火」の字です。","こちらも「火」の字です。","周瑜",},
        [5183] = {8710,"周瑜","ふふっ、二人とも同じ考えとわかった以上、案ずることはない。くれぐれも人に洩らさぬように頼むよ。","ふふっ、二人とも同じ考えとわかった以上、案ずることはない。くれぐれも人に洩らさぬように頼むよ。","諸葛亮",},
        [5184] = {8710,"諸葛亮","もちろんです。曹操は既に二度、私の火計にかかりましたが、今も備えはしていないでしょう。周瑜殿は思うようになさってください。","もちろんです。曹操は既に二度、私の火計にかかりましたが、今も備えはしていないでしょう。周瑜殿は思うようになさってください。","周瑜",},
        [5185] = {8801,"荀攸","何しろ呉のほうは周瑜と諸葛亮の両名が計りごとを巡らしていますから、すぐには破れません。","何しろ呉のほうは周瑜と諸葛亮の両名が計りごとを巡らしていますから、すぐには破れません。","曹操",},
        [5186] = {8801,"荀攸","誰かに偽りの投降をさせ、内通させましょう。","誰かに偽りの投降をさせ、内通させましょう。","曹操",},
        [5187] = {8801,"曹操","それはわしの考えどおりだが。軍中でそれができるのはおるか?","それはわしの考えどおりだが。軍中でそれができるのはおるか?","荀攸",},
        [5188] = {8801,"荀攸","蔡瑁は死罪となりましたが、彼の一族はまだ軍中におります。蔡中・蔡和は蔡瑁の族弟にあたり、今は副将を務めています。","蔡瑁は死罪となりましたが、彼の一族はまだ軍中におります。蔡中・蔡和は蔡瑁の族弟にあたり、今は副将を務めています。","曹操",},
        [5189] = {8801,"荀攸","あの二人に恩賞を授け、呉に偽って投降させれば、疑われることはないでしょう。","あの二人に恩賞を授け、呉に偽って投降させれば、疑われることはないでしょう。","曹操",},
        [5190] = {8801,"曹操","よい案だ。","よい案だ。","荀攸",},
        [5191] = {8802,"曹操","蔡中、蔡和の両人はわずかな手勢とともに呉へ行き、偽って降伏せよ。","蔡中、蔡和の両人はわずかな手勢とともに呉へ行き、偽って降伏せよ。","蔡和",},
        [5192] = {8802,"曹操","そして敵の動静を秘かに報告するのだ。うまくいけば、褒美は充分にとらす。くれぐれも、二心は抱くなよ。","そして敵の動静を秘かに報告するのだ。うまくいけば、褒美は充分にとらす。くれぐれも、二心は抱くなよ。","蔡和",},
        [5193] = {8802,"蔡和","二心など抱くわけはありません。私どもは妻子を荊州に置いていますので。必ず周瑜と諸葛亮の首を取って、献上いたします。","二心など抱くわけはありません。私どもは妻子を荊州に置いていますので。必ず周瑜と諸葛亮の首を取って、献上いたします。","曹操",},
        [5194] = {8802,"曹操","よし、では二人に手厚い褒美をとらせよう。","よし、では二人に手厚い褒美をとらせよう。","蔡和",},
        [5195] = {8802,"曹操","明日にも五百の兵を連れ、数隻の舟に乗って、南岸へ向かえ。","明日にも五百の兵を連れ、数隻の舟に乗って、南岸へ向かえ。","蔡和",},
        [5196] = {8802,"蔡和","ははっ。","ははっ。","曹操",},
        [5197] = {8803,"蔡和","私どもの族兄は罪もないのに、曹操めに殺されました。","私どもの族兄は罪もないのに、曹操めに殺されました。","周瑜",},
        [5198] = {8803,"蔡和","族兄の仇を討ちたくて、投降して参りました。どうかお手勢に加えて、先陣にお使いください。","族兄の仇を討ちたくて、投降して参りました。どうかお手勢に加えて、先陣にお使いください。","周瑜",},
        [5199] = {8803,"周瑜","そうか。二人とも、よく呉に参られた。厚く褒美を取らせよう。甘寧とともに先陣を率いてくれ。","そうか。二人とも、よく呉に参られた。厚く褒美を取らせよう。甘寧とともに先陣を率いてくれ。","蔡和",},
        [5200] = {8803,"蔡和","ははっ、ありがたき幸せ！","ははっ、ありがたき幸せ！","周瑜",},
        [5201] = {8804,"周瑜","蔡中と蔡和の二人は妻子も連れてきていない。偽りの投降でしょう。","蔡中と蔡和の二人は妻子も連れてきていない。偽りの投降でしょう。","甘寧",},
        [5202] = {8804,"周瑜","曹操が寄こした間者に違いありませんが、ここは相手の計略を逆手に取って、わざと内通させましょう。","曹操が寄こした間者に違いありませんが、ここは相手の計略を逆手に取って、わざと内通させましょう。","甘寧",},
        [5203] = {8804,"周瑜","なるべく丁重に扱い、秘かに用心するのだ。出陣の日が来たら、二人を血祭りにあげてやる。くれぐれも油断召されぬよう。","なるべく丁重に扱い、秘かに用心するのだ。出陣の日が来たら、二人を血祭りにあげてやる。くれぐれも油断召されぬよう。","甘寧",},
        [5204] = {8804,"甘寧","はっ！","はっ！","周瑜",},
        [5205] = {8805,"黄蓋","周瑜殿にお目にかかりたく参った！","周瑜殿にお目にかかりたく参った！","周瑜",},
        [5206] = {8805,"周瑜","黄蓋殿が夜分に訪ねてこられるとは。何か良策でも授けてくださるのか。","黄蓋殿が夜分に訪ねてこられるとは。何か良策でも授けてくださるのか。","黄蓋",},
        [5207] = {8805,"黄蓋","敵は多勢、味方は無勢ゆえ、戦が長引くのはまずい。火攻めはどうじゃ。","敵は多勢、味方は無勢ゆえ、戦が長引くのはまずい。火攻めはどうじゃ。","周瑜",},
        [5208] = {8805,"周瑜","……それは、誰から勧められたのだ?","……それは、誰から勧められたのだ?","黄蓋",},
        [5209] = {8805,"黄蓋","ワシの思いつきじゃ。誰からも教えられてはおらぬよ","ワシの思いつきじゃ。誰からも教えられてはおらぬよ","周瑜",},
        [5210] = {8805,"周瑜","奇遇ですね。実は私も同じ考えなのです。それで蔡中・蔡和の投降を偽りと承知で、内通させるためにわざと置いてあります。","奇遇ですね。実は私も同じ考えなのです。それで蔡中・蔡和の投降を偽りと承知で、内通させるためにわざと置いてあります。","黄蓋",},
        [5211] = {8805,"周瑜","ただ残念ながら、こちらから敵軍へ偽って降ってくれる者がいない。","ただ残念ながら、こちらから敵軍へ偽って降ってくれる者がいない。","黄蓋",},
        [5212] = {8805,"黄蓋","ではワシが、その役を仰せつかろう。","ではワシが、その役を仰せつかろう。","周瑜",},
        [5213] = {8805,"周瑜","だがそれには敵を信用させるため、少々痛い目に遭っていただく必要があるが……","だがそれには敵を信用させるため、少々痛い目に遭っていただく必要があるが……","黄蓋",},
        [5214] = {8805,"黄蓋","ワシは孫家代々の御恩を受けてきた身じゃ。国のためなら、たとえ肝脳地に塗れようとも、恨みはない。","ワシは孫家代々の御恩を受けてきた身じゃ。国のためなら、たとえ肝脳地に塗れようとも、恨みはない。","周瑜",},
        [5215] = {8805,"周瑜","ご老体がこの苦肉の策を引き受けてくださるとは。呉にとって大変喜ばしいことだ。","ご老体がこの苦肉の策を引き受けてくださるとは。呉にとって大変喜ばしいことだ。","黄蓋",},
        [5216] = {8805,"黄蓋","なんの、ワシは死んでも本望じゃとも。","なんの、ワシは死んでも本望じゃとも。","周瑜",},
        [5217] = {8806,"周瑜","曹操は百万の大軍を率いて、三百里の陣を張っており、一朝一夕には破れません。","曹操は百万の大軍を率いて、三百里の陣を張っており、一朝一夕には破れません。","",},
        [5218] = {8806,"周瑜","今から諸将へ三ヶ月分の兵糧を支給するから、これで防御の用意をしてください。","今から諸将へ三ヶ月分の兵糧を支給するから、これで防御の用意をしてください。","",},
        [5219] = {8806,"黄蓋","三ヶ月どころか、三十ヶ月分の兵糧をもらったところで無駄じゃ。もし今月のうちに敵を破れるのならば、破ってしまえい！","三ヶ月どころか、三十ヶ月分の兵糧をもらったところで無駄じゃ。もし今月のうちに敵を破れるのならば、破ってしまえい！","周瑜",},
        [5220] = {8806,"黄蓋","今月中に破れぬのならば、敵に降参したほうがましじゃ！張昭の言うようにな！","今月中に破れぬのならば、敵に降参したほうがましじゃ！張昭の言うようにな！","周瑜",},
        [5221] = {8806,"周瑜","私は主君より、軍を率いて曹操を破れ、降伏を言う者は斬り捨てよとの命を奉じているのです。","私は主君より、軍を率いて曹操を破れ、降伏を言う者は斬り捨てよとの命を奉じているのです。","黄蓋",},
        [5222] = {8806,"周瑜","両軍対陣の折も折、そのような言葉を吐くとは、わが軍の士気を挫くもの。その首を刎ねて、見せしめとしましょう！","両軍対陣の折も折、そのような言葉を吐くとは、わが軍の士気を挫くもの。その首を刎ねて、見せしめとしましょう！","黄蓋",},
        [5223] = {8806,"黄蓋","ワシは孫堅様に従ってこのかた、東南の地を馳せ巡り、三代の主君に仕えてきたのじゃ。小僧がでしゃばりおってからに！","ワシは孫堅様に従ってこのかた、東南の地を馳せ巡り、三代の主君に仕えてきたのじゃ。小僧がでしゃばりおってからに！","周瑜",},
        [5224] = {8806,"周瑜","この老いぼれめ、とっとと斬り捨てよ！","この老いぼれめ、とっとと斬り捨てよ！","黄蓋",},
        [5225] = {8806,"甘寧","黄蓋殿はわが呉の功臣です。何とぞ寛大な処置を。","黄蓋殿はわが呉の功臣です。何とぞ寛大な処置を。","周瑜",},
        [5226] = {8806,"周瑜","あなたまで余計な口を挟んで、軍の紀律を乱すのか！甘寧を引っ立てて杖刑にしろ。","あなたまで余計な口を挟んで、軍の紀律を乱すのか！甘寧を引っ立てて杖刑にしろ。","甘寧",},
        [5227] = {8806,"魯粛","たとえ黄蓋に罪があろうとも、死罪にしてはわが軍に不利益です。","たとえ黄蓋に罪があろうとも、死罪にしてはわが軍に不利益です。","周瑜",},
        [5228] = {8806,"魯粛","どうかご寛容に、ひとまず罪をあずけ、敵を破った後で処刑しても遅くはないでしょう。","どうかご寛容に、ひとまず罪をあずけ、敵を破った後で処刑しても遅くはないでしょう。","周瑜",},
        [5229] = {8806,"周瑜","仕方ありません。本来なら首を刎ねるところですが、諸将の顔に免じて、命だけは預けておきましょう！","仕方ありません。本来なら首を刎ねるところですが、諸将の顔に免じて、命だけは預けておきましょう！","魯粛",},
        [5230] = {8806,"周瑜","だがその罪を正すため、百杖の刑を申しつけます！","だがその罪を正すため、百杖の刑を申しつけます！","魯粛",},
        [5231] = {8807,"魯粛","今日周瑜殿が怒って黄蓋殿に罰を加えられたとき、私たちは部下として、強くは諫められませんでした。","今日周瑜殿が怒って黄蓋殿に罰を加えられたとき、私たちは部下として、強くは諫められませんでした。","諸葛亮",},
        [5232] = {8807,"魯粛","先生は客人なのに、なぜ黙って見ておられたのです?","先生は客人なのに、なぜ黙って見ておられたのです?","諸葛亮",},
        [5233] = {8807,"諸葛亮","おやおや、魯粛殿は私を馬鹿にしていらっしゃるのですか。","おやおや、魯粛殿は私を馬鹿にしていらっしゃるのですか。","魯粛",},
        [5234] = {8807,"魯粛","先生を呉にお連れして以来、馬鹿にしたことなどありませんが。何を言われるのです?","先生を呉にお連れして以来、馬鹿にしたことなどありませんが。何を言われるのです?","諸葛亮",},
        [5235] = {8807,"諸葛亮","ではお気づきでないのですね。周瑜殿が今日黄蓋殿を容赦なく打たせたのは計略です。私が止めるわけがありません。","ではお気づきでないのですね。周瑜殿が今日黄蓋殿を容赦なく打たせたのは計略です。私が止めるわけがありません。","魯粛",},
        [5236] = {8807,"魯粛","えっ、計略?今初めて気がつきました！","えっ、計略?今初めて気がつきました！","諸葛亮",},
        [5237] = {8807,"諸葛亮","苦肉の計でもやらないと、曹操の目を欺けませんからね。","苦肉の計でもやらないと、曹操の目を欺けませんからね。","魯粛",},
        [5238] = {8807,"諸葛亮","今に黄蓋殿は偽って投降するでしょうが、蔡中と蔡和には本当だと報告させなければなりません。","今に黄蓋殿は偽って投降するでしょうが、蔡中と蔡和には本当だと報告させなければなりません。","魯粛",},
        [5239] = {8808,"闞沢","将軍は周瑜都督に恨まれるようなことでもあったのですか?","将軍は周瑜都督に恨まれるようなことでもあったのですか?","黄蓋",},
        [5240] = {8808,"黄蓋","いいや。","いいや。","闞沢",},
        [5241] = {8808,"闞沢","だとすればあんな責め苦を受けられたのは……苦肉の計ではありませんか?","だとすればあんな責め苦を受けられたのは……苦肉の計ではありませんか?","黄蓋",},
        [5242] = {8808,"黄蓋","なぜそれがわかる?","なぜそれがわかる?","闞沢",},
        [5243] = {8808,"闞沢","周瑜殿の度を過ぎた振る舞いを見て、ほぼそうだろうと推察しました。","周瑜殿の度を過ぎた振る舞いを見て、ほぼそうだろうと推察しました。","黄蓋",},
        [5244] = {8808,"黄蓋","呉君三代で受けた大恩に報いようと、この一計を案じたのじゃ。曹操を破るためなら、これしきの苦痛、恨みにも思わん。","呉君三代で受けた大恩に報いようと、この一計を案じたのじゃ。曹操を破るためなら、これしきの苦痛、恨みにも思わん。","闞沢",},
        [5245] = {8808,"黄蓋","しかし軍中を見渡したところ、腹の内を打ち明けられる者がおらん。","しかし軍中を見渡したところ、腹の内を打ち明けられる者がおらん。","闞沢",},
        [5246] = {8808,"黄蓋","じゃが平素よりおぬしの忠義心は知っておるゆえ、腹を割って話そう。","じゃが平素よりおぬしの忠義心は知っておるゆえ、腹を割って話そう。","闞沢",},
        [5247] = {8808,"闞沢","打ち明けてくださるということは、私に降伏の使いをせよとのお考えですね。","打ち明けてくださるということは、私に降伏の使いをせよとのお考えですね。","黄蓋",},
        [5248] = {8808,"黄蓋","いかにもそうじゃ。引き受けてくれるか?","いかにもそうじゃ。引き受けてくれるか?","闞沢",},
        [5249] = {8808,"闞沢","男と世に生まれて、功績も立てずに草木のように朽ち果てるわけにいきません。","男と世に生まれて、功績も立てずに草木のように朽ち果てるわけにいきません。","黄蓋",},
        [5250] = {8808,"闞沢","老将軍が一命をなげうって主君の恩義に報いようというときに、私がこの身を惜しみましょうか。","老将軍が一命をなげうって主君の恩義に報いようというときに、私がこの身を惜しみましょうか。","黄蓋",},
        [5251] = {8808,"黄蓋","ありがたい、何と礼を言えばよいかのう。","ありがたい、何と礼を言えばよいかのう。","闞沢",},
        [5252] = {8808,"闞沢","そうと決まればぐずぐずしてはおれません。今すぐ参りましょう。","そうと決まればぐずぐずしてはおれません。今すぐ参りましょう。","黄蓋",},
        [5253] = {8808,"黄蓋","おう、投降の書状はすでにしたためてある。","おう、投降の書状はすでにしたためてある。","闞沢",},
        [5254] = {8808,"闞沢","ではすぐに漁夫に化けて、小舟に乗って北岸に向かいます。","ではすぐに漁夫に化けて、小舟に乗って北岸に向かいます。","黄蓋",},
        [5255] = {8809,"曹操","お前は呉の参謀だそうだが、何のためにここに来た?","お前は呉の参謀だそうだが、何のためにここに来た?","闞沢",},
        [5256] = {8809,"闞沢","呉の三代に仕えた功臣の黄蓋が、先ごろ周瑜に諸将の面前で百打の刑を受けました。","呉の三代に仕えた功臣の黄蓋が、先ごろ周瑜に諸将の面前で百打の刑を受けました。","曹操",},
        [5257] = {8809,"闞沢","憤懣やるかたなく、丞相に投降したいと、私に相談を持ちかけたのです。受け入れてもらえますか?","憤懣やるかたなく、丞相に投降したいと、私に相談を持ちかけたのです。受け入れてもらえますか?","曹操",},
        [5258] = {8809,"曹操","黄蓋は苦肉の計を用いて、お前に詐りの書状を持たせて投降し、内通しようというのであろう。","黄蓋は苦肉の計を用いて、お前に詐りの書状を持たせて投降し、内通しようというのであろう。","闞沢",},
        [5259] = {8809,"曹操","このわしを欺けると思うのか！こやつを斬ってしまえ。","このわしを欺けると思うのか！こやつを斬ってしまえ。","闞沢",},
        [5260] = {8809,"闞沢","あっははは……","あっははは……","曹操",},
        [5261] = {8809,"曹操","……何が可笑しい。","……何が可笑しい。","闞沢",},
        [5262] = {8809,"闞沢","貴様を笑うのではない。黄蓋に人を見る目がなかったのが可笑しいのだ。","貴様を笑うのではない。黄蓋に人を見る目がなかったのが可笑しいのだ。","曹操",},
        [5263] = {8809,"曹操","ふん、わしは幼少から兵法の書を読んで、偽計はよく知っておる。こんな計略で人は騙せても、わしの目を欺けると思うか！","ふん、わしは幼少から兵法の書を読んで、偽計はよく知っておる。こんな計略で人は騙せても、わしの目を欺けると思うか！","闞沢",},
        [5264] = {8809,"闞沢","その程度で兵法に詳しいと自慢しているなら、さっさと兵を引き揚げて帰ったがよい。","その程度で兵法に詳しいと自慢しているなら、さっさと兵を引き揚げて帰ったがよい。","曹操",},
        [5265] = {8809,"闞沢","交戦したところで周瑜に生け捕られるのが落ちだ。こんな無知無学の輩のせいで死ぬのか。ああ無念！","交戦したところで周瑜に生け捕られるのが落ちだ。こんな無知無学の輩のせいで死ぬのか。ああ無念！","曹操",},
        [5266] = {8809,"曹操","わしのどこが無知無学だというのだ。","わしのどこが無知無学だというのだ。","闞沢",},
        [5267] = {8809,"闞沢","書状に投降の日時が記されていないから偽計だと?","書状に投降の日時が記されていないから偽計だと?","曹操",},
        [5268] = {8809,"闞沢","もし日時を約束して、急に支障があって実行できなければどうする。こちらから迎えを寄こせば、事が露見してしまうだろうが。","もし日時を約束して、急に支障があって実行できなければどうする。こちらから迎えを寄こせば、事が露見してしまうだろうが。","曹操",},
        [5269] = {8809,"闞沢","好機をうかがっているときに、日時の約束ができるものか。","好機をうかがっているときに、日時の約束ができるものか。","曹操",},
        [5270] = {8809,"闞沢","こんな道理もわからず、味方を疑ぐって殺そうとするから、無知無学だというのだ。","こんな道理もわからず、味方を疑ぐって殺そうとするから、無知無学だというのだ。","曹操",},
        [5271] = {8809,"曹操","そうか、わしの不明であった。先ほどからの無礼は水に流してくれ。","そうか、わしの不明であった。先ほどからの無礼は水に流してくれ。","闞沢",},
        [5272] = {8809,"闞沢","私と黄蓋は、赤子が父母を慕うがごとく、本心から投降するつもりです。偽りなどありません。","私と黄蓋は、赤子が父母を慕うがごとく、本心から投降するつもりです。偽りなどありません。","曹操",},
        [5273] = {8809,"曹操","もし二人が大きな手柄を立てたら、他の者より高い爵位を賜るだろう。","もし二人が大きな手柄を立てたら、他の者より高い爵位を賜るだろう。","闞沢",},
        [5274] = {8809,"闞沢","私どもは爵位を目当てに参ったのではありません。ただ天命に従うのみです。","私どもは爵位を目当てに参ったのではありません。ただ天命に従うのみです。","曹操",},
        [5275] = {8810,"闞沢","昨日将軍は黄蓋殿を救おうとしたのに、かえって周瑜殿の辱めを受けるとは。憤りを禁じ得ません。","昨日将軍は黄蓋殿を救おうとしたのに、かえって周瑜殿の辱めを受けるとは。憤りを禁じ得ません。","甘寧",},
        [5276] = {8810,"甘寧","周瑜はおのれの才を鼻にかけ、オレたちなど眼中にない奴だ。こんな侮辱を受けて、世間に顔向けできるか！","周瑜はおのれの才を鼻にかけ、オレたちなど眼中にない奴だ。こんな侮辱を受けて、世間に顔向けできるか！","闞沢",},
        [5277] = {8810,"蔡和","将軍、何を悩んでおられます?先生もご不満の様子ですが?","将軍、何を悩んでおられます?先生もご不満の様子ですが?","甘寧",},
        [5278] = {8810,"甘寧","オレたちの苦しみが、お前らにわかってたまるか！","オレたちの苦しみが、お前らにわかってたまるか！","蔡和",},
        [5279] = {8810,"蔡和","もしや、呉を裏切って曹操に降るおつもりでは?","もしや、呉を裏切って曹操に降るおつもりでは?","甘寧",},
        [5280] = {8810,"甘寧","なんでそのことを……こうなれば、口封じだ！","なんでそのことを……こうなれば、口封じだ！","蔡和",},
        [5281] = {8810,"蔡和","いえっ、その気遣いはございません。私どもも心中を打ち明けましょう。","いえっ、その気遣いはございません。私どもも心中を打ち明けましょう。","甘寧",},
        [5282] = {8810,"甘寧","なんだ、早く言え！","なんだ、早く言え！","蔡和",},
        [5283] = {8810,"蔡和","実は私ども二人は曹操殿の密命を受け、偽って投降してきたのです。","実は私ども二人は曹操殿の密命を受け、偽って投降してきたのです。","甘寧",},
        [5284] = {8810,"蔡和","もしお二方に帰順のお考えがあるのでしたら、手引きいたします。","もしお二方に帰順のお考えがあるのでしたら、手引きいたします。","甘寧",},
        [5285] = {8810,"甘寧","それは本当か?","それは本当か?","蔡和",},
        [5286] = {8810,"蔡和","嘘など申しません！","嘘など申しません！","甘寧",},
        [5287] = {8810,"甘寧","おお！ありがたい、天の助けだ！","おお！ありがたい、天の助けだ！","蔡和",},
        [5288] = {8810,"蔡和","黄蓋殿と将軍が辱めを受けたことも、既に丞相にお知らせしてあります。","黄蓋殿と将軍が辱めを受けたことも、既に丞相にお知らせしてあります。","甘寧",},
        [5289] = {8810,"闞沢","黄蓋殿の投降については、私から丞相に書状を差し上げた。今日は甘寧殿のもとへ、ともに降る約束をしに来たのです。","黄蓋殿の投降については、私から丞相に書状を差し上げた。今日は甘寧殿のもとへ、ともに降る約束をしに来たのです。","蔡和",},
        [5290] = {8810,"甘寧","鞍替えは当然だろう。明主に出会ってしまったんだからな！","鞍替えは当然だろう。明主に出会ってしまったんだからな！","蔡和",},
        [5291] = {8811,"曹操","呉の甘寧が、周瑜の辱めを受けて内通したいと申してきた。","呉の甘寧が、周瑜の辱めを受けて内通したいと申してきた。","",},
        [5292] = {8811,"曹操","黄蓋もひどい罰に遭い、闞沢を寄こして投降を申し入れてきたが、簡単には信用できぬ。","黄蓋もひどい罰に遭い、闞沢を寄こして投降を申し入れてきたが、簡単には信用できぬ。","",},
        [5293] = {8811,"曹操","誰か直に周瑜の陣へ行き、確かな消息を探って来る者はないか?","誰か直に周瑜の陣へ行き、確かな消息を探って来る者はないか?","",},
        [5294] = {8811,"蒋幹","先日は呉に使いしながら手ぶらで戻って、慚愧に耐えません。","先日は呉に使いしながら手ぶらで戻って、慚愧に耐えません。","曹操",},
        [5295] = {8811,"蒋幹","今一度身をなげうって呉に渡り、確かな証拠を手に入れ、丞相にご報告したいです。","今一度身をなげうって呉に渡り、確かな証拠を手に入れ、丞相にご報告したいです。","曹操",},
        [5296] = {8812,"周瑜","蒋幹。また私を騙そうと思って来たのか?","蒋幹。また私を騙そうと思って来たのか?","蒋幹",},
        [5297] = {8812,"蒋幹","し、心外だな。お、幼なじみで兄弟のような君だから、腹を割って話そうと思って来たんだ。","し、心外だな。お、幼なじみで兄弟のような君だから、腹を割って話そうと思って来たんだ。","周瑜",},
        [5298] = {8812,"周瑜","私に投降を勧める気でしょう。海の水が涸れようとも降りはせんよ。","私に投降を勧める気でしょう。海の水が涸れようとも降りはせんよ。","蒋幹",},
        [5299] = {8812,"周瑜","先ごろは昔の誼にほだされて、ずいぶんと飲み、一つ寝床に寝ましたが――","先ごろは昔の誼にほだされて、ずいぶんと飲み、一つ寝床に寝ましたが――","蒋幹",},
        [5300] = {8812,"周瑜","君は私の書簡を盗んでこっそり立ち去り、曹操に報告しましたね。","君は私の書簡を盗んでこっそり立ち去り、曹操に報告しましたね。","蒋幹",},
        [5301] = {8812,"周瑜","おかげで蔡瑁と張允は殺され、私の計略は失敗した。今日またのこのこやって来たのは、下心があるからでしょう。","おかげで蔡瑁と張允は殺され、私の計略は失敗した。今日またのこのこやって来たのは、下心があるからでしょう。","蒋幹",},
        [5302] = {8812,"周瑜","昔の誼がなければ、一刀両断にしてやるところです。","昔の誼がなければ、一刀両断にしてやるところです。","蒋幹",},
        [5303] = {8812,"周瑜","追い返すつもりでしたが、一両日中にも曹操を破るという時に、また消息を洩らされては困る。","追い返すつもりでしたが、一両日中にも曹操を破るという時に、また消息を洩らされては困る。","蒋幹",},
        [5304] = {8812,"周瑜","西山にある庵にでも押し込めておく。曹操を破った後で、送り返しても遅くはないだろうし。","西山にある庵にでも押し込めておく。曹操を破った後で、送り返しても遅くはないだろうし。","蒋幹",},
