        [5316] = {8901,"龐統","周瑜はおのれの小才を鼻にかけ、人を容れる器量がありません。","周瑜はおのれの小才を鼻にかけ、人を容れる器量がありません。","蒋幹",},
        [5317] = {8901,"龐統","それでここに隠れ住んでいる次第です。そういうあなたはどなたかな?","それでここに隠れ住んでいる次第です。そういうあなたはどなたかな?","蒋幹",},
        [5318] = {8901,"蒋幹","私は蒋幹と申します。あなたほどの才があれば、どこにでも仕えられるでしょうに……","私は蒋幹と申します。あなたほどの才があれば、どこにでも仕えられるでしょうに……","龐統",},
        [5319] = {8901,"蒋幹","もしよろしければ、曹丞相にお引き合わせしますが。","もしよろしければ、曹丞相にお引き合わせしますが。","龐統",},
        [5320] = {8901,"龐統","私も呉を去ろうと思っていたところです。","私も呉を去ろうと思っていたところです。","蒋幹",},
        [5321] = {8901,"龐統","もし推挙していただけるのなら、すぐにでも参りましょう。ぐずぐずして周瑜の耳に入ったら事です。","もし推挙していただけるのなら、すぐにでも参りましょう。ぐずぐずして周瑜の耳に入ったら事です。","蒋幹",},
        [5322] = {8901,"蒋幹","では今すぐ山を下り、舟を探して、急ぎ江北に向かいましょう。","では今すぐ山を下り、舟を探して、急ぎ江北に向かいましょう。","龐統",},
        [5323] = {8902,"曹操","周瑜は若輩者のくせに、才能のない者を見下し、知識のある者を用いない。","周瑜は若輩者のくせに、才能のない者を見下し、知識のある者を用いない。","龐統",},
        [5324] = {8902,"曹操","先生のご高名はつとに伺っております。せっかくお越しいただいたからには、忌憚ない意見をお聞かせください。","先生のご高名はつとに伺っております。せっかくお越しいただいたからには、忌憚ない意見をお聞かせください。","龐統",},
        [5325] = {8902,"龐統","丞相の用兵の妙は耳にしておりましたが、今布陣を拝見するに、","丞相の用兵の妙は耳にしておりましたが、今布陣を拝見するに、","曹操",},
        [5326] = {8902,"龐統","山に沿い林に拠り、前後の陣がよく連携し、出入りの門があり、進退曲折に巧みで、古の孫子や呉子でもこれ以上の布陣はできますまい。","山に沿い林に拠り、前後の陣がよく連携し、出入りの門があり、進退曲折に巧みで、古の孫子や呉子でもこれ以上の布陣はできますまい。","曹操",},
        [5327] = {8902,"曹操","いや、それは身に余るお言葉。","いや、それは身に余るお言葉。","龐統",},
        [5328] = {8902,"龐統","ときに、陣中には良い医者はおりますかな?","ときに、陣中には良い医者はおりますかな?","曹操",},
        [5329] = {8902,"曹操","なぜそんなことを聞かれる?","なぜそんなことを聞かれる?","龐統",},
        [5330] = {8902,"龐統","水軍には病人が多いようです。良い医者に診てもらわないと。","水軍には病人が多いようです。良い医者に診てもらわないと。","曹操",},
        [5331] = {8902,"曹操","将兵がここの風土に馴染めないのです。病にかかって、激しい嘔吐で死ぬ者も多い。わしも憂いていたところです。","将兵がここの風土に馴染めないのです。病にかかって、激しい嘔吐で死ぬ者も多い。わしも憂いていたところです。","龐統",},
        [5332] = {8902,"龐統","丞相の水軍の調練はお見事ですが、惜しいことに一つ欠けております。","丞相の水軍の調練はお見事ですが、惜しいことに一つ欠けております。","曹操",},
        [5333] = {8902,"龐統","私に一計があります。大小の水軍に、一人の病人も出さないようにしましょう。","私に一計があります。大小の水軍に、一人の病人も出さないようにしましょう。","曹操",},
        [5334] = {8902,"曹操","ほう、どんな策ですか?","ほう、どんな策ですか?","龐統",},
        [5335] = {8902,"龐統","長江には潮の満ち引きがあり、波風がやむこともありません。","長江には潮の満ち引きがあり、波風がやむこともありません。","曹操",},
        [5336] = {8902,"龐統","北の兵士は船に慣れていない上、激しく揺られて、病気になりやすいのです。","北の兵士は船に慣れていない上、激しく揺られて、病気になりやすいのです。","曹操",},
        [5337] = {8902,"龐統","そこで大小の船を残らず集結させ、三十隻あるいは五十隻を一列にし、船の首尾に鉄の環を打って数珠つなぎにします。","そこで大小の船を残らず集結させ、三十隻あるいは五十隻を一列にし、船の首尾に鉄の環を打って数珠つなぎにします。","曹操",},
        [5338] = {8902,"龐統","幅の広い橋板を架け渡せば――","幅の広い橋板を架け渡せば――","曹操",},
        [5339] = {8902,"龐統","人はもちろん、馬でも交互に往来できましょう。","人はもちろん、馬でも交互に往来できましょう。","曹操",},
        [5340] = {8902,"龐統","このように船を繋ぎ合わせれば、たとえ風や波が激しくとも、揺れ動く心配はありません。","このように船を繋ぎ合わせれば、たとえ風や波が激しくとも、揺れ動く心配はありません。","曹操",},
        [5341] = {8902,"曹操","なるほど、先生の妙計のおかげで、いよいよ呉が討てる！","なるほど、先生の妙計のおかげで、いよいよ呉が討てる！","龐統",},
        [5342] = {8902,"龐統","なんの、浅はかな思いつきです。丞相みずからご裁断ください。","なんの、浅はかな思いつきです。丞相みずからご裁断ください。","曹操",},
        [5343] = {8902,"曹操","すぐに従軍の鍛冶屋を呼び、連環の大釘を打たせ、船と船を繋ぐとしよう。","すぐに従軍の鍛冶屋を呼び、連環の大釘を打たせ、船と船を繋ぐとしよう。","龐統",},
        [5344] = {8903,"徐庶","曲者め！黄蓋が苦肉の計を用い、闞沢が偽りの投降を申し立てた上、お前まで連環の計をそそのかすとは。","曲者め！黄蓋が苦肉の計を用い、闞沢が偽りの投降を申し立てた上、お前まで連環の計をそそのかすとは。","龐統",},
        [5345] = {8903,"徐庶","そうやって一人残らず焼き殺す気か！","そうやって一人残らず焼き殺す気か！","龐統",},
        [5346] = {8903,"徐庶","お前達の数々の悪だくみ、たとえ曹操は騙せても、私は騙されんぞ！","お前達の数々の悪だくみ、たとえ曹操は騙せても、私は騙されんぞ！","龐統",},
        [5347] = {8903,"龐統","まっ待て、もしお前がこの計略をばらしたら、呉国八十一州の民は、お前が殺したことになるぞ！","まっ待て、もしお前がこの計略をばらしたら、呉国八十一州の民は、お前が殺したことになるぞ！","徐庶",},
        [5348] = {8903,"徐庶","それを言うなら、こちらの八十三万の兵士の命はどうなる?","それを言うなら、こちらの八十三万の兵士の命はどうなる?","龐統",},
        [5349] = {8903,"主君","徐庶殿は本気でこちらの計略を破るつもりなのですか?","徐庶殿は本気でこちらの計略を破るつもりなの?","徐庶",},
        [5350] = {8903,"徐庶","あはは、いえ、今のはほんの戯れ。私は劉備殿のご恩を片時も忘れてはおりませんよ。","あはは、いえ、今のはほんの戯れ。私は劉備殿のご恩を片時も忘れてはおりませんよ。","プレイヤー",},
        [5351] = {8903,"徐庶","曹操がわが母を死なせてからは、生涯献策はしないと誓ったのです。そちらの見事な策の邪魔立てはしません。","曹操がわが母を死なせてからは、生涯献策はしないと誓ったのです。そちらの見事な策の邪魔立てはしません。","プレイヤー",},
        [5352] = {8903,"徐庶","しかし私も従軍している身。戦に敗れれば、巻き添えを食います。この機会に黙って遠くの地へ逃げようかと。","しかし私も従軍している身。戦に敗れれば、巻き添えを食います。この機会に黙って遠くの地へ逃げようかと。","プレイヤー",},
        [5353] = {8903,"主君","徐庶殿ほど知略に富んだ人なら、何も難しいことはないでしょう。","徐庶殿ほど知略に富んだ人なら、何も難しいことはないでしょう。","徐庶",},
        [5354] = {8903,"徐庶","どうか教えていただきたい。","どうか教えていただきたい。","プレイヤー",},
        [5355] = {8903,"主君","ではちょっとお耳を……この計略のキモは……","ではちょっとお耳を……実はここ通りをぬけると……","徐庶",},
        [5356] = {8903,"徐庶","ははは、それは名案です。かたじけない、揚威将軍。","ははは、それは名案です。かたじけない、揚威将軍。","プレイヤー",},
        [5357] = {8904,"曹操","西涼州の韓遂と馬騰が謀反を起こし、許都に攻め寄せているとの噂が軍中で広まっておる。","西涼州の韓遂と馬騰が謀反を起こし、許都に攻め寄せているとの噂が軍中で広まっておる。","",},
        [5358] = {8904,"曹操","虚実は定かではないが、対策は講じておかねばならん。","虚実は定かではないが、対策は講じておかねばならん。","",},
        [5359] = {8904,"徐庶","わたくし、丞相にお仕えしてから一寸の功も無く、心苦しく思っておりました。","わたくし、丞相にお仕えしてから一寸の功も無く、心苦しく思っておりました。","曹操",},
        [5360] = {8904,"徐庶","三千の兵と馬をお貸しくだされば、急ぎ帰って散関の要害をしかと守りましょう。もし急変があればご報告申し上げます。","三千の兵と馬をお貸しくだされば、急ぎ帰って散関の要害をしかと守りましょう。もし急変があればご報告申し上げます。","曹操",},
        [5361] = {8904,"曹操","徐庶が行ってくれるのならば心配はない。","徐庶が行ってくれるのならば心配はない。","徐庶",},
        [5362] = {8904,"曹操","散関には守備軍もおるから、指揮は任せる。とりあえず三千の騎兵と歩兵をつかわす。臧覇を先鋒として、夜を日についで行け。遅れるな。","散関には守備軍もおるから、指揮は任せる。とりあえず三千の騎兵と歩兵をつかわす。臧覇を先鋒として、夜を日についで行け。遅れるな。","徐庶",},
        [5363] = {8904,"徐庶","はい、では直ちに臧覇と出発いたします。","はい、では直ちに臧覇と出発いたします。","曹操",},
        [5364] = {9001,"曹操","周瑜も魯粛も、天の時を知らぬ。投降してきた者が獅子身中の虫となっていることも知るまい。これは天が我を助けるものだ！","周瑜も魯粛も、天の時を知らぬ。投降してきた者が獅子身中の虫となっていることも知るまい。これは天が我を助けるものだ！","",},
        [5365] = {9001,"荀攸","丞相、滅多なことをお口に出してはなりませぬ。漏れ聞こえますぞ。","丞相、滅多なことをお口に出してはなりませぬ。漏れ聞こえますぞ。","曹操",},
        [5366] = {9001,"曹操","一座の諸将も側近の者も、みなわしの腹心だ。話したとて何の差し障りもなかろう。","一座の諸将も側近の者も、みなわしの腹心だ。話したとて何の差し障りもなかろう。","荀攸",},
        [5367] = {9001,"曹操","劉備に、諸葛亮よ。虫けらほどの力で、泰山を揺るがそうとは、なんと愚かな奴らよ！","劉備に、諸葛亮よ。虫けらほどの力で、泰山を揺るがそうとは、なんと愚かな奴らよ！","荀攸",},
        [5368] = {9002,"曹操","いま南へ飛んでゆく鴉の鳴き声を聞いたが、なぜこう夜中に鳴くのか。","いま南へ飛んでゆく鴉の鳴き声を聞いたが、なぜこう夜中に鳴くのか。","",},
        [5369] = {9002,"程昱","月が明るいので、夜が明けたかと思って木を離れ、鳴いたのでしょうな。","月が明るいので、夜が明けたかと思って木を離れ、鳴いたのでしょうな。","曹操",},
        [5370] = {9002,"曹操","思えば一剣をもって黄巾の賊を破り、呂布を擒にし、袁術を滅ぼし、袁紹を平げて、深く塞北に軍馬を進め、遼東に切り込んだ。","思えば一剣をもって黄巾の賊を破り、呂布を擒にし、袁術を滅ぼし、袁紹を平げて、深く塞北に軍馬を進め、遼東に切り込んだ。","程昱",},
        [5371] = {9002,"曹操","これだけ天下を駆け巡れば、まず大丈夫の志には背かぬだろう。","これだけ天下を駆け巡れば、まず大丈夫の志には背かぬだろう。","程昱",},
        [5372] = {9002,"曹操","今宵の景色は感激に耐えぬな。即興で詩を作ったから、みなも唱和せよ。","今宵の景色は感激に耐えぬな。即興で詩を作ったから、みなも唱和せよ。","程昱",},
        [5373] = {9002,"曹操","酒に対いては当に歌うべし、人生幾何ぞ。譬えば朝露の如く、去りし日は苦だ多し。","酒に対いては当に歌うべし、人生幾何ぞ。譬えば朝露の如く、去りし日は苦だ多し。","程昱",},
        [5374] = {9002,"曹操","慨して当にもって慷すべきも、憂う思いは忘れ難し。何をもってか憂いを解かん、ただ杜康有るのみ。","慨して当にもって慷すべきも、憂う思いは忘れ難し。何をもってか憂いを解かん、ただ杜康有るのみ。","程昱",},
        [5375] = {9002,"曹操","青青たる子が衿、悠悠たる我が心。ただ君が為め故に、沈吟して今に至る――","青青たる子が衿、悠悠たる我が心。ただ君が為め故に、沈吟して今に至る――","程昱",},
        [5376] = {9003,"曹操","鳳雛の妙計を得られたのは、天の助けだった。鉄索で繋いだ船は長江を渡っても揺れることなく、平地にいるようだ。","鳳雛の妙計を得られたのは、天の助けだった。鉄索で繋いだ船は長江を渡っても揺れることなく、平地にいるようだ。","",},
        [5377] = {9003,"程昱","船をみな鎖で繋いで、なるほど揺れはなくなりましたが、","船をみな鎖で繋いで、なるほど揺れはなくなりましたが、","曹操",},
        [5378] = {9003,"程昱","もし敵が火攻めを仕掛けてきたら、逃げることも防ぐこともできぬのではありませぬか。","もし敵が火攻めを仕掛けてきたら、逃げることも防ぐこともできぬのではありませぬか。","曹操",},
        [5379] = {9003,"曹操","ふっ、程昱は深謀遠慮だが、見落としていることがある。","ふっ、程昱は深謀遠慮だが、見落としていることがある。","程昱",},
        [5380] = {9003,"荀攸","程昱の話には一理があるが、丞相はなぜお笑いになるのです?","程昱の話には一理があるが、丞相はなぜお笑いになるのです?","曹操",},
        [5381] = {9003,"曹操","火攻めの計は、風の力を借りねばならぬが、今は冬だ。西風と北風は吹いても、東風や南風が吹くことはない。","火攻めの計は、風の力を借りねばならぬが、今は冬だ。西風と北風は吹いても、東風や南風が吹くことはない。","荀攸",},
        [5382] = {9003,"曹操","こちらは西北に陣取っており、敵は南岸に構えておる。もし向こうから火をかければ、自分の兵を焼くことになろう。","こちらは西北に陣取っており、敵は南岸に構えておる。もし向こうから火をかければ、自分の兵を焼くことになろう。","荀攸",},
        [5383] = {9003,"曹操","よって恐れることはない。仮に十月の小春日和だったら、わしも用心しておるわ。","よって恐れることはない。仮に十月の小春日和だったら、わしも用心しておるわ。","荀攸",},
        [5384] = {9003,"程昱","なるほど、丞相の卓見には、我々も及びませんですじゃ。","なるほど、丞相の卓見には、我々も及びませんですじゃ。","曹操",},
        [5385] = {9004,"主君","我が事成れり、だ。曹操は明日進撃する。私たちも急いで引き上げましょう。","我が事成れり、ね。曹操は明日進撃する。私たちも急いで引き上げましょう。","龐統",},
        [5386] = {9004,"龐統","ええ、小舟を一隻探して夜こっそり消えましょう。","ええ、小舟を一隻探して夜こっそり消えましょう。","プレイヤー",},
        [5387] = {9101,"周瑜","狂風が吹き荒れて、陣中の旗竿が折れ、黄色い旗が水上に落ちた。不吉な兆しでは……！","狂風が吹き荒れて、陣中の旗竿が折れ、黄色い旗が水上に落ちた。不吉な兆しでは……！","魯粛",},
        [5388] = {9101,"周瑜","この風でどうやって火攻めをやれというのか……これまでの策略が全て水の泡になってしまう。","この風でどうやって火攻めをやれというのか……これまでの策略が全て水の泡になってしまう。","魯粛",},
        [5389] = {9101,"魯粛","それは……","それは……","周瑜",},
        [5390] = {9101,"周瑜","ぐっ……ゲボッ！","ぐっ……ゲボッ！","魯粛",},
        [5391] = {9101,"魯粛","誰か、都督が血を吐かれた！早く軍医を呼べ！","誰か、都督が血を吐かれた！早く軍医を呼べ！","周瑜",},
        [5392] = {9102,"魯粛","狂風が吹き荒れ、火計は行えず、周瑜殿は急な病で倒れて……","狂風が吹き荒れ、火計は行えず、周瑜殿は急な病で倒れて……","諸葛亮",},
        [5393] = {9102,"諸葛亮","この出来事をどう思われます?","この出来事をどう思われます?","魯粛",},
        [5394] = {9102,"魯粛","曹操にとっては幸い、わが呉にとっては禍いです。","曹操にとっては幸い、わが呉にとっては禍いです。","諸葛亮",},
        [5395] = {9102,"諸葛亮","そうとも限りませんねえ。周瑜殿の病は、私が治せますよ。","そうとも限りませんねえ。周瑜殿の病は、私が治せますよ。","魯粛",},
        [5396] = {9102,"魯粛","おお！それができるなら、わが呉にとって何よりの幸いです！早くお見舞いに行きましょう。","おお！それができるなら、わが呉にとって何よりの幸いです！早くお見舞いに行きましょう。","諸葛亮",},
        [5397] = {9103,"魯粛","都督、お気分はいかがです?","都督、お気分はいかがです?","周瑜",},
        [5398] = {9103,"周瑜","胸が苦しくて、時々目まいがする。","胸が苦しくて、時々目まいがする。","魯粛",},
        [5399] = {9103,"魯粛","何かお薬は飲まれましたか?","何かお薬は飲まれましたか?","周瑜",},
        [5400] = {9103,"周瑜","胸がつかえていて、薬も喉を通らぬのだ。","胸がつかえていて、薬も喉を通らぬのだ。","魯粛",},
        [5401] = {9103,"魯粛","今しがた孔明に会って来たのですが、都督の病を治せると言われたので、陣中まで連れて参りました。お呼びしますか?","今しがた孔明に会って来たのですが、都督の病を治せると言われたので、陣中まで連れて参りました。お呼びしますか?","周瑜",},
        [5402] = {9103,"周瑜","すぐここへ通してくれ。","すぐここへ通してくれ。","魯粛",},
        [5403] = {9104,"諸葛亮","数日お目にかかりませんでしたが、病に伏せっておられたとは。","数日お目にかかりませんでしたが、病に伏せっておられたとは。","周瑜",},
        [5404] = {9104,"周瑜","人に旦夕の禍福あり、と言うが、先のことはわからないものだ。","人に旦夕の禍福あり、と言うが、先のことはわからないものだ。","諸葛亮",},
        [5405] = {9104,"諸葛亮","天に不測の風雲あり、とも申しますよ。天も人も気が変わりやすい。都督殿、気がふさいでおられますか?","天に不測の風雲あり、とも申しますよ。天も人も気が変わりやすい。都督殿、気がふさいでおられますか?","周瑜",},
        [5406] = {9104,"周瑜","ああ。","ああ。","諸葛亮",},
        [5407] = {9104,"諸葛亮","なるほど……私に良い処方がありますが。","なるほど……私に良い処方がありますが。","周瑜",},
        [5408] = {9104,"周瑜","ぜひ教えてほしい。","ぜひ教えてほしい。","諸葛亮",},
        [5409] = {9104,"諸葛亮","曹操を破る火攻めの準備は全て整ったのに、東風だけが吹かない――これが都督殿の病の根源ですね。","曹操を破る火攻めの準備は全て整ったのに、東風だけが吹かない――これが都督殿の病の根源ですね。","周瑜",},
        [5410] = {9104,"周瑜","ふっ、見抜かれていたか……治す薬はあるのか?火急の用だ、どうかお教え願いたい。","ふっ、見抜かれていたか……治す薬はあるのか?火急の用だ、どうかお教え願いたい。","諸葛亮",},
        [5411] = {9104,"諸葛亮","私は若い頃、さる方士から奇門遁甲の天書を伝授されましてね、風や雨を呼ぶことができるのです。","私は若い頃、さる方士から奇門遁甲の天書を伝授されましてね、風や雨を呼ぶことができるのです。","周瑜",},
        [5412] = {9104,"諸葛亮","もし都督殿が東南の風をお望みならば、南屏山に祭壇を築いてください。これは七星壇といいます。","もし都督殿が東南の風をお望みならば、南屏山に祭壇を築いてください。これは七星壇といいます。","周瑜",},
        [5413] = {9104,"諸葛亮","高さは九尺で、三重にし、百二十人に旗を持たせて周囲を取り囲んでください。","高さは九尺で、三重にし、百二十人に旗を持たせて周囲を取り囲んでください。","周瑜",},
        [5414] = {9104,"諸葛亮","私がその祭壇で方術を執り行い、三日三晩の間、東南の風を天より借り受け、都督殿の作戦をお助けしたら、いかがでしょう?","私がその祭壇で方術を執り行い、三日三晩の間、東南の風を天より借り受け、都督殿の作戦をお助けしたら、いかがでしょう?","周瑜",},
        [5415] = {9104,"周瑜","三日三晩もいらないよ。ただ一夜の大風が吹けば、事は成就する。だが目前に迫っていて、一刻の猶予もない。","三日三晩もいらないよ。ただ一夜の大風が吹けば、事は成就する。だが目前に迫っていて、一刻の猶予もない。","諸葛亮",},
        [5416] = {9104,"諸葛亮","では十一月二十日の甲子の日に風を祭り、二十二日の丙寅の日に風がやむようにしましょう。いかがです?","では十一月二十日の甲子の日に風を祭り、二十二日の丙寅の日に風がやむようにしましょう。いかがです?","周瑜",},
        [5417] = {9104,"周瑜","さっそく五百人の屈強な兵士を選んで、南屏山に祭壇を築かせましょう。","さっそく五百人の屈強な兵士を選んで、南屏山に祭壇を築かせましょう。","諸葛亮",},
        [5418] = {9104,"周瑜","また百二十人に旗を持って祭壇を守るよう、命令を伝えます。","また百二十人に旗を持って祭壇を守るよう、命令を伝えます。","諸葛亮",},
        [5419] = {9105,"周瑜","もう日も暮れたが、空は澄んで、そよ風も吹きませんね。","もう日も暮れたが、空は澄んで、そよ風も吹きませんね。","魯粛",},
        [5420] = {9105,"周瑜","孔明に騙されたのでしょうか。この冬のさなか、東南の風が吹くわけがない。","孔明に騙されたのでしょうか。この冬のさなか、東南の風が吹くわけがない。","魯粛",},
        [5421] = {9105,"魯粛","いや、孔明殿のこと、でたらめは言わないと思いますが……","いや、孔明殿のこと、でたらめは言わないと思いますが……","周瑜",},
        [5422] = {9105,"魯粛","あっ、都督、あれを！旗が翻りました！西北の方向に靡いています。いま、急に東南の風が吹きだしました！","あっ、都督、あれを！旗が翻りました！西北の方向に靡いています。いま、急に東南の風が吹きだしました！","周瑜",},
        [5423] = {9105,"周瑜","……なんと。天地造化の法を奪い、鬼神不測の術をなす男だね。わが呉の禍いの元になる。やはり生かしておいては……","……なんと。天地造化の法を奪い、鬼神不測の術をなす男だね。わが呉の禍いの元になる。やはり生かしておいては……","魯粛",},
        [5424] = {9105,"周瑜","丁奉、徐盛！二人は各々百人の兵を率いて、徐盛は水路を、丁奉は陸路を通り――","丁奉、徐盛！二人は各々百人の兵を率いて、徐盛は水路を、丁奉は陸路を通り――","魯粛",},
        [5425] = {9105,"周瑜","南屏山の七星壇に急行せよ。有無を言わさず、諸葛亮を引っ捕らえてその首を刎ね、首級をここに持って参るのだ。","南屏山の七星壇に急行せよ。有無を言わさず、諸葛亮を引っ捕らえてその首を刎ね、首級をここに持って参るのだ。","魯粛",},
        [5426] = {9105,"徐盛","はっ、都督。","はっ、都督。","周瑜",},
        [5427] = {9106,"主君","周瑜殿は度量が狭い。いま東南の風が起こったのを見て、きっと捕まえに来るでしょう。","周瑜殿は度量が狭い。いま東南の風が起こったのを見て、きっと捕まえに来るでしょう。","諸葛亮",},
        [5428] = {9106,"諸葛亮","ええ、想定済みです。ですから趙雲殿を迎えに寄こすようお願いしたのです。","ええ、想定済みです。ですから趙雲殿を迎えに寄こすようお願いしたのです。","プレイヤー",},
        [5429] = {9106,"主君","昨晩、この先の浅瀬に小舟を用意しておきました。急いで趙雲と落ち合いましょう。","昨晩、この先の浅瀬に小舟を用意しておきました。急いで趙雲と落ち合いましょう。","諸葛亮",},
        [5430] = {9107,"徐盛","軍師、お待ちください！都督がお呼びです！","軍師、お待ちください！都督がお呼びです！","諸葛亮",},
        [5431] = {9107,"諸葛亮","では都督殿にお伝えください。戦の手抜かりなきようにと。諸葛亮はしばらく夏口に帰ります。後日またお目にかかりましょう。","では都督殿にお伝えください。戦の手抜かりなきようにと。諸葛亮はしばらく夏口に帰ります。後日またお目にかかりましょう。","徐盛",},
        [5432] = {9107,"徐盛","しばし、お止まりください、大事な話があります！","しばし、お止まりください、大事な話があります！","諸葛亮",},
        [5433] = {9107,"諸葛亮","それはできませんねえ。都督殿は私が邪魔で、きっと殺しに来るであろうことは察していました。","それはできませんねえ。都督殿は私が邪魔で、きっと殺しに来るであろうことは察していました。","徐盛",},
        [5434] = {9107,"諸葛亮","そのため趙雲殿を迎えに呼んだのですから、追ってきても無駄ですよ。","そのため趙雲殿を迎えに呼んだのですから、追ってきても無駄ですよ。","徐盛",},
        [5435] = {9107,"趙雲","常山の趙雲、ここにあり！特命を受けて軍師を迎えに参ったのを、いつまで追ってくる気だ！","常山の趙雲、ここにあり！特命を受けて軍師を迎えに参ったのを、いつまで追ってくる気だ！","徐盛",},
        [5436] = {9107,"趙雲","本来ならばこの一矢で射殺すところ、両国の誼を思って放たずにいるのだ！","本来ならばこの一矢で射殺すところ、両国の誼を思って放たずにいるのだ！","徐盛",},
        [5437] = {9107,"丁奉","なんという……諸葛亮の神機妙算は、俺たちの及ぶところじゃない。","なんという……諸葛亮の神機妙算は、俺たちの及ぶところじゃない。","徐盛",},
        [5438] = {9107,"丁奉","おまけに趙雲といえば万夫不当の勇将だ。当陽の長坂の働きは知ってるだろう?","おまけに趙雲といえば万夫不当の勇将だ。当陽の長坂の働きは知ってるだろう?","徐盛",},
        [5439] = {9107,"丁奉","戻って報告するしかあるまい。","戻って報告するしかあるまい。","徐盛",},
        [5440] = {9107,"徐盛","そうだな。","そうだな。","丁奉",},
        [5441] = {9108,"徐盛","孔明はあらかじめ趙雲を迎えに呼んでおり、追いつけませんでした。","孔明はあらかじめ趙雲を迎えに呼んでおり、追いつけませんでした。","周瑜",},
        [5442] = {9108,"周瑜","これほど謀略を巡らす男が生きているうちは、夜も安らかに寝られんな……","これほど謀略を巡らす男が生きているうちは、夜も安らかに寝られんな……","徐盛",},
        [5443] = {9108,"魯粛","曹操を打ち破るのが先です。それから考えましょう。","曹操を打ち破るのが先です。それから考えましょう。","周瑜",},
        [5444] = {9108,"周瑜","ああ……","ああ……","魯粛",},
        [5445] = {9201,"魯粛","南のほうから帆船の船隊が風を受けて上って来るのがかすかに見えます。","南のほうから帆船の船隊が風を受けて上って来るのがかすかに見えます。","曹操",},
        [5446] = {9201,"魯粛","どの船も青龍の牙旗を立てており、その中の大旗に、先鋒黄蓋の字が見えます。","どの船も青龍の牙旗を立てており、その中の大旗に、先鋒黄蓋の字が見えます。","曹操",},
        [5447] = {9201,"曹操","黄蓋が投降してくるのは、わが魏軍を天が助けるということだ！","黄蓋が投降してくるのは、わが魏軍を天が助けるということだ！","魯粛",},
        [5448] = {9201,"程昱","いえ、あの船はどうも怪しいですじゃ。味方の陣に近づけてはなりませぬぞ。","いえ、あの船はどうも怪しいですじゃ。味方の陣に近づけてはなりませぬぞ。","曹操",},
        [5449] = {9201,"曹操","なぜ怪しいと思う?","なぜ怪しいと思う?","程昱",},
        [5450] = {9201,"程昱","兵糧を積み込んだ船ならば、船足は重いはずですじゃ。しかし来る船を見ると、船足が軽く浮かんでおります。","兵糧を積み込んだ船ならば、船足は重いはずですじゃ。しかし来る船を見ると、船足が軽く浮かんでおります。","曹操",},
        [5451] = {9201,"程昱","しかも今夜は東南の風が強い。もし謀があれば、防ぎようがありませぬぞ。","しかも今夜は東南の風が強い。もし謀があれば、防ぎようがありませぬぞ。","曹操",},
        [5452] = {9201,"曹操","ふむ……誰か行って、あれを止めよ！","ふむ……誰か行って、あれを止めよ！","程昱",},
        [5453] = {9201,"文聘","わしは水上の戦には慣れております。参りましょう。","わしは水上の戦には慣れております。参りましょう。","曹操",},
        [5454] = {9201,"曹操","頼んだぞ。","頼んだぞ。","文聘",},
        [5455] = {9202,"文聘","曹丞相の命令であるぞ。南から来る諸船は、こちらの陣に近寄らず、碇を下ろせ！","曹丞相の命令であるぞ。南から来る諸船は、こちらの陣に近寄らず、碇を下ろせ！","黄蓋",},
        [5456] = {9202,"文聘","帆を下ろせい！","帆を下ろせい！","黄蓋",},
        [5457] = {9202,"黄蓋","もう遅いわ！火矢、構え！放てい！","もう遅いわ！火矢、構え！放てい！","文聘",},
        [5458] = {9202,"黄蓋","火船、水寨に突っ込めい！","火船、水寨に突っ込めい！","文聘",},
        [5459] = {9202,"文聘","いかん！あの船を食い止めよ！","いかん！あの船を食い止めよ！","黄蓋",},
        [5460] = {9203,"黄蓋","黄蓋、ここにあり！曹操めが、逃がしはせぬぞ！","黄蓋、ここにあり！曹操めが、逃がしはせぬぞ！","曹操",},
        [5461] = {9203,"曹操","誰か、早く！援護せい！","誰か、早く！援護せい！","黄蓋",},
        [5462] = {9203,"張遼","鉄の鎖で繋がった船が、次々に炎上しています。火は風に煽られて燃え広がるばかり……","鉄の鎖で繋がった船が、次々に炎上しています。火は風に煽られて燃え広がるばかり……","曹操",},
        [5463] = {9203,"張遼","丞相、早く小艇に乗って岸へ！","丞相、早く小艇に乗って岸へ！","曹操",},
        [5464] = {9203,"黄蓋","待てい、逃げるな！","待てい、逃げるな！","張遼",},
        [5465] = {9203,"張遼","黄蓋め、この矢をくらえ！","黄蓋め、この矢をくらえ！","黄蓋",},
        [5466] = {9301,"諸葛亮","趙雲殿は三千の兵を率い、長江を渡って烏林の小路を進み、草木が茂る場所を選んで待ち伏せてください。","趙雲殿は三千の兵を率い、長江を渡って烏林の小路を進み、草木が茂る場所を選んで待ち伏せてください。","趙雲",},
        [5467] = {9301,"諸葛亮","今夜の夜半過ぎ、曹操は必ずその路を落ちて来るでしょう。","今夜の夜半過ぎ、曹操は必ずその路を落ちて来るでしょう。","趙雲",},
        [5468] = {9301,"諸葛亮","前の軍勢はやり過ごし、半ば通ったところで火を放つのです。敵を残らず討ち取らずとも、半数は潰すように。","前の軍勢はやり過ごし、半ば通ったところで火を放つのです。敵を残らず討ち取らずとも、半数は潰すように。","趙雲",},
        [5469] = {9301,"趙雲","烏林には二筋の道があり、一方は南郡に、もう一方は荊州に続いています。敵はどちらの道から来るでしょう。","烏林には二筋の道があり、一方は南郡に、もう一方は荊州に続いています。敵はどちらの道から来るでしょう。","諸葛亮",},
        [5470] = {9301,"諸葛亮","南郡は情勢が悪いので、曹操は必ず荊州に来るはず。それから大軍をまとめて許昌に帰ろうとするでしょう。","南郡は情勢が悪いので、曹操は必ず荊州に来るはず。それから大軍をまとめて許昌に帰ろうとするでしょう。","趙雲",},
        [5471] = {9301,"趙雲","はっ、ではすぐ向かいます。","はっ、ではすぐ向かいます。","諸葛亮",},
        [5472] = {9301,"諸葛亮","張飛殿は三千騎を連れて長江を渡り、夷陵の路を切りふさぎ、葫蘆谷に兵を伏せてください。","張飛殿は三千騎を連れて長江を渡り、夷陵の路を切りふさぎ、葫蘆谷に兵を伏せてください。","趙雲",},
        [5473] = {9301,"諸葛亮","曹操は南夷陵の道は避けて、北夷陵をさして逃げてくるでしょう。","曹操は南夷陵の道は避けて、北夷陵をさして逃げてくるでしょう。","趙雲",},
        [5474] = {9301,"諸葛亮","明日、一雨過ぎたあと、必ずその辺りで火を起こし兵糧を使わせるでしょう。","明日、一雨過ぎたあと、必ずその辺りで火を起こし兵糧を使わせるでしょう。","趙雲",},
        [5475] = {9301,"諸葛亮","煙が立つのを見たら、直ちに山に火を放ってください。曹操を生け捕れなくても、大きな手柄になりましょう。","煙が立つのを見たら、直ちに山に火を放ってください。曹操を生け捕れなくても、大きな手柄になりましょう。","趙雲",},
        [5476] = {9301,"張飛","よっしゃ、行ってくらあ！","よっしゃ、行ってくらあ！","諸葛亮",},
        [5477] = {9301,"諸葛亮","糜竺、糜芳、劉封の三人はそれぞれ軍船に乗り、江岸をめぐって敗軍を捕らえ、兵器を鹵獲してください。","糜竺、糜芳、劉封の三人はそれぞれ軍船に乗り、江岸をめぐって敗軍を捕らえ、兵器を鹵獲してください。","張飛",},
        [5478] = {9301,"糜竺","はっ。","はっ。","諸葛亮",},
        [5479] = {9301,"諸葛亮","武昌は四方を見渡せる要害の地です。若君はすぐお帰りになって、部下を江岸に配置してください。","武昌は四方を見渡せる要害の地です。若君はすぐお帰りになって、部下を江岸に配置してください。","糜竺",},
        [5480] = {9301,"諸葛亮","曹操が敗れて逃げてくる敵があるでしょうから、それを捕らえるのです。但し、うかつに城内から離れないように。","曹操が敗れて逃げてくる敵があるでしょうから、それを捕らえるのです。但し、うかつに城内から離れないように。","糜竺",},
        [5481] = {9301,"劉琦","はい、すぐに参ります。","はい、すぐに参ります。","諸葛亮",},
        [5482] = {9301,"諸葛亮","わが君は樊口の高地に陣取り、今宵、周瑜が大きな戦功を立てるところを見物なさってください。","わが君は樊口の高地に陣取り、今宵、周瑜が大きな戦功を立てるところを見物なさってください。","劉琦",},
        [5483] = {9301,"劉備","わかった。","わかった。","諸葛亮",},
        [5484] = {9302,"関羽","わしは長年兄者に付き従って戦に出てたが、一度も後れを取ったことはない。","わしは長年兄者に付き従って戦に出てたが、一度も後れを取ったことはない。","諸葛亮",},
        [5485] = {9302,"関羽","それが今日大敵とまみえんという時に用いられないのは、いかなるお考えか。","それが今日大敵とまみえんという時に用いられないのは、いかなるお考えか。","諸葛亮",},
        [5486] = {9302,"諸葛亮","お気を悪くなさらず、関羽殿。本当なら重要な持ち場を引き受けていただきたいのですが、少々不都合があるので控えたのです。","お気を悪くなさらず、関羽殿。本当なら重要な持ち場を引き受けていただきたいのですが、少々不都合があるので控えたのです。","関羽",},
        [5487] = {9302,"関羽","不都合とは何か?教えていただきたい。","不都合とは何か?教えていただきたい。","諸葛亮",},
        [5488] = {9302,"諸葛亮","関羽殿は以前、曹操に手厚くもてなされ、その恩に報いようと考えておられる。今日曹操は敗れ、必ず華容道に逃げて来ます。","関羽殿は以前、曹操に手厚くもてなされ、その恩に報いようと考えておられる。今日曹操は敗れ、必ず華容道に逃げて来ます。","関羽",},
        [5489] = {9302,"諸葛亮","もしあなたがその場におられたら、許して通してしまうに違いない。それで控えたのです。","もしあなたがその場におられたら、許して通してしまうに違いない。それで控えたのです。","関羽",},
        [5490] = {9302,"関羽","軍師は考え過ぎだ！確かに曹操はわしをもてなしてくれたが、顔良や文醜を討ち取り、白馬で包囲網を破るなど、恩返しはしてある。","軍師は考え過ぎだ！確かに曹操はわしをもてなしてくれたが、顔良や文醜を討ち取り、白馬で包囲網を破るなど、恩返しはしてある。","諸葛亮",},
        [5491] = {9302,"関羽","今日出会っても、見逃しはしない！","今日出会っても、見逃しはしない！","諸葛亮",},
        [5492] = {9302,"諸葛亮","では、万一見逃したときは、どうされます。","では、万一見逃したときは、どうされます。","関羽",},
        [5493] = {9302,"関羽","軍律に照らして処断してもらいたい。","軍律に照らして処断してもらいたい。","諸葛亮",},
        [5494] = {9302,"諸葛亮","ならば誓文をしたためてください。","ならば誓文をしたためてください。","関羽",},
        [5495] = {9302,"関羽","もし曹操がその道に逃げて来なかったら、どうされる。","もし曹操がその道に逃げて来なかったら、どうされる。","諸葛亮",},
        [5496] = {9302,"諸葛亮","私も誓文を書きましょう。","私も誓文を書きましょう。","関羽",},
        [5497] = {9302,"諸葛亮","では関羽殿は、華容道に潜んで小高い処に柴を積み、火をつけて煙を上げ、曹操を誘き寄せてください。","では関羽殿は、華容道に潜んで小高い処に柴を積み、火をつけて煙を上げ、曹操を誘き寄せてください。","関羽",},
        [5498] = {9302,"関羽","曹操が煙を見たら、伏兵ありと見るのでは?","曹操が煙を見たら、伏兵ありと見るのでは?","諸葛亮",},
        [5499] = {9302,"諸葛亮","いえいえ、兵法の虚々実々の論です。用兵に長けた曹操だからこそ欺けます。","いえいえ、兵法の虚々実々の論です。用兵に長けた曹操だからこそ欺けます。","関羽",},
        [5500] = {9302,"諸葛亮","虚勢を張る偽計と考え、あえて煙の上がるほうにやって来るでしょう。関羽殿は情にほだされませんように。","虚勢を張る偽計と考え、あえて煙の上がるほうにやって来るでしょう。関羽殿は情にほだされませんように。","関羽",},
        [5501] = {9303,"劉備","関羽は義理人情に厚い男だ。実際に曹操が華容道へ落ちて来たら、結局見逃してやるのではないか。","関羽は義理人情に厚い男だ。実際に曹操が華容道へ落ちて来たら、結局見逃してやるのではないか。","諸葛亮",},
        [5502] = {9303,"諸葛亮","天文を観たところ、曹操の命運はまだ尽きていません。","天文を観たところ、曹操の命運はまだ尽きていません。","劉備",},
        [5503] = {9303,"諸葛亮","それでわざと、関羽殿に情けをかけさせる機会を作ったのです。それも美しい人情でしょう。","それでわざと、関羽殿に情けをかけさせる機会を作ったのです。それも美しい人情でしょう。","劉備",},
        [5504] = {9303,"劉備","なるほど、孔明の神算にはまったく恐れ入る。","なるほど、孔明の神算にはまったく恐れ入る。","諸葛亮",},
        [5505] = {9401,"曹操","この道はどこに続く?","この道はどこに続く?","許褚",},
        [5506] = {9401,"許褚","一方は南夷陵の本道で、一方は北夷陵の山路だぞう。","一方は南夷陵の本道で、一方は北夷陵の山路だぞう。","曹操",},
        [5507] = {9401,"曹操","では、どちらが南郡や江陵へ向かうのに近い?","では、どちらが南郡や江陵へ向かうのに近い?","許褚",},
        [5508] = {9401,"許褚","南夷陵から葫蘆谷を通るのが一番近いぞう。","南夷陵から葫蘆谷を通るのが一番近いぞう。","曹操",},
        [5509] = {9401,"曹操","ならば、南夷陵を進もう。","ならば、南夷陵を進もう。","許褚",},
        [5510] = {9402,"曹操","わっはははは……","わっはははは……","",},
        [5511] = {9402,"張遼","丞相、何をお笑いになります?","丞相、何をお笑いになります?","曹操",},
        [5512] = {9402,"曹操","諸葛亮も周瑜も智謀が足らぬと思ってな。仮にわしが用兵をしたら、ここに一手の軍勢を伏せておくわ。","諸葛亮も周瑜も智謀が足らぬと思ってな。仮にわしが用兵をしたら、ここに一手の軍勢を伏せておくわ。","張遼",},
        [5513] = {9402,"曹操","疲れた敵は命からがら逃げおおせても、大きな痛手をこうむるだろう。そこまで見通せていないとは、実に片腹痛い。","疲れた敵は命からがら逃げおおせても、大きな痛手をこうむるだろう。そこまで見通せていないとは、実に片腹痛い。","張遼",},
        [5514] = {9402,"張飛","ハッハー！孔明の智謀が足りねえだって?張飛様がとうからここで待ってたぜ！","ハッハー！孔明の智謀が足りねえだって?張飛様がとうからここで待ってたぜ！","曹操",},
        [5515] = {9402,"張遼","丞相、先にお逃げください。俺が援護します！","丞相、先にお逃げください。俺が援護します！","張飛",},
        [5516] = {9403,"張遼","前に二筋の分かれ道があります。丞相、どちらに参りますか。","前に二筋の分かれ道があります。丞相、どちらに参りますか。","曹操",},
        [5517] = {9403,"曹操","どちらのほうが近い?","どちらのほうが近い?","張遼",},
        [5518] = {9403,"張遼","本道はなだらかですが、五十里ほど遠回りになります。山路は華容道を通り、近道です。しかし路は狭く険しく、越えにくいです。","本道はなだらかですが、五十里ほど遠回りになります。山路は華容道を通り、近道です。しかし路は狭く険しく、越えにくいです。","曹操",},
        [5519] = {9403,"張遼","また山路のほうは処どころ煙が立ちのぼり、本道にはそのような様子はありません。","また山路のほうは処どころ煙が立ちのぼり、本道にはそのような様子はありません。","曹操",},
        [5520] = {9403,"曹操","では、華容道の山路を進むぞ。","では、華容道の山路を進むぞ。","張遼",},
        [5521] = {9403,"張遼","煙が上がっているのですよ。敵兵がいると思われるのに、なぜそちらを通るのです?","煙が上がっているのですよ。敵兵がいると思われるのに、なぜそちらを通るのです?","曹操",},
        [5522] = {9403,"曹操","兵法の書に「虚なるときは実、実なるときは虚」とあるのを知らぬか。","兵法の書に「虚なるときは実、実なるときは虚」とあるのを知らぬか。","張遼",},
        [5523] = {9403,"曹操","諸葛亮は謀略家ゆえ、わざと山中に煙を上げさせ、わが軍を山路に来させないようにして、本道沿いに伏兵を置いて待ち伏せているに違いない。","諸葛亮は謀略家ゆえ、わざと山中に煙を上げさせ、わが軍を山路に来させないようにして、本道沿いに伏兵を置いて待ち伏せているに違いない。","張遼",},
        [5524] = {9403,"曹操","その手には乗るものか！","その手には乗るものか！","張遼",},
        [5525] = {9403,"張遼","丞相のご明察、恐れ入ります。","丞相のご明察、恐れ入ります。","曹操",},
        [5526] = {9404,"張遼","いかん！道の両側から部隊が現れた。青龍偃月刀を引っ提げ、赤兔馬にまたがった真ん中の大将は、関羽か！行く手をふさがれた。","いかん！道の両側から部隊が現れた。青龍偃月刀を引っ提げ、赤兔馬にまたがった真ん中の大将は、関羽か！行く手をふさがれた。","曹操",},
        [5527] = {9404,"曹操","おのれ！かくなる上は、死力を尽くして決戦あるのみ！","おのれ！かくなる上は、死力を尽くして決戦あるのみ！","張遼",},
        [5528] = {9404,"程昱","お待ちくだされ、ワシの知るところでは関羽は弱気を助け強きを挫く義侠の士ですじゃ。","お待ちくだされ、ワシの知るところでは関羽は弱気を助け強きを挫く義侠の士ですじゃ。","曹操",},
        [5529] = {9404,"程昱","恩を仇で返すことはなく、信義を重んじることで名高い。","恩を仇で返すことはなく、信義を重んじることで名高い。","曹操",},
        [5530] = {9404,"程昱","丞相はかつて大恩を施されましたゆえ、今ここで御自ら話をなされば、この難を逃れられるやもしれませぬぞ。","丞相はかつて大恩を施されましたゆえ、今ここで御自ら話をなされば、この難を逃れられるやもしれませぬぞ。","曹操",},
        [5531] = {9405,"曹操","これは将軍、あの時別れて以来だな。無事であったか?","これは将軍、あの時別れて以来だな。無事であったか?","関羽",},
        [5532] = {9405,"関羽","軍師の命令を受け、ここで丞相を待ち受けていた。","軍師の命令を受け、ここで丞相を待ち受けていた。","曹操",},
        [5533] = {9405,"曹操","そうか。見ての通り、わしは戦に敗れ、もはや逃げ路もない。昔の恩を思って情けをかけてはくれまいか。","そうか。見ての通り、わしは戦に敗れ、もはや逃げ路もない。昔の恩を思って情けをかけてはくれまいか。","関羽",},
        [5534] = {9405,"関羽","命令に背くわけにはいかん。","命令に背くわけにはいかん。","曹操",},
        [5535] = {9405,"関羽","いかにも昔、丞相のご恩を厚くこうむったが、顔良と文醜を斬り、白馬の危難を救ったことで、借りは返したと思っている。","いかにも昔、丞相のご恩を厚くこうむったが、顔良と文醜を斬り、白馬の危難を救ったことで、借りは返したと思っている。","曹操",},
        [5536] = {9405,"曹操","だが、五関でわが六将を斬ったことはどうなる?大丈夫は信義をもって重しとなす。将軍は『春秋』の書にも明るいと聞く。","だが、五関でわが六将を斬ったことはどうなる?大丈夫は信義をもって重しとなす。将軍は『春秋』の書にも明るいと聞く。","関羽",},
        [5537] = {9405,"曹操","弓の名手、庾公が大師匠の子濯を追った時、鏃を外した矢四本を放っただけで帰った故事は知っておろう。","弓の名手、庾公が大師匠の子濯を追った時、鏃を外した矢四本を放っただけで帰った故事は知っておろう。","関羽",},
        [5538] = {9405,"関羽","……皆の者、しばし道をあけよ。","……皆の者、しばし道をあけよ。","曹操",},
        [5539] = {9405,"曹操","心より感謝する。","心より感謝する。","関羽",},
        [5540] = {9501,"曹操","南方から山鶏が献上された。山鶏は美しい羽が好きで、水に映る自分の姿を見ては舞うという。","南方から山鶏が献上された。山鶏は美しい羽が好きで、水に映る自分の姿を見ては舞うという。","",},
        [5541] = {9501,"曹操","これが舞って鳴くところが見てみたいものよ。","これが舞って鳴くところが見てみたいものよ。","",},
        [5542] = {9501,"曹沖","山鶏を銅鏡の前に置いてみたらどうかな。","山鶏を銅鏡の前に置いてみたらどうかな。","曹操",},
        [5543] = {9501,"曹操","うむ、いい考えだ。","うむ、いい考えだ。","曹沖",},
        [5544] = {9502,"曹操","大きな銅鏡を山鶏の前に置くと、果たして舞い始めた。","大きな銅鏡を山鶏の前に置くと、果たして舞い始めた。","曹沖",},
        [5545] = {9502,"曹操","しかしいつまでも舞うのをやめず、とうとう死んでしまった。","しかしいつまでも舞うのをやめず、とうとう死んでしまった。","曹沖",},
        [5546] = {9502,"曹沖","自分に酔うのもほどほどにしなさい、って教訓だね。","自分に酔うのもほどほどにしなさい、って教訓だね。","曹操",},
        [5547] = {9503,"環夫人","曹沖が突然重い病に伏してしまいました。早く見てやってくださいまし。","曹沖が突然重い病に伏してしまいました。早く見てやってくださいまし。","曹操",},
        [5548] = {9503,"曹操","なに?！よし、すぐ許都に戻るぞ。","なに?！よし、すぐ許都に戻るぞ。","",},
        [5549] = {9504,"曹操","曹沖は何の病なのだ?医者は何と?","曹沖は何の病なのだ?医者は何と?","",},
        [5550] = {9504,"環夫人","お医者様には診ていただきましたが、何の病かはわかりませぬ。","お医者様には診ていただきましたが、何の病かはわかりませぬ。","曹操",},
        [5551] = {9504,"曹操","天下の名医を集めて曹沖の病を治させよう。","天下の名医を集めて曹沖の病を治させよう。","",},
        [5552] = {9504,"曹操","大至急、名医を呼べ！","大至急、名医を呼べ！","",},
        [5553] = {9504,"曹仁","はっ。","はっ。","曹操",},
        [5554] = {9505,"曹丕","曹沖の急逝は、まことに不幸なことでした。お悔やみ申し上げます。","曹沖の急逝は、まことに不幸なことでした。お悔やみ申し上げます。","曹操",},
        [5555] = {9505,"曹操","もう十年も前……わしに従って張繍を攻めた曹昂は、わしを逃がそうとして死んだ。","もう十年も前……わしに従って張繍を攻めた曹昂は、わしを逃がそうとして死んだ。","曹丕",},
        [5556] = {9505,"曹操","今度は聡明な曹沖が、わずか十三歳で逝ってしまった……悲しくてならん！","今度は聡明な曹沖が、わずか十三歳で逝ってしまった……悲しくてならん！","曹丕",},
        [5557] = {9505,"曹操","わしにとっては不幸だが、世継ぎのお前にとっては幸いだったな。","わしにとっては不幸だが、世継ぎのお前にとっては幸いだったな。","曹丕",},
        [5558] = {9601,"周瑜","劉備殿がこちらへ兵を移されたのは、南郡を攻め取ろうとのお考えか。","劉備殿がこちらへ兵を移されたのは、南郡を攻め取ろうとのお考えか。","劉備",},
        [5559] = {9601,"劉備","なに、都督殿が南郡を攻め落とすと聞き及び、手助けに来たのです。もし都督殿がお取りにならないなら、私が攻め取りましょう。","なに、都督殿が南郡を攻め落とすと聞き及び、手助けに来たのです。もし都督殿がお取りにならないなら、私が攻め取りましょう。","周瑜",},
        [5560] = {9601,"周瑜","いやいや、わが呉は長らく漢江の九郡を併呑せんと望んでいました。","いやいや、わが呉は長らく漢江の九郡を併呑せんと望んでいました。","劉備",},
        [5561] = {9601,"周瑜","いま南郡は呉の掌の上にあるも同然なのに、取らぬわけがありません。","いま南郡は呉の掌の上にあるも同然なのに、取らぬわけがありません。","劉備",},
        [5562] = {9601,"劉備","しかし戦の勝敗はわからないものです。曹操は北に帰りましたが、曹仁を残して南郡その他を守らせているのは、計略があるのでしょう。","しかし戦の勝敗はわからないものです。曹操は北に帰りましたが、曹仁を残して南郡その他を守らせているのは、計略があるのでしょう。","周瑜",},
        [5563] = {9601,"劉備","奴は万夫不当のつわもの。たやすくは落とせないでしょう。","奴は万夫不当のつわもの。たやすくは落とせないでしょう。","周瑜",},
        [5564] = {9601,"周瑜","ふっ……では私の手で取れなかった時は、劉備殿が好きにお取りください。","ふっ……では私の手で取れなかった時は、劉備殿が好きにお取りください。","劉備",},
        [5565] = {9601,"劉備","いいでしょう。魯粛殿と孔明が証人ですよ。今のお言葉、後悔なさらぬよう。","いいでしょう。魯粛殿と孔明が証人ですよ。今のお言葉、後悔なさらぬよう。","周瑜",},
        [5566] = {9601,"周瑜","大丈夫に二言はない。後悔などしません。","大丈夫に二言はない。後悔などしません。","劉備",},
        [5567] = {9601,"劉備","さすが都督殿、お言葉はもっともです。","さすが都督殿、お言葉はもっともです。","周瑜",},
        [5568] = {9601,"劉備","ではまず呉軍で存分に攻めてください。万一落城しなかったら、私が攻め取りますから。","ではまず呉軍で存分に攻めてください。万一落城しなかったら、私が攻め取りますから。","周瑜",},
        [5569] = {9602,"劉備","先ほどは孔明に言われたとおりに受け答えしたが、よくよく考えれば、どうも不安だ。","先ほどは孔明に言われたとおりに受け答えしたが、よくよく考えれば、どうも不安だ。","諸葛亮",},
        [5570] = {9602,"劉備","今の私には寄る辺も拠点もない。南郡を攻め落せば、しばらく身を落ち着けられるが――","今の私には寄る辺も拠点もない。南郡を攻め落せば、しばらく身を落ち着けられるが――","諸葛亮",},
        [5571] = {9602,"劉備","先に周瑜に取られてしまったら、領地は呉に属してしまう。","先に周瑜に取られてしまったら、領地は呉に属してしまう。","諸葛亮",},
        [5572] = {9602,"諸葛亮","おやおや、先ごろ私が荊州をお取りなさいと、あれほど申しましたのにお聞き入れにならないで、今ごろ欲しいと仰いますか。","おやおや、先ごろ私が荊州をお取りなさいと、あれほど申しましたのにお聞き入れにならないで、今ごろ欲しいと仰いますか。","劉備",},
        [5573] = {9602,"劉備","前は劉表殿の領地だったから、取るに忍びなかったのだ。しかし今は曹操の物だ。攻め取るに遠慮は要らない。","前は劉表殿の領地だったから、取るに忍びなかったのだ。しかし今は曹操の物だ。攻め取るに遠慮は要らない。","諸葛亮",},
        [5574] = {9602,"諸葛亮","ご心配には及びません。周瑜には好きなだけ戦をさせておいて、やがてわが君が南郡城に入れるようにいたします。","ご心配には及びません。周瑜には好きなだけ戦をさせておいて、やがてわが君が南郡城に入れるようにいたします。","劉備",},
        [5575] = {9602,"劉備","何かいい手立てでも?","何かいい手立てでも?","諸葛亮",},
        [5576] = {9602,"諸葛亮","ええ、ちょっとお耳を拝借。","ええ、ちょっとお耳を拝借。","劉備",},
        [5577] = {9603,"魯粛","都督はどうして劉備殿に南郡を取ってもよいなどと言われたのです?","都督はどうして劉備殿に南郡を取ってもよいなどと言われたのです?","周瑜",},
        [5578] = {9603,"周瑜","ほんの社交辞令だよ。南郡など瞬く間に取ってみせる。","ほんの社交辞令だよ。南郡など瞬く間に取ってみせる。","魯粛",},
        [5579] = {9603,"周瑜","南郡攻略の先駆けになりたい者はいるか?","南郡攻略の先駆けになりたい者はいるか?","魯粛",},
        [5580] = {9603,"蒋欽","ぜひ私に！","ぜひ私に！","周瑜",},
        [5581] = {9603,"周瑜","では蒋欽を先陣に、徐盛と丁奉を副将として、五千の精鋭兵を率い、先に江を渡るのだ。私が後詰めとなる。","では蒋欽を先陣に、徐盛と丁奉を副将として、五千の精鋭兵を率い、先に江を渡るのだ。私が後詰めとなる。","蒋欽",},
        [5582] = {9604,"牛金","呉軍はもう漢江を渡って来ています。","呉軍はもう漢江を渡って来ています。","曹仁",},
        [5583] = {9604,"曹仁","城を堅く守って取りあわぬのが上策だ。","城を堅く守って取りあわぬのが上策だ。","牛金",},
        [5584] = {9604,"牛金","敵兵が城下に迫っているのに出撃しないのは臆病です。","敵兵が城下に迫っているのに出撃しないのは臆病です。","曹仁",},
        [5585] = {9604,"牛金","ましてわが軍は敗れたばかり、再び士気を奮い起こさねば。五百の精兵をたまわれば、決死の覚悟で戦いましょう。","ましてわが軍は敗れたばかり、再び士気を奮い起こさねば。五百の精兵をたまわれば、決死の覚悟で戦いましょう。","曹仁",},
        [5586] = {9604,"曹仁","よし、では牛金は五百の兵を率いて打って出よ。","よし、では牛金は五百の兵を率いて打って出よ。","牛金",},
        [5587] = {9605,"周瑜","私みずから兵を率いて、曹仁と決戦する！","私みずから兵を率いて、曹仁と決戦する！","甘寧",},
        [5588] = {9605,"甘寧","都督は軽々しく出ないでくれ！いま曹仁は曹洪に夷陵を守らせて、掎角の計をとって連携してるんだ。","都督は軽々しく出ないでくれ！いま曹仁は曹洪に夷陵を守らせて、掎角の計をとって連携してるんだ。","周瑜",},
        [5589] = {9605,"甘寧","オレが三千の精兵を借りて、夷陵を攻め破るよ。そのあと南郡を取ってくれよ。","オレが三千の精兵を借りて、夷陵を攻め破るよ。そのあと南郡を取ってくれよ。","周瑜",},
        [5590] = {9605,"周瑜","承知した。ならば甘寧は三千の兵を率いて夷陵を攻めるのだ。","承知した。ならば甘寧は三千の兵を率いて夷陵を攻めるのだ。","甘寧",},
        [5591] = {9606,"曹仁","間者の報告では、呉の甘寧が三千の兵を率いて夷陵攻めに向かったそうだ。","間者の報告では、呉の甘寧が三千の兵を率いて夷陵攻めに向かったそうだ。","牛金",},
        [5592] = {9606,"曹仁","夷陵を失えば、南郡も守れない。急いで救いに行かねば。","夷陵を失えば、南郡も守れない。急いで救いに行かねば。","牛金",},
        [5593] = {9606,"曹仁","大至急使者をやって曹洪に知らせ、城を出て敵を誘き寄せてもらおう。","大至急使者をやって曹洪に知らせ、城を出て敵を誘き寄せてもらおう。","牛金",},
        [5594] = {9606,"曹仁","お前と曹純は密かに兵を率いて曹洪の救援に行け。","お前と曹純は密かに兵を率いて曹洪の救援に行け。","牛金",},
        [5595] = {9606,"牛金","はっ。","はっ。","曹仁",},
        [5596] = {9607,"甘寧","甘寧ここにあり！さっさと夷陵を棄てて降参しろ！","甘寧ここにあり！さっさと夷陵を棄てて降参しろ！","曹洪",},
        [5597] = {9607,"曹洪","この曹洪が貴様ごとき錦帆賊を怖れるものか！相手してやろう！","この曹洪が貴様ごとき錦帆賊を怖れるものか！相手してやろう！","甘寧",},
        [5598] = {9608,"甘寧","ハッ！曹洪がこんなに弱かったとはな。二十合打ち合っただけでもう敗走かよ！","ハッ！曹洪がこんなに弱かったとはな。二十合打ち合っただけでもう敗走かよ！","",},
        [5599] = {9608,"甘寧","夷陵は、オレが取った！","夷陵は、オレが取った！","",},
        [5600] = {9609,"曹洪","馬鹿な錦帆賊めが、この夷陵がそう簡単に奪えると思うか！","馬鹿な錦帆賊めが、この夷陵がそう簡単に奪えると思うか！","甘寧",},
        [5601] = {9609,"牛金","城は完全に包囲されているぞ！","城は完全に包囲されているぞ！","甘寧",},
        [5602] = {9609,"曹仁","おとなしく降参しろ！","おとなしく降参しろ！","甘寧",},
        [5603] = {9609,"甘寧","クソッ、罠だったか！","クソッ、罠だったか！","曹仁",},
        [5604] = {9701,"周瑜","甘寧が敵の計略にかかり、夷陵城内に封じ込められた。","甘寧が敵の計略にかかり、夷陵城内に封じ込められた。","",},
        [5605] = {9701,"程普","急いで救出の兵を出しましょう。","急いで救出の兵を出しましょう。","周瑜",},
        [5606] = {9701,"周瑜","ここは要害の地だ。兵力を分けたあとへ、曹仁が襲撃してきたらどうする?","ここは要害の地だ。兵力を分けたあとへ、曹仁が襲撃してきたらどうする?","程普",},
        [5607] = {9701,"呂蒙","甘寧はわが呉有数の大将ですぞ。救わなくてどうします。","甘寧はわが呉有数の大将ですぞ。救わなくてどうします。","周瑜",},
        [5608] = {9701,"周瑜","ならば私みずから救出に向かおう。だが誰がここに残って、代わりを務めていただきたい。","ならば私みずから救出に向かおう。だが誰がここに残って、代わりを務めていただきたい。","呂蒙",},
        [5609] = {9701,"呂蒙","凌統を守りに残しましょう。私が先駆けとなり、都督に殿を務めていただければ、十日を待たず目的は達せられましょう。","凌統を守りに残しましょう。私が先駆けとなり、都督に殿を務めていただければ、十日を待たず目的は達せられましょう。","周瑜",},
        [5610] = {9701,"周瑜","凌統、しばらく私の役目を任るが、こなせるか?","凌統、しばらく私の役目を任るが、こなせるか?","呂蒙",},
        [5611] = {9701,"凌統","十日とのお約束ならば、お引き受けいたしましょう。それ以上はもたないかもしれません。","十日とのお約束ならば、お引き受けいたしましょう。それ以上はもたないかもしれません。","周瑜",},
        [5612] = {9701,"周瑜","凌統に兵一万あまりを残し、その他の主力を即日夷陵へ動かす。","凌統に兵一万あまりを残し、その他の主力を即日夷陵へ動かす。","凌統",},
        [5613] = {9701,"呂蒙","夷陵の南に、南郡への近道があります。五百ほどの一隊を差し向け、周りの木を切り倒して道をふさがせましょう。","夷陵の南に、南郡への近道があります。五百ほどの一隊を差し向け、周りの木を切り倒して道をふさがせましょう。","周瑜",},
        [5614] = {9701,"呂蒙","敵軍は敗れたら、必ずこの道を通ります。道がふさがっていれば、馬を捨てて逃げるでしょうから、この馬を分捕りましょう。","敵軍は敗れたら、必ずこの道を通ります。道がふさがっていれば、馬を捨てて逃げるでしょうから、この馬を分捕りましょう。","周瑜",},
        [5615] = {9701,"周瑜","いいだろう。","いいだろう。","呂蒙",},
        [5616] = {9702,"周瑜","まもなく夷陵に到着する。誰か囲みを破って突入し、甘寧を救い出す者は?","まもなく夷陵に到着する。誰か囲みを破って突入し、甘寧を救い出す者は?","",},
        [5617] = {9702,"周泰","俺に行かせてください！","俺に行かせてください！","周瑜",},
        [5618] = {9702,"周瑜","周泰か、いいだろう。","周泰か、いいだろう。","周泰",},
        [5619] = {9703,"周泰","もう大丈夫だ、都督みずから救出に来られたぞ。","もう大丈夫だ、都督みずから救出に来られたぞ。","甘寧",},
        [5620] = {9703,"甘寧","そうか、ではすぐ兵たちに武装と腹ごしらえをするよう命じる。援軍が到着しだい出撃できるようにな。","そうか、ではすぐ兵たちに武装と腹ごしらえをするよう命じる。援軍が到着しだい出撃できるようにな。","周泰",},
        [5621] = {9704,"曹洪","夷陵を奪われた今、丞相が残していかれた秘策の封を切って打開策を見るしかないだろう。","夷陵を奪われた今、丞相が残していかれた秘策の封を切って打開策を見るしかないだろう。","曹仁",},
        [5622] = {9704,"曹仁","ああ、俺もそう思っていた。","ああ、俺もそう思っていた。","曹洪",},
        [5623] = {9704,"曹仁","……ふむふむ……わっはっは！","……ふむふむ……わっはっは！","曹洪",},
        [5624] = {9704,"曹仁","丞相の命令はこうだ。","丞相の命令はこうだ。","曹洪",},
        [5625] = {9704,"曹仁","未明のうちに食事をすませ、夜明けとともに兵も馬もみな城を棄てて出よ。城壁には旗や幟をびっしり立て、虚勢を張れ。","未明のうちに食事をすませ、夜明けとともに兵も馬もみな城を棄てて出よ。城壁には旗や幟をびっしり立て、虚勢を張れ。","曹洪",},
        [5626] = {9704,"曹洪","なるほど、良い計略だ。","なるほど、良い計略だ。","曹仁",},
        [5627] = {9705,"曹仁","丞相の妙計が見事的中した！周瑜甘寧も罠にかかったわ。","丞相の妙計が見事的中した！周瑜甘寧も罠にかかったわ。","",},
        [5628] = {9705,"曹仁","呉の兵士が先争って空っぽの城内に入り、落とし穴にはまった。左右から矢が一斉に降りそそぎ、おびただしい死傷者が出ている。","呉の兵士が先争って空っぽの城内に入り、落とし穴にはまった。左右から矢が一斉に降りそそぎ、おびただしい死傷者が出ている。","",},
        [5629] = {9705,"曹仁","急いで引き返そうとした周瑜も、矢に当たって、真っ逆さまに落馬した。","急いで引き返そうとした周瑜も、矢に当たって、真っ逆さまに落馬した。","",},
        [5630] = {9706,"周瑜","太鼓や鬨の声がするが?","太鼓や鬨の声がするが?","",},
        [5631] = {9706,"甘寧","軍中で兵士の調練をしているんだ。","軍中で兵士の調練をしているんだ。","周瑜",},
        [5632] = {9706,"周瑜","嘘もいい加減になさい。","嘘もいい加減になさい。","甘寧",},
        [5633] = {9706,"周瑜","曹軍がたびたび陣の前まで来て罵っていくのは知っています。程普殿は総大将でありながら、なぜ取りあわないのです?","曹軍がたびたび陣の前まで来て罵っていくのは知っています。程普殿は総大将でありながら、なぜ取りあわないのです?","甘寧",},
        [5634] = {9706,"程普","周瑜殿の傷が深く、医者から刺激せぬよう言われておるのだ。だから曹軍が挑発してきても、お知らせしなかった。","周瑜殿の傷が深く、医者から刺激せぬよう言われておるのだ。だから曹軍が挑発してきても、お知らせしなかった。","周瑜",},
        [5635] = {9706,"周瑜","戦わぬとは、どういうお考えか。","戦わぬとは、どういうお考えか。","程普",},
        [5636] = {9706,"程普","諸将の意見では、ひとまず兵を収めて呉に帰り、周瑜殿の傷の快復を待ってから出直そうと。","諸将の意見では、ひとまず兵を収めて呉に帰り、周瑜殿の傷の快復を待ってから出直そうと。","周瑜",},
        [5637] = {9706,"周瑜","大丈夫たる者、主君の禄を食んでいるからには、戦場で死に、屍を馬の革に包んで帰ってこそ本望です。","大丈夫たる者、主君の禄を食んでいるからには、戦場で死に、屍を馬の革に包んで帰ってこそ本望です。","程普",},
        [5638] = {9706,"周瑜","私一人のために、国の大事を誤らないでいただきたい！","私一人のために、国の大事を誤らないでいただきたい！","程普",},
        [5639] = {9707,"曹仁","周瑜はおおかたのたれ死んで、もう我らの軍を見ることもなかろう。","周瑜はおおかたのたれ死んで、もう我らの軍を見ることもなかろう。","周瑜",},
        [5640] = {9707,"周瑜","ほざけ曹仁！周瑜はここだ、とくと見よ！","ほざけ曹仁！周瑜はここだ、とくと見よ！","曹仁",},
        [5641] = {9707,"曹仁","ほう、やるか?！","ほう、やるか?！","周瑜",},
        [5642] = {9707,"周瑜","潘璋が相手になろう。","潘璋が相手になろう。","曹仁",},
        [5643] = {9708,"周瑜","ぐはっ！(周瑜は急に叫び声をあげ、口から血を吐いて落馬した)","ぐはっ！(周瑜は急に叫び声をあげ、口から血を吐いて落馬した)","",},
        [5644] = {9708,"程普","都督、お加減はいかがかな。","都督、お加減はいかがかな。","周瑜",},
        [5645] = {9708,"周瑜","これは計略だ。","これは計略だ。","程普",},
        [5646] = {9708,"程普","なに、計略とな?","なに、計略とな?","周瑜",},
        [5647] = {9708,"周瑜","私の傷は、さしたる痛みはない。曹軍によほど重い怪我だと思わせるために、わざとああしたのだ。敵も欺かれただろう。","私の傷は、さしたる痛みはない。曹軍によほど重い怪我だと思わせるために、わざとああしたのだ。敵も欺かれただろう。","程普",},
        [5648] = {9708,"周瑜","腹心の兵士を偽って投降させ、私が死んだと言わせなさい。そうすれば曹仁は必ず夜襲をかけて来るでしょう。","腹心の兵士を偽って投降させ、私が死んだと言わせなさい。そうすれば曹仁は必ず夜襲をかけて来るでしょう。","程普",},
        [5649] = {9708,"周瑜","四方に伏兵を置いておけば、曹仁を生け捕りにできます。","四方に伏兵を置いておけば、曹仁を生け捕りにできます。","程普",},
        [5650] = {9708,"程普","なるほど、それは妙計。","なるほど、それは妙計。","周瑜",},
        [5651] = {9709,"曹仁","周瑜は矢傷が破れ、帰陣したのち死亡したという。諸将も喪に服しているそうだ。","周瑜は矢傷が破れ、帰陣したのち死亡したという。諸将も喪に服しているそうだ。","",},
        [5652] = {9709,"曹仁","今夜のうちにも夜襲をかけ、周瑜の屍を奪い、その首を取って許都へ送り届ける。","今夜のうちにも夜襲をかけ、周瑜の屍を奪い、その首を取って許都へ送り届ける。","",},
        [5653] = {9709,"曹仁","牛金を先鋒とし、俺は中軍、曹洪、曹純を後詰めとする。","牛金を先鋒とし、俺は中軍、曹洪、曹純を後詰めとする。","",},
        [5654] = {9709,"曹仁","陳矯だけを残し、少数の兵で城を守らせるが、それ以外は全軍を挙げて出陣する。","陳矯だけを残し、少数の兵で城を守らせるが、それ以外は全軍を挙げて出陣する。","",},
        [5655] = {9710,"曹洪","陣中には人っ子一人いない。ただ旗や槍を立ててあるだけだ。","陣中には人っ子一人いない。ただ旗や槍を立ててあるだけだ。","曹仁",},
        [5656] = {9710,"曹仁","いかん、計略にかかった！急いで軍を退き、襄陽に逃げよう。","いかん、計略にかかった！急いで軍を退き、襄陽に逃げよう。","曹洪",},
        [5657] = {9801,"程普","南郡の城壁には、旗や幟が立ち並んでいる。常山の趙雲に虚をつかれ、城を奪われた。","南郡の城壁には、旗や幟が立ち並んでいる。常山の趙雲に虚をつかれ、城を奪われた。","周瑜",},
        [5658] = {9801,"周瑜","なんだと！すぐに兵を整えて城を攻めるのだ。","なんだと！すぐに兵を整えて城を攻めるのだ。","程普",},
        [5659] = {9801,"程普","今は攻めようがない！城壁の上から矢を浴びせられておる。","今は攻めようがない！城壁の上から矢を浴びせられておる。","周瑜",},
        [5660] = {9801,"周瑜","甘寧、数千の騎兵をもって荊州を奪うのだ！","甘寧、数千の騎兵をもって荊州を奪うのだ！","程普",},
        [5661] = {9801,"周瑜","凌統も数千の騎兵をもって襄陽を奪い取るのだ。それから南郡を攻略しても遅くはない。","凌統も数千の騎兵をもって襄陽を奪い取るのだ。それから南郡を攻略しても遅くはない。","程普",},
        [5662] = {9802,"程普","早馬の報告によれば、諸葛亮は南郡の城を占領すると、すぐに曹仁の兵符を使って、荊州の守備軍を救援に呼び出し――","早馬の報告によれば、諸葛亮は南郡の城を占領すると、すぐに曹仁の兵符を使って、荊州の守備軍を救援に呼び出し――","周瑜",},
        [5663] = {9802,"程普","手薄になった荊州を張飛が奪い取ったとのこと。","手薄になった荊州を張飛が奪い取ったとのこと。","周瑜",},
        [5664] = {9802,"周瑜","なんと！","なんと！","程普",},
        [5665] = {9803,"程普","また別の報告では、諸葛亮は襄陽を守っていた夏侯惇のもとへも人をやり、曹仁の兵符を使って救援を求めた。","また別の報告では、諸葛亮は襄陽を守っていた夏侯惇のもとへも人をやり、曹仁の兵符を使って救援を求めた。","周瑜",},
        [5666] = {9803,"程普","夏侯惇が急ぎ出兵したあと、関羽が襄陽を乗っ取ったそうだ。","夏侯惇が急ぎ出兵したあと、関羽が襄陽を乗っ取ったそうだ。","周瑜",},
        [5667] = {9803,"程普","城二つが、やすやすと劉備のものになったということだな。","城二つが、やすやすと劉備のものになったということだな。","周瑜",},
        [5668] = {9803,"周瑜","諸葛亮はどうやって曹仁の兵符を手に入れたのだ?","諸葛亮はどうやって曹仁の兵符を手に入れたのだ?","程普",},
        [5669] = {9803,"程普","荊州で兵符を預かっていた陳矯を捕らえたに違いない。","荊州で兵符を預かっていた陳矯を捕らえたに違いない。","周瑜",},
        [5670] = {9803,"周瑜","くっ、そういうこと……ぐほっ！(傷口が破れ、周瑜が叫び声をあげる)","くっ、そういうこと……ぐほっ！(傷口が破れ、周瑜が叫び声をあげる)","程普",},
        [5671] = {9803,"程普","誰か来てくれ、都督を助けて帰陣する！","誰か来てくれ、都督を助けて帰陣する！","周瑜",},
        [5672] = {9804,"周瑜","孔明め……、奴を殺さぬ限り、この恨みは晴らせぬ！","孔明め……、奴を殺さぬ限り、この恨みは晴らせぬ！","魯粛",},
        [5673] = {9804,"周瑜","兵を挙げて劉備、諸葛亮と雌雄を決し、城を奪い返したい。手伝ってくれるな。","兵を挙げて劉備、諸葛亮と雌雄を決し、城を奪い返したい。手伝ってくれるな。","魯粛",},
        [5674] = {9804,"魯粛","なりません。目下は曹操と対峙している最中で、勝敗の行方は見えず、孫権様がいま攻めておられる合肥もまだ落ちていません。","なりません。目下は曹操と対峙している最中で、勝敗の行方は見えず、孫権様がいま攻めておられる合肥もまだ落ちていません。","周瑜",},
        [5675] = {9804,"魯粛","そんなおりに同士討ちを始めれば、曹操軍がつけこんできて、危うくなります。","そんなおりに同士討ちを始めれば、曹操軍がつけこんできて、危うくなります。","周瑜",},
        [5676] = {9804,"魯粛","まして劉備は曹操とはなじみがあります。あまりに追いつめると、城を曹操に献じて、共に呉を攻めてこないとも限りません。","まして劉備は曹操とはなじみがあります。あまりに追いつめると、城を曹操に献じて、共に呉を攻めてこないとも限りません。","周瑜",},
        [5677] = {9804,"周瑜","なにを弱気なことを……","なにを弱気なことを……","魯粛",},
        [5678] = {9804,"周瑜","我らが計画を立て、兵馬を損ない、財貨と兵糧を費やして得た戦果を、奴が横からかっさらってゆくのですよ。口惜しくはないのですか！","我らが計画を立て、兵馬を損ない、財貨と兵糧を費やして得た戦果を、奴が横からかっさらってゆくのですよ。口惜しくはないのですか！","魯粛",},
        [5679] = {9804,"魯粛","今はこらえてください。私が劉備に直に会って、道理を説いてみます。","今はこらえてください。私が劉備に直に会って、道理を説いてみます。","周瑜",},
        [5680] = {9804,"魯粛","どうしても聞き入れなければ、そのとき兵を出しても遅くないでしょう。","どうしても聞き入れなければ、そのとき兵を出しても遅くないでしょう。","周瑜",},
        [5681] = {9901,"魯粛","わが主呉侯ならびに周瑜都督より、劉備殿に申し入れたい。","わが主呉侯ならびに周瑜都督より、劉備殿に申し入れたい。","諸葛亮",},
        [5682] = {9901,"魯粛","先ごろ曹操は百万の大軍をもって江南を攻めて来ました。本心では一番に劉備殿を捕えたかったでしょう。","先ごろ曹操は百万の大軍をもって江南を攻めて来ました。本心では一番に劉備殿を捕えたかったでしょう。","諸葛亮",},
        [5683] = {9901,"魯粛","幸い呉が曹操軍を撃退して、劉備殿をお救いしました。よって荊州の九郡はすべて、呉に属するのが当然と思われます。","幸い呉が曹操軍を撃退して、劉備殿をお救いしました。よって荊州の九郡はすべて、呉に属するのが当然と思われます。","諸葛亮",},
        [5684] = {9901,"魯粛","ところが今、劉備殿が詭計を用いて荊州と襄陽を奪ってしまわれた。","ところが今、劉備殿が詭計を用いて荊州と襄陽を奪ってしまわれた。","諸葛亮",},
        [5685] = {9901,"魯粛","わが呉が財物兵糧を費やしたのに、劉備殿がやすやすと利益を得るのは、納得がいきません。","わが呉が財物兵糧を費やしたのに、劉備殿がやすやすと利益を得るのは、納得がいきません。","諸葛亮",},
        [5686] = {9901,"諸葛亮","これは意外なお言葉ですねえ。魯粛殿は道理のわかる方と思っていたのですが。","これは意外なお言葉ですねえ。魯粛殿は道理のわかる方と思っていたのですが。","魯粛",},
        [5687] = {9901,"諸葛亮","物は元の持ち主に返さねばならぬと申します。そもそも荊・襄九郡は、呉の領地ではありません。劉表殿の本領です。","物は元の持ち主に返さねばならぬと申します。そもそも荊・襄九郡は、呉の領地ではありません。劉表殿の本領です。","魯粛",},
        [5688] = {9901,"諸葛亮","そしてわが君は劉表殿の弟分でした。","そしてわが君は劉表殿の弟分でした。","魯粛",},
        [5689] = {9901,"諸葛亮","劉表殿は亡くなられましたが、若君はご存命です。叔父が甥を助けて荊州を取るのに、何の不道理がありましょう。","劉表殿は亡くなられましたが、若君はご存命です。叔父が甥を助けて荊州を取るのに、何の不道理がありましょう。","魯粛",},
        [5690] = {9901,"魯粛","若君の劉琦殿が領主としておられるなら、なるほど道理ですが、今は江夏にいると聞きます。ここにはおられまい。","若君の劉琦殿が領主としておられるなら、なるほど道理ですが、今は江夏にいると聞きます。ここにはおられまい。","諸葛亮",},
        [5691] = {9901,"諸葛亮","魯粛殿は若君にお会いになりたいですか?","魯粛殿は若君にお会いになりたいですか?","魯粛",},
        [5692] = {9901,"魯粛","ええ、それは……","ええ、それは……","諸葛亮",},
        [5693] = {9901,"諸葛亮","では、どうぞこちらへ。","では、どうぞこちらへ。","魯粛",},
        [5694] = {9902,"劉琦","病の身ゆえ、お辞儀もできませんが、ご容赦ください。(劉琦が病床に横たわっている)","病の身ゆえ、お辞儀もできませんが、ご容赦ください。(劉琦が病床に横たわっている)","魯粛",},
        [5695] = {9902,"魯粛","若君が、もしみまかられたら、どうするのです?","若君が、もしみまかられたら、どうするのです?","劉琦",},
        [5696] = {9902,"諸葛亮","若君がおられる限りは、ここを守ります。もしみまかられたら、また別に相談しましょう。","若君がおられる限りは、ここを守ります。もしみまかられたら、また別に相談しましょう。","魯粛",},
        [5697] = {9902,"魯粛","ではその際は、ここは必ず呉にお返し願いたい。","ではその際は、ここは必ず呉にお返し願いたい。","諸葛亮",},
        [5698] = {9902,"諸葛亮","お言葉、ごもっともです。","お言葉、ごもっともです。","魯粛",},
        [5699] = {9903,"魯粛","劉琦が亡くなったら、荊州は呉に返すとのことです。","劉琦が亡くなったら、荊州は呉に返すとのことです。","周瑜",},
        [5700] = {9903,"周瑜","劉琦はまだ若いだろう。死ぬのを待っていたら、いつ返ってくるかわからない。","劉琦はまだ若いだろう。死ぬのを待っていたら、いつ返ってくるかわからない。","魯粛",},
        [5701] = {9903,"魯粛","ご安心ください。この魯粛が必ず荊州を呉に返すよう要求します。","ご安心ください。この魯粛が必ず荊州を呉に返すよう要求します。","周瑜",},
        [5702] = {9903,"周瑜","何か思うところがあるのか?","何か思うところがあるのか?","魯粛",},
        [5703] = {9903,"魯粛","私の見たところ、劉琦は酒に溺れて、病状も深刻です。すっかり痩せ衰え、息切れや吐血もあります。半年もたたずに死ぬでしょう。","私の見たところ、劉琦は酒に溺れて、病状も深刻です。すっかり痩せ衰え、息切れや吐血もあります。半年もたたずに死ぬでしょう。","周瑜",},
        [5704] = {9903,"魯粛","そのとき荊州を取りに行けば、劉備も断りはしないでしょう。","そのとき荊州を取りに行けば、劉備も断りはしないでしょう。","周瑜",},
        [5705] = {9903,"周瑜","いいだろう。","いいだろう。","魯粛",},
        [5706] = {9904,"伊籍","荊州の長久の計をお考えでしたら、賢人にお尋ねになるのがよろしいかと。","荊州の長久の計をお考えでしたら、賢人にお尋ねになるのがよろしいかと。","劉備",},
        [5707] = {9904,"劉備","その賢人はどこにいる?","その賢人はどこにいる?","伊籍",},
        [5708] = {9904,"伊籍","襄陽の馬氏の兄弟五人はみな優秀と評判です。末の弟は馬謖といい、最も優れているのは馬良といって、眉に白い毛があります。","襄陽の馬氏の兄弟五人はみな優秀と評判です。末の弟は馬謖といい、最も優れているのは馬良といって、眉に白い毛があります。","劉備",},
        [5709] = {9904,"伊籍","郷里では「馬氏の五常、白眉最も良し」と言われています。この者を召してお尋ねになっては。","郷里では「馬氏の五常、白眉最も良し」と言われています。この者を召してお尋ねになっては。","劉備",},
        [5710] = {9904,"劉備","うむ、すぐに迎えをやろう。","うむ、すぐに迎えをやろう。","伊籍",},
        [5711] = {9905,"劉備","先生、荊州を保つ策はあるでしょうか。","先生、荊州を保つ策はあるでしょうか。","馬良",},
        [5712] = {9905,"馬良","荊州の地は四方から敵の攻撃を受けるので、長くは守れません。若君の劉琦殿を養生かたがたここに置かれ、旧臣を招き集めて守らせ、","荊州の地は四方から敵の攻撃を受けるので、長くは守れません。若君の劉琦殿を養生かたがたここに置かれ、旧臣を招き集めて守らせ、","劉備",},
        [5713] = {9905,"馬良","また朝廷に上奏して劉琦殿を荊州刺史とし、領民を安心させるがよろしいでしょう。","また朝廷に上奏して劉琦殿を荊州刺史とし、領民を安心させるがよろしいでしょう。","劉備",},
        [5714] = {9905,"馬良","その上で南の武陵、長沙、桂陽、零陵の四郡を攻め、財貨や食糧を蓄えて基礎とすれば、長久を計るに足りましょう。","その上で南の武陵、長沙、桂陽、零陵の四郡を攻め、財貨や食糧を蓄えて基礎とすれば、長久を計るに足りましょう。","劉備",},
        [5715] = {9905,"劉備","四郡のどこから先に攻めるのがよいでしょう?","四郡のどこから先に攻めるのがよいでしょう?","馬良",},
        [5716] = {9905,"馬良","湘江の西にある零陵が一番近いので、まずそこをお取りなさい。","湘江の西にある零陵が一番近いので、まずそこをお取りなさい。","劉備",},
        [5717] = {9905,"馬良","次に武陵を取り、それから湘江の東にある桂陽を取って、最後に長沙です。","次に武陵を取り、それから湘江の東にある桂陽を取って、最後に長沙です。","劉備",},
        [5718] = {9905,"劉備","わかりました。あなたを従事の官につけ、伊籍を副官としよう。劉琦殿を襄陽に送り、代わりに関羽を荊州に呼び寄せよう。","わかりました。あなたを従事の官につけ、伊籍を副官としよう。劉琦殿を襄陽に送り、代わりに関羽を荊州に呼び寄せよう。","馬良",},
        [5719] = {9905,"劉備","総勢一万五千で零陵に兵を進める。張飛を先陣、趙雲を後陣とし、私と孔明は中軍につく。","総勢一万五千で零陵に兵を進める。張飛を先陣、趙雲を後陣とし、私と孔明は中軍につく。","馬良",},
        [5720] = {9905,"劉備","関羽は荊州の留守をし、糜竺、劉封は江陵を守るよう。","関羽は荊州の留守をし、糜竺、劉封は江陵を守るよう。","馬良",},
        [5721] = {10001,"諸葛亮","私が南陽の諸葛孔明です。さきに曹操が百万の大軍をもって押し寄せましたが、私の計略にかかって、惨敗を喫しました。","私が南陽の諸葛孔明です。さきに曹操が百万の大軍をもって押し寄せましたが、私の計略にかかって、惨敗を喫しました。","邢道栄",},
        [5722] = {10001,"諸葛亮","あなたたちでは相手になりません。このたびは帰順をすすめに来たのです。速やかに降参しなさい。","あなたたちでは相手になりません。このたびは帰順をすすめに来たのです。速やかに降参しなさい。","邢道栄",},
        [5723] = {10001,"邢道栄","たわごとをぬかすな！赤壁の大敗は周瑜の計りごとによるもんだ。てめえなんか関係ねえ！","たわごとをぬかすな！赤壁の大敗は周瑜の計りごとによるもんだ。てめえなんか関係ねえ！","諸葛亮",},
        [5724] = {10001,"邢道栄","このわしの得物を食らってみやがれ！","このわしの得物を食らってみやがれ！","諸葛亮",},
        [5725] = {10002,"趙雲","常山の趙雲の名を知らぬか！","常山の趙雲の名を知らぬか！","邢道栄",},
        [5726] = {10002,"趙雲","逃がしはせん！","逃がしはせん！","邢道栄",},
        [5727] = {10002,"邢道栄","ヒエーッ！趙雲様、命ばかりはお助けを！","ヒエーッ！趙雲様、命ばかりはお助けを！","趙雲",},
        [5728] = {10003,"諸葛亮","劉賢を捕らえて来てくれるなら、許してあげますよ。","劉賢を捕らえて来てくれるなら、許してあげますよ。","邢道栄",},
        [5729] = {10003,"邢道栄","行きます行きます！","行きます行きます！","諸葛亮",},
        [5730] = {10003,"諸葛亮","どうやって捕らえるつもりです?","どうやって捕らえるつもりです?","邢道栄",},
        [5731] = {10003,"邢道栄","わしを放してくれるなら、いい考えがあります。軍師は今夜こちらの陣に夜討ちをかけてくだせえ。","わしを放してくれるなら、いい考えがあります。軍師は今夜こちらの陣に夜討ちをかけてくだせえ。","諸葛亮",},
        [5732] = {10003,"邢道栄","わしが劉賢を生け捕って献上しやしょう。劉賢が捕まれば、父の劉度も降ってくるはずでやす。","わしが劉賢を生け捕って献上しやしょう。劉賢が捕まれば、父の劉度も降ってくるはずでやす。","諸葛亮",},
        [5733] = {10003,"劉備","お前を信用しろというのか?","お前を信用しろというのか?","邢道栄",},
        [5734] = {10003,"邢道栄","絶対にでたらめは申しません！","絶対にでたらめは申しません！","劉備",},
        [5735] = {10003,"諸葛亮","わかりました。嘘でないと信じましょう。帰りなさい。","わかりました。嘘でないと信じましょう。帰りなさい。","邢道栄",},
        [5736] = {10003,"邢道栄","ありがとうごぜえやす、孔明様！","ありがとうごぜえやす、孔明様！","諸葛亮",},
        [5737] = {10004,"邢道栄","劉備がわしを放して帰してくれやした。わしが内応して、劉賢殿を生け捕りにし、劉度殿を降らせる計画でさあ。","劉備がわしを放して帰してくれやした。わしが内応して、劉賢殿を生け捕りにし、劉度殿を降らせる計画でさあ。","劉賢",},
        [5738] = {10004,"劉賢","どうすればいいのだ。","どうすればいいのだ。","邢道栄",},
        [5739] = {10004,"邢道栄","逆手に取りやしょう。","逆手に取りやしょう。","劉賢",},
        [5740] = {10004,"邢道栄","今夜、陣の中には旗や幟ばかり立てておき、兵は陣の外に埋伏させ、孔明が夜討ちに来たら生け捕りにするんでさあ。","今夜、陣の中には旗や幟ばかり立てておき、兵は陣の外に埋伏させ、孔明が夜討ちに来たら生け捕りにするんでさあ。","劉賢",},
        [5741] = {10004,"劉賢","よし。","よし。","邢道栄",},
        [5742] = {10005,"劉賢","一団の軍勢が、手に手に炬火を持ち、陣屋の口まで攻めて来た。やつらは火をつけるつもりだ。","一団の軍勢が、手に手に炬火を持ち、陣屋の口まで攻めて来た。やつらは火をつけるつもりだ。","邢道栄",},
        [5743] = {10005,"邢道栄","急いで包囲して、孔明を生け捕りにしろ！","急いで包囲して、孔明を生け捕りにしろ！","劉賢",},
        [5744] = {10005,"劉賢","よし、かかれ！","よし、かかれ！","邢道栄",},
        [5745] = {10006,"邢道栄","勢いにまかせて十里あまり追ってきたが、敵軍の姿が見えなくなりやした。","勢いにまかせて十里あまり追ってきたが、敵軍の姿が見えなくなりやした。","劉賢",},
        [5746] = {10006,"邢道栄","敵の計略かも知れねえです。急いで引き返しましょう。","敵の計略かも知れねえです。急いで引き返しましょう。","劉賢",},
        [5747] = {10006,"劉賢","うかつに陣に入るな。おそらく既に占領されていよう。","うかつに陣に入るな。おそらく既に占領されていよう。","邢道栄",},
        [5748] = {10006,"劉賢","かくなる上は孔明の本陣を襲撃するしか……！","かくなる上は孔明の本陣を襲撃するしか……！","邢道栄",},
        [5749] = {10007,"趙雲","劉賢、どこへ行く気だ。さっさと降参しろ！","劉賢、どこへ行く気だ。さっさと降参しろ！","劉賢",},
        [5750] = {10007,"劉賢","はっはい、降参します！","はっはい、降参します！","趙雲",},
        [5751] = {10007,"劉賢","私は邢道栄にそそのかされたのです。決して本心ではございません。","私は邢道栄にそそのかされたのです。決して本心ではございません。","趙雲",},
        [5752] = {10008,"趙雲","劉賢は既に捕らえた。劉度、お前も降伏しろ。さもなくば城を攻め落とし、一族皆殺しにするぞ。","劉賢は既に捕らえた。劉度、お前も降伏しろ。さもなくば城を攻め落とし、一族皆殺しにするぞ。","劉度",},
        [5753] = {10008,"劉賢","私は何も危害は加えられておりません。それどころか着物や酒まで賜りました。","私は何も危害は加えられておりません。それどころか着物や酒まで賜りました。","劉度",},
        [5754] = {10008,"劉賢","父上、太守の印を捧げて城を出て、降伏してください。","父上、太守の印を捧げて城を出て、降伏してください。","劉度",},
        [5755] = {10008,"劉度","わかった、劉備の本陣に降伏を申し出よう。","わかった、劉備の本陣に降伏を申し出よう。","劉賢",},
        [5756] = {10008,"諸葛亮","劉備様のはからいで、劉度はそのまま郡守とします。劉賢は荊州で軍事の職務に就いてください。","劉備様のはからいで、劉度はそのまま郡守とします。劉賢は荊州で軍事の職務に就いてください。","劉度",},
        [5757] = {10008,"劉度","ご厚恩に感謝いたします、孔明様、劉備様。","ご厚恩に感謝いたします、孔明様、劉備様。","諸葛亮",},
        [5758] = {10101,"趙雲","わが主君劉備様は劉表殿の弟として、若君の劉琦殿をたすけて荊州を領している。","わが主君劉備様は劉表殿の弟として、若君の劉琦殿をたすけて荊州を領している。","陳応",},
        [5759] = {10101,"趙雲","いま民を安んずるために参ったのだ。なぜ敵対するか。","いま民を安んずるために参ったのだ。なぜ敵対するか。","陳応",},
        [5760] = {10101,"陳応","我らが主は曹丞相だ。劉備なんぞに従うものか！","我らが主は曹丞相だ。劉備なんぞに従うものか！","趙雲",},
        [5761] = {10101,"趙雲","よくも言ったな、わが槍を食らうがいい！","よくも言ったな、わが槍を食らうがいい！","陳応",},
        [5762] = {10102,"趙雲","戦をするなら自分の力量を考えてみるがいい。放してやるから、さっさと帰って降伏するよう趙範に伝えろ。","戦をするなら自分の力量を考えてみるがいい。放してやるから、さっさと帰って降伏するよう趙範に伝えろ。","陳応",},
        [5763] = {10102,"陳応","ははっ、ありがたき幸せ。","ははっ、ありがたき幸せ。","趙雲",},
        [5764] = {10103,"趙範","だから最初から降伏しようと言ったのだ！お前が戦うと聞かなかったから、この始末ではないか。","だから最初から降伏しようと言ったのだ！お前が戦うと聞かなかったから、この始末ではないか。","陳応",},
        [5765] = {10103,"趙範","退がっていろ。太守の印を持って趙雲に降伏を申し入れに行く。","退がっていろ。太守の印を持って趙雲に降伏を申し入れに行く。","陳応",},
        [5766] = {10104,"趙範","将軍も私も同じ趙の姓ですから、五百年前まで遡ればきっと同じ一家だったのでしょう。私も真定の生まれで、将軍と同郷です。","将軍も私も同じ趙の姓ですから、五百年前まで遡ればきっと同じ一家だったのでしょう。私も真定の生まれで、将軍と同郷です。","趙雲",},
        [5767] = {10104,"趙範","もしよろしければ、兄弟の契りを結んでいただければ、この上なき幸せ。","もしよろしければ、兄弟の契りを結んでいただければ、この上なき幸せ。","趙雲",},
        [5768] = {10104,"趙範","将軍とは同い年ですが、私より生まれが四ヶ月早い。兄と呼ばせていただきます！","将軍とは同い年ですが、私より生まれが四ヶ月早い。兄と呼ばせていただきます！","趙雲",},
        [5769] = {10104,"趙雲","いやいや、丁重すぎて恐れ入る。","いやいや、丁重すぎて恐れ入る。","趙範",},
        [5770] = {10105,"趙範","実はおりいってお話があるので、どうか聞いてください。","実はおりいってお話があるので、どうか聞いてください。","趙雲",},
        [5771] = {10105,"趙範","兄嫁の樊氏は、兄と死に別れて三年になります。たびたび再婚をすすめているのですが――","兄嫁の樊氏は、兄と死に別れて三年になります。たびたび再婚をすすめているのですが――","趙雲",},
        [5772] = {10105,"趙範","再婚するなら三つ条件があると言うのです。","再婚するなら三つ条件があると言うのです。","趙雲",},
        [5773] = {10105,"趙範","一つは文武両道で、天下に名が聞こえていること。二つには威風堂々として人並み優れていること。三つには兄と同姓であること。","一つは文武両道で、天下に名が聞こえていること。二つには威風堂々として人並み優れていること。三つには兄と同姓であること。","趙雲",},
        [5774] = {10105,"趙範","兄嫁の希望は、将軍にぴったり合てはまります。持参金は用立てしますので、どうか将軍、妻にもらってくださいませんか。","兄嫁の希望は、将軍にぴったり合てはまります。持参金は用立てしますので、どうか将軍、妻にもらってくださいませんか。","趙雲",},
        [5775] = {10105,"趙雲","不埒者っ！お前とは義兄弟になったのだから、お前の義姉なら俺にとっても義姉だ。そんな人倫に外れたことができるか！","不埒者っ！お前とは義兄弟になったのだから、お前の義姉なら俺にとっても義姉だ。そんな人倫に外れたことができるか！","趙範",},
        [5776] = {10105,"趙範","好意で申したのに、なんと無礼な！","好意で申したのに、なんと無礼な！","趙雲",},
        [5777] = {10106,"陳応","趙雲は激怒して城を出て行きました。こうなったら徹底的に戦うしかないでしょう。","趙雲は激怒して城を出て行きました。こうなったら徹底的に戦うしかないでしょう。","趙範",},
        [5778] = {10106,"趙範","無駄だ。敵うわけがない。","無駄だ。敵うわけがない。","陳応",},
        [5779] = {10106,"陳応","私と鮑隆が偽って趙雲の陣中に入り込みますから、太守は兵を率いて来てください。内外で呼応して趙雲を生け捕りましょう。","私と鮑隆が偽って趙雲の陣中に入り込みますから、太守は兵を率いて来てください。内外で呼応して趙雲を生け捕りましょう。","趙範",},
        [5780] = {10107,"陳応","趙範は美人の計を用いて将軍を酔わせ、奥の間に誘い込んで首を取り、曹丞相に献上しようとの企みでした。","趙範は美人の計を用いて将軍を酔わせ、奥の間に誘い込んで首を取り、曹丞相に献上しようとの企みでした。","趙雲",},
        [5781] = {10107,"陳応","我ら二人は将軍がご立腹で立ち去られたのを見て、巻き添えになっては困ると思い、投降して来たのです。","我ら二人は将軍がご立腹で立ち去られたのを見て、巻き添えになっては困ると思い、投降して来たのです。","趙雲",},
        [5782] = {10107,"趙雲","ふっ、ははは……者ども、この二人を縛り上げろ！俺がお前たちの詐術を見抜けないとでも思うか！","ふっ、ははは……者ども、この二人を縛り上げろ！俺がお前たちの詐術を見抜けないとでも思うか！","",},
        [5783] = {10107,"趙雲","供の者に伝えよ。俺を殺そうと計ったのは、陳応と鮑隆だけで、ほかの者に罪はない。俺の言うとおりに働けば褒美をとらすと。","供の者に伝えよ。俺を殺そうと計ったのは、陳応と鮑隆だけで、ほかの者に罪はない。俺の言うとおりに働けば褒美をとらすと。","",},
        [5784] = {10107,"趙雲","では、桂陽城に向かうぞ。趙範と片をつけてやる！","では、桂陽城に向かうぞ。趙範と片をつけてやる！","",},
        [5785] = {10108,"劉備","趙雲、見事だった。計略で桂陽を取ったのは手柄だ。","趙雲、見事だった。計略で桂陽を取ったのは手柄だ。","趙雲",},
        [5786] = {10108,"主君","しかし趙範が兄嫁をもらってくれと言ったのは、もともと好意からでしょう?趙雲殿はどうしてここまで怒ったのです?","しかし趙範が兄嫁をもらってくれと言ったのは、もともと好意からでしょう?趙雲殿はどうしてここまで怒ったの?","趙雲",},
        [5787] = {10108,"趙雲","趙範と義兄弟の契りを結んだ以上、その兄嫁を娶っては、世間の人に軽蔑されるだろう。それにご婦人の貞節を汚すことにもなる。","趙範と義兄弟の契りを結んだ以上、その兄嫁を娶っては、世間の人に軽蔑されるだろう。それにご婦人の貞節を汚すことにもなる。","劉備",},
        [5788] = {10108,"趙雲","趙範は降伏したばかりで、心底も知れない。","趙範は降伏したばかりで、心底も知れない。","劉備",},
        [5789] = {10108,"趙雲","主君が荊州を平定されてまだ日も浅いときに、俺が一人の婦人のために、主君の大事を損なうようなまねはできん。","主君が荊州を平定されてまだ日も浅いときに、俺が一人の婦人のために、主君の大事を損なうようなまねはできん。","劉備",},
        [5790] = {10108,"劉備","今もう大事は成ったのだから、娶ればよいではないか。","今もう大事は成ったのだから、娶ればよいではないか。","趙雲",},
        [5791] = {10108,"趙雲","天下に女はいくらでもいます。私は名誉が傷つくことは怖れますが、妻子がなくても気にはしません。","天下に女はいくらでもいます。私は名誉が傷つくことは怖れますが、妻子がなくても気にはしません。","",},
        [5792] = {10108,"主君","趙雲殿こそ正に義の人ですね。","趙雲殿こそ正に義の人ですね。","",},
        [5793] = {10201,"張飛","趙雲と義弟が一郡ずつ取ったのに、オレは役立たずだってのか！","趙雲と義妹が一郡ずつ取ったのに、オレは役立たずだってのか！","諸葛亮",},
        [5794] = {10201,"張飛","三千の兵をくれりゃ、武陵郡に攻め込んで、太守の金旋を生け捕ってきてやらあ！","三千の兵をくれりゃ、武陵郡に攻め込んで、太守の金旋を生け捕ってきてやらあ！","諸葛亮",},
        [5795] = {10201,"諸葛亮","前回趙雲殿は桂陽郡を攻めるさい、誓文を書いて行かれたのです。","前回趙雲殿は桂陽郡を攻めるさい、誓文を書いて行かれたのです。","張飛",},
        [5796] = {10201,"諸葛亮","今回張飛殿が武陵を攻めるときも、やはり誓文を立ててから行ってください。","今回張飛殿が武陵を攻めるときも、やはり誓文を立ててから行ってください。","張飛",},
        [5797] = {10201,"張星彩","すっごく心配だわ。誓文を立てて武陵郡を攻めに行って、何か事故でも起こさないかって。父さまは短気なんだもん。","すっごく心配だわ。誓文を立てて武陵郡を攻めに行って、何か事故でも起こさないかって。父さまは短気なんだもん。","プレイヤー",},
        [5798] = {10201,"主君","大丈夫だよ、星彩。こっそりついて行って、手助けしよう。","大丈夫よ、星彩。こっそりついて行って、手助けしましょう。","張星彩",},
        [5799] = {10202,"金旋","あの張飛が兵を率いてわが郡に攻めてくる。精兵を選りすぐって、応戦に出よう。","あの張飛が兵を率いてわが郡に攻めてくる。精兵を選りすぐって、応戦に出よう。","鞏志",},
        [5800] = {10202,"鞏志","劉備は大漢の皇叔で、仁義をもって天下に聞こえています。しかも張飛は猛将。敵対せず、降伏するのが上策です。","劉備は大漢の皇叔で、仁義をもって天下に聞こえています。しかも張飛は猛将。敵対せず、降伏するのが上策です。","金旋",},
        [5801] = {10202,"金旋","けしからんことを！さては賊軍に内通しようという胆だな?者ども！鞏志を引っぱりだして首を刎ねよ！","けしからんことを！さては賊軍に内通しようという胆だな?者ども！鞏志を引っぱりだして首を刎ねよ！","鞏志",},
        [5802] = {10202,"金旋の従者","なりません！先に味方を殺すと、軍の士気に影響します！","なりません！先に味方を殺すと、軍の士気に影響します！","金旋",},
        [5803] = {10202,"金旋","ふん！ならばひとまず命は預けておいてやる。わし自ら兵を率いて出陣し、張飛と相まみえよう。","ふん！ならばひとまず命は預けておいてやる。わし自ら兵を率いて出陣し、張飛と相まみえよう。","鞏志",},
        [5804] = {10203,"張飛","オレ様が今日武陵郡を取ってやらあ！金旋、さっさと首を寄こしやがれ！","オレ様が今日武陵郡を取ってやらあ！金旋、さっさと首を寄こしやがれ！","",},
        [5805] = {10203,"金旋","誰か、相手になる者は?……誰か?……誰も戦いに出んのか！……ええい、もうよい、わし自ら出る！","誰か、相手になる者は?……誰か?……誰も戦いに出んのか！……ええい、もうよい、わし自ら出る！","",},
        [5806] = {10203,"張飛","待ちやがれ！逃げる気か！","待ちやがれ！逃げる気か！","",},
        [5807] = {10204,"金旋","鞏志、貴様、何をする気だ?わしに矢を射ってどうする！早く城門を開けて、入れてくれ！","鞏志、貴様、何をする気だ?わしに矢を射ってどうする！早く城門を開けて、入れてくれ！","鞏志",},
        [5808] = {10204,"鞏志","天に従わず、自らが招いた敗北だ。私と領民は劉備に降伏する。","天に従わず、自らが招いた敗北だ。私と領民は劉備に降伏する。","金旋",},
        [5809] = {10204,"金旋","きさま！ぐおっ……(一矢が命中する)","きさま！ぐおっ……(一矢が命中する)","鞏志",},
        [5810] = {10205,"張星彩","ご主君様の計略どおり鞏志を説得したおかげで、簡単に降伏させられたわね。","ご主君様の計略どおり鞏志を説得したおかげで、簡単に降伏させられたわね。","",},
        [5811] = {10205,"主君","いやいや星彩、劉備の兄者が仁徳で天下に名高いおかげで、領民が喜んで従ってくれたんだよ。","違うわよ星彩、劉備兄さんが仁徳で天下に名高いおかげで、領民が喜んで従ってくれたのよ。","",},
        [5812] = {10206,"劉備","張飛、このたびの武陵郡の攻略、お手柄だったぞ。よくやった！","張飛、このたびの武陵郡の攻略、お手柄だったぞ。よくやった！","張飛",},
        [5813] = {10206,"張飛","武陵郡がこんなにやすやすと取れるとは思わなかったぜ。兵三千は多すぎた。五百で足りたな！","武陵郡がこんなにやすやすと取れるとは思わなかったぜ。兵三千は多すぎた。五百で足りたな！","劉備",},
        [5814] = {10301,"関羽","張飛と趙雲、それに義弟が各々一郡を取ったと聞いた。","張飛と趙雲、それに義弟が各々一郡を取ったと聞いた。","諸葛亮",},
        [5815] = {10301,"関羽","長沙はまだ取れていないとのこと。今度はわしに手柄を立てさせてもらえないか。","長沙はまだ取れていないとのこと。今度はわしに手柄を立てさせてもらえないか。","諸葛亮",},
        [5816] = {10301,"諸葛亮","長沙の太守・韓玄は取るに足らぬ男だと聞きます。","長沙の太守・韓玄は取るに足らぬ男だと聞きます。","関羽",},
        [5817] = {10301,"諸葛亮","ただ配下に黄忠という大将がおり、歳はもう六十近いですが、万夫不当の武勇の持ち主です。軽んじてはなりません。","ただ配下に黄忠という大将がおり、歳はもう六十近いですが、万夫不当の武勇の持ち主です。軽んじてはなりません。","関羽",},
        [5818] = {10301,"関羽","軍師はなにゆえ味方の威勢を削ぐようなことを申される。老将一人、たかが知れていよう。","軍師はなにゆえ味方の威勢を削ぐようなことを申される。老将一人、たかが知れていよう。","諸葛亮",},
        [5819] = {10301,"関羽","三千の兵もいらぬ。手勢の五百だけで充分だ。必ず黄忠と韓玄の首を取って献上しよう。","三千の兵もいらぬ。手勢の五百だけで充分だ。必ず黄忠と韓玄の首を取って献上しよう。","諸葛亮",},
        [5820] = {10301,"諸葛亮","はぁ……心配ですね。関羽殿が黄忠を侮って、しくじらなければよいのですが。","はぁ……心配ですね。関羽殿が黄忠を侮って、しくじらなければよいのですが。","関羽",},
        [5821] = {10301,"主君","私が関羽の兄者と一緒に行って、協力しましょう。","私が関羽兄さんと一緒に行って、協力しましょう。","諸葛亮",},
        [5822] = {10301,"諸葛亮","では任せましたよ。","では任せましたよ。","プレイヤー",},
        [5823] = {10302,"韓玄","あの関羽がこの長沙を取りに来る。どうしたらいいだろう?","あの関羽がこの長沙を取りに来る。どうしたらいいだろう?","",},
        [5824] = {10302,"黄忠","ご心配には及ばぬ。ワシがこの刀と弓で、千人来れば千人倒してみせようぞ。","ご心配には及ばぬ。ワシがこの刀と弓で、千人来れば千人倒してみせようぞ。","韓玄",},
        [5825] = {10302,"楊齢","老将軍がお出になるには及びません。私の手で必ず関羽を生け捕ってごらんにいれます。","老将軍がお出になるには及びません。私の手で必ず関羽を生け捕ってごらんにいれます。","韓玄",},
        [5826] = {10302,"韓玄","よし、一千騎の兵を与える。城を出て迎え討て！","よし、一千騎の兵を与える。城を出て迎え討て！","楊齢",},
        [5827] = {10303,"関羽","さきほどの楊齢という者は弱すぎた。一刀のもとに斬り伏せたわ。今、そこに来るのは黄忠か?","さきほどの楊齢という者は弱すぎた。一刀のもとに斬り伏せたわ。今、そこに来るのは黄忠か?","黄忠",},
        [5828] = {10303,"黄忠","ワシの名を知っておりながら、何とて侵入してくる！","ワシの名を知っておりながら、何とて侵入してくる！","関羽",},
        [5829] = {10303,"関羽","むろん、その白髪首をもらいに来たのだ！","むろん、その白髪首をもらいに来たのだ！","黄忠",},
        [5830] = {10304,"韓玄","もう百合も打ち合っているのに勝負がつかん。黄将軍、早く戻れ。万一の事故があってはならん。","もう百合も打ち合っているのに勝負がつかん。黄将軍、早く戻れ。万一の事故があってはならん。","",},
        [5831] = {10304,"関羽","なるほど、老将黄忠は評判通りのつわもの。百合も打ち合って、少しも隙がなかった。次は少々戦い方を考えねば勝てんな。","なるほど、老将黄忠は評判通りのつわもの。百合も打ち合って、少しも隙がなかった。次は少々戦い方を考えねば勝てんな。","",},
        [5832] = {10305,"関羽","今度は譲らんぞ。必ず勝負をつけてやる。","今度は譲らんぞ。必ず勝負をつけてやる。","黄忠",},
        [5833] = {10305,"黄忠","フン、大きな口をたたきおる。どちらが勝つかまだわからんわい。","フン、大きな口をたたきおる。どちらが勝つかまだわからんわい。","関羽",},
        [5834] = {10305,"関羽","くらえ！","くらえ！","黄忠",},
        [5835] = {10305,"黄忠","ほいっ！……うおっ！(乗っていた馬が前足を挫き、黄忠は地面に投げ出される)","ほいっ！……うおっ！(乗っていた馬が前足を挫き、黄忠は地面に投げ出される)","関羽",},
        [5836] = {10305,"関羽","(馬を返し刀を収めて)命は助けてやる。早く馬を換えて出直すがいい。","(馬を返し刀を収めて)命は助けてやる。早く馬を換えて出直すがいい。","黄忠",},
        [5837] = {10306,"韓玄","どうしたのだ、黄将軍?自分の馬に放り出されるとは。","どうしたのだ、黄将軍?自分の馬に放り出されるとは。","黄忠",},
        [5838] = {10306,"黄忠","思わぬ過ちじゃった。あの馬はしばらく戦に出ておらんかった。明日の戦は弓を使いまする。","思わぬ過ちじゃった。あの馬はしばらく戦に出ておらんかった。明日の戦は弓を使いまする。","韓玄",},
        [5839] = {10307,"黄忠","関羽のように侠気のある者は珍しい。","関羽のように侠気のある者は珍しい。","",},
        [5840] = {10307,"黄忠","ワシに止めをささず情けをかけた。射殺すには忍びぬが、それでは命令に背くことになるのう……","ワシに止めをささず情けをかけた。射殺すには忍びぬが、それでは命令に背くことになるのう……","",},
        [5841] = {10307,"黄忠","弓を二度、弦打ちし、脅かして退かせるとしよう。","弓を二度、弦打ちし、脅かして退かせるとしよう。","",},
        [5842] = {10307,"関羽","黄忠の弓の腕は百発百中と聞くが、弦音がするだけで矢が見えない。ただの噂なのか?","黄忠の弓の腕は百発百中と聞くが、弦音がするだけで矢が見えない。ただの噂なのか?","",},
        [5843] = {10307,"黄忠","なんの、くらえ！(矢をつがえて弓を引く。弦音が響き、矢は関羽の兜の緒に命中)","なんの、くらえ！(矢をつがえて弓を引く。弦音が響き、矢は関羽の兜の緒に命中)","",},
        [5844] = {10307,"関羽","なるほど、百歩離れた柳の葉を射抜けるほどの腕前だ。今日わざと兜の緒をねらったのは、昨日の情けを返したということか。","なるほど、百歩離れた柳の葉を射抜けるほどの腕前だ。今日わざと兜の緒をねらったのは、昨日の情けを返したということか。","",},
        [5845] = {10401,"韓玄","三日間見ておったが、一昨日は戦で全力を尽くさず、昨日は落馬しても斬られなかった。明らかに敵と通じている証拠だ。","三日間見ておったが、一昨日は戦で全力を尽くさず、昨日は落馬しても斬られなかった。明らかに敵と通じている証拠だ。","黄忠",},
        [5846] = {10401,"韓玄","今日は二度も弦打ちし、三本目の矢で兜の緒を射ただけではないか。内通でなければ何だ。おのれを斬らねば、のちの煩いとなろう！","今日は二度も弦打ちし、三本目の矢で兜の緒を射ただけではないか。内通でなければ何だ。おのれを斬らねば、のちの煩いとなろう！","黄忠",},
        [5847] = {10401,"黄忠","何を言われる、ワシは無実じゃ！","何を言われる、ワシは無実じゃ！","韓玄",},
        [5848] = {10401,"韓玄","言い訳は聞かぬ！誰か、黄忠を引きだして斬れ！助命を乞う奴も同罪だ、一緒に斬れ！","言い訳は聞かぬ！誰か、黄忠を引きだして斬れ！助命を乞う奴も同罪だ、一緒に斬れ！","",},
        [5849] = {10401,"魏延","馬鹿な！黄忠将軍は長沙の守護神だぞ。ここで殺すとは、長沙の民を殺すに等しい。","馬鹿な！黄忠将軍は長沙の守護神だぞ。ここで殺すとは、長沙の民を殺すに等しい。","",},
        [5850] = {10401,"魏延","韓玄こそ暴虐で無慈悲で、才子を軽んじている。いっそ斬ってしまえ！従う者はついて来い！","韓玄こそ暴虐で無慈悲で、才子を軽んじている。いっそ斬ってしまえ！従う者はついて来い！","",},
        [5851] = {10402,"関羽","兄者、長沙郡を取り、降将の黄忠、魏延を得ました。","兄者、長沙郡を取り、降将の黄忠、魏延を得ました。","劉備",},
        [5852] = {10402,"劉備","でかしたぞ、関羽。私から黄忠将軍を迎えに行こう。","でかしたぞ、関羽。私から黄忠将軍を迎えに行こう。","関羽",},
        [5853] = {10402,"黄忠","劉備殿に帰順いたす。しかしその前に旧主の韓玄殿を手厚く葬らせてほしい。","劉備殿に帰順いたす。しかしその前に旧主の韓玄殿を手厚く葬らせてほしい。","劉備",},
        [5854] = {10402,"劉備","将軍はまことに忠義の士でいらっしゃる。","将軍はまことに忠義の士でいらっしゃる。","黄忠",},
        [5855] = {10403,"関羽","兄者、紹介しましょう。こちらが魏延です。","兄者、紹介しましょう。こちらが魏延です。","劉備",},
        [5856] = {10403,"諸葛亮","誰か、魏延を引きずり出して斬りなさい！","誰か、魏延を引きずり出して斬りなさい！","",},
        [5857] = {10403,"劉備","魏延には功績こそあれ罪はない。なぜ斬れというのか?","魏延には功績こそあれ罪はない。なぜ斬れというのか?","諸葛亮",},
        [5858] = {10403,"諸葛亮","禄を食んでおきながらその主君を殺すのは不忠。また住んでおきながらその領地を他人に献上するのは不義です。","禄を食んでおきながらその主君を殺すのは不忠。また住んでおきながらその領地を他人に献上するのは不義です。","劉備",},
        [5859] = {10403,"諸葛亮","魏延の骨相を見ますに、後頭部に反骨が突き出しています。","魏延の骨相を見ますに、後頭部に反骨が突き出しています。","劉備",},
        [5860] = {10403,"諸葛亮","これは謀反人の相ですから、後々必ず叛くでしょう。今のうちに斬って、禍いの根を断つのです。","これは謀反人の相ですから、後々必ず叛くでしょう。今のうちに斬って、禍いの根を断つのです。","劉備",},
        [5861] = {10403,"劉備","この者を殺せば、降伏してきた者がみな自分も危ないと感じるだろう。許してやってくれ。","この者を殺せば、降伏してきた者がみな自分も危ないと感じるだろう。許してやってくれ。","諸葛亮",},
        [5862] = {10403,"諸葛亮","……仕方ありません。魏延、命は預けておきます。","……仕方ありません。魏延、命は預けておきます。","魏延",},
        [5863] = {10403,"諸葛亮","主君に忠義を尽くしてお仕えしなさい。くれぐれも二心は抱かぬよう。さもなくば私が首を刎ねます。","主君に忠義を尽くしてお仕えしなさい。くれぐれも二心は抱かぬよう。さもなくば私が首を刎ねます。","魏延",},
        [5864] = {10501,"周瑜","兵を挙げて劉備、諸葛亮と雌雄を決します。","兵を挙げて劉備、諸葛亮と雌雄を決します。","魯粛",},
        [5865] = {10501,"周瑜","魯粛に程普殿、力をかしなさい。南郡を攻めて、何としてもわが呉の手に奪い返すのです。","魯粛に程普殿、力をかしなさい。南郡を攻めて、何としてもわが呉の手に奪い返すのです。","魯粛",},
        [5866] = {10501,"魯粛","なりません。目下は曹操と対峙している最中で、勝敗の行方はまだ見えません。まして劉備は曹操とはなじみがあります。","なりません。目下は曹操と対峙している最中で、勝敗の行方はまだ見えません。まして劉備は曹操とはなじみがあります。","周瑜",},
        [5867] = {10501,"魯粛","あまりに追いつめると、一緒になって呉を攻めてこないとも限りません。","あまりに追いつめると、一緒になって呉を攻めてこないとも限りません。","周瑜",},
        [5868] = {10501,"周瑜","なにを弱気なことを……","なにを弱気なことを……","魯粛",},
        [5869] = {10501,"周瑜","我らが計画を立て、兵馬を損ない、財貨と兵糧を費やして得た戦果を、奴が横からかっさらってゆくのですよ。口惜しくはないのですか！","我らが計画を立て、兵馬を損ない、財貨と兵糧を費やして得た戦果を、奴が横からかっさらってゆくのですよ。口惜しくはないのですか！","魯粛",},
        [5870] = {10501,"魯粛","今はこらえてください。私が劉備に直に会って、道理を説いてみます。","今はこらえてください。私が劉備に直に会って、道理を説いてみます。","周瑜",},
        [5871] = {10501,"魯粛","どうしても聞き入れなければ、そのとき兵を出しても遅くないでしょう。","どうしても聞き入れなければ、そのとき兵を出しても遅くないでしょう。","周瑜",},
        [5872] = {10501,"周瑜の従者","そうです、ここは魯粛殿の言われる通りです。","そうです、ここは魯粛殿の言われる通りです。","周瑜",},
        [5873] = {10502,"魯粛","劉備殿にお目にかかってお話がしたい。","劉備殿にお目にかかってお話がしたい。","趙雲",},
        [5874] = {10502,"趙雲","わが君は軍師とともに荊州城におられる。","わが君は軍師とともに荊州城におられる。","魯粛",},
        [5875] = {10502,"主君","魯粛が兄者を訪ねてきたのはきっと南郡を取り戻すためでしょう。ついていって、兄者の手助けをしようと思います。","魯粛が兄さんを訪ねてきたのはきっと南郡を取り戻すためでしょう。ついていって、兄さんの手助けをしようと思います。","趙雲",},
        [5876] = {10503,"魯粛","わが主呉侯ならびに周瑜都督より、劉備殿に申し入れたい。","わが主呉侯ならびに周瑜都督より、劉備殿に申し入れたい。","諸葛亮",},
        [5877] = {10503,"魯粛","先ごろ曹操が劉備殿を攻めて来たとき、わが呉が曹操軍を撃退し、劉備殿をお救いしました。","先ごろ曹操が劉備殿を攻めて来たとき、わが呉が曹操軍を撃退し、劉備殿をお救いしました。","諸葛亮",},
        [5878] = {10503,"魯粛","よって荊州の九郡はすべて、呉に属するのが当然と思われます。ところが今、劉備殿が詭計を用いて荊州と襄陽を奪ってしまわれた。","よって荊州の九郡はすべて、呉に属するのが当然と思われます。ところが今、劉備殿が詭計を用いて荊州と襄陽を奪ってしまわれた。","諸葛亮",},
        [5879] = {10503,"魯粛","わが呉が財物兵糧を費やしたのに、劉備殿がやすやすと利益を得るのは、納得がいきません。","わが呉が財物兵糧を費やしたのに、劉備殿がやすやすと利益を得るのは、納得がいきません。","諸葛亮",},
        [5880] = {10503,"諸葛亮","これは意外なお言葉ですね、魯粛殿。そもそも荊・襄九郡は、呉の領地ではありません。","これは意外なお言葉ですね、魯粛殿。そもそも荊・襄九郡は、呉の領地ではありません。","魯粛",},
        [5881] = {10503,"諸葛亮","劉表殿の本領です。そしてわが君は劉表殿の弟分でした。","劉表殿の本領です。そしてわが君は劉表殿の弟分でした。","魯粛",},
        [5882] = {10503,"諸葛亮","劉表殿は亡くなられましたが、若君はご存命です。叔父が甥を助けて荊州を取るのに、何の不道理がありましょう。","劉表殿は亡くなられましたが、若君はご存命です。叔父が甥を助けて荊州を取るのに、何の不道理がありましょう。","魯粛",},
        [5883] = {10504,"魯粛","若君の劉琦殿が領主としておられるなら、なるほど道理ですが、今は江夏にいると聞きます。ここにはおられまい。","若君の劉琦殿が領主としておられるなら、なるほど道理ですが、今は江夏にいると聞きます。ここにはおられまい。","プレイヤー",},
        [5884] = {10504,"主君","魯粛殿は若君にお会いになりたいですか?誰か、若君をこちらへ。","魯粛殿は若君にお会いになりたいですか?誰か、若君をこちらへ。","魯粛",},
        [5885] = {10504,"劉琦","病の身ゆえ、お辞儀もできませんが、ご容赦ください。","病の身ゆえ、お辞儀もできませんが、ご容赦ください。","魯粛",},
        [5886] = {10504,"魯粛","若君が、もしみまかられたら、どうするのです?","若君が、もしみまかられたら、どうするのです?","プレイヤー",},
        [5887] = {10504,"主君","若君がおられる限りは、ここを守ります。もしみまかられたら、また別に相談しましょう。","若君がおられる限りは、ここを守ります。もしみまかられたら、また別に相談しましょう。","魯粛",},
        [5888] = {10504,"魯粛","ではその際は、ここは必ず呉にお返し願いたい。","ではその際は、ここは必ず呉にお返し願いたい。","プレイヤー",},
        [5889] = {10504,"主君","お言葉、ごもっともです。","魯粛殿のお言葉はもっともです。","魯粛",},
        [5890] = {10505,"魯粛","都督に申し上げます。揚威将軍が言うには、劉琦がいる限りは荊州を守る。劉琦が亡くなったら、城は呉に返すとのことです。","都督に申し上げます。揚威将軍が言うには、劉琦がいる限りは荊州を守る。劉琦が亡くなったら、城は呉に返すとのことです。","周瑜",},
        [5891] = {10505,"周瑜","劉琦はまだ若いだろう。死ぬのを待っていたら、いつ返ってくるかわからない。","劉琦はまだ若いだろう。死ぬのを待っていたら、いつ返ってくるかわからない。","魯粛",},
        [5892] = {10505,"魯粛","ご安心ください。この魯粛が必ず荊州を呉に返すよう要求します。","ご安心ください。この魯粛が必ず荊州を呉に返すよう要求します。","周瑜",},
        [5893] = {10505,"周瑜","何か思うところがあるのか?","何か思うところがあるのか?","魯粛",},
        [5894] = {10505,"魯粛","私の見たところ、劉琦は酒に溺れて、病状も深刻です。今やすっかり痩せ衰え、息切れや吐血もあります。","私の見たところ、劉琦は酒に溺れて、病状も深刻です。今やすっかり痩せ衰え、息切れや吐血もあります。","周瑜",},
        [5895] = {10505,"魯粛","半年もたたずに死ぬでしょう。そのとき荊州を取りに行けば、劉備も断りはしないでしょう。","半年もたたずに死ぬでしょう。そのとき荊州を取りに行けば、劉備も断りはしないでしょう。","周瑜",},
        [5896] = {10505,"孫権の使者","主君は合肥の城を取り囲みましたが、攻めても攻めても落ちません。","主君は合肥の城を取り囲みましたが、攻めても攻めても落ちません。","周瑜",},
        [5897] = {10505,"孫権の使者","都督殿には急ぎ軍を引き揚げ、合肥の支援に派遣してほしいとのことです。","都督殿には急ぎ軍を引き揚げ、合肥の支援に派遣してほしいとのことです。","周瑜",},
        [5898] = {10601,"主君","孫権は赤壁の戦いのあと、ずっと合肥で曹軍と交戦しているそうだ。","孫権は赤壁の戦いのあと、ずっと合肥で曹軍と交戦しているそうよ。","張星彩",},
        [5899] = {10601,"主君","十回以上も戦って、まだ勝敗がつかないらしい。一度様子を見に行ってみるか。","十回以上も戦って、まだ勝敗がつかないらしいの。一度様子を見に行ってみましょう。","張星彩",},
        [5900] = {10601,"張星彩","星彩もついていくわ。ご主君様の行くところならどこへでも！","星彩もついていくわ。ご主君様の行くところならどこへでも！","プレイヤー",},
        [5901] = {10602,"孫権","曹軍とはどれだけ矛を交えたか知れぬが、勝負がつかん。曹軍は援軍を待っていると聞くが、本当だろうか。","曹軍とはどれだけ矛を交えたか知れぬが、勝負がつかん。曹軍は援軍を待っていると聞くが、本当だろうか。","",},
        [5902] = {10602,"孫権の使者","奴らの使者を捕まえて問いただし、虚実を探ってみてはいかがでしょう。","奴らの使者を捕まえて問いただし、虚実を探ってみてはいかがでしょう。","孫権",},
        [5903] = {10603,"孫権の使者","申し上げます。曹軍の使者を二人捕らえました。携えていた密書がこちらです。","申し上げます。曹軍の使者を二人捕らえました。携えていた密書がこちらです。","孫権",},
        [5904] = {10603,"孫権","なに?敵の四万の援軍がまもなく到着するだと?それは……よく考えてみねば……","なに?敵の四万の援軍がまもなく到着するだと?それは……よく考えてみねば……","",},
        [5905] = {10603,"孫権","これまで大小の戦を重ねてきて、兵も馬も疲れ果てている。","これまで大小の戦を重ねてきて、兵も馬も疲れ果てている。","",},
        [5906] = {10603,"孫権","士気も振るわん。敵の大軍がやって来たら、このまま対峙を続けてはわが方に極めて不利だ……","士気も振るわん。敵の大軍がやって来たら、このまま対峙を続けてはわが方に極めて不利だ……","",},
        [5907] = {10603,"孫権","やはり兵を引き揚げよう。帰って英気を養い、後日の再戦に備えるのだ。","やはり兵を引き揚げよう。帰って英気を養い、後日の再戦に備えるのだ。","",},
        [5908] = {10603,"主君","お待ちください孫権様。その四万の大軍という情報は怪しい。偽計かもしれません。","お待ちください孫権様。その四万の大軍という情報は怪しい。偽計かもしれません。","孫権",},
        [5909] = {10603,"主君","私が虚実を探りに行きましょう。曹軍には張遼という大将がいると聞きます。ちょうど手合わせしたいと思っていたところです。","私が虚実を探りに行きましょう。曹軍には張遼という大将がいると聞きます。ちょうど手合わせしたいと思っていたところです。","孫権",},
        [5910] = {10603,"孫権","では将軍、くれぐれも気をつけてくれ。","では将軍、くれぐれも気をつけてくれ。","プレイヤー",},
        [5911] = {10604,"張星彩","ご主君様、その曹軍の張遼は、勇猛無比の武将だそうよ。気をつけて！","ご主君様、その曹軍の張遼は、勇猛無比の武将だそうよ。気をつけて！","プレイヤー",},
        [5912] = {10604,"主君","面は紫玉の如く、目は朗らかな星の如く、か。張遼将軍は確かに勇ましいな。","面は紫玉の如く、目は朗らかな星の如く、ね。張遼将軍は確かに勇ましい。","張遼",},
        [5913] = {10604,"主君","今日はお手合わせ願います、将軍。","今日はお手合わせ願います、将軍。","張遼",},
        [5914] = {10604,"張遼","いざ、参る！","いざ、参る！","プレイヤー",},
        [5915] = {10701,"孫権","今日、程普の大軍が到着する。ちょうどいいときに来てくれた。俺みずから出迎えたい。","今日、程普の大軍が到着する。ちょうどいいときに来てくれた。俺みずから出迎えたい。","魯粛",},
        [5916] = {10701,"魯粛","わが君に馬を下りてお出迎えいただくとは、恐縮の極みです。","わが君に馬を下りてお出迎えいただくとは、恐縮の極みです。","孫権",},
        [5917] = {10701,"孫権","俺が馬を下りて出迎えの礼をとったら、おぬしの功績を讃えたことになるのか?","俺が馬を下りて出迎えの礼をとったら、おぬしの功績を讃えたことになるのか?","魯粛",},
        [5918] = {10701,"魯粛","願わくはわが君が天下をことごとく治められ、帝業を成就された暁に――","願わくはわが君が天下をことごとく治められ、帝業を成就された暁に――","孫権",},
        [5919] = {10701,"魯粛","私の名を歴史に残していただければ、私の功績が讃えられたことになります。","私の名を歴史に残していただければ、私の功績が讃えられたことになります。","孫権",},
        [5920] = {10701,"孫権","はははっ、よし！","はははっ、よし！","魯粛",},
        [5921] = {10702,"孫権の使者","申し上げます。張遼から決戦状が送られてきました。","申し上げます。張遼から決戦状が送られてきました。","孫権",},
        [5922] = {10702,"孫権","張遼め、馬鹿にしおって！程普の大軍が到着したと聞くや決戦しようとは、俺は眼中にないということか?","張遼め、馬鹿にしおって！程普の大軍が到着したと聞くや決戦しようとは、俺は眼中にないということか?","",},
        [5923] = {10702,"孫権","明日、新手の勢は戦に出るな。俺の真面目を見せてやる！","明日、新手の勢は戦に出るな。俺の真面目を見せてやる！","",},
        [5924] = {10703,"李典","あそこに金の兜をかぶっているのが孫権だ。あれを生け捕ったら、赤壁でやられた八十三万の味方の仇討ちに足るぞ。","あそこに金の兜をかぶっているのが孫権だ。あれを生け捕ったら、赤壁でやられた八十三万の味方の仇討ちに足るぞ。","楽進",},
        [5925] = {10703,"楽進","よし、まず宋謙と太史慈を片づけよう。それから孫権を捕らえるぞ。","よし、まず宋謙と太史慈を片づけよう。それから孫権を捕らえるぞ。","李典",},
        [5926] = {10704,"宋謙","殿、早くお逃げください、ここはわしが！","殿、早くお逃げください、ここはわしが！","",},
        [5927] = {10704,"太史慈","いかん、宋謙が射たれた。ご主君をお守りしろ！","いかん、宋謙が射たれた。ご主君をお守りしろ！","",},
        [5928] = {10704,"程普","殿、大丈夫です、程普がおります！","殿、大丈夫です、程普がおります！","",},
        [5929] = {10704,"孫権","ええい、本陣に戻るぞ！","ええい、本陣に戻るぞ！","",},
        [5930] = {10705,"魯粛","わが君は血気にはやり、大敵を侮られることがあり、これには全軍みな怖れを抱いております。","わが君は血気にはやり、大敵を侮られることがあり、これには全軍みな怖れを抱いております。","孫権",},
        [5931] = {10705,"魯粛","今日宋謙が敵の矢に命を落としたのも、敵を軽んじたためです。今後はなにとぞご自重ください。","今日宋謙が敵の矢に命を落としたのも、敵を軽んじたためです。今後はなにとぞご自重ください。","孫権",},
        [5932] = {10705,"孫権","俺の過ちだった。以後は改めよう。","俺の過ちだった。以後は改めよう。","魯粛",},
        [5933] = {10705,"太史慈","俺の部下に戈定と申す者がおります。これが張遼の配下の馬飼と兄弟なのです。","俺の部下に戈定と申す者がおります。これが張遼の配下の馬飼と兄弟なのです。","孫権",},
        [5934] = {10705,"太史慈","その馬飼は叱責されたことで恨みを抱き、今晩火の手を合図に張遼を闇討ちしようと言ってきております。","その馬飼は叱責されたことで恨みを抱き、今晩火の手を合図に張遼を闇討ちしようと言ってきております。","孫権",},
        [5935] = {10705,"太史慈","宋謙の仇に報いるため、俺も外から呼応したく、どうか兵をお貸しください。","宋謙の仇に報いるため、俺も外から呼応したく、どうか兵をお貸しください。","孫権",},
        [5936] = {10705,"諸葛瑾","張遼は智謀にも長けていますから、備えはしておりましょう。用心してください。","張遼は智謀にも長けていますから、備えはしておりましょう。用心してください。","太史慈",},
        [5937] = {10706,"戈定","太史慈将軍にはもうお伝えしてある。今夜きっと攻めて来てくれるだろう。どんなふうにやる?","太史慈将軍にはもうお伝えしてある。今夜きっと攻めて来てくれるだろう。どんなふうにやる?","",},
        [5938] = {10706,"馬丁","ここは本隊からだいぶ遠くて、夜分急には入り込めねえ。俺がまぐさ置き場に火をつけて回るから、お前は前のほうで謀反だと叫ぶがいい。","ここは本隊からだいぶ遠くて、夜分急には入り込めねえ。俺がまぐさ置き場に火をつけて回るから、お前は前のほうで謀反だと叫ぶがいい。","",},
        [5939] = {10706,"馬丁","城内が混乱したすきに張遼を闇討ちすれば、他の奴は逃げていくだろう。","城内が混乱したすきに張遼を闇討ちすれば、他の奴は逃げていくだろう。","",},
        [5940] = {10706,"張遼","命令を伝える。今夜は城の防備を強化し、鎧を脱いだり、眠ったりせぬように。","命令を伝える。今夜は城の防備を強化し、鎧を脱いだり、眠ったりせぬように。","",},
        [5941] = {10706,"張遼の従者","今日は大勝利で、呉の軍勢は遠く陣地をさげてしまったのに、なぜ鎧を解いて休まないのですか?","今日は大勝利で、呉の軍勢は遠く陣地をさげてしまったのに、なぜ鎧を解いて休まないのですか?","張遼",},
        [5942] = {10706,"張遼","いや、こちらが無防備になった隙をついて呉の軍が攻めて来たらどうする。今夜はいつもにまして気を引き締めねばならんぞ。","いや、こちらが無防備になった隙をついて呉の軍が攻めて来たらどうする。今夜はいつもにまして気を引き締めねばならんぞ。","張遼の従者",},
        [5943] = {10706,"張遼の従者","報告します！陣の後方から火の手が上がり、謀反人だという叫び声が聞こえます。将軍、ご検分になってください！","報告します！陣の後方から火の手が上がり、謀反人だという叫び声が聞こえます。将軍、ご検分になってください！","張遼",},
        [5944] = {10706,"張遼","なに、城内すべてが謀反したわけではあるまい。","なに、城内すべてが謀反したわけではあるまい。","張遼の従者",},
        [5945] = {10706,"張遼","あれは謀反の者が、わざと兵士どもを混乱させようとしているだけだ。みだりに騒ぐ者は斬れ！","あれは謀反の者が、わざと兵士どもを混乱させようとしているだけだ。みだりに騒ぐ者は斬れ！","張遼の従者",},
        [5946] = {10706,"張遼の従者","報告します！城外で鬨の声が上がりました。敵が大勢やって来ます！","報告します！城外で鬨の声が上がりました。敵が大勢やって来ます！","張遼",},
        [5947] = {10706,"張遼","それは呉の軍勢が示し合わせて来たのだろう。敵の計略を逆手にとってやろう。","それは呉の軍勢が示し合わせて来たのだろう。敵の計略を逆手にとってやろう。","張遼の従者",},
        [5948] = {10707,"太史慈","城内で火の手が上がり、騒ぎ声がする。戈定たちが成功したのだろう。一気に攻め込んで張遼を討ち取れ！宋謙の仇だ！","城内で火の手が上がり、騒ぎ声がする。戈定たちが成功したのだろう。一気に攻め込んで張遼を討ち取れ！宋謙の仇だ！","",},
        [5949] = {10707,"張遼","フン、かかったな。矢を放て！","フン、かかったな。矢を放て！","",},
        [5950] = {10707,"陸遜","太史慈将軍、伏兵です！退いてください！","太史慈将軍、伏兵です！退いてください！","太史慈",},
        [5951] = {10707,"太史慈","大丈夫たるもの、乱世に生まれたからには、三尺の剣を帯びて大功を立てるべきなのに……","大丈夫たるもの、乱世に生まれたからには、三尺の剣を帯びて大功を立てるべきなのに……","",},
        [5952] = {10707,"太史慈","志が遂げられず、無念だ！(言い終わると息を引き取った。齢四十一歳。)","志が遂げられず、無念だ！(言い終わると息を引き取った。齢四十一歳。)","",},
        [5953] = {10801,"諸葛亮","昨夜天象を見ておりましたところ、西北の方に星が落ちました。皇族のどなたかが亡くなられたようです。","昨夜天象を見ておりましたところ、西北の方に星が落ちました。皇族のどなたかが亡くなられたようです。","劉備",},
        [5954] = {10801,"劉備の従者","報告します。若君の劉琦様がみまかられました。","報告します。若君の劉琦様がみまかられました。","劉備",},
        [5955] = {10801,"諸葛亮","寿命は定めです。あまりにお嘆きになっては、お体に障りますよ。","寿命は定めです。あまりにお嘆きになっては、お体に障りますよ。","劉備",},
        [5956] = {10801,"劉備","劉琦殿が亡くなった今、呉は必ず荊州を返せと言ってくるだろう。何と答えればいいのか……","劉琦殿が亡くなった今、呉は必ず荊州を返せと言ってくるだろう。何と答えればいいのか……","諸葛亮",},
        [5957] = {10801,"諸葛亮","ご心配なく。使いが参りましたら、私が応対いたします。","ご心配なく。使いが参りましたら、私が応対いたします。","劉備",},
        [5958] = {10802,"魯粛","甥御殿ご逝去との報を承り、呉侯の使いとしていささかの礼物を供え、喪を弔いに参りました。","甥御殿ご逝去との報を承り、呉侯の使いとしていささかの礼物を供え、喪を弔いに参りました。","劉備",},
        [5959] = {10802,"魯粛","周都督も劉備殿と諸葛先生にくれぐれもよろしくとのこと。","周都督も劉備殿と諸葛先生にくれぐれもよろしくとのこと。","劉備",},
        [5960] = {10802,"魯粛","先ごろ劉備殿は、若君がみまかられたら、荊州は返すと仰せられた。","先ごろ劉備殿は、若君がみまかられたら、荊州は返すと仰せられた。","劉備",},
        [5961] = {10802,"魯粛","若君が逝去された今、必ずお返し願えるでしょうな。いつごろお引き渡しくださいますか。","若君が逝去された今、必ずお返し願えるでしょうな。いつごろお引き渡しくださいますか。","劉備",},
        [5962] = {10802,"劉備","まあそう慌てず、酒でもどうぞ。ゆるりとお話しましょう。","まあそう慌てず、酒でもどうぞ。ゆるりとお話しましょう。","魯粛",},
        [5963] = {10802,"魯粛","先にきちんと問題を片づけてから飲んでも遅くありますまい。","先にきちんと問題を片づけてから飲んでも遅くありますまい。","劉備",},
        [5964] = {10802,"諸葛亮","魯粛殿は礼儀をわきまえませんねえ。強引に話を進めようとなさるなんて。そもそもわが君は現皇帝の皇叔で、劉表殿の弟分にあたられるのです。","魯粛殿は礼儀をわきまえませんねえ。強引に話を進めようとなさるなんて。そもそもわが君は現皇帝の皇叔で、劉表殿の弟分にあたられるのです。","魯粛",},
        [5965] = {10802,"諸葛亮","大漢は劉氏の天下なのに、わが君に何の取り分もなく、孫姓のそちらが取り立てに来るとはもってのほかでしょう。","大漢は劉氏の天下なのに、わが君に何の取り分もなく、孫姓のそちらが取り立てに来るとはもってのほかでしょう。","魯粛",},
        [5966] = {10802,"諸葛亮","赤壁の戦いでも、わが君にはご尽力いただき、諸将もそのご命令に従って働きました。呉一国の力ではありません。","赤壁の戦いでも、わが君にはご尽力いただき、諸将もそのご命令に従って働きました。呉一国の力ではありません。","魯粛",},
        [5967] = {10802,"諸葛亮","もし私が東南の風を天から借りなければ、周瑜殿とてなす術はなかったはずです。","もし私が東南の風を天から借りなければ、周瑜殿とてなす術はなかったはずです。","魯粛",},
        [5968] = {10802,"諸葛亮","さきほどわが君が即答を避けられたのは、賢明な魯粛殿には多く語らずとも、とうにご推察のことと思われたからですよ。","さきほどわが君が即答を避けられたのは、賢明な魯粛殿には多く語らずとも、とうにご推察のことと思われたからですよ。","魯粛",},
        [5969] = {10803,"魯粛","孔明殿のお言葉には一理あるかもしれませんが、この魯粛にとっては実に具合が悪いです。","孔明殿のお言葉には一理あるかもしれませんが、この魯粛にとっては実に具合が悪いです。","諸葛亮",},
        [5970] = {10803,"諸葛亮","具合が悪いとは?","具合が悪いとは?","魯粛",},
        [5971] = {10803,"魯粛","以前、劉備殿が当陽で難儀されたおり、孔明殿を呉侯にお引き合わせしたのは私です。","以前、劉備殿が当陽で難儀されたおり、孔明殿を呉侯にお引き合わせしたのは私です。","諸葛亮",},
        [5972] = {10803,"魯粛","後に周瑜殿が荊州を攻めようとした時も私が止めました。そして、若君が亡くなられたら荊州を返すとのお約束も、私が引き受けたのです。","後に周瑜殿が荊州を攻めようとした時も私が止めました。そして、若君が亡くなられたら荊州を返すとのお約束も、私が引き受けたのです。","諸葛亮",},
        [5973] = {10803,"魯粛","今その約束を反故にされて帰っては、何と復命できましょう?必ず主君と周瑜殿から責められ、罰せられます。","今その約束を反故にされて帰っては、何と復命できましょう?必ず主君と周瑜殿から責められ、罰せられます。","諸葛亮",},
        [5974] = {10803,"諸葛亮","では、わが君の名で証文を入れましょう。","では、わが君の名で証文を入れましょう。","魯粛",},
        [5975] = {10803,"諸葛亮","しばらく荊州を借りて拠点とし、ほかの領地が手に入ったら、すぐに呉へお返しするということで、どうですか?","しばらく荊州を借りて拠点とし、ほかの領地が手に入ったら、すぐに呉へお返しするということで、どうですか?","魯粛",},
        [5976] = {10803,"魯粛","どこを攻め取ったら、荊州を返すと言われるのですか。","どこを攻め取ったら、荊州を返すと言われるのですか。","諸葛亮",},
        [5977] = {10803,"諸葛亮","蜀です……西川の地を得たあかつきには、きっちりとお返しいたします。","蜀です……西川の地を得たあかつきには、きっちりとお返しいたします。","魯粛",},
        [5978] = {10804,"諸葛亮","ではここに証文をしたため、署名しました。魯粛殿にも連署していただければ、呉侯に対しても体裁がよろしいでしょう。","ではここに証文をしたため、署名しました。魯粛殿にも連署していただければ、呉侯に対しても体裁がよろしいでしょう。","魯粛",},
        [5979] = {10804,"魯粛","劉備殿は仁義の人、この契約を違えることはあるまいと信じております。","劉備殿は仁義の人、この契約を違えることはあるまいと信じております。","諸葛亮",},
        [5980] = {10804,"諸葛亮","魯粛殿、呉侯によろしくお取りなしを。","魯粛殿、呉侯によろしくお取りなしを。","魯粛",},
        [5981] = {10804,"諸葛亮","もしこの約束をお聞き入れなければ、こちらも今までの誼みは捨て、呉の八十一州すべて奪いに行きます。","もしこの約束をお聞き入れなければ、こちらも今までの誼みは捨て、呉の八十一州すべて奪いに行きます。","魯粛",},
        [5982] = {10804,"諸葛亮","今必要なのは両国の平和です。曹操の笑い種にならぬようお気を付けくださいね。","今必要なのは両国の平和です。曹操の笑い種にならぬようお気を付けくださいね。","魯粛",},
        [5983] = {10805,"周瑜","荊州の首尾はどうだったのだ?","荊州の首尾はどうだったのだ?","魯粛",},
        [5984] = {10805,"魯粛","ここに証文があります。","ここに証文があります。","周瑜",},
        [5985] = {10805,"周瑜","あぁ……魯粛、また孔明にはめられましたね。いかにも土地を借りたように見せかけて、実はごまかしです。","あぁ……魯粛、また孔明にはめられましたね。いかにも土地を借りたように見せかけて、実はごまかしです。","魯粛",},
        [5986] = {10805,"周瑜","蜀を取ったら返す?一体いつになるというのですか。","蜀を取ったら返す?一体いつになるというのですか。","魯粛",},
        [5987] = {10805,"周瑜","こんな証文、なんの役にも立ちはしませんよ。なのに保証人の署名までして……","こんな証文、なんの役にも立ちはしませんよ。なのに保証人の署名までして……","魯粛",},
        [5988] = {10805,"周瑜","もし返さなかったら、君も巻き添えになりますよ。ご主君のお咎めを受けたらどうするつもりですか。","もし返さなかったら、君も巻き添えになりますよ。ご主君のお咎めを受けたらどうするつもりですか。","魯粛",},
        [5989] = {10805,"魯粛","そんな……劉備殿が私を騙すはずは……","そんな……劉備殿が私を騙すはずは……","周瑜",},
        [5990] = {10805,"周瑜","とんだお人好しだね。劉備は梟雄で、孔明は狡猾の徒だ。君のように正直者ではない。","とんだお人好しだね。劉備は梟雄で、孔明は狡猾の徒だ。君のように正直者ではない。","魯粛",},
        [5991] = {10901,"間者","荊州の城内では弔旗を掲げて法事が営まれています。城外に新しい墓所が築かれ、諸将は喪服を着ています。","荊州の城内では弔旗を掲げて法事が営まれています。城外に新しい墓所が築かれ、諸将は喪服を着ています。","周瑜",},
        [5992] = {10901,"周瑜","誰が死んだのだ?","誰が死んだのだ?","",},
        [5993] = {10901,"間者","劉備の奥方の甘夫人が亡くなったので、その葬儀の準備です。","劉備の奥方の甘夫人が亡くなったので、その葬儀の準備です。","周瑜",},
        [5994] = {10901,"周瑜","よし、いい策ができたぞ！これなら劉備をやすやすと捕らえられ、荊州が手に入るだろう。","よし、いい策ができたぞ！これなら劉備をやすやすと捕らえられ、荊州が手に入るだろう。","",},
        [5995] = {10901,"周瑜","今すぐ書面で主君に申し上げましょう。荊州へ縁談の使いをやり、劉備を婿に取ると言って、南徐まで誘き寄せるのです。","今すぐ書面で主君に申し上げましょう。荊州へ縁談の使いをやり、劉備を婿に取ると言って、南徐まで誘き寄せるのです。","",},
        [5996] = {10901,"周瑜","むろん花嫁はやらず牢に閉じ込め、荊州と交換で返すと言ってやるのですよ。","むろん花嫁はやらず牢に閉じ込め、荊州と交換で返すと言ってやるのですよ。","",},
        [5997] = {10901,"周瑜","荊州の城が引き渡されたら、別に考えがある。魯粛も無事にすむだろう。","荊州の城が引き渡されたら、別に考えがある。魯粛も無事にすむだろう。","",},
        [5998] = {10902,"呂範","近頃奥方様がお亡くなりになったと承り、実は良い縁談がございまして。","近頃奥方様がお亡くなりになったと承り、実は良い縁談がございまして。","劉備",},
        [5999] = {10902,"呂範","お疑いをかえりみず、仲立ちをさせていただきたいと参った次第です。いかがでございましょう。","お疑いをかえりみず、仲立ちをさせていただきたいと参った次第です。いかがでございましょう。","劉備",},
        [6000] = {10902,"劉備","中年で妻をなくすのは、いかにも不幸です。しかし死に別れてまだ日も浅く、後妻を娶る気には、とても……","中年で妻をなくすのは、いかにも不幸です。しかし死に別れてまだ日も浅く、後妻を娶る気には、とても……","呂範",},
        [6001] = {10902,"呂範","妻の無い人は、梁の無い屋根のようなものです。","妻の無い人は、梁の無い屋根のようなものです。","劉備",},
        [6002] = {10902,"呂範","人生半ばで連れ合いがいないわけにもいきますまい。主君の呉侯には一人の妹君があられますが、才色兼備で奥方にふさわしいかと。","人生半ばで連れ合いがいないわけにもいきますまい。主君の呉侯には一人の妹君があられますが、才色兼備で奥方にふさわしいかと。","劉備",},
        [6003] = {10902,"呂範","ご両家の縁組みが整えば、曹操も東南をうかがうことはできますまい。","ご両家の縁組みが整えば、曹操も東南をうかがうことはできますまい。","劉備",},
        [6004] = {10902,"呂範","ただ母君の呉国太がたいそう可愛がっておいでで、遠くに嫁がせないので、劉備殿が呉にいらしていただきたいのです。","ただ母君の呉国太がたいそう可愛がっておいでで、遠くに嫁がせないので、劉備殿が呉にいらしていただきたいのです。","劉備",},
        [6005] = {10902,"劉備","私は歳すでに五十の白髪まじりだが、呉侯の妹君は妙齢でいられよう。不釣り合いではありませんか。","私は歳すでに五十の白髪まじりだが、呉侯の妹君は妙齢でいられよう。不釣り合いではありませんか。","呂範",},
        [6006] = {10902,"呂範","呉侯の妹君は男子に勝る志をお持ちです。","呉侯の妹君は男子に勝る志をお持ちです。","劉備",},
        [6007] = {10902,"呂範","天下に名の響く劉備殿は、まさしく淑女に配する君子でいらっしゃる。歳の差をご不満には思いますまい。","天下に名の響く劉備殿は、まさしく淑女に配する君子でいらっしゃる。歳の差をご不満には思いますまい。","劉備",},
        [6008] = {10902,"劉備","少々お待ちください。明日お返事いたします。","少々お待ちください。明日お返事いたします。","呂範",},
        [6009] = {10903,"諸葛亮","使者の来意はわかっておりました。","使者の来意はわかっておりました。","劉備",},
        [6010] = {10903,"諸葛亮","先ほど易を立てて占ってみたところ、大吉の卦がでました。この縁談をご承諾になり、日を選んで呉においでなさるのがよいでしょう。","先ほど易を立てて占ってみたところ、大吉の卦がでました。この縁談をご承諾になり、日を選んで呉においでなさるのがよいでしょう。","劉備",},
        [6011] = {10903,"劉備","これは周瑜が私を害しようとの計りごとに違いない。軽々しく危険な地に入ってよかろうか。","これは周瑜が私を害しようとの計りごとに違いない。軽々しく危険な地に入ってよかろうか。","諸葛亮",},
        [6012] = {10903,"諸葛亮","大丈夫です。私の想定を外れることはありません。","大丈夫です。私の想定を外れることはありません。","劉備",},
        [6013] = {10904,"孫乾","呉侯はご主君が縁を結びに来られるのを待っておられます。","呉侯はご主君が縁を結びに来られるのを待っておられます。","劉備",},
        [6014] = {10904,"劉備","しかし罠かもしれぬ。軽々しくは行けぬぞ。","しかし罠かもしれぬ。軽々しくは行けぬぞ。","孫乾",},
        [6015] = {10904,"諸葛亮","三つの計略を定めておきました。これを成し遂げられるのは趙雲殿のほかにいません。","三つの計略を定めておきました。これを成し遂げられるのは趙雲殿のほかにいません。","劉備",},
        [6016] = {10904,"諸葛亮","わが君を護衛して、呉に行ってください。","わが君を護衛して、呉に行ってください。","劉備",},
        [6017] = {10904,"諸葛亮","錦の袋を三つ授けます。この中に三つの計略を入れておきましたから、順を追って進めてください。","錦の袋を三つ授けます。この中に三つの計略を入れておきましたから、順を追って進めてください。","劉備",},
        [6018] = {10905,"趙雲","軍師より三つの妙計を預かり、順番に実行するよう言いつかった。南徐に到着したから、一つ目の袋を開いてみよう。","軍師より三つの妙計を預かり、順番に実行するよう言いつかった。南徐に到着したから、一つ目の袋を開いてみよう。","",},
        [6019] = {10905,"趙雲","ふむふむ……なるほど、この通りにしよう。","ふむふむ……なるほど、この通りにしよう。","",},
        [6020] = {10906,"喬国老","ご息女が劉備殿の奥方になられるそうですな。もう劉備殿も見えておるのに、お隠しにならずとも。","ご息女が劉備殿の奥方になられるそうですな。もう劉備殿も見えておるのに、お隠しにならずとも。","呉国太",},
        [6021] = {10906,"呉国太","わたしは何も聞いておりませぬよ！","わたしは何も聞いておりませぬよ！","喬国老",},
        [6022] = {10906,"喬国老","いやいや確かに、婿殿は河口の宿駅でひと休みしておられる。","いやいや確かに、婿殿は河口の宿駅でひと休みしておられる。","呉国太",},
        [6023] = {10906,"喬国老","供の五百人の兵士が城内で豚や羊や果物を買って、婚礼の準備をしておるそうですぞ。","供の五百人の兵士が城内で豚や羊や果物を買って、婚礼の準備をしておるそうですぞ。","呉国太",},
        [6024] = {10906,"喬国老","仲立ちは、嫁のほうは呂範、婿のほうは孫乾とのこと。双方宿駅で待っておられる。","仲立ちは、嫁のほうは呂範、婿のほうは孫乾とのこと。双方宿駅で待っておられる。","呉国太",},
        [6025] = {10906,"呉国太","それでは、なぜわたしに言ってこないのです！","それでは、なぜわたしに言ってこないのです！","喬国老",},
        [6026] = {10907,"孫権","母上様、何を嘆いておられます?","母上様、何を嘆いておられます?","呉国太",},
        [6027] = {10907,"呉国太","お前はわたしのことなど何とも思っていないのでしょう。姉上が臨終のおり、何と言われたか忘れましたか?","お前はわたしのことなど何とも思っていないのでしょう。姉上が臨終のおり、何と言われたか忘れましたか?","孫権",},
        [6028] = {10907,"孫権","もっとはっきりおっしゃってください。何がそんなにお辛いのです?","もっとはっきりおっしゃってください。何がそんなにお辛いのです?","呉国太",},
        [6029] = {10907,"呉国太","大きくなれば男は嫁を取り、女は嫁に行くのは当たり前ですが、わたしを母と思っているなら、話してくれてもよさそうなもの。","大きくなれば男は嫁を取り、女は嫁に行くのは当たり前ですが、わたしを母と思っているなら、話してくれてもよさそうなもの。","孫権",},
        [6030] = {10907,"呉国太","劉備を婿に取るとの話を、なぜ隠していたのです?わたしの娘ですよ！","劉備を婿に取るとの話を、なぜ隠していたのです?わたしの娘ですよ！","孫権",},
        [6031] = {10907,"孫権","落ち着いてください、これは周瑜の計略なのです。荊州を取り戻すため、婚礼の名目で劉備を招いて捕らえ、荊州と交換しようというのです。","落ち着いてください、これは周瑜の計略なのです。荊州を取り戻すため、婚礼の名目で劉備を招いて捕らえ、荊州と交換しようというのです。","呉国太",},
        [6032] = {10907,"孫権","従わねば、劉備を斬ります。本当の縁組みではありません。","従わねば、劉備を斬ります。本当の縁組みではありません。","呉国太",},
        [6033] = {10907,"呉国太","周瑜は呉の六郡八十一州の大都督でありながら、荊州を攻め取ることもできず、わたしの娘を囮に美人の計を使おうとは……！","周瑜は呉の六郡八十一州の大都督でありながら、荊州を攻め取ることもできず、わたしの娘を囮に美人の計を使おうとは……！","孫権",},
        [6034] = {10907,"呉国太","劉備を殺せば、わたしの娘は未亡人です。どうやって再婚させるのです?娘の一生を台無しにするつもりですか！なんと浅はかな！","劉備を殺せば、わたしの娘は未亡人です。どうやって再婚させるのです?娘の一生を台無しにするつもりですか！なんと浅はかな！","孫権",},
        [6035] = {10907,"喬国老","たとえこの計略で荊州を得ても、天下の笑い者になりましょう。許されるはずがない！","たとえこの計略で荊州を得ても、天下の笑い者になりましょう。許されるはずがない！","呉国太",},
        [6036] = {10908,"喬国老","このさい、漢室のご一門である劉備殿を本当に婿にお取りになれば、面目を潰さずにすむとのではないでしょうかな。","このさい、漢室のご一門である劉備殿を本当に婿にお取りになれば、面目を潰さずにすむとのではないでしょうかな。","孫権",},
        [6037] = {10908,"孫権","しかし、歳が釣り合わぬでしょう。","しかし、歳が釣り合わぬでしょう。","喬国老",},
        [6038] = {10908,"喬国老","なんの、劉備殿は当世の豪傑だ。こんな婿をお取りになれば、妹君にとっても決して恥ずかしくはない。","なんの、劉備殿は当世の豪傑だ。こんな婿をお取りになれば、妹君にとっても決して恥ずかしくはない。","孫権",},
        [6039] = {10908,"呉国太","わたしは劉備殿を存じ上げぬ。明日甘露寺で顔合わせをしましょう。","わたしは劉備殿を存じ上げぬ。明日甘露寺で顔合わせをしましょう。","孫権",},
        [6040] = {10908,"呉国太","わたしが気に入らなければ、お前達の好きなようにしなさい。もし気に入ったら、娘をやりましょう。","わたしが気に入らなければ、お前達の好きなようにしなさい。もし気に入ったら、娘をやりましょう。","孫権",},
        [6041] = {10909,"孫乾","明日、呉侯と呉国太と顔合わせをします。充分にお気をつけて。","明日、呉侯と呉国太と顔合わせをします。充分にお気をつけて。","趙雲",},
        [6042] = {10909,"趙雲","明日の会は、吉より凶のほうが多そうだ。俺が五百の兵を連れて護衛しよう。","明日の会は、吉より凶のほうが多そうだ。俺が五百の兵を連れて護衛しよう。","孫乾",},
        [6043] = {10910,"劉備","私が劉備です。お初にお目にかかります。","私が劉備です。お初にお目にかかります。","呉国太",},
        [6044] = {10910,"呉国太","ほう、まことに婿どのにふさわしい！","ほう、まことに婿どのにふさわしい！","劉備",},
        [6045] = {10910,"喬国老","劉備殿のお姿は龍や鳳凰のように高貴で、天子の相をもっておられる。","劉備殿のお姿は龍や鳳凰のように高貴で、天子の相をもっておられる。","劉備",},
        [6046] = {10910,"喬国老","さらに仁徳は天下に知られた方だ。こんなすばらしい婿殿を得られれば、まことにおめでたい限り。","さらに仁徳は天下に知られた方だ。こんなすばらしい婿殿を得られれば、まことにおめでたい限り。","劉備",},
        [6047] = {10910,"趙雲","(そっと劉備にささやく)さっき廊下を見回りましたところ、部屋の陰に伏兵が隠れている気配がします。","(そっと劉備にささやく)さっき廊下を見回りましたところ、部屋の陰に伏兵が隠れている気配がします。","劉備",},
        [6048] = {10910,"趙雲","良からぬ企みがありましょう。呉国太に告げるべきかと。","良からぬ企みがありましょう。呉国太に告げるべきかと。","劉備",},
        [6049] = {10910,"劉備","恐れ入りますが、廊下に伏兵を忍ばせておられるとは、どういう狙いでしょうか。","恐れ入りますが、廊下に伏兵を忍ばせておられるとは、どういう狙いでしょうか。","呉国太",},
        [6050] = {10910,"劉備","もし劉備を殺そうとの思し召しなら、この場で御手にかけてください。","もし劉備を殺そうとの思し召しなら、この場で御手にかけてください。","呉国太",},
        [6051] = {10910,"呉国太","(激怒して)なんですって！劉備殿が婿になった以上は、わたしの息子も同然です。","(激怒して)なんですって！劉備殿が婿になった以上は、わたしの息子も同然です。","劉備",},
        [6052] = {10910,"呉国太","伏兵を廊下に忍ばせるとは、どういう了見ですか！","伏兵を廊下に忍ばせるとは、どういう了見ですか！","劉備",},
        [6053] = {10910,"孫権","お、俺は、よくわかりませぬ……呂範、お、お前の仕業であろう?","お、俺は、よくわかりませぬ……呂範、お、お前の仕業であろう?","呂範",},
        [6054] = {10910,"呂範","ええっ?！いえ、わ、私も存じませぬ……賈華、こ、これはどういうことか?","ええっ?！いえ、わ、私も存じませぬ……賈華、こ、これはどういうことか?","",},
        [6055] = {10910,"賈華","……","……","",},
        [6056] = {11001,"劉備","もし私が荊州に戻って覇業がなせるならば、この一刀のもとに岩よ、斬れよ！ここで死ぬ運命ならば、刃は立つまい。","もし私が荊州に戻って覇業がなせるならば、この一刀のもとに岩よ、斬れよ！ここで死ぬ運命ならば、刃は立つまい。","",},
        [6057] = {11001,"孫権","劉備殿、この岩に何か恨みでも?真っ二つに斬れましたが。","劉備殿、この岩に何か恨みでも?真っ二つに斬れましたが。","劉備",},
        [6058] = {11001,"劉備","もう五十近くになるのに、国のために逆賊を滅ぼすこともならないのが、いつも無念なのです。","もう五十近くになるのに、国のために逆賊を滅ぼすこともならないのが、いつも無念なのです。","孫権",},
        [6059] = {11001,"劉備","今日呉国太のお招きで婿と定まったのは、一生の幸運でした。","今日呉国太のお招きで婿と定まったのは、一生の幸運でした。","孫権",},
        [6060] = {11001,"劉備","そこで天に願掛けをしてみたのです。もし曹操を破れるものならば、この岩斬れよと。果たしてその通りになりました。","そこで天に願掛けをしてみたのです。もし曹操を破れるものならば、この岩斬れよと。果たしてその通りになりました。","孫権",},
        [6061] = {11001,"孫権","なるほど。では、俺も天に願掛けしてみよう。曹操を打ち破れるものなら、この岩よ、今一度斬れよ！","なるほど。では、俺も天に願掛けしてみよう。曹操を打ち破れるものなら、この岩よ、今一度斬れよ！","",},
        [6062] = {11001,"孫権","(内心で祈る)もし荊州が再び手に入り、呉の国が栄えるならば、この岩が斬れろ！","(内心で祈る)もし荊州が再び手に入り、呉の国が栄えるならば、この岩が斬れろ！","",},
        [6063] = {11002,"劉備","北の人はよく馬に乗り、南の人はよく舟を走らすと申しますが、本当にその通りですな。","北の人はよく馬に乗り、南の人はよく舟を走らすと申しますが、本当にその通りですな。","孫権",},
        [6064] = {11002,"孫権","劉備殿、それは俺がうまく馬に乗れぬと馬鹿にしておられるのか?","劉備殿、それは俺がうまく馬に乗れぬと馬鹿にしておられるのか?","劉備",},
        [6065] = {11002,"孫権","馬をひけい！劉備殿にご覧に入れよう、南の者でも馬に乗る術は心得ておる。","馬をひけい！劉備殿にご覧に入れよう、南の者でも馬に乗る術は心得ておる。","",},
        [6066] = {11002,"劉備","では競ってみますか?ハァッ！","では競ってみますか?ハァッ！","孫権",},
        [6067] = {11003,"主君","劉備の兄者も孫権殿も楽しそうに付き合っていますね。前に軍師に渡された錦の袋はいつ使えばいいのでしょう。","劉備兄さんも孫権殿も楽しそうに付き合っていますね。前に軍師に渡された錦の袋はいつ使えばいいのでしょう。","趙雲",},
        [6068] = {11003,"趙雲","軍師は神の如く先の先まで見通している。今回面倒ごとが解消されたのもやはりこの錦の袋に入っていた妙計のおかげだ。","軍師は神の如く先の先まで見通している。今回面倒ごとが解消されたのもやはりこの錦の袋に入っていた妙計のおかげだ。","プレイヤー",},
        [6069] = {11003,"趙雲","錦の袋を開く時機は軍師から聞いたとおり、しかと心に留めている。気を緩めず、何事も用心してあたろう。","錦の袋を開く時機は軍師から聞いたとおり、しかと心に留めている。気を緩めず、何事も用心してあたろう。","プレイヤー",},
        [6070] = {11003,"主君","そうするほかありませんね。","そうするほかありませんね。","趙雲",},
        [6071] = {11101,"劉備","江東には、私を害しようと考えている方が多いようですので、長居はできません。","江東には、私を害しようと考えている方が多いようですので、長居はできません。","喬国老",},
        [6072] = {11101,"喬国老","ご安心くだされ。わしから呉国太に申し上げて、危害が及ばぬよう取り計らいましょう。","ご安心くだされ。わしから呉国太に申し上げて、危害が及ばぬよう取り計らいましょう。","劉備",},
        [6073] = {11101,"呉国太","わたしの婿を、誰が害しようというのです！明日にもこちらの屋敷に移り住みなさい。吉日を選んで婚礼を挙げますから。","わたしの婿を、誰が害しようというのです！明日にもこちらの屋敷に移り住みなさい。吉日を選んで婚礼を挙げますから。","劉備",},
        [6074] = {11101,"劉備","しかし趙雲が持ち場を離れますと、兵士を取り締まれなくなってしまいます。","しかし趙雲が持ち場を離れますと、兵士を取り締まれなくなってしまいます。","呉国太",},
        [6075] = {11101,"呉国太","では兵士も残らず屋敷へ引っ越しなさい。宿駅に留まって、不要ないさかいを起こさぬように。","では兵士も残らず屋敷へ引っ越しなさい。宿駅に留まって、不要ないさかいを起こさぬように。","劉備",},
        [6076] = {11102,"小喬","劉備様が孫尚香を妻にするんですって?","劉備様が孫尚香を妻にするんですって?","劉備",},
        [6077] = {11102,"劉備","幸い呉国太のお眼鏡にかなって、愛娘をやってもいいと言われたよ。幸いなことだ。","幸い呉国太のお眼鏡にかなって、愛娘をやってもいいと言われたよ。幸いなことだ。","小喬",},
        [6078] = {11102,"小喬","孫尚香は男勝りの女の子よ。嫁にするのは簡単じゃないわ。先にあたしと手合わせなさい！","孫尚香は男勝りの女の子よ。嫁にするのは簡単じゃないわ。先にあたしと手合わせなさい！","劉備",},
        [6079] = {11102,"主君","お受けしましょう！しばらく体がなまっていたし、ちょうどいい。怪我しないよう、気をつけてくださいよ！","お受けしましょう！しばらく体がなまっていたし、ちょうどいい。怪我しないよう、気をつけてくださいね！","小喬",},
        [6080] = {11103,"大喬","小喬ったら、こんなにあっさり通してしまったのね。でも、この大喬はそう簡単にはいかないわよ！","小喬ったら、こんなにあっさり通してしまったのね。でも、この大喬はそう簡単にはいかないわよ！","プレイヤー",},
        [6081] = {11103,"主君","大喬……呉国太でさえ劉備の兄者を娘婿と認めてくださったのに、これ以上邪魔はご遠慮願いたい。","大喬さん……呉国太でさえ劉備兄さんを娘婿と認めてくださったのに、これ以上の邪魔はご遠慮願えません?","大喬",},
        [6082] = {11103,"大喬","孫尚香はわたしの義理の妹よ。真の英雄でなければ、婿として認めるわけにいかないわ！","孫尚香はわたしの義理の妹よ。真の英雄でなければ、婿として認めるわけにいかないわ！","プレイヤー",},
        [6083] = {11103,"主君","仕方がないか……ケガをしても知りませんよ。","仕方がないか……ケガをしても知りませんからね。","大喬",},
        [6084] = {11104,"主君","兄者、ご結婚まことにおめでとうございます！","兄さん、ご結婚まことにおめでとうございます！","劉備",},
        [6085] = {11104,"劉備","これは……新婚夫婦の寝室に槍や刀が山ほどあるとは?両側に居並ぶ侍女もみな剣や刀を携えている。もしや孫権の伏兵か?","これは……新婚夫婦の寝室に槍や刀が山ほどあるとは?両側に居並ぶ侍女もみな剣や刀を携えている。もしや孫権の伏兵か?","孫尚香",},
        [6086] = {11104,"孫尚香侍女","オホホ、そんなに脅えずとも。姫様は幼い頃から武芸がお好きで、いつも侍女たちに剣術の試合をさせて楽しんでおられるのです。","オホホ、そんなに脅えずとも。姫様は幼い頃から武芸がお好きで、いつも侍女たちに剣術の試合をさせて楽しんでおられるのです。","劉備",},
        [6087] = {11104,"劉備","それは姫のすることなのか……さすがにいい気分はしないので、しばらく片づけてもらいたいのだが。","それは姫のすることなのか……さすがにいい気分はしないので、しばらく片づけてもらいたいのだが。","孫尚香",},
        [6088] = {11104,"孫尚香","あははっ、普段から戦ばっかりしてても、武器は怖いって言うのね。","あははっ、普段から戦ばっかりしてても、武器は怖いって言うのね。","劉備",},
        [6089] = {11104,"孫尚香","そんなに怯えなくたっていいのよ、婿殿。すぐに片付けさせるから。","そんなに怯えなくたっていいのよ、婿殿。すぐに片付けさせるから。","劉備",},
        [6090] = {11105,"孫権","母上は頑として譲らず、本当に妹を劉備に嫁がせてしまった。まさか嘘が真実になるとはな......","母上は頑として譲らず、本当に妹を劉備に嫁がせてしまった。まさか嘘が真実になるとはな......","周瑜",},
        [6091] = {11105,"周瑜","計りごとが思わぬ結果となりましたが、これをまた計りごとに使いましょう。","計りごとが思わぬ結果となりましたが、これをまた計りごとに使いましょう。","孫権",},
        [6092] = {11105,"周瑜","劉備を呉に取り込め置いて、豪邸を築き、美色と娯楽の数々を送って、志を失わせるのです。","劉備を呉に取り込め置いて、豪邸を築き、美色と娯楽の数々を送って、志を失わせるのです。","孫権",},
        [6093] = {11105,"周瑜","そうして関羽、張飛との情、諸葛亮との契りから遠ざけ、その後、兵をもって攻め入れば目的は達せられるだろう。","そうして関羽、張飛との情、諸葛亮との契りから遠ざけ、その後、兵をもって攻め入れば目的は達せられるだろう。","孫権",},
        [6094] = {11105,"張昭","私も同感です。劉備は貧しい生まれ育ちで、富貴栄耀を味わったことはありますまい。","私も同感です。劉備は貧しい生まれ育ちで、富貴栄耀を味わったことはありますまい。","孫権",},
        [6095] = {11105,"張昭","おのずと関羽や張飛とも疎遠になり、荊州もこちらの手に帰しましょう。","おのずと関羽や張飛とも疎遠になり、荊州もこちらの手に帰しましょう。","孫権",},
        [6096] = {11105,"孫権","よし、では東府に豪邸を建て、劉備と妹をここに住まわせよ。舞姫や歌姫を増やし、金銀宝玉に錦綾、宝物を取り揃えるのだ。","よし、では東府に豪邸を建て、劉備と妹をここに住まわせよ。舞姫や歌姫を増やし、金銀宝玉に錦綾、宝物を取り揃えるのだ。","周瑜",},
        [6097] = {11201,"趙雲","孔明殿より三つの錦の袋を預かり、一つ目は南徐に到着したら開けよと言われた。","孔明殿より三つの錦の袋を預かり、一つ目は南徐に到着したら開けよと言われた。","",},
        [6098] = {11201,"趙雲","年の暮れになったら二つ目、いよいよ切羽詰まったら三つ目の袋を開けるように、か……","年の暮れになったら二つ目、いよいよ切羽詰まったら三つ目の袋を開けるように、か……","",},
        [6099] = {11201,"趙雲","今や年の暮れだが、主君は女色に溺れ、顔も見せない。今こそ二つ目の袋を開いて見るべきだな。","今や年の暮れだが、主君は女色に溺れ、顔も見せない。今こそ二つ目の袋を開いて見るべきだな。","",},
        [6100] = {11202,"趙雲","今朝、孔明殿より知らせがあり、赤壁の怨みをすすがんと、曹操が五十万の兵を率いて荊州に攻め込んで来たそうです。","今朝、孔明殿より知らせがあり、赤壁の怨みをすすがんと、曹操が五十万の兵を率いて荊州に攻め込んで来たそうです。","劉備",},
        [6101] = {11202,"趙雲","危急の時ゆえ、主君に帰っていただきたいと。","危急の時ゆえ、主君に帰っていただきたいと。","劉備",},
        [6102] = {11202,"劉備","それは大事だ……妻にも相談せねば。","それは大事だ……妻にも相談せねば。","趙雲",},
        [6103] = {11202,"趙雲","奥方に話せば、必ずお引き留めになるでしょう。お知らせせず、今夜にも発ちましょう。遅れては一大事です！","奥方に話せば、必ずお引き留めになるでしょう。お知らせせず、今夜にも発ちましょう。遅れては一大事です！","劉備",},
        [6104] = {11202,"劉備","しばらく退がっていてくれ。私にも思うところがあるのだ。","しばらく退がっていてくれ。私にも思うところがあるのだ。","趙雲",},
        [6105] = {11202,"趙雲","では急ぎご決断ください。くれぐれも躊躇なさらず。","では急ぎご決断ください。くれぐれも躊躇なさらず。","劉備",},
        [6106] = {11203,"孫尚香","旦那さま、何を悩んでいるの?","旦那さま、何を悩んでいるの?","劉備",},
        [6107] = {11203,"劉備","私は単身で異郷をさすらって、両親に孝行も尽くせず、先祖を祀ることもできぬ。","私は単身で異郷をさすらって、両親に孝行も尽くせず、先祖を祀ることもできぬ。","孫尚香",},
        [6108] = {11203,"劉備","まったくの不孝者だ。正月が近づくと、それがひとしお悲しくなるのだ。","まったくの不孝者だ。正月が近づくと、それがひとしお悲しくなるのだ。","孫尚香",},
        [6109] = {11203,"孫尚香","ウソおっしゃい。あたし知ってるわよ！本当はさっき来た趙雲に荊州が危険だと言われたから、帰りたいと思ってるんでしょう。","ウソおっしゃい。あたし知ってるわよ！本当はさっき来た趙雲に荊州が危険だと言われたから、帰りたいと思ってるんでしょう。","劉備",},
        [6110] = {11203,"劉備","聞いていたか……もはや隠し立てはするまい。","聞いていたか……もはや隠し立てはするまい。","孫尚香",},
        [6111] = {11203,"劉備","帰らねば荊州を失って、天下の人に笑われよう。しかしお前のことも思い切れぬ。それで思い惑っているのだ。","帰らねば荊州を失って、天下の人に笑われよう。しかしお前のことも思い切れぬ。それで思い惑っているのだ。","孫尚香",},
        [6112] = {11203,"孫尚香","悩むことなんてないわ。あたしも一緒に行く。もうあなたに付いていくと決めたのよ。","悩むことなんてないわ。あたしも一緒に行く。もうあなたに付いていくと決めたのよ。","劉備",},
        [6113] = {11203,"劉備","お前がそう思ってくれても、呉国太さまと孫権殿が許しはしまい。もしこの劉備を哀れに思うなら、しばしの別れを承知してくれ。","お前がそう思ってくれても、呉国太さまと孫権殿が許しはしまい。もしこの劉備を哀れに思うなら、しばしの別れを承知してくれ。","孫尚香",},
        [6114] = {11203,"孫尚香","だったら、こういうのはどう?あたしとあなたで正月の拝礼をする時に、川岸で祖先を祭ると言って、そのまま出発しちゃうの。","だったら、こういうのはどう?あたしとあなたで正月の拝礼をする時に、川岸で祖先を祭ると言って、そのまま出発しちゃうの。","劉備",},
        [6115] = {11203,"劉備","そうしてくれるなら、生涯この恩は忘れぬ！だが、計画が漏れぬよう気を付けてな。","そうしてくれるなら、生涯この恩は忘れぬ！だが、計画が漏れぬよう気を付けてな。","孫尚香",},
        [6116] = {11204,"孫権","なに?！劉備が妹を連れて逃げただと?","なに?！劉備が妹を連れて逃げただと?","張昭",},
        [6117] = {11204,"張昭","劉備を逃がしては、早晩禍いとなりましょう。急いで追わねば。","劉備を逃がしては、早晩禍いとなりましょう。急いで追わねば。","孫権",},
        [6118] = {11204,"程普","お怒りはごもっともですが、追っ手が陳武と潘璋ではおそらく捕まりますまい。","お怒りはごもっともですが、追っ手が陳武と潘璋ではおそらく捕まりますまい。","孫権",},
        [6119] = {11204,"程普","妹君は幼少の頃より武芸を好まれて、男勝りのご気性は、将士も怖れております。","妹君は幼少の頃より武芸を好まれて、男勝りのご気性は、将士も怖れております。","孫権",},
        [6120] = {11204,"程普","劉備に従って出られたのなら、同意の上でしょう。追っ手の武将も妹君のお姿を見ては、手を下しかねるかと。","劉備に従って出られたのなら、同意の上でしょう。追っ手の武将も妹君のお姿を見ては、手を下しかねるかと。","孫権",},
        [6121] = {11204,"孫権","俺の佩剣を授ける。お前達はこの剣を持って、妹と劉備の首を取ってこい。命令を違えたら斬るぞ！","俺の佩剣を授ける。お前達はこの剣を持って、妹と劉備の首を取ってこい。命令を違えたら斬るぞ！","程普",},
        [6122] = {11205,"徐盛","劉備、さっさと馬を下りてお縄にかかれ！周都督の命令により、ここで待ち受けていたぞ！","劉備、さっさと馬を下りてお縄にかかれ！周都督の命令により、ここで待ち受けていたぞ！","",},
        [6123] = {11205,"劉備","前は待ち伏せ、後ろは追っ手だ。進退窮まったな。どうしたものか……","前は待ち伏せ、後ろは追っ手だ。進退窮まったな。どうしたものか……","趙雲",},
        [6124] = {11205,"趙雲","落ち着いてください。軍師の三つの妙計を錦の袋に入れて預かっております。これまで二つを開いて、いずれも当たりました。","落ち着いてください。軍師の三つの妙計を錦の袋に入れて預かっております。これまで二つを開いて、いずれも当たりました。","劉備",},
        [6125] = {11205,"趙雲","三つめの袋がまだここにあります。今こそ開いて見ましょう。","三つめの袋がまだここにあります。今こそ開いて見ましょう。","劉備",},
        [6126] = {11206,"劉備","そもそもこの結婚は呉侯と周瑜が共謀して、お前を餌に私を釣ったものだったのだ。私を呉に封じ込め、荊州を奪おうという企みだった。","そもそもこの結婚は呉侯と周瑜が共謀して、お前を餌に私を釣ったものだったのだ。私を呉に封じ込め、荊州を奪おうという企みだった。","孫尚香",},
        [6127] = {11206,"劉備","私がその危険を怖れずやって来たのは、お前ならきっと同情してくれると思ったからだ。","私がその危険を怖れずやって来たのは、お前ならきっと同情してくれると思ったからだ。","孫尚香",},
        [6128] = {11206,"劉備","このたび呉侯が私を害するつもりだと聞いたので、荊州の危急にかこつけて、帰る計を案じた。","このたび呉侯が私を害するつもりだと聞いたので、荊州の危急にかこつけて、帰る計を案じた。","孫尚香",},
        [6129] = {11206,"劉備","お前を思い切れずに、ここまで連れて来たが――","お前を思い切れずに、ここまで連れて来たが――","孫尚香",},
        [6130] = {11206,"劉備","今後ろから呉侯の追っ手が迫り、前には周瑜の待ち伏せの兵がいる。","今後ろから呉侯の追っ手が迫り、前には周瑜の待ち伏せの兵がいる。","孫尚香",},
        [6131] = {11206,"劉備","この難儀を救えるのはお前しかいない。さもなければ私は今ここで自害して、せめてもの恩返しをしよう。","この難儀を救えるのはお前しかいない。さもなければ私は今ここで自害して、せめてもの恩返しをしよう。","孫尚香",},
        [6132] = {11206,"孫尚香","兄があたしを妹とも思わないなら、あたしだって兄の顔を見るのも嫌よ。この危機はきっと切り抜けてみせるわ！","兄があたしを妹とも思わないなら、あたしだって兄の顔を見るのも嫌よ。この危機はきっと切り抜けてみせるわ！","劉備",},
        [6133] = {11207,"孫尚香","お前たち二人は、謀反人なの?","お前たち二人は、謀反人なの?","徐盛",},
        [6134] = {11207,"徐盛","まさか！周都督の命令で、ここに陣を張って劉備を待ち受けていたのです。","まさか！周都督の命令で、ここに陣を張って劉備を待ち受けていたのです。","孫尚香",},
        [6135] = {11207,"孫尚香","へえ、周瑜は主君に楯突くつもりなんだ。わが呉が何か悪いことでもしたかしら！","へえ、周瑜は主君に楯突くつもりなんだ。わが呉が何か悪いことでもしたかしら！","徐盛",},
        [6136] = {11207,"孫尚香","劉備様は大漢の皇叔で、今はあたしの夫よ。母にも兄にも、荊州に戻ると申し上げて来たわ。","劉備様は大漢の皇叔で、今はあたしの夫よ。母にも兄にも、荊州に戻ると申し上げて来たわ。","徐盛",},
        [6137] = {11207,"孫尚香","それなのに山陰に兵を集めて行く手を阻むなんて。強盗でもしようというの?","それなのに山陰に兵を集めて行く手を阻むなんて。強盗でもしようというの?","徐盛",},
        [6138] = {11207,"徐盛","とんでもない！何とぞ、お怒りをお鎮めください。これは我ら二人のせいではなく、周都督の命令なのです。","とんでもない！何とぞ、お怒りをお鎮めください。これは我ら二人のせいではなく、周都督の命令なのです。","孫尚香",},
        [6139] = {11207,"孫尚香","へえ～周瑜は怖いけど、あたしは怖くないっていうんだ。","へえ～周瑜は怖いけど、あたしは怖くないっていうんだ。","徐盛",},
        [6140] = {11207,"孫尚香","もし周瑜があんたたちを罰するっていうなら、あたしが周瑜を叩き斬ってやるわ。","もし周瑜があんたたちを罰するっていうなら、あたしが周瑜を叩き斬ってやるわ。","徐盛",},
        [6141] = {11208,"孫尚香","あんたたちごときが、あたしと兄を仲違いさせようというの?！あたしは嫁いだ身よ、駆け落ちでも何でもないわ。","あんたたちごときが、あたしと兄を仲違いさせようというの?！あたしは嫁いだ身よ、駆け落ちでも何でもないわ。","陳武",},
        [6142] = {11208,"孫尚香","まして母上のお許しをいただいているのよ。","まして母上のお許しをいただいているのよ。","陳武",},
        [6143] = {11208,"孫尚香","なぜ止めるの?たとえ兄上がここへみえても、挨拶して行くだけよ。あんたたちは、殺そうとでも言うの?","なぜ止めるの?たとえ兄上がここへみえても、挨拶して行くだけよ。あんたたちは、殺そうとでも言うの?","陳武",},
        [6144] = {11208,"陳武","(……確かにいつになってもご兄妹には違いない。それに呉国太がお許しになったのであれば、孝行な呉侯が母君の意向に逆らうはずはない。","(……確かにいつになってもご兄妹には違いない。それに呉国太がお許しになったのであれば、孝行な呉侯が母君の意向に逆らうはずはない。","孫尚香",},
        [6145] = {11208,"陳武","明日になったら御意が変わり、我々の落ち度になるだけだ。ここは見逃したほうがいい。)","明日になったら御意が変わり、我々の落ち度になるだけだ。ここは見逃したほうがいい。)","孫尚香",},
        [6146] = {11209,"周泰","劉備には追いついたか?なぜ手を下さん?","劉備には追いついたか?なぜ手を下さん?","徐盛",},
        [6147] = {11209,"徐盛","孫夫人がおられて、しかも呉国太のお許しだと言われるので、軽率なまねはできなかった。","孫夫人がおられて、しかも呉国太のお許しだと言われるので、軽率なまねはできなかった。","周泰",},
        [6148] = {11209,"周泰","それを呉侯も案じられて、この通り御剣を賜ったのだ。まず妹を斬って、それから劉備を討てと。","それを呉侯も案じられて、この通り御剣を賜ったのだ。まず妹を斬って、それから劉備を討てと。","徐盛",},
        [6149] = {11209,"周泰","この命令に背けば斬るとのことだぞ。","この命令に背けば斬るとのことだぞ。","徐盛",},
        [6150] = {11209,"徐盛","な、なんと……ならばすぐ追おう！","な、なんと……ならばすぐ追おう！","周泰",},
        [6151] = {11209,"徐盛","劉備一行は大半が徒歩で、まだそう遠くには行っていまい。我ら四人で追撃し、先に追いついた者が有無を言わさず斬るのだ。","劉備一行は大半が徒歩で、まだそう遠くには行っていまい。我ら四人で追撃し、先に追いついた者が有無を言わさず斬るのだ。","周泰",},
        [6152] = {11209,"劉備","まずい、奴らがまた追いついて来たか。かくなる上は……","まずい、奴らがまた追いついて来たか。かくなる上は……","プレイヤー",},
        [6153] = {11209,"主君","兄者は先に行ってください。ここは私に任せて！","兄さんは先に行ってください。ここは私に任せて！","劉備",},
        [6154] = {11209,"劉備","義弟も気をつけてくれよ！","義妹も気をつけてくれよ！","プレイヤー",},
        [6155] = {11301,"劉備","連日の逃避行で、人も馬も疲れ果てたところへ、また追っ手が来た。もはやこれまでか。","連日の逃避行で、人も馬も疲れ果てたところへ、また追っ手が来た。もはやこれまでか。","",},
        [6156] = {11301,"主君","兄者、諦めるな。追っ手は私に任せて、兄者は荊州に帰るんだ。","兄さん、諦めないで。追っ手は私に任せて、兄さんは荊州に帰るんです。","劉備",},
        [6157] = {11301,"趙雲","おお、天の助けだ、あそこに船が！早く乗って、向こう岸へ渡り、手立てを考えよう。","おお、天の助けだ、あそこに船が！早く乗って、向こう岸へ渡り、手立てを考えよう。","",},
        [6158] = {11302,"諸葛亮","おかえりなさいませ！諸葛亮、これにてお待ちしておりました。……ご主君がお着きです、船を出しなさい。","おかえりなさいませ！諸葛亮、これにてお待ちしておりました。……ご主君がお着きです、船を出しなさい。","",},
        [6159] = {11302,"周泰","待て、劉備、止まれ！","待て、劉備、止まれ！","",},
        [6160] = {11302,"主君","追っても無駄だ！船はもうこんなに離れてる。追いつけやしないさ。","追っても無駄よ！船はもうこんなに離れてる。追いつけるものですか。","周泰",},
        [6161] = {11302,"諸葛亮","とうに見抜いておりましたよ。帰って周瑜殿に伝えなさい。二度と美人計のような下策は使わぬようにとね。","とうに見抜いておりましたよ。帰って周瑜殿に伝えなさい。二度と美人計のような下策は使わぬようにとね。","周泰",},
        [6162] = {11303,"周瑜","フン、我らに船がないとでも思うのか?わが呉の軍船がここに来たからには、もう逃がしはせんぞ！","フン、我らに船がないとでも思うのか?わが呉の軍船がここに来たからには、もう逃がしはせんぞ！","",},
        [6163] = {11303,"諸葛亮","北岸に船をつけなさい。船を捨てて陸路を取りましょう。","北岸に船をつけなさい。船を捨てて陸路を取りましょう。","",},
        [6164] = {11303,"周瑜","こちらも陸に上がって追撃するぞ。劉備一行はすぐ先だ。追え！","こちらも陸に上がって追撃するぞ。劉備一行はすぐ先だ。追え！","",},
        [6165] = {11303,"関羽","関羽ここにあり！周瑜よ、わしと手合わせしてみるか?","関羽ここにあり！周瑜よ、わしと手合わせしてみるか?","周瑜",},
        [6166] = {11303,"周瑜","くっ、備えがあったか！退け！","くっ、備えがあったか！退け！","",},
        [6167] = {11304,"主君","周瑜の計略は出尽くした。やはり孔明殿のほうが一枚上手だな。ふふっ、周瑜、見事な策だな、夫人は取られ、兵は討たれ！","周瑜の計略は出尽くしたようね。やはり孔明殿のほうが一枚上手ね。ふふっ、周瑜、見事な策ね、夫人は取られ、兵は討たれ！","",},
        [6168] = {11304,"周瑜","おのれ、言わせておけば……！陸に戻って、もう一戦してやる！","おのれ、言わせておけば……！陸に戻って、もう一戦してやる！","",},
        [6169] = {11304,"黄蓋","都督、怒りをお鎮めくだされ！傷がまだ癒えておりませぬぞ！","都督、怒りをお鎮めくだされ！傷がまだ癒えておりませぬぞ！","周瑜",},
        [6170] = {11304,"周瑜","まんまと裏をかかれた。呉侯に合わせる顔がない！ゲフッ……(血を吐いて意識を失う)","まんまと裏をかかれた。呉侯に合わせる顔がない！ゲフッ……(血を吐いて意識を失う)","黄蓋",},
        [6171] = {11401,"張星彩","劉備様が孫夫人まで連れて呉から無事に帰って来られたのは、ご主君様のおかげね！","劉備様が孫夫人まで連れて呉から無事に帰って来られたのは、ご主君様のおかげね！","プレイヤー",},
        [6172] = {11401,"主君","とんでもない。劉備の兄者の人柄が孫夫人を魅了したんだよ。","とんでもない。劉備兄さんの人柄が孫夫人を魅了したのよ。","張星彩",},
        [6173] = {11401,"張星彩","呉もこれで落ち着くといいんだけど。前に曹操軍と戦って、だいぶ気勢がそがれちゃったし。","呉もこれで落ち着くといいんだけど。前に曹操軍と戦って、だいぶ気勢がそがれちゃったし。","プレイヤー",},
        [6174] = {11401,"主君","そういえば、曹操はどうなったんだろう。様子を見に行ってみるか。","そういえば、曹操はどうなったのかしら。様子を見に行ってみましょうか。","張星彩",},
        [6175] = {11401,"張星彩","う～ん……あまり会いたくないんだけど……ご主君様が行くならついて行くわ！","う～ん……あまり会いたくないんだけど……ご主君様が行くならついて行くわ！","プレイヤー",},
        [6176] = {11402,"曹操","赤壁の戦いで我が軍はだいぶ勢いがそがれてしまった……実に口惜しい。","赤壁の戦いで我が軍はだいぶ勢いがそがれてしまった……実に口惜しい。","プレイヤー",},
        [6177] = {11402,"主君","曹操兄はどうして破れたと思いますか?少数の孫・劉連合軍に。","曹操兄さんはどうして破れたと思いますか?少数の孫・劉連合軍に。","曹操",},
        [6178] = {11402,"曹操","お前はどう思うのだ、若き英雄よ。","お前はどう思うのだ、若き英雄よ。","プレイヤー",},
        [6179] = {11402,"主君","馬に乗らず、船を使っての戦は、呉の得意とするところ。曹操兄の軍隊は北方出身の兵が多くて、","馬に乗らず、船を使っての戦は、呉の得意とするところ。曹操兄さんの軍隊は北方出身の兵が多くて、","曹操",},
        [6180] = {11402,"主君","水上の戦には慣れておらず、さらに船同士を鉄の鎖で繋いでしまったから移動が不便でした。これが一つ。","水上の戦には慣れておらず、さらに船同士を鉄の鎖で繋いでしまったから移動が不便でした。これが一つ。","曹操",},
        [6181] = {11402,"主君","兵士たちは北から南に遠征して、長江に着いたころには、もう力が尽き果てていました。","兵士たちは北から南に遠征して、長江に着いたころには、もう力が尽き果てていました。","曹操",},
        [6182] = {11402,"主君","加えて南方の風土に馴染めず、戦闘力が大幅に低下していました。これが二つ。","加えて南方の風土に馴染めず、戦闘力が大幅に低下していました。これが二つ。","曹操",},
        [6183] = {11402,"曹操","そんなことはとうにわかっておる。だから兵の調練に力を入れ、新たに軍の態勢を立て直し、来たるべき再戦に備えておるのだ。","そんなことはとうにわかっておる。だから兵の調練に力を入れ、新たに軍の態勢を立て直し、来たるべき再戦に備えておるのだ。","プレイヤー",},
        [6184] = {11402,"主君","いえいえ、もう一つ、もっと大事な原因があります。曹操兄は知っておくべきです。","いえいえ、もう一つ、もっと大きな要因があります。曹操兄さんは知っておくべきです。","曹操",},
        [6185] = {11402,"曹操","ほう、詳しく聞かせてもらおうか。","ほう、詳しく聞かせてもらおうか。","プレイヤー",},
        [6186] = {11403,"主君","曹操兄の兵力は強大で、北方を統一し、南進してまた着々と勝利をおさめています。向かうところ敵無しです。","曹操兄さんの兵力は強大で、北方を統一し、南進してまた着々と勝利をおさめています。向かうところ敵無しです。","曹操",},
        [6187] = {11403,"主君","だからこそ、兵士たちは少し傲慢になってませんか?自分たちが負けるはずないと思っているのでは?","だからこそ、兵士たちは少し傲慢になってませんか?自分たちが負けるはずないと思っているのでは?","曹操",},
        [6188] = {11403,"曹操","ふむ、言われてみれば……","ふむ、言われてみれば……","プレイヤー",},
        [6189] = {11403,"主君","いっぽう孫権・劉備側は存亡に関わるので、何をするにも慎重ですし、将士の団結は固く、兵士もよく命令に従います。","いっぽう孫権・劉備側は存亡に関わるので、何をするにも慎重ですし、将士の団結は固く、兵士もよく命令に従います。","曹操",},
        [6190] = {11403,"曹操","なるほど、確かに言う通りだ。","なるほど、確かに言う通りだ。","プレイヤー",},
        [6191] = {11404,"曹操","では今の情勢について、どう見ている?","では今の情勢について、どう見ている?","プレイヤー",},
        [6192] = {11404,"主君","天下の情勢はもう定まっていますから、曹操兄が冒険をする必要はありません。","天下の情勢はもう定まっていますから、曹操兄さんが冒険をする必要はありません。","曹操",},
        [6193] = {11404,"主君","まずは領内を安定させ、発展させましょう。拡張を考えるのはその後です。","まずは領内を安定させ、発展させましょう。拡張を考えるのはその後です。","曹操",},
        [6194] = {11404,"曹操","どうやって発展させる?","どうやって発展させる?","プレイヤー",},
        [6195] = {11404,"主君","天地の間、人をもって貴しとなす。人材が全てです。家柄や出自の制限を無くして、","天地の間、人をもって貴しとなす。人材が全てです。家柄や出自の制限を無くして、","曹操",},
        [6196] = {11404,"主君","「ただ才のみこれ挙げよ」という精神で能力のある人を広く受け入れないと、ともに大業をなそうという人は見つかりませんよ。","「ただ才のみこれ挙げよ」という精神で能力のある人を広く受け入れないと、ともに大業をなそうという人は見つかりませんよ。","曹操",},
        [6197] = {11404,"曹操","よし、求賢令を出す。広く天下の人材を招き、用いるのだ。","よし、求賢令を出す。広く天下の人材を招き、用いるのだ。","プレイヤー",},
        [6198] = {11501,"曹操","銅雀台が完成したぞ。文武百官を招いて落成祝いをするから、若き英雄も一緒に来るといい。","銅雀台が完成したぞ。文武百官を招いて落成祝いをするから、若き英雄も一緒に来るといい。","プレイヤー",},
        [6199] = {11501,"主君","ぜひとも。賑やかなのは大好きですよ！","ぜひとも。賑やかなのは大好きです！","曹操",},
        [6200] = {11502,"曹操","銅雀台は漳河を臨み、左の宮殿は玉龍台、右の宮殿は金鳳台という。","銅雀台は漳河を臨み、左の宮殿は玉龍台、右の宮殿は金鳳台という。","プレイヤー",},
        [6201] = {11502,"曹操","それぞれ高さは十丈で、二つの橋を渡って行き来できる。さらに周りを豪華できらびやかな建物が取り囲んでいるだろう。","それぞれ高さは十丈で、二つの橋を渡って行き来できる。さらに周りを豪華できらびやかな建物が取り囲んでいるだろう。","プレイヤー",},
        [6202] = {11502,"主君","実に素晴らしいです。","実に素晴らしいですわ。","曹操",},
        [6203] = {11502,"主君","ご馳走は美味しいし、舞姫は綺麗ですし。しかし、何か物足りないような。体を動かしたくてむずむずする。","ご馳走は美味しいし、舞姫は綺麗ですし。でも、何か物足りないような。体を動かしたくてむずむずする。","曹操",},
        [6204] = {11502,"曹操","変わらぬな、お前は。ここにはわしの自慢の文官と武将が揃っているぞ。誰と手合わせしたい?","変わらぬな、お前は。ここにはわしの自慢の文官と武将が揃っているぞ。誰と手合わせしたい?","プレイヤー",},
        [6205] = {11502,"文聘","この文聘がお相手しましょう！揚威将軍のお名はつとに聞いております。ご指導たまわりたい！","この文聘がお相手しましょう！揚威将軍のお名はつとに聞いております。ご指導たまわりたい！","曹操",},
        [6206] = {11503,"張郃","お見事よ、揚威将軍！張郃もお手合わせ願いたいわ！","お見事よ、揚威将軍！張郃もお手合わせ願いたいわ！","プレイヤー",},
        [6207] = {11503,"主君","望むところ！","望むところ！","張郃",},
        [6208] = {11504,"夏侯淵","二人とも揚威将軍の威名に恐れをなして、全力を出せていないのではないか。俺も将軍と勝負してみたい。","二人とも揚威将軍の威名に恐れをなして、全力を出せていないのではないか。俺も将軍と勝負してみたい。","プレイヤー",},
        [6209] = {11504,"主君","いいですよ、遠慮なくかかって来てください！","いいですよ、遠慮なくかかって来てください！","夏侯淵",},
        [6210] = {11504,"夏侯淵","では将軍、覚悟されよ。夏侯淵 、見参！","では将軍、覚悟されよ。夏侯淵 、見参！","プレイヤー",},
        [6211] = {11505,"荀彧","揚威将軍の武芸は本当にお見事ですね。これだけ多くの大将に連勝するのですから。","揚威将軍の武芸は本当にお見事ですね。これだけ多くの大将に連勝するのですから。","プレイヤー",},
        [6212] = {11505,"曹操","わっはっは、いいぞ！さすがは若き英雄だ。さあ、一献受けてくれ！","わっはっは、いいぞ！さすがは若き英雄だ。さあ、一献受けてくれ！","プレイヤー",},
        [6213] = {11505,"主君","ありがたく頂戴します。","ありがたく頂戴します。","曹操",},
        [6214] = {11601,"曹操","さて、ここにある錦の戦袍を柳の枝にかけて、その下に的を置け。","さて、ここにある錦の戦袍を柳の枝にかけて、その下に的を置け。","",},
        [6215] = {11601,"曹操","百歩離れたところから中心の赤い円に射あてた者には、その錦袍を褒美として取らせる。外した者は罰杯を飲ませるぞ。","百歩離れたところから中心の赤い円に射あてた者には、その錦袍を褒美として取らせる。外した者は罰杯を飲ませるぞ。","",},
        [6216] = {11601,"曹休","では、私が！(一矢で的の中心を射る)","では、私が！(一矢で的の中心を射る)","",},
        [6217] = {11601,"曹操","うむ！見事だ！","うむ！見事だ！","",},
        [6218] = {11601,"曹休","ははっ、錦袍は私のものだな！","ははっ、錦袍は私のものだな！","",},
        [6219] = {11601,"文聘","なんの！錦袍は、我ら異姓の者にまず取らせていただきたい。","なんの！錦袍は、我ら異姓の者にまず取らせていただきたい。","",},
        [6220] = {11601,"文聘","ご一族より抜け駆けはいけませんぞ。ご覧あれ！(一矢で的の中心を射る)","ご一族より抜け駆けはいけませんぞ。ご覧あれ！(一矢で的の中心を射る)","",},
        [6221] = {11602,"文聘","早く錦袍をこちらに！","早く錦袍をこちらに！","",},
        [6222] = {11602,"曹洪","待て待て、曹休が先に射たのに、奪おうとは何事だ。俺が片を付けてやろう！(一矢で的の中心を射る)","待て待て、曹休が先に射たのに、奪おうとは何事だ。俺が片を付けてやろう！(一矢で的の中心を射る)","文聘",},
        [6223] = {11602,"曹洪","これなら文句はあるまい。","これなら文句はあるまい。","",},
        [6224] = {11602,"張郃","お待ちなさい！お三方の射法は、珍しくも何ともないわ。","お待ちなさい！お三方の射法は、珍しくも何ともないわ。","曹洪",},
        [6225] = {11602,"張郃","私の華麗なる技を見なさい！(身を翻し、後ろ向きになって、一矢で的の中心を射る)","私の華麗なる技を見なさい！(身を翻し、後ろ向きになって、一矢で的の中心を射る)","曹洪",},
        [6226] = {11602,"張郃","フッ……錦袍は私のものね！","フッ……錦袍は私のものね！","",},
        [6227] = {11603,"夏侯淵","後ろ手に射るくらい、大したことは無い。俺がど真ん中を奪ってやろう！(一矢が四本の矢の真ん中に突き刺さる)","後ろ手に射るくらい、大したことは無い。俺がど真ん中を奪ってやろう！(一矢が四本の矢の真ん中に突き刺さる)","張郃",},
        [6228] = {11603,"夏侯淵","俺がもらってよいな?","俺がもらってよいな?","",},
        [6229] = {11603,"徐晃","いやいや、この徐晃に譲ってもらおう！","いやいや、この徐晃に譲ってもらおう！","夏侯淵",},
        [6230] = {11603,"夏侯淵","お前の弓の腕で、どうやって俺から錦袍を奪おうというのだ?","お前の弓の腕で、どうやって俺から錦袍を奪おうというのだ?","",},
        [6231] = {11603,"徐晃","ふははは、的を射るのではないわ、直接いただくのよ！(一矢で錦袍がかけられた柳の枝を射切る)","ふははは、的を射るのではないわ、直接いただくのよ！(一矢で錦袍がかけられた柳の枝を射切る)","",},
        [6232] = {11604,"曹操","(大笑して)よしよし、お前達が錦袍一つのために顔を真っ赤にして争っていては、世の人々に笑われる。","(大笑して)よしよし、お前達が錦袍一つのために顔を真っ赤にして争っていては、世の人々に笑われる。","",},
        [6233] = {11604,"曹操","全員に蜀紅の錦を一匹ずつ賜ろう。それでいいな?","全員に蜀紅の錦を一匹ずつ賜ろう。それでいいな?","",},
        [6234] = {11604,"主君","武将たちは弓を競って武勇をあらわしました。","武将たちは弓を競って武勇をあらわしました。","曹操",},
        [6235] = {11604,"主君","博学揃いの文官たちも、この銅雀台に登ったからには、今日の盛会の記念となるような名作を作ってみてはいかがでしょう?","博学揃いの文官たちも、この銅雀台に登ったからには、今日の盛会の記念となるような名作を作ってみてはいかがでしょう?","曹操",},
        [6236] = {11604,"曹植","明らかなる后に従いて嬉しみ遊び、層なれる台に登りて情を娯しましむ。太いなる府の広く開くを見、聖き徳の営む所を観る。","明らかなる后に従いて嬉しみ遊び、層なれる台に登りて情を娯しましむ。太いなる府の広く開くを見、聖き徳の営む所を観る。","",},
        [6237] = {11604,"曹植","……我が皇家を翼佐け、かの四方を寧んぜん。天地の規量に同じかれ。日月の暉光に斉しかれ。","……我が皇家を翼佐け、かの四方を寧んぜん。天地の規量に同じかれ。日月の暉光に斉しかれ。","",},
        [6238] = {11604,"曹操","うむ！曹植はさすがわしの子だ。文才にあふれている。きっと大成するだろう。","うむ！曹植はさすがわしの子だ。文才にあふれている。きっと大成するだろう。","",},
        [6239] = {11701,"華歆","呉の使者として曹丞相にご報告に参りました。","呉の使者として曹丞相にご報告に参りました。","曹操",},
        [6240] = {11701,"華歆","劉備殿を荊州の牧にしたく、上奏しました。孫権様は妹君を劉備殿にめあわせ、漢上の九郡は大半が劉備殿に属しました。","劉備殿を荊州の牧にしたく、上奏しました。孫権様は妹君を劉備殿にめあわせ、漢上の九郡は大半が劉備殿に属しました。","曹操",},
        [6241] = {11701,"曹操","なに?劉備に荊州を取らせた上、孫権と姻戚関係になっただと?手を組んでわしに対抗しようというのか?","なに?劉備に荊州を取らせた上、孫権と姻戚関係になっただと?手を組んでわしに対抗しようというのか?","華歆",},
        [6242] = {11701,"程昱","丞相は矢弾が飛び交う千万の敵軍の中でも動じられませぬのに、劉備が荊州を得たとの知らせには、それほど驚きになりますか。","丞相は矢弾が飛び交う千万の敵軍の中でも動じられませぬのに、劉備が荊州を得たとの知らせには、それほど驚きになりますか。","曹操",},
        [6243] = {11701,"曹操","これが驚かずにおれるか。劉備は人中の龍だが、これまで水を得られずにいた。","これが驚かずにおれるか。劉備は人中の龍だが、これまで水を得られずにいた。","程昱",},
        [6244] = {11701,"曹操","いま荊州を得たとあっては、龍を大海に放ったようなものだぞ。","いま荊州を得たとあっては、龍を大海に放ったようなものだぞ。","程昱",},
        [6245] = {11702,"程昱","丞相は華歆の来意がおわかりですかな。","丞相は華歆の来意がおわかりですかな。","曹操",},
        [6246] = {11702,"曹操","いや……まさか計略だとでも?","いや……まさか計略だとでも?","程昱",},
        [6247] = {11702,"程昱","孫権はもともと劉備を嫌っておりますじゃ。攻めたいのは山々ながら、兵を出せば丞相にその虚を突かれるのを怖れておるのでしょう。","孫権はもともと劉備を嫌っておりますじゃ。攻めたいのは山々ながら、兵を出せば丞相にその虚を突かれるのを怖れておるのでしょう。","曹操",},
        [6248] = {11702,"程昱","華歆を使いとして劉備を推薦したのは、劉備の心を和らげ、丞相のお手出しを封じるつもりですじゃ。","華歆を使いとして劉備を推薦したのは、劉備の心を和らげ、丞相のお手出しを封じるつもりですじゃ。","曹操",},
        [6249] = {11702,"曹操","なるほど。","なるほど。","程昱",},
        [6250] = {11702,"程昱","一計がございますじゃ。孫権と劉備を相争わせ、丞相がその隙に手を下されましたら、一挙に両方の敵を破れますぞ。","一計がございますじゃ。孫権と劉備を相争わせ、丞相がその隙に手を下されましたら、一挙に両方の敵を破れますぞ。","曹操",},
        [6251] = {11703,"曹操","その妙計とは?","その妙計とは?","程昱",},
        [6252] = {11703,"程昱","呉が柱と頼むのは周瑜ですじゃ。","呉が柱と頼むのは周瑜ですじゃ。","曹操",},
        [6253] = {11703,"程昱","丞相から奏上して、周瑜を南郡の太守に封じ、程普を江夏の太守とします。華歆はしばし朝廷に留め置いて重用します。","丞相から奏上して、周瑜を南郡の太守に封じ、程普を江夏の太守とします。華歆はしばし朝廷に留め置いて重用します。","曹操",},
        [6254] = {11703,"程昱","南郡も江夏も劉備が領有している地ですから、周瑜と劉備は仇敵となります。その同士討ちにつけこめば宜しいのですじゃ。","南郡も江夏も劉備が領有している地ですから、周瑜と劉備は仇敵となります。その同士討ちにつけこめば宜しいのですじゃ。","曹操",},
        [6255] = {11703,"曹操","程昱の進言は、まさにわしの思い通りだ。","程昱の進言は、まさにわしの思い通りだ。","程昱",},
        [6256] = {11704,"張星彩","ご主君様、程昱が曹操に進言したそうだけど、策略よ。劉備様と孫権を仲違いさせるのが狙いだわ。","ご主君様、程昱が曹操に進言したそうだけど、策略よ。劉備様と孫権を仲違いさせるのが狙いだわ。","プレイヤー",},
        [6257] = {11704,"主君","ああ、私も聞いた。急いで荊州に帰って、このことを劉備の兄者に伝えよう。","ええ、私も聞いた。急いで荊州に帰って、このことを劉備兄さんに伝えましょう。","張星彩",},
        [6258] = {11704,"張星彩","そろそろ家が恋しくなってきたしね。","そろそろ家が恋しくなってきたしね。","プレイヤー",},
        [6259] = {11801,"孫権","前におぬしが保証人となって荊州を劉備に貸し与えたが、延び延びにして返さない。いつまで待てばいいのだ。","前におぬしが保証人となって荊州を劉備に貸し与えたが、延び延びにして返さない。いつまで待てばいいのだ。","魯粛",},
        [6260] = {11801,"魯粛","証文に書いてあります通り、蜀を取ったら返すと……","証文に書いてあります通り、蜀を取ったら返すと……","孫権",},
        [6261] = {11801,"孫権","蜀を取ると言うばかりで、今まで兵を動かした気配もないぞ。老人になるまで待てというのか！","蜀を取ると言うばかりで、今まで兵を動かした気配もないぞ。老人になるまで待てというのか！","魯粛",},
        [6262] = {11801,"魯粛","わかりました。もう一度談判に行かせてください。","わかりました。もう一度談判に行かせてください。","孫権",},
        [6263] = {11802,"魯粛","このたび呉侯の命を受けて、荊州の件で参りました。劉備殿に長らくお貸ししましたが、お返しがございません。","このたび呉侯の命を受けて、荊州の件で参りました。劉備殿に長らくお貸ししましたが、お返しがございません。","劉備",},
        [6264] = {11802,"魯粛","いま両家はご婚姻により親族となられたのですから、早々にお引き渡し願いたい。","いま両家はご婚姻により親族となられたのですから、早々にお引き渡し願いたい。","劉備",},
        [6265] = {11802,"劉備","それは……うっ……ううう……","それは……うっ……ううう……","",},
        [6266] = {11802,"魯粛","なっ、なぜお泣きになるのです?","なっ、なぜお泣きになるのです?","劉備",},
        [6267] = {11802,"諸葛亮","当初、たしかに蜀を取れば返すとお約束しました。しかし、よくよく思い巡らせば、益州の劉璋殿はわが君にとって兄弟も同然。","当初、たしかに蜀を取れば返すとお約束しました。しかし、よくよく思い巡らせば、益州の劉璋殿はわが君にとって兄弟も同然。","魯粛",},
        [6268] = {11802,"諸葛亮","兵を起こしてその城を攻め取っては、世の人々から痛罵されましょう。","兵を起こしてその城を攻め取っては、世の人々から痛罵されましょう。","魯粛",},
        [6269] = {11802,"諸葛亮","さりとて蜀を取らないままに荊州をお返ししたら、身を置く所もありません。","さりとて蜀を取らないままに荊州をお返ししたら、身を置く所もありません。","魯粛",},
        [6270] = {11802,"諸葛亮","しかし返さないでは、義理の兄たる呉侯にも顔向けできない。進退窮まられ、苦しみ嘆いておられるのです。ううっ……","しかし返さないでは、義理の兄たる呉侯にも顔向けできない。進退窮まられ、苦しみ嘆いておられるのです。ううっ……","魯粛",},
        [6271] = {11802,"魯粛","お、お二方ともそんなに嘆かれますな。劉備殿もあまり思い悩まないでください。孔明殿とゆっくり相談しますから。","お、お二方ともそんなに嘆かれますな。劉備殿もあまり思い悩まないでください。孔明殿とゆっくり相談しますから。","諸葛亮",},
        [6272] = {11802,"諸葛亮","すみませんね、魯粛殿。帰って呉侯に会われたときは、この苦衷のご様子をどうぞ宜しくお伝えください。","すみませんね、魯粛殿。帰って呉侯に会われたときは、この苦衷のご様子をどうぞ宜しくお伝えください。","魯粛",},
        [6273] = {11802,"諸葛亮","そして今しばらくのご猶予をいただきたい。","そして今しばらくのご猶予をいただきたい。","魯粛",},
        [6274] = {11803,"周瑜","魯粛、また諸葛亮に一杯食わされましたね！","魯粛、また諸葛亮に一杯食わされましたね！","魯粛",},
        [6275] = {11803,"周瑜","劉備は劉表の厄介になっていた頃から荊州を狙っていたのです。蜀の劉璋に気兼ねなどするものですか。","劉備は劉表の厄介になっていた頃から荊州を狙っていたのです。蜀の劉璋に気兼ねなどするものですか。","魯粛",},
        [6276] = {11803,"周瑜","ならばこちらも一計を案じましょう。","ならばこちらも一計を案じましょう。","魯粛",},
        [6277] = {11803,"周瑜","この件は呉侯に復命せず、もう一度荊州に行って劉備にこう言うのです。孫・劉両家は婚姻によりもはや一家となったのだから――","この件は呉侯に復命せず、もう一度荊州に行って劉備にこう言うのです。孫・劉両家は婚姻によりもはや一家となったのだから――","魯粛",},
        [6278] = {11803,"周瑜","もし劉氏が蜀を取るに忍びないと言われるなら、わが呉より兵を出して蜀を取り、持参金代わりに進ぜよう。","もし劉氏が蜀を取るに忍びないと言われるなら、わが呉より兵を出して蜀を取り、持参金代わりに進ぜよう。","魯粛",},
        [6279] = {11803,"周瑜","しかし荊州はお返し願いたい、と。","しかし荊州はお返し願いたい、と。","魯粛",},
        [6280] = {11803,"魯粛","いかがなものでしょう。蜀までは遠いですし、そう簡単には取れますまい。","いかがなものでしょう。蜀までは遠いですし、そう簡単には取れますまい。","周瑜",},
        [6281] = {11803,"周瑜","ただの口実ですよ。荊州を攻め取るために、油断させておくのです。呉軍が蜀に攻め入るには、必ず荊州を通ります。","ただの口実ですよ。荊州を攻め取るために、油断させておくのです。呉軍が蜀に攻め入るには、必ず荊州を通ります。","魯粛",},
        [6282] = {11803,"周瑜","そのとき兵糧や軍用金を求めれば、劉備は軍の慰労に城を出てくるでしょう。その機に乗じて討つのです。","そのとき兵糧や軍用金を求めれば、劉備は軍の慰労に城を出てくるでしょう。その機に乗じて討つのです。","魯粛",},
        [6283] = {11804,"魯粛","荊州に行って話したところ、劉備も諸葛亮も大喜びで、城から出て軍の慰労の準備をしています。","荊州に行って話したところ、劉備も諸葛亮も大喜びで、城から出て軍の慰労の準備をしています。","周瑜",},
        [6284] = {11804,"周瑜","今度こそ、私の術中に落ちたな！荊州から出迎えは来たか?","今度こそ、私の術中に落ちたな！荊州から出迎えは来たか?","魯粛",},
        [6285] = {11804,"糜竺","劉備様の使いで参りました、糜竺です。準備はすべて整えてあります。","劉備様の使いで参りました、糜竺です。準備はすべて整えてあります。","周瑜",},
        [6286] = {11804,"糜竺","主君は荊州の城門の外でお待ちです。都督殿に一献差し上げたいとのこと。","主君は荊州の城門の外でお待ちです。都督殿に一献差し上げたいとのこと。","周瑜",},
        [6287] = {11804,"周瑜","ああ、充分なもてなしと礼にあずかりたいところだ。貴国のために遠征して来たのだから。","ああ、充分なもてなしと礼にあずかりたいところだ。貴国のために遠征して来たのだから。","糜竺",},
        [6288] = {11805,"周瑜","なぜ出迎えに会わぬのだ?我が軍はもう荊州の城下に着いたというのに。","なぜ出迎えに会わぬのだ?我が軍はもう荊州の城下に着いたというのに。","趙雲",},
        [6289] = {11805,"趙雲","皆の者、敵に警戒せよ！","皆の者、敵に警戒せよ！","周瑜",},
        [6290] = {11805,"周瑜","なんのマネだ?そちらの主君に代わって蜀を取りに来たのを、知らぬというのか?","なんのマネだ?そちらの主君に代わって蜀を取りに来たのを、知らぬというのか?","趙雲",},
        [6291] = {11805,"趙雲","都督の「道を借りて草を枯らす」の計など、孔明軍師はとうにお見通しだ。それゆえこの趙雲をここに留めおかれた。","都督の「道を借りて草を枯らす」の計など、孔明軍師はとうにお見通しだ。それゆえこの趙雲をここに留めおかれた。","周瑜",},
        [6292] = {11805,"趙雲","わが君が言われるには、劉璋は同じ漢王室の一族ゆえ、義に背いて蜀を取るには忍びない。","わが君が言われるには、劉璋は同じ漢王室の一族ゆえ、義に背いて蜀を取るには忍びない。","周瑜",},
        [6293] = {11805,"趙雲","もし呉が本当に蜀を取ったら、自分は天下の信義を失わないよう、山中に隠れると。","もし呉が本当に蜀を取ったら、自分は天下の信義を失わないよう、山中に隠れると。","周瑜",},
        [6294] = {11805,"周瑜","チッ……退け！","チッ……退け！","",},
        [6295] = {11806,"周瑜","劉備、諸葛亮め、人を侮るにもほどがある！私に蜀は取れないとでもいうのか?いいだろう、誓って取ってみせる！","劉備、諸葛亮め、人を侮るにもほどがある！私に蜀は取れないとでもいうのか?いいだろう、誓って取ってみせる！","",},
        [6296] = {11806,"周瑜の従者","報告します。諸葛亮の使いが書簡を持ってまいりました。","報告します。諸葛亮の使いが書簡を持ってまいりました。","周瑜",},
        [6297] = {11806,"周瑜","ふん、このうえ何をほざきたいのか見てみましょう。","ふん、このうえ何をほざきたいのか見てみましょう。","",},
        [6298] = {11806,"周瑜","「……貴殿が蜀を取るおつもりだと伺いましたが、無理でしょう。益州の民は強く、要害堅固の地で……」","「……貴殿が蜀を取るおつもりだと伺いましたが、無理でしょう。益州の民は強く、要害堅固の地で……」","",},
        [6299] = {11806,"周瑜","「……赤壁で敗れた曹操が、片時でも復讐を忘れるとお思いですか?今貴殿が遠征の兵を起こせば、","「……赤壁で敗れた曹操が、片時でも復讐を忘れるとお思いですか?今貴殿が遠征の兵を起こせば、","",},
        [6300] = {11806,"周瑜","曹操がその虚をついて攻めて来て、江南は粉砕されましょう。座視するに忍びず、特にお伝えする次第です。」……ふざけるな！","曹操がその虚をついて攻めて来て、江南は粉砕されましょう。座視するに忍びず、特にお伝えする次第です。」……ふざけるな！","",},
        [6301] = {11806,"周瑜","ゴフッ……(鮮血を吐く)国のため忠誠を尽くしたかったが……もはや天命は尽きたか……","ゴフッ……(鮮血を吐く)国のため忠誠を尽くしたかったが……もはや天命は尽きたか……","",},
        [6302] = {11806,"周瑜","天はこの世に周瑜を生みながら、なぜ孔明まで生んだのだ！(何度も叫び息を引き取る)","天はこの世に周瑜を生みながら、なぜ孔明まで生んだのだ！(何度も叫び息を引き取る)","",},
        [6303] = {11901,"孫権","なに?！周瑜が......あのような王佐の才を早くに亡くして、俺は誰に頼ればいいのだ......","なに?！周瑜が......あのような王佐の才を早くに亡くして、俺は誰に頼ればいいのだ......","",},
        [6304] = {11901,"孫権","遺書で魯粛を薦めておるのに従うが……(泣いて声にならない)","遺書で魯粛を薦めておるのに従うが……(泣いて声にならない)","",},
        [6305] = {11901,"魯粛","どうかお気を落とさずに。都督は生前私に引き継ぎ書を残してくれました。必ずや全力でご主君を輔佐いたします。","どうかお気を落とさずに。都督は生前私に引き継ぎ書を残してくれました。必ずや全力でご主君を輔佐いたします。","孫権",},
        [6306] = {11901,"孫権","魯粛を都督に任じ、軍事はすべて任せる。周瑜の柩を柴桑に送って手厚く葬らせるよう。","魯粛を都督に任じ、軍事はすべて任せる。周瑜の柩を柴桑に送って手厚く葬らせるよう。","魯粛",},
        [6307] = {11902,"諸葛亮","夜、天象を観ていたところ、将星が地に落ちました。周瑜が死んだようです。","夜、天象を観ていたところ、将星が地に落ちました。周瑜が死んだようです。","劉備",},
        [6308] = {11902,"劉備","周瑜が死んだら、あとはどうなるのか。","周瑜が死んだら、あとはどうなるのか。","諸葛亮",},
        [6309] = {11902,"諸葛亮","代わって軍を率いるのは魯粛でしょう。","代わって軍を率いるのは魯粛でしょう。","劉備",},
        [6310] = {11902,"諸葛亮","天象を見ますと、将星が東のほうに集まっております。弔問を口実に呉に参り、わが君を助けてくれる賢者を探しましょう。","天象を見ますと、将星が東のほうに集まっております。弔問を口実に呉に参り、わが君を助けてくれる賢者を探しましょう。","劉備",},
        [6311] = {11902,"劉備","呉の将士が孔明に危害を加えはしないだろうか。","呉の将士が孔明に危害を加えはしないだろうか。","諸葛亮",},
        [6312] = {11902,"諸葛亮","周瑜の存命中でも、怖れはしませんでした。彼が死んだ今、何の心配もありませんよ。","周瑜の存命中でも、怖れはしませんでした。彼が死んだ今、何の心配もありませんよ。","劉備",},
        [6313] = {11903,"周瑜の部下","なに?あの諸葛亮が弔問に来ただと?都督の仇討ちだ、殺してしまえ！","なに?あの諸葛亮が弔問に来ただと?都督の仇討ちだ、殺してしまえ！","",},
        [6314] = {11903,"魯粛","落ち着くのだ。諸葛亮が呉に来てから、どこへ行くにも趙雲が付き従っている。猛将の趙雲だぞ。軽はずみな行動は慎むのだ。","落ち着くのだ。諸葛亮が呉に来てから、どこへ行くにも趙雲が付き従っている。猛将の趙雲だぞ。軽はずみな行動は慎むのだ。","",},
        [6315] = {11903,"諸葛亮","(慟哭して)ああ周瑜殿、不幸にも夭折されてしまうとは！寿命は天の定めとはいえ、悼む心に変わりはありません。","(慟哭して)ああ周瑜殿、不幸にも夭折されてしまうとは！寿命は天の定めとはいえ、悼む心に変わりはありません。","",},
        [6316] = {11903,"諸葛亮","忠義の心と英才を持っておられた。命は三十六歳で尽きても、その名は千年のちまで残りましょう。","忠義の心と英才を持っておられた。命は三十六歳で尽きても、その名は千年のちまで残りましょう。","",},
        [6317] = {11903,"諸葛亮","君を悼み、断腸の思いです。わが心の悲しみが途絶えることはありません。","君を悼み、断腸の思いです。わが心の悲しみが途絶えることはありません。","",},
        [6318] = {11903,"諸葛亮","国のために忠誠を尽くし、冥土に旅立たれた。まだ霊魂がここにおられるなら、わが心をご覧ください。","国のために忠誠を尽くし、冥土に旅立たれた。まだ霊魂がここにおられるなら、わが心をご覧ください。","",},
        [6319] = {11903,"諸葛亮","天下にこれほどあなたの心がわかる者がおりましょうか。ああ痛ましいかな！","天下にこれほどあなたの心がわかる者がおりましょうか。ああ痛ましいかな！","",},
        [6320] = {11903,"魯粛","孔明殿は情が深い。周瑜殿は度量が狭くて、自ら死を招いたのだろう。","孔明殿は情が深い。周瑜殿は度量が狭くて、自ら死を招いたのだろう。","",},
        [6321] = {11904,"龐統","周瑜殿を憤死させておきながら、ぬけぬけと弔問に来るとは。呉は間抜け揃いだとでも思っているのか！","周瑜殿を憤死させておきながら、ぬけぬけと弔問に来るとは。呉は間抜け揃いだとでも思っているのか！","諸葛亮",},
        [6322] = {11904,"諸葛亮","ははは……まあそうおっしゃらず。","ははは……まあそうおっしゃらず。","龐統",},
        [6323] = {11904,"龐統","久しぶりだな、まあ思い出話でもしよう。","久しぶりだな、まあ思い出話でもしよう。","諸葛亮",},
        [6324] = {11904,"諸葛亮","呉侯はおそらく、あなたを重用しないでしょう。","呉侯はおそらく、あなたを重用しないでしょう。","龐統",},
        [6325] = {11904,"諸葛亮","不本意でしたら、荊州に来て劉備様を助けてもらえませんか。寛大で仁徳のある方ですから、あなたの志にも背かないと思います。","不本意でしたら、荊州に来て劉備様を助けてもらえませんか。寛大で仁徳のある方ですから、あなたの志にも背かないと思います。","龐統",},
        [6326] = {12001,"魯粛","私は凡庸の才で、周瑜殿に推挙されましたが、この職に堪え得ません。","私は凡庸の才で、周瑜殿に推挙されましたが、この職に堪え得ません。","孫権",},
        [6327] = {12001,"魯粛","そこでわが君の助けに一人お薦めしたい者がおります。天文から地理にまで通じており――","そこでわが君の助けに一人お薦めしたい者がおります。天文から地理にまで通じており――","孫権",},
        [6328] = {12001,"魯粛","謀略は古の管仲・楽毅にも劣らず、用兵は孫子・呉子にも肩を並べます。","謀略は古の管仲・楽毅にも劣らず、用兵は孫子・呉子にも肩を並べます。","孫権",},
        [6329] = {12001,"魯粛","周瑜殿もよく彼の意見を用いておられ、孔明もその知略に感服しております。ただいま江南におりますので、ぜひ重用されては。","周瑜殿もよく彼の意見を用いておられ、孔明もその知略に感服しております。ただいま江南におりますので、ぜひ重用されては。","孫権",},
        [6330] = {12001,"孫権","本当か?その者の名は?","本当か?その者の名は?","魯粛",},
        [6331] = {12001,"魯粛","襄陽の者で、姓を龐、名を統、道号を鳳雛先生と申します。","襄陽の者で、姓を龐、名を統、道号を鳳雛先生と申します。","孫権",},
        [6332] = {12001,"孫権","その名は俺も久しく聞いていた。今この地にいるというなら、すぐに招いて会いたい。","その名は俺も久しく聞いていた。今この地にいるというなら、すぐに招いて会いたい。","魯粛",},
        [6333] = {12002,"魯粛","こちらが鳳雛先生こと、龐統です。","こちらが鳳雛先生こと、龐統です。","孫権",},
        [6334] = {12002,"孫権","(何とも風采のあがらぬ男だ)貴殿は何の学問をしておられるのだ?","(何とも風采のあがらぬ男だ)貴殿は何の学問をしておられるのだ?","龐統",},
        [6335] = {12002,"龐統","特にこだわりはありません。臨機応変にやっております。","特にこだわりはありません。臨機応変にやっております。","孫権",},
        [6336] = {12002,"孫権","貴殿の才能と学問は、周瑜と比べてどうか?","貴殿の才能と学問は、周瑜と比べてどうか?","龐統",},
        [6337] = {12002,"龐統","さあ。周瑜殿と同列には言えませんな。","さあ。周瑜殿と同列には言えませんな。","孫権",},
        [6338] = {12002,"孫権","……ひとまず退がれ。用いるときがあれば、また呼び出す。","……ひとまず退がれ。用いるときがあれば、また呼び出す。","龐統",},
        [6339] = {12002,"魯粛","なぜ龐統を用いられないのですか?","なぜ龐統を用いられないのですか?","孫権",},
        [6340] = {12002,"孫権","ふん...あれはただの変人だ。用いたところで役には立つまい。","ふん...あれはただの変人だ。用いたところで役には立つまい。","魯粛",},
        [6341] = {12003,"魯粛","貴公を推挙したのですが、呉侯に用いられないと言われ、私には何ともできません。しばらく辛抱してお待ちください。","貴公を推挙したのですが、呉侯に用いられないと言われ、私には何ともできません。しばらく辛抱してお待ちください。","龐統",},
        [6342] = {12003,"龐統","いやはや……","いやはや……","魯粛",},
        [6343] = {12003,"魯粛","もしや、呉を去るおつもりですか?","もしや、呉を去るおつもりですか?","龐統",},
        [6344] = {12003,"魯粛","貴公を推挙したのですが、呉侯に用いられないと言われ、私には何ともできません。しばらく辛抱してお待ちください。","貴公を推挙したのですが、呉侯に用いられないと言われ、私には何ともできません。しばらく辛抱してお待ちください。","龐統",},
        [6345] = {12003,"龐統","荊州の劉備殿のところに行こうかと。","荊州の劉備殿のところに行こうかと。","魯粛",},
        [6346] = {12003,"魯粛","それでしたら私から推薦状を書きましょう。","それでしたら私から推薦状を書きましょう。","龐統",},
        [6347] = {12003,"魯粛","貴公が劉備殿を輔佐されるなら、孫・劉両家が争うこともなく、力を合わせて曹操を破れましょう。","貴公が劉備殿を輔佐されるなら、孫・劉両家が争うこともなく、力を合わせて曹操を破れましょう。","龐統",},
        [6348] = {12003,"龐統","それがかねてよりの志だよ。","それがかねてよりの志だよ。","魯粛",},
        [6349] = {12004,"劉備","遠路はるばるご苦労だった。","遠路はるばるご苦労だった。","龐統",},
        [6350] = {12004,"龐統","劉備殿が人材を求めておられると承り、参上しました。","劉備殿が人材を求めておられると承り、参上しました。","劉備",},
        [6351] = {12004,"劉備","ありがたいのだが、荊州もまずまず落ち着いて、これという職も空いていない。","ありがたいのだが、荊州もまずまず落ち着いて、これという職も空いていない。","龐統",},
        [6352] = {12004,"劉備","ここから百三十里ほど離れた耒陽という県で県令の職が空いているから、そこを任せよう。","ここから百三十里ほど離れた耒陽という県で県令の職が空いているから、そこを任せよう。","龐統",},
        [6353] = {12004,"龐統","へっ、劉備殿の待遇もこの程度か。しかし今日は孔明もいないし、また後日じっくり話すとしよう。","へっ、劉備殿の待遇もこの程度か。しかし今日は孔明もいないし、また後日じっくり話すとしよう。","劉備",},
        [6354] = {12005,"張飛","龐統とかいう野郎、耒陽県の県令を任されておきながら、政務をほっぽり出してやがるみてえだな。なめやがって！","龐統とかいう野郎、耒陽県の県令を任されておきながら、政務をほっぽり出してやがるみてえだな。なめやがって！","",},
        [6355] = {12005,"龐統","百里そこそこの小さな県の政務など、決裁に手間はかかりません。しばらくお待ちください。すぐに片づけましょう。","百里そこそこの小さな県の政務など、決裁に手間はかかりません。しばらくお待ちください。すぐに片づけましょう。","張飛",},
        [6356] = {12006,"龐統","(百日あまり滞っていた事務を全て処理する)ほっぽり出した政務というのはどこでしょうな。","(百日あまり滞っていた事務を全て処理する)ほっぽり出した政務というのはどこでしょうな。","張飛",},
        [6357] = {12006,"龐統","曹操、孫権のことかな?だとしても私の目にはしっかり見えております。こんな田舎の県の問題、どうということもない。","曹操、孫権のことかな?だとしても私の目にはしっかり見えております。こんな田舎の県の問題、どうということもない。","張飛",},
        [6358] = {12006,"張飛","先生はすげえ人だ。お見それしやした。兄貴へはオレから強く推挙しておくぜ。","先生はすげえ人だ。お見それしやした。兄貴へはオレから強く推挙しておくぜ。","龐統",},
        [6359] = {12006,"龐統","いや、実はここに魯粛殿に書いていただいた推薦状があるんですわ。","いや、実はここに魯粛殿に書いていただいた推薦状があるんですわ。","張飛",},
        [6360] = {12006,"張飛","ええっ！?最初に兄貴に会ったとき、なんですぐそれを出さねえんだよ?","ええっ！?最初に兄貴に会ったとき、なんですぐそれを出さねえんだよ?","龐統",},
        [6361] = {12006,"龐統","すぐに出したら、推薦状を頼りに扶持にありつこうとしているみたいでしょう?","すぐに出したら、推薦状を頼りに扶持にありつこうとしているみたいでしょう?","張飛",},
        [6362] = {12006,"劉備","そんな大賢人を粗末に扱ったのは、私の過ちだ。張飛が教えてくれなかったら、危うく失うところだった。","そんな大賢人を粗末に扱ったのは、私の過ちだ。張飛が教えてくれなかったら、危うく失うところだった。","龐統",},
        [6363] = {12006,"劉備","むかし司馬徽殿から、伏龍・鳳雛の二人のうち一人でも得られたら、天下は安定するだろうと言われた。","むかし司馬徽殿から、伏龍・鳳雛の二人のうち一人でも得られたら、天下は安定するだろうと言われた。","龐統",},
        [6364] = {12006,"劉備","今や二人とも来てくれたからには、漢王室の再興も目の前だ。","今や二人とも来てくれたからには、漢王室の再興も目の前だ。","龐統",},
        [6365] = {12101,"馬騰","帝より密勅を授かってからというもの、逆賊・曹操を討とうと劉備らと謀ったが、不幸にも董承は死に、劉備も敗戦を重ねている。","帝より密勅を授かってからというもの、逆賊・曹操を討とうと劉備らと謀ったが、不幸にも董承は死に、劉備も敗戦を重ねている。","",},
        [6366] = {12101,"馬騰","このたび曹操がふいに詔を降してわしを征南将軍に任じ、孫権の討伐に行けという。どうしたものだろうな。","このたび曹操がふいに詔を降してわしを征南将軍に任じ、孫権の討伐に行けという。どうしたものだろうな。","",},
        [6367] = {12101,"馬岱","曹操の本心は測りがたいです。叔父上を都に召し出し、害する気かもしれません。","曹操の本心は測りがたいです。叔父上を都に召し出し、害する気かもしれません。","馬騰",},
        [6368] = {12101,"馬超","ならば俺が西涼の兵を残らず率いて、父上のお供をしましょう。許昌に攻め入り、天下の悪党を滅ぼしましょう！","ならば俺が西涼の兵を残らず率いて、父上のお供をしましょう。許昌に攻め入り、天下の悪党を滅ぼしましょう！","馬騰",},
        [6369] = {12101,"馬騰","いや、お前は残って西涼を守れ。馬休と馬岱はわしと一緒に来い。曹操もお前が西涼におれば、わしに害を加えることはあるまい。","いや、お前は残って西涼を守れ。馬休と馬岱はわしと一緒に来い。曹操もお前が西涼におれば、わしに害を加えることはあるまい。","馬超",},
        [6370] = {12101,"馬超","はっ、では父上もお気をつけて。うかつに都に入らず、動静をよく見極めて対処してください。","はっ、では父上もお気をつけて。うかつに都に入らず、動静をよく見極めて対処してください。","馬騰",},
        [6371] = {12102,"黄奎","曹操が将軍に城内に入って天子に謁見せよというのは、企みがあるにちがいない。","曹操が将軍に城内に入って天子に謁見せよというのは、企みがあるにちがいない。","馬騰",},
        [6372] = {12102,"黄奎","明日曹操が閲兵のために城から出てくるのを待って殺そう。それで大事は成就する。","明日曹操が閲兵のために城から出てくるのを待って殺そう。それで大事は成就する。","馬騰",},
        [6373] = {12102,"馬騰","本心を打ち明けてくれて感謝する。","本心を打ち明けてくれて感謝する。","黄奎",},
        [6374] = {12103,"李春香","世間の人は劉備は仁徳の人で、曹操は奸雄だと言っておりますが、そうなのですか?","世間の人は劉備は仁徳の人で、曹操は奸雄だと言っておりますが、そうなのですか?","黄奎",},
        [6375] = {12103,"黄奎","お前のような女子でさえ正邪をわかっておるのだから、私がわきまえぬわけがない。曹操を殺してやりたいのだ！","お前のような女子でさえ正邪をわかっておるのだから、私がわきまえぬわけがない。曹操を殺してやりたいのだ！","李春香",},
        [6376] = {12103,"李春香","こ、殺すですって?どんなお手立てで?","こ、殺すですって?どんなお手立てで?","黄奎",},
        [6377] = {12103,"黄奎","もう馬将軍と手はずは整えてあるのだ。明日城外で閲兵のときに討つ。","もう馬将軍と手はずは整えてあるのだ。明日城外で閲兵のときに討つ。","李春香",},
        [6378] = {12103,"李春香","まあ……そうでございましたか……(急いで曹操に密告する)","まあ……そうでございましたか……(急いで曹操に密告する)","黄奎",},
        [6379] = {12104,"曹操","ふん、馬将軍がこうも大胆だったとはな。今日わしが城を出て閲兵するさいに殺す計画があると、密告があったのだ。","ふん、馬将軍がこうも大胆だったとはな。今日わしが城を出て閲兵するさいに殺す計画があると、密告があったのだ。","",},
        [6380] = {12104,"曹操","者ども、一斉に矢を放て！","者ども、一斉に矢を放て！","",},
        [6381] = {12104,"馬騰","ぐぬぬ……国に仇なす逆賊を討ち果たせぬとは……これも天命か！(恨みをのんで死ぬ)","ぐぬぬ……国に仇なす逆賊を討ち果たせぬとは……これも天命か！(恨みをのんで死ぬ)","",},
        [6382] = {12201,"曹操","聞けば劉備は最近兵馬を調練し、武器も整えているという。もし蜀の地を奪ったら、翼を得るようなものだ。どうしたものか。","聞けば劉備は最近兵馬を調練し、武器も整えているという。もし蜀の地を奪ったら、翼を得るようなものだ。どうしたものか。","",},
        [6383] = {12201,"陳羣","いま劉備と孫権は唇歯の関係を結んでいます。","いま劉備と孫権は唇歯の関係を結んでいます。","曹操",},
        [6384] = {12201,"陳羣","しかし丞相が大軍を率いて呉を攻めれば、蜀に気が向いている劉備に援軍を出す余裕はなく、呉は丞相の手に入りましょう。","しかし丞相が大軍を率いて呉を攻めれば、蜀に気が向いている劉備に援軍を出す余裕はなく、呉は丞相の手に入りましょう。","曹操",},
        [6385] = {12201,"曹操","陳羣の言うことは、わしの考えにぴったりだ。直ちに三十万の兵を起こし、呉を攻める準備をせよ！","陳羣の言うことは、わしの考えにぴったりだ。直ちに三十万の兵を起こし、呉を攻める準備をせよ！","陳羣",},
        [6386] = {12202,"孫権","間者の報告では、曹操が三十万の大軍で攻めて来るそうだ。","間者の報告では、曹操が三十万の大軍で攻めて来るそうだ。","",},
        [6387] = {12202,"張昭","魯粛に劉備宛てに協力を求める書簡を書いてもらいましょう。","魯粛に劉備宛てに協力を求める書簡を書いてもらいましょう。","孫権",},
        [6388] = {12202,"張昭","恩がありますから劉備も従うはずです。しかも呉にとっては婿ですから、義理からも断れますまい。","恩がありますから劉備も従うはずです。しかも呉にとっては婿ですから、義理からも断れますまい。","孫権",},
        [6389] = {12203,"諸葛亮","江南の兵も、荊州の兵も動かさず、曹操に東南を狙う暇もないようにさせる方法があります。","江南の兵も、荊州の兵も動かさず、曹操に東南を狙う暇もないようにさせる方法があります。","",},
        [6390] = {12203,"諸葛亮","魯粛殿は枕を高くして眠っていてください。もし北から魏軍が侵入してきても、劉備様には退ける策がありますから。","魯粛殿は枕を高くして眠っていてください。もし北から魏軍が侵入してきても、劉備様には退ける策がありますから。","",},
        [6391] = {12203,"劉備","どんな妙計があるというのだ?","どんな妙計があるというのだ?","諸葛亮",},
        [6392] = {12203,"諸葛亮","曹操がずっと気にかけていたのは、西涼の軍です。","曹操がずっと気にかけていたのは、西涼の軍です。","劉備",},
        [6393] = {12203,"諸葛亮","先ごろ曹操は馬騰を殺しましたが、息子の馬超が今も西涼で軍を統率しており、復讐を誓っているでしょう。","先ごろ曹操は馬騰を殺しましたが、息子の馬超が今も西涼で軍を統率しており、復讐を誓っているでしょう。","劉備",},
        [6394] = {12203,"諸葛亮","わが君より書簡を送り、馬超に兵を挙げ関内に攻め入るよう促せば、曹操も呉に攻め下る余裕はないでしょう。","わが君より書簡を送り、馬超に兵を挙げ関内に攻め入るよう促せば、曹操も呉に攻め下る余裕はないでしょう。","劉備",},
        [6395] = {12301,"馬超","昨夜不思議な夢を見た。","昨夜不思議な夢を見た。","龐徳",},
        [6396] = {12301,"馬超","雪野原に寝ていたら、何頭もの虎が襲って来て噛みつこうとするのだ。驚いて目を覚ましたが、非常に気になる。","雪野原に寝ていたら、何頭もの虎が襲って来て噛みつこうとするのだ。驚いて目を覚ましたが、非常に気になる。","龐徳",},
        [6397] = {12301,"龐徳","雪の中で虎に出会うのは不吉のしらせ。許昌に行かれた老将軍に異変があったのでは?","雪の中で虎に出会うのは不吉のしらせ。許昌に行かれた老将軍に異変があったのでは?","馬超",},
        [6398] = {12301,"馬岱","(よろめきながら入って来る)うぐっ、うう……叔父上も弟君も、曹操に殺されました！","(よろめきながら入って来る)うぐっ、うう……叔父上も弟君も、曹操に殺されました！","馬超",},
        [6399] = {12301,"馬超","なっ……なにぃ、そんなバカな！……おのれ曹操、生かしてはおけぬ！","なっ……なにぃ、そんなバカな！……おのれ曹操、生かしてはおけぬ！","龐徳",},
        [6400] = {12301,"龐徳","劉備殿の使いが書簡を携えてきました。将軍と手を組んで逆賊・曹操を討ちたいとのこと。","劉備殿の使いが書簡を携えてきました。将軍と手を組んで逆賊・曹操を討ちたいとのこと。","馬超",},
        [6401] = {12301,"馬超","いいだろう！父の仇、討たずにおくものか！すぐに大軍を起こし、劉備と落ち合おう。","いいだろう！父の仇、討たずにおくものか！すぐに大軍を起こし、劉備と落ち合おう。","龐徳",},
        [6402] = {12302,"韓遂","曹操がこんな書簡を寄こした。お前を生け捕りにして許都に来れば、わしを西涼侯に封じようというのだ。","曹操がこんな書簡を寄こした。お前を生け捕りにして許都に来れば、わしを西涼侯に封じようというのだ。","馬超",},
        [6403] = {12302,"馬超","では、どうぞ俺と馬岱の二人を縄にかけて許昌に送ってください。叔父上に危害が及ばぬように。","では、どうぞ俺と馬岱の二人を縄にかけて許昌に送ってください。叔父上に危害が及ばぬように。","韓遂",},
        [6404] = {12302,"韓遂","何を言うか、お前の父上とは義兄弟の間柄だ、そんなことをするものか。お前が兵を起こすというなら、わしも力添えしよう。","何を言うか、お前の父上とは義兄弟の間柄だ、そんなことをするものか。お前が兵を起こすというなら、わしも力添えしよう。","馬超",},
        [6405] = {12303,"馬超","長安城はむかし漢の都だっただけあって、さすがに堅固だ。","長安城はむかし漢の都だっただけあって、さすがに堅固だ。","",},
        [6406] = {12303,"馬超","わが軍が包囲してもう十日になるが、攻めても攻めても破れない。さてどうするか……","わが軍が包囲してもう十日になるが、攻めても攻めても破れない。さてどうするか……","",},
        [6407] = {12303,"主君","長安城内は土質が硬く、水は悪くて飲めない。","長安城内は土質が硬く、水は悪くて飲めない。","馬超",},
        [6408] = {12303,"主君","もう十日も囲んでいるから、軍民ともに困り果てている。ほどなくあそこは落ちるでしょう。あとは……ヒソヒソ。","もう十日も囲んでいるから、軍民ともに困り果てている。ほどなくあそこは落ちるでしょう。あとは……ここをこうすることで","馬超",},
        [6409] = {12303,"馬超","なるほど、それは妙計だ。","なるほど、それは妙計だ。","プレイヤー",},
        [6410] = {12304,"龐徳","五日が過ぎ、軍民が城を出て薪や水を運んでいるとき、わが軍の者が隙をみて城内に入り込み、内と外から挟み撃ちにした。","五日が過ぎ、軍民が城を出て薪や水を運んでいるとき、わが軍の者が隙をみて城内に入り込み、内と外から挟み撃ちにした。","馬超",},
        [6411] = {12304,"龐徳","敵には手向かう暇も与えなかった。","敵には手向かう暇も与えなかった。","馬超",},
        [6412] = {12304,"馬超","このたびの長安城の攻略では、みな大手柄だったな。褒美を与えよう！","このたびの長安城の攻略では、みな大手柄だったな。褒美を与えよう！","龐徳",},
        [6413] = {12401,"曹操","長安を失っただと?！馬鹿めが！","長安を失っただと?！馬鹿めが！","",},
        [6414] = {12401,"曹操","徐晃と曹洪はすぐ潼関に行け。十日間守り抜けば、わしが後から大軍を率いて駆けつける。万一この要害を奪われた者は死罪にするぞ！","徐晃と曹洪はすぐ潼関に行け。十日間守り抜けば、わしが後から大軍を率いて駆けつける。万一この要害を奪われた者は死罪にするぞ！","",},
        [6415] = {12401,"曹仁","曹洪は短気ですから、何か過ちがあってはと心配です。","曹洪は短気ですから、何か過ちがあってはと心配です。","曹操",},
        [6416] = {12401,"曹操","お前はわしに従って兵糧の護送をして、続けて出発せよ。","お前はわしに従って兵糧の護送をして、続けて出発せよ。","曹仁",},
        [6417] = {12402,"馬超","曹操の一族は臆病者ぞろいか！腕に覚えがあるなら出て来て俺と勝負しろ！それとも、城内にいるのは首を縮めた亀ばかりか?","曹操の一族は臆病者ぞろいか！腕に覚えがあるなら出て来て俺と勝負しろ！それとも、城内にいるのは首を縮めた亀ばかりか?","",},
        [6418] = {12402,"曹洪","ぐぬぬ、ふざけやがって！出て行ってあの思い上がった身の程知らずを懲らしめてやる！","ぐぬぬ、ふざけやがって！出て行ってあの思い上がった身の程知らずを懲らしめてやる！","徐晃",},
        [6419] = {12402,"徐晃","あれは馬超のたくらみで、わざと将軍を怒らせているのです。","あれは馬超のたくらみで、わざと将軍を怒らせているのです。","曹洪",},
        [6420] = {12402,"徐晃","決して乗せられてはなりません。丞相の大軍が到着すれば、しかるべき作戦がありましょう。","決して乗せられてはなりません。丞相の大軍が到着すれば、しかるべき作戦がありましょう。","曹洪",},
        [6421] = {12403,"曹洪","奴らの兵も馬も疲れているな……いい機会だ、出て行って馬超に決戦を挑んでやる！","奴らの兵も馬も疲れているな……いい機会だ、出て行って馬超に決戦を挑んでやる！","",},
        [6422] = {12403,"曹洪","毎日あらん限りの悪態をつきやがって、もう我慢ならん！","毎日あらん限りの悪態をつきやがって、もう我慢ならん！","",},
        [6423] = {12403,"徐晃","曹洪将軍、行ってはなりませぬ、お戻りください！","曹洪将軍、行ってはなりませぬ、お戻りください！","",},
        [6424] = {12403,"馬超","もう遅いわ！わが槍を食らえ！","もう遅いわ！わが槍を食らえ！","",},
        [6425] = {12403,"徐晃","しまった、敵の罠にかかった！曹洪将軍、早く逃げなさい、私が援護します！","しまった、敵の罠にかかった！曹洪将軍、早く逃げなさい、私が援護します！","",},
        [6426] = {12501,"曹操","十日と日限を切っておいただろうが！なぜ九日目に潼関を失ったのだ?","十日と日限を切っておいただろうが！なぜ九日目に潼関を失ったのだ?","曹洪",},
        [6427] = {12501,"曹洪","西涼軍の馬超めに、一族を誹謗中傷され、怒りをこらえきれず……","西涼軍の馬超めに、一族を誹謗中傷され、怒りをこらえきれず……","曹操",},
        [6428] = {12501,"曹洪","敵軍の士気が緩んだ隙に懲らしめてやろうと思い、計略に乗せられた次第です。","敵軍の士気が緩んだ隙に懲らしめてやろうと思い、計略に乗せられた次第です。","曹操",},
        [6429] = {12501,"曹操","若い曹洪が血気にはやるのはともかく、なぜ徐晃までわきまえなかったのか。","若い曹洪が血気にはやるのはともかく、なぜ徐晃までわきまえなかったのか。","徐晃",},
        [6430] = {12501,"徐晃","何度もお諫めしたのですが、聞き入れてもらえず……","何度もお諫めしたのですが、聞き入れてもらえず……","曹操",},
        [6431] = {12501,"徐晃","あの日は糧秣車を点検していて、気づいたときには将軍はもう関門を出た後で……","あの日は糧秣車を点検していて、気づいたときには将軍はもう関門を出た後で……","曹操",},
        [6432] = {12501,"曹操","曹洪！本来ならお前の首を刎ねるところだが、諸将の命乞いに免じて、一命は預けておく。明日わしが潼関を奪い返しにゆこう。","曹洪！本来ならお前の首を刎ねるところだが、諸将の命乞いに免じて、一命は預けておく。明日わしが潼関を奪い返しにゆこう。","曹洪",},
        [6433] = {12502,"馬超","逆賊・曹操め！天子をないがしろにした罪の数々は、死刑にしても足りぬ！","逆賊・曹操め！天子をないがしろにした罪の数々は、死刑にしても足りぬ！","",},
        [6434] = {12502,"馬超","あまつさえわが父上と弟を殺したとあっては、不倶戴天の敵だ！","あまつさえわが父上と弟を殺したとあっては、不倶戴天の敵だ！","",},
        [6435] = {12502,"曹操","まずい、この馬超という小僧、実に勇猛だ。わしでは相手にならん。今は逃げるしかないか。","まずい、この馬超という小僧、実に勇猛だ。わしでは相手にならん。今は逃げるしかないか。","",},
        [6436] = {12502,"西涼軍","追え、逃がすな！赤い戦袍を着た奴が曹操だ！","追え、逃がすな！赤い戦袍を着た奴が曹操だ！","",},
        [6437] = {12502,"曹操","この戦袍は目印になる、脱ぎ捨ててしまえ。","この戦袍は目印になる、脱ぎ捨ててしまえ。","",},
        [6438] = {12502,"西涼軍","ひげの長い奴が曹操だ！","ひげの長い奴が曹操だ！","",},
        [6439] = {12502,"曹操","おのれ、ひげまで切り落とすことになろうとは……！","おのれ、ひげまで切り落とすことになろうとは……！","",},
        [6440] = {12601,"許褚","ブヒッ！馬超ってのはすごい奴だそうだあ。おらが決闘してやるぞうっ！","ブヒッ！馬超ってのはすごい奴だそうだあ。おらが決闘してやるぞうっ！","",},
        [6441] = {12601,"馬超","許褚が果たし状を寄こしたぞ。馬鹿にしやがって！","許褚が果たし状を寄こしたぞ。馬鹿にしやがって！","",},
        [6442] = {12601,"許褚","ごちゃごちゃ言ってねえで、戦いに来るんだぞう！","ごちゃごちゃ言ってねえで、戦いに来るんだぞう！","馬超",},
        [6443] = {12601,"馬超","チッ……こんな手強い奴は見たことがない。「虎痴」の異名はダテじゃないな。","チッ……こんな手強い奴は見たことがない。「虎痴」の異名はダテじゃないな。","許褚",},
        [6444] = {12701,"曹操","韓遂の使いが書簡を持って来た。占領した地を割譲して和睦したいというのだ。賈詡はどう思う?","韓遂の使いが書簡を持って来た。占領した地を割譲して和睦したいというのだ。賈詡はどう思う?","賈詡",},
        [6445] = {12701,"賈詡","兵は詐りを厭わず。まあ計略でしょうが、ひとまず受け入れましょう。","兵は詐りを厭わず。まあ計略でしょうが、ひとまず受け入れましょう。","曹操",},
        [6446] = {12701,"賈詡","その上で離間の計を用いて、韓遂と馬超の仲を裂けば、敵を破れます。","その上で離間の計を用いて、韓遂と馬超の仲を裂けば、敵を破れます。","曹操",},
        [6447] = {12701,"曹操","妙計は一致するものだな。賈詡の計略は、ちょうどわしも考えていたところだ。","妙計は一致するものだな。賈詡の計略は、ちょうどわしも考えていたところだ。","賈詡",},
        [6448] = {12701,"曹操","馬超に返書を送れ。我が方は徐々に兵を退くから、そちらも河西の占領地を返還せよと。","馬超に返書を送れ。我が方は徐々に兵を退くから、そちらも河西の占領地を返還せよと。","",},
        [6449] = {12702,"馬超","曹操から叔父上に手紙が送られてきたそうですが、ひとつ見せていただけますか?","曹操から叔父上に手紙が送られてきたそうですが、ひとつ見せていただけますか?","韓遂",},
        [6450] = {12702,"韓遂","これがそうだ。なぜかあちこちに書き直しや塗りつぶしがある。間違って下書きを送って来たのだろう。","これがそうだ。なぜかあちこちに書き直しや塗りつぶしがある。間違って下書きを送って来たのだろう。","馬超",},
        [6451] = {12702,"馬超","下書きを人に送る者がいるでしょうか?もしや叔父上……俺に知られては困ることがあって、塗りつぶされたのでは……","下書きを人に送る者がいるでしょうか?もしや叔父上……俺に知られては困ることがあって、塗りつぶされたのでは……","韓遂",},
        [6452] = {12702,"韓遂","疑うのはよしてくれ。わしに二心はない。","疑うのはよしてくれ。わしに二心はない。","馬超",},
        [6453] = {12703,"楊秋","馬超はおのれの武勇を鼻にかけ、日ごろ韓将軍を侮っております。","馬超はおのれの武勇を鼻にかけ、日ごろ韓将軍を侮っております。","韓遂",},
        [6454] = {12703,"楊秋","いっそ曹操に降ってしまわれては?諸侯の地位は約束されましょう。","いっそ曹操に降ってしまわれては?諸侯の地位は約束されましょう。","韓遂",},
        [6455] = {12703,"韓遂","わしは馬騰とは義兄弟の契りを結んだ仲だ。その息子を裏切れるものか。","わしは馬騰とは義兄弟の契りを結んだ仲だ。その息子を裏切れるものか。","楊秋",},
        [6456] = {12703,"楊秋","こうなってはもう仕方ありませぬ。","こうなってはもう仕方ありませぬ。","韓遂",},
        [6457] = {12703,"楊秋","密使をやって曹操に情報を送り、今夜火を放つのを合図に内外呼応して、馬超を討とうと取り決めました。","密使をやって曹操に情報を送り、今夜火を放つのを合図に内外呼応して、馬超を討とうと取り決めました。","韓遂",},
        [6458] = {12704,"馬超","裏切り者ども、俺を殺そうというのか！おのれ！(剣を抜いて韓遂の左手を斬り落とす)","裏切り者ども、俺を殺そうというのか！おのれ！(剣を抜いて韓遂の左手を斬り落とす)","",},
        [6459] = {12704,"于禁","曹丞相のもとより、韓将軍の支援に参った！","曹丞相のもとより、韓将軍の支援に参った！","",},
        [6460] = {12704,"主君","ここで犬死にするわけにいかない。馬超将軍、私について来て。逃げのびて、再挙を図ろう！","ここで犬死にするわけにいかない。馬超将軍、私について来て。逃げのびて、再挙を図るのよ！","馬超",},
        [6461] = {12801,"張魯","西涼の馬騰が曹操に殺され、先ごろ馬超も敗れた。","西涼の馬騰が曹操に殺され、先ごろ馬超も敗れた。","閻圃",},
        [6462] = {12801,"張魯","曹操はきっとこの漢中に攻めて来よう。わしは漢寧王と称し、曹操を防ぐ兵を起こそうと思うが、どうだろう?","曹操はきっとこの漢中に攻めて来よう。わしは漢寧王と称し、曹操を防ぐ兵を起こそうと思うが、どうだろう?","閻圃",},
        [6463] = {12801,"閻圃","この漢川は財は豊かで糧は満ち足り、四方は堅固な要害です。","この漢川は財は豊かで糧は満ち足り、四方は堅固な要害です。","張魯",},
        [6464] = {12801,"閻圃","馬超が敗れたため、西涼からわが漢中に流れてきた民は数万にのぼります。","馬超が敗れたため、西涼からわが漢中に流れてきた民は数万にのぼります。","張魯",},
        [6465] = {12801,"閻圃","益州の劉璋は暗君ですから、先に倒して蜀四十一州を併合し、それから王を称しても遅くはありません。","益州の劉璋は暗君ですから、先に倒して蜀四十一州を併合し、それから王を称しても遅くはありません。","張魯",},
        [6466] = {12802,"劉璋","張魯がわが蜀に攻めてくるそうだ。どうしたらよかろう?","張魯がわが蜀に攻めてくるそうだ。どうしたらよかろう?","張松",},
        [6467] = {12802,"張松","ご案じなさいますな。不肖ながらこの三寸の舌をふるって、張魯がこの蜀に手出しできないようにしてみせます。","ご案じなさいますな。不肖ながらこの三寸の舌をふるって、張魯がこの蜀に手出しできないようにしてみせます。","劉璋",},
        [6468] = {12802,"劉璋","詳しく聞かせてくれ。","詳しく聞かせてくれ。","張松",},
        [6469] = {12802,"張松","曹操は呂布を滅ぼし、また馬超をも破って、天下無敵とのこと。","曹操は呂布を滅ぼし、また馬超をも破って、天下無敵とのこと。","劉璋",},
        [6470] = {12802,"張松","わが君の貢ぎ物を携えて、曹操に漢中を攻め、張魯を討つよう説きすすめましょう。","わが君の貢ぎ物を携えて、曹操に漢中を攻め、張魯を討つよう説きすすめましょう。","劉璋",},
        [6471] = {12802,"張松","そうすれば張魯も自分のことで手一杯で、わが蜀を窺うこともできないでしょう。","そうすれば張魯も自分のことで手一杯で、わが蜀を窺うこともできないでしょう。","劉璋",},
        [6472] = {12802,"劉璋","それはいい。お前の言う通りにしよう。","それはいい。お前の言う通りにしよう。","張松",},
        [6473] = {12901,"曹操","そちらの主、劉璋は、なぜ毎年貢ぎ物を送ってこない?","そちらの主、劉璋は、なぜ毎年貢ぎ物を送ってこない?","張松",},
        [6474] = {12901,"張松","道が険阻な上に山賊が横行し、なかなか通行もできないのです。","道が険阻な上に山賊が横行し、なかなか通行もできないのです。","曹操",},
        [6475] = {12901,"曹操","中原はわしが平定したのだぞ。盗賊などどこから出る?","中原はわしが平定したのだぞ。盗賊などどこから出る?","張松",},
        [6476] = {12901,"張松","いえいえ、南には孫権、北には張魯、西には劉備がおります。とても天下泰平などは申せません。","いえいえ、南には孫権、北には張魯、西には劉備がおります。とても天下泰平などは申せません。","曹操",},
        [6477] = {12901,"曹操","ふん、卑しい面構えの上に、不愉快なことを言う。無礼な奴め。","ふん、卑しい面構えの上に、不愉快なことを言う。無礼な奴め。","張松",},
        [6478] = {12902,"張松","曹丞相も大したことはない。文においては孔子や孟子の道にも明らかでなく、武においては孫子や呉子の域にも達しない。","曹丞相も大したことはない。文においては孔子や孟子の道にも明らかでなく、武においては孫子や呉子の域にも達しない。","",},
        [6479] = {12902,"張松","ただ覇道をもって大きな位を占めているだけだ。","ただ覇道をもって大きな位を占めているだけだ。","",},
        [6480] = {12902,"楊修","貴殿は片田舎に住んでいて、丞相の大才をご存知ないのです。これをご覧なさい、『孟徳新書』という丞相の著作です。","貴殿は片田舎に住んでいて、丞相の大才をご存知ないのです。これをご覧なさい、『孟徳新書』という丞相の著作です。","張松",},
        [6481] = {12902,"張松","(一通り目を通して)……これしきの書物ならば、わが蜀では子どもでも諳んじている。目新しいことはない。","(一通り目を通して)……これしきの書物ならば、わが蜀では子どもでも諳んじている。目新しいことはない。","楊修",},
        [6482] = {12902,"楊修","これは丞相の秘蔵の書で、まだ世には伝えられていません。蜀の子どもが覚えているわけないでしょう。","これは丞相の秘蔵の書で、まだ世には伝えられていません。蜀の子どもが覚えているわけないでしょう。","張松",},
        [6483] = {12902,"張松","お疑いならば、そらで読んで聞かせよう。(最初から最後まで一字の誤りもなく暗誦する)","お疑いならば、そらで読んで聞かせよう。(最初から最後まで一字の誤りもなく暗誦する)","楊修",},
        [6484] = {12902,"楊修","ひと目見ただけで覚えるとは……！まことに天下の奇才でいらっしゃる！","ひと目見ただけで覚えるとは……！まことに天下の奇才でいらっしゃる！","張松",},
        [6485] = {12903,"曹操","天下のつまらぬ者など、わしの目には塵芥のようなものだ。我に従う者は生き延び、逆らう者は死ぬ。わかっているか?","天下のつまらぬ者など、わしの目には塵芥のようなものだ。我に従う者は生き延び、逆らう者は死ぬ。わかっているか?","張松",},
        [6486] = {12903,"張松","いかにも、丞相の兵が進むところ、戦えば必ず勝ち、攻めれば必ず取りたもう。","いかにも、丞相の兵が進むところ、戦えば必ず勝ち、攻めれば必ず取りたもう。","曹操",},
        [6487] = {12903,"張松","その昔濮陽で呂布にもてあそばれ、宛城で張繍に追われ、赤壁で周瑜にしてやられ、華容で関羽に泣きすがり――","その昔濮陽で呂布にもてあそばれ、宛城で張繍に追われ、赤壁で周瑜にしてやられ、華容で関羽に泣きすがり――","曹操",},
        [6488] = {12903,"張松","潼関ではひげを切って戦袍を捨て、渭水では船を奪って矢を避け、難を逃れられた。さすがは天下無敵！","潼関ではひげを切って戦袍を捨て、渭水では船を奪って矢を避け、難を逃れられた。さすがは天下無敵！","曹操",},
        [6489] = {12903,"曹操","腐れ儒者めが、わしの負け戦ばかり並べ立ておって！棒叩きにして放り出せ！","腐れ儒者めが、わしの負け戦ばかり並べ立ておって！棒叩きにして放り出せ！","張松",},
        [6490] = {13001,"張松","曹操がこれほど傲慢な奴だったとは！","曹操がこれほど傲慢な奴だったとは！","",},
        [6491] = {13001,"張松","もともと蜀の地図を献じてもいいと思って来たのに……帰りは回り道をして荊州の劉備の様子を見てみよう。","もともと蜀の地図を献じてもいいと思って来たのに……帰りは回り道をして荊州の劉備の様子を見てみよう。","",},
        [6492] = {13001,"主君","そこにいらしたのは張松殿ですか?兄上も私も長らくお待ちしておりました。","そこにいらしたのは張松殿ですか?兄上も私も長らくお待ちしておりました。","張松",},
        [6493] = {13001,"劉備","ご雷名を耳にし、ここまでお出迎えに参りました。宜しければ、私の城でしばし休んでゆかれませんか。","ご雷名を耳にし、ここまでお出迎えに参りました。宜しければ、私の城でしばし休んでゆかれませんか。","張松",},
        [6494] = {13001,"劉備","敬慕の情を述べさせてください。","敬慕の情を述べさせてください。","張松",},
        [6495] = {13001,"張松","ふぅむ。劉備は寛容で客を大事にすると聞いていたが、たしかにその通りだ。","ふぅむ。劉備は寛容で客を大事にすると聞いていたが、たしかにその通りだ。","",},
        [6496] = {13002,"張松","この数日間のおもてなしに感謝いたします。そろそろ帰らねばなりませんので、ここでおいとまします。","この数日間のおもてなしに感謝いたします。そろそろ帰らねばなりませんので、ここでおいとまします。","劉備",},
        [6497] = {13002,"劉備","よく三日もの間お留まりくださいました。今日お別れしたら、次はいつお教えを仰げるでしょう。","よく三日もの間お留まりくださいました。今日お別れしたら、次はいつお教えを仰げるでしょう。","張松",},
        [6498] = {13002,"張松","主君を売って栄華を求めるのではありませんが、明君にお会いしたからには申し上げます。","主君を売って栄華を求めるのではありませんが、明君にお会いしたからには申し上げます。","劉備",},
        [6499] = {13002,"張松","まず蜀を取って基礎とし、それから北の漢中を望み、さらに中原を攻め、漢王室を助けてください。","まず蜀を取って基礎とし、それから北の漢中を望み、さらに中原を攻め、漢王室を助けてください。","劉備",},
        [6500] = {13002,"張松","もし劉備殿に蜀を取るおつもりがあれば、私が犬馬の労をとって内応いたしますが、いかがでしょう?","もし劉備殿に蜀を取るおつもりがあれば、私が犬馬の労をとって内応いたしますが、いかがでしょう?","劉備",},
        [6501] = {13002,"劉備","劉璋殿はわが同族です。それを攻めては、天下の人に痛罵されましょう。","劉璋殿はわが同族です。それを攻めては、天下の人に痛罵されましょう。","張松",},
        [6502] = {13003,"主君","世に出て功績を立てたいなら、先鞭をつけないと。今取らずに、他人に奪われてから後悔しても遅いですよ。","世に出て功績を立てたいなら、先鞭をつけないと。今取らずに、他人に奪われてから後悔しても遅いですよ。","",},
        [6503] = {13003,"張松","楊威将軍の言われる通りです。劉備殿の人徳には感じ入りましたので、この地図を特別に献上しましょう。","楊威将軍の言われる通りです。劉備殿の人徳には感じ入りましたので、この地図を特別に献上しましょう。","プレイヤー",},
        [6504] = {13003,"張松","これを見れば、蜀中の道は一目でわかります。","これを見れば、蜀中の道は一目でわかります。","プレイヤー",},
        [6505] = {13003,"張松","このことは速やかにお図りください。私には法正と孟達という親友がおりますが、この二人が必ずお助けします。","このことは速やかにお図りください。私には法正と孟達という親友がおりますが、この二人が必ずお助けします。","プレイヤー",},
        [6506] = {13003,"主君","このご恩は決して忘れません。いつか事が成ったら、必ずご厚意に報います。","このご恩は決して忘れません。いつか事が成ったら、必ずご厚意に報います。","張松",},
        [6507] = {13004,"張松","このほど曹操に会ってきたが、賢才を軽んじる男で、楽しみを共にすべきではない。そこで益州を劉備殿に委ねる約束をしたのだ。","このほど曹操に会ってきたが、賢才を軽んじる男で、楽しみを共にすべきではない。そこで益州を劉備殿に委ねる約束をしたのだ。","法正",},
        [6508] = {13004,"法正","私もとうから劉璋は無能だと感じて、劉備殿にお目にかかりたいと思っていたのだ。考えが一致した以上、ためらうことはあるまい。","私もとうから劉璋は無能だと感じて、劉備殿にお目にかかりたいと思っていたのだ。考えが一致した以上、ためらうことはあるまい。","張松",},
        [6509] = {13004,"張松","明日劉璋に会って、君と孟達の二人を荊州に使いにやるよう推薦しよう。劉備殿と大計を相談してきてくれ。","明日劉璋に会って、君と孟達の二人を荊州に使いにやるよう推薦しよう。劉備殿と大計を相談してきてくれ。","法正",},
        [6510] = {13101,"法正","益州は天府の国で、乱を治められない君主がいるべきではありません。","益州は天府の国で、乱を治められない君主がいるべきではありません。","劉備",},
        [6511] = {13101,"法正","劉璋殿は賢才を用いられないので、この地もやがて他人のものになるでしょう。","劉璋殿は賢才を用いられないので、この地もやがて他人のものになるでしょう。","劉備",},
        [6512] = {13101,"法正","それを将軍に渡そうというこの機会をお見逃しなく。もし取ろうとのお心があれば、私が死力を尽くします。","それを将軍に渡そうというこの機会をお見逃しなく。もし取ろうとのお心があれば、私が死力を尽くします。","劉備",},
        [6513] = {13101,"龐統","宴会の席で劉璋を除き去りましょう。百人の伏兵を忍ばせておき、わが君が杯を投げつけるのを合図に……","宴会の席で劉璋を除き去りましょう。百人の伏兵を忍ばせておき、わが君が杯を投げつけるのを合図に……","劉備",},
        [6514] = {13101,"劉備","それ以上は言うな。劉璋殿はわが同族。手にかけられるものか。","それ以上は言うな。劉璋殿はわが同族。手にかけられるものか。","",},
        [6515] = {13102,"劉璋の部下","劉備を益州に招き入れるのは、狼を家に引き入れるようなものです。のちの禍いにならぬよう、早く帰すべきです。","劉備を益州に招き入れるのは、狼を家に引き入れるようなものです。のちの禍いにならぬよう、早く帰すべきです。","劉璋",},
        [6516] = {13102,"劉璋","劉備殿はわしの兄のようなもの。他の者とは違うぞ。","劉備殿はわしの兄のようなもの。他の者とは違うぞ。","",},
        [6517] = {13102,"劉璋の部下","劉備に他意はなくとも、その手下の者は蜀を横取りして、富貴を図ろうとしています。","劉備に他意はなくとも、その手下の者は蜀を横取りして、富貴を図ろうとしています。","劉璋",},
        [6518] = {13102,"劉璋","それ以上言うな。われら兄弟の仲を裂こうとするでない。","それ以上言うな。われら兄弟の仲を裂こうとするでない。","",},
        [6519] = {13103,"劉璋","張魯がついに出兵し、葭萌関に攻め寄せてきたと報告を受けました。劉備殿、何か良策は?","張魯がついに出兵し、葭萌関に攻め寄せてきたと報告を受けました。劉備殿、何か良策は?","劉備",},
        [6520] = {13103,"劉備","私が手勢を率いて葭萌関を守り、食い止めましょう。","私が手勢を率いて葭萌関を守り、食い止めましょう。","劉璋",},
        [6521] = {13103,"劉璋","ではよろしくお頼みします。","ではよろしくお頼みします。","劉備",},
        [6522] = {13201,"顧雍","劉備は荊州の兵を分けて険阻な山道をはるばる越えていったそうです。すぐには戻れますまい。","劉備は荊州の兵を分けて険阻な山道をはるばる越えていったそうです。すぐには戻れますまい。","孫権",},
        [6523] = {13201,"顧雍","まず一軍を差し向けて蜀からの帰り道を断ち切り、続いて荊州を攻めればいかがかと。","まず一軍を差し向けて蜀からの帰り道を断ち切り、続いて荊州を攻めればいかがかと。","孫権",},
        [6524] = {13201,"孫権","それは妙計だ！","それは妙計だ！","顧雍",},
        [6525] = {13201,"呉国太","そんな進言をする者は斬り捨てますよ！一人娘を劉備に嫁がせてあるのに、兵を出そうとは。娘を殺す気ですか?","そんな進言をする者は斬り捨てますよ！一人娘を劉備に嫁がせてあるのに、兵を出そうとは。娘を殺す気ですか?","孫権",},
        [6526] = {13201,"呉国太","お前は父と兄の遺業を受け継いで、楽々と八十一州の土地を領しながら、それでも足りませぬか。","お前は父と兄の遺業を受け継いで、楽々と八十一州の土地を領しながら、それでも足りませぬか。","孫権",},
        [6527] = {13201,"呉国太","わずかな利益に目がくらんで、血を分けた妹の身を心にもかけぬとは。","わずかな利益に目がくらんで、血を分けた妹の身を心にもかけぬとは。","孫権",},
        [6528] = {13201,"孫権","母上の仰るとおりです。背いたりいたしませぬ。","母上の仰るとおりです。背いたりいたしませぬ。","呉国太",},
        [6529] = {13201,"張昭","わが君、わしに一計がございます。こうこうなさいませ……","わが君、わしに一計がございます。こうこうなさいませ……","孫権",},
        [6530] = {13202,"周善","呉国太様は病重く、昼も夜もあなた様を恋しがっておられます。","呉国太様は病重く、昼も夜もあなた様を恋しがっておられます。","孫尚香",},
        [6531] = {13202,"周善","阿斗様も連れてひと目会いに来てほしいと。すぐお帰りにならないと、今生のご対面はかなわないかもしれません。","阿斗様も連れてひと目会いに来てほしいと。すぐお帰りにならないと、今生のご対面はかなわないかもしれません。","孫尚香",},
        [6532] = {13202,"孫尚香","劉備様は兵を率いて遠くにいるのよ。帰るなら、軍師に伝えておかないと。","劉備様は兵を率いて遠くにいるのよ。帰るなら、軍師に伝えておかないと。","周善",},
        [6533] = {13202,"周善","もし軍師が劉備殿の返事を待てと言われたら?","もし軍師が劉備殿の返事を待てと言われたら?","孫尚香",},
        [6534] = {13202,"周善","一刻の猶予もございません。船はもうご用意してお待ちしていますから、今すぐ城を出られますよう。","一刻の猶予もございません。船はもうご用意してお待ちしていますから、今すぐ城を出られますよう。","孫尚香",},
        [6535] = {13202,"孫尚香","仕方ないわね、なら行きましょう。","仕方ないわね、なら行きましょう。","周善",},
        [6536] = {13203,"趙雲","その船、待て！奥方様に申し上げたい！","その船、待て！奥方様に申し上げたい！","",},
        [6537] = {13203,"趙雲","お発ちを止めはしないが、ひと言伺いたい。どちらへおいでになるのです！軍師にお断りもなく?","お発ちを止めはしないが、ひと言伺いたい。どちらへおいでになるのです！軍師にお断りもなく?","",},
        [6538] = {13203,"孫尚香","母上が危篤と聞いて、知らせる暇もなかったのよ！","母上が危篤と聞いて、知らせる暇もなかったのよ！","趙雲",},
        [6539] = {13203,"趙雲","お見舞いに行かれるのはよろしいが、若君は置いていかれませ。(阿斗を奪い取る)","お見舞いに行かれるのはよろしいが、若君は置いていかれませ。(阿斗を奪い取る)","孫尚香",},
        [6540] = {13203,"張飛","姐御、甥っ子は残してってくれ！(周善を斬る)","姐御、甥っ子は残してってくれ！(周善を斬る)","孫尚香",},
        [6541] = {13203,"孫尚香","無礼にもほどがあるわよ！","無礼にもほどがあるわよ！","張飛",},
        [6542] = {13203,"張飛","オレの兄貴は大漢の皇叔で、姐御の名誉を傷つけてもいねえ。","オレの兄貴は大漢の皇叔で、姐御の名誉を傷つけてもいねえ。","孫尚香",},
        [6543] = {13203,"張飛","今日のところはこれで別れるけどよ、もし少しでも恩義を感じたら、早く帰ってやってくれよ。","今日のところはこれで別れるけどよ、もし少しでも恩義を感じたら、早く帰ってやってくれよ。","孫尚香",},
        [6544] = {13301,"董昭","古来、人臣として丞相ほどの大功をあげられた方はおりません。丞相の偉大な功績を諸臣と同列にすべきではないでしょう。","古来、人臣として丞相ほどの大功をあげられた方はおりません。丞相の偉大な功績を諸臣と同列にすべきではないでしょう。","",},
        [6545] = {13301,"董昭","恐れながら、魏公の位をお受けになり、九錫を加え、その功績と徳行を天下に明らかにすべきかと。","恐れながら、魏公の位をお受けになり、九錫を加え、その功績と徳行を天下に明らかにすべきかと。","",},
        [6546] = {13301,"荀彧","それはいけません。もともと丞相が義兵を挙げたのは、漢王室を助けるためでした。忠節を貫き、あくまで謙虚な態度を守るべきです。","それはいけません。もともと丞相が義兵を挙げたのは、漢王室を助けるためでした。忠節を貫き、あくまで謙虚な態度を守るべきです。","董昭",},
        [6547] = {13301,"荀彧","君子は徳をもって人を愛すると言います。私利私欲に走ってはなりません。","君子は徳をもって人を愛すると言います。私利私欲に走ってはなりません。","董昭",},
        [6548] = {13301,"董昭","多くの者が望んでいることを、あなた一人で止められるおつもりか?","多くの者が望んでいることを、あなた一人で止められるおつもりか?","荀彧",},
        [6549] = {13301,"荀彧","そういうことになるのなら、致し方ありません。こんな日が来るのを望んではいませんでしたが。","そういうことになるのなら、致し方ありません。こんな日が来るのを望んではいませんでしたが。","董昭",},
        [6550] = {13302,"曹操","いよいよ江南に兵を進める。荀彧にも同行を命じる。","いよいよ江南に兵を進める。荀彧にも同行を命じる。","",},
        [6551] = {13302,"曹操の侍衛","荀彧殿はご病気のため、遠征は無理だそうです。寿春に留まって養生するしかないと。","荀彧殿はご病気のため、遠征は無理だそうです。寿春に留まって養生するしかないと。","曹操",},
        [6552] = {13302,"曹操","……よかろう。ならばこの重箱を届けてやれ。","……よかろう。ならばこの重箱を届けてやれ。","曹操の侍衛",},
        [6553] = {13303,"曹操の侍衛","荀彧殿、丞相よりお見舞いの重箱です。","荀彧殿、丞相よりお見舞いの重箱です。","荀彧",},
        [6554] = {13303,"荀彧","箱の中身は……空?……お気持ちはわかった。丞相は私にもう漢の禄は食むなと言われるのだな……(毒を飲んで自殺)","箱の中身は……空?……お気持ちはわかった。丞相は私にもう漢の禄は食むなと言われるのだな……(毒を飲んで自殺)","",},
        [6555] = {13401,"曹操の侍衛","長江一帯に旗や幟が無数に立ち並び、どの辺に兵が集結しているかも知れません。","長江一帯に旗や幟が無数に立ち並び、どの辺に兵が集結しているかも知れません。","",},
        [6556] = {13401,"曹操","ならば、小山に登って見るとしよう。","ならば、小山に登って見るとしよう。","",},
        [6557] = {13401,"曹操","……遠くに敵の軍船が見える。隊ごとに秩序正しく並び、五色の旗がひらめき、武器が輝いている。","……遠くに敵の軍船が見える。隊ごとに秩序正しく並び、五色の旗がひらめき、武器が輝いている。","",},
        [6558] = {13401,"曹操","雄々しいものだ！子を持つなら孫権のような男がよいな。劉表のせがれなどは、豚や犬のようだった。","雄々しいものだ！子を持つなら孫権のような男がよいな。劉表のせがれなどは、豚や犬のようだった。","",},
        [6559] = {13402,"孫権","丞相は中原をおさえ、富貴極めておるくせに、なぜ飽き足らず、わが江南まで侵しに来る！","丞相は中原をおさえ、富貴極めておるくせに、なぜ飽き足らず、わが江南まで侵しに来る！","曹操",},
        [6560] = {13402,"曹操","おぬしは臣下でありながら、王室を尊ばぬ。わしは天子の詔を奉じ、討伐に来たのだ。","おぬしは臣下でありながら、王室を尊ばぬ。わしは天子の詔を奉じ、討伐に来たのだ。","孫権",},
        [6561] = {13402,"孫権","笑止千万よ！おのれが天子を盾にとって諸侯に号令していることを、天下に知らぬ者があるか。","笑止千万よ！おのれが天子を盾にとって諸侯に号令していることを、天下に知らぬ者があるか。","曹操",},
        [6562] = {13402,"孫権","おのれを討って国を正したいのは、俺のほうだ！","おのれを討って国を正したいのは、俺のほうだ！","曹操",},
        [6563] = {13403,"曹操","昨夜、夢で空に二つの日輪を見た。孫権は並々ならぬ男だが、赤い日輪の夢は、やがて帝王になるという知らせだろうか。","昨夜、夢で空に二つの日輪を見た。孫権は並々ならぬ男だが、赤い日輪の夢は、やがて帝王になるという知らせだろうか。","",},
        [6564] = {13403,"曹操","幾度か戦ったが、勝負がつかん。今では大雨が降り続き、兵士たちは泥水に浸かって、ひどく難儀している。撤兵すべきだろうか。","幾度か戦ったが、勝負がつかん。今では大雨が降り続き、兵士たちは泥水に浸かって、ひどく難儀している。撤兵すべきだろうか。","",},
        [6565] = {13403,"曹操の従者","報告します。呉の使者が書簡を持って参りました。","報告します。呉の使者が書簡を持って参りました。","曹操",},
        [6566] = {13403,"曹操","孫権からの書簡か。「わしも丞相も、ともに漢朝に仕える臣だ。","孫権からの書簡か。「わしも丞相も、ともに漢朝に仕える臣だ。","",},
        [6567] = {13403,"曹操","……春の大雨の時季となった今、速やかに帰られたがよい。さもなくば、赤壁の愚が繰り返されよう――","……春の大雨の時季となった今、速やかに帰られたがよい。さもなくば、赤壁の愚が繰り返されよう――","",},
        [6568] = {13403,"曹操","「よろしく賢慮されよ。貴殿が死なんと、わしも落ち着かん。」……ふははっ！孫権は正直な男だ。よし、撤兵しよう！","「よろしく賢慮されよ。貴殿が死なんと、わしも落ち着かん。」……ふははっ！孫権は正直な男だ。よし、撤兵しよう！","",},
        [6569] = {13501,"劉備","曹操と孫権が戦ったそうだが、どちらが勝っても、荊州を取りに来るだろう。どうすべきか……","曹操と孫権が戦ったそうだが、どちらが勝っても、荊州を取りに来るだろう。どうすべきか……","",},
        [6570] = {13501,"主君","大丈夫ですよ、兄者。荊州には孔明殿がいるんだから、呉も敢えて攻めては来ませんよ。","大丈夫ですよ、兄さん。荊州には孔明殿がいるんだから、呉も敢えて攻めては来ませんよ。","劉備",},
        [6571] = {13501,"主君","それより兄者から劉璋に兵馬や兵糧を要求してみては?曹操に抵抗するためだと言って。","それより兄さんから劉璋に兵馬や兵糧を要求してみては?曹操に抵抗するためだと言って。","劉備",},
        [6572] = {13501,"劉備","よし、すぐに手紙をしたためるから、劉璋殿に届けに行ってくれ。","よし、すぐに手紙をしたためるから、劉璋殿に届けに行ってくれ。","プレイヤー",},
        [6573] = {13502,"楊懐","劉備が蜀に入ってより、恩恵を施して民心を手懐けているのは、良からぬ下心があってのことです。","劉備が蜀に入ってより、恩恵を施して民心を手懐けているのは、良からぬ下心があってのことです。","劉璋",},
        [6574] = {13502,"楊懐","いま兵馬や兵糧を要求してきましたが、決して与えてはなりません。","いま兵馬や兵糧を要求してきましたが、決して与えてはなりません。","劉璋",},
        [6575] = {13502,"劉璋","そういうわけにもいくまい。わしと劉備殿との間には兄弟の誼みがある。","そういうわけにもいくまい。わしと劉備殿との間には兄弟の誼みがある。","楊懐",},
        [6576] = {13502,"楊懐","劉備は梟雄です。あれを蜀の地に留めておくのは虎を家に入れるようなもの。","劉備は梟雄です。あれを蜀の地に留めておくのは虎を家に入れるようなもの。","劉璋",},
        [6577] = {13502,"楊懐","そのうえ兵馬や兵糧を与えては、虎に翼を添えることになります。","そのうえ兵馬や兵糧を与えては、虎に翼を添えることになります。","劉璋",},
        [6578] = {13502,"黄権","劉備には老兵や弱兵を貸し与えなさい。そうすれば兄弟の情も傷つかず、虎に翼を添えることにもなりますまい。","劉備には老兵や弱兵を貸し与えなさい。そうすれば兄弟の情も傷つかず、虎に翼を添えることにもなりますまい。","楊懐",},
        [6579] = {13503,"劉備","ふざけるな！こちらは劉璋のために骨を折って敵を防いでいるというのに、財貨を惜しんで老兵と弱兵しか寄こさぬとは。","ふざけるな！こちらは劉璋のために骨を折って敵を防いでいるというのに、財貨を惜しんで老兵と弱兵しか寄こさぬとは。","",},
        [6580] = {13503,"劉備","これで命がけで戦う気になれるか！","これで命がけで戦う気になれるか！","",},
        [6581] = {13503,"龐統","これは珍しい。仁愛の方と言われてきた我が君がこれほどご立腹なさるとは。これまでの誼みは捨てられましたか。","これは珍しい。仁愛の方と言われてきた我が君がこれほどご立腹なさるとは。これまでの誼みは捨てられましたか。","劉備",},
        [6582] = {13503,"劉備","そなたまで、よしてくれ……どう動くべきか。","そなたまで、よしてくれ……どう動くべきか。","龐統",},
        [6583] = {13503,"龐統","三つの策があります。一つめは今すぐ近道を急いで成都を急襲する。これが上策です。","三つの策があります。一つめは今すぐ近道を急いで成都を急襲する。これが上策です。","劉備",},
        [6584] = {13503,"龐統","二つめは荊州に帰るふりをする。そうすれば楊懐と高沛は必ず見送りに来るでしょう。","二つめは荊州に帰るふりをする。そうすれば楊懐と高沛は必ず見送りに来るでしょう。","劉備",},
        [6585] = {13503,"龐統","その機に乗じて二人を殺し、関所を奪う。まず涪城を落とし、それから成都に進軍します。これが中策です。","その機に乗じて二人を殺し、関所を奪う。まず涪城を落とし、それから成都に進軍します。これが中策です。","劉備",},
        [6586] = {13503,"龐統","三つめは白帝城まで兵を退き、すぐ荊州に帰って、ゆっくり後の策を練る。これが下策ですな。","三つめは白帝城まで兵を退き、すぐ荊州に帰って、ゆっくり後の策を練る。これが下策ですな。","劉備",},
        [6587] = {13503,"主君","三つの策のうち、上策は性急すぎるし、下策はのんびりしすぎる。中策が遅からず早からず、いいでしょう。","三つの策のうち、上策は性急すぎるし、下策はのんびりしすぎる。中策が遅からず早からず、いいでしょう。","劉備",},
        [6588] = {13601,"張松","劉備殿が私の勧めに従わず、荊州に引き揚げようとは……実に遺憾だ！","劉備殿が私の勧めに従わず、荊州に引き揚げようとは……実に遺憾だ！","",},
        [6589] = {13601,"張粛","(張松が劉備に宛てた手紙を拾う)……内応するから、しくじらぬようにだと?こっ、これはまさか、張松が謀反をたくらんでいるのか?主君に知らせねば！","(張松が劉備に宛てた手紙を拾う)……内応するから、しくじらぬようにだと?こっ、これはまさか、張松が謀反をたくらんでいるのか?主君に知らせねば！","",},
        [6590] = {13601,"劉璋","なんだと！張松め、これまで厚遇してやったのに、外部と通じて謀反を起こそうとは！張松の一家を皆殺しにしろ！","なんだと！張松め、これまで厚遇してやったのに、外部と通じて謀反を起こそうとは！張松の一家を皆殺しにしろ！","",},
        [6591] = {13601,"楊懐","私と高沛の二人が懐に刀を忍ばせ、見送りにかこつけて劉備を殺し、わざわいの根を断とう。","私と高沛の二人が懐に刀を忍ばせ、見送りにかこつけて劉備を殺し、わざわいの根を断とう。","",},
        [6592] = {13602,"龐統","楊懐と高沛がやって来たら、用心してくださいよ。もし来なければ、ためらうことなく、一気に関所を攻め落としましょう。","楊懐と高沛がやって来たら、用心してくださいよ。もし来なければ、ためらうことなく、一気に関所を攻め落としましょう。","劉備",},
        [6593] = {13602,"劉備","風が吹いて、「帥」の字の旗竿が倒れた。凶兆だろうか?","風が吹いて、「帥」の字の旗竿が倒れた。凶兆だろうか?","龐統",},
        [6594] = {13602,"龐統","天の知らせでしょうな。楊懐と高沛の二人が暗殺せんと待ち受けているはず。油断されますな。","天の知らせでしょうな。楊懐と高沛の二人が暗殺せんと待ち受けているはず。油断されますな。","劉備",},
        [6595] = {13602,"楊懐","劉備殿が荊州にご帰還されると承り、我ら二人でお見送りに参りました。","劉備殿が荊州にご帰還されると承り、我ら二人でお見送りに参りました。","劉備",},
        [6596] = {13602,"主君","見送りなら、なぜ刀を隠し持っている?","見送りなら、なぜ刀を隠し持っているの?","楊懐",},
        [6597] = {13602,"主君","兄者は劉璋のためにまじめに働いていたのに、お前達は仲を裂こうとしたうえ暗殺にまで来たのか。これは天誅だ！","兄さんは劉璋のためにまじめに働いていたのに、あなた達は仲を裂こうとしたうえ暗殺にまで来た。これは天誅よ！","楊懐",},
        [6598] = {13603,"主君","供の兵士ども、よく聞け！","供の兵士達、よく聞きなさい！","",},
        [6599] = {13603,"主君","この二人はご主君兄弟の仲を割こうとしたのみか、刀を隠し持って暗殺をたくらんだため、誅せられた。お前達に罪はないから、安心しろ。","この二人はご主君兄弟の仲を割こうとしたのみか、刀を隠し持って暗殺をたくらんだため、誅せられた。あなた達に罪はないから安心しなさい。","",},
        [6600] = {13603,"龐統","今度はお前達が案内人となって、こちらの大軍を関所に入れてくれ。褒美を取らせるぞ。","今度はお前達が案内人となって、こちらの大軍を関所に入れてくれ。褒美を取らせるぞ。","",},
        [6601] = {13701,"劉備","今や我々は涪水関を得た。","今や我々は涪水関を得た。","",},
        [6602] = {13701,"劉備","次は雒城を攻めよう。敵将は泠苞と鄧賢で、二万の兵を率いて二ヶ所に陣地を築いている。誰が魁の功名を立てに行く?","次は雒城を攻めよう。敵将は泠苞と鄧賢で、二万の兵を率いて二ヶ所に陣地を築いている。誰が魁の功名を立てに行く?","",},
        [6603] = {13701,"黄忠","ワシが参りましょう。","ワシが参りましょう。","劉備",},
        [6604] = {13701,"魏延","いやいや、老将軍のお歳では無理だ。俺が行きましょう。","いやいや、老将軍のお歳では無理だ。俺が行きましょう。","劉備",},
        [6605] = {13701,"龐統","お二方とも争いはそのくらいに。各々兵を率いて、それぞれの陣を攻めてください。","お二方とも争いはそのくらいに。各々兵を率いて、それぞれの陣を攻めてください。","魏延",},
        [6606] = {13701,"龐統","先に敵陣を奪ったほうを一番槍の手柄としましょう。","先に敵陣を奪ったほうを一番槍の手柄としましょう。","魏延",},
        [6607] = {13702,"魏延","黄忠将軍はまだ出発していないな。こちらが一足先に出発すれば、一番槍の手柄を立てられるぞ。","黄忠将軍はまだ出発していないな。こちらが一足先に出発すれば、一番槍の手柄を立てられるぞ。","",},
        [6608] = {13702,"魏延","早く出動したついでに、両方の陣を取ってしまえば、手柄を独り占めにできるぞ！","早く出動したついでに、両方の陣を取ってしまえば、手柄を独り占めにできるぞ！","",},
        [6609] = {13702,"鄧賢","魏延よ、さっさと馬を下りて降参しろ！","魏延よ、さっさと馬を下りて降参しろ！","魏延",},
        [6610] = {13702,"魏延","まずい、退け！","まずい、退け！","",},
        [6611] = {13703,"黄忠","老将黄忠ここにあり！鄧賢、死ねい！","老将黄忠ここにあり！鄧賢、死ねい！","",},
        [6612] = {13703,"主君","お前の陣地はすでに奪った。もう逃げ場はないぞ！","あなたの陣地はすでに奪った。もう逃げ場はないわよ！","",},
        [6613] = {13801,"劉備","孔明が荊州から手紙をくれた。天象を観るに、不吉な事が起きるかもしれない。","孔明が荊州から手紙をくれた。天象を観るに、不吉な事が起きるかもしれない。","",},
        [6614] = {13801,"劉備","将帥の身に凶事の多い兆しがあるから、くれぐれも慎むようにとのことだ。","将帥の身に凶事の多い兆しがあるから、くれぐれも慎むようにとのことだ。","",},
        [6615] = {13801,"劉備","それならば、ひとまず荊州に帰ろうか。","それならば、ひとまず荊州に帰ろうか。","",},
        [6616] = {13801,"龐統","占星術なら、私も少しは心得ています。","占星術なら、私も少しは心得ています。","劉備",},
        [6617] = {13801,"龐統","大凶の相は冷苞を斬ったことで済んだでしょう。案ずることなく、速やかに兵をお進めください。","大凶の相は冷苞を斬ったことで済んだでしょう。案ずることなく、速やかに兵をお進めください。","劉備",},
        [6618] = {13801,"劉備","そうか。それなら、兵を二手に分けて雒城に進もう。","そうか。それなら、兵を二手に分けて雒城に進もう。","龐統",},
        [6619] = {13802,"劉備","どう、どう……この馬、軍師を振り落としたぞ。なぜこんな暴れ馬に乗っているのだ?","どう、どう……この馬、軍師を振り落としたぞ。なぜこんな暴れ馬に乗っているのだ?","龐統",},
        [6620] = {13802,"龐統","この馬には長いこと乗っておりますが、こんなことは初めてですな。","この馬には長いこと乗っておりますが、こんなことは初めてですな。","劉備",},
        [6621] = {13802,"劉備","戦に臨んでは命に関わる。私の白馬はよく馴れているから、軍師はこちらに乗ると良い。間違いはないだろう。","戦に臨んでは命に関わる。私の白馬はよく馴れているから、軍師はこちらに乗ると良い。間違いはないだろう。","龐統",},
        [6622] = {13802,"龐統","恐れ入ります……この命を賭しても、ご厚恩に報います。","恐れ入ります……この命を賭しても、ご厚恩に報います。","劉備",},
        [6623] = {13803,"龐統","この地は、何という名か?","この地は、何という名か?","",},
        [6624] = {13803,"龐統","ほう、落鳳坡というのか。私の道号は鳳雛のゆえ、不吉な名だな。戻って道をかえるか。","ほう、落鳳坡というのか。私の道号は鳳雛のゆえ、不吉な名だな。戻って道をかえるか。","",},
        [6625] = {13803,"張任","白馬に乗っているのが劉備に違いない。奴が来たら、一斉に矢を射かけよ！","白馬に乗っているのが劉備に違いない。奴が来たら、一斉に矢を射かけよ！","",},
        [6626] = {13803,"龐統","いかん！伏兵か！退けッ！","いかん！伏兵か！退けッ！","",},
        [6627] = {13901,"諸葛亮","ああ、なんと……！西の空に星が墜ちた……龐統の命が尽きましたか……！","ああ、なんと……！西の空に星が墜ちた……龐統の命が尽きましたか……！","",},
        [6628] = {13901,"諸葛亮","今年は罡星が西にあるから、征軍には不利と観ました。","今年は罡星が西にあるから、征軍には不利と観ました。","",},
        [6629] = {13901,"諸葛亮","天狗星が我が軍の行く手に落ち、太白星が雒城に臨んでいるのも不吉。それでわが君にお慎みあるよう書簡をお送りしたのですが……","天狗星が我が軍の行く手に落ち、太白星が雒城に臨んでいるのも不吉。それでわが君にお慎みあるよう書簡をお送りしたのですが……","",},
        [6630] = {13901,"諸葛亮","今やわが君は片腕を失われた……やがて知らせがあるでしょう。","今やわが君は片腕を失われた……やがて知らせがあるでしょう。","",},
        [6631] = {13901,"関平","諸葛軍師に報告いたします。軍師龐統が落鳳坡にて、張任の矢により戦死しました。","諸葛軍師に報告いたします。軍師龐統が落鳳坡にて、張任の矢により戦死しました。","諸葛亮",},
        [6632] = {13902,"諸葛亮","わが君はいま涪城で進退窮まっておられる。私が行くしかないでしょう。","わが君はいま涪城で進退窮まっておられる。私が行くしかないでしょう。","",},
        [6633] = {13902,"関羽","軍師が行かれたら、誰が荊州を守るのです。ここは要地。責任は重大ですぞ。","軍師が行かれたら、誰が荊州を守るのです。ここは要地。責任は重大ですぞ。","諸葛亮",},
        [6634] = {13902,"諸葛亮","わが君からの書簡には名指しはなく、私に人選を任せられましたが、お心はよくわかっております。","わが君からの書簡には名指しはなく、私に人選を任せられましたが、お心はよくわかっております。","関羽",},
        [6635] = {13902,"諸葛亮","関羽殿にこの大任を引き受けていただきたい。","関羽殿にこの大任を引き受けていただきたい。","関羽",},
        [6636] = {13902,"関羽","承知いたした。死んでもお守りいたす。","承知いたした。死んでもお守りいたす。","諸葛亮",},
        [6637] = {13903,"諸葛亮","もし曹操が攻めて来たら、どう当たります?","もし曹操が攻めて来たら、どう当たります?","関羽",},
        [6638] = {13903,"関羽","力づくで防ぐのみ。","力づくで防ぐのみ。","諸葛亮",},
        [6639] = {13903,"諸葛亮","もし曹操と孫権が同時に攻めてきたら、どうしますか?","もし曹操と孫権が同時に攻めてきたら、どうしますか?","関羽",},
        [6640] = {13903,"関羽","兵を二手に分けて防ぎます。","兵を二手に分けて防ぎます。","諸葛亮",},
        [6641] = {13903,"諸葛亮","それでは、荊州は危ういですね。今から申し上げる戒めを、将軍はお忘れなきよう。そうすれば荊州は無事です。","それでは、荊州は危ういですね。今から申し上げる戒めを、将軍はお忘れなきよう。そうすれば荊州は無事です。","関羽",},
        [6642] = {13903,"関羽","ぜひ教えていただきたい。軍師のお言葉、肝に銘じておきます。","ぜひ教えていただきたい。軍師のお言葉、肝に銘じておきます。","諸葛亮",},
        [6643] = {13903,"諸葛亮","「北は曹操を拒ぎ、東は孫権と和す」","「北は曹操を拒ぎ、東は孫権と和す」","関羽",},
        [6644] = {14001,"諸葛亮","蜀は豪傑が多い。敵を侮ってはなりません。どこへ行っても住民には情けをかけ、兵卒をむやみに鞭打たぬように。","蜀は豪傑が多い。敵を侮ってはなりません。どこへ行っても住民には情けをかけ、兵卒をむやみに鞭打たぬように。","張飛",},
        [6645] = {14001,"諸葛亮","誤りなく、速やかに雒城へお越しください。","誤りなく、速やかに雒城へお越しください。","張飛",},
        [6646] = {14001,"張飛","軍師の命令どおりにするぜ。降参する者に危害は加えねえ。","軍師の命令どおりにするぜ。降参する者に危害は加えねえ。","諸葛亮",},
        [6647] = {14002,"厳顔の兵士","張飛は当陽の長坂坡で百万の曹操軍を一喝して退けた男、侮ってはいけません。","張飛は当陽の長坂坡で百万の曹操軍を一喝して退けた男、侮ってはいけません。","",},
        [6648] = {14002,"厳顔の兵士","こちらが堅く城を守って出なければ、あちらの兵糧は尽きてしまい、自ら退却するでしょう。","こちらが堅く城を守って出なければ、あちらの兵糧は尽きてしまい、自ら退却するでしょう。","",},
        [6649] = {14002,"張飛の従者","わが将軍より伝言だ。早々に降伏せよ。そうすれば領民の命は助けてやる。","わが将軍より伝言だ。早々に降伏せよ。そうすれば領民の命は助けてやる。","厳顔",},
        [6650] = {14002,"張飛の従者","もし帰順しないというなら城を踏み潰し、一人も生かしておかんぞ！","もし帰順しないというなら城を踏み潰し、一人も生かしておかんぞ！","厳顔",},
        [6651] = {14002,"厳顔","うぬぬ……！けしからん、無礼者のこわっぱめが！この使いの耳と鼻を斬ってつまみ出せ！","うぬぬ……！けしからん、無礼者のこわっぱめが！この使いの耳と鼻を斬ってつまみ出せ！","",},
        [6652] = {14003,"張飛","おのれ、厳顔のジジイめ！もう怒ったぜ！","おのれ、厳顔のジジイめ！もう怒ったぜ！","",},
        [6653] = {14003,"張飛の従者","将軍、落ち着いてください。ここ数日草刈りをしていた兵士が抜け道を見つけました。こっそり巴郡に抜けられます。","将軍、落ち着いてください。ここ数日草刈りをしていた兵士が抜け道を見つけました。こっそり巴郡に抜けられます。","張飛",},
        [6654] = {14003,"張飛","よっしゃ！ぐずぐずしてらんねえな。今夜遅くに兵糧を使い、真夜中の明月の出るころ、こっそり出発しろ。","よっしゃ！ぐずぐずしてらんねえな。今夜遅くに兵糧を使い、真夜中の明月の出るころ、こっそり出発しろ。","",},
        [6655] = {14003,"張飛","オレが先陣を切って前を進むから、お前らは後に続け。","オレが先陣を切って前を進むから、お前らは後に続け。","",},
        [6656] = {14003,"厳顔","やはり張飛はしびれをきらしたか。こちらの罠にかかったわ。前にいるのが張飛にちがいない。あやつを生け捕りにせよ！","やはり張飛はしびれをきらしたか。こちらの罠にかかったわ。前にいるのが張飛にちがいない。あやつを生け捕りにせよ！","",},
        [6657] = {14003,"張飛","待ちやがれ、ジジイ、オレはこっちだ！前にいたのは、影武者だァ！","待ちやがれ、ジジイ、オレはこっちだ！前にいたのは、影武者だァ！","",},
        [6658] = {14003,"厳顔","しまった！はかられたか！","しまった！はかられたか！","",},
        [6659] = {14004,"張飛","大将がこのザマで、まだ降らねえのか。あくまで刃向かう気か?","大将がこのザマで、まだ降らねえのか。あくまで刃向かう気か?","厳顔",},
        [6660] = {14004,"厳顔","おぬしらは正義に背き、わが州郡に侵入してきた敵じゃ。ここにはの、首を斬られる将軍はおっても、投降する将軍はおらんのじゃ！","おぬしらは正義に背き、わが州郡に侵入してきた敵じゃ。ここにはの、首を斬られる将軍はおっても、投降する将軍はおらんのじゃ！","張飛",},
        [6661] = {14004,"厳顔","さあ、こわっぱ、首を刎ねるというならさっさと刎ねよ！","さあ、こわっぱ、首を刎ねるというならさっさと刎ねよ！","張飛",},
        [6662] = {14004,"張飛","(みずから厳顔の縄を解き、上衣を着せてやる)さっきからの無礼、許してくれ。老将軍が豪傑の士だとはかねがね聞き知っていた。","(みずから厳顔の縄を解き、上衣を着せてやる)さっきからの無礼、許してくれ。老将軍が豪傑の士だとはかねがね聞き知っていた。","厳顔",},
        [6663] = {14004,"厳顔","(張飛の恩義に感動して、投降する)敗軍の将がこんな厚恩を受けるとは。犬馬の労を尽くして報いようぞ。","(張飛の恩義に感動して、投降する)敗軍の将がこんな厚恩を受けるとは。犬馬の労を尽くして報いようぞ。","張飛",},
        [6664] = {45,"張星彩","ご主君様！また新たな武将が仲間になってくれました！見に行きましょう！","ご主君様！また新たな武将が仲間になってくれました！見に行きましょう！","",},
